INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ACC 604', 'Statutory Interpretation', 0.5, 'LEC', 'This course will introduce the student to the Canadian standards related to the interpretation of tax legislation and decisions of the courts.', 'ACC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ACC 605', 'International Tax', 0.5, 'LEC', 'This course will introduce the student to Canadian taxation of international transactions, including the interpretation of tax treaties.', 'ACC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ACC 606', 'Business Valuations', 0.5, 'LEC', 'This course will provide the fundamentals of business valuation theory and practice. Students will receive advanced standing in part of the Canadian Insitute of Chartered Business Valuators'' Program of Studies.', 'ACC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ACC 607', 'Tax Issues Integration', 0.5, 'LEC', 'This course will develop the students'' skills in identifying and addressing tax issues, through the use of case studies.', 'ACC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ACC 609', 'Financial Statement Analysis', 0.5, 'LEC', 'This course introduces fundamental tools of analysis and evaluation that are widely used in a variety of fiancial careers.', 'ACC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ACC 610', 'Public Accounting Practice', 0.5, 'LEC, TUT', 'This course is designed to enhance the students'' abilities to apply knowledge of accounting principles, theories, and practices through the use of multi-dimensional case studies.', 'ACC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ACC 611', 'External Reporting', 0.5, 'LEC', 'This course examines accounting standards in evolution, adopting critical perspectives of current issues.', 'ACC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ACC 621', 'System Reliability Principles and Criteria', 0.5, 'LEC', 'This course will address the principles of reliable systems including availability, security, integrity and maintainability risks and control practices that can contribute to system reliability. (Course offerings will be internet-based.)', 'ACC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ACC 622', 'Electronic Commerce', 0.5, 'LEC', 'This course will address the features and related implementation issues of electronic commerce systems, including order processing, payment, fulfillment, integration with other entity systems and related issues of management, performance measurement and control. (Course offerings will be internet-based.)', 'ACC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ACC 623', 'Business Technology Law', 0.5, 'LEC', 'This course will address the legal issues related to information technology, including service agreements, intellectual property rights, taxation and other topics.', 'ACC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ACC 626', 'IT Assurance and Computer-Assisted Audit Techniques', 0.5, 'LEC', 'This course will address audit considerations and other assurance services in computer-based information systems. (Course offering will be internet-based.)', 'ACC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ACC 627', 'Business Process Enablement and Project Management', 0.5, 'LEC', 'This course will cover methods of improving business processes and managing related organizational change. (Course offering will be internet-based.)', 'ACC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ACC 650', 'Assurance and Governance', 0.5, 'LEC', 'This course considers the role of risk in the context of assurance and the client''s risk management process and addresses the impact of risk on an assurance provider''s professional practice.', 'ACC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ACC 652', 'Forensic Accounting', 0.5, 'SEM', 'Through case-based learning this course provides insight into financial statement fraud, frauds against organizations, fraud investigations and how frauds may be prevented and detected.', 'ACC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ACC 662', 'Tax Policy', 0.5, 'LEC', 'This course examines the economic, political, legal and administrative aspects of selected contemporary issues in Canadian tax policy.', 'ACC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ACC 680', 'Performance Measurement and Control systems for Implementing Strategy', 0.5, 'LEC', 'This course reviews and integrates theory and practice necessary for those intending to pursue management accounting careers. It provides a comprehensive insight into the problems facing top management accounting executives and includes a number of cases designed to expose students to real world situations requiring qualitative and quantitative analysis.', 'ACC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ACC 681', 'Understanding and Managing Organizational Change', 0.5, 'LEC', 'This course is designed for individuals who are interested in a deeper understanding of the process of change from a senior management perspective.', 'ACC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ACC 682', 'Measuring and Managing the Value Creation Process', 0.5, 'LEC', 'By examining the fundamentals of vlue creation, this course links internal performance systems with performance in financial markets.', 'ACC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ACC 683', 'Emerging Issues in Management and Marketing', 0.5, 'LEC', 'This course provides students with a review and synthesis of emerging issues in management and marketing in order to prepare students who are pursuing a career in financial management with a business background.  The course will consist of assigned readings, exercises, and term projects that involve evaluation of current business issues, and an examination.', 'ACC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ACC 684', 'Strategy and Business Models', 0.5, 'SEM', 'This course examines concepts within organizational strategy and performance by exploring business models and applying an analysis framework to assess the models'' connections with competivenesss and profitability.', 'ACC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ACC 685', 'Performance Management', 0.5, 'SEM', 'This course covers advanced management control topics including the design and use of performance metrics, aligning performance measurement with strategy, goal-setting and incentive programs, and risk management.', 'ACC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ACC 690', 'Topics in Accounting', 0.5, 'LEC', 'One or more one-term courses will be offered at different times as announced.', 'ACC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ACC 701', 'Financial Accounting Research Seminar', 0.5, 'SEM', 'Current research topics in financial accounting including applications of agency theory, capital markets theory and human information processing.', 'ACC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ACC 702', 'Management Accounting Research Seminar', 0.5, 'SEM', 'This course provides an in-depth look at the major research efforts that characterize contemporary management accounting and cost management systems. Particular emphasis is placed on field-based research techniques.', 'ACC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ACC 750', 'Auditing Research Seminar', 0.5, 'SEM', 'A broad survey of current auditing research covering: the socio-economic role of auditing in society; the agency relationships between shareholders, managers and auditors; the factors influencing the quality of auditing, in particular, experimental research into key attributes of the audit judgment process, and technological innovations such as statistical sampling, regression, and expert systems.', 'ACC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ACC 760', 'Taxation Research Seminar', 0.5, 'SEM', 'A survey of non-legal tax research in accounting. Tax research in related areas, especially economics, will also be reviewed.', 'ACC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ACC 770', 'Finance 1', 0.5, 'LEC', 'The course introduces options and other derivative securities in different asset classes. The main focus is on methods of pricing in a multi-period setting, but continuous-time models are also discussed. Topics may include no-arbitrage pricing theory, the fundamental theory of asset pricing, complete and incomplete markets,and pricing of complex financial instruments.', 'ACC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ACC 771', 'Finance 2', 0.5, 'LEC', 'The course discusses methods and tools for modeling of financial derivatives in the continuous-time setting. Both theory and practical applications are discussed. The first part covers methods of pricing and hedging of derivatives under different assumptions about the dynamics of the underlying economic factors. Topics normally include currency derivatives, American and exotic options, futures contracts, stochastic volatility models and mean-variance hedging. The second part deals with modeling and pricing of interest-rate products. Topics may include short interest rate models, the Heath-Jarrow-Morton Framework, and Libor and swap market models.', 'ACC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ACC 772', 'Finance 3', 0.5, 'LEC', 'The course will cover selected and advanced topics in quantitative finance and risk management, with a particular focus on current developments. Topics may include robust and Bayesian portfolio optimization, limits to arbitrage, derivatives pricing under model uncertainty, credit risk models, and models of systematic risk.', 'ACC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ACC 781', 'Introduction to Research 1', 0.5, 'SEM', 'ACC 781 provides an introduction to academic research in Accounting and Finance.  It covers elements of scientific inference, experimental and quasi-experimental design, and various threats to valid inference.', 'ACC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ACC 784', 'Selected Topics in Research Methodology', 0.5, 'SEM', 'None', 'ACC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ACTSC 221', 'Introductory Financial Mathematics (Non-Specialist Level)', 0.5, 'LEC', 'The theory of rates of interest and discount; annuities and sinking funds with practical applications to mortgage and bond questions. Yield rates. [Offered: F,W,S]', 'ACTSC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ACTSC 231', 'Introductory Financial Mathematics', 0.5, 'LEC, TST, TUT', 'The theory of rates of interest and discount including the theoretical continuous case of forces of interest and discount. Annuities and sinking funds, including the continuous case. Practical and theoretical applications primarily to mortgages and bonds. Yield rates. [Offered: F,W,S]', 'ACTSC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ACTSC 232', 'Life Contingencies 1', 0.5, 'LEC, TST, TUT', 'The future lifetime random variable: probability and survival functions; force of mortality; complete and curtate expectation of life; Makeham and Gompertz mortality laws. Life tables: characteristics of population and insurance life tables; selection; fractional age assumptions. Life insurance payments and annuity payments: present value random variables; expected present values; higher moments; actuarial notation. Annual, 1/mthly and continuous cases. Relationships between insurance and annuity functions. Premiums: expense loadings. Present value of future loss random variables and distribution, net and gross cases. Equivalence principle. Portfolio percentile principle. Extra risks.', 'ACTSC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ACTSC 291', 'Corporate Finance 1', 0.5, 'LEC, TST, TUT', 'This is the first in a two-course sequence that deals with corporate financial decision-making. Topics may include time value of money, capital budgeting, cost of capital, security issuance, capital structure, payout policy and dividends, short term finance, and risk management. Where suitable, topics are treated from a mathematical and quantitative perspective.', 'ACTSC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ACTSC 331', 'Life Contingencies 2', 0.5, 'LEC, TUT', 'Policy Values: Annual, 1/mthly and continuous cases. Thiele''s equation. Policy alterations. Modified policies. Multiple State Models: Applications in life contingencies; assumptions; Kolmogorov equations; premiums, policy values, multiple decrement models. Joint Life Models: Valuation of insurance benefits on joint lives, dependent and independent cases.', 'ACTSC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ACTSC 371', 'Introduction to Investments', 0.5, 'LEC, TST, TUT', 'Introduction to capital markets. Analysis of equity and fixed income investments. Introduction to derivative securities including futures, forwards, swaps, and options. [Offered: F,W,S]', 'ACTSC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ACTSC 372', 'Corporate Finance', 0.5, 'LEC, TUT', 'Capital budgeting. Real options. Investment decision using Markowitz and utility theory. Capital asset pricing model. Arbitrage pricing theory. Market efficiency. Capital structure and dividend policy. Advanced topics. [Offered: F,W,S]', 'ACTSC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ACTSC 391', 'Corporate Finance 2', 0.5, 'LEC, TST, TUT', 'This course is a continuation of AFM 272/ACTSC 291. Topics to be explored are covered under the listing for AFM 272/ACTSC 291.', 'ACTSC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ACTSC 431', 'Loss Models 1', 0.5, 'LEC, TUT', 'Models for loss severity: parametric models, effect of policy modifications, tail behaviour. Models for loss frequency: (a, b, 0), (a, b, 1), mixed Poisson models; compound Poisson models, Aggregate claims models: moments and moment generating function: recursion. Classical ruin theory. [Offered: F,S]', 'ACTSC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ACTSC 432', 'Loss Models 2', 0.5, 'LEC, TUT', 'Credibility theory: limited fluctuation; Bayesian; Buhlmann; Buhlmann-Straub; empirical Bayes parameter estimation; statistical inference for loss models; maximum likelihood estimation; effect of policy modifications; model selection. [Offered: F,S]', 'ACTSC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ACTSC 433', 'Analysis of Survival Data', 0.5, 'LEC', 'The mathematics of survival models, some examples of parametric survival models. Tabular survival models, estimates from complete and incomplete data samples. Parametric survival models, determining the optimal parameters. Maximum likelihood estimators, derivation, and properties. Product limit estimators, Kaplan-Meier, and Nelson-Aalen. Practical aspects. [Offered: W]', 'ACTSC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ACTSC 445', 'Quantitative Enterprise Risk Management', 0.5, 'LEC, TUT', 'This course introduces enterprise risk management, with a focus on quantitative analysis and economic capital. Risk classification is first discussed with an emphasis on the types of risk most suited to quantitative methods. Risk measures, such as Value-at-Risk (VaR) and Conditional Tail Expectation (CTE or TVaR), are then introduced and their use by firms and regulators to determine risk capital requirements is further highlighted. Different approaches are considered for developing loss distributions, including frequency/severity analysis and extreme value theory. Copulas and economic scenario generators are used to aggregate dependent risks. Different strategies for mitigating or transferring risk are reviewed. Additional topics that may be covered include credit risk, capital allocation, and regulation of financial institutions. [Offered: F,S]', 'ACTSC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ACTSC 446', 'Mathematics of Financial Markets', 0.5, 'LEC, TUT', 'This course covers mathematical techniques for no-arbitrage pricing and hedging financial derivatives. Topics to be covered can be classified into three broad areas: derivatives markets (options; forwards and futures; other derivatives; put-call parity), discrete-time financial models (binomial models; general multi-period models; Fundamental Theorems of Asset Pricing; risk-neutral probability), and continuous-time financial models (basic stochastic calculus and It√¥''s lemma; Black-Scholes model; interest rate models and bond pricing). [Offered: F,W]', 'ACTSC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ACTSC 453', 'Basic Pension Mathematics', 0.5, 'LEC', 'Theory and practice of pension plan funding. Assumptions, basic actuarial functions, and population theory applied to private pensions. Concepts of normal costs, supplemental liability, unfunded liability arising from individual accrued benefit, and projected benefit cost methods.', 'ACTSC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ACTSC 455', 'Advanced Life Insurance Practice', 0.5, 'LEC', 'Cash flow projection methods for pricing, reserving and profit testing; deterministic, stochastic and stress testing; pricing and risk management of embedded options in insurance products; mortality and maturity guarantees for equity-linked life insurance.', 'ACTSC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ACTSC 462', 'Introduction to Property and Casualty Pricing', 0.5, 'LEC', 'An introduction to property/casualty rate making. The economics of insurance. The ratemaking process. Individual risk rating. Reinsurance, expense issues. Pricing for deductibles and increased limits.', 'ACTSC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ACTSC 463', 'Introduction to Property and Casualty Loss Reserving', 0.5, 'LEC', 'An introduction to property/casualty loss reserving techniques. Claim payment process. Chain-ladder methods. Stochastic models.', 'ACTSC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ACTSC 468', 'Readings in Actuarial Science 1', 0.5, 'RDG', 'Reading course as announced by the department.', 'ACTSC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ACTSC 469', 'Readings in Actuarial Science 2', 0.5, 'RDG', 'Reading course as announced by the department.', 'ACTSC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ACTSC 471', 'Advanced Corporate Finance', 0.5, 'LEC, TST, TUT', 'This course covers various advanced topics in corporate finance, with emphasis on theories of corporate incentives and asymmetric information. Illustrative applications using cases are provided.', 'ACTSC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ACTSC 611', 'Financial Mathematics I', 0.5, 'LEC, TUT', 'Time value of money; simple and compound interest and discount; real returns; equations of value; loan schedules; valuation of fixed coupon bonds; valuation of real return bonds; term structure of interest rates; no arbitrage pricing; valuation of forward contracts; binomial option valuation.  Duration and Immunization', 'ACTSC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ACTSC 612', 'Life Insurance Mathematics I', 0.5, 'LEC, TUT', 'Models for future lifetime; insurance and annuity functions; life tables and their use; future loss random variable for a contract; calculations of premiums and reserves; standard international actuarial notation.', 'ACTSC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ACTSC 613', 'Statistics for Actuarial Science', 0.5, 'LEC, TUT', 'Discrete and continuous random variables; generating functions; dependence; maximum likelihood estimation, functions of random variables; confidence intervals, hypothesis tests; condition expectation; compound distributions.', 'ACTSC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ACTSC 614', 'Corporate Finance', 0.5, 'LEC, TUT', 'Agency theory; investment decisions; long-term financing and cost of capital; principles of taxation; financial reporting; assessment of capital investment projects.', 'ACTSC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ACTSC 615', 'Economics', 0.5, 'LEC, TUT', 'Micro: Supply and demand; utility theory and risk aversion; production choices; competition; Macro: Fiscal and monetary policy; exchange rates; factors affecting inflation, unemployment, exchange rates and economic growth; introductory game theory; introduction to insurance economics.', 'ACTSC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ACTSC 621', 'Financial Mathematics II', 0.5, 'LEC, TUT', 'Mean-Variance portfolio theory; Capital-Asset Pricing Method, Arbitrage Pricing Theory, Efficient Markets Hypotheses; Capital structure and dividend policy.', 'ACTSC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ACTSC 622', 'Life Insurance Mathematics II', 0.5, 'LEC, TUT', 'Multiple state models; premiums and reserves for stat dependent policies, including joint life and last survivor benefits; cashflow projection methods; deterministic, stochastic and stress testing; embedded options; introduction to pension valuation and funding.', 'ACTSC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ACTSC 623', 'Applied Statistics', 0.5, 'LEC, TUT', 'Generalized linear models: multiple linear regression and normal linear model; exponential family; link functions; linear predicators; estimation; testing.  Time series: Univariate ARIMA; multivariate AR; applications to economic series.', 'ACTSC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ACTSC 624', 'Stochastic Processes for Actuarial Science', 0.5, 'LEC, TUT', 'Counting processes; Markov processes and Kolmogorov equations; Brownian motion and geometric Brownian motion; Ito''s lemma Monte Carlo simulation.', 'ACTSC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ACTSC 625', 'Casualty and Health Insurance Mathematics', 0.5, 'LEC, TUT', 'Frequency and severity models; compound distributions, calculation of moments and probabilities using recursion; Bayesian estimation and credibility; claims reserving for non-life insurance using run-off triangle methods, introductory ruin theory.', 'ACTSC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ACTSC 631', 'Financial Mathematics III', 0.5, 'LEC, TUT', 'Risk measures, Binomial and lattice models for option pricing, Black-Scholes option pricing; term structure models.  Credit risk; types of models and types of derivatives.', 'ACTSC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ACTSC 632', 'Life Insurance Mathematics III', 0.5, 'LEC, TUT', 'Estimation for lifetime models; estimation for multiple state modes.  Graduation.  Mortality projection using the Lee Carter model. MLE for Markov multiple state models.', 'ACTSC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ACTSC 633', 'Actuarial Risk Management', 0.5, 'LEC, TUT', 'The Actuarial Profession.  The Actuarial Control Cycle  Impact of Regulation. Consumer needs. Assessing risk. Modeling. Monitoring Experience. Pricing and reserving in life and non-life insurance.', 'ACTSC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ACTSC 634', 'Quantitative Risk Management', 0.5, 'LEC, TUT', 'Enterprise Risk management. Pricing and valuation. Economic and regulatory capital. Solvency. Investment management.', 'ACTSC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ACTSC 635', 'Profession Communications in Actuarial Science', 0.5, 'LEC, TUT', 'Elements of writing. Written project on an advanced topic, with a communications focus. Presentations: preparation and delivery.', 'ACTSC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ACTSC 690', 'Literature & Research Studies', 0.5, 'RDG', 'None', 'ACTSC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ACTSC 831', 'Loss Models 1', 0.5, 'LEC, TUT', 'Models for loss severity: parametric models, effect of policy modifications; tail behabiour.  Models for loss frequency: (a,b,0), (a,b,1), mixed Poisson models; compound Poisson models.  Aggregate claims models: moments and moment generating function: recursion. Classical ruin theory.', 'ACTSC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ACTSC 832', 'Loss Models 2', 0.5, 'LEC', 'Credibility theory: limited fluctuation; Bayesian; Buhlmann; Buhlmann-Straub; empirical Bayes parameter estimation statistical inference for loss models; maximum likelihood estimation; effect of policy modifications; model selection.', 'ACTSC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ACTSC 833', 'Analysis of Mortality Data', 0.5, 'LEC', 'The Mathematics of Survival Models, some examples of parametric survival models. Tabular survival models, estimates from complete and incomplete data samples. Parametric survival models, determining the optimal parameters. Maximum likelihood estimators, derivation and properties. Product limit estimators, Kaplan-Meier and Nelson Aalen. Practical aspects.', 'ACTSC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ACTSC 845', 'Quantitative Enterprise Risk Management', 0.5, 'LEC, TUT', 'This course introduces enterprise risk management, with a focus on quantitative analysis and economic capital. Risk classification is first discussed with an emphasis on the types of risk most suited to quantitative methods. Risk measures, such as Value-at-Risk (VaR) and Conditional Tail Expectation (CTE or TVaR), are then introduced, and their use by firms and regulators to determine risk capital requirements is further highlighted. Different approaches are considered for developing loss distributions, including frequency/severity analysis and extreme value theory. Copulas and economic scenario generators are used to aggregate dependent risks.  Different strategies for mitigating or transferring risk are reviewed. Additional topics that may be covered include credit risk, capital allocation and regulation of financial institutions.', 'ACTSC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ACTSC 846', 'Mathematics of Financial Markets', 0.5, 'LEC, TUT', 'This course covers mathematical techniques for no-arbitrage pricing and hedging financial derivatives. Topics to be covered can be classified into three broad ares: derivatives markets (options; forwards and futures; other derivatives; put-call parity), discrete-time financial models (binomial models; general multi-period models; fundamental theorems of asset pricing; risk-neutral probability), and continuous-time financial models (basic stochastic calculus and Ito''s lemma; Black-Scholes model; interest rate models and bond pricing).', 'ACTSC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ACTSC 853', 'Basic Pension Mathematics', 0.5, 'LEC', 'Theory and practice of pension plan funding. Assumptions, basic actuarial functions and population theory applied to private pensions. Concepts of normal costs, supplemental liability, unfunded liability arising from individual accrued benefit and projected benefit cost methods.', 'ACTSC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ACTSC 855', 'Advanced Life Insurance Practice', 0.5, 'LEC', 'Cash flow projection methods for pricing, reserving and profit testing, deterministic, stochastic and stress testing; pricing and risk management of embedded options in insurance products; mortality and maturity guarantees for equity-linked life insurance.', 'ACTSC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ACTSC 862', 'Introduction to Property and Casualty Pricing', 0.5, 'LEC', 'An introduction ot property/casualty rate making.  The economics of insurance.  The ratemaking process.  Individual risk rating. Reinsuance, expense issues.  Pricing for dedutibles and increased limits.', 'ACTSC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ACTSC 863', 'Introduction fo Property and Casuality Loss Reserving', 0.5, 'LEC', 'An introduction to property/casualty loss reserving techniques.  Claim payment process. Chain-ladder methods, Stochastic models.', 'ACTSC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ACTSC 936', 'Longitudinal Data Analysis', 0.5, 'LEC', 'This course is designed to teach students the appropriate techniques for analyzing data that is collected over time.  This data could arise from biomedical, population public health studies as well as finance and actuarial science applications.  The course will teach how to recognize the added complexity of longitudinal data versus the univariate response data which is typically seen in introductory and generalized linear model courses.  The course emphasizes the importance of the covariance structure for longitudinal responses.  The students will study the difference between subject-specific and population-averaged models and how to recognize problems where one or the other approach might be more appropriate.  They will be expected to use statistical software in applications in order to analyze longitudinal data.', 'ACTSC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ACTSC 961', 'Mathematical Methods of Loss Reserving', 0.5, 'LEC', 'Macro methods of runoff analysis: chain-ladder, least squares, separation, payment per claim incurred. Stochastic methods: Reid''s method, see-saw, payment per unit of risk, autoregressive models, Kalman filter.', 'ACTSC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ACTSC 963', 'Insurance Surplus Mathematics', 0.5, 'LEC', 'The analysis of the development over time of the surplus on a portfolio of insurance business is considered.  The classical Poisson and Sparre Andersen risk models are studied.  Unified treatment of moments and distributions of quantities related to the event of ruin is done through a discounted penalty function approach.  Random variables of interest include the time of ruin itself, the deficit immediately after ruin occurs, and the surplus immediately prior to ruin.  Defective renewal equations and Laplace transforms are utilized extensively.  Prerequisites are familiarity with aggregate loss models at the level of ActSc 431/831 or equivalent.', 'ACTSC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ACTSC 964', 'Topics in Quantitative Risk Management', 0.5, 'LEC', 'Fundamental concepts in quantitative risk management. Topics typically include: risk measures, extreme value theory, multivariate distributions and copulas. This course has a focus on mathematical and statistical techniques. Other topics may be covered at the discretion of the instructor.', 'ACTSC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ACTSC 965', 'Extreme Value Theory', 0.5, 'LEC', 'Ruin Theory for heavy-tailed distributions. Fluctuation of maxima and upper order statistics. Extreme value distributions: Weibull, Frechet, Gumbel and generalized Pareto. Mean excess function. Statistical methods for external events. Estimation of parameters of extreme value and excess distributions. Applications in finance and insurance.', 'ACTSC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ACTSC 966', 'Aggregate Claims Models', 0.5, 'LEC', 'Mixed Poisson and nonhomogeneous birth processes for claim counts; analytic, recursive, asymptotic and approximate evaluation of compound distributions for aggregate claims; reliability concepts and analysis of stop-loss moments; applications for inflation, incurred but not reported claims, and infinite server queues.  Prerequisites are familiarity with aggregate loss models at the level of ActSc 431/831 or equivalent.', 'ACTSC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ACTSC 970', 'Finance 1', 0.5, 'LEC', 'The course introduces options and other derivative securities in different asset classes. The main focus is on methods of pricing in a multi-period setting, but continuous-time models are also discussed. Topics may include no-arbitrage pricing theory, the fundamental theory of asset pricing, complete and incomplete markets,and pricing of complex financial instruments.', 'ACTSC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ACTSC 971', 'Finance 2', 0.5, 'LEC', 'The course discusses methods and tools for modeling of financial derivatives in the continuous-time setting. Both theory and practical applications are discussed. The first part covers methods of pricing and hedging of derivatives under different assumptions about the dynamics of the underlying economic factors. Topics normally include currency derivatives, American and exotic options, futures contracts, stochastic volatility models and mean-variance hedging. The second part deals with modeling and pricing of interest-rate products. Topics may include short interest rate models, the Heath-Jarrow-Morton Framework, and Libor and swap market models.', 'ACTSC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ACTSC 972', 'Finance 3', 0.5, 'LEC', 'The course will cover selected and advanced topics in quantitative finance and risk management, with a particular focus on current developments. Topics may include robust and Bayesian portfolio optimization, limits to arbitrage, derivatives pricing under model uncertainty, credit risk models, and models of systematic risk.', 'ACTSC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ACTSC 973', 'Portfolio Optimization', 0.5, 'LEC', 'Basic optimization: quadratic minimization subject to leanear equality constraints. Effecient portfolios: the efficient frontier, the capital market line, Sharpe ratios and threshold returns. Practical portfolio optimization: short sales restrictions target portfolios, transactions costs. Quadratic programming theory. Special purpose quadratic programming algorithms for portfolio optimization: today''s large investment firms expect to solve problems with at least 1000 assets, transactions costs and various side constraints in just a few minutes of computation time. This requires very specialized QP algorithms. An overview of such algorithms will be presented with computational results from commercial problems. The efficient frontier, the capital market line, Sharpe ratios and threshold returns in practice.', 'ACTSC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ACTSC 974', 'Financial Econometrics', 0.5, 'LEC', 'The focus of this course is on the statistical modelling, estimation and inference and forecasting of nonlinear financial time series, with a special emphasis on volatility and correlation of asset prices and returns.  Topics to be covered normally include: review on distribution and dynamic behaviour of financial time series, univariate and multivariate GARCH processes, long-memory time-series processes, stochastic volatility models, modelling of extreme values, copulas, realized volatility and correlation modelling for ultra high frequency data and continuous time models.', 'ACTSC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ACTSC 980', 'Social Insurance', 0.5, 'LEC', 'Review of the history and present status of the major Canadian Social Insurance systems such as CPP, Medicare, UIC and OAS. Possible future developments. Costing problems and trends. Financing, past, present and future.', 'ACTSC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ACTSC 991', 'Topics in Actuarial Science', 0.5, 'LEC', 'None', 'ACTSC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ADMGT 601', 'Introduction to Financial and Managerial Accounting', 0.5, 'LEC', 'The course introduces students to the core elements of financial and managerial accounting.  Specifically, the course objectives are to 1) assist managers to become proficient at interpreting numbers in financial statements and assessing their usefulness for business decisions, and 2) assist managers to understand the vital role that internal accounting information, or management accounting, plays in making better planning, organizing, controlling, improvement and compensation decisions.  This course is only open to employees of the Abu Dhabi Water and Electricity Authority.', 'ADMGT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ACTSC 992', 'Seminar in Actuarial Science', 0.5, 'SEM', 'None', 'ACTSC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AFM 101', 'Introduction to Financial Accounting', 0.5, 'LEC, TST, TUT', 'This course is an introduction to financial accounting. The preparation and use of financial statements is examined. The accounting cycle and assets and liabilities reporting, is discussed.', 'AFM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AFM 102', 'Introduction to Managerial Accounting', 0.5, 'LEC, TST, TUT', 'This course is an introduction to the preparation and use of accounting information for management decision-making and reporting. Cost behaviour, cost accumulation systems, and short and long-term decision models are discussed.', 'AFM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AFM 121', 'Introduction to Global Financial Markets', 0.5, 'LEC, TST, TUT', 'This course describes the role of finance in the modern global economy and introduces some basic principles of financial decision-making.', 'AFM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AFM 123', 'Accounting Information for Managers', 0.5, 'LEC, TST, TUT', 'This course is designed for non-accountants who will use accounting information for planning, control, and decision-making.', 'AFM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AFM 131', 'Introduction to Business in North America', 0.5, 'LEC, TST', 'The functional areas of business: finance, personnel administration, production, marketing, and accounting are examined within differing organizational structures. Coverage also includes study of the principles of effective management and the financial system as a source of corporate capital.', 'AFM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AFM 201', 'Introduction to Professional Practice', 0.5, 'LEC, TUT', 'This course discusses auditing and taxation concepts as they relate to professional accounting and management.', 'AFM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AFM 202', 'Introduction to Public Practice', 0.5, 'LEC, TST, TUT', 'This course focuses on the knowledge and skills that enhance experiential learning during a first co-op work term. Topic areas include professional ethics, assurance, taxation, and leveraging technology for assurance and taxation competencies.', 'AFM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AFM 203', 'Introduction to Decision Support', 0.5, 'LEC, TST, TUT', 'This course focuses on the knowledge and skills that enhance experiential learning during a first co-op work term while performing a decision support role within a public or private sector organization. Topic areas include professional ethics, performance analysis, corporate finance, and leveraging technology for performance analysis and corporate finance competencies.', 'AFM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AFM 204', 'Introduction to Applied Finance', 0.5, 'LEC, TST, TUT', 'This course focuses on the basic knowledge and skills required for employment in the finance area during the first co-op work term. The course covers topics such as an overview of the financial services industry, the role of treasury management within organizations, professional ethics, and problem-solving skills that are applicable to financial decisions.', 'AFM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AFM 211', 'Connections to Business Context', 0.5, 'LEC, SEM, TST, TUT', 'This course focuses on developing the qualities and transferable skills necessary for integration, continuous learning, and professional development. The course integrates an understanding of business, basic functional competencies, leadership, collaboration, and communication skills to address a range of business decisions.', 'AFM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AFM 231', 'Business Law', 0.5, 'LEC, TST', 'Particular attention is given to the law relating to contracts and business organizations. Other areas of study include sources of law, the judicial process, real and personal property, torts, agency, credit, and negotiable instruments.', 'AFM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AFM 241', 'Introduction to Business Information Technology', 0.5, 'LEC, SEM, TST', 'This course considers various aspects of information from a business and problem-solving perspective. It is intended to provide a basic foundation for understanding the potential benefits and problems in utilizing information technology to improve business performance, and an appreciation for a wide range of technology choices available, rather than a detailed understanding of any particular hardware or software technology.', 'AFM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AFM 271', 'Managerial Finance 1', 0.5, 'LEC, TST', 'This is the first in a two-course sequence that introduces the theory of financial decision-making within firms. Topics include capital budgeting, cost of capital, security issuance, capital structure, payout policy and dividends, short term finance, and risk management, among others.', 'AFM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AFM 272', 'Corporate Finance 1', 0.5, 'LEC, TST, TUT', 'This is the first in a two-course sequence that deals with corporate financial decision-making. Topics may include time value of money, capital budgeting, cost of capital, security issuance, capital structure, payout policy and dividends, short term finance, and risk management. Where suitable, topics are treated from a mathematical and quantitative perspective.', 'AFM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AFM 432', 'Legal Environment and Corporate Governance', 0.5, 'LEC, TST', 'This course will examine legal issues specific to the operation of corporations, addressing topics such as criminal and/or civil legal obligations relating to the regulation of securities, corporate mergers and acquisitions, secured transactions in company financing, and corporate governance matters including internal corporate operations, and directors'' and executive officers'' legal obligations.', 'AFM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AFM 273', 'Managerial Finance 1', 0.5, 'LEC, TST', 'This is the first in a two-course sequence that introduces the theory of financial decision-making within firms. Topics include capital budgeting, cost of capital, security issuance, capital structure, payout policy and dividends, short term finance, and risk management.', 'AFM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AFM 274', 'Managerial Finance 2', 0.5, 'LEC, TST', 'This course is a continuation of AFM 273. Topics to be explored are covered under the listing for AFM 273.', 'AFM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AFM 280', 'Introduction to Organizational Behaviour', 0.5, 'LEC, TST, TUT', 'An introduction to the concepts concerning the behaviour of individuals and groups in organizations. Topics may include motivation, influence, communication, diversity, goal-setting and incentive compensation, culture and ethical systems, and decision-making.', 'AFM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AFM 291', 'Intermediate Financial Accounting 1', 0.5, 'LEC, TST', 'A first course in intermediate accounting dealing with the theory and practice of financial statement preparation and reporting. The emphasis will be on asset valuation and the related impact on income measurement.', 'AFM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AFM 311', 'Connections to Ethical Context', 0.5, 'LEC, SEM, TST, TUT', 'This course focuses on developing the qualities and transferable skills necessary for integration, continuous learning, and professional development. The course is oriented around the ethical and moral issues faced by accounting and finance professionals.', 'AFM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AFM 321', 'Personal Financial Planning', 0.5, 'LEC, TST', 'This course covers financial planning for individual investors, with particular emphasis on taxation and other institutional aspects governing decisions such as retirement savings, education savings, and estate planning.', 'AFM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AFM 322', 'Derivative Securities', 0.5, 'LEC, TST', 'Introduction to valuation and applications of financial derivatives such as options, futures, forwards, and swaps.', 'AFM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AFM 323', 'Quantitative Foundations for Finance', 0.5, 'LEC, TST', 'This course introduces analytical and statistical methods commonly used in finance, with applications to investment management and corporate finance.', 'AFM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AFM 328', 'Investment Management - Junior Analyst', 0.25, 'PRJ', 'This course provides students hands-on training in equity valuation and enables students to follow one industry sector. As a junior analyst, a student will understand why funds management is broken into different sectors, monitor existing equity holdings in a particular sector, make new equity selections in the sector, prepare equity research reports, and present trading recommendations to an investment team in a student-run investment portfolio.', 'AFM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AFM 329', 'Investment Management - Senior Analyst', 0.25, 'PRJ', 'This course provides students hands-on training in equity valuation and enables students to follow more than one industry sector. As a senior analyst, a student will monitor existing equity holdings in different sectors, make new equity selections in the sectors, prepare equity research reports, and present trading recommendations to an investment team in a student-run investment portfolio.', 'AFM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AFM 331', 'Business Strategy', 0.5, 'LEC, TST, TUT', 'This course focuses on strategic management of the total enterprise. Managers contribute to the organization through their analytical and leadership capabilities as well as their technical expertise. The course provides a framework for developing and implementing strategy that fits the firm''s environment, managerial values, and organization.', 'AFM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AFM 332', 'Accounting, Assurance, and the Law', 0.5, 'LEC, TST, TUT', 'This course examines the interrelationship between legal and accounting environments. Students will be introduced to concepts of corporate governance and malfeasance and will examine a range of issues including the relationship between accounting and internal control, the roles of internal and external audits, and the regulatory environment of business including the obligations of boards, officers, managers, and shareholders.', 'AFM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AFM 333', 'International Business', 0.5, 'LEC, TST', 'This course examines the opportunities, risks, and challenges faced by businesses in international markets, as well as the preparation required to operate them.', 'AFM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AFM 341', 'Accounting Information Systems', 0.5, 'LEC, TST', 'Examines the planning, requirements analysis, acquisition, and evaluation of information systems, with an emphasis on accounting information systems. Introduces information systems assurance concepts, and considers the role of information technology in the improvement of business performance.', 'AFM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AFM 351', 'Audit Strategy', 0.5, 'LEC, TST, TUT', 'An examination of elements of audit strategy and their interrelationships, including financial assertions, types and sources of audit assurance, and evidence-gathering procedures within a framework of professional judgment.', 'AFM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AFM 352', 'Comprehensive/Operational Auditing', 0.5, 'LEC, TST', 'Examination of the value for money audit concept in the private and public sectors. This approach goes beyond the scope of the traditional financial audit and looks at all facets of the organization, including human resource management.', 'AFM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AFM 361', 'Taxation 1', 0.5, 'LEC, TST', 'A continuation of AFM 201 with application emphasis on business income, capital gains and calculation of corporate tax fact and problem situations.', 'AFM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AFM 362', 'Taxation 1 - Foundations', 0.5, 'LEC, TST, TUT', 'This course is an introduction to the foundational concepts in the taxation of individuals and corporations.', 'AFM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AFM 363', 'Taxation 2 - Integration', 0.5, 'LEC, TST, TUT', 'This course builds on the foundational concepts established in AFM 362 by exploring the taxation of individuals and corporations in more depth.', 'AFM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AFM 371', 'Managerial Finance 2', 0.5, 'LEC, TST', 'This course is a continuation of AFM 273. Topics to be explored are covered under the listing for AFM 273.', 'AFM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AFM 372', 'Corporate Finance 2', 0.5, 'LEC, TST, TUT', 'This course is a continuation of AFM 272/ACTSC 291. Topics to be explored are covered under the listing for AFM 272/ACTSC 291.', 'AFM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AFM 373', 'Cases and Applications in Corporate Finance', 0.5, 'LEC, TST', 'This course builds on the theory of financial management using cases to illustrate a variety of corporate financial decisions.', 'AFM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AFM 382', 'Performance Measurement and Organization Control', 0.5, 'LEC, TST', 'This course will trace the evolution of the role of performance measurement systems in supporting areas of organization control. Topics will include the role of both financial and nonfinancial performance measures in: the DuPont method of control, the Harvard model of control, internal control, contemporary approaches to governance, and strategic management systems. After completing this course students will be able to evaluate the nature and suitability of a proposed performance measurement system given its design and purpose.', 'AFM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AFM 391', 'Intermediate Financial Accounting 2', 0.5, 'LEC, TST', 'This is an intermediate financial accounting course that deals with problems related to the measurement of liabilities, measurement of income, and the reporting and measuring of corporate equities.', 'AFM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AFM 401', 'Accounting Theory', 0.5, 'LEC, TST, TUT', 'A review of accounting theory as a background for applying underlying concepts to current accounting problems. Emphasis is on current literature, with a major term paper required.', 'AFM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AFM 411', 'Connections Across Competencies for Accounting Professionals', 0.5, 'LEC, TST, TUT', 'This course focuses on developing the qualities and transferable skills necessary for integration, continuous learning, and professional development. The course adds the dimension of integration across the functional competencies required by students pursuing a professional accounting designation.', 'AFM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AFM 412', 'Connections Across Competencies for Finance Professionals', 0.5, 'LEC, TST, TUT', 'This course focuses on developing the qualities and transferable skills necessary for integration, continuous learning, and professional development. The course adds the dimension of integration across the functional competencies required by students pursuing a career as a finance professional.', 'AFM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AFM 415', 'Special Topics', 0.5, 'LEC, SEM, TST', 'A course offered from time to time on a significant accounting and financial management issue.', 'AFM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AFM 416', 'Special Topics in Finance', 0.5, 'LEC, SEM, TST', 'A course offered from time to time on a significant finance issue.', 'AFM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AFM 417', 'Special Topics in Accounting', 0.5, 'LEC, SEM, TST', 'A course offered from time to time on a significant accounting issue.', 'AFM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AFM 418', 'Special Topics in Finance or Accounting', 0.25, 'PRJ, SEM', 'A course offered from time to time in relation to a significant school-related accounting or finance project or activity.', 'AFM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AFM 422', 'Management of Financial Institutions', 0.5, 'LEC, TUT', 'This course studies the measurement and control of market, credit, and operational risks faced by financial institutions, with particular emphasis on the international regulatory framework.', 'AFM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AFM 423', 'Topics in Financial Econometrics', 0.5, 'LEC', 'This course introduces the use of advanced econometric/statistical methods in studying financial market data, and in quantitatively assessing risks associated with financial investments. The methods presented in this course are tailored to address specific issues of interest in finance, such as the quantification of the risk-return tradeoff, the modelling of time-varying stock/bond market volatility and, possibly, also the statistical analysis of financial derivatives such as options.', 'AFM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AFM 424', 'Equity Investments', 0.5, 'LEC, TST', 'This course addresses principles of equity investments, including risk and return relationships, fundamental analysis of equities based on macroeconomic, industry and company-specific factors, financial statement analysis, and technical analysis. Portfolio allocation, performance measurement, and ethical and professional standards in the investment profession are also covered.', 'AFM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AFM 425', 'Fixed Income Securities', 0.5, 'LEC, TST', 'Introduction to various aspects of fixed income investments, including valuation, risk management, portfolio management, interest rate models, interest rate derivatives, and institutional features of bond markets.', 'AFM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AFM 428', 'Investment Management - Junior Portfolio Manager', 0.25, 'PRJ', 'This course provides students hands-on training in equity valuation and portfolio management. As a junior portfolio manager, a student will make allocations in two sectors, monitor the performance of the existing equity holdings in these two sectors, mentor two analysts, make equity trading decisions for these two sectors, and present portfolio performance reports to the investment team and the advisory board.', 'AFM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AFM 429', 'Investment Management - Senior Portfolio Manager', 0.25, 'PRJ', 'This course provides students hands-on training in equity valuation and portfolio management. As a senior portfolio manager, a student will make allocations in different industry sectors, monitor the performance of the existing equity holdings in these sectors, mentor analysts, make equity trading decisions for these sectors, and present portfolio performance reports to the investment team and the advisory board.', 'AFM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AFM 431', 'Professional Ethics for Financial Managers', 0.5, 'LEC, TST', 'The study of ethical and moral issues that arise in professional lives of accountants and financial managers.', 'AFM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AFM 433', 'Business Strategy', 0.5, 'LEC, TST, TUT', 'This course focuses on strategic management of the total enterprise. Managers contribute to the organization through their analytical and leadership capabilities as well as their technical expertise. The course provides a framework for developing and implementing strategy that fits the firm''s environment, managerial values, and organization.', 'AFM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AFM 434', 'Governance and Enterprise Risk Management for Global Organizations', 0.5, 'LEC, TST', 'This course is an introduction to governance and enterprise risk management challenges faced by global organizations. Topic areas include governance, organization and management systems, and enterprise risk management approaches and techniques.', 'AFM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AFM 442', 'E-business: Enterprise Systems', 0.5, 'LEC, TST', 'This course examines the role of integrated company-wide information systems in improving organizational performances. The course will focus on the selection, acquisition, and implementation of these systems, including consideration of business process alignment, change management, and development of business cases to support their acquisition. The role of enterprise systems in inter-organizational systems and e-commerce will also be considered. The course will make use of case studies as well as examine selected current enterprise software.', 'AFM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AFM 443', 'E-business: Introduction to Electronic Commerce', 0.5, 'LEC, TST', 'This course examines key topics in electronic commerce such as the structure of the Internet, basic e-commerce processes and technologies (website, catalogs, customer attraction, ordering processes, payment processes, and fulfilment processes), control issues (availability, security, integrity, and maintainability), business-to-consumer models, business-to-business models, business-to-employee models, e-business strategies, integration of e-commerce activities into other business operations, performance measurement, legal and regulatory issues, and assurance services.', 'AFM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AFM 451', 'Audit Strategy', 0.5, 'LEC, TST, TUT', 'An examination of elements of audit strategy and their interrelationships, including financial assertions, types and sources of audit assurance, and evidence-gathering procedures within a framework of professional judgment.', 'AFM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AFM 452', 'Comprehensive/Operational Auditing', 0.5, 'LEC, TST', 'Examination of the value for money audit concept in the private and public sectors. This approach goes beyond the scope of the traditional financial audit and looks at all facets of the organization, including human resource management.', 'AFM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AFM 461', 'Taxation 2', 0.5, 'LEC, TST', 'Integration of topics from AFM 201 and AFM 361 with an emphasis on basic planning with the use of corporate reorganizations, partnerships and trusts.', 'AFM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AFM 462', 'Taxation 3 - Tax Planning Topics', 0.5, 'LEC, TST, TUT', 'This course focuses on the more specialized topics in introductory taxation, with an emphasis on basic planning for owner-managers and the use of corporate reorganizations, partnerships, and trusts.', 'AFM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AFM 471', 'Cases and Applications in Corporate Finance', 0.5, 'LEC, TST', 'This course builds on the theory of financial management using cases to illustrate a variety of corporate financial decisions.', 'AFM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AFM 472', 'Equity Investments', 0.5, 'LEC, TST', 'This course addresses principles of equity investments, including risk and return relationships, fundamental analysis of equities based on macroeconomic, industry and company-specific factors, financial statement analysis, and technical analysis. Portfolio allocation, performance measurement, and ethical and professional standards in the investment profession are also covered.', 'AFM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AFM 473', 'Advanced Topics in Corporate Finance', 0.5, 'LEC, TST', 'Topics include items such as corporate governance, mergers and acquisitions, spinoffs and divestitures, security issuance, and capital budgeting.', 'AFM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AFM 474', 'Derivative Securities', 0.5, 'LEC, TST', 'Introduction to valuation and applications of financial derivatives such as options, futures, forwards, and swaps.', 'AFM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AFM 475', 'Fixed Income Securities', 0.5, 'LEC, TST', 'Introduction to various aspects of fixed income investments, including valuation, risk management, portfolio management, interest rate models, interest rate derivatives, and institutional features of bond markets.', 'AFM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AFM 476', 'Advanced Corporate Finance', 0.5, 'LEC, TST, TUT', 'This course covers various advanced topics in corporate finance, with emphasis on theories of corporate incentives and asymmetric information. Illustrative applications using cases are provided.', 'AFM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AFM 477', 'Mergers and Acquisitions', 0.5, 'LEC, TST', 'This course develops understanding of the strategic acquisition and divestiture process, particularly as it relates to corporations in Canada. The course focuses on integrating key analytical skills in the interpretation of financial statements and valuation methodologies in the context of mergers and acquisitions of existing firms, as well as spin-offs, restructurings, buyouts and divestitures of existing assets. The course concentrates on value creation and institutional, strategic, ethical, governance, and control issues.', 'AFM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AFM 478', 'International Financial Management', 0.5, 'LEC, TST', 'This course examines various aspects of corporate decision-making in a global firm, such as cross-border investments and financing, international risk management, multinational working capital management, and the impact of the international regulatory environment.', 'AFM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AFM 481', 'Cost Management Systems', 0.5, 'LEC, TST, TUT', 'Consideration of more complex topics in management planning and control. Emphasis is on traditional and contemporary cost accumulation systems and their application in modern day organizations. Cases, simulations, projects and presentations are the key instructional methods used to understand and integrate the course material. At the end of the course, students will have a solid understanding of how the correct choice of a costing model adds value to the organization.', 'AFM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AFM 482', 'Performance Measurement and Organization Control', 0.5, 'LEC, TST', 'This course will trace the evolution of the role of performance measurement systems in supporting areas of organization control. Topics will include the role of both financial and nonfinancial performance measures in: the DuPont method of control, the Harvard model of control, internal control, contemporary approaches to governance, and strategic management systems. After completing this course students will be able to evaluate the nature and suitability of a proposed performance measurement system given its design and purpose.', 'AFM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AFM 483', 'Applications of Analytics to Business', 0.5, 'LEC, TST', 'This course explores the metrics, quantitative analyses, and technologies used across a range of applications where an investigation of past business performance provides insights that drive business decisions.', 'AFM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AFM 484', 'Advanced Management Control Systems', 0.5, 'LEC, TST', 'This course provides an in-depth examination of operations management and related issues in performance evaluation and control for various business sectors (e.g., manufacturing, service, and IT intensive).', 'AFM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AFM 491', 'Advanced Financial Accounting', 0.5, 'LEC, TST, TUT', 'An advanced accounting course considering specific problems of accounting for the corporate entity, such as business combinations, intercorporate investments, consolidated financial statements, accounting for foreign operations and foreign currency transactions, and segment reporting.', 'AFM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AFM 492', 'Financial Statement Analysis', 0.5, 'LEC, TST', 'This course introduces fundamental tools of analysis and valuation that are widely used in a variety of financial careers.', 'AFM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AFM 501', 'Contemporary Issues in Assurance and Accounting', 0.5, 'LEC', 'The objective of this course is to examine assurance in a broad context. The course builds on students'' prior knowledge of assurance and examines the role of the assurer and assure judgement in such broader issues as assurance levels, special assurance problems such as controls in governance and other forms of assurance. The course integrates a number of accounting and assurance issues.', 'AFM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AFM 502', 'Control Systems in a Computer Environment', 0.5, 'LEC, TUT', 'This is an advanced course that deals with the risks introduced by computer technology and the general and application controls used to reduce such risks to a tolerable level. The course helps students develop insights into the implications of emerging information technologies and helps students build practical skills to review and evaluate controls in computer-based systems.', 'AFM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AFM 503', 'Issues and Problems in Accounting Practice', 0.5, 'LEC', 'This course introduces students to a number of accounting issues, principles, practices and problems that they are likely to encounter in professional accounting practice.', 'AFM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AFM 504', 'Issues and Problems in External Reporting', 0.5, 'LEC', 'This is a capstone course designed to utilize students'' understanding of financial accounting, cost and managerial accounting, finance, auditing, tax and accounting theory to interpret the current developments taking place in external reporting regulations in Canada.', 'AFM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AHS 150', 'Foundations of Human Anatomy and Physiology', 0.5, 'LEC', 'This course covers the anatomy and physiology of the human body systems, with an emphasis on the relationship between a body organ''s structure and its function. Topics include organization of the human body, basic chemistry, the cell, tissues, bones and muscles, the nervous system, the blood, cardiovascular system and the urinary system. It is designed for students who have little formal knowledge of the human body but who wish to learn about the human body, or train for a career in Therapeutic Recreation or other social and community services.', 'AHS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AMATH 261', 'Classical Mechanics and Special Relativity', 0.5, 'LEC', 'Newtonian dynamics of particles and systems of particles. Oscillations. Gravity and the central force problem. Lorentz transformations and relativistic dynamics. [Offered: W,S]', 'AMATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AHS 782', 'Selected Topics in Applied Health Sciences', 0.5, 'RDG', 'Doctoral students will complete an analysis of specific topics of interest in AHS under the (co)-supervison of faculty member(s) with the appropriate expertise from another department of faculty.  The form of study may involve a review of literature or the planning and execution of an independent study resulting in a paper for possible publications.', 'AHS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AMATH 231', 'Calculus 4', 0.5, 'LEC, TST, TUT', 'Vector integral calculus-line integrals, surface integrals and vector fields, Green''s theorem, the Divergence theorem, and Stokes'' theorem. Applications include conservation laws, fluid flow and electromagnetic fields. An introduction to Fourier analysis. Fourier series and the Fourier transform. Parseval''s formula. Frequency analysis of signals. Discrete and continuous spectra. [Offered: F,W,S]', 'AMATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AMATH 242', 'Introduction to Computational Mathematics', 0.5, 'LAB, LEC', 'A rigorous introduction to the field of computational mathematics. The focus is on the interplay between continuous models and their solution via discrete processes. Topics include pitfalls in computation, solution of linear systems, interpolation, discrete Fourier transforms, and numerical integration. Applications are used as motivation.', 'AMATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AMATH 250', 'Introduction to Differential Equations', 0.5, 'LEC, TST, TUT', 'Physical systems which lead to differential equations (examples include mechanical vibrations, population dynamics, and mixing processes). Dimensional analysis and dimensionless variables. Solving linear differential equations: first- and second-order scalar equations and first-order vector equations. Laplace transform methods of solving differential equations. [Offered: F,W,S]', 'AMATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AMATH 251', 'Introduction to Differential Equations (Advanced level)', 0.5, 'LEC, TUT', 'AMATH 251 is an advanced-level version of AMATH 250. This course offers a more theoretical treatment of differential equations and solution methods. In addition, emphasis will be placed on computational analysis of differential equations and on applications in science and engineering.', 'AMATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AMATH 271', 'Introduction to Theoretical Mechanics', 0.5, 'LEC', 'Newtonian dynamics, gravity and the two-body problem, introduction to Lagrangian mechanics, introduction to Hamiltonian mechanics, non-conservative forces, oscillations, introduction to special relativity [Offered: F]', 'AMATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AMATH 310', 'Environmental Informatics', 0.5, 'LEC, TUT', 'The objective of this class is to understand and use quantitative and analytical techniques founded in mathematics and the geosciences to describe, model and predict environmental phenomena on planet Earth. The course aims to teach the mathematical tools as well as the physics underlying such phenomena. An introduction to data analysis will also be provided. Topics include chaos theory, turbulence, ocean-atmosphere interactions (El Nino/Southern Oscillation), simple climate models, and atmosphere-land interactions. [Offered: F every other year]', 'AMATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AMATH 331', 'Applied Real Analysis', 0.5, 'LEC', 'Topology of Euclidean spaces, continuity, norms, completeness. Contraction mapping principle. Fourier series. Various applications, for example, to ordinary differential equations, optimization and numerical approximation.', 'AMATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AMATH 332', 'Applied Complex Analysis', 0.5, 'LEC, TST, TUT', 'Complex numbers, Cauchy-Riemann equations, analytic functions, conformal maps and applications to the solution of Laplace''s equation, contour integrals, Cauchy integral formula, Taylor and Laurent expansions, residue calculus and applications.', 'AMATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AMATH 342', 'Computational Methods for Differential Equations', 0.5, 'LAB, LEC', 'An introduction to numerical methods for ordinary and partial differential equations. Ordinary differential equations: multistep and Runge-Kutta methods; stability and convergence; systems and stiffness; boundary value problems. Partial differential equations: finite difference methods for elliptic, hyperbolic and parabolic equations; stability and convergence. The course focuses on introducing widely used methods and highlights applications in the natural sciences, the health sciences, engineering, and finance. [Offered: F,W]', 'AMATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AMATH 343', 'Discrete Models in Applied Mathematics', 0.5, 'LEC', 'Difference equations and discrete dynamical systems. Mathematical models are taken from ecology, biology, economics, and other fields. [Offered: F]', 'AMATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AMATH 350', 'Differential Equations for Business and Economics', 0.5, 'LEC, TST', 'First order ordinary differential equations. Applications to continuous compounding and the dynamics of supply and demand. Higher order linear ordinary differential equations. Systems of linear ordinary differential equations. Introduction to linear partial differential equations. The Fourier Transform and the diffusion equation. Discussion of the Black-Scholes partial differential equations, and solutions thereof. [Offered: F,W]', 'AMATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AMATH 351', 'Ordinary Differential Equations 2', 0.5, 'LEC, TUT', 'Second order linear differential equations with non-constant coefficients, Sturm comparison, oscillation and separation theorems, series solutions and special functions. Linear vector differential equations in Rn, an introduction to dynamical systems. Laplace transforms applied to linear vector differential equations, transfer functions, the convolution theorem. Perturbation methods for differential equations. Numerical methods for differential equations. Applications are discussed throughout. [Offered: F,S]', 'AMATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AMATH 353', 'Partial Differential Equations 1', 0.5, 'LEC, TUT', 'Second order linear partial differential equations - the diffusion equation, wave equation, and Laplace''s equation. Methods of solution - separation of variables and eigenfunction expansions, the Fourier transform. Physical interpretation of solutions in terms of diffusion, waves and steady states. First order non-linear partial differential equations and the method of characteristics. Applications are emphasized throughout. [Offered: W,S]', 'AMATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AMATH 361', 'Continuum Mechanics', 0.5, 'LEC', 'Stress and strain tensors; analysis of stress and strain. Lagrangian and Eulerian methods for describing flow. Equations of continuity, motion and energy, constitutive equations. Navier-Stokes equation. Basic equations of elasticity. Various applications. [Offered: W]', 'AMATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AMATH 373', 'Quantum Theory 1', 0.5, 'LEC', 'Critical experiments and old quantum theory. Basic concepts of quantum mechanics: observables, wavefunctions, Hamiltonians, and the Schroedinger equation. Uncertainty, correspondence, and superposition principles. Simple applications to finite and extended one-dimensional systems, harmonic oscillator, rigid rotor, and hydrogen atom. [Offered: W]', 'AMATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AMATH 382', 'Computational Modelling of Cellular Systems', 0.5, 'LEC, TUT', 'An introduction to dynamic mathematical modeling of cellular processes. The emphasis is on using computational tools to investigate differential equation-based models. A variety of cellular phenomena are discussed, including ion pumps, membrane potentials, intercellular communication, genetic networks, regulation of metabolic pathways, and signal transduction.', 'AMATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AMATH 391', 'From Fourier to Wavelets', 0.5, 'LEC', 'An introduction to contemporary mathematical concepts in signal analysis. Fourier series and Fourier transforms (FFT), the classical sampling theorem and the time-frequency uncertainty principle. Wavelets and multiresolution analysis. Applications include oversampling, denoising of audio, data compression, and singularity detection.', 'AMATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AMATH 442', 'Computational Methods for Partial Differential Equations', 0.5, 'LAB, LEC', 'This course studies several classes of methods for the numerical solution of partial differential equations in multiple dimensions on structured and unstructured grids. Finite volume methods for hyperbolic conservation laws: linear and nonlinear hyperbolic systems; stability; numerical conservation. Finite element methods for elliptic and parabolic equations: weak forms; existence of solutions; optimal convergence; higher-order methods. Examples from fluid and solid mechanics. Additional topics as time permits. [Offered: F]', 'AMATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AMATH 444', 'Applications of Computational Differential Equations', 0.5, 'LAB, LEC', 'This course will present two major applications of differential equations based modeling, and focus on the specific problems encountered in each application area. The areas may vary from year to year. Students will gain some understanding of the steps involved in carrying out a realistic numerical modelling exercise. Possible areas include: Fluid Dynamics, Finance, Control, Acoustics, Fate and Transport of Environmental Contaminants.', 'AMATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AMATH 451', 'Introduction to Dynamical Systems', 0.5, 'LEC', 'A unified view of linear and nonlinear systems of ordinary differential equations in Rn. Flow operators and their classification: contractions, expansions, hyperbolic flows. Stable and unstable manifolds. Phase-space analysis. Nonlinear systems, stability of equilibria and Lyapunov functions. The special case of flows in the plane, Poincare-Bendixson theorem and limit cycles. Applications to physical problems will be a motivating influence. [Offered: W]', 'AMATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AMATH 453', 'Partial Differential Equations 2', 0.5, 'LEC, TUT', 'A thorough discussion of the class of second order linear partial differential equations with constant coefficients, in two independent variables. Laplace''s equation, the wave equation and the heat equation in higher dimensions. Theoretical/qualitative aspects: well-posed problems, maximum principles for elliptic and parabolic equations, continuous dependence results, uniqueness results (including consideration of unbounded domains), domain of dependence for hyperbolic equations. Solution procedures: elliptic equations -- Green functions, conformal mapping; hyperbolic equations -- generalized d''Alembert solution, spherical means, method of descent; transform methods -- Fourier, multiple Fourier, Laplace, Hankel (for all three types of partial differential equations); Duhamel''s method for inhomogeneous hyperbolic and parabolic equations.', 'AMATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AMATH 455', 'Control Theory', 0.5, 'LEC', 'Feedback control with applications. System theory in both time and frequency domain, state-space computations, stability, system uncertainty, loopshaping, linear quadratic regulators, and estimation. [Offered: W]', 'AMATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AMATH 456', 'Calculus of Variations', 0.5, 'LEC', 'Concept of functional and its variations. The solution of problems using variational methods - the Euler-Lagrange equations. Applications include an introduction to Hamilton''s principle and optimal control. [Offered: F]', 'AMATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AMATH 463', 'Fluid Mechanics', 0.5, 'LEC, TUT', 'Incompressible, irrotational flow. Incompressible viscous flow. Introduction to wave motion and geophysical fluid mechanics. Elements of compressible flow. [Offered: F]', 'AMATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AMATH 473', 'Quantum Theory 2', 0.5, 'LEC', 'The Hilbert space of states, observables, and time evolution. Feynman path integral and Greens functions. Approximation methods. Co-ordinate transformations, angular momentum, and spin. The relation between symmetries and conservation laws. Density matrix, Ehrenfest theorem, and decoherence. Multiparticle quantum mechanics. Bell inequality and basics of quantum computing. [Offered: F]', 'AMATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AMATH 475', 'Introduction to General Relativity', 0.5, 'LEC', 'Tensor analysis. Curved space-time and the Einstein field equations. The Schwarzschild solution and applications. The Friedmann-Robertson-Walker cosmological models. [Offered: W]', 'AMATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AMATH 495', 'Reading Course', 0.5, 'LEC', 'Reading course as announced by the department.', 'AMATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AMATH 655', 'Control Theory', 0.5, 'LEC', 'Feedback control with applications. System theory in both time and frequency domain, state-space computations, stability, system uncertainty, loopshaping, linear quadratic regulators and estimation. (Heldwith AMATH 455)', 'AMATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AMATH 663', 'Fluid Mechanics', 0.5, 'LEC', 'Incompressible, irrotational flow. Incompressible viscous flow. Introduction to wave motion and geophysical fluid mechanics. Elements of compressible flow. (Heldwith AMATH 463)', 'AMATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AMATH 673', 'Quantum Mechanics', 0.5, 'LEC', 'Vector space formalism, Schrodinger and Heisenberg pictures, elements of second quantization.  Angular momentum, selection rules, symmetry and conservation laws. Approximation methods: variation principle, perturbation theory and WKB approximation. Identical particles, Pauli principle and simple applications to atomic, molecular, solid state, scattering and nuclear problems. (Heldwith AMATH 473)
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AMATH 675', 'General Relativity', 0.5, 'LEC', 'Flat space-time and Lorentz transformations. Relativistic mechanics. Maxwell''s equations. Curved space-time and the Einstein field equations. The Schwarzschild solution and some experimental tests of general relativity. The weak-field limit. Introduction to black holes and cosmology. (Heldwith AMATH 675)', 'AMATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AMATH 731', 'Applied Functional Analysis', 0.5, 'LEC', 'Basic concepts of functional analysis. Topics include: theory of linear operators, nonlinear operators and the Frechet derivative, fixed point theorems, approximate solution of operator equations, Hilbert space, spectral theory. Applications from various areas will be used to motivate and illustrate the theory. A previous undergraduate course in real analysis is strongly recommended.', 'AMATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AMATH 732', 'Asymptotic Analysis and Perturbation Theory', 0.5, 'LEC', 'Elements of asymptotic analysis. Techniques of perturbation theory such as Poincare-Lindstedt, matched asymptotic expansions and multiple scales. Applications to various areas form an essential aspect of the course. Previous courses in real analysis and differential equations at the undergraduate level are strongly recommended.', 'AMATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AMATH 740', 'Numerical Analysis', 0.5, 'LEC', 'Introduction to basic algorithms and techniques for numerical computing.  Error analysis, interpolation (including splines), numerical differentiation and integration, numerical linear algebra (including methods for linear systems, eigenvalue problems, and the singular value decomposition), root finding for nonlinear equations and systems, numerical ordinary differential equations, and approximation methods (including least squares, orthogonal polynomials, and Fourier transforms).', 'AMATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AMATH 741', 'Numerical Solution of Partial Differential Equations', 0.5, 'LEC', 'Discretization methods for partial differential equations, including finite difference, finite volume and finite element methods. Application to elliptic, hyperbolic and parabolic equations. Convergence and stability issues, properties of discrete equations, and treatment of non-linearities. Stiffness matrix assembly and use of sparse matric software. Students should have completed a course in numerical computation at the undergraduate level.', 'AMATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AMATH 751', 'Advanced Ordinary Differential Equations', 0.5, 'LEC', 'Qualitative theory of systems of ODEs. Topics include: existence/uniqueness of solutions, comparison principle, iterative techniques, stability and boundedness, Lyapunov method, periodic solutions, Floquet theory and Poincare maps, hyperbolicity, stable, unstable and center manifolds, structural stability and bifurcation. Applications from various areas will be used to motivate and illustrate the theory. A previous course in ordinary differential equations at the undergraduate level is strongly recommended.', 'AMATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AMATH 753', 'Advanced Partial Differential Equations', 0.5, 'LEC', 'The main themes are well-posedness of problems, Hilbert space methods, variational principles and integral equation methods. Topics include: first-order nonlinear partial differential equations, quasilinear hyperbolic systems, potential theory, eigenfunctions and eigenvalues, semi-groups, and power series solutions. Applications from various areas will be used to motivate and illustrate the theory. A previous course in partial differential equations at the undergraduate level is strongly recommended.', 'AMATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AMATH 777', 'Stochastic Processes in the Physical Sciences', 0.5, 'LEC', 'Basic concepts and classification of stochastic processes. Stochastic differentiation and integration, Markov processes, Chapman-Kolmogorov equation, Fokker-Planck equation, Master equations: mesoscopic vs. macroscopic description. Spectral representation of stationary processes. Correlation function theory. A previous course in probability theory at the undergraduate level is strongly recommended.', 'AMATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AMATH 851', 'Stability Theory and Applications', 0.5, 'LEC', 'Concepts of stability and boundedness, basic stability criteria, comparison methods, large scale systems, method of decomposition and aggregation, method of several Lyapunov functions, method of vector Lyapunov functions, method of higher derivatives. Stability problems in ecology, mechanics, neural networks and control systems. Students should have completed AM751 or equivalent.', 'AMATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AMATH 855', 'Advanced Systems Analysis and Control', 0.5, 'LEC', 'The main theme is the extension of control theory beyond systems modelled by linear ordinary differential equations. Topics include: advanced systems theory, control of nonlinear systems, control of partial differential equations and delay equations. Students should have completed an introductory undergraduate course in control theory.', 'AMATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AMATH 863', 'Hydrodynamic Stability and Turbulence', 0.5, 'LEC', 'Mathematical methods, stability of parellel flows for unstratified and stratified fluids, Rayleigh-Taylor instability, centrifugal instability, barotropic and baroclinic instabilities, the effects of viscosity and the Orr-Sommerfeld equation, transition to turbulence, averaged equations, closure problem, homogeneous isotropic turbulence, turbulent boundary layers, effects of stratification. Students should have completed an introductory undergraduate course in fluid mechanics.', 'AMATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AMATH 867', 'Dispersive and Nonlinear Waves', 0.5, 'LEC', 'Dispersive waves, propagation of dispersive waves in an inhomogeneous medium (WKB theory). Nonlinear resonant interactions. Solitons: completely integrable nonlinear wave equations (e.g., the KdV equation, nonlinear Schrodinger equations) and the inverse Scattering Transform. Applications to water waves and nonlinear optics. Introducation to weakly nonlocal solitary waves and beyond-all-orders asymptotics. Completion of an upper year course in partial differential equations is strongly recommended.', 'AMATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AMATH 871', 'Quantum Information Processing', 0.5, 'LEC', 'Review of basics of quantum information and computational complexity; Simple quantum algorithms; Quantum Fourier transform and Shor factoring algorithm: Amplitude amplification, Grover search algorithm and its optimality; Completely positive trace-preserving maps and Kraus representation; Non-locality and communication complexity; Physical realizations of quantum computation: requirements and examples; Quantum error-correction, including CSS codes, and elements of fault-tolerant computation; Quantum cryptography; Security proofs of quantum key distribution protocols; Quantum proof systems.  Familiarity with theoretical computer science or quantum mechanics will also be an asset, though most students will not be familiar with both.', 'AMATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AMATH 872', 'Introduction to Quantum Field Theory for Cosmology', 0.5, 'LEC', 'Introduction to scalar field theory and its canonical quantization in flat and curved spacetimes.  The flat space effects of Casimir and Unruh.  Quantum fluctuations of scalar fields and of the metric on curved space-times and application to inflationary cosmology.  Hawking radiation.', 'AMATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AMATH 873', 'Introduction to Quantum Field Theory', 0.5, 'LEC', 'Review of relativistic quantum mechanics and classical field theory.  Quantization of free quantum fields (the particle interpretation of field quanta).  Canonical quantization of interacting fields (Feynman rules).  Application of the formalism of interactin quantum fields to lowest-order quantum electrodynamic processes.  Radiative corrections and renormalization.', 'AMATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AMATH 874', 'Advanced techniques in General Relativity and Applications to Black Holes', 0.5, 'LEC', 'Review of elementary general relativity.  Timelike and null geodesic congruences.  Hypersurfaces and junction conditions.  Lagrangian and Hamiltonian formulations of general relativity.  Mass and angular momentum of a gravitating body.  The laws of black-hole mechanics.', 'AMATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AMATH 875', 'Introduction to General Relativity with Applications to Cosmology', 0.5, 'LEC', 'Introduction to the differential geometry of Lorentzian manifolds.  The priniciples of general relativity.  Causal structure and cosmological singularities.  Cosmological space-times with Killing vector fields.  Friedmann-Lemaitre cosmologies, scalar, vector and tensor perburbations in the linear and nonlinear regimes.  De Sitter space-times and inflationary models.', 'AMATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AMATH 876', 'Open Quantum Systems', 0.5, 'LEC', 'Review of the axioms of quantum theory and derivation of generalized axioms by considering states, transformations, and measurements in an extended Hilbert space.  Master equations and the Markov approximation.  Standard models of system-environment interactions and the phenomenology of decoherence.  Introduction to quantum control with applications in NMR, quantum optics, and quantum computing.', 'AMATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AMATH 881', 'Introduction to Mathematical Oncology', 0.5, 'LEC', 'Biological and clinical aspects of cancer.  Overview of recent mathematical models developed to examine different stages of cancer growth and therapeutic strategies, including ordinary and partial differential equation models, discrete models and models based on continuum mechanics.  Various analytical and numerical methods will be used in the analysis of these models.', 'AMATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AMATH 882', 'Mathematical Cell Biology', 0.5, 'LEC', 'Dynamic mathematical modelling of biological process at the cellular level.  Intracellular networks:  metabolism, signal transduction, and genetic regulatory networks.  Neural networks: from biophysical modelling of single neurons to the analysis of network behaviour.  Modelling will be carried out primarily through ordinary differential equations; analysis will involve application of dynamical systems tools and simulations.  Other relevant modelling frameworks (PDEs, delay equations, stochastic methods) will be touched on as time allows.', 'AMATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AMATH 900', 'Topics in Applied Mathematics', 0.5, 'LEC', 'None', 'AMATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BET 606', 'Entrepreneurial Applications of Digital Media', 0.5, 'LEC', 'Digital media is becoming a critical component of business success.  This is especially true for new, entrepreneurial ventures who, by their nature, seek to change the competitive landscape.  This course provides an overview of the strategic, operational and development issues associated with integrating digital media into business operations.  Topics include the impact of digital media on business models, and examination of associated strategic, marketing and regulatory issues. Practical aspects related to the management of implementation projects will also be examined.', 'BET');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ANTH 101', 'Human and Cultural Evolution', 0.5, 'LEC', 'This course surveys the evolution of the human species and outlines our cultural development from the earliest tool use through the beginnings of civilization. Lecture topics include evolutionary theory, human and primate fossil remains, and archaeological evidence concerning the origins and development of culture.', 'ANTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ANTH 102', 'Introduction to Social and Cultural Anthropology', 0.5, 'LEC', 'The dynamic nature of cultural systems is examined. Topics include language, technology, social organization, economics, politics, and religion. Data are drawn from a broad global and historical ethnographic base.', 'ANTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ANTH 201', 'Introduction to Archaeology', 0.5, 'LEC', 'An introduction to the working assumptions, analytic approaches, and integrative and descriptive methods of archaeological anthropology.', 'ANTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ANTH 202', 'Social and Cultural Anthropology', 0.5, 'LEC', 'This course introduces students to the history and key concepts of social and cultural anthropology through the examination of classic works as well as contemporary ethnographic texts and films. Topics include anthropological approaches to understanding cultural identity and difference, social structure, kinship, politics, and exchange.', 'ANTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ANTH 233', 'Inuit Cultures', 0.5, 'LEC', 'An examination of Inuit and Eskimo cultures of Alaska, Canada, and Greenland from their prehistoric origins to the present. Administrative systems imposed upon the Inuit and Eskimo will be analyzed and compared, as will the contemporary problems these communities face.', 'ANTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ANTH 260', 'Human Evolution', 0.5, 'LAB, LEC', 'Data, methods and theory in the study of the origin and evolution of humans are surveyed. Topics will include genetic theory, primate evolution, human fossils and modern human adaptation.', 'ANTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ANTH 261', 'Primate Behaviour', 0.5, 'LEC', 'An introduction to the behaviour of non-human primates and its relevance to human evolution. Topics will include social organization, ecology, conservation, and communication, as well as the history of primate studies.', 'ANTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ANTH 290', 'Visual Anthropology', 0.5, 'LEC', 'This course examines relations between culture and vision, the history of photography in anthropological research, and ethnographic film and its ethical implications. Topics include the roles of museums and representation, indigenous media, advocacy, and activism.', 'ANTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ANTH 300', 'Practicing Anthropology', 0.5, 'LEC', 'Thinking anthropologically means bringing observations and empirical findings into a dialogue with theoretical approaches. This course explores how anthropologists have developed the methods they use, considers how they interpret their findings through such lenses as functionalism and structuralism, and examines how they formulate critiques of fieldwork.', 'ANTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ANTH 302', 'Anthropology of Violence: Political Conflict and Change', 0.5, 'LEC, TUT', 'In this course, we explore anthropological perspectives on political violence and conflict. Emphasis will be placed on developing an anthropological understanding of structural, direct, and symbolic violence, memorialization and recovery, and the conduct of fieldwork in violent contexts.', 'ANTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ANTH 303', 'Anthropology of Digital Media', 0.5, 'LEC', 'This course examines diverse forms of digital media and associated practices anthropologically and inquires into the social orders and cultural assumptions that sustain them or are themselves sustained by them. Topics include social media, virtual worlds, and hacker activism.', 'ANTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ANTH 305', 'Paleopathology of Health and Disease', 0.5, 'LEC', 'This course introduces students to the identification and interpretation of skeletal evidence for disease, ancient and modern concepts of health and disease, and current clinical interpretations of skeletal pathologies. Topics include pathology in archaeological remains and the process of developing a differential diagnosis of skeletal lesions.', 'ANTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ANTH 320', 'Hunter-Gatherer Archaeology', 0.5, 'LEC', 'Detailed consideration of prehistoric cultural developments from earliest toolmaking to the transition to agriculture. An examination of the human mode of adaptation and the increasing complexity of cultural systems among prehistoric hunters and gatherers. Areas and periods of emphasis will vary from year to year.', 'ANTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ANTH 321', 'Archaeology of Complex Cultures', 0.5, 'LEC', 'Cultural development from the agricultural revolution to the rise of literacy. Special attention to the development of agriculture as a means of subsistence and to the rise of early civilization. Areas and periods of emphasis will vary from year to year.', 'ANTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ANTH 322', 'The Archaeology of the Great Lakes Area', 0.5, 'LEC', 'An in-depth study of the archaeological evidence for prehistoric cultures in the Great Lakes area from their arrival ca. 11,000 years ago to the coming of Europeans. Cultural ecology and cultural evolution will be stressed.', 'ANTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ANTH 330', 'Environmental Anthropology', 0.5, 'LEC', 'This course examines the relationships between environments and human societies, focusing on the nature/culture divide and social and economic organization. Topics may include politics and practice, the influence of globalization, and regional perspectives.', 'ANTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ANTH 345', 'Directed Research in Anthropology', 0.5, 'PRJ', 'This course provides selected students with an opportunity to work (up to a maximum of eight hours per week over one term) as apprentices with a specific instructor on a research project in which the instructor is currently engaged.', 'ANTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ANTH 347', 'Medical Anthropology', 0.5, 'LEC', 'This course teaches students the fundamentals of anthropological approaches to illness, health, medicine, and bioethics. It covers the history of medical anthropology as well as major movements, debates, and current topics. Topics include cross-cultural healing systems, social suffering, and concerns raised by new biomedical technologies and within global health.', 'ANTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ANTH 348', 'Anthropology of Tourism', 0.5, 'LEC', 'This course examines the nature of tourism as cross-cultural contact and critiques the phenomena of mass travel and globalized tourism. The various perspectives of anthropologists and tourists in understanding culture will be explored.', 'ANTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ANTH 351', 'Indigenous Practices & Relations: A Comparative Approach', 0.5, 'SEM', 'An examination of the legal, social, and cultural position of indigenous communities within the nation-state. The course will compare Canada''s relationships to those in the United States, New Zealand and/or Australia, and South America.', 'ANTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ANTH 352', 'Anthropological Thought', 0.5, 'LEC', 'An examination of the development of anthropological theory. The primary emphasis will be on modern and post-modern perspectives.', 'ANTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ANTH 355', 'Human Osteology', 0.5, 'LAB, SEM', 'A laboratory course in the anatomy and function of the human skeleton with an emphasis on recovering and identifying skeletal remains from archaeological and forensic contexts.', 'ANTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ANTH 361', 'Biocultural Examination of Primate Conservation', 0.5, 'LEC', 'This course examines the science of conservation biology, with a focus on the Order Primates and discusses the primary threats to primates in the wild, such as habitat alteration and hunting. It also examines and evaluates competing conservation strategies, in light of their ramifications for both the human and nonhuman residents.', 'ANTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ANTH 365', 'Human Evolution', 0.5, 'LEC, SEM', 'This course reviews the evolutionary history of humans. It includes an examination of the hominin fossil record, evidence from genetic analyses, and behavioural inferences that have been drawn from nonhuman primate models. Emphasis is placed on the process of knowledge production and interpretation, and the representation of human evolution in the public realm.', 'ANTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ANTH 370', 'Issues in Contemporary Native Communities in Canada', 0.5, 'LEC', 'Selected aspects of the contemporary native experience as defined by the local native community. The topics examined will be placed in historical perspective. Specially selected course lecturers will be representative of the wider native community.', 'ANTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ANTH 371', 'Anthropological Field Experience', 0.5, 'FLD, LEC', 'This course features a combination of academic content and field experience.', 'ANTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ANTH 390A', 'Reading in Anthropology', 0.5, 'RDG', 'Guided reading in a selected portion of the anthropological literature.', 'ANTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ANTH 390B', 'Reading in Anthropology', 0.5, 'RDG', 'Guided reading in a selected portion of the anthropological literature.', 'ANTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ANTH 391', 'Reading in Anthropology', 0.5, 'RDG', 'Student-initiated independent research on a selected topic. A high standard of written work is required.', 'ANTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ANTH 393', 'Reading in Anthropology', 0.5, 'RDG', 'Guided reading in a selected portion of the anthropological literature.', 'ANTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ANTH 395', 'Anthropological Study Abroad', 0.5, 'FLD, LEC', 'This course features a combination of academic study and first-hand field study of cultures and visits to or work experience at museums and archaeological sites.', 'ANTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ANTH 400', 'Special Topics in Anthropology', 0.5, 'SEM', 'Seminar on current topics in Anthropology. Focus will vary from year to year.', 'ANTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ANTH 402', 'Palestine/Israel: Anthropological Perspectives', 0.5, 'SEM', 'In this seminar, we explore contemporary ethnographies of Israel and Palestine. Emphasis will be placed on developing an anthropological understanding of historical and commemorative narratives, diaspora and exile communities, gender, political organization and the law, migrant labour, peace and human rights activism, and war.', 'ANTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ANTH 403', 'Anthropological Inquiry into the Origin of Language and Cultural Behaviour', 0.5, 'SEM', 'This seminar course will explore the origins of language and cultural behaviour from a biological anthropology perspective, addressing the question of how the ability to produce a complex symbolic system evolved in humans. The course will integrate multiple fields of anthropological inquiry, including human and primate evolution, primate behaviour and cognition, and anthropological linguistics.', 'ANTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ANTH 440', 'Archaeological Analysis and Interpretation', 0.5, 'LAB, SEM', 'A study of contemporary archaeological method and theory with emphasis on the process of deriving inferences concerning past peoples and societies from different kinds of archaeological materials and data. Students will be required to carry out an analysis of an actual archaeological assemblage. Specific topics will vary from year to year.', 'ANTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ANTH 455', 'Skeletal Biology and Forensics', 0.5, 'LAB, LEC', 'This laboratory course focuses on the evaluation of human skeletal remains in archaeological and forensic contexts. Topics include determination of basic biological categories, e.g., age, sex, race, evaluation of paleopathological conditions, and aspects of forensic anthropology.', 'ANTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ANTH 460', 'Human Adaptation and Variation', 0.5, 'SEM', 'An examination of the principles of variation in human evolution past and present. Topics will include adaptation of modern populations to altitude, temperature, and disease.', 'ANTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ANTH 470', 'Archaeological Field Methods', 0.5, 'FLD, LAB, PRA', 'Data gathering techniques will be studied and applied in field work on archaeological sites. Enrolment is limited.', 'ANTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ANTH 492A', 'Reading in Anthropology', 0.5, 'RDG', 'Guided reading in a selected portion of the anthropological literature.', 'ANTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ANTH 492B', 'Reading in Anthropology', 0.5, 'RDG', 'Guided reading in a selected portion of the anthropological literature.', 'ANTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ANTH 495', 'Reading in Anthropology', 0.5, 'RDG', 'Guided reading in a selected portion of the anthropological literature.', 'ANTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ANTH 497', 'Reading in Anthropology', 0.5, 'RDG', 'Guided reading in a selected portion of the anthropological literature.', 'ANTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ANTH 499A', 'Honours Essay', 0.5, 'PRJ', 'Directed reading and research in a selected area of anthropology inquiry.', 'ANTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ANTH 499B', 'Honours Essay', 0.5, 'PRJ', 'Directed reading and research in a selected area of anthropology inquiry.', 'ANTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ANTH 600', 'Public Issues Anthropology', 0.5, 'SEM', 'An examination of the application of anthropological knowledge to public issues. The issues studied will vary from year to year; they may include such topics as race and ethnicity, citizenship and the state, marriage and sexuality, conservation and the environment, and the ownership, interpretation and display of artifacts and human remains. Students will present their research in forms appropriate to both academic and general audiences.', 'ANTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ANTH 604', 'Human Development in a Cross-Cultural Perspective', 0.5, 'SEM', 'Seminar in current issues in the anthropology of the life cycle.  This course will deal with child rearing, young adulthood, aging and the female and male life cycles, among other topics, from the perspective of various cultures.', 'ANTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ANTH 605', 'Selected Topics in Theory and Research', 0.5, 'LEC, SEM', 'This course will be offered with varying content focusing on theory or research.', 'ANTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ANTH 608', 'Anthropological Theory', 0.5, 'LEC, SEM', 'An examination of classical and contemporary anthropological theory, including an emphasis on the most recent directions in the discipline.', 'ANTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ANTH 614', 'Research Methods', 0.5, 'LEC, SEM', 'An examination of the methods of qualitative research, including participant observation and unstructured interviews, as well as the ethical considerations of fieldwork. Other topics, such as comparative and historical methods, may be included.', 'ANTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ANTH 655', 'Skeletal Biology and Forensics', 0.5, 'LAB, LEC, SEM', 'This laboratory course will focus on the evaluation of human skeletal remains in archaeological and forensic contexts. Topics will include determination of basic biological categories, e.g., age, sex, race, evaluation of paleopathological conditions, and aspects of forensic anthropology.', 'ANTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ANTH 659', 'Conservation, Communities and Globalization', 0.5, 'SEM', 'Biological anthropology has a long history of examining our closest living relatives, the nonhuman primates.  Increasingly, these species are threatened by extinction, which brings to light larger questions regarding our place in the natural world.  Anthropology is uniquely positioned to examine the interaction between efforts to conserve biodiversity and natural resources, community rights, and globalization.  This course will focus on attempts to balance the preservation of nature within the health and socioeconomic well-being of neighbouring communities, and how these relationships are affected by processes of globalization.  Other topics such as the effects of climate change, heritage management, and indigenous rights may be addressed.', 'ANTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ANTH 660', 'Reading Course', 0.5, 'RDG', 'A program of directed reading, complemented with the writing of papers or participation in research.  Reading courses are arranged by students through their advisors or advisory committees and must be approved by the graduate chair of the department.  This course may be repeated provided different content is involved.', 'ANTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ANTH 661', 'Research Seminar in Public Issues Anthropology', 0.5, 'SEM', 'The objective is to write a research paper in Public Issues Anthropology. In this course, you will learn how to craft a research statement, construct a theoretical model, and interpret, analyze and communicate your results.', 'ANTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ANTH 662', 'Human Adaptation and Evolution', 0.5, 'SEM', 'An examination of the Principles of Variation in human evolution past and present. This will include the development of the genus Homo, adaptation of modern populations to heat, cold, altitude and stress, and the continuing micro-evolutionary development of humans.', 'ANTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('APPLS 205R', 'Second Language Acquisition', 0.5, 'LEC', 'This course introduces major theories of second language acquisition along with reasons for variations in the speed and accuracy of learner progress. It addresses such issues as error analysis, grammatical accuracy, and the effectiveness of bilingual or immersion education.', 'APPLS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('APPLS 301', 'Language, Culture, and Identity', 0.5, 'SEM', 'This course provides an introductory overview of fundamental concepts of language, culture, and identity and relates them to foreign/second language learning and teaching. The course is not language-specific but rather addresses general questions related to learning and using more than one language.', 'APPLS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('APPLS 304R', 'Second Language Teaching Methodology', 0.5, 'LEC', 'This course offers a foundation for developing competence as a professional second language instructor. In classes exploring theories of second language instruction, students discover the strengths and weaknesses of traditional and popular methodologies and integrated approaches applied to such areas as communicative competence and general language skill development.', 'APPLS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('APPLS 306R', 'Second Language Assessment and Testing', 0.5, 'LEC', 'This course explores the principles of second language testing - reliability, validity, practicality, authenticity, and impact - and applies them to language classrooms and high stakes proficiency tests such as the Test of English as a Foreign Language (TOEFL). It considers the implications of testing for both teachers and students. Of interest to prospective teachers of English and other languages.', 'APPLS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARBUS 100', 'Introduction to Arts and Business', 0, 'LEC', 'The objective of this non-credit course is to expose students to the nature and scope of Arts and Business and prepare them in selecting their major.', 'ARBUS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARBUS 101', 'Introduction to Business in North America', 0.5, 'LEC, TST', 'The functional areas of business: finance, personnel administration, production, marketing, and accounting are examined within differing organizational structures. Coverage also includes study of the principles of effective management and the financial system as a source of corporate capital.', 'ARBUS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARBUS 102', 'Accounting Information for Managers', 0.5, 'LEC, TST, TUT', 'This course is designed for non-accountants who will use accounting information for planning, control, and decision-making.', 'ARBUS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARBUS 200', 'Entrepreneurship Principles and Practices', 0.5, 'LEC, PRA', 'The role of the entrepreneur, the entrepreneurial firm, and the creative society are examined. Students are exposed to the practices that enable workplace creativity and innovation with exposure to leadership theory and project management.', 'ARBUS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARBUS 202', 'Professional and Business Ethics', 0.5, 'LEC', 'Study of ethical and moral issues that typically arise in professional and business activity. What responsibilities to society at large do people in such business and professional activities as teaching, engineering, planning, architecture, and accounting have? How far should professional autonomy extend?', 'ARBUS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARBUS 300', 'Practical Business Skills', 0.5, 'LEC, PRA', 'This course will introduce students to important and relevant business skills that will assist them as they prepare for successful careers. The course will supplement contemporary theories with appropriate case studies.', 'ARBUS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARBUS 301', 'International Business', 0.5, 'LEC, TST', 'This course examines the opportunities, risks, and challenges faced by businesses in international markets, as well as the preparation required to operate them.', 'ARBUS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARBUS 302', 'Principles of Marketing', 0.5, 'LEC', 'This course is designed to provide a broad understanding of the field of marketing, including consumer behaviour, segmentation, targeting, positioning, branding, and the marketing mix. Students will gain an understanding of how organizations identify the needs of potential consumers and create and deliver value to these consumers through the marketing process.', 'ARBUS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARBUS 400', 'Strategy and Program Integration', 0.5, 'LEC, PRA', 'A capstone course designed to integrate the arts and business components of the program that incorporate the principles of strategy for the purpose of enabling students to effectively position themselves for opportunities after graduation.', 'ARBUS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 100', 'An Introduction to Architecture', 0.5, 'LEC', 'An introduction which explores in broad terms the nature of architecture and its evolution over time. The course will familiarize students with the primary concepts and architecture of our culture; develop an awareness of the primary influences on the evaluation of architectural form; establish the concept that architecture conveys meaning through its own expressive language; and introduce the language of architectural criticism and interpretation.', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 110', 'Visual and Digital Media 1', 0.5, 'STU', 'Introduction to the use of graphic media in architecture. Students will engage in exercises in drawing using various media, acquire digital skills, and develop fluency in diverse forms of architectural presentation. [Offered: F]', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 113', 'Visual and Digital Media 2', 0.5, 'LAB, LEC', 'Introduction to computing techniques in architecture. Students will be instructed in the conceptual foundations for computer use in architecture, graphic applications for the computer and skills for two-dimensional drawing, three-dimensional modelling and graphic techniques for visualization and portfolio development. Students will gain fluency in a range of software applications for the purposes of developing technical and visual proficiencies to be integrated into the design process. [Offered: W]', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 125', 'Principles of Environmental Design', 0.5, 'LEC', 'An introduction to the environmental aspects of architectural design and to an analysis of the form that buildings and landscapes take and the processes and ideals leading to those forms. Topics of discussion include environmental concepts and influences on design, site planning, landscape, sustainability, embodied energy, climatic influences and microclimates.', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 142', 'Introduction to Cultural History', 0.5, 'LEC', 'This course will introduce an approach to cultural history and attempt to localize modern humanity by looking at the principal icons and images that have been central to the compelling myths of Modernism, the principal movement and source of art and architecture in 20th century Western culture. This course will examine how living myths are recapitulated or transformed in relationship to the artistic and cultural conventions of the period. [Offered: F]', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 143', 'The Ancient World and Foundations of Europe', 1, 'LEC', 'An overview of the cultural history of antiquity and the Early Middle Ages up to the rise of feudalism and the rediscovery of Aristotle: from 2000 BC to 1100 AD. Recognition of patterns of life and the concepts of order and conduct, the metaphors and myths which evolve during this time period through the study of the ideas, literature, art, architecture, technology and town design from ancient Greece and Rome and the lands surrounding the Mediterranean until the end of the first millennium. [Offered: W]', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 172', 'Building Construction 1', 0.5, 'LEC', 'A focus on the construction of small-scale buildings will introduce the fundamentals of building construction demonstrating relationships between contemporary design and material selections. Design development practices will reference; regulatory frameworks, building science, soils, foundations, light wood construction, engineered wood, masonry (brick, concrete block, load bearing, veneer systems), shallow foundations, residential codes, barrier free design.', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 431', 'Science as Practice and Culture', 0.5, 'LEC', 'An examination of science as being constituted by practices which are inevitably social and cultural, as well as of scientific expertise in contemporary issues of new technology, biomedicine, and environmental conflict.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 173', 'Building Construction 2', 0.5, 'LEC', 'Focusing on the construction of medium-to large-scale buildings examines relationships between design development, and the building science, and construction practices of structural systems and enclosures. Case studies and projects will be used to investigate steel framing systems (traditional, long span, architectural exposed structural steel [AESS]); reinforced, precast, fibre reinforced and prestressed concrete construction; heavy timber construction (traditional, glulam, cross laminated timber [CLT]); deep foundations; building envelopes (curtain wall, window walls, glazing, insulation strategies, and roofing systems); fire protective design. [Offered: W]', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 212', 'Digital Fabrication', 0.5, 'LEC', 'This course will introduce students to the tools, work-flows, and culture surrounding computer-aided design/computer-aided manufacturing (CAD/CAM) and its creative applications within architecture. Students will learn how to work with CAD/CAM technologies such as laser cutters, three-dimensional (3D) printers and computer numerical control (CNC) routers while expanding their knowledge of two- and three-dimensional CAD geometries that inform the digital fabrication process. [Offered: F]', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 174', 'Experimental Course', 0.5, 'LEC', 'This course offers a vehicle for introducing additional electives to the program on a short-term basis, and for developing future permanent courses.', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 175', 'Experimental Course', 0.5, 'LEC', 'This course offers a vehicle for introducing additional electives to the program on a short-term basis, and for developing future permanent courses.', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 192', 'Design Studio', 1.5, 'STU', 'Development of the means to appreciate the art and science of building; introduction to the study of theories of architecture; development of skills in graphic communication; introduction to a study of building elements; promotion of the application of theory in the practice of design.', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 193', 'Design Studio', 1.5, 'STU', 'Further development of basic skills, and the application of theory and design in small scale architectural design projects. Introduction to issues of inhabitation, program and context. [Offered: W]', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 215', 'Communication Design', 0.5, 'LEC', 'This course will elaborate upon the graphic and communication conventions established in the fundamental architecture curriculum through a series of assignments ranging from conventional architectural techniques to introductory principles of graphic and industrial design. The intention is to: cultivate an understanding of fundamental concepts and techniques in leading vector and raster-based graphic tools for design development and presentation; to develop an understanding of the paradigm shift from digital media as a representation tool to that of design development; to expand the depth and breath of skills necessary for modern design industry; and to apply design techniques and technologies to and from industrial and graphic design. [Offered: S]', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 226', 'Environmental Building Design', 0.5, 'LEC', 'This study of building construction and design examines relationships between design development and environmental building practices. Case studies, testing exercises, and projects will be used to investigate: solar geometry, influences of climate, regional circumstances, sustainability, vernacular building practice, daylighting, and passive design. Energy related issues will be addressed and energy based software design programs will be introduced. The detailed design of an energy efficient/passive solar building as the final term project will be undertaken.', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 246', 'Pre-Renaissance to Reformation', 1, 'LEC, TUT', 'This course presents the cultural history of the High and Late Middle Ages, the Renaissance, and the Reformation: from the 12th to 17th centuries. Recognition of the patterns of life of feudalism, the medieval world, scholasticism, the humanist re-interpretation of the place of Man in the universe, the protestant challenge to established doctrines of faith, and the new understanding of the human and natural worlds in the wake of the European discovery of Asia and the Americas. Analysis of the art and architecture from this era with emphasis on the French and German Gothic, and the Italian Renaissance and its spread and transformation in the rest of Europe. [Offered: F]', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 247', 'Cultural History 4: Renaissance to Revolution', 1, 'LEC, TUT', 'Analysis of the various styles emerging out of provincial and international Gothic, especially Italian use of classical models, the spread of this Renaissance mode, leading to consideration of the Mannerist, the Baroque, the Rococo, the Neoclassical; investigation of the course of attitudes from humanism, nationalism, and Reformation through the Enlightenment until the French Revolution and Hume''s dethronement of Reason. [Offered: S]', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 252', 'Creative Problem Solving', 0.5, 'LEC', 'Development of creative skills through group behaviour in problem solving sessions by: developing a clear understanding of each participant''s own creative thought processes; increasing the ability to consciously and deliberately make use of one''s own creative potential; engendering an awareness of the capacity to use active collaboration between self and colleagues to produce better solutions to the problems identified by the group.', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 256', 'Introduction to Photography', 0.5, 'LAB, LEC', 'Introduction to the main concepts in creating and using photographic images. This will be accomplished in the context of various academic applications including site and model documentation, portfolio, and thesis presentation. The course will include both analog and digital procedures but with a heavy emphasis on monochrome silver images. A limited number of analog and digital cameras are available on a loan basis. Materials at student''s expense.', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 260', 'Principles of Structures', 0.5, 'LEC, TUT', 'Fundamental concepts of mechanics and structures, as related to architectural design, study of loading conditions, forces, moments, systems of forces, conditions of equilibrium for two- and three-dimensional structures, centre of gravity of loads and areas, bar forces in trusses, simple frame analysis, moment of inertia. Concepts of simple stress and strain; shear and bending moments in simple beams; shear and moment diagrams, qualitative deflected shapes, flexural and shearing stresses, deflection calculations; compression members; Euler''s formula. [Offered: F]', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 272', 'Interior Environments: Acoustics and Lighting', 0.5, 'LAB, LEC', 'A detailed study of the interior environments of buildings focusing on acoustics, artificial source lighting and day lighting. The course also addresses building services such as electrical distribution, vertical transportation and exterior site services. [Offered: F]', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 273', 'Environmental Systems', 0.5, 'LEC', 'A focus on the air and water systems of buildings with an aim to developing knowledge and skills appropriate to architectural practice. Subjects covered include environmental parameters, heating and cooling loads, energy conservative design, the selection of heating, ventilating and air conditioning systems, plumbing systems, and fire protection criteria and systems, with reference to building codes and standards. [Offered: S]', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 274', 'Experimental Course', 0.5, 'LEC', 'This course offers a vehicle for introducing additional electives to the program on a short-term basis, and for developing future permanent courses.', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 275', 'Experimental Course', 0.5, 'LEC', 'This course offers a vehicle for introducing additional electives to the program on a short-term basis, and for developing future permanent courses.', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 276', 'Timber: Design, Structure and Construction', 0.5, 'LEC', 'Architectural case studies are used to examine conceptual development, structural design, building process and the selection of structural timber systems. Topics such as flexural, compression and truss members, connections, and plywood construction are studied using calculations, design aids, rules of thumb and the latest CSA design standards. [Offered: S]', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 277', 'Timber: Design, Structure and Construction for Engineers', 0.5, 'LEC', 'Architectural case studies are used to examine conceptual development, structural design, building process and the selection of structural timber systems. Topics such as flexural, compression and truss members, connections, and plywood construction are studied using calculations, design aids, rules of thumb and the latest CSA design standards. [Offered: S]', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 284', 'Architectural Research', 0.5, 'RDG', 'This course offers a student an opportunity for independent research into architectural problems not offered in the regular curriculum, guided exploration of specific architectural problem areas, of appropriate complexity to the particular term.', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 285', 'Architectural Research', 0.5, 'LEC', 'This course offers a student an opportunity for independent research into architectural problems not offered in the regular curriculum, guided exploration of specific architectural problem areas, of appropriate complexity to the particular term.', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 292', 'Design Studio', 1.5, 'STU', 'The exploration of design as a thinking process through the medium of small scale design projects. The development and analysis of architectural propositions concerning personal space within the context of a larger community.', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 293', 'Design Studio', 1.5, 'STU', 'Design involving problems of human perception and dimension in more complex environments, and dealing with issues of public and private space. Development of skills in analysis and programming, and further exploration of questions of siting and context.', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 314', 'Digital Design', 0.5, 'LEC', 'This intermediate level course provides a more in-depth theoretical foundation in architectural design by computer, including three-dimensional modelling, light and colour, rendering, image processing and animation. Practice is provided by weekly tutorial exercises and a small term design project.', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 327', 'Architecture of the Urban Environment', 0.5, 'LEC', 'An introduction to the structure and form of urban environments as understood through the urban architecture. The forces that determine the creation and development of urban places will be examined. Topics include the plan as a generative form, urban building types, urban morphology, and the shape of the public realm, infrastructure as both system and architectural object, nature and the park, and real estate and development controls. Of special interest will be analyses of the suburb, and urban master plans. [Offered: W]', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 328', 'Approaches to Architecture and Urbanism', 0.5, 'SEM', 'The course develops a trans-disciplinary overview to design approaches in architecture and urbanism using image- and symbol-making as key activities in designing environments. The course evaluates the quality of results and the quality of process of design of environments. The students address topics within a wide range of subject matter groupings, from sustainable architecture to engineering, landscape and infrastructure, to branding and aesthetic practices, to design within the municipal political forum. Students present seminars on diverse categories of design as a means to develop a personal design ethos. The final submission includes a set of drawings, diagrams and images that represent a set of design principles allowing students to consciously take a position in the world as a designer. [Offered: F, W]', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 331', 'Working with Wood', 0.5, 'LEC', 'This course focuses on understanding the premise that hand skills can facilitate and help inform conceptual and academic skills. The content provides a broad-based introduction to woodworking equipment, its fabrication methods and methodology. A combination of lectures and hands-on experiences are used to equip students with the basic skills and knowledge required to conceptualize, fabricate, and finish a designed object from wood. [Offered: W]', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 332', 'Design/Build Workshop', 0.5, 'LEC', 'A design/build workshop which offers opportunities for hands-on experience in three-dimensional design. Advancements of technical and design skills provide the underpinnings for the projects. Students are encouraged to explore a variety of media and techniques such as woodworking and metalworking, allowing for both individual and small team investigations. A logbook will be kept to record creative intentions and the design process from conception to completion. [Offered: F, W]', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 342', 'Modernism to the 21st Century', 1, 'LEC, TUT', 'This course examines the cultural, architectural and urban history of that complex period known as modernism. It is an overview of the individual works, movements and critical texts associated with the evolution of modern architecture and urbanism from the beginning of the 20th century to the early years of the second millennium. The social, technological and aesthetic ambitions of the modern project are critically reviewed and its development and dissemination in Europe, the Americas and Asia is traced. Selected works are examined in depth as examples of the canonic and variant forms of modernism. [Offered: W]', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 343', 'Enlightenment, Romanticism and the 19th Century', 0.5, 'LEC, TUT', 'Depiction of modern culture as one in which the notion of environmental order as the fulfilling of natural law is replaced by a notion of order as the historical creation of autonomous wills. Selected works in philosophy, literature, art and architecture will be studied. [Offered: W]', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 345', 'Architectural Theory 1850-1990', 0.5, 'LEC', 'Beginning with the introduction of important theories of architecture in vogue prior to 1850, this course examines texts, movements, buildings, projects, and urban proposals of the period in order to understand the structure of contemporary architectural theory.', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 346', 'Competitions in Architecture', 0.5, 'SEM', 'This course provides an opportunity for the student to independently engage in the respected tradition of the Architectural Competition. The competition entry and accompanying research paper must focus on the use of architectural precedents as the basis for the creation of typologically based propositions. Submission to the external competition is mandatory, the timing and detailed requirements of which will determine the personalized academic requirements for this course. [Offered: W, S]', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 347', 'Philosophy in Architecture', 0.5, 'SEM', 'The course introduces the study of philosophical issues as they pertain to architecture, urbanism, image-making and symbol manipulation. The course focuses on the work of a selection of living and recent philosophers whose issues are relevant to questions of everyday 21st century life, with reference to their basis in the history of philosophy. Work by philosophers such as Giorgio Agamben, Kwame Anthony Appiah and Judith Butler on ethics, Gilles Deleuze on meaning, Friedrich Kittler on media and technology, Jean-Luc Nancy on community and Slavoj Zizek on psychoanalysis and political economy, as well as Canadian scholars such as Marshall McLuhan and Harold Innis is addressed. Students participate in a seminar format with a series of presentations linking their design projects with philosophical issues, and submit a final project making use of text and diagrams. [Offered: F, W]', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 362', 'Steel and Concrete: Design, Structure and Construction', 0.5, 'LEC, TUT', 'Architectural case studies are used to examine conceptual development, structural design, building process and the selection of structural steel and concrete systems. Topics such as tension, flexural and compression members; and connections are studied using calculations, design aids, rules of thumb and the latest CSA design standards. [Offered: W]', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 364', 'Building Science', 0.5, 'LEC', 'The physio-technical factors that influence building design for performance, durability, efficiency, health, and sustainability will be explored. Common building design construction problems, their causes and solutions, will be examined with the aid of case studies. Using the principles of building science, good details of masonry, wood, steel, and glass will be developed. [Offered: W]', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 365', 'Structural Design Build Workshop', 0.5, 'LEC, TUT', 'This course builds on the principles learned in the previous structural design courses to the field of application. Students will design, construct, and test full size prototypes of chairs and other structures of limited scale. Performance will be predicted and analyzed in the wake of testing. A full structural report is required. [Offered: F]', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 374', 'Experimental Course', 0.5, 'LEC', 'This course allows for additional electives to the program on a short-term basis, and for developing future permanent courses.', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 375', 'Experimental Course', 0.5, 'LEC', 'This course allows for additional electives to the program on a short-term basis, and for developing future permanent courses.', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 384', 'Architectural Research', 0.5, 'RDG', 'This course offers a student an opportunity for independent research into architectural problems not offered in the regular curriculum. It allows guided exploration of a specific architectural problem area, of appropriate complexity to the particular term.', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 385', 'Architectural Research', 0.5, 'RDG', 'This course offers a student an opportunity for independent research into architectural problems not offered in the regular curriculum. It allows guided exploration of a specific architectural problem area, of appropriate complexity to the particular term.', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 392', 'Design Studio', 1.5, 'STU', 'Development of design skills and theoretical knowledge through their application in projects involving various building types in urban situations. Emphasis is placed upon issues of materiality and technology in architectural design.', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 393', 'Option Design Studio', 1.5, 'STU', 'This Design Studio is subdivided into distinct studio sections, each of which provides a specific platform for advanced research and design presented within the context of a topic or set of issues to be explored in relation to a specific design project, program and site. These studios range in both scale and scope, traversing an array of academic investigations, design hypotheses, research agendas, interdisciplinary explorations, and pedagogical intentions. These topics foreground the disciplinary arenas within architecture in the areas of architectural theory, media, technology, urbanism, and landscape. [Offered: F]', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 425', 'Theory and Design of the Contemporary Landscape', 0.5, 'LEC', 'This course provides an historical overview of the ideas of nature and landscape in Western thought. ''Nature'', ''Ecology'', and ''Landscape'' are treated as cultural constructs, related to specific philosophical, technological, economic, political, and social issues. Many of these issues will be considered as the course of study traces the evolution and transformation of contemporary landscape. [Offered: S]', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 442', 'Contemporary Architectural Theory', 0.5, 'LEC', 'This course presents a thematically organized survey of contemporary architectural theory that focuses on the relationship between seminal theoretical texts and critical developments in contemporary architectural theory and practice. The course is intended to be a forum for discussion of selected topics in contemporary culture, and to provide students with an advanced knowledge base in contemporary architectural theory to ground more specialized architectural electives while acting as a support for thesis and other forms of advanced architectural research. [Offered: F]', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 443', 'Architecture and Film', 0.5, 'LEC', 'This course explores the relationship between Architecture and the development of early, modern and science fiction films via the examination of the source and methods of portrayal of architectural expression in film. Films will be viewed to examine precedents for imagery, set design, location selection, as well as the integrated vision of the urban and dystopic environmental future. Futuristic film architecture will provide an opportunity to study a vision of the future of urban built form and provide a forum for critical discussion. Assignments will require that the students become familiar with different methods of media; for example - video filming, sound and visual editing, web page production. [Offered: F]', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 446', 'Italian Urban History (Rome)', 0.5, 'LEC', 'The course provides a survey of the history of settlement and urban form on the Italian peninsula from antiquity to the present day. In it the influences upon the structure of public and private space are outlined for each historical period. These include constants such as geography and climate, but more especially the factors that induce and manifest change: politics, warfare, economics, social structure, the arts and theory.', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 448', 'Rome and the Campagna (Rome)', 0.5, 'LEC, SEM', 'History of settlement and building in Rome and the surrounding area from antiquity to the present. Acts of design in architecture, urban form and landscape related to political, cultural and spiritual authority of Rome. Comparison drawn between the image of the city, represented in literature and art, and the material facts of the place. Field trips, lecture.', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 449', 'The Development of Modern Italian Architecture (Rome)', 0.5, 'LEC', 'The course addresses the issues of architecture and urbanism in Rome and Italy from 1750 to the present. It explores the relationship between cultural, political and artistic phenomena such as Futurism, Novecento and Rationalism, that anticipate and create modernism in Italy. [Two one day field trips, estimated cost $60.00. Offered: F]', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 473', 'Technical Report', 0.5, 'LEC', 'Students will investigate and report on technical issues as they relate to the development of the comprehensive building project in the parallel Design Studio. Innovation and integration in architectural design will be stressed with respect to structure, building envelope, environmental systems, health and life safety, movement systems, site planning and the integration of information technology. [Offered: S]', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 474', 'Experimental Course', 0.5, 'LEC', 'This course allows for additional electives to the program on a short-term basis, and for developing future permanent courses.', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 475', 'Experimental Course', 0.5, 'LEC', 'This course allows for additional electives to the program on a short-term basis, and for developing future permanent courses.', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 484', 'Architectural Research', 0.5, 'LEC', 'This course offers a student an opportunity for independent research into architectural problems not offered in the regular curriculum. It allows guided exploration of a specific architectural problem area, of appropriate complexity to the particular term.', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 485', 'Architectural Research', 0.5, 'LEC', 'This course offers a student an opportunity for independent research into architectural problems not offered in the regular curriculum. It allows guided exploration of a specific architectural problem area, of appropriate complexity to the particular term.', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 492', 'Design Studio', 1.5, 'STU', 'The studio course is mounted in Rome, Italy, with the school''s own faculty and premises, and offers a unique opportunity to undertake design studies in a truly rich architectural heritage. The main focus is the nature of the institution and its relationship to the city and its culture. Students participating in the Rome term are expected to defray the costs of travel, accommodation and food. For students unable to study in Rome, an alternative studio is offered in Cambridge. It presents similar design projects and theoretical questions in a North American context. [Offered: F]', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 493', 'Design Studio/Comprehensive Building Design', 1.5, 'STU', 'This studio represents a culmination of the pre-professional degree, through the integration and application of skills and knowledge to a complex building project. Students will develop designs to a high level of detail. A concern for technical material, environmental and legal aspects of architecture will support open speculation and innovative design. [Offered: S]', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 611', 'Drawing, Representation and Practice', 0.5, 'LEC', 'In architecture, there is a crucial tension between the act of drawing -- the representation of three dimensioned forms in two dimensions -- and the fabrication of those forms in a concrete context. Using case studies of artisanal production and industrial design and manufacture, and addressing the possibilities and limitations of the computer as a design and production tool in architecture, this seminar draws out a discourse on the cultural momentum of composition and representation, and the fabrication of architectural forms in both virtual and material realities, establishing provisional guidelines for their interplay.  Consent of instructor required for those outside of the M.Arch program', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 612', 'Originality and Invention in Architecture', 0.5, 'LEC', 'Professional knowledge in architecture involves not only the application of science or theory to instrumental problems, but also the innovation that calls for cognitive risk-taking, reflection-in-action, and learning-by-doing. This practice is legitimized by a discourse that evolved  in the nineteenth century, and that defines a good project as an original project. Critically engaging this discourse of originality, this course addresses the processes and strategies that foster invention in architecture in a reflective practicum that emphasizes the value of synthetic thinking across disciplines.  Consent of instructor required for those outside of the M.Arch program.', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 613', 'Light, Colour and Darkness', 0.5, 'LEC', 'Light, colour and darkness define an essential condition in the experience and the understanding of the environments that we imagine, create, and inhabit. This seminar addresses the empirical nature and the cultural, philosophical and spiritual meanings of light, colour and darkness and explores with the aid of, among other tools, digital simulation, the generation, presence, significance, experience and representation of light, colour and darkness in architecture.  Consent of instructor required for those outside of the M.Arch program.', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 622', 'Urban Revitalization & Design', 0.5, 'LEC', 'This course will discuss issues/challenges faced by cities (from downtown revitalization to suburbanization).  Research has shown that their physical environments will fare better if such cities are designed, planned, and modeled in collaboration with professionals (i.e. architects and urban designers) and the community.  Students will investigate how design projects improve the quality of life of mid-size cities (i.e. creative city concept).  Together, we will focus on identifying opportunities for innovative collaborations and tangible improvements to the design of urban environments.  The school is especially interested in strengthening collaboration among universities, community residents, planning and design practitioners, and policymkaers to seize their own destiny to design, model, and plan.  Accordingly, we will investigate thematic (e.g. urban revitalization, design/aesthetics, environmental/greening, gentrification, housing, and multiculturalism) and policy-oriented areas (e.g. urban design guidelines, new urbanism, intensification, and land-use development patterns) that relate to the mid-size city.  This is a seminar-based course and student participation is key to it''s success.', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 623', 'Ecosystem Design for Urban Landscapes', 0.5, 'LEC', 'Landscape design is being reshaped by ecosystems thinking. This course introduces the concepts of ecosystems design for urban and peripheral landscapes. Beginning with case studies on the spatial forms taken by the historic co-evolution of nature and cultural forms in urban land systems, the course introduces ecosystems-based classifications combining abiotic, biotic, and cultural analyses; urban land mosaics based on urban landscape ecologies; and green infrastructures. It concludes with contemporary case studies demonstrating the scope of ecosystems-based urban design and planning, guidelines for ecosystems design, and evolutionary design approaches.', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 624', 'The Social Ecology of the Urban Periphery', 0.5, 'LEC', 'With the rise of the metropolis, the development of new transport and leisure infrastructures, the rapid adoption of new  technologies of communication (radio, television, internet), and the pervasive influence of marketed ""lifestyles,"" public spaces have lost much of their customary function as a common ground, while the periphery is invested as a new semi-public commons. This course explores the dynamic relation between the old politics of the architectural centre and  the new social ecologies of the sub-urban and natural peripheries, to identify fresh design opportunities within the development economy of the sub-urban, semi-rural, and  ""cottage-country"" edge. Consent of instructor required for those who do not have the required course.', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 641', 'The Inner Studio', 0.5, 'LEC', 'The built world and the inner world are linked, and have the potential to touch us through the act of design.  The inspiration and meaning for the creative designs we need are available from the psyche but we have not been given the tools to access these resources and bring them into the built world.  The purpose of this elective is to connect architects with new pathways and creative tools for bringing their imagination into the world.  This course presents a psychological view of the built world in order to help architects understand the wisdom of their dreams, shadows and body during the process of design.  Its in-depth approach allows the architect to experience the act of design as inseparable from enriching the self.', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 642', 'Modern Architecture', 0.5, 'LEC', 'An overview of the individuals and movements associated with the birth of modern architecture in the nineteenth and early twentieth century. The social, technological and aesthetic ambitions of the modern project are critically reviewed and its development and dissemination in Europe, the Americas and Asia is traced. Selected works are examined in depth as examples of the canonic and variant forms of modernism. Heldwith ARCH 342- graduate students will be required to complete additional coursework for graduate level course credit.', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 643', 'The Study and Design of Cultural Sites', 0.5, 'LEC', 'Heritage districts, parks, memorials, and reserves have become an important focus of architectural activity, calling on knowledge of cultural conditions, environmental impact and values, economic uses, managerial practices, and legal limitations. This course will introduce ways to identify and define potential cultural sites, and study their history and place in the collective memory, their economy, and the role of architectural intervention in them. Consent of instructor required for those outside of the M.Arch program.', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 644', 'Architecture, Memory and Commemoration', 0.5, 'LEC', 'From its very beginnings, building has consecrated sites of history by transforming, through a process of reification, the fleeting memories of individuals into a communally shared and politically significant history. This course examines the different ways buildings and architecturally articulated precincts have operated as instruments of memory in the traditional tribal society, the ancient city-states, the multi-ethnic empire, Christendom, the modern nation state, and the post-industrial, post-modern, and multi-cultural commonwealths of today. Consent of instructor required for those who do not have the required prerequisite.', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 645', 'Architecture and the State', 0.5, 'LEC', 'Through the creation of buildings, infrastructure programs, funding agencies, policy initiatives, procurement practices and regulatory agencies central governments have used architecture to shape and unify the nation and define the national territory. This course investigates the
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 646', 'Architecture and Film', 0.5, 'LEC', 'This course explores the relationship between Architecture and the development of early, modern and science fiction films via the examination of the source and methods of portrayal of architectural expression in film.  Films will be viewed to examine precedents for imagery, set design, location selection, as well as the integrated vision of the urban and dystopic environmental future.  Futuristic film architecture will provide an opportunity to study a vision of the future of urban built form and provide a forum for critical discussion.  Assignments will require that the students become familiar with different methods of media:  video filming, sound and visual editing, web page production.', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 652', 'Specifications', 0.25, 'LEC', 'Architectural working drawings and specifications, bidding requirements; general conditions;general requirements trade divisions; reference and source material; assembly and production; structural, mechanical and electrical consultants.', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 654', 'Acts and Codes', 0.25, 'LEC', 'The course introduces students to the legislative context within which architects operate, specifically: The Architect''s Act, The Planning Act, and the Ontario and National Building Codes.', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 655', 'Architectural Professional Practice: Ethics, Business, Legal Issues, and Contract Administration', 1, 'LEC', 'This course is a comprehensive overview of Architectural Professional Practice. It is an introduction to the nature of the self-governing profession in Ontario, the legal and legislative context in which architects practice in Canada including its Acts and Codes (The Architecture''s Act, the Planning Act, and the Ontario and National Building Codes), and the essential fiduciary relationships between professionals and others. Forms of partnerships, rules of professional conduct, the constituents of an enforceable contract, professional liability, client-architect relations and the typical challenges encountered in practice are among the essential elements to be studies. Factors affecting both the cost of construction and the ability of an architect to conduct a viable business will be presented. Students will also study the role of the architect in relation to contract administration, architectural working drawings and specifications, bidding requirements; general conditions; general requirements trade divisions; reference and source material; assembly and production; and structural, mechanical and electrical consultants.', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 672', 'Energy Effective Design', 0.5, 'LEC', 'Vernacular building techniques once manifested the principles of passive solar heating, passive cooling and daylighting by solar orientation. Searching for ways to design energy efficient,  comfortable and functional buildings, this workshop revisits the principles of passive heating,  cooling and solar lighting, and explores the operation and use of energy simulation computer programs and post occupancy analysis within the context of sustainable design. The methods will  be tested in two design projects: a freestanding suburban house and an urban row house. Consent of instructor required for those outside of the M.Arch program.', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 673', 'The Science of the Building Envelope', 0.5, 'LEC', 'The building envelope has become the single most significant and element within the contemporary economy of building. It bears much of the identity and amenity of buildings, is the site of significant expenditure, the occasion of environmental concern and attention, the object of much technological innovation, and the locus of entropy. This course provides an advanced study of the building envelope as the place where design, technology, building science, and environmental concerns converge. It discusses matters such as natural light and ventilation or the short- and long-term behaviours of building materials, and assesses the use of new generations of "smart" mechanical environmental devices. Consent of instructor required for those outside of the M.Arch program.', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 675', 'Sheltering Systems', 0.5, 'LAB, LEC', 'This studio/workshop explores the architectural, technological, social and economic aspects of building system design - building systems are considered in the broadest terms. ARCH 675 expects participants to be aware of and pay particular attention to the matrix of cultural, sociological and geographic extremes.  Participants are expected to be capable of considerable independent study supported by frequently scheduled meetings with the course instructor.  This course incorporates design-build experience, wherein the construction of proposed systems is explored through the development of prototypes. Consent of instructor required for those outside of the M.Arch program.', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 676', 'Lightweight Structures', 0.5, 'LEC', 'Innovative structures - frames, fabrics and shells -  give architects design options that have not previously existed, especially with long spans and large, single building volumes. The course deals with the applications, analysis, economics, design and fabrication of modern lightweight structures.  Consent of instructor required for those outside of the M.Arch program. 
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 677', 'Survey of Digital Design Technologies for Architecture', 0.5, 'LEC', 'This course provides an overview of the software and hardware technologies to support the design of an architecture that is more holistically conceived on cultural, social, health, technical and formal grounds that would otherwise be possible.  What is taught facilitates an integrated design process where all disciplines collaborate through all phases of a project to achieve a sustainable design. There will be an exploration of software applications including the generation of landscapes and vegetation, energy analysis, acoustics, lighting, structure, people movement, air flow analysis, microclimate analysis and various types of three-dimensional modelling software, particularly, packages that facilitate interfacing with rapid prototyping and allow complex form generation. Rapid prototyping techniques will include laser cutting, three-dimensional printing and CNC 3-axis machining. The course format is a seminar in which each student will explore some of these technologies in depth by means of a small design example and presentations to the class while obtaining an overview from the course lectures and presentations by other students on other applications.', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 678', 'Digital Lighting Design for Architecture', 0.5, 'LAB, LEC, TUT', 'The intention of the course is to learn to achieve design intent in terms of artificial and natural lighting by digital means. There are three componentsd to the course. One is lighting concepts in terms of colour and materiality, light propagation, sky conditions, sun movement and quantitative lighting analysis including glare. Another component is the use of physically accurate lighting simulation and rendering software. A third is lighting design and critical evaluation through exercises and a small design project.', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 684', 'Special Topics in Architecture', 0.5, 'LEC', 'This course allows for additions in the program on a short term basis, and for the development of future permanent courses.', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 685', 'Readings and Seminars in Architecture', 0.5, 'RDG', 'Issues to be negotiated on an individual basis with faculty members. An outline of this course, approved by the professor in charge, must be submitted to the Graduate Officer within three weeks of registration.', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 686', 'Competitions in Architecture', 0.5, 'RDG', 'This course provides an opportunity for the student to independently engage in the respected tradition of the Architectural Competition.  The competition entry and accompanying research paper must focus on the use of architectural precedents as the basis for the creation of typologically based propositions.  Submission to the external competition is mandatory, the timing and detailed requirements of which will determine the personalized academic requirements for this course.', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 692', 'Thesis Research and Design Studio I', 1.5, 'STU', 'The Graduate Thesis is designed to develop competence in design research and critical analysis while providing students with the opportunity to examine in depth current topics in architectural theory and practice, and to position these within a broader cultural context. Graduate Thesis Research and Design is a two-semester sequence of courses that guide students in the development of an individualized topic that is related to broader thematic areas within architecture, urbanism and landscape. ARCH 692 requires each student to formulate a focused research agenda, the premise of which will be developed throughout the preparation of a written research document and/or tested through a specific design proposal in ARCH 693. Studio fee $25.00.', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AVIA 100', 'Introduction to Aviation', 0.5, 'LEC, TUT', 'This course incorporates a survey of topic areas that impact the aviation industry in Canada. Key areas include aviation history, civil and military aviation, human factors, airspace and air traffic control systems, airport management, and aviation safety. The nature and impact of each of these topics is explored through a combination of lecture, class discussion and activities, and student presentations. [Offered: F]', 'AVIA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARTS 101', 'Foundations for Writing', 0.5, 'LEC, WSP', 'This course helps students to develop the fundamental writing skills required by the Faculty of Arts. Students will be introduced to theories of strong composition and grammar, as well as relevant terminology, and will focus on practical applications, based on readings from Arts disciplines.', 'ARTS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARTS 111', 'Career Development and Decision-Making', 0.5, 'LEC', 'This course is designed to assist students in understanding themselves, the career development process, and occupational information in order to make informed and appropriate occupational and educational decisions. Students will study key career development theories and learn how to integrate self-knowledge into occupational/life decisions, set goals, and devise strategies to attain these goals.', 'ARTS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARTS 122', 'Quest for Meaning in the Modern World', 0.5, 'LEC', 'This course invites students on a quest for meaning in the context of a time in which traditional meanings and definitions have been challenged by rapidly-shifting cultural and religious values.', 'ARTS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARTS 125', 'Who are the Mennonites?', 0.5, 'LEC', 'This course is an interdisciplinary introduction to the range of Mennonite identities, beliefs, practices, and cultural expressions locally and globally. Themes such as Mennonite history, cultural diversity, theology, religious life, peacemaking, community development, music, literature, and artistic expression will be explored.', 'ARTS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARTS 190', 'First-Year Topics in Arts Disciplines', 0.5, 'LEC', 'This topics course will be offered from time to time by particular disciplines in Arts, to cover areas of emerging research and teaching interest.', 'ARTS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARTS 280', 'Statistics for Arts Students', 0.5, 'LEC, TST', 'A basic course in social statistics for Faculty of Arts students. Introduces descriptive statistics (measures of central tendency, dispersion, cross-tabular analysis) and inferential statistics (sampling, statistical significance, hypothesis testing, test assumptions). Covers a range of statistical techniques including t-tests, one-way ANOVA, Chi square, and bivariate correlation/regression.', 'ARTS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARTS 290', 'Second-Year Topics in Arts Disciplines', 0.5, 'LEC', 'This topics course will be offered from time to time by particular disciplines in Arts, to cover areas of emerging research and teaching interest.', 'ARTS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARTS 301', 'Studies in Ideas', 0.5, 'LEC, TUT', 'A course consisting of a series of lectures, and tutorials, on specific topics dealing with important ideas in the humanities, social sciences, fine and performing arts, and languages and cultures.', 'ARTS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARTS 365', 'Arts Study Abroad', 2.5, 'LEC', 'Study abroad in the fall term under exchange agreements supported by the Faculty of Arts. Discussion and approval of options with your advisor is required. Credit for specific courses will be evaluated following receipt of a transcript of academic results and supporting course outline documentation. The approved study agreement will be recorded on the student''s record.', 'ARTS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARTS 366', 'Arts Study Abroad', 2.5, 'LEC', 'Study abroad in the winter term under exchange agreements supported by the Faculty of Arts. Discussion and approval of options with your advisor is required. Credit for specific courses will be evaluated following receipt of a transcript of academic results and supporting course outline documentation. The approved study agreement will be recorded on the student''s record.', 'ARTS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARTS 367', 'Arts Study Abroad', 2.5, 'LEC', 'Study abroad in the spring term under exchange agreements supported by the Faculty of Arts. Discussion and approval of options with your advisor is required. Credit for specific courses will be evaluated following receipt of a transcript of academic results and supporting course outline documentation. The approved study agreement will be recorded on the student''s record.', 'ARTS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARTS 390', 'Third-Year Topics in Arts Disciplines', 0.5, 'LEC', 'This topics course will be offered from time to time by particular disciplines in Arts, to cover areas of emerging research and teaching interest.', 'ARTS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARTS 490', 'Fourth-Year Topics in Arts Disciplines', 0.5, 'LEC', 'This topics course will be offered from time to time by particular disciplines in Arts, to cover areas of emerging research and teaching interest.', 'ARTS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARTS 600', 'Knowledge Mobilzation to Serve Society', 0.5, 'SEM', 'The goal of knowledge transfer is to make publicly-funded research more useful to policy, practice, and the public.  In this interdisciplinary course students learn how to gather, evaluate, synthesize and summarize scholarly knowledge in ways that meet the needs, timeframes, culture, and realities of stakeholders.  Students will examine strategies and techniques used in a variety of fields to make research findings more useful and usable beyond the academy.  Experts from the areas of policy, media, and practice will offer their perspectives on knowledge transfer and the need for increased research impact.', 'ARTS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARTS 601', 'Building Community-University Research Alliances', 0.5, 'SEM', 'This interdisciplinary course examines the impediments to, as well as those developments that facilitate, community-university partnerships from historic, cross-cultural, and empirical perspectives.  Community is defined broadly to include local social service organizations, health practitioners, policy makers, and commercial enterprises.  Students develop research alliances relevant to their areas of study, skills, and interests.  Through the development of research alliances students learn how to facilitate the uptake of existing research to increase organizational capacity for sustainability and innovation.', 'ARTS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AVIA 101', 'Professional Pilot Program Course I', 0.75, '', 'This course provides the required Preparatory Ground Instruction to prepare students for the air exercises to be completed during in-aircraft instruction that is conducted as part of the course. Students will complete flight training to the First Solo level and acquire approximately 20 hours of flight time. This course also includes the ground school required to prepare the student to write the Transport Canada Private Pilot Licence - Airplane examination. The course provides a solid base of the knowledge required to be a safe and proficient professional pilot.', 'AVIA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AVIA 102', 'Professional Pilot Program Course II', 0.5, '', 'This course is a continuation of AVIA 101 and provides the required Preparatory Ground Instruction to prepare students for the air exercises to be completed during in-aircraft instruction that is conducted as part of the course. Students will complete flight training to the Private Pilot Licence level and acquire approximately 45 hours of flight time in the semester to total 65 hours of flight time to-date.', 'AVIA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AVIA 203', 'Professional Pilot Program Course III', 0.5, '', 'This course is a continuation of AVIA 102 and provides the required Preparatory Ground Instruction to prepare students for the air exercises to be completed during in-aircraft instruction that is conducted as part of the course. Students will complete flight training required to acquire the Night Rating and acquire additional cross-country Pilot-in-Command time to total approximately 85 hours flight time by the end of the course.', 'AVIA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AVIA 204', 'Professional Pilot Program Course IV', 0.75, '', 'This course continues from AVIA 203 and provides the required Preparatory Ground Instruction to prepare students for the air exercises to be completed during in-aircraft instruction that is conducted as part of the course. Students will complete a dual cross border flight to the United States as well as a dual flight into high density controlled airspace (CYYZ). Students also complete a check-out in a complex aircraft and acquire additional cross-country pilot-in-command time. Students will have acquired approximately 110 hours total flight time by the end of the course. This course also includes the ground school required to prepare the student to write the Transport Canada Commercial Pilot Licence - Airplane examination.', 'AVIA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AVIA 205', 'Professional Pilot Program Course V', 0.5, '', 'This course continues from AVIA 204 and provides the required Preparatory Ground Instruction to prepare students for the air exercises to be completed during in-aircraft instruction that is conducted as part of the course. Students will complete a solo cross border flight to the United States as well as a dual flight into a grass field. Students also complete a check-out in a glass cockpit aircraft and work on acquiring cross-country Pilot-in-Command time. Preparation for the Commercial Pilot Flight Test is started during this course as well. The student will have acquired approximately 155 hours total flight time by the end of the course.', 'AVIA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AVIA 306', 'Professional Pilot Program Course VI', 0.75, '', 'This course continues from AVIA 205 and provides the required Preparatory Ground Instruction to prepare students for the air exercises to be completed during in-aircraft instruction that is conducted as part of the course. Students will complete the Commercial Pilot Flight Test early in the course and then complete the training for the Multi-Engine Class Rating. Students will have acquired approximately 175 hours total flight time by the end of the course. This course also includes ground school to prepare students to write the Transport Canada Instrument Rating examination.', 'AVIA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AVIA 307', 'Professional Pilot Program Course VII', 0.75, '', 'This course continues from AVIA 306 and focuses on exercises that apply knowledge of various considerations and procedures for flight in instrument meteorological conditions. Pairs of students will function as two-person flight crews during simulated IFR flights. Scenarios will enable students to recognize, avoid or manage situations that lead to incidents. Also included is a tour of the Toronto Area Control Centre. The flight portion focuses on acquiring cross-country Pilot-in-Command time to meet the requirements for the Instrument Rating. This course also includes ground school in preparation for students to write the Transport Canada IATRA examination and training in wilderness survival and First Aid skills.', 'AVIA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AVIA 310', 'Human Factors in Aviation', 0.5, 'LAB, LEC', 'A case study-influenced course emphasizing the need for pilots to recognize and improve interpersonal skills for problem solving and conflict management. Components introduce Crew Resource Management (CRM), the human component of the human-technology interface, and the cumulative act effect.', 'AVIA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AVIA 408', 'Professional Pilot Program Course VIII', 0.5, '', 'This course continues from AVIA 307 and completes the instruction toward the Group 1 Instrument Rating. Training will be conducted using a combination of Simulator, Single Engine aircraft and Multi-engine aircraft. Flight in instrument meteorological conditions will be conducted whenever practicable. Any outstanding flight time requirements for the issue of the Commercial Pilot Licence will also be completed. This course completes the flight training.', 'AVIA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AVIA 474', 'Special Topics in Aviation', 0.5, 'LEC, SEM, TUT', 'An advanced special topics course offered in a particular branch of aviation, subject to availability of instructor.', 'AVIA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AVIA 475', 'Independent Studies of Selected Topics', 0.5, 'RDG', 'Individual study of special topics not covered in other aviation courses. Students will not be given permission to register for this course until a faculty member has agreed to supervise the study and the student has developed a brief outline of study to be approved by the Director of Aviation.', 'AVIA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BE 600', 'Management and Leadership', 0.5, 'LEC, RDG, SEM', 'This course provides students with the opportunity to develop a range of soft skills and the business acumen necessary to maximize the likelihood of business success. Topics include communication and interpersonal skills, leadership and negotiation skills. A range of applied approaches are used, including integrated cases, simulations and interactions with the local business community.', 'BE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BE 601', 'Introduction to Financial and Managerial Accounting', 0.5, 'LEC, RDG, SEM', 'The course introduces students to the core elements of financial and managerial accounting.  Specifically, the course objectives are to 1) assist managers to become proficient at interpreting numbers in financial statements and assessing their usefulness for business decisions, and 2) assist managers to understand the vital role that internal accounting information, or management accounting, plays in making better planning, organizing, controlling, improvement and compensation decisions.  MEng and GDip students only.', 'BE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BE 602', 'Data Analysis and Management', 0.5, 'LEC, RDG, SEM', 'Introduces students to the basic tools in using data to make informed management  decisions.  Coverage includes introductory probability, decision analysis, basic statistics, regression, simulation and linear optimization.  Computer spreadsheet exercises, cases and examples will be drawn from marketing, finance, operations manangement and other management functions.  MEng and GDip students only.', 'BE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BE 603', 'Operations and Supply Chain Management', 0.5, 'LEC, RDG, SEM', 'Introduces students to supply chain and operations management.  Covers systems integration, scheduling, forecasting, inventory management, quality management, lean thinking, supply chain management, supply chain collaboration and people and operations.  The course uses case studies, a simulation, class discussions, exercises and lectures covering a broad range of operations environments.  MEng and GDip students only.', 'BE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BE 604', 'Marketing Management', 0.5, 'LEC, RDG, SEM', 'This course looks at how organizations develop a market orientation and examines the development of marketing strategy in relation to target markets selected, competitive advantages created and exploited and market positioning.  The marketing mix of product, price promotion and distribution and the new product development process are also covered. MEng and GDip students only.', 'BE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BE 605', 'Project Management', 0.5, 'LEC, RDG, SEM', 'This course introduces students to the key areas of Project Management.  Specifically, the course objectives are to: 1) assist managers in identifying and scheduling project resources, 2) assist managers to conceptualize, plan, schedule, control and execute projects, 3) assist managers to be conversant in project risk management and 4) enable managers to gain a better understanding of the comprehensive nature of project management, including subjects such as best practices, leadership skills and operations.  MEng and GDip students only.', 'BE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BE 606', 'Entrepreneurship and Innovation', 0.5, 'LEC, RDG, SEM', 'This course introduces students to the theory underlying entrepreneurship, venture creation and innovation management, as well as its practical implications.  Topics covered include introduction to entrepreneurship and innovation, the dynamics of innovation, corporate entrepreneurship and commercializaiton, venture creation and the management of high-performance innovative teams. MEng and GDip students only.', 'BE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BE 610', 'Special Topics in Business and Entrepreneurship', 0.5, 'LEC, RDG, SEM', 'None', 'BE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BET 300', 'Foundations of Venture Creation', 0.5, 'LEC', 'This course provides students with an introduction to the processes involved in moving an idea for a new venture from concept through to launch. The theoretical knowledge and practical skills needed to create a successful small entrepreneurial enterprise are developed. Topics include definition and evaluation of entrepreneurial opportunities, business planning, funding strategies and early-stage revenue models, legal issues and intellectual property protection. [Offered: F, W, S]', 'BET');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BET 400', 'Marketing Foundations for Early-stage Ventures', 0.5, 'LEC', 'This course develops the theoretical knowledge and practical skills needed to develop a marketing strategy for early-stage ventures. Topics include managing product development, marketing and brand development, segmentation and targeting, competitor analysis, and positioning. [Offered: W]', 'BET');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BET 600', 'Applied Business Leadership Skills for Entrepreneurs', 0.5, 'LEC', 'The course provides students with the opportunity to develop a range of soft skills and the business acumen necessary to maximize the likelihood of success in an entrepreneurial environment.  Topics include communication and interpersonal skills, leadership, and negotiation skills.  A range of applied approaches are used, including integrated cases, simulations, and interaction with the local business community.  Most activities are team based.', 'BET');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BET 601', 'Strategically Managing the Entrepreneurial Organization', 0.5, 'LEC', 'Strategy development and implementation are examined as a means to guide decisions at each stage of the innovation and commercialization process. Insight is gained into the strategic issues faced by new ventures as they progress from seed/concept through market execution stages.  Strategic analysis techniques are used to identify and analyse issues and as input into the design of the business concept and business model.  The organizational structures, processes and policies used to build and maintain an entrepreneurial culture are key topics for the market execution stage.', 'BET');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BET 602', 'Marketing Strategies for New Technology-based Ventures', 0.5, 'LEC', 'Market-based management practices and the key relationships that enable businesses to attract, satisfy and retain customers, and grow their profits are examined.  The contribution of marketing strategy to the creation of value for both customers and shareholders is emphasized.  Particular attention is paid to the unique contexts of marketing knowledge-intensive products and services, new and rapidly growing markets and business markets.  Familiarity is developed with the marketing activities typical for each of the seed/concept, product development and market execution stages of a new venture.', 'BET');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BET 603', 'Entrepreneurial Finance for the Technology-based Enterprise', 0.5, 'LEC', 'This course will provide entrepreneurs and enterprise intrapreneurs with an understanding of financing practices and principles relevant to entrepreneurial opportunities. Classes will feature a combination of theory and "real world" practices drawing from the instructor''s experiences.', 'BET');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BET 604', 'New Technology-based Venture Creation', 0.5, 'LEC', 'The creation of new ventures is a defining attribute of entrepreneurs.  While the specific focus will be on creating new independent ventures, the theory and principles discussed in this course can be applied to creating new ventures within existing corporation and to new social ventures. 
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BET 605', 'Essential Accounting for Entrepreneurs', 0.5, 'LEC', 'The purpose of this course is to introduce students to how accounting information can be used by entrepreneurs and enterprise intrapreneurs to manage an enterprise, improve decision making, and implement organizational strategy as the venture proceeds from start-up to SME to maturity.  The course is split into two parts: Financial accounting (external use of financial information) and Management Accounting (the internal use of financial information).', 'BET');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BET 607', 'Managing Technological Innovation', 0.5, 'LEC', 'Management of technological innovation requires an understanding of the interaction of technology with all aspects of the organization to build and maintain a sustainable competitive advantage.  The focus of this course is the creation of new products and services from concept through to launch.  Evaluating new technologies, product development and deployment strategies are key topics.', 'BET');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BET 608', 'Entrepreneurial Application of Technology', 0.5, 'LEC', 'This course is designed to leverage the high-potential complementarities between MBET entrepreneurship students and University of Waterloo and Waterloo Region technology developers.  The objectives are high-potential business models and licensing plans to facilitate the launch of technology-based commercial or social ventures and technology commercialization, enriched linkages between MBET and University of Waterloo researchers and technology developers, and experience in networking, opportunity assessment, partnership development, and working in and managing diverse teams.', 'BET');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BET 620', 'Social Entrepreneurship and Corporate Social Responsibility', 0.5, 'LEC', 'Introduces students to the concept of social entrepreneurship from a multidisciplinary perspective.  Participants are challenged to rethink the historical focus on the single bottom line by showcasing emerging trends in how to make money and social impact as mutually reinforcing concepts.  The course provides access to the latest thinking on social enterprises; emerging legal structures and new approaches to financing social purpose work.', 'BET');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BET 700', 'Topics in Business, Entrepreneurship and Technology', 0.5, 'RDG', 'None', 'BET');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 110', 'Introductory Zoology', 0.5, 'LAB, LEC, TST', 'A study of the functional morphology of selected animals with special emphasis on the various grades of organization and development in the different phyla. [Offered: F]', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 112', 'Introductory Biology 2', 0.5, 'LEC, TUT', 'An introduction to the basic principles of the structure and function of plants and animals within an ecological and evolutionary framework. The biology of multicellular organisms will be emphasized.', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 120', 'Introduction to Plant Structure and Function', 0.5, 'LAB, LEC, TST', 'A brief introduction to plant diversity, and the anatomy and physiology of vascular plants. The course will include a description of major cell and tissue types, and their organization in roots, stems, and leaves. Topics such as the processes of water and ion uptake, photosynthesis, long distance transport, and growth regulation will also be covered. [Offered: W,S]', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 130', 'Introductory Cell Biology', 0.5, 'LEC, TST', 'An introduction to the concepts of cell biology with emphasis on (1) the structural organization of the cell and (2) the function of critical molecular processes that are characteristic of living organisms.', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 130L', 'Cell Biology Laboratory', 0.25, 'LAB', 'Experiments to study the principles of cell biology that are elaborated in BIOL 130. [Offered: F,S]', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 150', 'Organismal and Evolutionary Ecology', 0.5, 'LEC', 'This course provides students with an introduction to the basic principles of Scientific Reasoning, Ecology and Evolution. Coverage includes hypothesis testing and the nature of scientific inquiry, basic population genetics, physiological ecology, life histories, dispersal, basic population and community ecology, macroevolution, systematics and classification, as well as functional morphology. [Formerly BIOL 250. Offered: F]', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 165', 'Diversity of Life', 0.5, 'LEC', 'An introduction to the diversity of living organisms from simple prokaryotes to complex eukaryotes. Current ideas on classification and phylogeny will be compared with traditional schemes. Morphology, ecology, and economic uses of representative Phyla and Divisions will be discussed. [Formerly BIOL 265. Offered: W]', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 211', 'Introductory Vertebrate Zoology', 0.5, 'LAB, LEC, TST', 'An introduction to the structure, evolution and development of vertebrate organ systems. [Offered: W]', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 225', 'Plants and Civilization', 0.5, 'LEC', 'This course examines the importance of plants and the role they have in the local and global community. It introduces students to the impact of plants on the past, present and future of human civilization and describes the importance of plants as sources of food, medicine, fuel, shelter, clothing and psychoactive agents. Topics include medicinal plants and human health, the origin of agricultural crops, plants that changed the course of history, plant-based biofuels, the green revolution, plant biotechnology and genetic engineering of plants. [Offered: F]', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 239', 'Genetics', 0.5, 'LEC, TUT', 'Mendelian genetics. Chromosomal mechanisms in mitosis and meiosis. The origin, inheritance and adaptive significance of chromosomal changes. Nucleic acids as the carriers of genetic information. Natural selection and the evolution of genetic systems. [Formerly BIOL 139. Offered: W,S; online F]', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 240', 'Fundamentals of Microbiology', 0.5, 'LEC', 'Introduction to the biology of bacterial and archaeal organisms. Topics include cell structure and function, methods of cultivation, genetics, phylogeny and taxonomy, and metabolic and genetic diversity.', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 240L', 'Microbiology Laboratory', 0.25, 'LAB', 'Experiments to study the principles of microbiology that are elaborated in BIOL 240. [Formerly BIOL 140L. Offered: S, F]', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 241', 'Introduction to Applied Microbiology', 0.5, 'LAB, LEC', 'Introduction to microbial ecology, environmental microbiology, food microbiology and medical microbiology. Topics in environmental microbiology include biogeochemical cycling and biological treatment of wastes and pollutants. Topics in medical microbiology include concepts of immunology and host-parasite relationships. [Offered: W,S]', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 273', 'Principles of Human Physiology 1', 0.5, 'LEC, TST', 'The physiology of major organ systems of the human body. Topics include neurophysiology and peripheral nervous system, muscle, the cardiovascular system, the components of blood, respiratory system and immune system. The combination of BIOL 273 and BIOL 373 covers all of the major topics of human physiology.', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 273L', 'Human Physiology 1 Laboratory', 0.25, 'LAB', 'Experiments to study the principles of human physiology that are elaborated in BIOL 273. [Offered: W,S]', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 280', 'Introduction to Biophysics', 0.5, 'LEC', 'Introduction to a physical understanding of biological systems at macro and molecular scales. The course is intended for 2nd year science and engineering students and will cover a broad spectrum of topics in biophysics, as well as an introduction to neurobiology, nanotechnology and biotechnology. [Offered: W,S]', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 301', 'Human Anatomy', 0.5, 'LAB, LEC', 'This course will survey the basic human anatomical features of the skeletal, muscular, nervous, cardiovascular, endocrine and reproductive systems. Emphasis will be placed on functional and clinical anatomy.', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 360', 'Evolution 2: Fossil Record', 0.5, 'LEC, TUT', 'An overview of the origin of life, how it evolved and diversified, modern principles of paleontology with particular emphasis on analytical tools to interpret ancient life forms. Patterns, processes of evolution and paleoecology of key groups of microfossils, invertebrates, plants, vertebrates and their trace fossils. [Offered: W]', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 302', 'Functional Histology', 0.5, 'LAB, LEC', 'A hierarchical approach to biological structure with an emphasis on functional morphology. Starting with the cell, the fundamental unit of structure and function, the material progressively develops how cells organize to form tissues such as epithelium, connective tissue and muscle. Emphasis on how these tissue building blocks cooperate to form the major organs and organ systems of the human body. [Offered: W]', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 303', 'Introductory Developmental Biology and Embryology', 0.5, 'LEC', 'Fundamental processes and concepts in embryonic development including the acquisition of multicellularity, organization of the early embryo, morphogenesis of tissues, major organ systems, fetal membranes, growth, differentiation and analysis of common developmental defects. [Offered: F, S]', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 308', 'Principles of Molecular Biology', 0.5, 'LEC', 'Prokaryote and eukaryote genome structure and replication; mechanisms of gene expression and regulation.', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 309', 'Analytical Methods in Molecular Biology', 0.5, 'LEC, TUT', 'An introduction to molecular methods used to analyze the structure of genes and genomes, including DNA sequencing, cloning, restriction mapping and bioinformatic tools. Techniques to monitor transcript and protein abundance, protein-protein and DNA-protein interactions will also be covered.', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 310', 'Invertebrate Zoology', 0.5, 'LAB, LEC', 'The diversity of invertebrate animals will be explored in this class. Topics covered will include reproduction, development, life history, feeding, locomotion, and behaviour. [Offered: F]', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 321', 'Plant Anatomy and Morphogenesis', 0.5, 'LAB, LEC', 'Plant structure in relation to tissue formation and development with particular reference to the angiosperms. Cell, tissue and organ differentiation will be discussed. Phenotypic variation in response to environmental influences will also be covered. [Offered in Fall of even years]', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 323', 'Plant Physiology', 0.5, 'LAB, LEC', 'A study of plant physiological processes with an emphasis on the role of key metabolic pathways in plant growth and development. Topics such as photosynthesis, nitrogen assimilation, growth regulators, mineral nutrition, water relations, and stress physiology will be covered. [Offered in Fall of odd years]', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 325', 'Flowering Plants', 0.5, 'LAB, LEC', 'A study of floral morphology in relation to classification and evolution. An introduction to taxonomy and nomenclature. History of taxonomy. Systems of classification. Mechanisms of pollination. [Offered: F]', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 331', 'Advanced Cell Biology', 0.5, 'LAB, LEC, TST', 'The functional organization of cells with particular reference to cell-cell interaction, the structure, function and development of organelles and the biological roles of cellular membranes. [Offered: W]', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 335L', 'Molecular Biology Techniques', 0.5, 'LAB, TUT', 'Selected experiments to provide students with a range of laboratory skills in recombinant DNA technology.', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 342', 'Molecular Biotechnology 1', 0.5, 'LEC, TUT', 'Molecular biotechnology applies the principles of recombinant DNA technology (genetic engineering, gene cloning) to the development of commercial products. The methods of recombinant DNA technology, molecular diagnostic systems for detecting diseases and transgenic organisms will be discussed.', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 345', 'Microorganisms in Foods', 0.5, 'LAB, LEC', 'Food preservation, spoilage, poisoning and modern concepts in quality assurance programs are studied. The aim is to understand factors governing microbial changes in foods. Problem solving in the food industry is emphasized. Laboratory work will reflect current practices in quality control and testing.', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 346', 'Microbial Ecology and Diversity', 0.5, 'LAB, LEC', 'Examples from terrestrial and aquatic ecosystems, and plant- and animal-associated environments, will be used to illustrate the activities and diversity of microorganisms in these habitats. The importance of the ecological roles of microbes to aspects of agriculture, geochemistry, human biology, and the biology of extreme environments will be considered. [Offered: W]', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 348L', 'Laboratory Methods in Microbiology', 0.5, 'LAB, TUT', 'Selected experiments to provide students with a range of laboratory skills in microbiology. [Offered: W]', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 349', 'Synthetic Biology Project Design', 0.5, 'PRJ, TUT', 'Synthetic biology involves developing new approaches, based on engineering principles, for genetic engineering of biological systems. Students will prepare a comprehensive research proposal for a synthetic biology project of their own design, under the supervision of a faculty member. Attendance at a weekly journal club focused on synthetic biology will be mandatory [Offered: F,W,S].', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 350', 'Ecosystem Ecology', 0.5, 'LEC, TUT', 'This course provides an overview of the dynamic interactions among microbes, plants, animals and their physical environment with emphasis on ecosystem structure and function. Topics include the hydrological cycle, biogeochemical cycling, ecological energetics, roles of population and community interactions, paleoecology and current topics in ecosystem science. [Offered: F]', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 351', 'Aquatic Ecology', 0.5, 'LEC', 'Study of the structure and function of lake and stream ecosystems. The course emphasizes biological components and processes, but includes the origin and nature of lake and stream systems and the fundamentals of surface water chemistry and physics. Human influences, management options and current issues will be examined with readings and project work.', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 354', 'Environmental Toxicology 1', 0.5, 'LAB, LEC, TST', 'An introduction to the basic theories, principles and techniques of environmental toxicology. A comparative study of the effects of specific groups of toxicants on ecosystems; biodegradation and cycling. [Offered: F,S]', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 355', 'Biology of Human Aging', 0.5, 'LEC', 'An overview of current aspects of the biology of human aging and the functional changes associated with both normal aging and pathological problems often present in the elderly. Biological theories of aging and normal age-related structural and physiological changes on all of the major body systems will be discussed. Extrinsic factors such as diet and exercise and their role on the aging process will be examined as well as various medical treatments. [Offered: W]', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 359', 'Evolution 1: Mechanisms', 0.5, 'LEC, TST', 'A study of the processes of evolution; the differentiation of populations and the origin of new forms of life. [Offered: F, W; online S]', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 361', 'Biostatistics and Experimental Design', 0.5, 'LAB, LEC', 'An introduction to hypothesis testing and experimental design in Biology. Topics will include: exploratory data analysis, analysis of variance, categorical data analysis, correlation, linear regression, multiple linear regression, power analysis, visualization tools and statistical software. [Offered: W]', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 364', 'Mathematical Modelling in Biology', 0.5, 'LAB, LEC', 'An introduction to the mathematical modelling of biological processes using a variety of techniques including linear difference models, ordinary differential equations, and Markov models. Modelling applications ranging from genetics to ecosystem biology will be addressed. [Offered: F]', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 365', 'Methods in Bioinformatics', 0.5, 'LAB, LEC', 'This course covers bioinformatics methods with an emphasis on analysis of high-throughput `-omics'' data. Topics include analysis of genome-scale gene and protein expression, construction of species and gene trees from molecular sequence data, and analysis of biological systems using bioinformatics tools. Included will be practical experience with bioinformatics tools and datasets.
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 366', 'Introduction to Bioinformatics', 0.5, 'LEC', 'An introduction to the basics of bioinformatics including computational tools and databases used in the collection and analysis of DNA, RNA and proteins. [Notes: Offered in even numbered years. Offered: W]', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 370', 'Comparative Animal Physiology: Environmental Aspects', 0.5, 'LEC', 'A comparative study of salt and water balance, circulation, respiratory systems, nitrogenous excretion, and mechanisms of energy acquisition and metabolism in animals. Adaptations to different environments will be emphasized. [Offered: F]', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 371', 'Comparative Animal Physiology: Evolutionary Themes', 0.5, 'LEC', 'A comparative study of neural, sensory, endocrine, digestive and reproductive strategies across animal taxa. The emphasis will be on evolutionary themes at the organismal, cellular and molecular levels. [Offered: W]', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 373', 'Principles of Human Physiology 2', 0.5, 'LEC', 'The physiology of major organ systems of the human body. Topics include the central nervous system, the sense organs, the digestive system, the endocrine system, the reproductive system, and the excretory system. [Offered: W]', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 373L', 'Human Physiology 2 Laboratory', 0.25, 'LAB, TST', 'Laboratory exercises to study the principles of human physiology that are described in BIOL 373. [Offered: W]', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 376', 'Cellular Neurophysiology', 0.5, 'LAB, LEC', 'This course gives students background in neuronal signaling, ion channels, and methods in electrophysiological recording methods. The course requires completion of neuro-simulation laboratories, which provide an introduction to biophysical modelling to explain the functions of neural systems.', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 377', 'Systems Neuroscience: From Neurons to Behaviour', 0.5, 'LEC, TUT', 'Systems neuroscience attempts to explain complex functions of the nervous system in terms of underlying neural circuits and molecules. A diverse range of systems - from sea snails and fruit flies to rodents and humans - with the help of interactive computer simulations will be used to illustrate principles of systems neuroscience.', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 382', 'Computational Modelling of Cellular Systems', 0.5, 'LEC, TUT', 'An introduction to dynamic mathematical modeling of cellular processes. The emphasis is on using computational tools to investigate differential equation-based models. A variety of cellular phenomena are discussed, including ion pumps, membrane potentials, intercellular communication, genetic networks, regulation of metabolic pathways, and signal transduction.', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 383', 'Tropical Ecosystems', 0.5, 'LEC', 'This course examines the fundamental concepts of terrestrial ecosystems in tropical climates. The course has three sections: (1) biophysical aspects (climate, location, landforms, soil, vegetation), (2) tropical resource systems (forest- and agroecosystems) within the framework of conventional and sustainable resource extraction, and (3) current conservation issues. Case studies are presented.', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 403', 'Advanced Topics in Developmental Biology', 0.5, 'LEC', 'This course explores the cellular and molecular basis of developmental phenomena in animals. Lectures will emphasize the experimental basis for both historical and contemporary knowledge with a focus on the major developmental systems in model organisms. Course content will concentrate on themes that permeate current published research.', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 412', 'Arthropod Zoology', 0.5, 'LAB, LEC', 'A survey of the phylum Arthropoda, including the insects, with emphasis on their classification, interrelationships and ways of life. [Offered: W]', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 426', 'Phycology', 0.5, 'LAB, LEC', 'Algae and applied uses of algae. Topics include examination of algal groups from evolutionary and ecological perspectives. Uses of algae in industry and food as well as negative impacts of various algal groups. [Notes: Offered in odd numbered years. Offered: W]', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 428', 'Plant Molecular Genetics', 0.5, 'LAB, LEC, TUT', 'An examination of the current molecular techniques used to study plant development physiology. Topics include mutant isolation, transcript and metabolite profiling, gene silencing and protein localization. [Offered: W]', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 431', 'Bacterial Molecular Genetics', 0.5, 'LEC', 'Bacterial molecular biology with an emphasis on the use of genetic tools to study the biology of microorganisms. Topics include mutagenesis, conjugation, recombination, gene regulation, plasmids, transposons, bacteriophage and genomics. [Offered: W]', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 432', 'Molecular Biotechnology 2', 0.5, 'LEC, TST', 'How recombinant DNA technology is used to produce vaccines, pharmaceuticals, crop plants, and other commercial products will be discussed. [Offered: W]', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 433', 'Plant Biotechnology', 0.5, 'LEC', 'Biotechnological approaches and their applications in plant genetic manipulation, transformation and cell culture for plant improvement, propagation, and biochemical production. [Offered: W]', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 434', 'Human Molecular Genetics', 0.5, 'LEC, SEM', 'Recent advances in human molecular genetics will be examined with emphasis on how human disease-causing genes are mapped, identified, isolated and characterized. Examples will draw from research on Duchenne muscular dystrophy, Huntington disease, cystic fibrosis, Alzheimer disease, cancer, vision defects and other disorders. [Offered: F]', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 438', 'Molecular Biology of Animal Development', 0.5, 'LEC, SEM', 'An examination of the current major issues in the regulation of gene expression during animal development with emphasis on technical and conceptual advances. Current research literature will be reviewed. [Offered: W]', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 439', 'Environmental and Natural Products Biochemistry', 0.5, 'LEC, TUT', 'This course deals with the functions, distribution and environmental ramifications of natural compounds produced by plants and other biological systems. Natural products are those compounds usually described as secondary metabolites, i.e. those apparently non-essential products whose physiological and ecological functions are either obscure or are of peripheral importance to the organism. However, many of these non-essential products have profound competitive, economic and pharmacological significance; and as research proceeds, their physiological roles within the parent organisms are becoming clearer. As well, this course has a strong emphasis on how environmental chemical and physical processes impact on living organisms and their biochemistry. [Offered: F]', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 441', 'Advances in Immunology', 0.5, 'LEC, SEM', 'A survey of the recent advances in the vertebrate immune response; the cells and tissues of the lymphoid system; humoral and cell-mediated immunity; initiation and regulation of the immune response; the immune system and disease, emerging techniques used in immunology. [Offered: W]', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 442', 'Virology', 0.5, 'LAB, LEC', 'A survey of viral structures, life cycles, and the interactions of viruses with their hosts. The laboratory component will include procedures used for viral detection and titration, as well as individual library research projects. [Offered: W]', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 443', 'Fermentation Biotechnology', 0.5, 'LAB, LEC', 'Biology of industrial micro-organisms: fermentation systems; fermentation raw materials; downstream processing; biomass production; food fermentations; production of industrial chemicals, food additives, enzymes and other products by fermentation. [Offered: F]', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 444', 'Bacterial Pathogenesis', 0.5, 'LAB, LEC', 'A study of the bacteria involved in pathogenesis, their mode of infection, symptoms and prevention of diseases. The laboratory will focus on identification of bacterial isolates. [Offered: F]', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 447', 'Environmental Microbiology', 0.5, 'LEC, TUT', 'A study of the environmental impact of microorganisms. Aspects of pollution, waste treatment, biodegradation of environmental contaminants, and nutrient cycling will be examined. [Offered: F]', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 448', 'Microbial Physiology and Biochemistry', 0.5, 'LAB, LEC', 'A study of the physiology of microorganisms. Provides biochemical and molecular level detail on the diverse structures and metabolic functions of bacterial, archaeal, and eukaryal cells. Aspects of microbial growth, nutrition and metabolism are examined in the context of how microorganisms develop diverse solutions for meeting essential requirements for life. [Offered: F]', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 449', 'Public Health Microbiology', 0.5, 'LEC', 'How the health of human populations is influenced by microbial communities. Aspects of the epidemiology of infectious diseases, factors influencing the emergence of microbial pathogens and antibiotic-resistant strains, means to control undesired microbial populations in habitats such as water, biodegradable materials and food will be discussed. Approaches to the regulation and monitoring of microbial agents will also be examined.', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 450', 'Marine Biology', 0.5, 'LEC, SEM', 'Ecological processes and evolutionary adaptation are explored in the world''s largest and most diverse ecosystems. The major ocean habitats will be characterized, stressing their importance as resources, moderators of climate and reservoirs of biodiversity. [Notes: Offered: W]', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 452', 'Quantitative Fisheries Biology', 0.5, 'LEC', 'The practices of fisheries science including the effects of industrial fisheries on fish stocks, methods of capture, obtaining, using and interpretation of vital statistics of fish stocks, population estimation, stock-recruitment, growth, mortality and fecundity. Emphasis is placed on the use of statistical information for making ecological inferences about the status of fish populations. Familiarity with linear regression is essential. [Offered: F]', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 455', 'Ecological Risk Assessment and Management', 0.5, 'LEC, TUT', 'Examination of the use of scientific information characterizing the risks posed to the environment by anthropogenic stresses. Discussions will take place in the context of aquatic ecology and presume a background of standard aquatic toxicology methods. Methods for assessing risks, including environmental impact assessment, risk quotients, national and international risk assessment paradigms, and cumulative effects assessment will be examined. Critical connections between assessment and management will also be discussed. [Offered: F of even years]', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 456', 'Population Biology', 0.5, 'LEC, TUT', 'The analysis of the structure and dynamics of plant and animal populations. Theoretical, mathematical and experimental approaches to the study of population ecology.', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 457', 'Analysis of Communities', 0.5, 'LEC, TUT', 'A study of the organization, structure and development of communities with emphasis on vegetation change. Topics include: diversity, stability; succession; sampling procedures and multivariate analysis. [Offered: W]', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 458', 'Quantitative Ecology', 0.5, 'LAB, LEC', 'The course addresses the integration of mathematical and statistical tools to solve problems in ecology, using examples from individual, population, community, and ecosystem scales. Coursework will explore the relationships between models, hypotheses, experiments and data.', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 461', 'Advanced Biostatistics', 0.5, 'LEC', 'Advanced aspects of statistics and experimental design for biologists. Topics will include analysis of variance (factorial, hierarchical and blocking designs; fixed- and random-effects models); a-priori and a-posteriori comparisons; multivariate analysis of variance; analysis of covariance; multiple linear regression; multivariate statistics (indirect and direct gradient analysis). [Offered: W]', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 462', 'Applied Wetland Science', 0.5, 'LEC, TUT', 'Advanced concepts on wetland ecosystems in the context of regional and global earth systems processes such as carbon and nitrogen cycling and climate change, applications of wetland paleoecology, use of isotopes and other geochemical tools in wetland science, and wetland engineering in landscape rehabilitation and ecotechnology. Current issues in Canada and abroad will be examined. [Offered: F]', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 465', 'Structural Bioinformatics', 0.5, 'LEC, TUT', 'Functional and structural analysis of proteins using bioinformatics tools. Topics include protein structure visualization, structure comparison and prediction, prediction of protein function and interactions, molecular dynamics, and protein design. [Offered: W]', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 466', 'Biogeochemical Microbiology', 0.5, 'LEC', 'A study of the microbial contribution to Earth''s biogeochemical cycles. This course examines the bacterial and archaeal microbial communities that contribute to nutrient cycling in terrestrial and aquatic environments. Emphasis is placed on the discovery of new organisms and enzymes involved in these processes and the use of innovative methodologies to explore their ecology', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 467', 'Plant-Bacterial Interactions', 0.5, 'LEC, SEM', 'Molecular and biochemical mechanisms used by soil bacteria; bacterial genes involved in plant growth promotion; biocontrol of pathogens; communication between bacteria in the soil; environmental cleanup facilitated by plants and bacteria; regulatory and social issues regarding the deliberate release of bacteria to the environment.', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 470', 'Methods of Aquatic Ecology', 0.5, 'LAB, TUT', 'An introduction to methods used to sample and characterize the ecological structure and function of aquatic ecosystems, including basic aspects of the abiotic environment. Field trips to lake and stream sites are combined with laboratory analysis of samples and data. While the main emphasis is on the techniques, the exercises also provide direct experience with some of the natural and anthropogenic variation observable in aquatic systems.', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 472', 'Cell Biology of Human Disease', 0.5, 'LEC', 'This course focuses on the nature and mechanisms of disease processes. Abnormalities in the structure and function of cells, tissues and organs that underlie disease are explored. Emphasis is placed on current research aimed at understanding the mechanisms of disease and disease therapy, covering topics including inherited disorders, cellular pathology and immunology, diabetes and cancer.', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 473', 'Mammalian Reproduction', 0.5, 'LAB, LEC', 'This course will provide an in-depth coverage of reproductive biology in a range of mammalian species. Emphasis will be on the principles underlying the regulation of key reproductive processes, from the whole animal to the molecular level. Topics will include applications of these principles to human and veterinary medicine, and ethical problems posed by some reproductive technologies. [Offered: F]', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 474', 'Bioprocessing', 0.5, 'LEC, TUT', 'The course will provide an understanding of the principles and practices of processing biological materials, which represents an essential core activity of the biotechnology, agri-food, chemical and pharmaceutical industries. Individual processing operations involved and important industrial processes will be developed in this course. [Offered: W]', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 475', 'Current Topics in Microbiology', 0.5, 'LEC', 'Students will explore and analyze a variety of often controversial topics in microbiology as reflected in popular culture and the current scientific literature. This course involves significant in-class group work. An essay and presentations are required. [Offered W]', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 476', 'Systems Neuroscience: From Neurons to Behaviour', 0.5, 'LEC', 'Systems neuroscience attempts to explain complex functions of the nervous system in terms of underlying neural circuits. The course focuses on how neural circuits analyze sensory information, form perceptions, make decisions, and execute movements, as well as contribute to high-level mental functions such as language, memory, and self-awareness.', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BME 399', 'Directed Biomedical Research Project', 0.25, 'PRJ', 'A biomedical research project carried out under the supervision of a faculty member. Students interested in graduate studies or industrial research careers will gain experience in advanced research techniques and develop valuable research skills. A written report is to be submitted to the course co-ordinator. This course is extra over and above the normal course load.', 'BME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 477L', 'Techniques in Animal Physiology', 0.5, 'LAB, TUT', 'Laboratory exercises and computer-based data collection to study the functions of the cardiovascular, respiratory, nervous, digestive and excretory systems of vertebrates and invertebrate animals. Students will learn animal handling techniques, data collection with state of the art physiological equipment and data analysis using computer software. Tutorials will allow students to investigate and discuss current animal physiology research topics in greater detail. [Formerly BIOL 374L. Offered: W]', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 479', 'Population Genetics and Evolution', 0.5, 'LEC', 'This course provides an overview of the interplay among mutation, random and non-random mating, genetic drift, gene flow and selection within and among natural populations. Special emphasis is placed on the dynamic role these factors play in the process of population divergence, and ultimately speciation. Case studies will illustrate the application of population genetic methodology in fields such as ecology, conservation biology, and forensic sciences. [Offered: F]', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 480', 'Molecular Ecology', 0.5, 'LEC', 'This course will provide an overview of the application of modern molecular methodologies such as DNA sequencing and microsatellite genotyping to Ecology and Organismal Biology. Topics covered will include parentage and mating systems, population analysis, species delineation and phylogeography. Special emphasis will be placed on the importance and use of molecular methods in conservation biology. [Offered: Winter in even numbered years]', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 483', 'Animal Cell Biotechnology', 0.5, 'LEC', 'A study of the techniques and applications of animal cell culture to biotechnology. Topics include basic cell culture technique, gene modification, products of animal cell culture, large-scale productions, and tissue engineering. [Offered: F]', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 484', 'Advanced Eukaryotic Genetics', 0.5, 'LEC, TUT', 'The role of classical genetic analysis in the context of today''s genomic era. Topics: meiotic recombination, meiosis, complementation analysis, chromosome aberrations, and genetic interactions. Methods used in model eukaryotic genetic organisms such as forward and reverse genetic screens, genetic mosaics, conditional mutants, and genetic mapping will be discussed. Examples will illustrate how genes continue to be identified using classical-based approaches in a variety of biological processes, including cell cycle progression, cancer and metastasis, learning and memory, as well as pattern formation and embryonic development. Alternatives to traditional mutagenesis-based screens and the links between classical and molecular genetics will also be discussed. [Offered: W]', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 485', 'Conservation Biology', 0.5, 'LEC', 'An introduction to conservation biology and the ongoing biodiversity crisis. Topics will include the history of conservation biology and impacts on populations, communities, and ecosystems. [Offered: W]', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 486', 'Glycobiology', 0.5, 'LEC, SEM', 'Monosaccharides and their linkages; conformations of oligosaccharides; glycoconjugates/glycoproteins: their physiological functions and how they are synthesized; O- and N-glycosylation; receptors and lectins; glycobiology of plants, viruses and microbes; glycobiology and disease; glycans as bio-energy sources.', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 487', 'Computational Neuroscience', 0.5, 'LEC, TUT', 'Introduction to quantitative principles in the analysis of neurophysiological systems. Biophysics of excitable membranes. Encoding of sensory information in neural spiking activity. Bayesian models in perception and motor control. Models of synaptic plasticity, learning, and memory. [Note for systems design engineering students: It is recommended that one of BIOL 273, 376, 377 or BME 284 or SYDE 384 be taken before or concurrently with SYDE 552. Offered: W]', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 488', 'Ecotoxicology from a Watershed Perspective', 0.5, 'LEC', 'The course will explore the scientific principles behind predicting the risk of environmental contaminants on ecosystems at the watershed scale. Using an environmental risk assessment framework, case studies will be used with an emphasis on local watershed issues, to more fully understand the mechanisms and processes that control and modify impacts of natural gradients and human activities on the ecology of drainage basins. The course may include a local field trip. [Offered W, even years] Field trip fee: $30-50 to offset cost of transportation.', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 489', 'Arctic Ecology', 0.5, 'LEC', 'This course explores the unique ecology of Arctic and sub-Arctic ecosystems. Topics addressed will include postglacial history, climate, permafrost, nutrient cycling and food web structure of terrestrial, marine, and freshwater systems, plant and animal adaptations to cold, human ecology, traditional ecological knowledge, climate change, resource extraction, and other human impacts.', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 490A', 'Biology Field Course I', 0.5, 'LAB, LEC', 'A two-week field study of terrestrial, aquatic and/or marine biology offered by the Ontario Universities Program in Field Biology (OUPFB). OUPFB offers a diverse array of courses annually that normally take place at off campus locations. Field courses consist of lectures, field exercises, and a small project chosen in consultation with the course instructor(s). Contact the Biology Department field course coordinator for additional information.', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 490B', 'Biology Field Course II', 0.5, 'LAB, LEC', 'A two-week field study of terrestrial, aquatic and/or marine biology offered by the Ontario Universities Program in Field Biology (OUPFB). OUPFB offers a diverse array of courses annually that normally take place at off campus locations. Field courses consist of lectures, field exercises, and a small project chosen in consultation with the course instructor(s). Contact the Biology Department field course coordinator for additional information.', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 491A', 'Aquatic Field Biology', 0.5, 'LAB, LEC', 'A two-week study of the flora and fauna of lakes, bogs and streams. Emphasis on biosystematics, distribution and dynamics of organisms. Both population and community approaches are stressed. This course will normally be held in Algonquin Park, Ontario each September. Courses sponsored by Ontario Universities Program in Field Biology, at other times of the year also qualify.', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 490C', 'Biology Field Course III', 0.5, 'LAB, LEC', 'A two-week field study of terrestrial, aquatic and/or marine biology offered by the Ontario Universities Program in Field Biology (OUPFB). OUPFB offers a diverse array of courses annually that normally take place at off campus locations. Field courses consist of lectures, field exercises, and a small project chosen in consultation with the course instructor(s). Contact the Biology Department field course coordinator for additional information.', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 490D', 'Biology Field Course IV', 0.5, 'LAB, LEC', 'A two-week field study of terrestrial, aquatic and/or marine biology offered by the Ontario universities program in field biology (OUPFB). OUPFB offers a diverse array of courses annually that normally take place at off campus locations. Field courses consist of lectures, field exercises, and a small project chosen in consultation with the course instructor(s). Contact the Biology Department field course coordinator for additional information.', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 492', 'Marine Mammals and Seabirds', 0.5, 'LAB, LEC', 'A three-week field course at the Huntsman Marine Science Centre, St. Andrews, NB. Marine mammals and seabirds will be observed under natural conditions through frequent field trips at sea and ashore. There is a strong emphasis on field research and each student will complete an independent research project. Lectures and labs will introduce the evolution, zoogeography, morphology, ecology, physiology, and behaviour of diving air-breathing vertebrates.', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 496', 'Neuroscience Research Seminar', 0.5, 'LEC, SEM', 'Current and classical topics in neuroscience research will be explored, discussed and presented by students. [Offered: W, even-numbered years]', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 498A', 'Short Biology Field Course 1', 0.25, 'LAB, LEC', 'A one-week field study of terrestrial, aquatic and/or marine biology offered by the Ontario Universities Program in Field Biology (OUPFB). OUPFB offers a diverse array of courses annually that normally take place at off campus locations. Field courses consist of lectures, field exercises, and a small project chosen in consultation with the course instructor(s). Contact the Biology Department field course coordinator for additional information.', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BUS 121W', 'Functional Areas of the Organization (WLU)', 0.5, 'LAB, LEC', 'Taught at Wilfrid Laurier University. Refer to WLU Undergraduate Calendar for course description.', 'BUS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 498B', 'Short Biology Field Course 2', 0.25, 'LAB, LEC', 'A one-week field study of terrestrial, aquatic and/or marine biology offered by the Ontario Universities Program in Field Biology (OUPFB). OUPFB offers a diverse array of courses annually that normally take place at off campus locations. Field courses consist of lectures, field exercises, and a small project chosen in consultation with the course instructor(s). Contact the Biology Department field course coordinator for additional information.', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BUS 111W', 'Introduction to Business Organization (WLU)', 0.5, 'LAB, LEC', 'Taught at Wilfrid Laurier University. Refer to WLU Undergraduate Calendar for course description.', 'BUS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 499A', 'Senior Honours Project', 0.5, 'PRJ', 'A senior-year research project.', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 499B', 'Senior Honours Project', 0.5, 'PRJ', 'A senior-year research project.', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 602', 'Fisheries Biology', 0.5, 'LEC', 'The literature and methods of Fisheries Biology. Examination and discussion of selected topics of interest to the class. Emphasis will be primarily, but not exclusively on the ecology, habitats, and management issues related to temperature freshwater fish.', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 604', 'Animal Cells in Culture', 0.5, 'LEC', 'The theoretical and technological aspects of growing cells from multicellular organisms in vitro and the use of these cell cultures in cell biology, biotechnology, cancer research, developmental biology, immunology and toxicology. Specific topics will include tissue dissociation, media, contamination, microscopic examination, cell storage, cell fusion, heterokaryons, reconstituted cells, hybrids, hybridomas, and the introduction of macromolecules into cells. The stress on specific topics might change from year to year.', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 605', 'Environmental Animal Physiology', 0.5, 'LEC', 'An advanced study of the physiological processes used by animals to respond to changes in environmental conditions. The study will focus on adaptation strategies used by animals to changes in temperature and other rate controlling environmental factors.', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 606', 'Advanced Aquatic Ecology', 0.5, 'LEC', 'Team taught by faculty of the Aquatic Ecology Group, fundamental and emerging topics will be examined through literature review, seminar discussions and summary papers.', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 608', 'Advanced Molecular Genetics', 0.5, 'LEC', 'Genetic aspects of the control of gene expression in bacteria, bacteriophages and (or) eukaryotes will be stressed. Recently published works will serve as the focal points for discussion. The specific content of any one set of topics to be analyzed in a particular term will be determined in consultation with the participants.', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 610', 'Biosystematics and Evolution', 0.5, 'LEC', 'The processes of organic evolution are examined in detail under the following topics: the nature and origin of variation; the transmission of variation from one population to another (gene flow); breeding systems and isolation; hybridization and introgression; modes of speciation and the species concept. Although plants will be emphasized, examples will be taken from the other major groups of organisms. (Offered in Winter Term, concurrently with BIOL 459) (previously BIOL 473).', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 611', 'Plant Growth Regulation I', 0.5, 'LEC', 'Critical analysis of the chemistry functions, modes of action and interactions of auxins, gibberellins, cytokinins, ethylene, abscisic acid and other growth substances (including phenolics and synthetic growth regulators) at the molecular, cellular and organismic levels.', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 612', 'Phylogenetic Reconstruction and Analysis', 0.5, 'LEC', 'An examination of the theory and tools used in the reconstruction of phylogenies.  This includes sequence retrieval, alignment and analysis as well as character and distance based phylogenetic applications.  Emphasis will be placed on the application of these elements to real data sets.', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 614', 'Bioinformatics Tools and Techniques', 0.5, 'LEC', 'The thoretical basis of various bioinformation tools will be presented and discussed, and applied to research tasks within the laboratory component of the course.  Topics may include advanced BLAST searching, multiple sequence alignments, sequence logos, expression databases, functional annotation of proteins, structural visualization, and comparative structural modeling.', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 617', 'Advanced Topics in Environmental Toxicology', 0.5, 'LEC', 'A critical evaluation of current research topics in environmental toxicology will be undertaken. Emphasis will be placed on the ecosystem approach to toxicology including cycling of toxicants, routes by which toxicants are removed from the environment and the impact of toxicants on species interaction within communities. Consideration will also be given to new methods for toxic hazard prediction and evaluation, as well as to biotic and abiotic factors that modify toxicant impact.', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 618', 'Advanced Microbial Physiology', 0.5, 'LEC', 'Discussion of current advances in selected topics in physiology of prokaryotic and/or eukaryotic microorganisms. Recently published research results will be the central points for the discussion. The topics might include but are not limited to microbiol growth behaviour under extreme conditions, microbial roles in the cycles of specific elements, processes of energy conservation, and properties and functions of key enzymes in microbial metabolic pathways.', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 621', 'Transport Phenomena in Plants', 0.5, 'LEC', 'Critical review of current concepts in the water relations, ion uptake and transport processes of higher plants. Structure and function will be emphasized and appropriate consideration given to the biophysical and biochemical principles involved in each physiological process.', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 622', 'Selected Topics in Plant Physiology', 0.5, 'LEC', 'Discussions of selected topics not covered in other courses. These may include juvenility, dormancy, senescence, plant response to environmental stress, morphogenesis, photosynthesis and biochemistry.', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 623', 'Floral Morphology and Taxonomy', 0.5, 'LEC', 'An advanced study of floral structure in which the following topics are dealt with: the diversity of floral structure and how this relates to modes of pollination, systems of classification and the identification of flowering plants, nomenclature, and the origin and evolution of flowering plants.', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 624', 'Environmental Biogeochemistry', 0.5, 'LEC', 'The influence of physical, chemical and microbiological processes on groundwater geochemistry are examined. Background concepts in microbial ecology and organic geochemistry are developed and related to subsurface environments. Treatment is given to biodegradation of organic pollutants, microbially-mediated redox reactions and organic-metal interactions. EARTH 439 is strongly recommended.', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 625', 'Applied  Limnology', 0.5, 'LEC', 'Management and restoration of lakes and reservoirs. Diagnosis and evaluation of environmental problems, economics, method selection. Analysis of physical and chemical remediation methods including advanced sewage treatment, phosphorus precipitation, hypolimnetic withdrawal and aeration, sediment treatment. Discussion of procedures to control algal and macrophyte biomass including artificial circulation, water level drawdown, mechanical harvesting, biomanipulation and biological controls. Restoration and management of stream habitats and the riparian zone. Recommended: undergraduate course in limnology or water pollution biology.', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 627', 'Topics in Applied and Industrial Microbiology', 0.5, 'SEM, TUT', 'One or more topics will be addressed in detail through review of literature, including patents, and current practices in industrial microbiology. The topics might include but are not limited to Bacillus subtilis: a workhorse in industrial fermentations; production of microbiol lipids; Aspergillus fermentations. Each student will present a seminar on an assigned topic and the class will work as a group to prepare an up-to-date review of publication quality.', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 628', 'Morphogenesis', 0.5, 'LEC', 'Aspects of genetic and developmental processes that underlie morphogenetic phenomena in diverse eukaryotes will be discussed. Topics may include limb morphogenesis and regeneration, transcriptional response to hormonal action, erythropoiesis, morphogenetic-dependent cell and tissue interactions, differentiation and organization of germ-line tissue, use of developmental mutants to study developing systems, etc. Areas of concentration will be determined at the beginning of each term.', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 629', 'Cell Growth and Differentiation', 0.5, 'LEC', 'Discussion and evaluation of recent studies relating to the cell cycle and cell differentiation. The cell cycle: mechanisms and regulation of cell growth, DNA replication, mitosis and differential gene expression. The role of cell interactions, hormones and growth factors in the control of proliferation. Cell-cycle models. Cell differentiation: cell interactions and experimental control of differentiation in vitro.', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 631', 'Multivariate Methods in Ecology', 0.5, 'LEC', 'This course introduces multivariate statistical methods commonly used in ecology. Topics covered will include clustering, ordination, discriminant functions analysis, canonical variates, multiple regression, and some multivariate analyses of variance.', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 636', 'Advanced Immunology', 0.5, 'LEC', 'Discussion of current advances in selected topics in immunology. The areas to be covered will include cell-mediated immunity, humoral immunity, comparative immunology, and other topics of current interest.', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 642', 'Current Topics in Biotechnology', 0.5, 'LEC', 'Recent developments in biotechnology, both applied and basic, will be emphasized. The course will cover such topics as the development of vectors for the stable transformation of plant cells, the isolation, characterization and manipulation of plant, animal and microbial genes, and the use of microorganisms to develop new products and processes.', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 645', 'Recent  Advances in Microbial Ecology', 0.5, 'LEC', 'Recent advances in selected topics of microbial ecology will be examined. Topics will be selected from soil, fresh water or other ecosystems with an important microbial component.', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 646', 'Paleolimnology', 0.5, 'LEC', 'This course examines the use of stratigraphic records from lakes to reconstruct environmental change.  Topics include sediment coring techniques, radiometric dating methods, analysis of physical, geochemical and biological indicators, and case studies demonstrating applications to address scientific and management questions. Practical experience is gained from a lake sediment coring field trip and subsequent laboratory analyses.  *eligible for MES.', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 650', 'Bio-Molecular Tools', 0.5, 'LEC', 'The ability to detect, isolate and manipulate bio molecules such as DNA and RNA has created powerful tools that have been instrumental in advancing science and medicine.  In BIOL 650 we will investigate the current state of the art biological tools.  This course will be prefaced by a historic overview of RNA and DNA as will as other bio-molecules detailing their discovery, chemistry and the relationships between them.  Subsequent lectures will then concentrate on how these molecules are manipulated and used in modern biology with a particular emphasis on methods that integrate bio molecules into systems for diagnostics, high-throughput genomics and proteomics, and imaging.', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 651', 'Hydroecology for Freshwater Ecosystem Management', 0.5, 'SEM', 'This course examines the interaction in both space and time between hydrological processes and lacustrine environments.  Applications to freshwater ecosystem management are explored.', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 652', 'Advanced Ecology', 0.5, 'LEC, RDG, SEM', 'Discussion of the major concepts in ecology, and current advances in selected topics.  A broad overview of important ideas in population, community and ecosystem ecology will be provided, and following this, recently published research results on a subset of topics will become the central focus of the course.  Topics might include but are not limited to: spatial-temporal dynamics of populations, meta- and hierarchical community structure, and species-specific influences on ecosystem function.', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 667', 'Animal Molecular Biology', 0.5, 'LEC', 'Selected topics in molecular biology will be presented at an advanced level with the aim of evaluating recent contributions and developments. Basic concepts and organismic interrelationships will be emphasized. The topics will concentrate on mechanisms of replication, transcription and (or) translation. Specific themes for discussion will be arranged each time the course is presented.', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 669', 'Plant Molecular Biology', 0.5, 'LEC', 'Critical discussion of current developments in plant biology. The course will cover such topics as the structure, organization, replication and expression of plant and algal genetic material. Emphasis will be placed on understanding basic molecular mechanisms and processes.', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 670', 'Photobiology', 0.5, 'LEC', 'The involvement of light in biological processes. The first half of the course involves detailed discussions on the physical properties of light, the interactions of light with matter, and photochemical and photobiological methods. The second half of the course deals with the current literature in photobiology. Topics include photomorphogenesis, photoinduced toxicity, photodynamic therapy, photodamage, DNA repair, vision, and transduction of light signals.', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 675', 'Advanced Topics in Animal Behaviour', 0.5, 'LEC', 'This course will deal intensively with a subject area in the field of animal behaviour. The particular topic for a given term will be determined by the interests of the participating members of the class. Suggested topics include: mating systems in the animal kingdom; patterns of parental care; cost/benefit analyses of social living. Recommended: a basic knowledge of animal behaviour and a devoted interest.', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 678', 'Current Topics in Neurophysiology', 0.5, 'LEC', 'Principles and applications for the study of membrane proteins in physiological processes will be covered, with particular emphasis on voltage-, receptop- and second messenger-operated channels.  Discussions will focus on modern techniques employed in electrophysiology, pharmacology, imaging and biochemistry.', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 680', 'Specialized Studies of Selected Research Procedures, Strategies or Topics', 0.5, 'RDG', 'Critical evaluation and discussion of topics and procedures in biology that are not covered in existing graduate courses. The course must be specially arranged with a faculty member. Students may take only one BIOL 680 course for credit. (Offered to Master''s students only.)', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 681', 'Specialized Studies of Selected Research Procedures, Strategies or Topics', 0.5, 'RDG', 'Critical evaluation and discussion of topics and procedures in biology that are not covered in existing courses. The course must be specially arranged with a faculty member. Students may take only one BIOL 681 course for credit. (Offered to PhD students only.)', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 690', 'Scientific Communication', 0.5, 'SEM', 'The goal of the course is to introduce new graduate students in the department of Biology to the basic skills that will be necessary for them to acquire and organize information as well as present it effectively.  The course will be given in two sections, in different terms; Topic 1: Life Science Research Skills (fall of a student''s first year) and Topic 2: Scientific presentation (winter of a student''s second or third year).  The participants are expected to also attend and participate in the weekly "Biology Brown Bag Gathering" which is a forum for students and post doctoral fellows to present their research results or lead discussions on current topics of general interest.  This series will start each fall with a presentation on "How to give a talk".', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BME 101', 'Communications in Biomedical Engineering-Written and Oral', 0.25, 'LEC, TUT', 'This course introduces first-year students to biomedical engineering with a focus on the engineering profession and technical communication skills. Through in-class and independent activities leading to formative and summative assessments, students will consider and reflect on communication intention, audience, content, medium, format, and tone to demonstrate and improve upon their listening, written and oral communication skills for academic, engineering, and professional context. [Offered: F]', 'BME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BME 101L', 'Communications in Biomedical Engineering-Visualization', 0.25, 'LAB, LEC', 'This laboratory course introduces students to visual communication methods relevant to engineering analysis and design. Through in-class and independent activities leading to formative and summative assessments, students will consider and reflect on communication intention, audience, content, medium, format, and level of detail to demonstrate and improve upon their skills in graphing, freehand sketching, technical drawing, and computer-aided design (CAD). [Offered: F]', 'BME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BME 121', 'Digital Computation', 0.5, 'LAB, LEC, TUT', 'The key skills necessary to develop software solutions to solve biomedical engineering problems. Topics include software development, software design, programming language syntax, object oriented programming, structured programming, arrays, matrices, pointers, and algorithm efficiency. The topics will be reinforced in the context of practical biomedical software systems such as physiological monitoring systems and clinical support systems. [Offered: F]', 'BME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BME 122', 'Data Structures and Algorithms', 0.5, 'LEC, TUT', 'Topics of structured software design, data structures, abstract data types, recursive algorithms, algorithm and data structure analysis and design from both computational and memory perspectives, lists, stacks, queues, trees, graphs, sorting and searching, hashing, and problem-solving strategies. Embedded programming in health monitoring systems and healthcare management systems. [Offered: W]', 'BME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BME 161', 'Introduction to Biomedical Design', 0.5, 'LEC, TUT', 'Topics related to biomedical design will be covered: multidisciplinary system design, design process, problem definition, life-cycle design, design specification, function analysis, design evaluation and decision-making, introduction to mechanical design, prototyping, experimentation, safety and responsibility in engineering design, design for society and environment, and design documentation. [Offered: F]', 'BME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BME 162', 'Human Factors in the Design of Biomedical and Health Systems', 0.5, 'LEC, TUT', 'Design of human-machine environments, designing for patient safety and reduce human error in decision-making, analytical methods of determining user needs, information processing and human sensory processes and consideration of these elements in the design of systems with humans, and consideration of human physical capabilities in ergonomic design. Topics will be reinforced in the context of the design of prosthetics or rehabilitation devices. [Offered: W]', 'BME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BME 181', 'Physics I - Statics', 0.5, 'LEC, TUT', 'Introduction to the basic theory and principles of mechanics of static systems. Topics covered include statics of particles, rigid bodies and force systems, equilibrium of rigid bodies, analysis of joints and frames, distributed forces, centroids and moments of inertia, and friction. Applications of mechanical principles to musculoskeletal systems will be presented. [Offered: F]', 'BME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BME 182', 'Physics II - Dynamics', 0.5, 'LEC, TUT', 'The science of motion is taught with initial focus on particles, and then progressing to planar rigid body systems. Concepts such as inertia, momentum, work, energy, Newton''s laws, and contact dynamics are covered, with particular application to human motions (e.g., walking, running, jumping, lifting, and throwing). [Offered: F]', 'BME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHE 100', 'Chemical Engineering Concepts 1', 0.5, 'LEC, TST, TUT', 'Introduction to basic methods and principles in chemical engineering. The fundamentals of engineering calculations (units and dimensions), behaviour of fluids, mass balances, processes, and process variables. [Offered: F]', 'CHE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHE 120', 'Computer Literacy and Programming for Chemical Engineers', 0.5, 'LAB, LEC', 'Using computers as problem-solving tools. Introduction to computer architecture and networking. Effective use of command line interfaces and graphical user interfaces. Word processing, spreadsheet, and presentation software. Introduction to programming and numerical computing using a high-level interpreted programming language. Programming fundamentals including flow charts, variables, design and use of functions, strings and text input/output, relational operators, conditionals, collections, loops. Numerical computing, plotting and file input/output. [Offered: F]', 'CHE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHE 101', 'Chemical Engineering Concepts 2', 0.5, 'LAB, LEC, TST, TUT', 'Development of chemical process analysis skills. Introduction to single- and multi-phase physical equilibria. Material and energy balances in reactive and non-reactive systems. Introduction to investigation and technical communication through laboratory experiments illustrating the physical principles discussed. (In the Winter term only: brief review of co-op fundamentals.) [Offered: W, S]', 'CHE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHE 102', 'Chemistry for Engineers', 0.5, 'LEC, TST, TUT', 'Chemical principles with applications in engineering. Stoichiometric calculations, properties of gases, properties of liquids and solutions, gas phase chemical equilibrium, ionic equilibrium in aqueous solution, oxidation-reduction reactions, chemical kinetics. [Offered: F]', 'CHE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHE 121', 'Engineering Computation', 0.5, 'LEC, TST, TUT', 'Introduction to digital computers, hardware and software organization. Programming fundamentals. Algorithms and control structures. Computer communication. Spreadsheets for problem solving, plotting, fitting data, building new functions, and making iterations and loops. Problem solution, plotting, and creating complex programs in a programming environment. Elementary numerical methods (e.g. Taylor series summations, roots of equations, roots of polynomials, system of linear and nonlinear algebraic equations, integration). Applications in Chemical Engineering. [Offered: W, S]', 'CHE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHE 161', 'Engineering Biology', 0.5, 'LEC, TST, TUT', 'Introduction to basic concepts of biochemistry and cell biology. Overview of the chemistry of amino acids, carbohydrates, lipids and nucleic acids. Properties and functions of biopolymers. Elements of cell structure and diversity, and relationship of biochemistry with cell metabolism. A focus on biotechnologically relevant examples such as biomimetic engineering design, proteomics, system biology and high throughput biology. [Offered: W, S]', 'CHE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHE 200', 'Equilibrium Stage Operations', 0.5, 'LEC, TUT', 'Equilibrium between phases; the equilibrium stage concept. Cascades of stages with and without reflux; group methods and stage-by-stage approaches; graphical solutions. Applications in the separation of components by distillation, absorption, stripping, extraction and leaching. [Offered: F, W]', 'CHE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHE 201', 'Seminar', 0, 'SEM', 'General Seminar.', 'CHE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHE 202', 'Seminar', 0, 'SEM', 'General Seminar.', 'CHE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHE 211', 'Fluid Mechanics', 0.5, 'LEC, TUT', 'Fundamentals of fluid flow. Conservation laws for mass, momentum and mechanical energy. Flow of fluids in conduits. Flow past immersed bodies. Flow through beds of solids, fluidization. Transportation and metering of fluids. Dimensional analysis. [Offered: F, S]', 'CHE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHE 220', 'Process Data Analysis', 0.5, 'LEC, TUT', 'Introduction to statistical methods for analyzing and interpreting process data. Introduction to statistical ideas, probability theory, distribution theory, sampling theory, confidence intervals and significance tests. Introduction to regression analysis. Introduction to design of experiments and statistical quality control. [Offered: F, W]', 'CHE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHE 230', 'Physical Chemistry 1', 0.5, 'LEC, TUT', 'Thermodynamics: work and heat as forms of energy. First law, internal energy and enthalpy. Heats of chemical and physical changes. Cycles and the second law, entropy. Spontaneity and equilibrium, free energies. Systems of variable composition, chemical equilibrium. Phase equilibrium and the phase rule. Ideal solutions, colligative properties. [Offered: F, W]', 'CHE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHE 231', 'Physical Chemistry 2', 0.5, 'LEC, TUT', 'Thermodynamics: ideal solutions; non-ideal solutions, non-electrolytic and electrolytic solutions, phase equilibrium and phase diagrams, reaction equilibrium. Surface phenomena: surface tension, capillarity, properties of small particles, adsorption. Chemical kinetics: rate laws, reaction rates, mechanisms, catalysis, heterogeneous reactions. [Offered: F, S]', 'CHE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHE 241', 'Materials Science and Engineering', 0.5, 'LEC, TUT', 'Fundamentals; atomic bonding, crystalline structure, crystal defects, non-crystalline materials; structure and properties of metals, ceramics, glasses, semi-conductors. Amorphous materials, polymers, composites. Processing and concepts of engineering design of materials. [Offered: F, S]', 'CHE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHE 290', 'Chemical Engineering Lab 1', 0.25, 'LAB', 'A selection of computer and laboratory exercises refreshing and reinforcing material covered in the previous term. Topics may include: basic microbiology and biotechnology, introductory physical chemistry, mass and energy balances. [Offered: F, W]', 'CHE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHE 291', 'Chemical Engineering Lab 2', 0.25, 'LAB', 'A selection of computer and laboratory exercises refreshing and reinforcing material covered in the previous term. Topics may include: physical chemistry, design of experiments and statistics, and equilibrium stage operations. [Offered: F, S]', 'CHE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHE 298', 'Directed Research Project', 0.25, 'PRJ', 'Directed research project under the supervision of faculty members. Participation will give students experience in advanced research techniques, with valuable training for those potentially interested in graduate school or industrial research careers. Taken over and above normal course load. Good standing and permission of department required for registration.', 'CHE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHE 299', 'Directed Research Project', 0.25, 'PRJ', 'Directed research project under the supervision of faculty members. Participation will give students experience in advanced research techniques, with valuable training for those potentially interested in graduate school or industrial research careers. Taken over and above normal course load. Good standing and permission of department required for registration.', 'CHE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHE 301', 'Seminar', 0, 'SEM', 'General Seminar.', 'CHE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHE 302', 'Seminar', 0, 'SEM', 'General Seminar.', 'CHE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHE 311', 'Chemical Reaction Engineering', 0.5, 'LEC, TUT', 'Review of stoichiometry and chemical kinetics. Homogeneous reactors: isothermal operation; batch; semi-batch; continuous tank; plug flow reactor design. CSTRs in series; plug flow reactor with recycle. Multiple reactions in reactor networks. Temperature effects in adiabatic and non-isothermal reactors. Yield, selectivity and optimal operation of reactors. Heterogeneous catalysis and effectiveness factors in two-phase reactors. [Offered: F, W]', 'CHE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHE 312', 'Mathematics of Heat and Mass Transfer', 0.5, 'LEC, TUT', 'Fundamentals of mass transfer: species and mixture velocities, diffusive and convective fluxes, Fick''s law and the diffusion coefficient. Vector form of the microscopic (differential) mass balance. Fundamentals of heat transfer: conduction, convection and radiation, Fourier''s law and thermal conductivity. Vector form of the microscopic thermal energy balance for solids and incompressible fluids. Mass and heat transfer models leading to ordinary differential equation problems in Cartesian, cylindrical and spherical geometries; types of boundary conditions. Bessel functions. Analytical solution of linear partial differential equations: combination of variables, separation of variables and Sturm-Liouville theory, Laplace transform. Mass and heat transfer models leading to linear partial differential equations: 1D transient diffusion and conduction in Cartesian, cylindrical and spherical geometries; steady-state 2D conduction and diffusion. Heat-mass transfer analogies and dimensionless numbers. [Offered: W,S]', 'CHE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHE 313', 'Applications of Heat and Mass Transfer', 0.5, 'LEC, TUT', 'Convective heat transfer. Analysis of convective heat transfer in external flows using the boundary layer approach. Analysis of convective heat transfer in internal flows. Empirical correlations for convective heat transfer. Heat transfer with phase change: condensation and boiling. Heat exchanger design. Convective mass transfer. Empirical correlations for convective mass transfer. Mass transfer at fluid-fluid interfaces. Design of continuous differential contactors for absorption/stripping, distillation and liquid-liquid extraction. Analogy between heat, mass and momentum transfer. Dimensional analysis. Simultaneous heat and mass transfer operations. [Offered: F, W]', 'CHE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHE 322', 'Numerical Methods for Process Analysis and Design', 0.5, 'LEC, TUT', 'Systems of linear and non-linear algebraic equations; polynomial and spline interpolation; numerical differentiation and integration; numerical solution of initial value and boundary value ordinary differential equation problems: accuracy and stability, step size control and stiffness; finite differences for the numerical solution of elliptic and parabolic partial differential equations: method of lines, explicit vs. implicit finite-difference methods; introduction to the finite element method (optional). The course extends material on numerical methods and their implementation in Matlab, covered in CHE 121, to address a variety of models of chemical engineering processes. [Offered: W, S]', 'CHE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 140L', 'Introductory Scientific Calculations Laboratory', 0.5, 'LAB, TUT', 'An introductory laboratory for the use and applications of computer software packages, such as Excel and Mathcad, for scientific calculations. The use of such software packages for basic calculations, data analysis, regression analysis, plotting of scientific graphs, data manipulation, and equation solving will be covered, with an emphasis placed upon chemical and biochemical concepts and applications. 
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHE 325', 'Strategies for Process Improvement and Product Development', 0.5, 'LEC, TUT', 'Examines the role of the statistical design of experiments and data analysis in continuous process improvement and product development. The application of screening designs, single and multifactor including two-level factorial designs, response surface designs such as central composite and Box-Behnken, and designs for the study of mixture variables for recipe optimization. Use of statistical analysis software to apply these techniques. [Offered: F, W]', 'CHE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHE 330', 'Chemical Engineering Thermodynamics', 0.5, 'LEC, TUT', 'Review of fundamentals, including 2nd law and concepts of equilibrium, phase and reaction equilibria, fugacity, exergy. Thermodynamics applied to practical situations. Examples chosen from: fluid flow; power generation; refrigeration; air conditioning and water cooling; liquefaction of gases; equilibria in complex chemical reactions and separation processes; surface phenomena; electrochemical reactions; biological processes. [Offered: W, S]', 'CHE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHE 331', 'Electrochemical Engineering', 0.5, 'LEC, TUT', 'Topics and applications of electrochemistry and electrochemical engineering. Industrial process examples. Environmental aspects. Ionic equilibria. Laws of electrolysis. Theory of electrolytes. Transport properties of electrolytes. Reversible cell potentials. Irreversible electrode processes. Thermodynamic and kinetic aspects of corrosion. Common examples of corrosion. Electrochemical energy conversion and storage. [Offered: F, W]', 'CHE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHE 360', 'Bioprocess Engineering', 0.5, 'LEC, TUT', 'Review of elementary aspects of molecular biology, genetic engineering, biochemistry, microbiology. Biological systems for the production of commercial goods and services: foods, pharmaceuticals, chemicals, fuels, equipment, diagnostics, waste treatment, and biomaterials. Properties of microbial, plant and animal cells, and of enzymes used in bioprocess applications. Classification and characterization of biological agents and materials; quantification of metabolism, biokinetics, bioenergetics. Introduction to design of bioprocess systems, including biosafety and good manufacturing practices. [Offered: W, S]', 'CHE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHE 390', 'Chemical Engineering Lab 3', 0.5, 'LAB', 'A selection of computer and laboratory exercises, and project-based investigations, refreshing and reinforcing material covered in the previous term. Topics may include fluid mechanics, physical chemistry and kinetics, materials properties, and testing. [Offered: W, S]', 'CHE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHE 391', 'Chemical Engineering Lab 4', 0.25, 'LAB', 'A selection of computer and laboratory exercises refreshing and reinforcing material covered in the previous term. Topics may include: electrochemistry, heat transfer, mass transfer, fermentation and bioseparations. [Offered: F, W, last offered Winter 2020]', 'CHE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHE 398', 'Directed Research Project', 0.25, 'PRJ', 'Directed research project under the supervision of faculty members. Participation will give students experience in advanced research techniques, with valuable training for those potentially interested in graduate school or industrial research careers. Taken over and above normal course load. Good standing and permission of department required for registration.', 'CHE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHE 399', 'Directed Research Project', 0.25, 'PRJ', 'Directed research project under the supervision of faculty members. Participation will give students experience in advanced research techniques, with valuable training for those potentially interested in graduate school or industrial research careers. Taken over and above normal course load. Good standing and permission of department required for registration.', 'CHE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHE 401', 'Seminar', 0, 'SEM', 'General Seminar.', 'CHE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHE 402', 'Seminar', 0, 'SEM', 'General Seminar.', 'CHE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHE 420', 'Introduction to Process Control', 0.5, 'LEC, TUT', 'Laplace transform techniques. Proportional-integral-derivative control. Frequency response methods. Stability analysis. Controller tuning. Process control simulation and computer control systems. Process identification. [Offered: F, S, last offered Fall 2020]', 'CHE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHE 480', 'Process Analysis and Design', 0.5, 'LEC, TUT', 'Development and analysis of process flowsheets and chemical product design. Design and selection of common process equipment such as heat exchangers, pumps, piping, staged separations. Incorporation of pollution prevention and inherently safer design principles. Equipment and project cost estimation. [Offered: F, S]', 'CHE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHE 482', 'Chemical Engineering Design Workshop', 0.5, 'LEC, PRJ', 'In this course, students study the design process including: problem definition and needs analysis; process synthesis, process debottlenecking and troubleshooting; safety and environmental protection in design; written and oral communication for design reports. A significant portion of the term work will be devoted to a group design project, culminating in a design proposal that will be presented to the department. [Offered: F, S]', 'CHE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHE 483', 'Group Design Project', 0.5, 'LEC, PRJ', 'Student design teams of two to four members work on design projects of industrial scope and importance under the supervision of a faculty member. The projects are a continuation of those initiated in CHE 482. [Offered: W]', 'CHE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHE 490', 'Chemical Engineering Lab 5', 0.25, 'LAB', 'A selection of computer and laboratory exercises refreshing and reinforcing material covered in the previous term. Topics may include: reaction kinetics and reactor engineering, heat and mass transfer unit operations, numerical methods, principles of design and safety. [Offered: F, S]', 'CHE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHE 498', 'Directed Research Project', 0.25, 'PRJ', 'Directed research project under the supervision of faculty members. Participation will give students experience in advanced research techniques, with valuable training for those potentially interested in graduate school or industrial research careers. Taken over and above normal course load. Good standing and permission of department required for registration.', 'CHE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHE 499', 'Elective Research Project', 0.5, 'PRJ', 'A major undergraduate research project carried out as a technical elective (TE) under the supervision of a faculty member. An oral presentation of results and a written report are the minimum requirements. Other requirements may be set by the faculty supervisor or department. [Offered: F,W,S]', 'CHE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHE 500', 'Special Topics in Chemical Engineering', 0.5, 'LEC', 'Special courses on advanced topics may be offered from time to time, when resources are available. For current offerings, inquire at the CHE undergraduate office.', 'CHE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHE 514', 'Fundamentals of Petroleum Production', 0.5, 'LEC', 'Fundamentals of surface chemistry, capillary pressure and wettability. Petrophysics, measurement and interpretation of electrical, capillary and flow properties of reservoir rock. Hydrostatic pressure regimes and estimation of oil and gas reserves. Darcy''s law and modelling of steady-state and transient incompressible and compressible single-phase flow through porous media. Thermodynamics of petroleum fluids. Material balance for oil and gas reservoirs: subsurface withdrawal and primary production mechanisms. Oil well testing. Two-phase flow in oil reservoirs, relative permeability, Buckley-Leverett theory of linear water flooding and sweep efficiency. Introduction to enhanced oil recovery. [Offered: F]', 'CHE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHE 516', 'Energy Systems Engineering', 0.5, 'LEC', 'Energy systems in society. Review of fossil fuel reserves, production and consumption trends, including unconventional sources such as shale gas, and oil sands. Transportation fuels and alternative fuels; the design of hybrid power trains, fuel cells and batteries. Design considerations of carbon-free energy generation and carbon-neutral technologies, including nuclear, wind, solar and bio-energy. Design of energy storage on a ''utility scale'' and portable power sources. Applications of thermodynamics in the design of energy systems, including the Rankin and Brayton cycles. Other energy system topics as appropriate. [Offered: F]', 'CHE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHE 522', 'Advanced Process Dynamics and Control', 0.5, 'LEC', 'State space methods. Sampled-data systems. Discrete systems. Transform methods. Multivariable control. Computer control. Closed-loop analysis. Design of controllers. Control of complex chemical systems. [Offered: W]', 'CHE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHE 524', 'Process Control Laboratory', 0.5, 'LAB, LEC', 'Experiments on process dynamics, control and simulation of processes. Time constant; step and frequency response; controller tuning; multivariable control strategies. Implementation using simulation systems, mainframe computer control, microcomputers. [Offered: W]', 'CHE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHE 541', 'Introduction to Polymer Science and Properties', 0.5, 'LEC', 'An introduction to principles governing polymerization reactions and the resultant physical properties of polymers. Molecular weight distribution. Crystallinity. Step-growth and chain-growth polymerization and copolymerization. Selected additional topics in polymer characterization/ properties. [Offered: F]', 'CHE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHE 543', 'Polymer Production: Polymer Reaction Engineering', 0.5, 'LEC', 'Overview of polymer production technology and polymer reaction engineering analysis tools. Examples from chain growth polymerization processes (free radical, ionic, living/controlled radical variants, metal co-ordination catalysis, etc.), different modes of polymer reactor operation (batch, semi-batch, continuous flow stirred-tank reactor (CSTR), tubular, trains of CSTRs), homogeneous (bulk/solution/suspension) and heterogeneous (emulsion/dispersion/co-ordinated/etc.), linear and branched/cross-linked chains. Calculations and mathematical models for rate, copolymer composition, molecular weight and sequence length characteristics, for polymer reactor design/optimization of polymer productivity and quality variables. [Offered: W]', 'CHE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHE 562', 'Advanced Bioprocess Engineering', 0.5, 'LEC', 'Application of process engineering principles to the design and operation of fermentation reactors which are widely used in the pharmaceutical, food, brewing and waste treatment industries. Aspects of mass transfer, heat transfer, mixing and rheology with biochemical and biological constraints. [Offered: W]', 'CHE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHE 564', 'Food Process Engineering', 0.5, 'LEC', 'Applications of unsteady and steady state heat and/or mass transfer operations to processing natural and texturized foods. Design and analysis of sterilization, low temperature preservation, concentration, separation and purification processes. Effects of formulation, additives and processing on organoleptic and nutritional quality. [Offered: W]', 'CHE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHE 571', 'Industrial Ecology', 0.5, 'LEC', 'Industrial Ecology is a rapidly growing field that systematically examines local, regional, and global uses and flows of materials and energy in products, processes, industrial sectors, and economies. It focuses on the potential role of industry in reducing environmental burdens throughout the product life cycle from the extraction of raw materials to the production of goods, to the use of those goods and to the management of the resulting wastes. This course will review the environmental issues associated with chemical industries and the roles of engineers to manage these issues. The principles and philosophy of green chemistry will be addressed including pollution prevention in unit operations. The concepts and practices of environmental life cycle analysis and accounting will be addressed in detail, together with the basics of risk assessment, management and communication. [Offered: F]', 'CHE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHE 572', 'Air Pollution Control', 0.5, 'LEC', 'Nature and sources of air pollutants. Transport of pollutants and dispersion modeling for regulatory purposes. Design of industrial particulate capture systems using cyclones, electrostatic precipitators, filters, scrubbers. Design of organic compound emissions control using incineration, biofiltration, adsorption and absorption. Overview of NOx and SOx control. Indoor air quality assessment techniques. [Offered: W]', 'CHE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHE 574', 'Industrial Wastewater Pollution Control', 0.5, 'LEC', 'Primary focus is on the control and treatment of inorganic aqueous waste from chemical process industries. Waste minimization methods with specific examples such as rinsewater circuit design. Principles and design of treatment methods: chemical treatment, precipitation, coagulation and flocculation, ion exchange and membrane separation. Treatment of organic aqueous waste. [Offered: W]', 'CHE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHE 610', 'Theory and Application of Transport Phenomena', 0.5, 'LEC', 'Mathematical analysis of momentum, heat and mass transport in systems of chemical engineering interest:  development of the differential equations of change (continuity, motion and energy) for forced convection in isothermal, non-isothermal and multi-component systems; description of velocity, temperature and concentration profiles and computation of momentum, energy and mass fluxes at surfaces under conditions of laminar flow; description of transport in turbulent flow by time-smoothing of the equations of change; turbulent velocity, temperature and concentration profiles.', 'CHE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHE 612', 'Interfacial Phenomena', 0.5, 'LEC', 'An overview of interfacial phenomena, surface physical chemistry, importance of surfaces for small particles, and relevance to other subjects such as biology, nanotechnology and biomedicine.  The fundamental concepts fo surface thermodynamics and intermolecular forces will be discussed.', 'CHE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHE 614', 'Capillary and Transport Phenomena in Porous Media', 0.5, 'LEC', 'This course covers fundamental and advanced topics on 1) capillary phenomena in porous media, 2) characterization of pore structure and fluid flow in porous media (single phase and multiphase flow in porous media), 3) percolation theory concepts and application to modelling capillary and transport phenomena in porous media, 4) hydrodynamic dispersion, oil spill migration in porous media and remediation techniques.', 'CHE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHE 620', 'Applied Engineering Mathematics', 0.5, 'LEC', 'Advanced engineering mathematics for the solution of linear differential equations arising from physiochemical models.  Topics include scaling analysis of differential equations, series solutions for ordinary differential equations (ODEs), numerical solutions for initial/boundary value problems, Laplace transforms, characterization of partial differential equations (PDEs), and solution methods for PDEs (Sturm-Liouville transform, numerical methods).', 'CHE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHE 622', 'Statistics in Engineering', 0.5, 'LEC, TUT', 'A course in practical statistics at a level one step beyond an elementary course.  Material includes regression analysis for linear and nonlinear models, analysis of variance, statistical interference, single and multiple comparisons, and an introduction to the design of experiments including single factor designs, multifactor designs, response surface methods, d-optimality (with empirical and mechanistic models), and the analysis of undersigned data.  Applications to process improvement, product development and research problems will be explored.  Use of statistical analysis software to apply these techniques.', 'CHE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHE 624', 'Advanced Process Dynamics and Control', 0.5, 'LEC', 'Sampled-data systems. Discrete-time control (discrete PID/IMC versions and tuning). Multivariable control (RGA, decouplers). Other advanced control designs may be included such as constrained minimum variance, linear quadratic gaussian, adaptive and nonlinear and introduction to state estimation/Kalman filtering. Held with CH E 522. Additional requirements beyond CH E 522 requirements.', 'CHE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHE 630', 'Chemical Reactor Analysis', 0.5, 'LEC', 'Mixing effects (segregation and micromixing) on reactor performance analysis of reactor stability, 1- and 2- D models for packed bed reactors, heterogeneous non-catalytic reactions, heat and mass transfer effects in porous catalyst particles, reduction of data for catalytic reactions and scale-up concepts.  Examples will be drawn from packed, fluidized bed and transport reactors, polymer reactors and 3-phase reactors.', 'CHE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHE 632', 'Introduction to Catalysis', 0.5, 'LEC', 'Principles and applications of heterogeneous and homogeneous catalysis. Adsorption, reaction kinetics and mass transfer effects. Catalyst synthesis and characterization. Industrial catalytic processes. The fundamentals of catalytic phenomena will focus on catalytic technology, catalyst materials, properties and preparation as well as catalyst characterization and selection.', 'CHE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHE 640', 'Principles of Polymer Science', 0.5, 'LEC', 'Introduction to the physical chemistry of high polymers, principles of polymer synthesis, mechanisms and kinetics of polymerization reactions, copolymerization theory, polymerization in homogeneous and heterogeneous systems, chemical reactions of polymers. Theory and experimental methods for the molecular characterization of polymers.', 'CHE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHE 641', 'Physical Properties of Polymers', 0.5, 'LEC', 'The physical properties of polymers are considered in depth from a molecular viewpoint. Rubber elasticity, mechanical properties, rheology and solution behaviour are quantitatively treated.', 'CHE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHE 660', 'Principles of Biochemical Engineering', 0.5, 'LEC', 'Aspects of mass-transfer, heat-transfer, fluid flow, cell growth, metabolic engineering and enzyme kinetics related to the design of biological processes and process equipment. Sterilization techniques, fermentation, bioreactor design and operation, including immobilized cell or enzyme systems and aspects of bioseparations engineering.', 'CHE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHE 661', 'Advances in Biochemical Engineering', 0.5, 'LEC', 'Design and control of bioprocesses with application to advanced or novel systems including enzymes, mixed cultures, genetically engineered cells, plant cells and animal cells.  Exploration of new methods of producing materials, food, pharmaceuticals and providing services such as biological waste treatment.', 'CHE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHE 672', 'Air Pollution Control', 0.5, 'LEC', 'Treatment of gaseous waste products from representative Canadian industries. Characterization and toxicity of filtration, scrubbing, cycloning, electrostatic precipitation and other chemical treatments. Legal, sociopolitical, economic and engineering aspects. Held with CH E 572. A special project or topic required in addition to CH E 572 requirements.', 'CHE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHE 674', 'Industrial Waste Treatment', 0.5, 'LEC', 'Characterization of industrial wastewater. Physical/chemical methods or pre- and primary treatment. Principles, mechanisms and kinetics of biological degradation of industrial compounds. Design and operation of biological treatment systems, including aerobic, anaerobic and tertiary treatment. Sludge and solids disposal. Other physical/chemical treatments, possibly including adsorption, advanced oxidation and incineration.  Additional course material and/or requirements beyond ChE 574 may be required.', 'CHE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHE 710', 'Special Topics in Transport Phenomena', 0.5, 'LEC', 'Various courses dealing with particle-fluid dynamics, non-Newtonian flows, flow through porous media, heat and mass transfer in two-phase systems, and the use of transient and frequency response to measure physical quantities.', 'CHE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHE 715', 'Research Topics in Transport Phenomena', 0.5, 'RDG', 'Various special research topics will be offered in the area of transport phenomena to support independent and individual research by instructor consent.', 'CHE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHE 720', 'Special Topics in Analysis of Chemical Processes', 0.5, 'LEC', 'Various courses dealing with selected special topics such as advanced statistics, mathematical analysis, modelling, optimization and/or control of chemical processes.', 'CHE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHE 725', 'Research Topics in Analysis of Chemical Processes', 0.5, 'RDG', 'Various special research topics will be offered in the area of analysis of chemical processes to support independent and individual research by instructor consent.', 'CHE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHE 730', 'Special Topics in Chemical Kinetics,Catalysis and Advanced Reactor Engineering', 0.5, 'LEC', 'Various courses dealing with selected topics such as kinetics of chemical and biological systems, theories of catalysis, catalyst manufacture, residence time distribution, reactor flow models and reactor stability and optimization.', 'CHE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHE 735', 'Research Topics in Chemical Kinetics, Catalysis and Advanced Reactor Engineering', 0.5, 'RDG', 'Various special research topics will be offered in the area of advanced reactor engineering including topics such as chemical kinetics and catalysis to support independent and individual research by instructor consent.', 'CHE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHE 740', 'Special Topics in Polymer Science and Engineering', 0.5, 'LEC', 'None', 'CHE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHE 745', 'Research Topics in Polymer Science and Engineering', 0.5, 'RDG', 'Various special research topics will be offered in the area of polymer science and engineering to suport independent and individual research by instructor consent.', 'CHE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 620', 'Advanced Construction Engineering and Project Management', 0.5, 'LEC', 'The objective of this course is to learn about how advanced construction engineering and project management techniques can be applied to improve cost, schedule, safety, and quality of projects.  The course covers a variety of topics including: Critical Path Method, bidding strategy models; uncertainty and risK assessment; multiple-criteria decision analysis; planning of linear, repetitive & distributed projects; project control & delay analysis; Enterprise Resource Planning; heavy construction equipments and methods; modelling & simulation; construction methods design; positioning & locating technologies; equipment automation & robotics; opportunity analysis & process of innovation; modularization & prefabrication; and construction human resource management.', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHE 750', 'Special Topics in Electrochemical Engineering, Interfacial Engineering & Materials Science', 0.5, 'LEC', 'Various courses dealing with selected special topics such as extractive metallurgy, hydrometallurgy, electrochemistry,  electrochemical engineering, corrosion, materials science and engineering and interfacial phenomena.', 'CHE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHE 755', 'Res Topics in Electrochemical Engineering, Interfacial Eng &  Material Science', 0.5, 'RDG', 'Various special research topics will be offered in the areas of electrochemical engineering, interfacial engineering and materials science to support independent and individual research by instructor consent.', 'CHE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHE 760', 'Special Topics in Biochemical Engineering', 0.5, 'LEC', 'Various courses dealing with selected topics in biochemical engineering, biotechnology, tissue engineering, food engineering, waste treatment technology and microbial engineering.', 'CHE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHE 765', 'Research Topics in Biochemical Engineering', 0.5, 'RDG', 'Various special research topics will be offered in the area of biochemical engineering to support independent and individual research by instructor consent.', 'CHE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHE 770', 'Special Topics in Environmental Engineering and Pollution Control', 0.5, 'LEC', 'Various courses dealing with advanced methods of controlling air, water and solid waste pollution. Systems analysis and applications.', 'CHE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHE 775', 'Research Topics in Environmental Engineering and Pollution Control', 0.5, 'RDG', 'Various special research topics will be offered in the area of environmental engineering and pollution control to support independent and individual research by instructor consent.', 'CHE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 1', 'Pre-University Chemistry', 0, 'LEC', 'Essential preparation for first year chemistry courses. Formulae, nomenclature, stoichiometry, an introduction to thermochemistry, solution chemistry, chemical equilibria, acids, bases, oxidation-reduction reactions, kinetics and bonding.', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 100', 'Introduction to Chemical Sciences', 0, 'SEM', 'The Chemist in society: Lectures, workshops, or seminars focusing on academic and professional development [Offered: F]', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 120', 'Physical and Chemical Properties of Matter', 0.5, 'LEC, TST, TUT', 'The stoichiometry of compounds and chemical reactions. Properties of gases. Periodicity and chemical bonding. Energy changes in chemical systems. Electronic structure of atoms and molecules; correlation with the chemical reactivity of common elements, inorganic and organic compounds.', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 120L', 'Chemical Reaction Laboratory 1', 0.25, 'LAB', 'Selected experiments based on introductory level chemistry topics. This course is an introduction to the chemistry laboratory environment and focuses on the development of basic lab skills. [Offered: F, S]', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 123', 'Chemical Reactions, Equilibria and Kinetics', 0.5, 'LEC, TST, TUT', 'Properties of liquids and solutions. Introduction to chemical equilibria. Principles of acid-base equilibria, solubility and electrochemical processes. Chemical kinetics.', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 123L', 'Chemical Reaction Laboratory 2', 0.25, 'LAB', 'Selected experiments based on introductory level chemistry topics. This course is a continuation of CHEM 120L, with increased emphasis on assessment of experimental design.', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 140', 'Introduction to Scientific Calculations', 0.5, 'LAB, LEC', 'The application of numeric computing software to perform basic calculations, data analysis, regression analysis, plotting of scientific graphs, data manipulation, and equation solving will be covered in this course, with an emphasis placed upon chemical and biochemical concepts and applications. [Offered: F,W,S]', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 209', 'Introductory Spectroscopy and Structure', 0.5, 'LEC, TUT', 'The nature of electromagnetic radiation and an elementary outline of quantum mechanics in one dimension. For each of microwave, infrared, Raman, electronic, photoelectron, and nuclear magnetic resonance spectroscopy, the nature of the molecular energy levels involved and the type of molecular information that can be obtained using it are examined. [Offered: F]', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 212', 'Structure and Bonding', 0.5, 'LEC, TUT', 'An introduction to the principles of chemical structure and bonding, with emphasis on their application to inorganic systems. Topics include: atoms, orbitals, and periodicity; localized bonding models; symmetry and group theory; and molecular orbital theory. The subjects treated in this course are foundational components for advanced studies in all areas of chemistry. [Offered: W,S]', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 217', 'Chemical Bonding', 0.5, 'LEC', 'Atomic and molecular structure. Molecular symmetry. Localized bonding models. Molecular orbital theory. Structures of solids. [Not open to students in the following plans: Biochemistry, Chemical Physics, Chemistry, Computational Science (Biochemistry Specialization), Computational Science (Chemistry Specialization), Geochemistry, Science and Business (Biochemistry Specialization), Science and Business (Chemistry Specialization). Offered: F]', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 220', 'Intro Analytical Chemistry', 0.5, 'LEC, TUT', 'Quantitative and analytical chemistry including ionic equilibria, classical and more recent methods. Emphasis on planning and decision-making in the analytical process. [Offered: F]', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 220L', 'Analytical Chemistry Lab 1', 0.25, 'LAB', 'Selected experiments for students taking CHEM 220 or 228. [Offered: F]', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 221', 'Multi-Component Analysis', 0.5, 'LEC, TUT', 'Instrumental analytical chemistry, including traditional and more recent methods. Emphasis on planning and decision-making in the analytical process. [Offered W,S]', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 224L', 'Analytical Chemistry Laboratory 2', 0.5, 'LAB, TUT', 'Extensive lab experience for students who have taken CHEM 220. [Offered: W,S]', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 228', 'Chemical Analysis', 0.5, 'LEC', 'An introduction to methods of quantitative chemical analysis. [Not open to students in the following plans: Biochemistry, Chemical Physics, Chemistry, Geochemistry, Science and Business (Biochemistry Specialization), Science and Business (Chemistry Specialization), Offered: W]', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 228L', 'Analytical Chemistry Laboratory for Life Sciences', 0.25, 'LAB', 'Selected experiments for students taking CHEM 228. [Offered: S]', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 233', 'Fundamentals of Biochemistry', 0.5, 'LEC, TUT', 'Chemistry of amino acids, carbohydrates, lipids and nucleic acids, with special emphasis on representative proteins and enzymes, including hemoglobin, cytochrome c and chymotrypsin. [Offered: W,S]', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 237', 'Introductory Biochemistry', 0.5, 'LEC, TST, TUT', 'An introduction to the chemistry of amino acids, carbohydrates, lipids and nucleic acids. Structure and properties of proteins and enzymes. [Offered: W, S]', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 237L', 'Introductory Biochemistry Laboratory', 0.25, 'LAB, TUT', 'Selected experiments for students taking CHEM 237. [Offered: W, S]', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 240', 'Mathematical Methods for Chemistry', 0.5, 'LEC, TUT', 'Mathematical techniques useful for chemistry students. Introduction to complex numbers, plus topics chosen from: calculus; differential equations; vector spaces and vector algebra; matrices and determinants; elementary probability theory; basic group theory and symmetry. Applications to problems of chemical interest. [Offered: F]', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 250L', 'Physical Chemistry Laboratory 1', 0.25, 'LAB, SEM', 'Selected experiments for students in year two. [Offered: F]', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 254', 'Introductory Chemical Thermodynamics', 0.5, 'LEC, TUT', 'An introduction to the first, second and third laws of thermodynamics and the application of these laws to ideal systems, mixtures, and chemical reactions. Thermodynamic principles are used to study changes in state, including phase changes, and to establish the link between the equilibrium constant and the properties of the substances involved in a chemical reaction. [Offered: W,S]', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 262', 'Organic Chemistry for Engineering', 0.5, 'LEC, TUT', 'Bonding, structure and nomenclature in organic chemistry. Physical properties and simple reactions associated with the important functional groups.[Offered: F,W]', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 262L', 'Organic Chemistry Laboratory for Engineering Students', 0.25, 'LAB', 'Selected experiments for engineering students taking CHEM 262.[Offered: F,W]', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 264', 'Organic Chemistry 1', 0.5, 'LEC, TUT', 'Structure and bonding in organic chemistry. Isomerism and stereoisomerism in organic compounds. Acidity of organic compounds and substituent effects on acidity. Reaction mechanisms and energetics. Chemistry of alkanes, haloalkanes, alcohols and ethers, alkenes and alkynes. [Offered: F, S]', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 265', 'Organic Chemistry 2', 0.5, 'LEC, TUT', 'Nucleophilic addition and substitution at CO carbon. Aromaticity and simple MO theory of conjugated systems. Electrophilic and nucleophilic aromatic substitution reactions. Applications of spectroscopic techniques in organic chemistry. [Offered: W,S]', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 265L', 'Organic Chemistry Laboratory 1', 0.25, 'LAB', 'Selected experiments for students taking CHEM 265. [Offered: W,S]', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 266', 'Basic Organic Chemistry 1', 0.5, 'LEC, TST', 'Structure, nomenclature and reactions of important classes of organic compounds. Stereochemistry and its role in reaction mechanisms. [Offered: F]', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 266L', 'Organic Chemistry Laboratory', 0.25, 'LAB', 'Selected experiments for students taking (or who have taken) CHEM 266.', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 267', 'Basic Organic Chemistry 2', 0.5, 'LEC, TST', 'A continuation of the concepts of CHEM 266, including material on amines, aromatics, enols and enolates, and nucleophilic addition and substitution at the carbonyl group. Introduction to nuclear magnetic resonance and infrared spectroscopy. [Offered: W]', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 267L', 'Organic Chemistry Laboratory', 0.25, 'LAB', 'Selected experiments for students taking CHEM 267.', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 310', 'Transition Element Compounds and Inorganic Materials', 0.5, 'LEC, TUT', 'The inorganic, organic and solid state chemistry of the d-block elements. The structure and physical properties of coordination compounds and transition metal containing solids. The role of transition metal organometallics in catalysis. [Offered: F]', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 310L', 'Inorganic Chemistry Laboratory 2', 0.5, 'LAB, SEM', 'Synthesis of transition and non-transition metal compounds. Characterization of compounds using IR, UV-VIS and NMR spectroscopy. [Offered: F]', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 313', 'Main Group and Solid State Chemistry', 0.5, 'LEC', 'This course provides a detailed examination of the structure and bonding in main group and solid state compounds, including valence bond and molecular orbital theory for describing electronic structures, Hueckel and extended Hueckel approximations. Structures of simple solids, including close packing of spheres and derived ionic lattice types; aspects of chemical crystallography, Bravais lattices, point groups, space groups, crystal planes, and X-ray diffraction; Ionic interactions in gases and solution; the thermodynamics of acid-base interactions; descriptive chemistry and characterization of main group element compounds. [Offered: W]', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 313L', 'Inorganic Chemistry Laboratory 1', 0.25, 'LAB', 'Introduction to synthetic inorganic chemistry. [Offered: W]', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 323', 'Analytical Instrumentation', 0.5, 'LEC', 'Detailed study of selected instruments and instrumental methods. Introduction to chemometrics and to computer interfacing. [Offered: F]', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 331', 'Fundamentals of Metabolism 1', 0.5, 'LEC, TUT', 'Thermodynamics of metabolism. Metabolism of carbohydrates and lipids. Chemistry of oxidative phosphorylation and photosynthesis. Emphasis is put on the role and chemical mechanisms of the enzymes in these processes [Offered: F]', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 333', 'Metabolism 1', 0.5, 'LEC, TUT', 'Metabolism of carbohydrates, lipids and amino acids. [Offered: F]', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 335L', 'Advanced Biochemistry Laboratory', 0.5, 'LAB, TUT', 'Selected experiments for students having completed or concurrently taking CHEM 331. Topics to be covered include: NMR, allostery, enzymology, electrophoresis, carbohydrates, lipids, photosynthesis, and respiration. [Offered: F, W]', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 340', 'Introductory Computational Chemistry', 0.5, 'LAB, LEC, TUT', 'Introduction to the theory and practice of computational methods used in chemistry. Use of molecular modeling software to investigate the electronic structure and geometry of molecules, to calculate potential energy surfaces for chemical reactions, and to predict and understand the behaviour of chemical systems. [Offered: W]', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 350', 'Chemical Kinetics', 0.5, 'LEC, TUT', 'Basic chemical kinetics; treatment of kinetic data; complex reaction mechanisms; fast reactions; the canonical ensemble and the canonical partition function; statistical mechanics applied to chemistry; statistical theory of reaction rates. [Offered: W]', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 350L', 'Physical Chemistry Laboratory 2', 0.5, 'LAB', 'Selected experiments for students in year three. [Offered: F]', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 356', 'Introductory Quantum Mechanics', 0.5, 'LEC, TUT', 'Historical background; the differential equation approach to quantum mechanics; treatments of solvable problems such as the particle-in-a-box, harmonic oscillator, rigid rotator and the hydrogen atom; introduction to approximation methods for more complicated systems. [Offered: F]', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 357', 'Physical Biochemistry', 0.5, 'LEC, TUT', 'The use of diffusion, ultracentrifugation, osmotic pressure, eletrophoresis and X-ray diffraction to study the properties of biopolymers. Hyperbolic and allosteric enzyme kinetics, inhibition and regulation. Some spectroscopies important to the life sciences. [Offered: W]', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 360', 'Organic Chemistry 3', 0.5, 'LEC, TUT', 'Enolate alkylation, condensation, conjugate addition and pericyclic reactions. FMO theory, organometallics and transition metal-catalyzed C-C bond formation. [Offered: W]', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 360L', 'Senior Organic Chemistry Laboratory', 0.5, 'LAB', 'Selected microscale synthetic experiments for students in Year Three Chemistry and Biochemistry programs, including spectroscopic identification of organic compounds. [Offered: W]', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 370', 'Introduction to Polymer Science', 0.5, 'LEC, TUT', 'Basic definitions and polymer nomenclature, molecular weight averages and distributions, constitutional and configurational isomerism, rubber elasticity, step-growth and free radical chain growth polymerizations, emulsion polymerization. [Offered: F]', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 381', 'Bioorganic Chemistry', 0.5, 'LEC', 'Review of stereochemistry, racemic and enantioselective syntheses of amino acids, peptide synthesis, development of peptides into drugs, physical properties and structures of monosaccharides, reactions of monosaccharides, glycoside synthesis, structures of disaccharides and polysaccharides, synthesis of oligosaccharides, physical properties and synthesis of nucleosides, nucleotides, and nucleic acids, nucleotides and nucleic acids as drugs, general drug development. [Offered: W]', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 382L', 'Advanced Organic Synthesis Laboratory', 0.5, 'LAB, TUT', 'A laboratory course intended for students in the Medicinal Chemistry Specialization of the Honours Chemistry plan. The purpose of this course is to introduce students to advanced laboratory techniques used in synthetic organic chemistry. [Offered: F]', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 392A', 'Research Project 1', 0.75, 'PRJ', 'This course is only for exchange students wishing to carry out a research project.', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 392B', 'Research Project 2', 0.75, 'PRJ', 'This course is only for exchange students wishing to carry out a research project.', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 404', 'Physicochemical Aspects of Natural Waters', 0.5, 'LEC, TUT', 'Properties of water; chemicals in the environment; environmental fate of inorganic and organic pollutants; basic phenomena affecting the fate of water pollutants (vapor pressure; activity, solubility, partitioning, diffusion, sorption); acids and bases in water; dissolved carbon dioxide; trace metals in water. [Offered: F or W]', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 410', 'Special Topics in Inorganic Chemistry', 0.5, 'LEC', 'For a current list of offerings see the Undergraduate Officer.', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 420', 'Special Topics in Analytical Chem', 0.5, 'LEC', 'For a current list of offerings see the Undergraduate Officer. [Formerly CHEM 425. Offered: F,W.]', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 430', 'Special Topics in Biochemistry', 0.5, 'LEC', 'For a current list of offerings see the Undergraduate Officer.', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 432', 'Metabolism 2', 0.5, 'LEC, TUT', 'Properties and metabolism of porphyrins, purines, pyrimidines and biogenic amines. Biosynthesis and mode of action of selected cofactors. Structure-function relationships of enzymes. Regulation of enzyme activity. [Offered: F]', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 433', 'Advanced Biochemistry', 0.5, 'LEC, TUT', 'Nitrogen fixation. Assimilation of nitrogen. Amino acid metabolism. Metabolic regulation. Proteolytic enzymes, ubiquitin. Blood coagulation. Signal transduction and amplification. Biochemistry of nitric oxide. Biochemistry of vision. [Offered: W]', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 440', 'Special Topics in Computational/Theoretical Chemistry', 0.5, 'LEC', 'For a current list of offerings see the Undergraduate Advisor. [Offered: F,W]', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 450', 'Special Topics in Physical Chemistry', 0.5, 'LEC', 'For a current list of offerings see the Undergraduate Officer. [Formerly CHEM 452. Offered: F,W ]', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 460', 'Special Topics in Organic Chemistry', 0.5, 'LEC', 'For a current list of offerings see the Undergraduate Officer.', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 464', 'Spectroscopy in Organic Chemistry', 0.5, 'LEC', 'Elucidation and identification of organic structures by contemporary spectroscopic techniques. [Offered: F]', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 470', 'Special Topics in Polymer Chemistry', 0.5, 'LEC', 'For a current list of offerings see the Undergraduate Advisor. [Offered: F,W]', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 481', 'Rational Design of Potential Drug Candidates', 0.5, 'LEC, TUT', 'This course provides an introduction to strategies for design of potential drug candidates. It builds upon molecular modelling principles introduced in CHEM 340 and will apply them to specific problems in drug design. Topics include: conformational analysis, molecular mechanics, and molecular dynamics; computational studies of drug-receptor interactions, docking of small organic molecules to biological receptors, and alteration of molecular structures for improvement of bioactivity.', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 482', 'Advanced Topics in Medicinal Chemistry', 0.5, 'LEC, TUT', 'This course discusses the drug discovery process and drug synthesis. Topics include enzymes and receptors, hit and lead discovery, lead optimization, QSAR, prodrugs, drug delivery, drug metabolism, mechanisms of selected drugs and laboratory and process scale synthesis of selected drugs.', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 491A', 'Biobased Chemistry Research Project 1', 0.5, 'PRJ', 'Student teams work on the projects defined in CHEM 302 (Innovation and Project Management). [Offered: F]', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 494A', 'Research Project', 0.5, 'PRJ', 'Laboratory work on a senior year research project. Enrolment into this course requires permission of the CHEM 494 co-ordinator. See the CHEM 494 coordinator for course details. No credit or grade will be provided for this course until the two-term sequence CHEM 494A/B has been completed. CHEM 494A/B may not be taken concurrently without prior permission of the CHEM 494 co-ordinator.', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 494B', 'Research Project', 0.5, 'PRJ', 'A continuation of CHEM 494A. No credit or grade will be provided for this course until the two-term sequence CHEM 494A/B has been completed. CHEM 494A/B may not be taken concurrently without prior permission of the CHEM 494 co-ordinator.', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 495', 'Advanced Research Project', 2.5, 'PRJ', 'This course is only for exchange students wishing to carry out an advanced research project during the fall term.', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 496', 'Advanced Research Project', 2.5, 'PRJ', 'This course is only for exchange students wishing to carry out a research project during the winter term.', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 497', 'Advanced Research Project', 2.5, 'PRJ', 'This course is only for exchange students wishing to carry out a research project during the spring term.', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 710', 'Selected Topics in Inorganic Chemistry', 0.5, 'LEC', 'Discussion of specialized topics related to the research interests of members of the Centre. Special topics could include, for example, bioinorganic chemistry; inorganic reaction mechanisms; synthetic methods in inorganic and organometallic chemistry; homogeneous and heterogeneous catalysis; chemistry of polynuclear compounds.', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 712', 'X-Ray Crystallography', 0.5, 'LEC', 'Introduction: crystals, basic concepts; space groups; the reciprocal lattice; x-ray diffraction; the phase problem; structure factors; electron density; small molecule structure solution, structure refinement, structure results, journals and data bases, paper writing.', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 713', 'Chemistry of Inorganic Solid State Materials', 0.5, 'LEC', 'Introduction to solid state chemistry, common crystal structures, principles of solid state synthesis, theory and experimental methods for characterizing solids, including thermal analysis techniques, powder x-ray and neutron diffraction methods; special topics to include one or more of the optical, electronic, magnetic, or conductive properties of inorganic materials.', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 715', 'Structure & Bonding in Inorganic Chemistry', 0.5, 'LEC', 'Free electron, Hueckel and extended Hueckel methods for molecules and clusters. Perturbation theory. Applications of group theory in inorganic chemistry; Jahn-Teller effects in molecular and solids. Energy bands in one, two and three dimensions.', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 717', 'Advanced Transition Metal Chemistry', 0.5, 'LEC', 'Magnetochemistry of transition metal compounds. Electronic spectra of complex ions including applications of molecular orbital and ligand field theories. Stabilization of unusual oxidation states and coordination numbers. Bonding, structure and reactivity of certain important classes of metal complexes, e.g. metal hydrides, metal-metal bonded species, biologically-significant model systems such as macrocycles.', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 718', 'Advanced Organometallic Chemistry', 0.5, 'LEC', 'Reactions, structure and bonding of organometallic compounds of transition and non-transition metals.', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 720', 'Selected Topics in Analytical Chemistry', 0.5, 'LEC', 'Special topics could include for example: trace analysis using modern instrumental and spectroscopic methods; advanced mass spectrometry (instrumentation and interpretation of spectra); analytical aspects of gas and liquid chromatography.', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 724', 'Chemical Instrumentation', 0.5, 'LEC', 'Instrumental components and optimum application; rudiments of design; electrical, spectral, migrational and other methods.', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 726', 'Topics in Analytical Spectroscopy', 0.5, 'LEC', 'Atomic emission and absorption spectroscopy; methods of excitation and detection; quantitative applications. Molecular electronic spectroscopy: UV, visible and Raman; instrumental characteristics; applications to quantitative determinations, speciation, measurements of equilibrium, etc. Sources and control of errors and interferences. Determination and description of colour.', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 727', 'Separations', 0.5, 'LEC', 'Material to be covered will be drawn from the following topics: Diffusion; Isolation of organic material from the matrix; Chromatographic techniques - principles of chromatographic separations; Gas (GLC, GSC), Liquid (LLC, LSC, GPC, IEC), Supercritical Fluid (SFC) Chromatographies; GC-MS, GC-FTIR; Electrophoresis; Flow field fractionation.', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 728', 'Electroanalytical Chemistry', 0.5, 'LEC', 'A study of electroanalytical techniques and their role in modern analytical chemistry. The underlying principles will be developed. Techniques will include chronoamperometry, chronocoulometry, polarography, voltammetry, chronopotentiometry, coulometric titrations, flow techniques, electrochemical sensors and chemically modified electrodes.', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 729', 'Surface Analysis', 0.5, 'LEC', 'Modern surface analysis: description and importance of surfaces. Surface area determinations. High surface area solids, supports. Scanning electron microscopy: principles and applications. Auger electron spectroscopy. Applications in metallurgy and materials science. Depth profiling. ESCA: elemental compositions and studies of catalyst surfaces. SIMS. Infrared spectroscopy. Studies of supported metal and metal oxides. Acid surface sites and their role in catalysis. Scanning tunnelling microscopy; theory and applications. Single crystal surfaces. LEED. Studies of chemisorption.', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 730', 'Proteins and Nucleic Acids', 0.5, 'LEC', 'Protein structure and function; intermolecular interactions and protein-protein association. Nucleic acid and DNA structure; protein-nucleic acid interactions.', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 731', 'Selected Topics in Biochemistry', 0.5, 'LEC', 'Discussion of specialized topics related to the research interests of the members of the Centre. For example, recent offerings have included: Metalloproteins and Metalloenzymes; Heme Proteins; Chemistry of Enzymatic Reaction Mechanisms; Peptides - Synthesis, Structure and Function; Food Enzymology; Advanced Microbial Physicology; NMR in Biological Systems.', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 615', 'Theories of Plates and Shells', 0.5, 'LEC', 'Plate theory, membrane theory of shells, general theory of axisymmetric shells. Application of the three theories to linear and nonlinear engineering analysis.', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 736', 'Regulations in Biological Systems', 0.5, 'LEC', 'Regulation of replication, transcription, translation, RNA processing, and protein degradation. Cell cycle and control of cell division.', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 737', 'Enzymes', 0.5, 'LEC', 'Discussion of the underlying factors contributing to enzyme, abzyme and ribozyme catalysis.', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 738', 'Cell Membranes and Cell Surfaces', 0.5, 'LEC', 'Structure, function and dynamics of membrane lipids and proteins. Membrane transport. Biogenesis and trafficking of membrane proteins. Signal transduction.', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 740', 'Selected Topics in Theoretical Chemistry', 0.5, 'LEC', 'Discussion of specialized topics related to the research interests of the members of the Centre. Special topics could include for example: theory of intermolecular forces; density matrices; configuration interaction; correlation energies of open and closed shell systems; kinetic theory and gas transport properties; theory of the chemical bond.', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 745', 'Statistical Mechanics', 0.5, 'LEC', 'Review of classical and quantum mechanics; principles of statistical mechanics; applications to systems of interacting molecules; imperfect gases, liquids, solids, surfaces and solutions.', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 746', 'Quantum Chemistry', 0.5, 'LEC', 'Approximate solutions of the Schrodinger equation and calculations of atomic and molecular properties.', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 750', 'Selected Topics in Physical Chemistry', 0.5, 'LEC', 'Discussion of specialized topics related to the research interests of members of the Centre. Special topics could include for example: principles of magnetic resonance in biological systems; collisions, spectroscopy and intermolecular forces, surface chemistry; catalysis; electrolyte theory; non-electrolyte solution theory; thermodynamics of biological systems; thermodynamics.', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 755', 'Kinetics - Dynamics', 0.5, 'LEC', 'Empirical analysis. Kinetic theory of gases. Potential energy surfaces. Unimolecular rates. Relaxation and steady state methods. Diffusion rates. Rates between polar molecules. Energy transfer.', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 756', 'Spectroscopy', 0.5, 'LEC', 'Aspects of electronic vibrational and rotational spectroscopy of atoms, molecules, and the solid state. Relevant aspects of quantum mechanics, Dirac notation, and angular momentum will be discussed. Group Theory will be presented and its implications for spectroscopy introduced.', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 760', 'Selected Topics in Organic Chemistry', 0.5, 'LEC', 'Two or three topics from a range including: bio-organic chemistry; environmental organic chemistry; free radicals; heterocyclic molecules; molecular rearrangements; organometallic chemistry; photochemistry; natural products.', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 764', 'Synthetic Organic Reactions', 0.5, 'LEC', 'Named organic reactions and other synthetically useful reactions will be discussed. The mechanism, stereochemical implications and use in organic synthesis of these rections will be presented. Examples from the organic literature will be used to illustrate these aspects.', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 765', 'Strategies in Organic Synthesis', 0.5, 'LEC', 'The synthesis of organic compounds is discussed and emphasis is placed on the design of synthetic routes. Examples drawn from the literature are used to illustrate this synthetic planning.', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 766', 'Organic Spectroscopy', 0.5, 'LEC', 'Ultraviolet, infrared, resonance spectroscopy and mass spectrometry, with emphasis on applications to studies of organic molecules.', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 769', 'Physical Organic Chemistry', 0.5, 'LEC', 'Linear free energy relationships; substituent effects and reactive intermediates.', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 770', 'Principles of Polymer Science', 0.5, 'LEC', 'Introduction to the physical chemistry of high polymers, principles of polymer synthesis, mechanisms and kinetics of polymerization reactions, copolymerization theory, polymerization in homogeneous and heterogeneous systems, chemical reactions of polymers. Theory and experimental methods for the molecular characterization of polymers.', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 771', 'Physical Properties of Polymers', 0.5, 'LEC', 'The physical properties of polymers are considered in depth from a molecular viewpoint. Rubber elasticity, mechanical properties, rheology and solution behaviour are quantitatively treated.', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 772', 'Polymerization and Polymer Reactions', 0.5, 'LEC', 'The reactions leading to the production of polymers are considered with emphasis on emulsion and suspension polymerization and polymerization reaction engineering. Polymer degradation, stabilization and modification are also considered in depth.', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 773', 'Selected Topics in Polymer Chemistry', 0.5, 'LEC', 'Discussion of specialized topics of polymer chemistry related to the research interests of the faculty or prominent scientific visitors. Special topics could include, for example, polymer stabilization and degradation; mechanical properties; polymer principles in surface coatings; organic chemistry of synthetic high polymers; estimation of polymer properties; reactions of polymers; polymerization kinetics.', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 613', 'Structural Stability', 0.5, 'LEC', 'Fundamental concepts in elastic stability: equilibrium equations, stability criteria and post-buckling behaviour. Applications to the buckling of columns, frames, arches, plates and shells. Interactive buckling phenomena in light metallic constructions.', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 794', 'Master''s Seminar', 0.5, 'SEM', 'A written research proposal, and a public seminar and defence of this proposal, to be given by all MSc students within two terms of entering this program.', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHINA 101R', 'First-Year Chinese 1', 0.5, 'LAB, LEC, TUT', 'An introductory course for students who have little or no prior background in writing, speaking, or understanding any dialect of the Chinese language to develop basic listening, speaking, reading, and writing skills. Practical oral and written exercises provide a firm grammatical foundation for further study.', 'CHINA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHINA 102R', 'First-Year Chinese 2', 0.5, 'LAB, LEC', 'With the completion of the study of the rudiments of phonetics (as provided in CHINA 101R), the emphasis in this course shifts to grammar and character writing. Vocabulary will be expanded to between 500 and 700 words.', 'CHINA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHINA 120R', 'Advanced First-Year Chinese', 0.5, 'LAB, LEC, TUT', 'Equivalent to 101R and 102R but covered in one term. This introductory Chinese course is designed for two major groups of students who have different initial advantages in learning Chinese: 1) students who have substantial aural-oral proficiency but limited ability in reading and writing Chinese characters and 2) those who know characters but cannot speak Mandarin (Putonghua).', 'CHINA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHINA 200R', 'Preliminary Second-Year Chinese', 0.5, 'LAB, LEC', 'This course expands the vocabulary and sentence patterns for students who have acquired 400 Chinese characters. Attention is paid to grammar, vocabulary, and discourse by interweaving cultural norms and customs. Reading comprehension is emphasized, encouraging the transition from pinyin to characters.', 'CHINA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHINA 201R', 'Second-Year Chinese 1', 0.5, 'LAB, LEC', 'Development of speaking, writing, reading, and listening skills. This course and its follow-up (CHINA 202R) include a survey of grammar, complex sentences, and logical stress.', 'CHINA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHINA 202R', 'Second-Year Chinese 2', 0.5, 'LAB, LEC', 'The study of Chinese characters receives more emphasis. Grammar instruction includes comparisons and different kinds of complements. The course includes topics of interest to students illustrating cultural differences between China and the West. Upon completion of CHINA 201R and 202R, the student should have a reading vocabulary of 1,600 Chinese characters and have learned about 300 key sentence patterns.', 'CHINA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHINA 272R', 'Chinese Culture and Society', 0.5, 'LEC', 'The course explores the interface of tradition and modernity in Chinese life. Topics include traditional and modern Chinese culture, language, education, art, technology, and business.', 'CHINA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHINA 301R', 'Third-Year Chinese 1', 0.5, 'LAB, LEC', 'A course to consolidate, expand, and deepen the understanding of lexical items and sentence patterns. Besides introducing more vocabulary and grammar, this course concentrates on complex sentences and paragraphs not emphasized in earlier textbooks.', 'CHINA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHINA 302R', 'Third-Year Chinese 2', 0.5, 'LAB, LEC', 'A continuation of China 301R to further students'' ability to comprehend and communicate at a higher level in Chinese. In addition, the course introduces aspects of Chinese society, highlighting traditional and contemporary cultural life.', 'CHINA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHINA 310R', 'Chinese for Business Settings', 0.5, 'LAB, LEC', 'A course to develop comprehension, speaking, reading, and writing of Mandarin specifically related to the Chinese business environment. This course is designed for students who are already familiar with Chinese characters and Mandarin tonality.', 'CHINA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHINA 320R', 'Chinese in Mass Media', 0.5, 'LAB, LEC', 'A course to develop Chinese language skills in conversation, reading, writing, and critical thinking in both practical and cultural situations through contemporary films, television programs, newspapers, magazines, and literary works. Selected important issues and themes in Chinese culture and history are considered.', 'CHINA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHINA 390R', 'Introduction to Professional Translation (Chinese to English)', 0.5, 'LEC', 'This course is for students with a strong command of Chinese and English who seek to learn the basics of professional translation between the two languages. Focus is on translation involving government and business documents, as well as news stories.', 'CHINA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 121', 'Computational Methods', 0.5, 'LAB, LEC, TST', 'Introduction to computer programming, examples of efficient numerical algorithms for basic scientific computations. Programming and problem solving concepts introduced in the course will be incorporated into group projects involving civil, environmental, or geological engineering applications. The language of instruction will be Matlab. [Offered as: CIVE 121 (W), ENVE 121 (S), GEOE 121 (S)]', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 125', 'Civil Engineering Concepts 1', 0.8, 'LAB, LEC, TST, TUT', 'An introduction to some of the basic methods and principles in Civil Engineering. The fundamentals of engineering calculations: units and dimensions. Surveying, data collection, measurement and error analysis. Laboratory on visual communication: engineering graphics including projections, computer software including spread sheets, computer aided design. Introduction to engineering design. Technical communication: word processing software, elements of technical report writing. Aspects of the engineering profession including ethics, safety, and intellectual property. Professional development including r√©sum√© skills, interview skills, and preparation for co-op terms. [Offered: F]', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 127', 'Statics & Solid Mechanics 1', 0.5, 'LEC, TST, TUT', 'Two-dimensional force systems, moments, couples, and resultants. Two-dimensional equilibrium problems including trusses and frames. Distributed forces, centroids and moment of inertia. Stress-strain-temperature relationships. Behaviour of prismatic members in tension, compression, shear, bending and torsion. [Offered: W, S]', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 153', 'Earth Engineering', 0.5, 'LAB, LEC, TST, TUT', 'This course studies earth materials and processes from an engineering point of view through case histories and problem sets. The course develops a geological knowledge for applications to any physical environment and provides an appreciation of the impact of engineering work on the environment. Topics include mineral and rock identification, the rock cycle, structural geology and tectonics, geology of Canada, effects of water, ice and wind. Students are also introduced to the concept of geologic time, topographic and geologic maps, and the basic principles and tools used to determine geologic history. [Offered as: CIVE 153 (W), EARTH 153 (S), ENVE 153 (S), GEOE 153 (S)]', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 199', 'Seminar', 0, 'SEM', 'General seminar. [Offered: W]', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 204', 'Solid Mechanics 1', 0.5, 'LEC, TST, TUT', 'Three-dimensional force systems, moments, couples, and resultants. Three-dimensional equilibrium problems. Shear stresses in beams. Plastic bending. Beam deflection. Torsion of shafts and thin-walled closed sections. Shear, bending moment, and deflection diagrams for beams. Compound stress and stress transformations. Design concepts. [Offered: F]', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 205', 'Solid Mechanics 2', 0.5, 'LEC, TST, TUT', 'Frames, arches and suspended structures. Stress and strain transformations. Strain energy. Energy methods. Virtual work. Buckling of columns. [Offered: S]', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 221', 'Advanced Calculus', 0.5, 'LEC, TST, TUT', 'Calculus of functions of several variables. Differentiation; partial derivatives of implicit and explicit functions, applications including optimizations. Integration; multiple integrals in various co-ordinate systems with applications. Vector calculus; vector fields, line integrals, surface integrals, and applications. Numerical integration and differentiation. [Offered: F]', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 222', 'Differential Equations', 0.5, 'LEC, TST, TUT', 'An introduction to linear and partial differential equations. Standard methods of solution, applications to physical and engineering problems, linear equations with constant coefficients, basic systems of differential equations, partial differential equations. Applications. [Offered: S]', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 224', 'Probability and Statistics', 0.5, 'LEC, TST, TUT', 'Role of probability in engineering and decision-making under uncertainty. Data analysis. Basic probability concepts. Probability distributions. Functions of random variables. Estimation theory. Empirical determination of distribution models. Regression analysis. Introduction to risk. [Offered: F]', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 240', 'Engineering and Sustainable Development', 0.5, 'LEC, TST, TUT', 'This course explores the concepts of sustainability, namely the balancing of economic, environmental, social, cultural, health and political needs, as it pertains to Civil Engineering decisions. The course examines aspects of urban transportation and infrastructure planning, land-use, and issues related to water, air, and noise pollution. Methods of quantifying costs associated with health risks and consumption of non-renewable resources are presented. Case studies from a range of Civil Engineering application areas are used to examine the effect of engineering decisions on sustainability. [Offered: S]', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 265', 'Structure and Properties of Materials', 0.5, 'LAB, LEC, TST, TUT', 'A basic course in structure, behaviour and uses of engineering materials. Topics include monotonic and cyclic stress-strain behaviour of metals. Phase diagrams. Diffusion, nucleation and growth of grains. Metallurgy and mechanical properties of irons and steels. Structure and mechanical properties of wood, cements and concrete. Fracture, fatigue and corrosion. Three lab sessions. [Offered: F]', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 280', 'Fluid Mechanics', 0.5, 'LAB, LEC, TST, TUT', 'An introduction to fluid mechanics. Fluid properties. Review of fluid statics. Buoyancy. Bernoulli equation. The momentum equation and applications. Laminar and turbulent flow. Dimensionless numbers. Closed conduit flow including friction losses. Pipe network analysis. Pump systems. Four lab sessions. [Offered: S]', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 291', 'Survey Camp', 0.5, 'LAB', 'Introduction to surveying, length measurements, levelling, transit surveys.', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 292', 'Engineering Economics', 0.5, 'LEC, TST, TUT', 'An introductory course on the principles of engineering economy. Basic concepts. Capital. Interest formulas and derivations. Annual worth comparisons. Present worth. Return on investment. Benefit-cost ratio depreciation. Effect of taxes. [Offered: F]', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 298', 'Seminar', 0, 'SEM', 'General seminar. [Offered: F]', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 299', 'Seminar', 0, 'SEM', 'General seminar. [Offered: S]', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 303', 'Structural Analysis', 0.5, 'LEC, TST, TUT', 'Analysis of statically indeterminate structures using force and displacement methods. Influence lines for indeterminate structures. Introduction to the matrix stiffness method. Computer applications using commercial structural analysis software. [Offered: W]', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 306', 'Mechanics of Solids 3', 0.5, 'LEC, TUT', 'Membrane stresses in shells. Buckling. Beams on elastic foundations. Plane elasticity. Torsion of non-circular sections. [Offered: F]', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 313', 'Structural Concrete Design 1', 0.5, 'LEC, TUT', 'Reinforced concrete members. Concrete and reinforcing steel materials. Safety, loads, design criteria. Flexure, shear, combined bending and axial force. Serviceability. One-way slabs, beams, columns, foundations and retaining walls. [Offered: F]', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 331', 'Advanced Mathematics for Civil Engineers', 0.5, 'LEC, TST, TUT', 'This course is an integration of CIVE 121, CIVE 221, and CIVE 222 in which both classical calculus theory and basic computational algorithms were discussed. Partial differential equations (PDEs) with application in the modelling of civil engineering processes (e.g., wave, diffusion, Laplace and Poisson equations). Boundary and initial conditions. Numerical integration. Numerical interpolation schemes for irregularly spaced spatial data (e.g., splines, Lagrange polynomials, etc). Solution methods for linear and non-linear systems of algebraic equations. Numerical solution of PDEs using the finite difference method. Aspects of the finite element method. An emphasis will be placed on algorithm development and implementation. Maple and Visual Basic will be integral tools in this course. [Offered: W]', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 332', 'Civil Systems and Project Management', 0.5, 'LEC, TST, TUT', 'Introduction to conceptual planning, construction, management, optimization and life-cycle performance assessment of civil engineering systems including capital projects. Fundamentals of decision theory including concepts of risk, uncertainty, utility, probability, value of information and game theory. Tools for supporting decision-making process, including linear and integer programming, network models, optimization, and Monte Carlo simulation. Risk- and cost-benefit analysis of public projects and their impact on sustainability, and quality of life. [Offered: W]', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 342', 'Transport Principles and Applications', 0.5, 'LEC, TST, TUT', 'Introduction to basic principles and procedures of transport planning and engineering applied to Canadian intercity transport problems. [Offered: W]', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 343', 'Traffic Simulation Modelling and Applications', 0.5, 'LAB, LEC, TUT', 'Fundamental knowledge on the principles and applications of traffic simulations. System theory, traffic flow dynamics, stochastic simulation methods. Calibration and validation of simulation models, and interpretation and analysis of simulation output. Applications of state-of-the-art computer simulation software packages for solving real traffic engineering problems, involving scenario analysis, prediction, and optimization. [Offered: F]', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CLAS 123', 'Classical Studies in Pop Culture', 0.5, 'LEC', 'An introduction to the role that classical studies and the classical cultures have played in pop culture. This course will examine specific references, such as ancient Greek myths, and more general trends, such as literary types, in contemporary movies, television shows, comic books, and video games to illustrate the continued fascination with the cultures of Ancient Greece and Rome.', 'CLAS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 353', 'Geotechnical Engineering 1', 0.5, 'LAB, LEC, TST, TUT', 'An introduction to geologic processes. Subsurface exploration. Classification systems. Weight-volume relationships. Soil mechanics principles including state of stress, ground water flow, consolidation and shear strength. Four lab sessions. [Offered as: CIVE 353 (W), GEOE 353 (S)]', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 354', 'Geotechnical Engineering 2', 0.5, 'LEC, TST, TUT', 'Foundation engineering. Earth pressure theories. Retaining walls. Anchors. Shallow and deep foundations. Braced trenches and excavations. Slope stability. [Offered: F]', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 375', 'Environmental Engineering Principles', 0.5, 'LAB, LEC, TST, TUT', 'Water quality, air pollution, fate and transport of contaminants in natural and engineered systems, and pollution prevention. Solid and hazardous waste management. Water and wastewater treatment systems and design principles. Four lab sessions. [Offered: F]', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 781', 'Principles of Hydrologic Modelling', 0.5, 'LEC', 'This course addresses the development of computational models of watershed hydrology in support of water resources management and scientific investigation.  The full model development and application cycle is considered:  Pre-processing, understanding, and generating input forcing data; system discretization and algorithms for simulating hydrologic processes;  parameter estimation; and interpreting model output in the context of often significant system uncertainty.  The course will include practical applications of models to alpine, boreal forest, prairie, and agricultural settings in Canada.', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 381', 'Hydraulics', 0.5, 'LAB, LEC, TST, TUT', 'Energy, momentum and continuity equations for open channel flow. Dimensional analysis and modelling. Design of lined and unlined open channels. Water profile computations. Bridge and culvert hydraulics. Hydraulic structures and energy dissipators. Pumping stations. Water hammer. Four lab sessions. [Offered: F, W]', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 398', 'Seminar', 0, 'SEM', 'General seminar. [Offered: W]', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 399', 'Seminar', 0, 'SEM', 'General seminar. [Offered: F]', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 400', 'Civil Engineering Design Project 1', 0.5, 'LEC, PRJ', 'Students must undertake an independent civil engineering design project during the last two terms of their plan. The purpose of the project is to demonstrate students'' abilities to practise in a civil engineering capacity in their chosen area of expertise, using knowledge gained from their academic and employment experiences. The first part of the project (CIVE 400) will include problem identification, generation and selection of solutions, and time management. Incorporation of technical, ecological, social, political, and economic issues in the solution for the project will be required. A basic requirement of the proposed solution is that it must be compatible with the principles of sustainability. Requirements include proposal, progress report, oral presentation, and a final report containing recommendations for part two of the project, CIVE 401. [Offered: S]', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 401', 'Civil Engineering Design Project 2', 0.5, 'PRJ', 'A continuation of CIVE 400. The final design of the major civil engineering project proposed in CIVE 400 will be undertaken. The purpose of this phase of the project is to carry out a detailed technical design of the solution proposed in CIVE 400. Requirements of this part of the two-term project include a symposium presentation, and a final report. [Offered: W]', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 403', 'Structural Analysis 2', 0.5, 'LEC, TUT', 'Advanced structural analysis; linear and nonlinear behaviour. Computer applications. [Offered: S]', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 405', 'Structural Dynamics', 0.5, 'LEC, TUT', 'Dynamics of continuous and discretized structures. Free and forced vibrations of single and multidegree of freedom systems. Impact, earthquake loads, wind loads. Vibration of beams, frames, structural systems. [Offered: W]', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 413', 'Structural Steel Design', 0.5, 'LEC, TST, TUT', 'Advanced coverage of design of structural steel members and connections, building on CIVE 310. Design of laterally-unsupported beams, compression members and beam-columns. Plate girders. Connections. Special topics in design of structural steel or other metals. [Offered: S]', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 414', 'Structural Concrete Design', 0.5, 'LEC, TUT', 'Advanced analysis and design of concrete members for flexure. Beam shear analysis and design. D-region shear design. Column design. Special topics. [Offered: S]', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 415', 'Structural System Design', 0.5, 'LEC, TUT', 'Building loads. Lateral load systems. Floor systems for buildings. Composite construction. Introduction to bridge design. Special topics.[Offered: W]', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 422', 'Finite Element Analysis', 0.5, 'LEC, TUT', 'This course focuses on the development of the basic fundamentals of finite element method with applications in fluid flow, mass transport, solid mechanics and structures. Topics include discrete problems, matrix methods, variational principle, method of weighted residuals, element shapes, and interpolation functions. [Offered: W]', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 440', 'Transit Planning and Operations', 0.5, 'LEC, TUT', 'The historical evolution of transit in cities; the technological innovations which made transit possible; and transit mode definitions. Models of transit vehicle motion are presented; transit travel times under different travel regimes are derived. Transit scheduling methods are shown. System operational characteristics are defined and quantitative measures of effectiveness are introduced. Transit network planning objectives are identified; actual geometries are qualitatively and quantitatively analyzed. Transit ownership structures and economics are discussed; contemporary ITS applications are presented. Methods for selecting appropriate transit modes are covered. [Offered: W]', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 444', 'Urban Transport Planning', 0.5, 'LEC, TUT', 'The course develops a number of standard methods for predicting travel in urban areas. General characteristics of urban travel and urban transport systems are presented along with a discussion of typical issues pertaining to urban areas. Methods used to evaluate alternatives and resolve issues are presented. These include trip generation, trip distribution and mode split. [Offered: S]', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 460', 'Engineering Biomechanics', 0.5, 'LEC, TUT', 'Introduction to engineering technologies applicable to the field of biomechanics. Specific topics covered may include biological growth, form and function; biomaterials; kinematics and neurology of gait; biotribology; joint anatomy, function and repair; occupational biomechanics; trauma prevention. [Offered: W]', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 486', 'Hydrology', 0.5, 'LEC, TST, TUT', 'Basic components of the hydrologic cycle. Introduction to frequency analysis and time series analysis. Rainfall-runoff relationships. Unit hydrograph theory. Hydrologic and hydraulic routing. Introduction to hydrologic design: design storms and storm water management. Rural and urban simulation models. [Offered: F, S]', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 491', 'Engineering Law and Ethics', 0.5, 'LEC, TST', 'Background (Charter of Rights and Freedoms), contracts, torts (negligent malpractice), forms of carrying on business, professional practice (professional engineers act, joint practice rules, professional misconduct and sexual harassment), alternate dispute resolution, construction liens, intellectual property (patents, trade marks, copyrights and industrial designs), labour relations and employment law, environmental law. [Offered: S]', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 497', 'Special Topics in Civil Engineering', 0.5, 'LEC, TUT', 'A special course on advanced topics in civil engineering is offered from time to time, when resources are available. For the current offering, inquire at the Department.', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 498', 'Seminar', 0, 'SEM', 'General seminar. [Offered: S]', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 499', 'Seminar', 0, 'SEM', 'General seminar. [Offered: W]', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 507', 'Building Science and Technology', 0.5, 'LEC, TUT', 'The building process. Loadings; gravity, wind, thermal, moisture, and fire. Enclosure design; walls, windows, and roof. Subgrade construction. Energy related considerations. [Offered: W]', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 512', 'Rehabilitation of Structures', 0.5, 'LEC, TUT', 'This course deals with the assessment, rehabilitation and/or strengthening of building and bridge infrastructure. Topics include damage mechanisms, instrumentation and non-destructive test methods, conventional repair techniques, innovative repair and strengthening techniques with composites. Case studies provide students with the opportunity to learn from field applications. The laboratory portion involves test methods used to evaluate repair and strengthening measures. [Offered: W]', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 542', 'Pavement Structural Design', 0.5, 'LAB, LEC', 'Pavement design, soil identification, subgrade design, base courses, flexible pavement design, design and testing of asphaltic concrete mixes, surface treatments. [Offered: W]', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 554', 'Geotechnical Engineering 3', 0.5, 'LEC, TST, TUT', 'Simulation of geotechnical consulting practice. Students are required to complete several projects, based on actual case studies, which require problem identification, evaluation of geotechnical data, analysis, design, and report preparations. [Offered: W]', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 572', 'Wastewater Treatment', 0.5, 'LAB, LEC, TUT', 'Wastewater quantity and characteristics. Primary treatment and secondary treatment. Reverse osmosis, ultra filtration, adsorption, air stripping, air flotation, chemical precipitation. Sludge treatment and disposal. Groundwater and leachate treatment. Industrial wastewater management. [Offered: S]', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 583', 'Design of Urban Water Systems', 0.5, 'LAB, LEC', 'Design of water supply and distribution systems. Design of waste and storm water collection systems. Storm water management. The course consists of 24 hours of lectures and a subdivision design project. The emphasis is on computer aided design and sustainability, using commonly used software packages. [Offered: W]', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 596', 'Construction Engineering', 0.5, 'LEC, TUT', 'Topics in construction engineering and management including methods of delivering construction, contractual relationships, prevailing construction practices, construction equipment, concrete form design, concrete, steel, and masonry construction, introduction to trenchless technology, construction safety, planning and scheduling of repetitive construction, cash flow analysis, and construction project control. [Offered: S]', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 601', 'Engineering Risk and Reliability', 0.5, 'LEC', 'This course gives a broad treatment of the subject of engineering decision, risk, and reliability. Emphasis is on (1) the modelling of engineering problems and evaluation of systems performance under conditions of uncertainty; (2) systematic development of design criteria, explicitly taking into account the significance of uncertainty; and (3) logical framework for risk assessment and risk-benefit tradeoffs in decision making. The necessary mathematical concepts are developed in the context of engineering problems.', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 602', 'Prestressed Concrete', 0.5, 'LEC', 'This course emphasizes the basic concepts in prestressed concrete analysis and design. Prestressing methods: pre-tensioning vs. post-tensioning. Serviceability and limit state design, prestress losses, flexural design of bonded and unbonded sections, fully prestressed vs. partially prestressed sections, design for shear, compression members, continuous prestressed concrete members.', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 603', 'Mechanics of Reinforced Concrete', 0.5, 'LEC', 'This course deals with the behaviour of reinforced concrete structures, the analysis of such structures, and the background for the design standards. Topics include the analysis of reinforced concrete structures, shear in reinforced concrete members, members in bending and axial loading, and connections.', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 604', 'Advanced Structural Steel Design', 0.5, 'LEC', 'This course deals with limit states design, torsional analysis of structural steel members; bolted and welded connections; stability and vibration; analysis and design of braced and unbraced steel frames, long span structures, and industrial steel buildings.', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 605', 'Computer-Aided Structural Design', 0.5, 'LEC', 'Historical review and basic concepts of structural optimization. Linear and nonlinear programming applications. Approximation concepts. Sensitivity analysis. Optimality criteria methods. Historical review and basic concepts of knowledge-based expert systems. Rule-based structural design. Complex and large-scale structural systems.', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 610', 'Elasticity', 0.5, 'LEC', 'Analysis of strain and stress; stress-strain relations; equations of elasticity; plane strain, plane stress and generalized plane stress; torsion and flexure; three-dimensional problems; variational methods; dynamical problems.', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 611', 'Finite Element Analysis', 0.5, 'LEC', 'The concept and theoretical basis of the finite element method are presented as a logical extension to solid body stress analysis of the matrix methods applied to structural frames. Students will develop and use software to analyze representative two- and three-dimensional problems. Introduction of other field problem applications included.', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 614', 'Structural Dynamics', 0.5, 'LEC', 'Introduction to the mechanics of vibrations and Laplace transforms: dynamics of discretized systems; one degree of freedom systems; free and forced vibration; response to base excitation, stochastic excitation, impact. Lumped - mass multidegree systems: free and forced vibration of two degrees of freedom systems in response to harmonic and step functions, pulses, and general type. Matrix formulation for multiple degrees of freedom, natural frequencies (matrix iteration, Stodola-Vianello, Rayleigh), Lagrange equations, modal analysis. Flexural vibrations of beams, plates and frames. Earthquake response of single and multistorey buildings and practical considerations of earthquake design.', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 640', 'Urban Transportation Planning Models: Principles & Applications', 0.5, 'LEC', 'Urban transportation planning models.  land use transportation interaction, trip production and attraction, trip distribution, mode choice, tree building and capacitated and uncapacitated route assignment, aggregate and disaggregate model analysis.', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 641', 'Advances in Public Transportation Planning, Operations & Control', 0.5, 'LEC', 'The focus of this course is on the use of quantitative techniques to analyse and solve problems arising in the planning, design, operation and management of urban public transportation systems.  Topics include an introduction to public transportation modes, transit performance analysis, fleet sizing and route design; control of transit operations, and paratransit planning, scheduling and dispatching. The course also covers various transit modelling issues arising in the Advanced Public Transportation Systems that aim at maximising transit system efficiency and reliability using emerging technologies such as global positioning systems (GPS), electronic fare payment, and automatic passenger counters and pre-trip/en-route passenger information systems.', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 642', 'Pavement Design and Management I', 0.5, 'LEC', 'This course will focus on the fundamentals of pavement design, construction and management systems.  Structural behaviour of flexible and rigid pavements will be discussed in detail.  Other topics covered in the course will include: testing of pavement materials including aggregates, asphalt, concrete and various other specialized pavement materials, pavement distresses such as fatigue, rutting and temperature related cracking and the key elements to pavement management systems and their operation.  Theoretical principles are combined with practical examples of working systems that enable students to carry out various analyses of hypothetical and real life situations.', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 643', 'Fundamentals of Traffic Flow Theory', 0.5, 'LEC', 'This course examines the formulation, derivation, and application of theories associated with traffic flow on interrupted and uninterrupted road networks.  Topics include traffic stream characteristics, human factors, car following models, safety, energy and emissions, and traffic flows at signalised and unsignalised intersections. Theoretical models will be tested using field data and simulation.', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 650', 'Earth Structures Case Histories', 0.5, 'LEC', 'Principles and elements of the design and analysis of earth dams and embankments. Field exploration; laboratory tests; design requirements; seepage control; methods of stability analysis for circular and non-circular slip surfaces; computer applications; stability coefficients. Introduction to dynamic (earthquake) analysis; soil liquefaction; tailings disposal systems. Stability of natural slopes and cuts.', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 653', 'Numerical Methods in Geomechanics', 0.5, 'LEC', 'Theoretical basis of numerical modeling in geomechanics; constitutive relationships and failure models for soils and rocks; numerical implementation of constitutive models in finite element and finite difference computer codes; engineering applications in areas of embankment and slope stability, mining, tunneling, soil and structure interaction.  The course is structured to provide theorectical understanding and hands-on expericence with geotechnical analyses using FLAC3D computer code.', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 670', 'Physico-Chemical Processes of Water and Wastewater Treatment', 0.5, 'LEC', 'Principles and design of physico-chemical processes for effecting water quality transformations in water. Process dynamics, reactions and reactors. Filtration, coagulation, flocculation adsorption and ion exchange. Membrane processes including reverse osmosis, electrodialysis and ultrafiltration. Principles of aeration and gas transfer, disinfection solid liquid separation and sludge handling.', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 671', 'Aquatic Chemistry', 0.5, 'LEC', 'The course content includes, but is not limited to: the kinetics and thermodynamics of chemical reactions, acid-base chemistry, solubility and complexation chemistry, redox reactions and essential elements of organic chemistry. The information is applied to water and wastewater treatment processes, and to natural aqueous environmental systems such as those that may be found in stream deposits and groundwater.', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 673', 'Mathematical Methods in Environmental Engineering', 0.5, 'LEC', 'This course covers a variety of mathematical concepts and methods needed to develop deterministic models of water and environmental systems governed by ordinary and partial differential equations, including analytical and numerical solution of ODEs/PDEx using Laplace and Fourier transforms, Green''s functions, superposition, finite difference methods, finite element methods, complex variable theory, eigenmethods, vector calculus, separation of variables, Sturm-Liouville theorem and others.', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 676', 'Case Studies in Groundwater Management', 0.5, 'LEC', 'The use of numerical and analytic models in the assessment of groundwater flow and contaminant migration. Description of groundwater flow models and their limitations. Conservative and non-conservative solute transport models. Multi-phase flow, dissolution and volatilization processes. The development of conceptual models and the application of boundary conditions to field scale problems. The use of sensitivity and uncertainty methods including adjoint techniques, monte carlo and latin hypercube sampling. Waste disposal and spill site case studies.', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 680', 'Water Management', 0.5, 'LEC', 'Multipurpose nature of water resouces planning and operational problems - cost and benefit variations in water resouces and conditions for project optimality.  Optimization techniques - linear programming, dynamic programming, non-linear optimization - water management examples.  Capacity expansion problems and long-term planning problems - short-term operation problems - operations decisions for power generation, flood control and irrigation releases as examples - multi-objective analysis models.  Simulation of water resouce systems.  Introduction to stochasti optimization of water resource systems.', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 681', 'Surface Water: Theory and Modelling', 0.5, 'LEC', 'This course will introduce surface water modelling and the role it plays in environmental modelling.  The emphasis will be on physical processes that are relevant to near surface partitioning of the energy and water budget; methods for basin representation, including options for sub-grid process; and introduction to data handling, including data acquisition, data sources, remote sensing imagery, digital terrain models, mapping methods.', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 682', 'Free Surface Hydraulics', 0.5, 'LEC', 'Review of continuity, energy and momentum equations, resistance to flow in open channels. Gradually varied unsteady flow equations and kinematic wave approximation. Sediment transport equations and channel stability. Similitude of scale models, including distortion effects, laboratory techniques, and case studies.', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 700', 'Topics in Structural Engineering', 0.5, 'RDG', 'None', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 701', 'Topics in Mechanics', 0.5, 'LEC, RDG', 'None', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 705', 'Theories of Inelastic Structures', 0.5, 'LEC', 'This course deals with the analysis and design of structural systems loaded into the post-elastic range of response. Topics include: historical overview; static and kinematic limit theorems; rigid-plastic collapse load analysis; combined stress states; variable repeated loading; shakedown analysis; elastic-plastic analysis; plastic limit states design; computer applications.', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 702', 'Topics in Construction Engineering', 0.5, 'RDG', 'None', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 703', 'Cold Formed Steel Design', 0.5, 'LEC', 'Covered in this course are the major topics relating to the behaviour and design of cold formed steel structural elements and members. More specifically, the following topics will be addressed from a theoretical and design point of view: local buckling of compression elements subjected to uniform stress and stress gradient; design of flexural members, compression members, beam-columns, composite steel deck slabs and connections; diaphragm design and lightweight steel framing. The governing Canadian CSA Standard S 136 will be used as a guide document.', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 704', 'Bridge Design', 0.5, 'LEC', 'The course is based on the development of the Ontario Highway Bridge Design Codes (Editions 1, 2 and 3), and the Canadian Highway Bridge Design Code, as well as experience with the Limit States Design versions of AASHTO Specifications.  The course outline will generally follow these topics as these apply to short and intermediate span structures in North America: Bridge Geometry - types of bridges and various cross sections: Vehicle Loads including dynamic effects, temperature and wind: Earth Forces: Seismic Effects: Load Distribution - how are vehicle force effects transferred to the superstructure, for various cross sectional geometries: Design Criterion - limit states design: Fatigue in Connections: Foundation Design - pile and shallow foundations: Examples of Various Simple Span Designs: Concrete - Prestressed Concrete: Structural Steel: Repair and Rehabilitation.', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 707', 'Advanced Building Science', 0.5, 'LEC', 'This course deals with the science of heat and air flow, moisture storage and transport, and psychrometrics.  Through the use of worked examples, these principles are applied to the analysis of typical building enclosure systems. Basic concepts are developed for the design of building details that are effective in the control of heat, air, vapour, rain, and that accommodate building movements.  Various case studies of problems and solutions will be used.', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 708', 'Building Physics and Modelling', 0.5, 'LEC', 'This course covers methodologies for the quantitative prediction of building enclosure and building system performance.  Issues considered include heat conduction, radiation, and convection; air flow through cracks, openings, vents, and porous media; and moisture transport by diffusion, convection, capillary action, adsorbed flow and osmosis.  Students are introduced to research-quality formulations, commercial models, and simplified methods.', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 709', 'Durability Design of New Concrete Infrastructure', 0.5, 'LEC', 'This course addresses the design of new concrete infrastructure from a durability perspective.  Durability design is approached as a process of assessing forms and severity of attack on a concrete structure, and providing the necessary resistance to those forms of attack.  The course begins with a discussion of durability design as a limit state and an introduction to concrete material science.  Specific durability topics include corrosion of metals in concrete, sulfate attack, freezing and thawing damage and alkali-aggregate reactions.  Each durability topic is discussed in terms of exposure conditions, mechanisms of attack, influencing factors and protection methods.', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 710', 'Advanced Project Management', 0.5, 'LEC', 'This course presents advanced construction engineering and project management techniques that can be applied to improve cost, schedule, safety, and quality of projects.  The course covers a variety of topics including: Critical Path Method; bidding strategy models; uncertainty and risk assessment; multiple-criteria decision analysis; planning of linear, repetitive, and distributed projects; project control and delay analysis; Enterprise Resource Planning; heavy construction equipment and methods; modelling and simulation; construction methods design; positioning and locating technologies; equipment automation and robotics; opportunity analysis and process of innovation; modularization and prefabrication; and construction human resource management.', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 711', 'Computer-Aided Project Organization and Management', 0.5, 'LEC', 'This course deals with the application of computerized tools to develop decision-support systems to effectively manage time, money, and resources associated with construction projects.  It covers: introduction to computer tools, review of the CPM method and project management software, optimization using Excel Solver, Expert Systems, Neural Networks, OOP programming, Genetic Algorithms, process modeling and simulation, multi-criteria decision-making, integrated project Management tools, networking, workgroup management, Internet, dealing with project uncertainty using Monte-Carlo simulation, various case studies and computer workshops.', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 712', 'Aspects of Structural Design', 0.5, 'LEC', 'The course covers selected topics in the analysis and design of structural systems.  National Building Code of Canada will be introduced by reviewing concepts related to structural loads, load combinations and their effects, resistance factors and design criteria.  Typical structural systems will be reviewed and relevant principles for structural analysis will be introduced.  Structural design portion of the course will address both steel and concrete with the application in the design of typical structures.', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 720', 'Infrastructure Management', 0.5, 'LEC', 'This course will focus on the fundamentals of infrastructure management for civil engineering.  It will integrate design, construction, maintenance, rehabilitation and renovation with management procedures and systems.  A framework for asset management including the importance of asset valuation, needs assessment, and performance indicators will be discussed in detail.  Other topics covered in the course will include: sustainability concepts, decision support systems, database management, role of data in infrastructure management, monitoring and evaluation needs, failure analysis, quality management, economics and life cycle cost analysis and optimization.  This course will combine theoretical principles with practical application.  The course will include practical examples of engineering systems and will provide a basis for subsequent infrastructure management courses.', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 740', 'Topics in Transportation Engineering', 0.5, 'LEC, RDG', 'None', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 743', 'Transport Risk Analysis', 0.5, 'LEC', 'Definition of risk, stochastic processes, dangerous goods hazard area analysis, black spot identification, risk communication, risk tolerance and decision-making, analysis of complex system failures, uncertainty analysis.', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 741', 'Public Sector Economics and Finance', 0.5, 'LEC', 'Cash flows, Interest calculation, evaluation of mutually exclusive alternatives, evaluation of user benefits, pricing strategies, cost-effectiveness, ranking alternatives, financing options, decision making under uncertainty. Use of prior information in decision-making.', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 742', 'Pavement Design and Management II', 0.5, 'LEC', 'This course will focus on advanced pavement engineering.  The course is primarily directed toward the management of existing road networks, with emphasis on pavements. Topics include priority programming of investments, in-service evaluation of structural capacity, serviceability of condition and safety, structural design, construction and maintenance management, and data systems.  Example applications will be provided on various topic areas.', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 744', 'Transportation Networks: Models, Algorithms and Computer Applications', 0.5, 'LEC', 'The focus of this course is on the use of quantitative techniques of operations research to analyse and solve transportation network problems arising in the planning, design, operation and management of urban transportation systems.  Topics include an introduction to urban transportation systems and transportation network problems; graph theory including basic concepts, network representation, elementary data structures and algorithms; representation and coding of transportation networks for different applications; optimal and heuristic shortest path algorithms and applications; formulation and solution algorithms of traffic network assignment problems; fleet vehicle routing and scheduling problems and algorithms.', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 745', 'Intelligent Transportation Systems', 0.5, 'LEC', 'The goal of this course is to provide students with an understanding of the wide array of subsystems that constitute intelligent transportation systems (ITS) and to provide them with opportunities to design, build, and evaluate prototype subsystems.  Emphasis will be placed on the identification of specific transport problems, identification and formulation of potential ITS solutions, and the development of a methodology for evaluating these solutions. Field data, including loop detector data, automatic vehicle identification data, GPS data, incident detection logs, and road network data will be available. Students will make use of these data sources, as well other field and synthetic data, as input to their ITS subsystem design projects.', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 746', 'Contemporary Urban Travel Forecasting Methods', 0.5, 'LEC', 'This course provides an overview of the travel forecasting problem.  The course begins with defining the objectives of the problem, and reviews the weaknesses of the traditional 4-step procedure.  A general formulation of the travel forecasting problem is stated.  An integrated mathematical forecasting model is derived, in which the traffic assignment, mode choice and origin destination steps are solved simultaneously.  Traditional solution algorithms including Frank Wolfe, Dijkstra and Evans are presented; a more robust algorithm for the traffic assignment problem is introduced.  Potential extensions to the model are pursued.  Alternate model formulations are also described including activity based and combined land use - transportation models.', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 750', 'Topics in Geotechnical Engineering', 0.5, 'RDG', 'None', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 751', 'Advanced Geotechnical Testing', 0.5, 'LEC', 'The objective of this course is to expose students to advanced geotechnical-testing methods through class lectures, specified readings, student seminars, and the completion of laboratory experiments. Topics to be covered include soil classification; shear strength, hydraulic conductivity, and field quality control. This course will mainly be a laboratory-testing course that consists of one period of lecture and two periods of laboratory testing a week. Laboratory testing equipment to be used includes geotechnical centrifuge, triaxial cell, permeability apparatus, direct shear, and consolidation.', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 752', 'Trenchless Technologies', 0.5, 'LEC', 'Over the past ten years, a new group of construction methods, known as trenchless technologies, has gained widespread acceptance. Trenchless technologies include methods for installing and rehabilitating underground utility systems with minimal surface disruption and destruction that results from excavation. Underground utility systems include: water and wastewater distribution and collection systems; gas, petroleum and chemical pipelines; electrical and communications networks; access ways; and other small diameter tunnels used for a variety of applications. The objective of this course is to introduce trenchless technology methods and their importance in public works, pipeline construction, and rehabilitation. Students will be exposed to new topics and concepts through class lectures, specified readings, guest presentations, field trips, student seminars, and the completion of assignments. Topics to be covered include horizontal directional drilling, microtunneling, pipeline assessment, pipe bursting, and pipeline rehabilitation and renewal techniques.', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 753', 'Geotechnical Earthquake Engineering', 0.5, 'LEC', 'This course is designed to help students understand the fundamental principles and practical methods of geotechnical earthquake engineering. This course will present basic concepts of vibratory motion, dynamics, seismology, earthquakes, and strong ground motion, and introduces procedures of deterministic and probabilistic seismic hazard analysis. Basic concepts of wave propagation are used to develop procedures for ground response analysis and to provide insight into such important problems as local site effects, seismic slope stability, and seismic design of retaining structures.', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 770', 'Topics in Environmental Engineering', 0.5, 'RDG', 'None', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 772', 'Environmental Health and Fate Engineering', 0.5, 'LEC', 'Chemical partitioning and fate are examined from both the environmental and health perspectives.  Concepts include chemical speciation, equilibrium, phase distribution, and biodegradation. Chemical principles are applied to the study of the speciation, distribution, transformation, biodegradation and bioaccumulation of environmental contaminants, with an introduction to human exposure to contaminants and epidemiology.', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 771', 'Biological Wastewater Treatment: Theory and Practice', 0.5, 'LEC', 'This course focuses on theory, modelling and application of microbiological processes that are being utilised in the treatment of wastewater.  A review of relevant concepts in microbiology will be followed by core principles that are used to assess contaminant removal kinetics and to model bioreactor performance.  These principles will be drawn upon in discussion of applications of biological wastewater treatment technologies.  If time permits, advanced topics including nonsteady-state systems and complex multispecies interactions will be introduced.', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 774', 'Advanced Numerical Methods for Environmental Applications', 0.5, 'LEC', 'The analyses of natural and/or manmade environmental systems commonly lead to quantitative descriptions, or mathematical models, of the underlying chemical, biological and physical processes.  Numerical models are used for complex situations that may involve spatial variability of material properties, non-uniform geometry, and transient boundary conditions.  The objective of this course is to introduce you to theoretical and practical aspects associated with numerical methods for environmental applications.  Topics include: review of field equations, conservation laws, and continua; classification of PDEs; types of boundary and initial conditions; finite difference method, error analysis and stability; equation solvers; weighted residual techniques; finite element method; introduction to the finite volume method; techniques for advective dominated flows; sensitivity methods; and the solution to coupled non-linear equations.', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 776', 'Soil & Groundwater Remediation', 0.5, 'LEC', 'This course focuses on the various technologies available to remediate soil and groundwater.  Proven, emerging, and innovative technologies are investigated for application to both porous media and fractured porous media subsurface systems.  The underlying theory and relevant engineering design aspects for each technology are presented.  Case studies are critically examined.', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 777', 'Biofilms in Engineered and Natural Systems', 0.5, 'LEC', 'This course provides an introduction to the study of biofilms found in oligotrophic environments (e.g., groundwaters, natural waters and drinking water) and in wastewater systems.  Topics include: biofilm structure and composition, concepts of attachment and detachment, substrate utilization and transformation, mass transfer, uptake, control measures, biofilm process analysis, and biofilm modeling. Presentation and discussion of several case studies will be included.', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 779', 'Advanced Topics in Drinking Water Treatment', 0.5, 'RDG', 'This course is intended for graduate students in the field of Water Resources with a strong interest in drinking water treatment.  Several key concepts introduced in CIV E 670 are elaborated upon. Advanced drinking water treatment process theory is investigated through formal lectures and student design projects/presentations.  Key process components that are addressed include reactors/reactions, coagulation, flocculation, sedimentation, filtration, adsorption, and disinfection.', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 780', 'Urban Water Systems', 0.5, 'LEC', 'The course will provide the graduate student with the fundamentals of urban water systems and management.  Topics will include: the uban hydrological cycle; quantity and quality characteristics; behaviour and controls on drinking water and wastewater treatment plants; regulations, guidelines and policies; design of water supply and distribution systems; design of waste and storm water collection systems; aspects of storm water management with an emphasis on sustainable development and stream ecology; and source water protection including contamination prevention and aspects of Brownfield remediation.  As required computer aided design and commonly used software packages will be employed.', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CLAS 100', 'An Introduction to Classical Studies', 0.5, 'LEC', 'An introduction to Greek and Roman civilization, focusing on six key aspects of the discipline of classical studies: history, literature, philosophy, myth and religion, art and architecture, and classical archaeology.', 'CLAS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CLAS 103', 'Colossos - The Major Figures of Classical Antiquity', 0.5, 'LEC', 'An introductory study of the achievements of ancient Greece and/or Rome through some of their major figures. Each year two figures will be featured. These may include Homer, Pericles, Socrates, Alexander the Great, Cleopatra, Julius Caesar, Augustus, Nero, Seneca, Hadrian, and Constantine.', 'CLAS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CLAS 104', 'Classical Mythology', 0.5, 'LEC', 'A study of Greco-Roman mythology and legend, with special emphasis on the Olympian gods and the figure of the hero. Topics may include myths of creation, the rise of the gods, divine myths, the tales surrounding the cities of Troy, Mycenae, and Thebes and the heroes Herakles, Perseus, and Theseus.', 'CLAS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CLAS 105', 'Introduction to Medieval Studies', 0.5, 'LEC', 'An introduction to Medieval European civilization focusing on essential aspects of the discipline: history, literature, philosophy, religion, art, architecture and archaeology, law, and science and technology.', 'CLAS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CLAS 201', 'Ancient Greek Society', 0.5, 'LEC', 'A survey of the civilization of Classical Greece, featuring such topics as the individual (male and female), political institutions, art, religion, philosophy, literature, social life and leisure activities.', 'CLAS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CLAS 202', 'Ancient Roman Society', 0.5, 'LEC', 'A survey of the civilization of the Roman Republic and Empire, featuring such topics as the individual (male and female), political institutions, art, religion, philosophy, literature, social life and leisure activities.', 'CLAS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CLAS 205', 'Medieval Society', 0.5, 'LEC', 'A survey of Medieval civilization featuring such topics as the individual (male and female), political institutions, art, architecture, religion, philosophy, literature, social life, and leisure activities.', 'CLAS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CLAS 210', 'History of Ancient Law', 0.5, 'DIS, LEC', 'A historical introduction to law in the ancient world. Babylonian, Assyrian, Hittite, and Roman law, legal practices, and concepts will be examined.', 'CLAS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CLAS 221', 'Introduction to Archaeology', 0.5, 'LEC', 'An introduction to the working assumptions, analytic approaches, and integrative and descriptive methods of archaeological anthropology.', 'CLAS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CLAS 230', 'Classical Roots of English Vocabulary', 0.5, 'LEC', 'This course offers an introduction to the etymology of the English language, in particular that part which has been derived from Latin and ancient Greek. The main focus will be the most important classical roots from which the vocabulary of the life sciences and other academic disciplines derives.', 'CLAS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CLAS 231', 'Studies in Ancient Greek Literature', 0.5, 'LEC', 'Special topics in ancient Greek literature, as announced by the department. Authors, works, time periods, and topics will vary. Authors studied may include Homer (epic), Euripides (tragedy), Aristophanes (comedy), Thucydides (history), Sappho (poetry), and Plato (philosophy). Topics by which the course will be organized may include gender, slavery, politics, war, peace, death, and love.', 'CLAS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CLAS 232', 'Studies in Ancient Roman Literature', 0.5, 'LEC', 'Special topics in ancient Roman literature, as announced by the department. Authors, works, time periods, and topics will vary. Authors studied may include Vergil (epic), Seneca (tragedy), Plautus (comedy), Livy (history), Catullus (poetry), and Lucretius (philosophy). Topics by which the course will be organized may include gender, slavery, politics, war, peace, death, and love.', 'CLAS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CLAS 237', 'The Ancient Near East and Egypt', 0.5, 'DIS, LEC', 'A study of the civilizations of the ancient Near East focusing on Mesopotamia (Sumer and Akkad, the Babylonian Dynasty, and the Third Dynasty of Ur), Hatti, Assyria, Egypt, and Persia.', 'CLAS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CLAS 241', 'Survey of Greek Art and Architecture', 0.5, 'LEC', 'A survey of Greek art and architecture from the earliest times to the coming of the Romans. Material studied may include the art of the Bronze Age, the development of Greek sculpture, the evolution of the Acropolis at Athens, and the change in art and architecture after Alexander the Great.', 'CLAS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CLAS 242', 'Survey of Roman Art and Architecture', 0.5, 'LEC', 'A survey of Roman art and architecture from the earliest times to the age of Constantine the Great. Material studied may include the art of the Etruscans, the evolution of Roman portraiture, innovations in architectural materials and forms, the use of art and architecture by the Emperors, and the change to Late Antique art.', 'CLAS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CLAS 251', 'Greek History', 0.5, 'LEC', 'A survey of ancient Greek history, from the Bronze Age to Alexander the Great, emphasizing particularly its political and military aspects.', 'CLAS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CLAS 252', 'Roman History', 0.5, 'LEC', 'A survey of ancient Roman history, from the Republic to the Empire, emphasizing particularly its political and military aspects.', 'CLAS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CLAS 261', 'Great Works: Ancient and Medieval', 0.5, 'LEC', 'A historical survey of ancient and medieval philosophy in the Western tradition.', 'CLAS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CLAS 311', 'Sex and Gender in the Ancient World', 0.5, 'LEC', 'This course examines a variety of issues around sexuality and gender relations in antiquity, including sexual mores and behaviours; literary, artistic, and philosophical constructs of gender; the roots of misogyny; and the legal and social restrictions placed on women in most ancient societies.', 'CLAS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CLAS 321', 'Archaeology of Complex Cultures', 0.5, 'LEC', 'Cultural development from the agricultural revolution to the rise of literacy. Special attention to the development of agriculture as a means of subsistence and to the rise of early civilization. Areas and periods of emphasis will vary from year to year.', 'CLAS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CLAS 325', 'Greek and Roman Religion', 0.5, 'LEC', 'An examination of the religious beliefs and cult practices of the classical world. Topics include prayer and sacrifice; divination and oracles; temples, priests, and festivals; mystery cults and their relation to Christianity.', 'CLAS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CLAS 327', 'Astrology and Magic', 0.5, 'LEC', 'An examination of the theory and practice of astrology and magic in the classical and medieval worlds. Topics include the relationship of astrology and magic to traditional Greco-Roman religion and Christianity, occult practices, and the people who performed them.', 'CLAS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CLAS 331', 'Advanced Studies in Ancient Literature', 0.5, 'LEC', 'An in-depth examination, through English translation, of a genre(s), author(s), or selected topic(s) in Greek and/or Roman literature. Material studied may include the genres of epic, tragedy, comedy, lyric, and satire, and authors such as Homer, Virgil, Sophokles, Seneca, Aristophanes, Menander, Plautus, Sappho, Pindar, Catullus, and Horace.', 'CLAS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CLAS 341', 'Advanced Studies in Greek Art and Architecture', 0.5, 'LEC', 'An advanced survey of the art and architecture from a selected time period of Greek history. Material studied may include the art and architecture of the Aegean Bronze Age, and the Archaic, Classical, and Hellenistic periods. Archaeological, historical, and cultural issues specific to each time period will be discussed through the important media of the day.', 'CLAS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CLAS 342', 'Advanced Studies in Roman Art and Architecture', 0.5, 'LEC', 'An advanced survey of the art and architecture from a selected time period of Roman History. Material studied may include the art and architecture of the Etruscans, the Roman Republic, and the Roman Empire. Archaeological, historical, and cultural issues specific to each time period will be discussed through the important media of the day.', 'CLAS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CLAS 351', 'Advanced Studies in Greek History', 0.5, 'LEC', 'An advanced study of aspects of Greek history, through the examination of a specific time period, event(s), or theme(s). Topics studied may include the Archaic Age and the rise of the Polis, the Persian Wars, the Peloponnesian War, conflict in the fourth century BCE, the history of the Hellenistic period, and Greek social history.', 'CLAS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CLAS 352', 'Advanced Studies in Roman History', 0.5, 'LEC', 'An advanced study of aspects of Roman history, through the examination of a specific time period, event(s) or theme(s). Topics studied may include the Punic Wars, the end of the Republic, the Julio-Claudian Dynasty, history of the High Empire, later Roman history and Roman social history.', 'CLAS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CLAS 361', 'History of Ancient Philosophy', 0.5, 'LEC', 'In this course students will examine one or more important figures, periods, or issues in ancient philosophy. Plato and Aristotle are among the philosophers who may be covered.', 'CLAS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CLAS 362', 'History of Ancient Philosophy 2', 0.5, 'LEC', 'From Aristotle to the close of classical antiquity.', 'CLAS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CLAS 384', 'Science and Technology of Ancient Greece and Rome', 0.5, 'LEC', 'A study of scientific thought and achievements in such areas as astronomy, biology, anatomy, and medicine, and of the technological skills which produced and distributed raw materials, manufactured goods, and agricultural products.', 'CLAS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CLAS 390', 'Classical Studies Abroad', 0.5, 'FLD', 'This course features a combination of academic study and first-hand investigation of museums and ancient sites, normally in Greece and/or Italy.', 'CLAS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CLAS 395', 'Research Skills Training', 0.25, 'RDG', 'This course will provide students with an opportunity to improve their research skills by working within an instructor''s area of expertise. Students will utilize ancient source material (such as inscriptions, papyri, manuscripts, coins) and modern scholarly literature to investigate a particular topic.', 'CLAS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CLAS 461', 'Studies in Ancient Philosophy', 0.5, 'SEM', 'Special topics in ancient philosophy, as announced by the department.', 'CLAS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CLAS 485', 'Greco-Roman Civilization and History', 0.5, 'SEM', 'This is a topic-oriented directed study course intended for senior students.', 'CLAS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CLAS 486', 'Senior Seminar', 0.5, 'SEM', 'Each fall and winter term a senior seminar on some aspect of Greek or Roman civilization will be offered.', 'CLAS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CLAS 490A', 'Senior Honours Thesis', 0.5, 'SEM', 'Students wishing to undertake a senior honours thesis in their fourth year should consult the department''s undergraduate officer.', 'CLAS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CLAS 490B', 'Senior Honours Thesis', 0.5, 'RDG', 'Continuation of the Senior Honours Thesis.', 'CLAS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CLAS 492', 'Directed Study', 0.5, 'SEM', 'Under exceptional circumstances, and only with the prior approval of the department, a student may substitute an individualized course of study at the senior level. Such circumstances might include, for example, the student''s participation in an approved archaeological dig. For further details, consult the department.', 'CLAS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CLAS 600', 'Research Methods in Classical Studies', 0.5, 'SEM', 'A team-taught course exploring the variety of methodologies involved in studying the ancient Mediterranean, with a focus on Greco-Roman civilization and its transmission to us.  Each contributing faculty member will showcase the particular methodologies employed in his or her research.  This course is taught int he Fall term and is a required course for all MA students.', 'CLAS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CLAS 601', 'The Integration of the Ancient Mediterranean World', 0.5, 'SEM', 'The greater Mediterranean Basin, joining Europe, Africa and Asia, has a long and influential history.  This fall-term course introduces the major processes of cultural integration in the Mediterranean from the Bronze Age to the end of the Roman Empire and the appropriate research methods.  This course will be team-taught with a central coordinator.', 'CLAS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CLAS 631', 'Studies in Greek Literature', 0.5, 'SEM', 'An investigation of selected themes, topics, time periods or genres in ancient Greek literature.  This course is repeatable provided that the topic is different.  One example could be Attic Drama and Civic Identity.', 'CLAS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CLAS 632', 'Studies in Latin Literature', 0.5, 'SEM', 'An investigation of selected themes, topics, time periods or genres in Latin literature.  This course is repeatable provided that the topic is different.', 'CLAS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CLAS 633', 'Studies in Ancient Literature', 0.5, 'SEM', 'An investigation of selected themes, topics, time periods or genres in Ancient literature.  This course is repeatable provided that the topic is different.  One example could be Theories and Methods of Literary Criticism.', 'CLAS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CLAS 641', 'Studies in Greek Art and Architecture', 0.5, 'SEM', 'An investigation of selected themes, topics, time periods, artistic media or styles in ancient Greek art and architecture.  This course is repeatable provided that the topic is different.  One example could be Artistics and Cultural Commonalities in the Hellenistic Mediterranean.', 'CLAS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CLAS 642', 'Studies in Roman Art in Architecture', 0.5, 'SEM', 'An investigation of selected themes, topics, time periods, artistitc media or styles in Roman art and architecture.  This course is repeatable provided that the topic is different.', 'CLAS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CLAS 643', 'Studies in Ancient Art and Architecture', 0.5, 'SEM', 'An investigation of selected themes, topics, time periods, artistic media or styles in ancient art and architecture.  This course is repeatable provided that the topic is different.  One example could be The Sacred Island of Delos: Cultural Crossroads.', 'CLAS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CLAS 651', 'Topics in Greek History', 0.5, 'SEM', 'An investigation of selected themes, topics, time periods, and methods in ancient Greek history.  This course is repeatable provided that the topic is different.  Examples could include: The Hellenistic Kingdoms: Conquest and Cultural Interaction; Greeks and Egyptians in Ptolemaic Egypt; or Athenian Democracy and Political Discourse.', 'CLAS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CLAS 652', 'Topics in Roman History', 0.5, 'SEM', 'An investigation of selected themes, topics, time periods, and methods in Roman History. This course is repeatable provided that the topic is different.  Examples could include: Roman Frontiers and Provinces; The Decline of the Roman Empire and its Consequences; The History of Roman Citizenship (8th cent. BC-AD 6th cent.); or The World of Roman Law.', 'CLAS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CLAS 653', 'Topics in Ancient History', 0.5, 'SEM', 'An investigation of selected themes, topics, time periods, and methods in ancient history.  This course is repeatable provided that the topic is different.  Examples could include:  Slavery and Cultural Exhange in Greco-Roman Antiquity; Asia Minor in the Hellenistic and Roman Periods; or The Ruler Cult in the Greco-Roman World.', 'CLAS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CLAS 695', 'Classical Studies Abroad', 0.5, 'SEM', 'This course features a combination of academic study and firsthand investigation of museums and ancient sites, normally in Greece and/or Italy.  Students are required to give a presentation during the trip on a topic relevant to one of the sites being visited.  A piece of written work involving original research relating to some of the material investigated during the visit is also required.', 'CLAS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CM 361', 'Computational Statistics and Data Analysis', 0.5, 'LEC', 'Approximation and optimization of noisy functions. Simulation from univariate and multivariate distributions, multivariate normal distribution, mixture distributions and introduction to Markov Monte Carlo. Introduction to supervised statistical learning including discrimination methods. [Offered: F]', 'CM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CM 375', 'Computational Linear Algebra', 0.5, 'LAB, LEC', 'Basic concepts and implementation of numerical linear algebra techniques and their use in solving application problems. Special methods for solving linear systems having special features. Direct methods: symmetric, positive definite, band, general sparse structures, ordering methods.
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CM 670', 'Numerical Analysis', 0.5, 'LEC', 'Introduction to basic algorithms and techniques for numerical computing.  Error analysis, interpolation (including splines), numerical differentiation and integration, numerical linear algebra (including methods for linear systems, eigenvalue problems, and the singular value decomposition), root finding for nonlinear equations and systems, numerical ordinary differential equations, and approximation methods (including least squares, orthogonal polynomials, and Fourier transforms).', 'CM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CM 730', 'Introduction to Symbolic Computation', 0.5, 'LEC', 'An introduction to the use of computers for symbolic mathematical computation, involving traditional mathematical computations such as solving linear equations (exactly), analytic differentiation and integration of functions, and analytic solution of differential equations.', 'CM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CM 740', 'Fundamentals of Optimization', 0.5, 'LEC', 'Linear Optimization: Farkas'' Lemma, Duality, Simplex Method,Geometry Of Polyhedra. Combinatorial Optimization: lntegrality Of Polyheqra, Total Unimodularity, Flow Problems, Weighted Bipartite Matching. Continuous Optimization: Convex Sets, Separation Theorem, Convex Functions, Analytic Characterizations Of Convexity, Karush-Kuhn-Tucker Theorem.', 'CM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CM 750', 'Numerical Solution of Partial Differential Equations', 0.5, 'LEC', 'Discretization methods for partial differential equations, including finite difference, finite volume and finite element methods. Application to elliptic, hyperbolic and parabolic equations. Convergence and stability issues, properties of discrete equations, and treatment of non-linearities. Stiffness matrix assembly and use of sparse matric software. Students should have completed a course in numerical computation at the undergraduate level.', 'CM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CM 761', 'Computational Inference', 0.5, 'LEC', 'Introduction to and application of computational methods in statistical inference.  Monte Carlo evaluation of statistical procedures, exploration of the likelihood function through graphical and optimization techniques including EM.  Bootstrapping, Markov Chain Monte Carlo, and other computationally intensive methods.', 'CM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CM 762', 'Data Visualization', 0.5, 'LEC', 'Visualization of high dimensional data including interactive methods directed at exploration and assessment of structure and dependencies in data.  Methods for finding groups in data including traditional and modern methods of cluster analysis.  Dimension reduction methods including multi-dimensional scaling, nonlinear and other methods.', 'CM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CM 763', 'Statistical Learning - Classification', 0.5, 'LEC', 'Given known group membership, methods which learn from data how to classify objects into the groups are treated.  Review of likelihood and posterior based discrimination.  Main topics include logistic regression, neural networks, tree-based methods and nearest neighbour methods.  Model assessment, training and tuning.', 'CM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CM 764', 'Statistical Learning - Function Estimation', 0.5, 'LEC', 'Methods for finding surfaces in high dimensions from incomplete or noisy functional information.  Both data adaptive and methods based on fixed parametric structure will be treated.  Model assessment, training and tuning.', 'CM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CMW 201', 'Worship Practicum 1', 0.25, 'PRA', 'Skills development and practice in planning and leading Christian worship, including such activities as choosing and accompanying hymns, song-leading, teaching new music, the use of instrumental music, reading scripture, leading prayer, movement, and gesture.', 'CMW');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CMW 202', 'Worship Practicum 2', 0.25, 'PRA', 'A continuation of CMW 201. Skills development and practice in planning and leading Christian worship, including such activities as choosing and accompanying hymns, song-leading, teaching new music, the use of instrumental music, reading scripture, leading prayer, movement, and gesture.', 'CMW');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CMW 363', 'The Christian Hymn', 0.5, 'LEC', 'The origins of the Christian hymn and its development up to the present. The course considers the hymn as theological, poetic, musical, cultural, and spiritual expression, and the use of hymns in a variety of worship settings.', 'CMW');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CMW 364', 'Worship and its Music', 0.5, 'LEC', 'The nature of worship and the role of music within worship in historical, theological, and cultural perspectives. Field trips to services of various traditions.', 'CMW');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CMW 390', 'Special Topics in Church Music and Worship', 0.5, 'LEC', 'Term courses will be offered from time to time as announced. Topics will be dependent on research and/or instructional interests of faculty.', 'CMW');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CO 227', 'Introduction to Optimization (Non-Specialist Level)', 0.5, 'LEC', 'A broad introduction to the field of optimization, discussing applications, and solution techniques. Mathematical models for real life applications; algorithms: simplex, cutting plane, and branch & bound; linear programming duality. [Offered: F,W]', 'CO');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CO 250', 'Introduction to Optimization', 0.5, 'LEC', 'A broad introduction to the field of optimization, discussing applications and solution techniques. Mathematical models for real life applications; algorithms; aspects of computational complexity; geometry; linear programming duality, focusing on the development of algorithms. [Offered: F,W,S]', 'CO');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CO 255', 'Introduction to Optimization (Advanced Level)', 0.5, 'LEC', 'Linear optimization: feasibility theorems, duality, the simplex algorithm. Discrete optimization: integer linear programming, cutting planes, network flows. Continuous optimization: local and global optima, feasible directions, convexity, necessary optimality conditions.', 'CO');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CO 327', 'Deterministic OR Models (Non-Specialist Level)', 0.5, 'LEC', 'An applications-oriented course that illustrates how various mathematical models and methods of optimization can be used to solve problems arising in business, industry, and science. [Offered: W,S]', 'CO');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CO 330', 'Combinatorial Enumeration', 0.5, 'LEC', 'The algebra of formal power series. The combinatorics of the ordinary and exponential generating series. Lagrange''s implicit function theorem, applications to the enumeration of permutations, functions, trees and graphs. Integer partitions, geometric methods, enumerating linear transformations. Introduction to the pattern algebra, applications to the enumeration of strings. Lattice paths, Wiener-Hopf factorization. Enumeration under symmetries. [Offered: F]', 'CO');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CO 331', 'Coding Theory', 0.5, 'LEC', 'A first course in error-correcting codes. Linear block codes, Hamming-Golay codes, and multiple error-correcting BCH codes are studied. Various encoding and decoding schemes are considered. [Offered: W]', 'CO');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CO 342', 'Introduction to Graph Theory', 0.5, 'LEC', 'An introduction to some of the key topics in graph theory: connectivity, planarity, and matchings. Connectivity: Menger''s theorem, 3-connected graphs. Planarity: Kuratowski''s theorem, uniqueness of planar embeddings. Matchings: Review of Konig''s theorem, Tutte''s theorem. [Offered: F,S]', 'CO');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CO 351', 'Network Flow Theory', 0.5, 'LEC', 'Review of linear programming. Shortest path problems. The max-flow min-cut theorem and applications. Minimum cost flow problems. Network simplex and primal-dual algorithms. Applications to problems of transportation, distribution, job assignments, and critical-path planning. [Offered: F,S]', 'CO');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CO 353', 'Computational Discrete Optimization', 0.5, 'LAB, LEC', 'Formulations of combinatorial optimization problems, greedy algorithms, dynamic programming, branch-and-bound, cutting plane algorithms, decomposition techniques in integer programming, approximation algorithms.', 'CO');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CO 367', 'Nonlinear Optimization', 0.5, 'LAB, LEC', 'A course on the fundamentals of nonlinear optimization, including both the mathematical and the computational aspects. Necessary and sufficient optimality conditions for unconstrained and constrained problems. Convexity and its applications. Computational techniques and their analysis. [Offered: F]', 'CO');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CO 370', 'Deterministic OR Models', 0.5, 'LAB, LEC', 'An applications-oriented course that illustrates how various mathematical models and methods of optimization can be used to solve problems arising in business, industry, and science. [Offered: F,W]', 'CO');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CO 372', 'Portfolio Optimization Models', 0.5, 'LAB, LEC', 'Computational optimization methodologies underlying portfolio problems in finance. Computational linear algebra, determining derivatives, quadratic, and nonlinear optimization. The efficient frontier problem. Applications of optimization in finance such as volatility surface determination and global minimization for value-at-risk. [Offered: F,W]', 'CO');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CO 380', 'Mathematical Discovery and Invention', 0.5, 'LEC', 'A course in problem solving. 100 problems are studied. Problems are taken mainly from the elementary parts of algebra, geometry, number theory, combinatorics, and probability.', 'CO');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CO 430', 'Algebraic Enumeration', 0.5, 'LEC', 'The algebra of Laurent series and Lagrange''s Implicit function theorem, enumerative theory of planar embeddings (maps). The ring of symmetric functions: Schur functions, orthogonal bases, inner product, Young tableaux, and plane partitions. Non-intersecting paths, sieve methods, partially ordered sets, and Mobius inversion, strings with forbidden substrings, the Cartier-Foata commutation monoid. Introduction to the group algebra of the symmetric group, enumerative applications of sl(2). [Offered: W]', 'CO');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CO 434', 'Combinatorial Designs', 0.5, 'LEC', 'Pairwise orthogonal latin squares. Transversal designs and finite planes. Balanced incomplete block designs, group divisible designs, and pairwise balanced designs. Symmetric designs and Hadamard matrices. Recursive constructions. Wilson''s fundamental construction.', 'CO');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CO 439', 'Topics in Combinatorics', 0.5, 'LEC', 'An undergraduate seminar in combinatorics. The primary objective is to study current work in specific areas of combinatorics. Course content may vary from term to term.', 'CO');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CO 440', 'Topics in Graph Theory', 0.5, 'LEC', 'An in-depth study of one or two topics in graph theory. Course content may vary from term to term. Topics may include planar graphs, extremal graph theory, directed graphs, enumeration, algebraic graph theory, probabilistic graph theory, connectivity, graph embedding, colouring problems.', 'CO');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CO 442', 'Graph Theory', 0.5, 'LEC', 'Connectivity (Menger''s theorem, ear decomposition, and Tutte''s wheels theorem) and matchings (Hall''s theorem and Tutte''s theorem). Flows: integer and group-valued flows, the flow polynomial, the 6-flow theorem. Ramsey theory: upper and lower bounds, explicit constructions. External graph theory: Turan''s theorem, the Erdos-Gallai theorem. Probabilistic methods. [Offered: F]', 'CO');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CO 444', 'Algebraic Graph Theory', 0.5, 'LEC', 'An introduction to the methods of and some interesting current topics in algebraic graph theory. Topics covered will include vertex-transitive graphs, eigenvalue methods, strongly regular graphs and may include graph homomorphisms, Laplacians or knot and link invariants.', 'CO');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CO 446', 'Matroid Theory', 0.5, 'LEC', 'This is an introductory course on matroid theory, with particular emphasis on graphic matroids and on topics that are applicable to graph theory. The topics include matroid intersection and partition, graphic matroids, matroid connectivity, regular matroids, and representable matroids. Applications include matching, disjoint paths, disjoint spanning trees, the 8-flow theorem for graphs, planarity testing, and recognizing totally unimodular matrices. [Offered: S]', 'CO');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CO 450', 'Combinatorial Optimization', 0.5, 'LEC', 'Characterizations of optimal solutions and efficient algorithms for optimization problems over discrete structures. Topics include network flows, optimal matchings, T-joins and postman tours, matroid optimization. [Offered: F]', 'CO');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CO 452', 'Integer Programming', 0.5, 'LEC', 'Formulation of problems as integer linear programs. Solution by branch-and-bound and cutting plane algorithms. Introduction to the theory of valid inequalities and polyhedral combinatorics.', 'CO');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CO 453', 'Network Design', 0.5, 'LEC', 'Network design under constraints on cost, capacity, distance, and reliability. Approximation algorithms. The set covering problem. Tree solutions: spanning trees, Steiner trees, Gomory-Hu trees, optimum communication spanning trees. Connectivity, survivability, and reliability. Network design with concentrators: the terminal layout problem. Location problems on networks.', 'CO');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CO 454', 'Scheduling', 0.5, 'LEC', 'An overview of practical optimization problems that can be posed as scheduling problems. Characterizations of optimal schedules. Simple and efficient combinatorial algorithms for easy problems. A brief overview of computational complexity, definition of P, NP, NP-complete and NP-hard. Integer programming formulations, the traveling salesman problem, heuristics, dynamic programming, and branch-and-bound approaches. Polynomial-time approximation algorithms. [Offered: S]', 'CO');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CO 456', 'Introduction to Game Theory', 0.5, 'LEC', 'A broad introduction to game theory and its applications to the modeling of competition and cooperation in business, economics, and society. Two-person games in strategic form and Nash equilibria. Extensive form games, including multi-stage games. Coalition games and the core. Bayesian games, mechanism design, and auctions.', 'CO');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CO 459', 'Topics in Optimization', 0.5, 'SEM', 'An undergraduate seminar in optimization. The primary objective is to study recent work in specific areas of optimization. Course content may vary from term to term.', 'CO');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CO 463', 'Convex Optimization and Analysis', 0.5, 'LEC', 'An introduction to the modern theory of convex programming, its extensions and applications. Structure of convex sets, separation and support, subgradient calculus for convex functions, Fenchel conjugacy and duality, Lagrange multipliers. Ellipsoid method for convex optimization. [Offered: W]', 'CO');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KPE 217W', 'Physical Activity, Sport and the Social Sciences (WLU)', 0.5, 'LEC', 'None', 'KPE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('COGSCI 300', 'Intelligence in Machines, Humans, and Other Animals', 0.5, 'LEC', 'A comparison of the representations and processes that produce intelligent performance, such as problem-solving, learning, and language in natural and artificial systems.', 'COGSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CO 466', 'Continuous Optimization', 0.5, 'LEC', 'Numerical algorithms for nonlinear optimization. Newton, variable-metric, quasi-Newton and conjugate gradient methods. Obtaining derivatives. Convexity. Trust region methods. Constrained optimization including optimality conditions, sequential quadratic programming, interior point, and active set strategies.', 'CO');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CO 471', 'Semidefinite Optimization', 0.5, 'LEC', 'Optimization over convex sets described as the intersection of the set of symmetric, positive semidefinite matrices with affine spaces. Formulations of problems from combinatorial optimization, graph theory, number theory, probability and statistics, engineering design, and control theory. Theoretical and practical consequences of these formulations. Duality theory and algorithms.', 'CO');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CO 480', 'History of Mathematics', 0.5, 'LEC', 'An in-depth examination of the origins of mathematics, beginning with examples of Babylonian mathematics. Topics may include Pythagorean triples, solution of equations, estimation of pi, duplication of the cube, trisection of an angle, the Fibonacci sequence, the origins of calculus.', 'CO');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CO 481', 'Introduction to Quantum Information Processing', 0.5, 'LEC, TST', 'Basics of computational complexity; basics of quantum information; quantum phenomena; quantum circuits and universality; relationship between quantum and classical complexity classes; simple quantum algorithms; quantum Fourier transform; Shor factoring algorithm; Grover search algorithm; physical realization of quantum computation; error-correction and fault-tolerance; quantum key distribution. [Offered: W]', 'CO');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CO 485', 'The Mathematics of Public-Key Cryptography', 0.5, 'LEC', 'An in-depth study of public-key cryptography. Number-theoretic problems: prime generation, integer factorization, discrete logarithms. Public-key encryption, digital signatures, key establishment, secret sharing. Proofs of security. [Offered: F]', 'CO');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CO 487', 'Applied Cryptography', 0.5, 'LEC', 'A broad introduction to cryptography, highlighting the major developments of the past twenty years. Symmetric ciphers, hash functions and data integrity, public-key encryption and digital signatures, key establishment, key management. Applications to Internet security, computer security, communications security, and electronic commerce. [Offered: W]', 'CO');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CO 499', 'Reading in Combinatorics and Optimization', 0.5, 'RDG', 'Reading course as announced by the department.', 'CO');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CO 601', 'Fundamentals of Enumerative Combinatorics', 0.5, 'LEC', 'Elementary counting, bijections. Sets, weights, decompositions.  The ordinary generating series, sum, product, composition and differentiation lemmas; formal use in combinatorial identities.  The algebra of formal power series.  Strings on a finite alphabet.  Unique factorization of strings.  Pattern and noncommutative methods.  The Maximal Decomposition Theorem.  Rational and algebraic generating series, linear recurrence equations.  Integer partitions.  Functional equation.   Ferrers graph, geometric decompositions, Euler''s Pentagonal Number Theorem.  Vector spaces over GF(q), linear transformations, finite forms of partition theorems, Gaussian coefficients.  Inversions in permutations.  The exponential generating series, rule of sum and product, composition and differentiation lemmas.  Disjoint cycles and permutations, functions, set partitions, graphs.  Lagrange''s Implicit Function Theorem.  Lattice paths, Wiener-Hopf Factorization.  Lattice polygons, polyominos, trees and functions.  Formal use in combinatorial identities.  Enumeration under group action, Orbit-Stabilizer Theorem, Burnside''s Lemma, Polya''s Theorem.  Necklaces, abstract trees and trees embedded in the plane.', 'CO');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CO 602', 'Fundamentals of Optimization', 0.5, 'LEC', 'Linear Optimization: Farkas'' Lemma, Duality, Simplex Method,Geometry Of Polyhedra. Combinatorial Optimization: lntegrality Of Polyheqra, Total Unimodularity, Flow Problems, Weighted Bipartite Matching. Continuous Optimization: Convex Sets, Separation Theorem, Convex Functions, Analytic Characterizations Of Convexity, Karush-Kuhn-Tucker Theorem.', 'CO');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CO 630', 'Algebraic Enumeration', 0.5, 'LEC', 'The algebra of formal Laurent series.  Multivariate ordinary generating functions and exponential generating functions. The Lagrange Implicit Function Theorem, the MacMahon Master Theorem.  Enumeration of planar triangulations. The Transfer Matrix method.  Sieve methods, Inclusion/Exclusion, M√∂bius inversion.  P√≥lya Enumeration, Enumeration of Trees.  Basic hypergeometric series, q-analogues, Rogers-Ramanujan identities.  Asymptotic methods.', 'CO');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CO 631', 'Symmetric Functions', 0.5, 'LEC', 'The ring of symmetric functions, standard bases, the Hall inner project.  Young tableaux.  The Robinson-Schensted-Knuth correspondence, the hood-length formula, the Jacobi-Trudi formula, the Pieri rule, the Littlewood-Richardson rule.  Representation theory of the symmetric groups.  Enumeration of plane partitions.  Enumeration of maps on surfaces.  Other topics.', 'CO');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CO 634', 'Combinatorial Designs', 0.5, 'LEC', 'Pairwise orthogonal latin squares. Transversal designs and finite planes. Balanced incomplete block designs, group divisible designs and pairwise balanced designs. Symmetric designs and Hadamard matrices. Recursive constructions. Wilson''s fundamental construction.', 'CO');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CO 639', 'Topics in Combinatorics', 0.5, 'LEC', 'None', 'CO');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CO 640', 'Topics in Graph Theory', 0.5, 'LEC', 'None', 'CO');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CO 642', 'Graph Theory', 0.5, 'LEC', 'Colouring: Brooks'' Theorem and Vizing''s Theorem. Flows: integer and group-valued flows, the flow polynomial, the 6-flow theorem. Extremal graph theory; Ramsey''s theorem, Turan''s theorem, Mader''s theorem on graphs with no n-clique-minor. Probabilistic methods: Lower bounds for Ramsey numbers, graphs with large girth and chromatic number.', 'CO');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CO 644', 'Algebraic Graph Theory', 0.5, 'LEC', 'Automorphisms. Cayley graphs and their properties. Arc and distance transitive graphs. Generalised polygons. homomorphisms and covers. Adjacency and incidence matrices. Eigenvectors of graphs. Quotients. Interlacing. Strongly regular graphs. Line graphs and graphs with least eigenvalue -2. Expanders. Shannon capacity.', 'CO');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CO 646', 'Matroid Theory', 0.5, 'LEC', 'This is an introductory course on matroid theory, with particular emphasis on graphic matroids and on topics that are applicable to graph theory.  The topics include: matroid intersection and partition, graphic matroids, matroid connectivity, regular matroids, and representable matroids.  Applications include: matching, disjoint spanning trees, the 8-flow theorem for graphs, planarity testing, and recognizing totally unimodular matrices.', 'CO');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CO 650', 'Combinatorial Optimization', 0.5, 'LEC', 'Characterizations of optimalsolutions and efficient algorithms for optimization problems over discrete structures. Topics include network flows, optimal matchings, T-joins and postman tours, matroid optimization.', 'CO');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CO 652', 'Integer Programming', 0.5, 'LEC', 'Formulation of problems as integer linear programs. Solution by branch-and-bound and cutting plane algorithms. Introduction to the theory of valid inequalities and polyhedral combinatorics.', 'CO');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CO 663', 'Convex Optimization and  Analysis', 0.5, 'LEC', 'An introduction to the modern theory of convex programming, its extensions and applications. Structure of convex sets, separation and support, subgradient calculus for convex functions, Fenchel conjugacy and duality, Lagrange multipliers. Ellipsoid method for convex optimization.', 'CO');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CO 664', 'Quadratic Programming', 0.5, 'LEC', 'A course on theory and solution algorithms for the minimization of a convex quadratic function subject to linear constraints. Karush-Kuhn-Tucker conditions, duality theory. Active set solution algorithms and their parametric extensions. Quadratic programmes as linear complementarity problems. Applications in portfolio optimization and structural analysis.', 'CO');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CO 666', 'Continuous Optimization', 0.5, 'LEC', 'Numerical algorithms for nonlinear optimization. Newton, variable-metric, quasi-Newton and conjugate gradient methods. Obtaining derivatives. Convexity. Trust region methods. Constrained optimization including optimality conditions, sequential quadratic programming, interior point and active set strategies.', 'CO');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CO 671', 'Semidefinite Optimization', 0.5, 'LEC', 'Optimization over convex sets described as the intersections of the set of symmetric, positive semidefinite matrices with affine spaces.  Formulations of problems from combinatorial optimization, graph theory, number theory, probability and statistics, engineering design and control theory.  Theoretical and practical consequences of these formulations.  Duality theory and algorithms.', 'CO');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CO 681', 'Quantum Information Processing', 0.5, 'LEC', 'Review of basics of quantum information and computational complexity; Simple quantum algorithms; Quantum Fourier transform and Shor factoring algorithm: Amplitude amplification, Grover search algorithm and its optimality; Completely positive trace-preserving maps and Kraus representation; Non-locality and communication complexity; Physical realizations of quantum computation: requirements and examples; Quantum error-correction, including CSS codes, and elements of fault-tolerant computation; Quantum cryptography; Security proofs of quantum key distribution protocols; Quantum proof systems.  Familiarity with theoretical computer science or quantum mechanics will also be an asset, though most students will not be familiar with both.', 'CO');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CO 685', 'The Mathematics of Public-Key Cryptography', 0.5, 'LEC', 'An in-depth study of public-key cryptography, including: number-theoretic problems - prime generation, integer factorization, discrete logarithms; public-key encryption; digital signatures; key establishment; secret sharing; and security definitions and proofs.', 'CO');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CO 687', 'Applied Cryptography', 0.5, 'LEC', 'A broad introduction to cryptography, highlighting the major developments of the past twenty years, including symmetric ciphers, hash functions and data integrity, public-key encryption and digital signatures, key establishment, and key management. Applications to internet security, computer security, communications security, and electronic commerce will be studied.', 'CO');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CO 690', 'Literature and Research Studies', 0.5, 'RDG', 'None', 'CO');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CO 730', 'Asymptotic Enumeration', 0.5, 'LEC', 'Methods of obtaining asymptotic estimates for sums arising in enumeration. Application to Bell numbers, the distribution of balls into cells, and random graphs. Methods for obtaining asymptotic  estimates for the coefficients of generating functions. Application to random planar graph problems and to unimodality problems.', 'CO');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CO 734', 'Topics in Combinatorial Design', 0.5, 'LEC', 'None', 'CO');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CO 738', 'Probabilistic Methods in Discrete Mathematics', 0.5, 'LEC', 'The probabilitistic method proves the existence of a combinatorial structure by showing a random element in an appropriate probability space has the desired properties with positive probability. The course will introduce the basic techniques and give applications in graph theory, combinatorics, discrete optimization, theoretical CS. In particular, some of the following will be considered: linearity of expectation; alterations; the second moment method; bounding of large deviations; the local lemma; correlation inequalities; martingales and tight concentration; discrepancy; derandomization.', 'CO');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CO 739', 'Topics in Combinatorics', 0.5, 'LEC', 'None', 'CO');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CO 743', 'Directed Graphs and Applications', 0.5, 'LEC', 'An introduction to the concepts of directed graphs, problems about directed circuits and directed cuts, and minimax equalities relating these and other graphical objects.', 'CO');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CO 745', 'Graph Theory Algorithms', 0.5, 'LEC', 'A general study of algorithms for the solutions of problems in graph theory. Included are algorithms for graph isomorphism, planarity, connectedness, chromatic number, spanning trees, circuits and many others.', 'CO');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CO 749', 'Topics in Graph Theory', 0.5, 'LEC', 'None', 'CO');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CO 750', 'Topics in Combinatorial Optimization', 0.5, 'LEC', 'None', 'CO');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CO 751', 'Topics in Matroid Theory', 0.5, 'LEC', 'None', 'CO');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CO 754', 'Approximation Algorithms in Combinatorial Optimization', 0.5, 'LEC', 'The course studies some of the successful paradigms for designing approximation algorithms and for proving approximation guaranteees, such as the greedy method, formulating and solving linear programming relaxations, the primal-dual method, randomized rounding, semidefinite programming relaxations, approximation of metrics. Also, the PCP theorem, and its application to the hardness of approximating specific problems, e.g., set covering and shortest lattice vector.', 'CO');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CO 759', 'Topics in Discrete Optimization', 0.5, 'LEC', 'None', 'CO');
