INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('COOP 601', 'Career Success Strategies', 0, 'PRA, SEM', 'This course is a combination of career development advice and co-op work search process information.  The topics include navigating the online job system, preparing resumes, developing interview skills and preparing for a successful work term.  COOP 601 is taken the academic term prior to your first work term.  COOP 601 is held with PD1 for undergraduate co-op students.  Must be a grad co-op student.', 'COOP');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CROAT 101', 'Elementary Croatian 1', 0.5, 'LAB, LEC', 'For students with little or no knowledge of Croatian. The basic elements of Croatian grammar with emphasis on oral practice and pronunciation.', 'CROAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CROAT 102', 'Elementary Croatian II', 0.5, 'LAB, LEC', 'A continuation of CROAT 101.', 'CROAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CROAT 201', 'Intermediate Croatian I', 0.5, 'LAB, LEC', 'This course is a continuation of Elementary Croatian. It offers extensive practice in both the spoken and written language. Vocabulary building, comprehension, and pronunciation are stressed.', 'CROAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CROAT 202', 'Intermediate Croatian II', 0.5, 'LAB, LEC', 'A continuation of CROAT 201.', 'CROAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CROAT 496', 'Special Topics in Croatian Studies', 0.5, 'RDG', 'Selected topics in Croatian Studies.', 'CROAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 100', 'Introduction to Computing through Applications', 0.5, 'LEC', 'Using personal computers as effective problem solving tools for the present and the future. Effective use of spreadsheets to process, manipulate, and visualize numeric and textual information. Introduction to the Internet, World Wide Web, HTML, and XML. Algorithms underlying the functional components of web search engines and their influence on data access. Using wikis to publish, reshape, and organize data collaboratively. [Offered: F,W,S]', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 115', 'Introduction to Computer Science 1', 0.5, 'LAB, LEC, TST, TUT', 'An introduction to the fundamentals of computer science through the application of elementary programming patterns in the functional style of programming. Function definition and application. Tracing via substitution. Design, testing, and documentation. Recursive data definitions. Lists and trees. Functional and data abstraction.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 116', 'Introduction to Computer Science 2', 0.5, 'LAB, LEC, TST, TUT', 'This course builds on the techniques and patterns learned in CS 115 while making the transition to use of an imperative language. Generative and structural recursion. Mutation (assignment) and its role in an imperative language. Primitive types and basic I/O. Sequencing, selection, looping. Function definition and use. File and console I/O. Issues in computer science. [Offered: F,W,S]', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 135', 'Designing Functional Programs', 0.5, 'LAB, LEC, TST, TUT', 'An introduction to the fundamentals of computer science through the application of elementary programming patterns in the functional style of programming. Syntax and semantics of a functional programming language. Tracing via substitution. Design, testing, and documentation. Linear and nonlinear data structures. Recursive data definitions. Abstraction and encapsulation. Generative and structural recursion. Historical context.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 136', 'Elementary Algorithm Design and Data Abstraction', 0.5, 'LAB, LEC, TST, TUT', 'This course builds on the techniques and patterns learned in CS 135 while making the transition to use an imperative language. It introduces the design and analysis of algorithms, the management of information, and the programming mechanisms and methodologies required in implementations. Topics discussed include iterative and recursive sorting algorithms; lists, stacks, queues, trees, and their application; abstract data types and their implementations.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 137', 'Programming Principles', 0.5, 'LAB, LEC, TST, TUT', 'Review of fundamental programming concepts and their application. Procedures and parameter passing. Arrays and structures. Recursion. Sorting. Pointers and simple dynamic structures. Space and time analysis of designs. Design methodologies. [Offered: F]', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 138', 'Introduction to Data Abstraction and Implementation', 0.5, 'LAB, LEC, TST, TUT', 'Software abstractions via elementary data structures and their implementation; encapsulation and modularity; class and interface definitions; object instantiation; recursion; elementary abstract data types, including sequences, stacks, queues, and trees; implementation using linked structures and arrays; vectors and strings; memory models; automatic vs. dynamic memory management.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 145', 'Designing Functional Programs (Advanced Level)', 0.5, 'LAB, LEC, TST, TUT', 'CS 145 is an advanced-level version of CS 135.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 146', 'Elementary Algorithm Design and Data Abstraction (Advanced Level)', 0.5, 'LAB, LEC, TST, TUT', 'CS 146 is an advanced-level version of CS 136.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 200', 'Concepts for Advanced Computer Usage', 0.5, 'LAB, LEC, TST', 'Important concepts underlying major personal computer application categories; methodologies for learning and evaluating software; operating system and hardware design from the user''s point of view, with implications for maintaining a personal computer. Students are encouraged to use their own personal computer for assignments. Social media and the effect of technology on society are explored throughout the course. [Offered: W,S]', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 230', 'Introduction to Computers and Computer Systems', 0.5, 'LAB, LEC, TST, TUT', 'Basic computer architecture, organization, system services, and software. Typology of processors, memory, I/O devices and their performance.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 234', 'Data Types and Structures', 0.5, 'LAB, LEC, TST', 'Top-down design of data structures. Using representation-independent data types. Introduction to commonly used data types, including lists, sets, mappings, and trees. Selection of data representation.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 240', 'Data Structures and Data Management', 0.5, 'LAB, LEC, TST, TUT', 'Introduction to widely used and effective methods of data organization, focusing on data structures, their algorithms, and the performance of these algorithms. Specific topics include priority queues, sorting, dictionaries, data structures for text processing.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 241', 'Foundations of Sequential Programs', 0.5, 'LAB, LEC, TST, TUT', 'The relationship between high-level languages and the computer architecture that underlies their implementation, including basic machine architecture, assemblers, specification and translation of programming languages, linkers and loaders, block-structured languages, parameter passing mechanisms, and comparison of programming languages.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 245', 'Logic and Computation', 0.5, 'LEC, TST, TUT', 'Propositional and predicate logic. Soundness and completeness and theirimplications. Unprovability of formulae in certain systems. Undecidability of problems in computation, including the halting problem. Reasoning about programs. Correctness proofs for both recursive and iterative program constructions.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 246', 'Object-Oriented Software Development', 0.5, 'LAB, LEC, TST, TUT', 'Introduction to object-oriented programming and to tools and techniques for software development. Designing, coding, debugging, testing, and documenting medium-sized programs: reading specifications and designing software to implement them; selecting appropriate data structures and control structures; writing reusable code; reusing existing code; basic performance issues; debuggers; test suites.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 247', 'Software Engineering Principles', 0.5, 'LAB, LEC, TST, TUT', 'Systematic methods for designing, coding, testing, and documenting medium-sized programs. Major topics include abstraction, modularity, software modeling, object-oriented programming and design, generic programming, testing and debugging.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 251', 'Computer Organization and Design', 0.5, 'LAB, LEC, TST', 'Overview of computer organization and performance. Basics of digital logic design. Combinational and sequential elements. Data representation and manipulation. Basics of processor design. Pipelining. Memory hierarchies. Multiprocessors.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 330', 'Management Information Systems', 0.5, 'LEC, TST', 'An introduction to information systems and their strategic role in business. Topics include types of information systems, organizational requirements, systems development strategies, decision support systems, data and information management, and information systems management, control and implementation. [Offered: F,W,S]', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 335', 'Computational Methods in Business and Finance', 0.5, 'LEC, TST', 'An introduction to numerical methods for business and finance. Floating-point arithmetic, interpolation. Methods for portfolio optimization and contingent-claims valuation. Solution of nonlinear equations. Monte Carlo methods, lattice methods, simulation of hedging strategies. [Offered: F,W]', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 338', 'Computer Applications in Business: Databases', 0.5, 'LAB, LEC', 'A user-oriented approach to the management of large collections of data. Methods used for the storage, selection and presentation of data. Common database management systems.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 341', 'Algorithms', 0.5, 'LAB, LEC, TST', 'The study of efficient algorithms and effective algorithm design techniques. Program design with emphasis on pragmatic and mathematical aspects of program efficiency. Topics include divide and conquer algorithms, recurrences, greedy algorithms, dynamic programming, graph search and backtrack, problems without algorithms, NP-completeness and its implications.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 343', 'Concurrent and Parallel Programming', 0.5, 'LAB, LEC, TST', 'An introduction to concurrent and parallel programming, with an emphasis on language constructs. Major topics include: exceptions, coroutines, atomic operations, critical sections, mutual exclusion, semaphores, high-level concurrency, deadlock, interprocess communication, process structuring, shared memory and distributed architectures. Students will learn how to structure, implement and debug concurrent programs.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 348', 'Introduction to Database Management', 0.5, 'LAB, LEC, TST', 'The main objective of this course is to introduce students to fundamentals of database technology by studying databases from three viewpoints: those of the database user, the database designer, and the database administrator. It teaches the use of a database management system (DBMS) by treating it as a black box, focusing only on its functionality and its interfaces. Topics include: introduction to database systems, relational database systems, database design methodology, SQL and interfaces, database application development, concept of transactions, ODBC, JDBC, database tuning, database Administration, and current topics (distributed databases, data warehouses, data mining).', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 349', 'User Interfaces', 0.5, 'LAB, LEC, TST', 'An introduction to contemporary user interfaces, including the basics of human-computer interaction, the user interface design/evaluation process, the event abstraction, user interface components, specification of user interfaces, and the architectures within which user interfaces are developed. Implementation and evaluation of a typical user interface is considered.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 350', 'Operating Systems', 0.5, 'LAB, LEC, TST', 'An introduction to the fundamentals of operating system function, design, and implementation. Topics include concurrency, synchronization, processes, threads, scheduling, memory management, file systems, device management, and security.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 360', 'Introduction to the Theory of Computing', 0.5, 'LEC', 'Models of computers including finite automata and Turing machines. Basics of formal languages with applications to the syntax of programming languages. Alternate characterizations of language classes. Proving unrecognizability. Unsolvable problems and their relevance to the semantics of programming.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 365', 'Models of Computation', 0.5, 'LAB, LEC', 'Finite automata and regular expressions. Pushdown automata and context-free grammars. Turing machines and undecidability. Time and space complexity. Diagonalization and hierarchies. CS 365 covers the material in CS 360 at an accelerated pace plus additional topics in computational complexity.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 370', 'Numerical Computation', 0.5, 'LAB, LEC, TST', 'Principles and practices of basic numerical computation as a key aspect of scientific computation. Visualization of results. Approximation by splines, fast Fourier transforms, solution of linear and nonlinear equations, differential equations, floating point number systems, error, stability. Presented in the context of specific applications to image processing, analysis of data, scientific modeling.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 371', 'Introduction to Computational Mathematics', 0.5, 'LAB, LEC', 'A rigorous introduction to the field of computational mathematics. The focus is on the interplay between continuous models and their solution via discrete processes. Topics include pitfalls in computation, solution of linear systems, interpolation, discrete Fourier transforms, and numerical integration. Applications are used as motivation.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 431', 'Data-Intensive Distributed Analytics', 0.5, 'LEC', 'Introduces non-CS major students to infrastructure for data-intensive analytics, with a focus on abstractions, frameworks, and algorithms that allow developers to distribute computation across many machines. Topics include core concepts (partitioning, replication, locality, consistency), computational models (MapReduce, dataflows, stream processing, bulk-synchronous parallel), and applications. [Offered: F,W]', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 398', 'Topics in Computer Science', 0.5, 'LEC', 'See the Course Offerings List for topics available.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 399', 'Readings in Computer Science', 0.5, 'RDG', 'Reading course as announced by the department.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 430', 'Applications Software Engineering', 0.5, 'LEC, TST', 'An investigation into the role and function of software engineering practice in the construction of computer based systems. Topics include: requirements and specification; documentation techniques; analysis and design; implementation; testing and maintenance; management issues. [Offered: F,W]', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 432', 'Business Systems Analysis', 0.5, 'LEC, TST', 'Survey of organization and management theory. Systems theory and the systems approach. Systems design. Database concepts. Implementation and evaluation of computer based information systems. [Offered: F,S]', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 436', 'Networks and Distributed Computer Systems', 0.5, 'LAB, LEC, TST', 'An introduction to networks, protocols, and distributed systems. Layered models, resource management, naming, addressing and routing, reliable communication, security, and higher-level services.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 442', 'Principles of Programming Languages', 0.5, 'LAB, LEC, TST', 'An exposure to important concepts and issues in contemporary programming languages. Data types, abstraction, and polymorphism. Program structure. Lambda calculus and functional programming, logic programming, object-oriented programming. Semantics of programming languages. Critical comparison of language features and programming methodologies using examples drawn from a variety of programming languages including Lisp, Prolog, ML, Ada, Smalltalk, Icon, APL, and Lucid. Programming assignments involve the use of some of these languages.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 444', 'Compiler Construction', 0.5, 'LAB, LEC, TST', 'Phases of compilation. Lexical analysis and a review of parsing. Compiler-compilers and translator writing systems. LEX and YACC. Scope rules, block structure, and symbol tables. Runtime stack management. Parameter passage mechanisms. Stack storage organization and templates. Heap storage management. Intermediate code. Code generation. Macros.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 445', 'Software Requirements Specification and Analysis', 0.5, 'LAB, LEC, TST, TUT', 'Introduces students to the requirements definition phase of software development. Models, notations, and processes for software requirements identification, representation, analysis, and validation. Cost estimation from early documents and specifications.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 446', 'Software Design and Architectures', 0.5, 'LAB, LEC, TST, TUT', 'Introduces students to the design, implementation, and evolution phases of software development. Software design processes, methods, and notation. Implementation of designs. Evolution of designs and implementations. Management of design activities.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 447', 'Software Testing, Quality Assurance and Maintenance', 0.5, 'LAB, LEC, TST, TUT', 'Introduces students to systematic testing of software systems. Software verification, reviews, metrics, quality assurance, and prediction of software reliability and availability. Related management issues.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 448', 'Database Systems Implementation', 0.5, 'LAB, LEC, TST', 'The objective of this course is to introduce students to fundamentals of building a relational database management system. The course focuses on the database engine core technology by studying topics such as storage management (data layout, disk-based data structures), indexing, query processing algorithms, query optimization, transactional concurrency control, logging and recovery.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 449', 'Human-Computer Interaction', 0.5, 'LAB, LEC, TST', 'An introduction to the fundamental theories, methods, and research in the design and evaluation of novel computational artifacts designed to meet real-world human needs.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 450', 'Computer Architecture', 0.5, 'LAB, LEC, TST', 'The course is intended to provide the student with an appreciation of modern computer design and its relation to system architecture, compiler technology and operating system functionality. The course places an emphasis on design based on the measurement of performance and its dependency on parallelism, efficiency, latency and resource utilization.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 452', 'Real-time Programming', 0.5, 'LAB, LEC, TST', 'Intended to give students experience with tools and techniques of real-time programming, this course includes not only issues of microcomputer architecture and a real-time programming language and operating system, but also hands-on experience programming a microcomputer for applications such as process control, data acquisition and communication.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 454', 'Distributed Systems', 0.5, 'LAB, LEC, TST', 'An introduction to distributed systems, emphasizing the multiple levels of software in such systems. Specific topics include fundamentals of data communications, network architecture and protocols, local-area networks, concurrency control in distributed systems, recovery in distributed systems, and clock synchronization.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 456', 'Computer Networks', 0.5, 'LAB, LEC, TST', 'An introduction to network architectures and protocols, placing emphasis on protocols used in the Internet. Specific topics include application layer protocols, network programming, transport protocols, routing, multicast, data link layer issues, multimedia networking, network security, and network management. [Offered: F,W,S]', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 457', 'System Performance Evaluation', 0.5, 'LAB, LEC, TST', 'Basic techniques of system performance evaluation. Specific topics include: performance modeling, discrete event simulation, verification and validation of simulation models, analysis of simulation output, analysis of single server queue and queueing networks, modeling of computer systems, networks, and other queueing or non-queueing systems.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 458', 'Computer Security and Privacy', 0.5, 'LAB, LEC, TST', 'Security and privacy issues in various aspects of computing. Specific topics include: comparing security and privacy, program security, writing secure programs, controls against program threats, operating system security, formal security models, network security, Internet application security and privacy, privacy-enhancing technologies, database security and privacy, inference, data mining, security policies, physical security, economics of security, and legal and ethical issues.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 462', 'Formal Languages and Parsing', 0.5, 'LEC, TST', 'Languages and their representations. Grammars --Chomsky hierarchy. Regular sets and sequential machines. Context-free grammars -- normal forms, basic properties. Pushdown automata and transducers. Operations on languages. Undecidable problems in language theory. Applications to the design of programming languages and compiler construction.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 466', 'Algorithm Design and Analysis', 0.5, 'LEC, TST', 'Algorithmic approaches and methods of assessment that reflect a broad spectrum of criteria, including randomized algorithms, amortized analysis, lower bounds, approximation algorithms, and on-line algorithms. Particular examples will be chosen from different areas of active research and application. [Offered: F,S]', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 467', 'Introduction to Quantum Information Processing', 0.5, 'LEC, TST', 'Basics of computational complexity; basics of quantum information; quantum phenomena; quantum circuits and universality; relationship between quantum and classical complexity classes; simple quantum algorithms; quantum Fourier transform; Shor factoring algorithm; Grover search algorithm; physical realization of quantum computation; error-correction and fault-tolerance; quantum key distribution. [Offered: W]', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 473', 'Medical Image Processing', 0.5, 'LAB, LEC, TST', 'An introduction to computational problems in medical imaging. Sources of medical images (MRI, CT, ultrasound, PET) as well as reconstruction methods for MRI and CT. Image manipulation and enhancement such as denoising and deblurring. Patient motion correction and optimal image alignment. Tissue classification and organ delineation using image topology.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 475', 'Computational Linear Algebra', 0.5, 'LAB, LEC, TST', 'Basic concepts and implementation of numerical linear algebra techniques and their use in solving application problems. Special methods for solving linear systems having special features. Direct methods: symmetric, positive definite, band, general sparse structures, ordering methods.
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 476', 'Numeric Computation for Financial Modeling', 0.5, 'LAB, LEC, TST', 'The interaction of financial models, numerical methods, and computing environments. Basic computational aspects of option pricing and hedging. Numerical methods for stochastic differential equations, strong and weak convergence. Generating correlated random numbers. Time-stepping methods. Finite difference methods for the Black-Scholes equation. Discretization, stability, convergence. Methods for portfolio optimization, effect of data errors on portfolio weights.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 480', 'Introduction to Machine Learning', 0.5, 'LAB, LEC, TST', 'Introduction to modeling and algorithmic techniques for machines to learn concepts from data. Generalization: underfitting, overfitting, cross-validation. Tasks: classification, regression, clustering. Optimization-based learning: loss minimization. regularization. Statistical learning: maximum likelihood, Bayesian learning. Algorithms: nearest neighbour, (generalized) linear regression, mixtures of Gaussians, Gaussian processes, kernel methods, support vector machines, deep learning, sequence learning, ensemble techniques. Large scale learning: distributed learning and stream learning. Applications: Natural language processing, computer vision, data mining, human computer interaction, information retrieval.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 482', 'Computational Techniques in Biological Sequence Analysis', 0.5, 'LEC, TST, TUT', 'Computer science principles and algorithms in biological sequence analysis. Topics include algorithms for sequence comparison, for large-scale database search in biological databases, for sequence assembly, for evolutionary tree reconstruction, for identifying important features in DNA and RNA sequences, and underlying computational techniques for understanding strings and trees and for making probabilistic inferences. [Offered: W]', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 483', 'Computational Techniques in Structural Bioinformatics', 0.5, 'LEC', 'Algorithms and techniques used in the identification and functional characterization of cellular proteins. Topics include: protein databases, gene expression analysis, protein structure prediction, protein function prediction, active site detection and ligand docking, protein-protein interaction, HTCS (High Throughput Conformational Search), and QSAR (Quantitative Structure-Activity Relationships). [Offered: W]', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 484', 'Computational Vision', 0.5, 'LAB, LEC, TST', 'Introduction to image and vision understanding by computer. Camera-system geometry, image formation and lighting, and image acquisition. Basic visual processes for recognition of edges, regions, lines, and surfaces. Processing of stereo images, and motion in image sequences. Object recognition. Applications of computer-vision systems.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 485', 'Statistical and Computational Foundations of Machine Learning', 0.5, 'LEC, TST', 'Extracting meaningful patterns from random samples of large data sets. Statistical analysis of the resulting problems. Common algorithmic paradigms for such tasks. Central concepts: VC-dimension, margins of a classifier, sparsity and description length, other types of regularization. Performance guarantees: generalization bounds, data dependent error bounds and computational complexity of learning algorithms. Common paradigms: neural networks, kernel methods and support-vector machines, boosting, nearest neighbor classifiers. Applications to data mining.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 486', 'Introduction to Artificial Intelligence', 0.5, 'LAB, LEC, TST', 'Goals and methods of artificial intelligence. Methods of general problem solving. Knowledge representation and reasoning. Planning. Reasoning about uncertainty. Machine learning. Multi-agent systems. Natural language processing.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 487', 'Introduction to Symbolic Computation', 0.5, 'LAB, LEC, TST', 'An introduction to the use of computers for symbolic mathematical computation, involving traditional mathematical computations such as solving linear equations (exactly), analytic differentiation and integration of functions, and analytic solution of differential equations.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 488', 'Introduction to Computer Graphics', 0.5, 'LAB, LEC, TST', 'Software and hardware for interactive computer graphics. Implementation of device drivers, 3-D transformations, clipping, perspective, and input routines. Data structures, hidden surface removal, colour shading techniques, and some additional topics will be covered.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 489', 'Advanced Topics in Computer Science', 0.5, 'LEC, TUT', 'See the Course Offerings List for topics available.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 490', 'Information Systems Management', 0.5, 'LEC, TST', 'The integration of business and technical considerations in the design, implementation and management of information systems. Topics include: IS planning and development; business, management, executive, and strategic information systems, including case studies of selected large- scale systems; decision support systems; end-user training and development; systems security, disaster planning and recovery. Practical examples of information systems in industry. [Offered: W,S]', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 492', 'The Social Implications of Computing', 0.5, 'LEC, TST', 'This course is designed to consider the problems encountered by individuals, organizations and society as computer technology is adopted, with a view towards assessing possible courses of action. [Offered: W,S]', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 497', 'Multidisciplinary Studies in Computer Science', 0.5, 'LEC', 'See the Course Offerings list for topics available.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 499R', 'Readings in Computer Science', 0.5, 'RDG', 'Reading course as announced by the department.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 499T', 'Honours Thesis', 0.5, 'PRJ', 'The student will undertake new analysis, synthesis, measurement, or experimentation to produce a document that demonstrates a depth of understanding of a topic that goes beyond what is obtained in a standard undergraduate education.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 642', 'Principles of Programming Languages', 0.5, 'LEC', 'An exposure to important concepts and issues in contemporary programming languages. Data types, abstraction, and polymorphism. Program structure. Lambda calculus and functional programming, logic programming, object-oriented programming. Semantics of programming languages. Critical comparison of language features and programming methodologies using examples drawn from a variety of programming languages including Lisp, Prolog, ML, Ada, Smalltalk, Icon, APL, and Lucid. Programming assignments involve the use of some of these languages.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 644', 'Compiler Construction', 0.5, 'LEC', 'Phases of compilation. Lexical analysis and a review of parsing. Compiler-compilers and translator writing systems. LEX and YACC. Scope rules, block structure, and symbol tables. Runtime stack management. Parameter passage mechanisms. Stack storage organization and templates. Heap storage management. Intermediate code. Code generation. Macros.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 645', 'Software Requirements Specification and Analysis', 0.5, 'LEC, TUT', 'Introduces students to the requirements definition phase of software development: Models, notations, and processes for software requirements identification, representation, analysis, and validation.  Cost estimation from early documents and specifications.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 646', 'Software Design and  Architectures', 0.5, 'DIS, LEC, TUT', 'Introduces students to the design, implementation, and evolution phases of software development.  Software design processes, methods, and notation.  Implementation of designs. Evolution of designs and implementations.  Management of design activities.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 647', 'Software Testing, Quality Assurance and Maintenance', 0.5, 'DIS, LEC, TUT', 'Introduces students to systematic testing of software systems.  Software verification, reviews, metrics, quality assurance, and prediction of software reliability and availability.  Related management issues.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 648', 'Database Systems Implementation', 0.5, 'LEC', 'The objective of this course is to introduce students to fundamentals of building a relational database management system.  The course focuses on the database engine core technology by studying topics such as storage management (data layout, disk-based data structures), indexing, query processing algorithms, query optimization, transactional concurrency control, logging and recovery.  Preference will be given to CS graduate students.  All other require permission from the school.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 649', 'Human-Computer Interaction', 0.5, 'LEC', 'An introduction to the fundamental theories, methods, and research in the design and evaluation of description:  novel computational artifacts designed to meet real-world human needs.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 650', 'Computer Architecture', 0.5, 'LEC', 'The course is intended to provide the student with an appreciation of modern computer design and its relation to system architecture, compiler technology and operating system functionality. The course places an emphasis on design based on the measurement of performance and its dependency on parallelism, efficiency, latency and resource utilization.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 652', 'Real-Time Programming', 0.5, 'LEC', 'Intended to give students experience with tools and techniques of real-time programming, this course includes not only issues of microcomputer architecture and a real-time programming language and operating system, but also hands-on experience programming a microcomputer for applications such as process control, data acquisition and communication. .Preference will be given to CS graduate students.  All others require approval from the department.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 654', 'Distributed Systems', 0.5, 'LEC', 'An introduction to distributed systems, emphasizing the multiple levels of software in such systems. Specific topics include fundamentals of data communications, network architecture and protocols, local-area networks, concurrency control in distributed systems, recovery in distributed systems, and clock synchronization. Preference will be given to CS graduate students.  All others require approval from the department.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 656', 'Computer Networks', 0.5, 'LEC', 'An introduction to network architectures and protocols, placing emphasis on protocols used in the Internet.  Specific topics include application layer protocols, network programming, transport protocols, routing, multicast, data link layer issues, multimedia networking, network security, and network management.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 657', 'System Performance Evaluation', 0.5, 'LEC', 'Basic techniques of system performance evaluation. Specific topics include: performance modeling, discrete event simulation, verification and validation of simulation models, analysis of simulation output, analysis of single server queue and queueing networks, modeling of computer systems, networks, and other queueing or non-queueing systems.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 658', 'Computer Security and Privacy', 0.5, 'LAB, LEC', 'Security and privacy issues in various aspects of computing.  Specific topics include: comparing security and privacy, program security, writing secure programs, controls against program threats, operating system security, formal security models, network security, Internet application security and privacy, privacy-enhancing technologies, database security and privacy, inference data mining, security policies, physical security, economics of security, and legal and ethical issues.  (Note: Knowledge of operating systems equivalent to that obtained from CS 350 is assumed.)', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 662', 'Formal Languages and Parsing', 0.5, 'LEC', 'Languages and their representations. Grammars-Chomsky hierarchy. Regular sets and sequential machines. Context-free grammars-normal forms, basic properties. Pushdown automata and transducers. Operations on languages. Undecidable problems in language theory. Applications to the design of programming languages and compiler construction.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 664', 'Computational Complexity Theory', 0.5, 'LEC', 'The classification of problems according to the computational resources required for their solution, with emphasis on properties of feasible computations rather than on specific algorithms. Topics include: time and space complexity, tractable and intractable problems, computation using randomness, parallel computation.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 666', 'Algorithm Design and Analysis', 0.5, 'LEC', 'Design of good algorithms and analysis of the resources they consume. Lower bounds on the resource requirements of algorithms to compute certain functions. Problems from the following areas are discussed in this light: sorting and order statistics, data structures, arithmetic computations, the NP-complete problems.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 673', 'Medical Image Processing', 0.5, 'LEC', 'An introduction to computational problems in medical imaging.  Sources of medical images (MRI, CT, ultrasound, PET) as well as reconstruction methods for MRI and CT.  Image manipulation and enhancement such as denoising and deblurring.  Patient motion correction and optimal image alignment.  Tissue classification and organ delineation using image topology.  (Note: Lab is not scheduled and students are expected to find time in open hours to complete their work.  Offered: W)', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 675', 'Computational Linear Algebra', 0.5, 'LEC', 'Basic concepts and implementation of numerical linear algebra techniques and their use in solving application problems.  Special methods for solving linear systems having special features.  Direct methods: symmetric, positive definite, band, general sparse structures, ordering methods.  Iterative methods: Jacobi, Gauss-Seidel, SOR, conjugate gradient.  Computing and using orthogonal factorizations of matrices.  QR and SVD methods for solving least squares problems.  Eigenvalue and singular value decompositions.  Computation and uses of these decompositions in practice.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 676', 'Numeric Computation for Financial Modelling', 0.5, 'LEC', 'The interaction of financial modes, numerical methods, and computing environments.  Basic computational aspects of option pricing and hedging. Numerical methods for stochastic differential equations, strong and weak convergence.  Generating correlated random numbers.  Time-stepping methods.  Finite difference methods for Black-Scholes equation.  Discretization, stability, convergence.  Methods for portfolio optimization, effect of data errors on portfolio weights. (Heldwith CS 476).', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 682', 'Computational Techniques in Biological Sequence Analysis', 0.5, 'LEC', 'Computer science principles and algortihms in biological sequence analysis.  Topics include algotithms for sequence comparison, for large-scale database search in biological databases, for sequence assembly, for evolutionary tree reconstruction, for identifying important featrues in DNA and RNA sequences, and underlying computational techniques for understanding strings and trees and for making probabilistic inferences. (Heldwith CS 482).', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 683', 'Computational Techniques in Structural Bioinformatics', 0.5, 'LEC', 'Algorithms and techniques used in the identification and functional characterization of cellular proteins.  Topics include: protein databases, gene expression analysis, protein structure prediction, protein function prediction, active site detection and ligand docking, protein-protein interaction, HTCS (High Throughput Conformational Search), and QSAR (Quantitative Structure-Activity Relationships).  (Heldwith CS 483).', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 684', 'Computational Vision', 0.5, 'LEC', 'Introduction to image and vision understanding by computer.  Camera-system geometry, image formation and lighting, and image acquisition.  Basic visual processes for recognition of edges, regions, lines, and surfaces.  Processing of stereo images, and motion in image sequences.  Object recognition.  Applications of computer vision systems.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 685', 'Machine Learning: Statistical and Computational Foundations', 0.5, 'LEC', 'Extracting meaningful patterns from random samples of large data sets.  Statistical analysis of the resulting problems. Common algorithm paradigms for such tasks.  Central concepts: VC-dimension, Margins of classifier, Sparsity and description length.  Performance guarantees: Generalization bounds, data dependent error bounds and computational complexity of learning algorithms.  Common paradigms: Neural networks, Kernel methods and Support Vector machines, Applications to Data Mining.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 686', 'Introduction to Artificial Intelligence', 0.5, 'LEC', 'Goals and methods of artificial intelligence. Methods of general problem solving. Introduction to mathematical logic Mechanical theorem proving. Game playing. Natural language processing. Preference will be given to CS graduate students.  All others require approval from the department. Department approval will be by Undergraduate Advisor.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 687', 'Introduction to Symbolic Computation', 0.5, 'LEC', 'An introduction to the use of computers for symbolic mathematical computation, involving traditional mathematical computations such as solving linear equations (exactly), analytic differentiation and integration of functions, and analytic solution of differential equations.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 688', 'Introduction to Computer Graphics', 0.5, 'LEC', 'Software and hardware for interactive computer graphics. Implementation of device drivers, 3-D transformations, clipping, perspective, and input routines. Data structures, hidden surface removal, colour shading techniques, and some additional topics will be covered. Preference will be given to CS graduate students.  All others require approval from the department.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 690A', 'Literature and Research Studies', 0.5, 'RDG', 'This is an individual study course carried out under the supervision of a Computer Science faculty member. The topic should be agreed upon by both the student and the instructor. This is a credit/no credit course. Department permission will be by Coordinator of Graduate Studies.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 690B', 'Literature and Research Studies', 0.5, 'RDG', 'This is an individual study course carried out under the supervision of a Computer Science faculty member. The topic should be agreed upon by both the student and the instructor. This is a grade course. Department permission will be by Coordinator of Graduate Studies.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 692', 'The Social Implications of Computing', 0.5, 'LEC', 'This course is designed to consider the problems encountered by individuals, organizations and society as computer technology is adopted, with a view towards assessing possible courses of action.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 697', 'Graduate Research Skills Seminar', 0, 'SEM', 'Research and life skills that can help graduate students improve their academic and interpersonal competence-research methodologies, library research skills, creative and critical thinking, time management, stress management, technical reading skills, listening skills, oral communication, writing and publishing in computer science, jobs in academica versus industry.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 698', 'Introductory Research Topics', 0.5, 'LEC, RDG', 'This number is used for courses being offered on a temporary basis. Such a course may be available only once, for example to take advantage of a visiting professor''s expertise, or may be offered experimentally until it is determined whether of not the course should become part of the regular course offerings. It may also be used for an individual study course carried out under the supervision of a Computer Science faculty member with the approval from the Associate Chair, Graduate Studies. This is a grade course. Preference will be given to CS graduate students.  All others require approval of the Department.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 740', 'Database Engineering', 0.5, 'LEC, TUT', 'Project-oriented course that covers the implementation of relational database management systems. Topics include database system architecture; managing primary and secondary storage; query processing; metadata and catalog management; language processing; query optimization and plan generation; concurrency; failures and recovery; extensibility; client-server interactions.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 741', 'Non-Traditional Databases', 0.5, 'LEC', 'Management of non-relational databases, such as multimedia databases, text databases, temporal databases or spatial databases. Each offering will target a specific type of data. Topics include rationale for and common applications of non-relational database management; systems and standards; the abstract data model; data definition and manipulation languages; data storage and indexing; query processing and optimization; updates and transaction management.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 742', 'Parallel and Distributed Database Systems', 0.5, 'LEC', 'Algorithms and architectures used in parallel database management systems, with a focus on relational systems. Topics include system architectures; parallel and distributed query processing; federated dtabase systems; distributed transactions; data replication.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 743', 'Principles of Database Management and Use', 0.5, 'LEC', 'An overview of relational databases and how they are used; exposure to relational database technology.  Fundamentals of transactions.  Database views. Introductions to two or three alternative data models and systems, such as those used for structured text, spatial data, multimedia data, and information retrieval.  Introduction to several current topics in database research, such as warehousing, data mining, managing data streams, data cleaning, data integration, and distributed databases.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 744', 'Advanced Compiler Design', 0.5, 'LEC', 'Project-oriented course that covers optimizing compilers and the implementation of advanced programming language features. Topics include intermediate representations; data-flow, dependence, and alias analysis; optimizing transforms, register allocation, instruction scheduling; memory management, garbage collection, threads, concurrency; single and multiple inheritance, generics, templates, type inference.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 745', 'Computer-Aided Verification', 0.5, 'LEC', 'Application of formal methods to the verification of computer-based systems. Algebraic and automata preliminaries. Temporal logic and model checking. Decision procedures. Mechanized theorem proving. Advanced topics chosen by the instructor.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 746', 'Software Architecture', 0.5, 'LEC', 'A project-oriented course that covers the concepts in software architecture. Topics include basic concepts (components and connectors, rationale, views, architectural diagrams, team implictions, evolution, size considerations), theory underlying softeware architecture, extraction of architecture from implementation artifacts, architecture of web-based and enterprise systems, clustering of subsystems, visualization approaches to software architecture, comprehension and cognitive aspects of software architecture.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 755', 'System and Network Architectures and Implementation', 0.5, 'LEC', 'Distributed, multi-user applications are designed and implemented using many underlying technologies that must be coordinated to provide important features such as robustness, scalability, manageability, ubiquitous access, privacy, security, authentication, and role-based access control, to name only a few.  The network supporting the application may be crucial to its successful implementation.  The application logic itself is likely implemented in a number of languages and programming environments.  Students will be provided with an advanced overview of current networking and distributed systems topics, and will apply it to case studies drawn form consumer internet applications, enterprise systems, and medical and healthcare systems.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 758', 'Cryptography/Network Security', 0.5, 'LEC', 'Cryptographic protocols and their application to secure communication, especially in a network setting. Identification and entity authentication; protocols for key establishment, transport, agreement and maintenance; secret sharing, broadcast encryption, tracing schemes; certificates, public-key infrastructure, PGP.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 761', 'Randomized Algorithms', 0.5, 'LEC', 'Introduction to the design and analysis of algorithms that make use of randomization. Topics include review of basic probabiloity and introduction to randomized algorithms; game theoretic techniques; uses of Markov and Chebyshev inequalities; tail inequalities; Markov chains and random walks; algebraic techniques; data structures and graph algorithms.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 762', 'Graph-Theoretic Algorithms', 0.5, 'LEC', 'Further exposure to the design, analysis and application of algorithms for problems defined on graphs. Topics include planarity testing and embedding; classes of planar graphs and fast algorithms for them; trees and tree-like graphs (bounded pathwidth and treewidth);  perfect graphs and intersection graphs.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 763', 'Computational Geometry', 0.5, 'LEC', 'Introduction to the design, analysis and application of algorithms for geometric problems. Topics include convex hull algorithms in two and three dimensions; Voronoi diagrams, Delaunay triangulations, and their applications; linear programming in low dimensions; line segments, planar subdivision, and polygons; range searching.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 764', 'Computational Complexity', 0.5, 'LEC', 'Further exposure to the classification of problems based on their computational requirements and to mathematical tools designed to explore the structural consequences of such classifications. Topics include relativization, alternation, provably intractable problems, feasible parallel computation; fixed-parameter tractability and the W-hierarchy; Kolmogorov complexity, including algorithmic and algorithmic prefix complexity and their applications.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 765', 'Algorithmic Number Theory', 0.5, 'LEC', 'Fundamental problems of elementary and algebraic number theory from an algorithmic and computational complexity point of view with emphasis placed on analysis of algorithms. Topics include basic arithmetic algorithms; computation over finite fields; primality testing; algorithms for integer factorization; algorithms in number fields.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 766', 'Theory of Quantum Information', 0.5, 'LEC', 'Fundamentals of quantum information theory including states, measurements, operations, and their representations as matrices; measures of distance between quantum states and operations; quantum Shannon theory including von Neumann entropy, quantum noiseless coding, strong subadditivity of von Neuman entropy, Holevo''s Theorem, and capabilities of quantum channels; theory of entanglement including measures of entanglement, entanglement transformation, and classifications of mixed-state entanglement; other topics in quantum information as time permits.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 767', 'Advanced Logic for Computer Science', 0.5, 'LEC', 'The course provides extended background in mathematical logic and its applications to various branches of computer science.  It covers some fundamental concepts such as soundness and completeness theorems, compactness, the expressive power of a logic and the computational complexity of its basic decision problems.  These concepts are being demonstrated on first order logic and modal logics.  Finally the course discusses examples of applications of these concepts and tools to formal reasoning about programs and about hardware, to data bases, and to knowledge representation.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 768', 'Quantum Information Processing', 0.5, 'LEC', 'Review of basics of quantum information and computational complexity; Simple quantum algorithms; Quantum Fourier transform and Shor factoring algorithm: Amplitude amplification, Grover search algorithm and its optimality; Completely positive trace-preserving maps and Kraus representation; Non-locality and communication complexity; Physical realizations of quantum computation: requirements and examples; Quantum error-correction, including CSS codes, and elements of fault-tolerant computation; Quantum cryptography; Security proofs of quantum key distribution protocols; Quantum proof systems.  Familiarity with theoretical computer science or quantum mechanics will also be an asset, though most students will not be familiar with both.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 770', 'Numerical Analysis', 0.5, 'LEC', 'Introduction to basic algorithms and techniques for numerical computing.  Error analysis, interpolation (including splines), numerical differentiation and integration, numerical linear algebra (including methods for linear systems, eigenvalue problems, and the singular value decomposition), root finding for nonlinear equations and systems, numerical ordinary differential equations, and approximation methods (including least squares, orthogonal polynomials, and Fourier transforms).', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 774', 'Advanced Computational Finance', 0.5, 'LEC', 'Option pricing, hedging, model calibration, and portfolio optimization will be discussed.  Computational methods, including PDE methods, Monte Carlo, and mathematical programming will be presented.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 775', 'Parallel Algorithm in Scientific Computing', 0.5, 'LEC', 'Techniques for obtaining maximum parallelism in various numberical algorithms, especially those occurring when solving matrix problems and partial differential equations, and the subsequent mapping onto the computer. Topics include: parallel architecture and performance models; message passing/shared memory programming; matrix computations; fast Fourier transform; graph partitioning; domain decomposition methods.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 778', 'Numerical Solution of Partial Differential Equations', 0.5, 'LEC', 'Discretization methods for partial differential equations, including finite difference, finite volume and finite element methods. Application to elliptic, hyperbolic and parabolic equations. Convergence and stability issues, properties of discrete equations, and treatment of non-linearities. Stiffness matrix assembly and use of sparse matric software. Students should have completed a course in numerical computation at the undergraduate level.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 779', 'Splines and Their Use in Computer Graphics', 0.5, 'LEC', 'Spline theory and recent developments in techniques for representing, manipulating and rendering curves and surfaces constructed from splines in a graphic environment. Applications of interest include computer-aided design, synthetic image generation and animation.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 780', 'Advanced Symbolic Computation', 0.5, 'LEC', 'A deep investigation into fundamental problems of symbolic computation. These may include algorithms for linear, non-linear, and differential systems of symbolic equations, symbolic integration, factoring polynomials, and symbolic-numeric algorithms. Covers the basic data types and structures for algebraic objects and operations. Issues in the design of computer algebra systems.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 781', 'Colour in Computer Graphics', 0.5, 'LEC', 'In computer graphics, 3D models are rendered through an ideal camera to a 2D image. The virtual camera is a powerful tool, controlling not only perspective and the optical path, but colour mapping as well. This course examines the notion of "colour" at different stages of the computer graphics pipeline, and describes techniques for modelling and managing colour through the virtual camera analogy. Topics include illumination, the virtual camera, colour spaces, gamut mapping and colour management. Implementation of the colour reproduction and gamut-mapping algorithms demands a number of numerical and statistical methods, including multidimensional interpolation and approximation, computational geometry, splines, principal components anlysis and optimization.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 782', 'Pattern Discovery in Biomolecular Data', 0.5, 'LEC', 'This course examines, from an algorithmic point of view, the pattern discovery techniques that are currently used to extract the functional knowledge hidden in biomolecular data that is derived from DNA, RNA, proteins and their reaction products. Topics include: DNA sequence analysis, RNA structure prediction techniques, protein motif discovery techniques, protein structure prediction, analysis of expression data.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 783', 'Computer Modeling of Biophysical Phenomena', 0.5, 'LEC', 'This course addresses the computer (in silico) simulation of biophysical processes involved in the interaction of light with organic and inorganic matter.  Computer models used to simulate these processes are presented, and key stages of the simulation pipeline, such as data gathering, design constraints and evaluation methodologies, are examined in detail.  This course also includes discussions of open problems and current trends in the computer simulation of biophysical processes.  Application fields include, but are not limited to computer graphics, remote sensing, biology and biomedical optics.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 784', 'Computational Linguistics', 0.5, 'LEC', 'Computer understanding and generation of natural (i.e., human) languages. Basic topics in natural language understanding (syntax, semantics, pragmatics, connected discourse). Selected applications (e.g., automated language generation, machine translation, natural language processing and the Web).', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 785', 'Intelligent Computer Interfaces', 0.5, 'LEC', 'Intelligence in interfaces-natural language processing, plan recognition, dialogue, generation, user modeling. Interfaces to intelligent systems-intelligent agents and multi-agent systems, information processing and data mining, knowledge-based systems.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 786', 'Probabilistic Inference and Machine Learning', 0.5, 'LEC', 'Covers the fundamental principles of probabilistic inference and computational learning systems. Topics include Bayes decision and utility theory, Monte Carlo and Markov chain Monte Carlo methods; learning with complete data; Bayesian networks, Markov random fields and factor graphs; models; learning with incomplete data; computational learning and PAC learning theory.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 787', 'Computational Vision', 0.5, 'LEC, TUT', 'Fundamental problems in computational vision where efficient and robust algorithms can be applied. Topics include image formation; linear systems and Fourier theory; image registration; feature detection; fitting models to data; optical flow; structure from motion; steriopsis; object recognition; high-level vision.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 788', 'High-Performance Image Synthesis', 0.5, 'LEC', 'Project-oriented course that covers high-performance image synthesis using techniques for both real-time interactive systems and offline physically-based rendering. Topics include numerical techniques; visual perception and light; mathematical models of rendering; global illumination algorithms; real-time rendering.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 789', 'User Interface Tools', 0.5, 'LEC', 'Graphics input devices and interaction techniques. The pragmatic factors of various physical, logical and virtual devices, human factors of interactive systems, interaction dialogue managers.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 791', 'Non-Photorealistic Rendering', 0.5, 'LEC', 'Fundamentals of non-photorealistic computer depiction.  Problems of style, abstraction and spatial and temporal coherence.  Stroke-based rendering and simulation of traditional artistic media.  Stylized processing of images and video.  Real-time non-photorealistic rendering of 3D models;  toon shaders and contour rendering.  Geometric art and ornamental design.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 792', 'Data Structures and Standards in Health Informatics', 0.5, 'LEC', 'This course focuses on health data as a key component of all health informatics systems.  Topics include ontologies and other classification taxonomies found in health systems, data standards (with a focus on Canadian implementations of international standards), privacy and security of health data, client/patient assessment tools, and ethical considerations.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 793', 'Health Informatics  - Applications Domains', 0.5, 'LEC', 'System architectures, interoperability, networking, and medical data storage are explored in the context of health information system applications such as digital image acquisition systems, telemedicine, laboratory automation, physician order entry systems, patient safety systems, administrative systems, and various forms of the electronic patient record.  Issues related to usability and human computer interaction feature prominently in this course.', 'CS');
