INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 629', 'Computer Arithmetic', 0.5, 'LEC', 'General class of fixed-radix number systems. SD numbers. Floating-point representation. Algorithms and architectures for sequential and fast computation of multiplication, division and square root extraction. Elementary Functions. Logarithmic and residue number systems. Finite field arithmetic operations. Error control in arithmetic processors.', 'ECE');
