INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CT 601', 'The Books of Church', 0.5, 'SEM', 'This course will examine the historical development of the Bible, the different types of writing found in the Bible, and the methods used to interpret the meaning of the scriptures.', 'CT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CT 602', 'The History of Catholicism', 0.5, 'SEM', 'Theis course will survey the development of Christianity from the time of Christ to the present, with a particular emphasis on the Roman Catholic tradition.', 'CT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CT 603', 'Foundations of Theology', 0.5, 'SEM', 'An examination of the fundamental aspects and categories of theological reflection including:  the nature of faith, religious and theological language, the mystery of God, the meaning of the Incarnation, creation and redemption, the human person, the nature and mission of the church, the role of Mary.', 'CT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CT 604', 'Catholic Moral Life and Thought', 0.5, 'SEM', 'This course will explore the field of ethics in the context of Catholic Christian faith.  It will examine fundamental concepts in the Catholic moral tradition and look at the significance of those concepts when dealing with contemporary ethical issues.', 'CT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CT 605', 'The Prayer of Church: Spirituality and Liturgy', 0.5, 'SEM', 'This course will deal with the essential aspects of Christian spiriturality and Catholic liturgical life.  It will examine these aspects from biblical, historical and theological perspectives and explore their role in contemporary Catholicism.', 'CT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CT 606A', 'Catholic Thought Research Paper or Project - Part I', 0.5, 'SEM', 'None', 'CT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CT 606B', 'Catholic Thought Research Paper or Project - Part II', 0.5, 'SEM', 'None', 'CT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CT 610', 'Catholic Sacramental Life', 0.5, 'SEM', 'This course provides biblical, historical, theological and pastoral perspectives on the scaramental life of the Catholic church.', 'CT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CT 611', 'Catholic Perspectives on Ecology', 0.5, 'SEM', 'An examination of the ways in which Roman Catholic thinkers and communities are involved in the theology and ecology discourse.  Historical background, recent debates, church teachings and contemporary approaches to the ethical issues will be explored.', 'CT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CT 612', 'Special Topics in Catholic Theology', 0.5, 'SEM', 'In-depth examination of select topics in Catholic theology.  Consult the MCT website or Graduate Studies calendar for current offerings.  Examples include topics such as:  The Papacy; Ecumenism and Interfaith Dialogue; the Bible and Peace.', 'CT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CT 613', 'The Catholic Imagination in Art and Literature', 0.5, 'SEM', 'An exploration of the role and significance of the Catholic imagination in a variety of art forms and literature.', 'CT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CT 614', 'Catholicism and Education', 0.5, 'SEM', 'This course provides an historical perspective on Catholic education in Canada and examines contemporary issues regarding the role of Catholicism in education.', 'CT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CT 615', 'Catholic Social Ethics', 0.5, 'SEM', 'A study of the basic principlies of Catholic social teaching through an examination of church documents and the rise of social movements.  Exploration of contemporary applications of the basic principles to issues such as poverty, immigration, the market economy, women, agriculture, ecology, work.', 'CT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CT 616', 'Gender Ethics in Roman Catholicism', 0.5, 'SEM', 'This course examines historical and contemporary Roman Catholic approaches to ethical questions about human sexuality and relationships.  Topics include: marriage, celibacy, single life, homosexuality, HIV/AIDS, sexual violence, gender roles, abortion, and sexuality and spirituality.', 'CT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CT 617', 'Contemporary Bioethics: Issues of Life and Death', 0.5, 'SEM', 'This course examines the relevance of Catholic moral principles to discussions of contemporary issues in bioeithics.  Topics include:  reproductive technologies, cloning and stem cell research, genetics, resource allocition, abortion, death and dying.', 'CT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CT 618', 'The Catholic Church in Canada', 0.5, 'SEM', 'An examination of the history of the Catholic Church in English and French-speaking Canada.  The role, significance and contributions of the Catholic Church to Canadian society will be explored.', 'CT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DAC 201', 'Designing Digital Images and Interaction', 0.5, 'LEC', 'This course draws on multiple theoretical perspectives to introduce students to the fundamental principles of multi-modal communication design in its social context. Students will analyze, design, and produce images and interactivity for use in a variety of digital platforms, including e-learning and business applications.', 'DAC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DAC 202', 'Designing Digital Video', 0.5, 'LEC', 'This course introduces students to the principles of designing time-based multi-modal communication in a social context. Students will analyse, design, and produce video for use in a variety of digital platforms, including e-learning and business applications.', 'DAC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DAC 300', 'Special Topics in Digital Design', 0.5, 'LEC', 'In this course students will learn advanced digital design theory. They will participate in workshops with professional designers, develop specialized digital materials, and contribute signature work to their digital portfolio.', 'DAC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DAC 301', 'Designing with Digital Sound', 0.5, 'LEC', 'In this course, students will be introduced to sound analysis and production. Students will learn to record, edit, and implement sound in a variety of linear and non-linear media forms, with emphasis on film and video games.', 'DAC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DAC 302', 'Digital Storytelling', 0.5, 'LEC', 'This course teaches students the principles and practices of telling stories using Machinima: 2D and 3D computer game engines used to design and produce short movies. Students will learn to develop and present story ideas, create storyboards, write short scripts, and then render their narratives using a variety of digital techniques and tools.', 'DAC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DAC 305', 'Design and User Experience of Interactive Games', 0.5, 'LEC', 'This course introduces fundamental design skills for two-dimensional interactive video games and provides the theoretical knowledge of the emotional and cognitive effects of video game play. Students will understand the importance of user experience assessment for video games as part of iterative design and development. Students will learn to theorize, design, prototype, and test digital games with a special focus on exploring the impact that video games have on human experience.', 'DAC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DAC 307', 'Digital Display Systems', 0.5, 'LEC', 'This course focuses on the creative, technical, and artistic challenges inherent to creating visual content (film, photos, graphics) for the increasingly ubiquitous out-of-home digital displays. The size, configuration, placement, and functions of these public digital displays require new artistic and technical techniques to fully exploit their capabilities. In the course students will explore the design affordances of several types of public digital display as well as produce dynamic, interactive content for them.', 'DAC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DAC 308', 'Cinematic Art and Practice', 0.5, 'LEC', 'This course explores cinematic representation from an audio-visual production perspective. Students will learn an explicit and replicable theoretical vocabulary for analyzing cinematic technique, as well as engage in practice by creating videographic essays that analyze, interrogate, and extend forms of cinematic representation. Students will engage in production workshops and have access to video and audio editing software.', 'DAC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DAC 309', 'User Experience Design', 0.5, 'LEC', 'This course introduces students to the theory and practice of user experience design as it applies to the design of interactive digital media. Topics will include user-centred design, personas, iterative testing, prototyping, information gathering interviews, and contextual inquiry.', 'DAC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DAC 329', 'Digital Presentations', 0.5, 'LEC', 'In this course students will be introduced to design and production of digital business presentations. They will develop specialized digital materials and contribute work to their digital portfolio.', 'DAC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DAC 403', 'Special Topics in Speech Communication and Technology', 0.5, 'LEC', 'In this course students will learn advanced digital design theory as it applies to speech communication. They will develop specialized digital materials and contribute work to their digital portfolio.', 'DAC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DEI 612', 'Working in Teams', 0.5, 'LAB, LEC, RDG, SEM, TUT', 'This course will cover the three key areas for working in teams:  communicating, differing and facilitating.  To master the art of managing and participating in high performing, problem-solving teams, team members must be aware of both their own and others'' strengths and weaknesses when working in teams.  They must also be aware of the dynamics of teams throughout the phases of a problem-solving cycle.  Theory on teams and on team development will be introduced, as well as the particulars of working in teams on early-stage innovation projects.  The course will then focus on deepening and practising relevant skills.  It will begin with a self-assessment of personal capacities and of the distribution of skills in the team as a whole.  Following this, it will focus on deepening skills in a sequence which shadows the phase of the project on which the teams are working.  Students will be trained in the skills of reflective listening, problem identification and analysis, supportive listening and facilitation, creative brainstorming and alternatives generation, construction differing and choice, idea development and presentation, and team process evaluation.  The format of the course will combine short lecture with experiential skill-building exercises.  Coaches assigned to each team will support individual and team learning.  Students will be required to enter a "contract" relative to their team role and performance.', 'DEI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DEI 613', 'Digital Media Solutions 1: Design Principles and Practice', 0.5, 'LAB, LEC, SEM', 'In this course, students will explore the principles of digital designs in terms of their visual design components (space, line and shape, tone, colour, movement, and rhythm) and the functions they perform in various forms of digital content production (images, video and film, sound, and animation). Students will receive hands-on training in digital image, video and sound production by engaging in a complete production cycle from conceptualization, through to production and post-production. To situate their practices within the wider social, cultural and economic contexts of digital design, students will also learn an explicit theoretical vocabulary for exploring, explaining, critiquing, and evaluating their design practices.', 'DEI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DEI 614', 'Principles of Marketing in a Globalized World: Leveraging Digital Technology', 0.5, 'LAB, LEC, SEM', 'The objectives of this course are to provide students with a firm understanding of market strategy and the techniques used for marketing in a globalized context. Over the past decade, marketing has embraced a broad set of online tools that have revolutionized and revitalized an approach to the marketplace, including blogs, online news releases, social media, streaming video, and viral marketing strategies, among others. This course aims to instruct students in two primary, fundamental areas of marketing: (1) basic principles of marketing, which includes market segmentation strategies, the use of marketing channels, how to develop promotion and pricing strategies, and how to develop competitive advantage, and (2) how to leverage technology-driven, digital media for achieving marketing goals. Students will learn through various examples of marketing problems, and they will learn to apply these principles within the context of a problem-oriented approach. A secondary but also important set of learning outcomes of the course is an understanding of the rewards of global marketing, social responsibility, and the benefits of a global vision in setting business goals.', 'DEI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DEI 615', 'New Perspectives: Media History and Analysis', 0.5, 'LEC, SEM', 'In this course, students will first be introduced to key moments in the building of networks, where knowledge and information are interlinked on a large scale, such as the printing press of Renaissance Europe, early cinema and photography at the end of the 19th and the beginning of the 20th century, and the internet, social media, and digital imagery of the 21st century. Secondly, students will receive training in the critical analysis of these media through the use of conceptual tools such as semiotics, cultural criticism, discourse analysis, visual anthropology and others.  The objectives of this course are twofold:  
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DEI 622', 'Working in Teams 2', 0.5, 'LAB, LEC, RDG, SEM, TUT', 'This course is a continuation of Working in Teams 1.', 'DEI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DEI 623', 'Digital Media Solutions 2: Project Management', 0.5, 'LAB, LEC, RDG, SEM, TUT', 'This course will focus on managing production processes and resources for digital media projects, from initial planning and budgeting through the management of milestones, tasks, personnel, technical resources and delivery.  Students will receive hands-on training in initiating and tracking individual tasks within the overall structure of the project, learning how to adjust for change in  project needs and available resources.  This practical knowledge will combine an ongoing understanding of project management concepts and techniques with the use of project management software.', 'DEI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DEI 624', 'Understanding the Consumer Universe: Market Research in Digital Media', 0.5, 'LAB, LEC, SEM', 'Understanding the consumer universe in ways that can lead to business success is a vital goal of market research, and a core component of business anthropology.  This course instructs students in the key methods of exploratory and confirmatory research for understanding consumer behaviour.  Students are instructed in the qualitative methods of market ethnography, where they learn how to develop the tools to recognize business opportunity through interviews, focus groups, and personal observation.  Students are also instructed in the quantitative methods of market survey, where they learn how to measure the potential of business opportunity through sampling with consumer polls, questionnaires, and combined research strategies.  Students will gain an understanding of the "do''s" and "don''ts" of market research, particularly in terms of how gaining insight in consumer needs and behaviours can influence important business decisions.', 'DEI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DEI 625', 'Media Innovation and Impact', 0.5, 'LEC, SEM', 'Changing media have a profound impact on our economy, governance, and community life.  Our institutions, our approaches to economic and cultural development, and traditional business models are challenged to adapt to the realities of an information-rich, technology-enabled society.  This course will provide a survey of the broad implications of evolving technology through research and case studies focusing on personal, institutional, and political choices.  Students will learn about the policy and social context (regulatory, copyright, privacy, industrial incentives) of innovation in an anthropological, globalized context, and will explore in depth varying social responses to innovation, from outright rejection, to benign neglect, to viral adoption.  Drawing on recent examples, students will address practical issues of business and institutional transformation as the digital world emerges.', 'DEI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DEI 626', 'User Experience (UX) Fundamentals and User Research (UER)', 0.5, 'LAB, SEM', 'This course will cover he fundamentals of UX design and provide the students with insights into the "real-life" processes, challenges, considerations, tools, teams, models, etc. of a user evaluating, implementing and measuring of UX.', 'DEI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EARTH 10', 'Enhancing Communications Skills for Earth Sciences Students', 0, 'SEM', 'The course develops communication skills to prepare Earth students for assignments, group work, oral presentations, and eventual thesis writing. The course combines Earth Science content and approaches to problem solving with specific learning objectives for written and spoken communication. Activities include a field trip and a tour of department laboratories.', 'EARTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EARTH 336', 'Evolution 2: Fossil Record', 0.5, 'LEC, TUT', 'An overview of the origin of life, how it evolved and diversified, modern principles of paleontology with particular emphasis on analytical tools to interpret ancient life forms. Patterns, processes of evolution and paleoecology of key groups of microfossils, invertebrates, plants, vertebrates and their trace fossils. [Offered: W]', 'EARTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DEI 631', 'Projects', 1.5, 'LAB, LEC, RDG, SEM, TUT', 'This final project course will serve as the culmination of work started in Projects 1 and 2.  Here, student teams will work intensively on their project, applying lessons learned during the other courses and working closely with industry consultants and coaches to bring their project to the stages of delivery and implementation. Students will be given an opportunity to publicly showcase their work at the end of the term.', 'DEI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DM 700', 'Numerical Simulation of Sheet Metal Forming', 0.5, 'LEC', 'This course will cover numerical simulation techniques used to accurately and efficiently simulate industrial sheet forming processes. A variety of finite element based techniques will be considered including explicit and so-called single and multi-step methods.
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DM 701', 'Metal Forming', 0.5, 'LEC', 'Course starts with a brief introduction to the theory of plasticity, directly applicable to the mathematical and physical analysis of problems in metal forming. A discussion of tribology and material attributes follows. Metallurgical phenomena, accompanying hot and cold forming of metals, are discussed. Application to bulk metal forming (rolling, extrusion, drawing and forging) and sheet metal forming (deep drawing, stretch forming, hydroforming) are considered. The use and limitations are demonstrated. Examples from laboratory experiments and from industrial applications are used to complement the presentation.', 'DM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DM 702', 'Surface Machining', 0.5, 'LEC', 'Surface Machining represents a major activity in many industries, like in the manufacture of tools and dies. This course addresses the issues of surface representation, and generation of the tool path on CNC milling machines for roughing and finishing of flat and sculptured surfaces.
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DM 712', 'Automation and Intelligent Manufacturing', 0.5, 'LEC', 'Robotics:
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DM 713', 'Life Extension', 0.5, 'LEC', 'Plant Life Management, including configuration management; time dependent deterioration in plant and assessment of failure probabilities; risk analysis which considers probability of failure and consequences of failure; risk based in-service inspection and various other inputs for safety cases that should (must) be prepared when considering life extension.', 'DM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DM 714', 'Industrial Noise Analysis and Control', 0.5, 'LEC', 'Government regulations are becoming tighter regarding noise levels at industrial installations. This course is aimed at acquiring the necessary design tools to implement different measures for reducing noise generated from sources encountered in typical manufacturing environments.
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DM 720', 'Design: Materials Selection', 0.5, 'LEC', 'The purpose of this course is to make sound, intelligent selections of materials for safe and efficient structures or components. A unique feature of the course is that it contains the methodology of design and how materials selection should be simultaneously chosen at every stage of the design process - concurrent engineering. The materials selection should be effected through consideration not only of their properties, their processing, and their fabrication, but also their recyclability, recovery, and disposal after their use, involving the concept of life-cycle analysis.
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DM 722', 'Mechatronics Engineering', 0.5, 'LEC', 'Mechatronics is the integration of mechnaical, electrical, computer and control engineering. This course deals with the analytical tools required to design, model, analyze and control mechatronic systems. Properties of linear and nonlinear systems, system identification methods, process modelling, sensor and actuators, computer interfacing, computer control of machines and processes (PLC and PC based). Laboratories will include PLC based automation applications and PC based advanced robotics.', 'DM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DM 723', 'Sensors, Actuators & Interfacing', 0.5, 'LEC', 'This course presents the theory and design implementation of several types of sensors and actuators.  Sensors discussed include solid-state optical sensors, temperature sensors, velocity sensors, piezoelectric sensors and accelerometers, strain and force sensors, analogue and digital position sensors, and flow sensors, and magnetometers and Hall effect sensors.  Theory and modeling of several common actuators including different electric motors, hydraulic and pneumatic motors and cylinders, as well as piezoelectric and magnetostrictive actuators are presented.  Component integration, design considerations, and interfacing are studied through examples selected from applications of machine tools, mechatronics, robotics, aerospace systems, and ground vehicles.  Four laboratory projects in robotics, vision, pneumatics, and hydraulic systems reinforce understanding of the topics.', 'DM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DM 730', 'Welding', 0.5, 'LEC', 'Features and advantages of the various welding processes. Welding arc characteristics. Temperature distributions around welds. Metallurgy of the weld metal and heat affected zone in various alloys, including carbon and stainless steels, and aluminum alloys. Origin of various weld defects and methods of detecting them. Static and dynamic design of welded joints. Residual stresses, distortion and fracture of welds.', 'DM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DM 766', 'Strategic Management of Technology', 0.5, 'LEC', 'This course focuses on the strategic management of technology and innovation established firms. We take an evolutionary process perspective. The fundamental ideas underlying the perspective are: (1) that a firms technology strategy emerges from its technological competencies and capabilities, (2) that the technology strategy is shaped by evolutionary external (environmental) and internal (organizational) Forces. The course draws on strategic management, economics and organization theory for analytical tools to address important challenges faced by senior and middle managers in technology based firms. The course is practice oriented. Case studies of various real life situations will require in-depth analysis to be complemented with specific action recommendations.
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DM 782', 'Integrating Product Development and Manufacturing', 0.5, 'LEC', 'This course covers the latest managerial tools on how to integrate the engineering design and manufacturing functions for best overall quality and shortest product development lead time. The scope of the course is multidisciplinary: we will cover both the analytical (quantitative) and qualitative aspects.
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DM 784', 'Product Development Planning and Execution', 0.5, 'LEC', 'This course deals with the effective product development project management. The scope is multidisciplinary: we will cover both the analytical (quantitative) and qualitative aspects of how to best run product development projects. It introduces concepts that a development project manager of engineering should know about. It only briefly covers how to manage the whole product design development process.
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DM 790', 'Logistics and Supply Chain Management', 0.5, 'LEC', 'Modern supply chain management encompasses the logistics of inventory and transportation flows, whether within a given organization or between that firm and other companies (suppliers, customers) that are part of its business.  This course thus deal with models and analyses of the inbound transportation of raw materials, manufactured components and sub-assemblies.  Another emphais is the (outbound) physical distribution of finished goods from factory to consumer: freight transportation (various modes, customer service, multi-location inventory management and distribution-centre site selection. Specialized topics (for term projects) may be chosen from among Logistics Information Systems; Global Supply Chain Management; Vehicle Routing; or the Logistics of e-Commerce.', 'DM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DM 791', 'Management of Quality', 0.5, 'LEC', 'This course provides an introduction to quality management by examining the philosophies, practices, tools, and decisions involved in the management of quality.  An applied approach is used in this study.  The course begins with a general overview of quality, including the various ways of defining and measuring quality and the Cost-of-quality model. This culminates in an introduction to total quality (TOM) and its three conrnerstones: continuous improvement, customer focus, and total organizational involvement.  The remainder of the course examines these cornerstones in greater detail.  The objectives of the course are: (1) to develop an appreciation for importance of managing the quality at all levels, and stages of the organization, (2) to introduce techniques for defining, monitoring, and improving quality, and (3) to encourage critical thinking about issues in managing quality, within the organization, and in society in general.', 'DM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DRAMA 101A', 'Introduction to the Theatre 1', 0.5, 'LEC', 'Introductory study of the theatre as a major art form. Selected plays as produced in their historical contexts. Contributions of the actor, designer and technician to theatrical production.', 'DRAMA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DRAMA 101B', 'Introduction to the Theatre 2', 0.5, 'LEC', 'An extension of the studies described in 101A.', 'DRAMA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DRAMA 102', 'Introduction to Performance', 0.5, 'WSP', 'This workshop course introduces students to the creative processes of performance in a range of formal and informal settings. Emphasis is placed on the student''s development as a performer. Prior performance experience is not required.', 'DRAMA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DRAMA 200', 'Theatre and Performance in Context', 0.5, 'LEC', 'Students experience, analyze, and interpret a range of works of theatre and performance. Close attention is paid to the original circumstances in which these works were produced, the ways in which they have reflected and influenced the communities that produced them, and their relevance to current performance practice and research.', 'DRAMA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DRAMA 220', 'Performance Studies', 0.5, 'WSP', 'This workshop course in performance studies explores performance as a way of knowing. It investigates performance as artistic practice and as a means of understanding historical, social and cultural practices, including drama/theatre texts, poetry, narratives and texts of everyday life.', 'DRAMA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DRAMA 221', 'Performing Text', 0.5, 'WSP', 'Students explore techniques to analyze, interpret, and perform texts.', 'DRAMA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DRAMA 222', 'Performing the Body', 0.5, 'WSP', 'Students explore techniques to access and develop the body as a resource for performance.', 'DRAMA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DRAMA 243', 'Technical Production 1', 0.5, 'LAB, LEC', 'Students develop basic proficiency in the technical elements of live theatre and performance production, including carpentry, lighting, projection, sound, wardrobe, and properties. Students apply these concepts in a departmental production.', 'DRAMA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DRAMA 244', 'Technical Production 2', 0.5, 'LAB', 'Students develop intermediate proficiency in the technical elements of live theatre and performance production, including carpentry, lighting, projection, sound, wardrobe, and properties. Students apply these concepts in a departmental production.', 'DRAMA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DRAMA 301', 'Performance Creation', 0.5, 'WSP', 'Students study, apply, and critique a range of processes for making theatre and performance, paying close attention to the relationship between the approach to creation and the significance of works in the communities where they are performed.', 'DRAMA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DRAMA 306', 'Production Participation 3', 0.5, 'WSP', 'Students work at an intermediate level with faculty, staff, and visiting artists to explore and increase proficiency in specific areas of performance creation. Areas include research, conception, writing, direction, design, performance, production, stage management, production management, publicity, dramaturgy, critique, and documentation.', 'DRAMA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DRAMA 307', 'Production Participation 4', 0.5, 'WSP', 'Students work at an intermediate level with faculty, staff, and visiting artists to explore and increase proficiency in specific areas of performance creation. Areas include research, conception, writing, direction, design, performance, production, stage management, production management, publicity, dramaturgy, critique, and documentation.', 'DRAMA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DRAMA 311', 'English Drama to 1642', 0.5, 'LEC', 'The Middle Ages, the Elizabethans and Jacobeans (excluding Shakespeare), and the Spanish Golden Age.', 'DRAMA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DRAMA 314', 'Survey of Dramatic Literature and Theory 5', 0.5, 'LEC', 'The first part of the 20th century.', 'DRAMA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DRAMA 315', 'Survey of Dramatic Literature and Theory 6', 0.5, 'LEC', 'The second part of the 20th century.', 'DRAMA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DRAMA 318', 'Musical Theatre and Musical Film', 0.5, 'LEC', 'The course explores the elements that are unique to the musical, and the translation of this essentially artificial art form into theatrical and cinematic versions. It will examine in particular the distinctions between musicals based on stage productions and musicals devised exclusively for film.', 'DRAMA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DRAMA 319B', 'Tennessee Williams in Performance', 0.5, 'LEC', 'This course focuses on a major dramatist. It will consider first of all the times, the life and the work. It will then concentrate on productions of plays in various media, and include interpretations, design styles, critical reception and related topics.', 'DRAMA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DRAMA 319E', 'Beckett in Performance', 0.5, 'SEM', 'This course focuses on the various ways in which the dramatic works of Samuel Beckett have been rendered in performance.', 'DRAMA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DRAMA 321', 'Approaches to Acting with Text', 0.5, 'WSP', 'Students apply conceptual and theoretical approaches to acting in traditions emphasizing the text as a resource for performance.', 'DRAMA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DRAMA 322', 'Approaches to Acting with the Body', 0.5, 'WSP', 'Students apply conceptual and theoretical approaches to performance in traditions emphasizing the body as a resource for performance.', 'DRAMA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DRAMA 326', 'Performing the Voice', 0.5, 'WSP', 'Students explore techniques to access and develop the voice as a resource for performance.', 'DRAMA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DRAMA 331', 'Design Theory and Practice', 0.5, 'WSP', 'Students apply conceptual and theoretical approaches to design in the areas of set/space, light, projection, and costume.', 'DRAMA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DRAMA 333', 'Costume Design', 0.5, 'WSP', 'This course examines the art form and practical craft of costume design for the theatre as it is practiced today. All aspects of the design and construction of stage costuming are addressed, with emphasis on text analysis, capturing a period look, fabric choice and methods of costume construction, and rendering approaches and techniques.', 'DRAMA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DRAMA 335', 'History of Costume', 0.5, 'LEC', 'This course surveys the development of costume, focusing primarily on fashionable clothing in Western societies from the Renaissance to today. It examines the influence of art and design movements, social roles and trends, and manufacturing and marketing methods on the changing fashionable style image of men and women. It includes the role of the fashion designer as well as theatrical and film costume design.', 'DRAMA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DRAMA 341', 'Lighting Design for the Theatre 1', 0.5, 'LAB', 'An introduction to the theory and practice of theatre lighting design through studio experience.', 'DRAMA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DRAMA 343', 'Stage Management', 0.5, 'SEM', 'Students explore approaches to stage management for theatre and performance.', 'DRAMA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DRAMA 366', 'Writing for Performance', 0.5, 'WSP', 'Students explore techniques for analyzing, interpreting, and creating texts for theatre and performance.', 'DRAMA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DRAMA 351', 'Central and East European Film', 0.5, 'LAB, SEM', 'Examination of the development of the motion picture art in Central and Eastern Europe after World War II. Selected work of prominent directors of the Czech Republic, Hungary, Poland, the former USSR, and former Yugoslavia will be discussed (Chytilova, Forman, Jancso, Makavejev, Tarkovsky, Wajda, and others). Film screenings.', 'DRAMA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DRAMA 359', 'Film and Television 1', 0.5, 'LAB, SEM', 'Examination of principles of the audiovisual language and the main structural elements of the cinematic work. Discussion of the relationship between film, television and other arts/media. Film screenings.', 'DRAMA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DRAMA 360', 'Film and Television 2', 0.5, 'LAB, SEM', 'Development of critical judgment and expression in the area of film and television. Investigation of the role of motion pictures and TV in society. Review of major theories (Eisenstein, Bazin, Metz, Kracauer, Esslin). Film screenings.', 'DRAMA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DRAMA 361', 'Approaches to Directing', 0.5, 'WSP', 'Exploration of the director''s task in its practical, theoretical and historical aspects.', 'DRAMA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DRAMA 362', 'Directing 2', 0.5, 'LEC', 'Students will be expected to form their own production company, mount a short play, and submit a detailed promptbook.', 'DRAMA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DRAMA 363', 'Stage Combat', 0.5, 'WSP', 'The basics of physical contact for the stage, with a strong emphasis on safety considerations. Hand-to-hand combat and work with a variety of weapons including foils are covered. In addition, aspects of fight choreography are explored, as well as falls and pratfalls.', 'DRAMA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DRAMA 371', 'Theatre History 1', 0.5, 'LEC', 'Theatre history from Classical Greece to the Renaissance.', 'DRAMA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DRAMA 372', 'Theatre History 2', 0.5, 'LEC', 'Theatre history from the Classical French and English Restoration periods to the present era.', 'DRAMA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DRAMA 380', 'Canadian Drama', 0.5, 'LEC', 'This course explores traditions and experiments in Canadian drama through an analysis of Canadian plays, especially those from 1960 to the present, in their historical and theatrical contexts.', 'DRAMA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DRAMA 381', 'Russian Drama before 1905', 0.5, 'LEC', 'A study of the origins and development of Russian drama up to 1905. Reading and critical analysis of major works in various genres with emphasis on authors of the 19th century.', 'DRAMA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DRAMA 382', 'Russian Drama after 1905', 0.5, 'LEC', 'A study of the origins and development of Russian drama after 1905. Reading and critical analysis of major works in various genres with emphasis on authors of the 20th century.', 'DRAMA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DRAMA 386', 'Shakespeare 1', 0.5, 'LEC', 'A study of the plays written before 1599-1600, excluding Julius Caesar.', 'DRAMA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DRAMA 387', 'Shakespeare 2', 0.5, 'LEC', 'A study of the plays written after 1599-1600, including Julius Caesar.', 'DRAMA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DRAMA 392', 'American Film', 0.5, 'LEC', 'American Film will examine the relationship between film and the social/political movements of each decade since 1930. In this way, the course will address the medium as both chronicler of history and agent for change and/or conformity. At the same time, attention will be paid to the nature of film, its technical development and the changing approaches to acting in American films that is a direct result of the development in theatre of a specific and distinctive American acting style.', 'DRAMA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DRAMA 394', 'The New Hollywood', 0.5, 'LEC', 'The course examines the impact of European New Wave films of the late 1950s and early 1960s on American filmmaking, focusing on the revolutionary changes evident during the later 1960s and the 1970s. The course considers the work of filmmakers such as Bogdanovich, Cimino, Coppola, Peckinpah, Penn, Scorsese and others.', 'DRAMA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DRAMA 396', 'Film Noir', 0.5, 'LEC', 'The principal focus will be on the American "noir" films between 1940-55, the period during which the genre itself was defined and developed. Beyond the style and the techniques of this unique world of film, the parallels between cinema noir and America''s social and political pressures will be examined. The course will include the neo-Noir school, the filmmakers who ''borrowed'' from the originators by re-applying the basics to the changing times in the 1970''s and beyond.', 'DRAMA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DRAMA 397', 'Women and Film', 0.5, 'LAB, SEM', 'The study of selected film texts is informed by contemporary critical readings in feminist and film theory. Subjects addressed may include representation, fetishism and the gaze, female spectatorship, women''s genres (e.g., melodrama, romance), female stereotypes (e.g., the femme fatale) and women''s documentary film.', 'DRAMA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DRAMA 401', 'Acting Styles', 0.5, 'SEM', 'Examines American and British acting styles from 1945 - 1965 through a study of representative films. The evolution of contemporary techniques and styles is considered, first by exploring the dominant methods in each country from the mid-century, and then by observing an increasing similarity between the two.', 'DRAMA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DRAMA 402', 'Political Theatre', 0.5, 'SEM', 'Considers the politics of governing, religion, family, marriage, work, race, gender and sexual orientation as they are expressed and commented on in a variety of contemporary, international play texts. Class discussion focuses on both the texts and the society that gave rise to each playwright''s passions.', 'DRAMA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DRAMA 403', 'Theories of the Modern Theatre', 0.5, 'SEM', 'An examination of the writings of selected theorists and practitioners of the modern theatre in terms of their contrasting ideas on the kind of expression and communication possible through the medium of theatre. Their works will be studied in relation to each other and to concurrent social, political, and aesthetic developments.', 'DRAMA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DRAMA 404', 'Genre', 0.5, 'SEM', 'A study of the various dramatic genres and sub-genres in terms of their distinguishing characteristics. Selected plays from various periods in theatre history will be examined on the basis of some of the most significant theoretical writings in the field. The usefulness of genre distinctions will be tested against plays/performances which appear to transcend them.', 'DRAMA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DRAMA 405', 'Theatre and the New Media', 0.5, 'LEC, SEM', 'The primary objective of this course is to investigate ways in which new media technologies have been, and can be, applied in theatrical practice. It examines the range of opportunities and challenges this synthesis poses and provides students with some of the insights and skills required to apply new media technologies in a theatrical context. On the basis of such practical and theoretical study, students will also engage in projects which explore creatively the coalition between theatre and the new media.', 'DRAMA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DRAMA 406', 'Production Participation 7', 0.5, 'WSP', 'Students work at an advanced, independent level with faculty, staff, and visiting artists to create and reflect upon a major, collaborative performance project. Students engage in all areas of project creation, including research, conception, writing, direction, design, performance, production, stage management, production management, publicity, dramaturgy, critique, and documentation.', 'DRAMA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DRAMA 407', 'Production Participation 8', 0.5, 'WSP', 'Students work at an intermediate level with faculty, staff, and visiting artists to explore and increase proficiency in specific areas of performance creation. Areas include research, conception, writing, direction, design, performance, production, stage management, production management, publicity, dramaturgy, critique, and documentation.', 'DRAMA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DRAMA 409', 'Theatre Criticism', 0.5, 'LEC', 'Study and practice of the criticism of theatre production and performance.', 'DRAMA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DRAMA 443', 'Theatre Technology and Management Apprenticeship 1', 0.5, 'LAB', 'An advanced course. Selected students are apprenticed in theatre technology or management functions in productions both on and off campus.', 'DRAMA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DRAMA 421', 'Advanced Acting Workshop 1', 0.5, 'LAB', 'An intensive workshop designed to develop performance skills. Special attention given to individual acting problems.', 'DRAMA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DRAMA 422', 'Advanced Acting Workshop 2', 0.5, 'LAB', 'An extension of DRAMA 421.', 'DRAMA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DRAMA 425', 'Audition Technique and Professional Orientation', 0.5, 'LAB', 'An intensive approach to monologue work, this course will prepare students for the audition process. Time will also be devoted to learning about the demands of the theatre profession, and the problems faced by the self-employed artist.', 'DRAMA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DRAMA 426', 'Advanced Voice Technique', 0.5, 'LAB', 'An advanced workshop course in voice for the actor and speaker designed to continue the exploration of voice technique in DRAMA/SPCOM 326.', 'DRAMA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DRAMA 440', 'Performative Inquiry and Practice', 0.5, 'LEC, SEM, WSP', 'This course explores how to create, perform and analyze performance texts, here defined as including drama/theatre texts, poetry, narratives, and the texts of everyday life. Through readings and creative investigation, students will explore the links between the participant, the researcher, the site and the impulse of inquiry.', 'DRAMA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DRAMA 490', 'Selected Seminars in Drama & Theatre Arts', 0.5, 'SEM, WSP', 'Seminars in special areas of drama and theatre.', 'DRAMA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DRAMA 491', 'Selected Seminars in Drama & Theatre Arts', 0.5, 'SEM, WSP', 'Seminars in special areas of drama and theatre.', 'DRAMA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DRAMA 499A', 'Senior Seminar', 0.5, 'LEC', 'This course is designed to give the student an opportunity to complete a comprehensive presentation in her/his major area of concentration.', 'DRAMA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DRAMA 499B', 'Senior Seminar', 0.5, 'LEC', 'This course is designed to give the student an opportunity to complete a comprehensive presentation in her/his major area of concentration. Second part of DRAMA/SPCOM 499.', 'DRAMA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DUTCH 101', 'Elementary Dutch I', 0.5, 'LAB, LEC', 'The basic elements of Dutch grammar with emphasis on oral practice and pronunciation. Introduction to aspects of Dutch culture.', 'DUTCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DUTCH 102', 'Elementary Dutch II', 0.5, 'LAB, LEC', 'A continuation of DUTCH 101.', 'DUTCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DUTCH 201', 'Intermediate Dutch I', 0.5, 'LEC', 'This course will be conducted partly in Dutch and offers advanced study in grammar, composition, and conversation. Special emphasis will be given to comprehension and practice in the spoken language.', 'DUTCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DUTCH 202', 'Intermediate Dutch II', 0.5, 'LEC', 'A continuation of DUTCH 201.', 'DUTCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EARTH 121', 'Introductory Earth Sciences', 0.5, 'LEC', 'This course explores the geological processes of the Earth''s interior and surface. These include volcanism, earthquakes, mountain building, glaciation and weathering. Students will gain an appreciation of how these processes have controlled the evolution of our planet and the role of geology in meeting society''s current and future demand for sustainable energy and mineral resources.', 'EARTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EARTH 121L', 'Introductory Earth Sciences Laboratory', 0.25, 'LAB', 'For students taking EARTH 121. Laboratory exercises on selected topics from EARTH 121 lectures. [Offered: F]', 'EARTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EARTH 122', 'Introductory Environmental Sciences', 0.5, 'LEC', 'This course presents a broad overview of earth system processes and their influence on humans. Course emphasis is placed on anthropogenic impacts on natural systems, the impacts of geologic, biologic, and atmospheric processes on humans, and the effects of human activities on the environment. Course topics include sustainable development and the availability and use of natural resources, principles of ecology and environmental science, biogeochemical cycles, climate and climate change, soils and food supply, energy systems, surface water and groundwater, waste generation and management, pollution, and catastrophic natural processes.', 'EARTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EARTH 122L', 'Introductory Environmental Sciences Laboratory', 0.25, 'LAB', 'For students taking EARTH 122. Laboratory exercises on selected topics from EARTH 122 lectures. [Offered: W]', 'EARTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EARTH 123', 'Introductory Hydrology', 0.5, 'LEC, TUT', 'Introduction to the main components of the hydrologic cycle including precipitation processes, evapotranspiration, soil water and groundwater flow, and surface water features. Emphasis will be placed on the atmospheric and geologic processes controlling water movement in the cycle at the global and local scales. Examples related to environmental impacts of urbanization and land-use management will be stressed. [Offered: F]', 'EARTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EARTH 123L', 'Field Methods in Hydrology', 0.25, 'LAB, TUT', 'This course consists of outdoor field exercises designed to provide students with hands-on experience with a variety of hydrological monitoring techniques. Emphasis will be on the practical aspects of collecting, interpreting, and reporting of groundwater, surface water, meterological, and water quality data within a watershed. Tutorials will be used to evaluate and interpret the data collected and discuss the accuracy and limitations of the different techniques.', 'EARTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EARTH 153', 'Earth Engineering', 0.5, 'LAB, LEC, TST, TUT', 'This course studies earth materials and processes from an engineering point of view through case histories and problem sets. The course develops a geological knowledge for applications to any physical environment and provides an appreciation of the impact of engineering work on the environment. Topics include mineral and rock identification, the rock cycle, structural geology and tectonics, geology of Canada, effects of water, ice and wind. Students are also introduced to the concept of geologic time, topographic and geologic maps, and the basic principles and tools used to determine geologic history. [Offered as: CIVE 153 (W), EARTH 153 (S), ENVE 153 (S), GEOE 153 (S)]', 'EARTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EARTH 221', 'Geochemistry 1', 0.5, 'LEC, TUT', 'Origin, abundance and geochemistry of elements. Introduction to stable isotope geology and radiometric dating. Basic aqueous geochemistry. [Offered: W,S]', 'EARTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EARTH 223', 'Field Methods in Hydrology', 0.5, 'LAB, LEC', 'This course consists of field exercises, lectures and laboratory experiments designed to provide students with hands-on experience with a variety of hydrological monitoring techniques. Emphasis will be on the practical aspects of collecting, interpreting, and reporting of groundwater, surface water, meteorological, and water quality data.', 'EARTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EARTH 231', 'Mineralogy', 0.5, 'LAB, LEC', 'This course provides a systematic study of the physical and chemical properties of the major rock-forming silicate minerals. Study of mineral chemistry, the internal geometry of crystalline solids and the interrelationship of crystal structure to the physical and chemical properties of minerals. Laboratory work focuses on observing and evaluating the physical properties of minerals and rocks in hand sample in order to identity them. This course includes a weekend field trip to Bancroft, Ontario.', 'EARTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EARTH 232', 'Petrography', 0.5, 'LAB, LEC', 'An introduction to optical mineralogy. Basic theory of the behaviour of light through minerals; concept of indicatrices; understanding relief, retardation, birefringence, interference figures and other optical properties of minerals. Microscopic examination of important rock forming minerals and the identification of igneous, metamorphic, and sedimentary rocks. Introduction to silicate phase equilibria; use of mineral stability diagrams to understand igneous and metamorphic petrogenesis. [Offered: W,S]', 'EARTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EARTH 235', 'Stratigraphic Approaches to Understanding Earth''s History', 0.5, 'LAB, LEC', 'An introduction to stratigraphic principles and methods used in deciphering geological history. The development of stratigraphic sequences as controlled by global and regional tectonics and sea level fluctuations. Interrelating aspects of Earth''s physical, chemical and biological history. Examples are drawn primarily from Canadian geology. Laboratory work will include construction and interpretation of various types of maps and cross sections. [Offered: F]', 'EARTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EARTH 236', 'Principles of Paleontology', 0.5, 'LAB, LEC', 'The principles of paleontology with particular stress on the paleontological concepts; examples will be drawn primarily from the fossil record of invertebrates. Laboratory work will include projects related to lecture topics. [Offered: F]', 'EARTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EARTH 238', 'Introductory Structural Geology', 0.5, 'LAB, LEC, TUT', 'Concepts of stress and strain; elementary rock mechanics; description and classification of folds, faults, foliations, lineations and joints; use of primary structures; introduction to geometrical analysis. Labs will emphasize geometrical problems, including geological maps and cross sections, and stereographic projection. [Offered: W]', 'EARTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EARTH 260', 'Applied Geophysics 1', 0.5, 'FLD, LEC, TUT', 'An introduction to seismic, gravity, electric, electromagnetic and magnetic methods of exploration geophysics. [Offered: F]', 'EARTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EARTH 270', 'Disasters and Natural Hazards', 0.5, 'LEC, TUT', 'The course will examine the physical causes and affects of natural disasters including landslides, snow avalanches, earthquakes, tsunamis, hurricanes, tornadoes, flooding, subsidence, volcanic eruptions and asteroid impacts. Illustrated by case histories, the course will analyse the factors that lead to disasters. The effect of climate change, human activity, and population growth on the magnitude and frequency of disasters will be explored. The course will introduce the basic principles of Geo-Risk management and its applications in natural hazards engineering, in the development of mitigation strategies and in the re-insurance industry. [Offered: W]', 'EARTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EARTH 281', 'Geological Impacts on Human Health', 0.5, 'LEC, TUT', 'The importance of geology to health. This includes examining the effects of volcanic gases and dust and other aerosols; tracing the sources and impacts of metals and other naturally occurring geological materials in the Earth''s hydrosphere and crust; geology and the bioaccumulation of metals; and distinguishing natural versus anthropogenic processes on the geochemical distribution of toxic material in the near-surface environment. [Offered: W]', 'EARTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EARTH 310', 'Environmental Informatics', 0.5, 'LEC, TUT', 'The objective of this class is to understand and use quantitative and analytical techniques founded in mathematics and the geosciences to describe, model and predict environmental phenomena on planet Earth. The course aims to teach the mathematical tools as well as the physics underlying such phenomena. An introduction to data analysis will also be provided. Topics include chaos theory, turbulence, ocean-atmosphere interactions (El Nino/Southern Oscillation), simple climate models, and atmosphere-land interactions. [Offered: F every other year]', 'EARTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EARTH 331', 'Volcanology and Igneous Petrology', 0.5, 'LAB, LEC', 'The principles and theories of the origins of volcanic and plutonic igneous rocks. Physics and chemistry of magma; controls of volcanic eruptions, magmatic differentiation and the distribution and occurrence of magma types. [Offered: F]', 'EARTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EARTH 332', 'Metamorphic Petrology', 0.5, 'LAB, LEC', 'Principles and theories of metamorphic rock genesis. Static, dynamic and polyphasal crystalloblastic growth. Processes of solid-state crystallization in metamorphic environments. Zonal and facies classifications; facies series and the place of metamorphism in global tectonics. Introduction to metasomatism. [Offered: W]', 'EARTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EARTH 333', 'Introductory Sedimentology', 0.5, 'LAB, LEC', 'The origin and physical properties of sediments. Fluid flow, glacier motion and sediment transport processes. Mechanical and chemical erosion rates, sediment fluxes and budget. Facies models of the major depositional environments. Diagenetic processes. Laboratories focussed on the description and interpretation of sediments and sedimentary rocks. [Offered: W]', 'EARTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EARTH 342', 'Geomorphology and GIS Applications', 0.5, 'LAB, LEC', 'An introduction of geomorphology with emphasis on landscape-forming processes. Characteristics and development of various landforms (e.g. mountain belts, volcanoes, impact craters, and those created by erosion/deposition of rivers/glaciers) are introduced. Skills for interpreting landforms in air photos or satellite images (in Google Earth or ArcGIS) are practised in the lab work. Application of Geographic Information Systems (GIS) to Earth Sciences. Basic concepts of GIS and GIS applications in mineral exploration industry and in fieldwork are introduced. Laboratory work of GIS involves map construction and spatial analysis (e.g. landslide hazard assessment). [Offered: F]', 'EARTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EARTH 358', 'Earth System Science', 0.5, 'LEC, TUT', 'Study of the Earth as a system, with a focus on global climate history and dynamics, biogeochemical cycling, and the impacts of human activity. Critical analysis and synthesis are emphasized in the context of group presentations and discussions. [Offered: W]', 'EARTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EARTH 359', 'Flow Through Porous Media', 0.5, 'LEC, TUT', 'Quantitative introduction to the physical principles that govern the flow of fluids through porous and fractured geologic materials. Physical properties of fluids and porous media will be presented and conservation, flux and state equations will be developed. Physics of flow of immiscible fluids, including air-water and oil-water combinations will be included. [ Offered: F]', 'EARTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EARTH 390', 'Methods in Geological Mapping', 0.5, 'LAB', 'Field study in Sudbury and Whitefish Falls areas. Held for at least 9 days at end of the Winter term. Geological and geotechnical field techniques, map construction, report writing.', 'EARTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EARTH 421', 'Geochemistry 2', 0.5, 'LAB, LEC', 'The application of chemical thermodynamics to geochemical problems. Development of the three laws of Thermodynamics; Gibbs free energy and equilibria constants. Introduction to various topics in aqueous geochemistry such as mineral equilibria, ion exchange and redox equilibria. Laboratory session will involve various experiments related to mineral solubility, chemical kinetics, acid-base equilibria and chemical modelling. [Offered: F]', 'EARTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EARTH 435', 'Advanced Structural Geology', 0.5, 'LAB, LEC', 'Stress and strain; deformational behaviour of rocks; origin of folds, foliations, lineations, joints and faults; geometrical and kinematic analysis; relationships of structures from the microscopic to the megascopic scale. Labs will include simple experiments, advanced geometrical problems and observation and measurement of microstructure and fabric.', 'EARTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EARTH 436A', 'Honours Thesis', 0.5, 'PRJ, TUT', 'The first term of a two-term supervised research project in which students plan and conduct research. Students learn to gather and interpret data and present their findings in a formal seminar and written thesis.', 'EARTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EARTH 436B', 'Honours Thesis', 0.5, 'PRJ, TUT', 'The second term of a two-term supervised research project in which students plan and conduct research. Students learn to gather and interpret data, and present their findings in a formal seminar and written thesis.', 'EARTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EARTH 437', 'Rock Mechanics', 0.5, 'LEC', 'Stress, strain and strength in geomaterials. Origins of stress and stress measurement methods, including hydraulic fracture and strain relief. Rock Mechanics principles and design procedures in areas of mining, civil engineering and petroleum engineering. Monitoring methods, including introduction to microseismic surveillance. Course includes laboratory and project work.', 'EARTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EARTH 438', 'Engineering Geology', 0.5, 'LAB, LEC', 'Review of basic concepts in engineering geology as applied to rock and soil, including material properties, variability in properties, external factors such as stress, and evaluation of design adequacy. Site investigation and characterisation techniques used to define and characterise the properties of geological materials and their use in selected engineering geologic design and construction problems. Laboratory assignments will focus on the determination of physical properties and site assessment problems. [Offered: W]', 'EARTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EARTH 440', 'Quaternary Geology', 0.5, 'LAB, LEC', 'Glacial-interglacial cycles and sub-Milankovitch oscillations from ocean sediments and ice cores. Quaternary geochronology. Glacial sediment-land systems. Mineral exploration techniques pertaining to glaciated terrains and hydrostratigraphic analyses of Quaternary basins. Local field trips. Laboratory studies on glacial sediments.', 'EARTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EARTH 444', 'Applied Wetland Science', 0.5, 'LEC, TUT', 'Advanced concepts on wetland ecosystems in the context of regional and global earth systems processes such as carbon and nitrogen cycling and climate change, applications of wetland paleoecology, use of isotopes and other geochemical tools in wetland science, and wetland engineering in landscape rehabilitation and ecotechnology. Current issues in Canada and abroad will be examined. [Offered: F]', 'EARTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EARTH 456', 'Numerical Methods in Hydrogeology', 0.5, 'LEC', 'An introduction to the theory and practice of groundwater flow and contaminant transport modeling. Topics related to variably-saturated flow will focus on: derivation of Richard''s equation, a control volume finite difference discretization, assembly and solution of the flow equation, material balance error, truncation error analysis, stability and monotonicity. Topics related to contaminant transport will focus on: derivation of the contaminant transport equation, a control volume finite difference discretization along with various spatial and temporal weighting schemes and their associated accuracy, monotonicity, and material balance error. Hands on experience is provided using software applied to industry-standard variably-saturated flow and transport problems, with an emphasis on visualization and interpretation of results.', 'EARTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 126', 'Introduction to Electrostatics, Magnetism and Electronics', 0.75, 'LAB, LEC, TUT', 'Coulomb''s law and electric field, Gauss'' law and electric flux, energy and potential, dielectrics, capacitors and capacitances, Poisson''s and Laplace''s equations, electric currents, metallic conductors, Ohm''s law, Kirchhoff''s voltage and current laws, resistances, electric energy dissipated, Ampere''s circuital law, magnetic materials and magnetic circuits, Faraday''s law, inductances, electric energy stored, semiconductors, pn junctions, Zener diode, diode circuits, ideal op-amp and op-amp circuits. [Offered: F]', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EARTH 458', 'Physical Hydrogeology', 0.5, 'LEC', 'An introduction to physical hydrogeology, including Darcy''s law, the groundwater flow equations for steady-state and transient conditions, applications to flow nets, aquifer testing, groundwater resources, and groundwater protection. The role of groundwater in the hydrologic cycle is explored with emphasis on natural groundwater flow systems and their influence on stream flow. Physical processes controlling groundwater contamination are introduced.', 'EARTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EARTH 458L', 'Field Methods in Hydrogeology', 0.25, 'LAB', 'This course exposes students to a wide variety of field and laboratory techniques for collecting hydrogeologic data and to gain experience in interpreting the data. Advantages and limitations of various measurement and data reduction techniques for evaluating groundwater flow systems are demonstrated in a set of field exercises carried out at the groundwater demonstration facility located on the university North Campus. These exercises illustrate the complexity of natural systems and the need for good data collection and interpretation skills when characterizing such systems . [Offered: F,S]', 'EARTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EARTH 459', 'Chemical Hydrogeology', 0.5, 'LEC, TUT', 'An introduction to the chemical side of hydrogeology with emphasis on groundwater quality and contaminants in the groundwater zone, the geochemical origin of major ions in natural groundwater, causes of hardness, groundwater age determination using isotopes, common causes of groundwater contamination; processes governing contaminant behaviour including dispersion, diffusion and adsorption, hydrogeologic aspects of site selection for waste disposal. [Offered: W]', 'EARTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EARTH 460', 'Applied Geophysics 2', 0.5, 'LEC, TUT', 'A detailed examination of selected topics in exploration geophysics, with an emphasis on data processing, time series analysis and computer modelling of geophysical responses.', 'EARTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EARTH 461', 'Applied Geophysics 3', 0.5, 'LEC, TUT', 'An application-oriented course emphasizing current methodology in near-surface geophysics, including electrical, electromagnetic, ground penetrating radar, seismic refraction and magnetic methods. The basic theory for geophysical data acquisition and interpretation is studied and applied to field data. The application of geophysical techniques to archeological, forensics, geotechnical and hydrogeological problems is discussed. [Offered: F]', 'EARTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EARTH 461L', 'Field Methods in Applied Geophysics', 0.25, 'LAB', 'Field exercises involving the use of standard near-surface geophysical methods. The practical aspects of performing geophysical surveys are a central theme of this course. [Offered: F]', 'EARTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EARTH 471', 'Mineral Deposits', 0.5, 'LAB, LEC', 'The principles of metal concentration and deposition in magmatic and hydrothermal environments. Classic ore deposits are examined using these principles, including applications of fluid inclusion, stable isotope and mineral stability to ore-forming processes. Basic aspects of mineral exploration are also covered. Laboratories involve hand sample and ore petrology of suites from diverse deposits. [Offered: W]', 'EARTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EARTH 490', 'Field Course', 0, 'LAB', 'One or more trips that emphasize field observations. Specific trips may be organized to examine field aspects of any of the disciplines within Earth Sciences or Geological Engineering. Field exercises and reports may be part of the requirements. Additional field trip fees will apply.', 'EARTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EARTH 491', 'Special Topics in Earth and Environmental Sciences', 0.5, 'LEC', 'A lecture course offered in a particular area of Earth and Environmental Sciences, subject to availablity of instructor.', 'EARTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EARTH 499', 'Research Project', 0.5, 'PRJ, TUT', 'A one-term supervised research project in which students plan and conduct research. Students learn to gather and interpret data and present their findings in a formal seminar and written report.', 'EARTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EARTH 601', 'Stratigraphic Paleontology', 0.5, 'LEC', 'Analysis of the factors controlling the distribution of organisms and fossils. Important zone fossils of invertebrates, vertebrates, and plants will be studied with reference to their vertical and lateral distribution. Problems of migration, dispersal, and provincialism will be considered in detail.', 'EARTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EARTH 602', 'Paleontology', 0.5, 'RDG', 'Selected topics in paleontology will be studied in depth with emphasis on evolution and paleoecology.', 'EARTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EARTH 603', 'Micropaleontology', 0.5, 'LEC', 'A systematic examination of major groups of microfossils, particularly palynomorphs, and insects, with emphasis on their classification, evolution, paleoecology and stratigraphic application.', 'EARTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EARTH 606', 'Principles of Palynology', 0.5, 'LEC', 'The morphology, taxonomy, biostratigraphy and paleoecology of marine and non-marine palynomorphs from Paleozoic to Recent time are examined. Palynomorph morphology and taxonomic principles and methods of pollen analysis, pollen dispersal, deposition and preservation are explored and related to the geological record and their use in environmental reconstructions. The role of pollen analysis in Quaternary paleoecology is presented.', 'EARTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EARTH 610', 'Sedimentology - Recent Sediments', 0.5, 'LEC', 'The origin of recent clastic and non-clastic sediments, the study of composition, structures, relationships, and environments towards an interpretation of ancient sedimentary rocks. The origin and significance of sedimentary structures, environmental associations, sedimentological models, hydrodynamics.', 'EARTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EARTH 611', 'Sedimentology - Ancient Sediments', 0.5, 'LEC', 'Interpretation of ancient sedimentary rocks. Sedimentation and tectonics, basin evolution, continental margins. Ancient clastic and chemical suites, diagenetic indicators, evolution of sedimentary suites through time.', 'EARTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EARTH 612', 'Carbonate Sedimentology', 0.5, 'LEC', 'Weekly seminars will introduce current concepts in a variety of subject areas in the deposition and diagenesis of carbonate sediments. Laboratory exercises will involve study of Pleistocene and older carbonate rock suites from a selection of geologic settings. Several laboratories will be devoted to introduction of common analytical techniques such as cathodoluminescence and SEM.', 'EARTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EARTH 620', 'Metamorphic Tectonites', 0.5, 'LEC', 'The study of textural and structural features of deformed metamorphic rocks. A synthesis of micro- and mega-scopic techniques directed at elucidating sequences of deformation and crystallization. Quantitative methods in the study of deformed rocks. Elements of style and symmetry in tectonites.', 'EARTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EARTH 621', 'Aqueous Geochemistry', 0.5, 'LEC', 'a) Basic thermodynamics and activity-fugacity relationships. Chemical equilibria, ion association and complexing, oxidation-reduction reactions. Models for aqueous equilibria in high concentration solutions. b) Interaction of groundwater with porous media; mineral dissolution/precipitation reaction kinetics, ion exchange, surface ionization of oxides. c) Use of computer codes such as PHREEQE and GEOCHEM to simulate mass transfer in geochemical systems. Examining and modelling chemical evolution in groundwater flow systems.', 'EARTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EARTH 622', 'Environmental Isotope Hydrology and Geochemistry', 0.5, 'LEC', 'This course explores the application of environmental isotopes (principally 2H, 13C, 15N, 18O,34S) as tracers of inorganic and organic processes and cycles in nature, with an emphasis on the use of isotope tracers in water and climate studies, including runoff generation analysis, isotope-mass balance studies, climatology and paleoclimatology, and in studies of biogeochemical cycling.  Other topics are tailored to student interest and research focus across the broad spectrum of geological, hydrological and biological questions that can be addressed using isotope techniques.  Note: Open to graduate students in physical and biological sciences, physical geography and engineering.', 'EARTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EARTH 623', 'Geochemistry of Hydrothermal Ore Deposits', 0.5, 'LEC', 'Application of light and heavy stable isotope systems as process tracers and exploration tools for a wide variety of low-temperature and high-temperature ore deposits.  The use of stable isotopes for oil-oil and oil-source rock correlations and for reconstruction of local and global redox conditions during deposition of petroleum source rocks.  Offered in alternate years.', 'EARTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EARTH 624', 'Environmental Biogeochemistry', 0.5, 'LEC', 'The influence of physical, chemical and microbiological processes on groundwater geochemistry are examined. Background concepts in microbial ecology and organic geochemistry are developed and related to subsurface environments. Treatment is given to biodegradation of organic pollutants, microbially-mediated redox reactions and organic-metal interactions. EARTH 439 is strongly recommended.', 'EARTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EARTH 625', 'Advanced Petrology', 0.5, 'LEC', 'Advanced numerical techniques and theory in quantifying the behavior of rocks and fluids at high temperatures and pressues.  Application of experimental and natural data to the study of tectonic, metamorphic and igneous processes.', 'EARTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EARTH 626', 'Global Biogeochemical Cycles', 0.5, 'LAB, LEC', 'The global cycles of carbon, oxygen, nitrogen, phosphorus , sulfur, silicon and iron, are intimately linked to the chemical composition of the atmosphere and ocean, earth''s climate, global biological productivity and diversity, and the evolution of sedimentary rocks.  This course focuses on the quantitative representation of the processes and mechanisms driving the global biogeochemical cycles of carbon and nutrient elements on both short (100-104 years) and long (>104 years) time scales.  Attention will also be given to the perturbation of biogeochemical cycles by human activity.  To explore the interactions between the processes, as well as between the elemental cycles themselves, we will use mass balance models.  Each student will carry out an independent project based on an elemental cycle of his/her choice.', 'EARTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EARTH 630', 'Genesis of Metalliferous Ore Deposits', 0.5, 'LEC', 'Selected topics will be studied in depth with emphasis on deposits associated with volcanic activity.', 'EARTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EARTH 631', 'Field Methods in Soil and Rock Mechanics', 0.5, 'LEC', 'Site investigation techniques for strength and deformation properties, load tests, bore hole tests, rock classification, monitoring methods and instrumentation, borehole geophysics for geomechanical properties, correlation to case histories.', 'EARTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EARTH 632', 'Geology of Industrial Minerals', 0.5, 'LEC', 'Geological aspects of locating, working and utilizing industrial minerals and constructional materials. Problems of quantity, quality and value of such deposits will be evaluated and will be specifically related to those deposits in Southern Ontario. Mineral economics and urban geology will be considered. Field trips will be an important part of the practical work of the course.', 'EARTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EARTH 634', 'Geomechanics of In Situ Processes', 0.5, 'LEC', 'Geomechanics of in situ methods of energy recovery for heavy oil and minerals. Thermoelasticity, hydraulic fracture geomechanics, multi-phase system compressibility, remote and direct monitoring methods.', 'EARTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EARTH 635', 'Clay Mineralogy', 0.5, 'LEC', 'Structure, classification, properties, and nomenclature of clay materials. Genesis and environmental and engineering significance of clay minerals. Identification of clay minerals by X-ray diffractometry and other techniques. Clay minerals in diagenesis and sedimentary basin analysis.', 'EARTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EARTH 638', 'Advanced Engineering Geology', 0.5, 'LEC', 'Time dependent behaviour of rocks, Griffiths crack theory, failure criteria for rocks, in situ stresses and their origins, behaviour of discontinuous rock masses, introductions to analytic and numerical methods for rock mechanics. Case studies, term project.', 'EARTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EARTH 640', 'Quaternary Geology of North America', 0.5, 'LEC', 'Selected topics in the North American Quaternary such as Great Lakes history, Canadian organic deposits and fossil record, glacial and non-glacial stratigraphy, and the history of the last continental ice-sheet.', 'EARTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EARTH 641', 'Advanced Quaternary Ecology', 0.5, 'LEC', 'Discussion of current topics in Quaternary ecology at the advanced level. Emphasis will be on application and significance of Quaternary ecological techniques to paleoenvironmental reconstructions, climatic change, land-use history, anthropogenic disturbance, and environmental modelling.', 'EARTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EARTH 642', 'Geoliminology', 0.5, 'LEC', 'An introduction to basic concepts on the morphology, geology, geochemistry, sediment-related processes, and paleoecology of lakes. The focus is on the lake/watershed system and processes of the water column and associated terrestrial processes.', 'EARTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EARTH 643', 'Nutrients in Watersheds', 0.5, 'LEC', 'Nutrients such as nitrogen (N), phosphorus (P), silica (Si) and carbon (C) are vital to organisms, and play an essential role in our everyday life.  Used in excess, they can also become a nuisance with unwanted effects on the environment.  This course focuses on nutrients from a hydrological perspective, using a river basin based viewpoint.  The course covers general nutrient cycles, then nutrient budgets in watersheds, and how to model nutrient fluxes in catchments, using different approaches.  We will also discuss pollution history, using nutrients and metal pollution in river basins over the last centuries.  This will enable students to understand challengers that might arise from a variety of issues, from pollution history to current practices.  Individual student projects will be based on either a particular element or a particular model.', 'EARTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EARTH 644', 'Global Problems of Quaternary Geology', 0.5, 'LEC', 'Selected topics with global perspective, such as the deep sea record, long terrestrial records, Quaternary time boundaries, dating methods, paleotemperature analyses, isostasy, and sea level changes.', 'EARTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EARTH 645', 'Geology of the Great Lakes Region', 0.5, 'LEC', 'Selected topics dealing with the stratigraphy, paleontology, tectonic development, and economic geology of the Great Lakes Basin. Approximately one third of the course will be devoted to each of Precambrian, Paleozoic, and Quaternary geology.', 'EARTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EARTH 646', 'Paleolimnology', 0.5, 'LEC', 'This course examines the use of stratigraphic records from lakes to reconstruct environmental change.  Topics include sediment coring techniques, radiometric dating methods, analysis of physical, geochemical and biological indicators, and case studies demonstrating applications to address scientific and management questions. Practical experience is gained from a lake sediment coring field trip and subsequent laboratory analyses.  *eligible for MES.', 'EARTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EARTH 650', 'Physical Processes in Groundwater Systems', 0.5, 'LEC', 'This course aims to deepen the student''s understanding of physical processes occurring in natural groundwater systems. Flow and transport processes and their interrelationships with the characteristics of the porous medium are studied and the governing equations developed. Topics covered include the fundamental concepts of physical hydrogeology, flow in the saturated and unsaturated zones, flownet analysis, immiscible flow of non-aqueous phase liquids (NAPLs), contaminant transport in porous and fractured media, stochastic principles of transport in heterogeneous media, coupled nonlinear processes, and applications in groundwater contamination/remediation. Recommended: EARTH 456 (can be taken concurrently with EARTH 650).', 'EARTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EARTH 651', 'Advanced Groundwater Modelling', 0.5, 'LEC', 'This course covers advanced numerical modelling topics in groundwater flow and contaminant transport in the subsurface. Topics to be explored include two- and three-dimensional transport in groundwater systems, density/ heat-dependent flow/transport, flow in the vadose zone, immiscible flow of non-aqueous phase liquids, multiphase dissolution and mass transfer processes, transport of biodegrading or chemically interacting contaminants, transport in fractured systems, transport in the vapour phase. The focus is on the use of models to obtain insight into the complex coupled processes that control groundwater contamination and remediation problems. Students will work with their own as well as existing models.', 'EARTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EARTH 653', 'Contaminant Hydrogeology', 0.5, 'LEC', 'Physical and chemical processes influencing contaminant behaviour in groundwater with emphasis on field approaches. Interpretation of process effects in the context of various causes of groundwater contamination such as those related to landfills, chemical spills, mining and agriculture. Field experiments and case studies. (EARTH 621 and EARTH 657 or equivalent are recommended).', 'EARTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EARTH 654', 'Groundwater Research Management', 0.5, 'LEC', 'Advanced topics in the design and analysis of aquifer tests, behaviour of aquifer-aquitard systems, aquifer exploration, groundwater resource evaluation in undeveloped areas, artificial recharge, induced recharge, conjunctive use of surface water and groundwater, effects of excessive groundwater withdrawals, role of analog and digital models in groundwater management.', 'EARTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EARTH 656', 'Groundwater Modelling', 0.5, 'LEC', 'This course is an enriched version of EARTH 456 for graduate students. It introduces the student to numerical modelling techniques in groundwater science, with an emphasis on a sound understanding of the fundamental principles as a basis for developing and using models in a research environment. Numerical techniques including finite difference, finite element, and particle tracking methods are studied and applied to the solution of problems in groundwater flow, aquifer mechanics, flownet generation, and advective-dispersive transport. Proper modelling approaches, error analysis, stability, discretization constraints, pitfalls, and model misuse are discussed. Students will be developing some of their own groundwater models, and will be obtaining hands-on experience with state-of-the-art interactive groundwater models in the PC laboratory. Prior experience with FORTRAN is essential. Recommended: MATH 111B or equivalent. Students without the required prerequisite may seek consent of the instructor.', 'EARTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EARTH 657', 'Organic Contaminants in the Subsurface', 0.5, 'LEC', 'Fundamental processes governing the fate of organic contaminants in soil and aquifer systems. Emphasis on partitioning between phases (dissolution, sorption and volatilization) with applications. Students without the required prerequisite may seek consent of the instructor.', 'EARTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EARTH 658', 'Flow and Transport in Fractured Rock', 0.5, 'LEC', 'An introduction to the physical hydrogeology of fractured rock. Topics will include a review of the structural geology of fractured rocks, development and validity of the cubic law, groundwater flow and contaminant transport in discrete fractures and fracture networks, hydraulic testing methods and the hydrogeology of low-permeability environments. Recommended: Differential Calculus.', 'EARTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EARTH 659', 'Chemical Hydrogeology', 0.5, 'LEC', 'This course builds on the fundamental principles of aqueous chemistry and water-rock interactions with emphasis on groundwater environments. Topics include the evolution of natural groundwater chemistry, chemical aspects of contaminant hydrogeology such as hydrocarbons, radioactive wastes, acid mine drainage and industrial and municipal waste disposal. Geochemical models are introduced and applied by students in a course project. Students without the required prerequisite may seek consent of the instructor.', 'EARTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EARTH 661', 'Analytical Methods in Mathematical Geology', 0.5, 'LEC', 'A course intended to provide MSc and PhD students with a working knowledge of analytical techniques for solution of differential equations governing flow and mass transfer in hydrogeologic systems. Use of various transform methods such as Fourier and Laplace will be covered and applied to a variety of problems of hydrogeological interest. The course will consist of lectures, selected problem assignments and/or individual projects. Recommended: Second Year Calculus.', 'EARTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EARTH 668', 'Advanced Applied Geophysics', 0.5, 'LEC', 'A field-oriented course on current methodology in the general area of geotechnical or engineering geophysics as applied to environmental problems. Typical topics covered: well logging and time domain electromagnetic surveys for groundwater supply; magnetic and terrain conductivity surveys for waste materials; ground penetrating radar applications to stratigraphy and contamination; streaming potential surveys of dam leakage. Usually taught with EARTH 461.', 'EARTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EARTH 671', 'Field Methods in Hydrogeology', 0.5, 'LEC', 'A course dealing with the theory and applications of field techniques in hydrology. Physical, chemical, and isotopic methods are included with applications to groundwater and surface water hydrology. The course will consist of lectures, field activities, projects and assignments. All will be conducted over a 3-week time period at the end of the Winter term in April. Students without the required prerequisite may seek consent of the instructor.', 'EARTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EARTH 690', 'Current Problems in Geology', 0.5, 'SEM', 'Seminars will be held to cover major topics excluded from other available courses. Graduate students will be required to present for discussion short colloquia based on their reading of assigned topics. Participation in the discussions, as well as the actual presentation of papers, will be an integral part of the course.', 'EARTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EARTH 691', 'Special Studies for MSc Students', 0.5, 'RDG', 'Course credit may be given for special studies related to the research interest of a student and a member of the faculty by special arrangement.', 'EARTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EARTH 692', 'Special Studies for PhD Students', 0.5, 'RDG', 'Course credit may be given for special studies related to the research interest of a student and a member of the faculty by special arrangement.', 'EARTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EASIA 201R', 'Introduction to East Asia', 0.5, 'LEC', 'An introductory survey of the history and cultures of East Asia with particular reference to China, Japan, and Korea.', 'EASIA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EASIA 205R', 'Religions of East Asia', 0.5, 'LEC', 'An examination of the leading religious and philosophical ideas that have shaped the cultures and histories of East Asia: China, Korea, and Japan. Folk, Shamanic, Confucian, Daoist, Shinto, and Buddhist traditions are examined.', 'EASIA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EASIA 206R', 'Japanese Religions', 0.5, 'LEC', 'An examination of religious phenomena in the history of Japan, including their impact on art, literature, philosophy, and politics. Buddhism, Shinto, Confucianism, folk beliefs, new religious movements, and other traditions are explored.', 'EASIA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EASIA 207R', 'Chinese Religions', 0.5, 'LEC', 'An examination of religious phenomena in the history of China, including their influence on art, literature, philosophy, and politics. Buddhism, Confucianism, Daoism, folk beliefs, new religious movements, and other traditions will be explored.', 'EASIA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EASIA 210R', 'Premodern Chinese Literature', 0.5, 'LEC', 'This course is an introductory survey of the main genres of classical and premodern Chinese literature. Individual works are related to the evolution of the literary tradition and its interaction with history, philosophy and politics. Classical and historical writing, poetry, drama, and novels will be introduced.', 'EASIA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EASIA 220R', 'The History of East Asian Communities in Canada', 0.5, 'DIS, LEC', 'This course examines the evolution of the Chinese, Japanese, and Korean communities in Canada as well as their significance for Canadian economic, social, and political life in the 19th and 20th centuries.', 'EASIA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EASIA 250R', 'Study Abroad in East Asia', 1, 'LEC', 'An intensive study of the culture and language of an East Asian country. With the guidance of a course director, students complete readings, attend lectures, and visit cultural sites for six weeks or more on location.', 'EASIA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EASIA 275R', 'Religion and Japanese Film', 0.5, 'LEC', 'The course examines how religious beliefs and values in Japan are explored in the medium of film.', 'EASIA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EASIA 277R', 'International Relations of East Asia', 0.5, 'LEC', 'This course explores key topics in the international relations of East Asia after World War II. Classes examine literature on major developments of East Asian relations, including the Cold War, East Asian regionalism, and the foreign policies of China, Japan, and Korea.', 'EASIA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EASIA 300R', 'Politics and Diplomacy of Contemporary Japan', 0.5, 'SEM', 'This course surveys several key topics in the politics and diplomacy of contemporary Japan, particularly in its relations with its Asia-Pacific neighbours. Topics may include the Pacific War and its legacies, the Japan-US security alliance, the Okinawa problem, and Japan-Canada relations.', 'EASIA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EASIA 301R', 'The Political Economy of East Asia', 0.5, 'LEC', 'This course explores the rise of the East Asian economy since the end of World War II. The subject is pursued through examining some of the most important aspects of the so-called Asian miracle/model, including the historical background of the Asian miracle, the role of the state in the region''s economic development, the relationship between Asian culture and economic development, social development, and East Asia''s business system and practice.', 'EASIA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EASIA 330R', 'Pure Land Buddhism', 0.5, 'LEC', 'An examination of Pure Land Buddhism, including its origins, development and influence in India, China, Korea, Japan, and the West.', 'EASIA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EASIA 375R', 'Special Topics in East Asian Studies', 0.5, 'LEC', 'This course deals with selected topics in East Asian studies according to the research and/or instructional interests of faculty.', 'EASIA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 100A', 'Electrical and Computer Engineering Practice', 0.2, 'SEM', 'Areas of research and professional practice in electrical and computer engineering, including the engineer''s role in protecting public safety. Exposure to concepts from other Engineering plans. Support material for the academic term, co-operative education, and professional or career development. [Offered: F]', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 100B', 'Electrical and Computer Engineering Practice', 0.2, 'SEM', 'Areas of research and professional practice in electrical and computer engineering. Exposure to concepts from other Engineering plans. Support material for the academic term, co-operative education, and professional or career development.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 103', 'Discrete Mathematics', 0.5, 'LEC, TUT', 'Propositional logic, predicate logic, set theory, finite automata, temporal logic. [Offered: W, S]', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 105', 'Classical Mechanics', 0.5, 'LAB, LEC, TUT', 'Forces in nature and Newton''s laws, dynamics and circular motion, work, energy, and conservation of energy. Linear momentum and linear impulse, rotational dynamics. Oscillations; simple harmonic motion. Wave motion; traveling waves and standing waves. [Offered: F]', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 106', 'Electricity and Magnetism', 0.5, 'LAB, LEC, TUT', 'Electrostatics; electric field, flux, Gauss''s Law, potential and potential energy. Capacitors; dielectric, capacitance, electric energy storage. Resistors; charge flow, current, resistance. Magnetostatic; magnetic force, magnetic fields, Ampere''s Law. Inductors; magnetic flux, inductance, magnetic materials, magnetic energy storage. Time-varying fields; Faraday''s Law, mutual inductance, simple motors and generators. [Offered: W, S]', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 124', 'Digital Circuits and Systems', 0.5, 'LAB, LEC, TUT', 'Number systems and Boolean arithmetic. Boolean algebra and simplification of Boolean functions. Combinational circuits. Sequential circuits; design and implementation. Hardware description languages. Timing analysis. Implementation technologies. [Offered: W,S]', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 140', 'Linear Circuits', 0.5, 'LAB, LEC, TUT', 'Analysis of linear circuits. Voltage, current, resistance, capacitance, inductance, voltage source, current source, dependent sources, Ohm''s Law, Kirchhoff''s Laws, nodal analysis, mesh analysis, circuit transformations, operational amplifier circuits, time response, sinusoidal steady-state response. Preparing for, conducting, and reporting of laboratory experiments. Safety-orientation training, including WHMIS assessment, is included in this course. [Offered: W, S]', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 150', 'Fundamentals of Programming', 0.5, 'LAB, LEC, TUT', 'Software design process in a high-level programming environment. Programming fundamentals, language syntax, simple data types, control constructs, functions, parameter passing, recursion, classes, arrays and lists, list traversals, introduction to searching and sorting algorithms, basic object-oriented design, polymorphism and inheritance, simple testing and debugging strategies, pointers and references, basic memory management. [Offered: F]', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 155', 'Engineering Design with Embedded Systems', 0.5, 'LAB, LEC, TUT', 'Introduction to embedded systems, review of engineering design and analysis principles, software development life cycle, integrated development environments, use of software requirements and specifications, unified modelling language and documentation, event handling, simulation, project management, project scheduling, testing, verification, and maintenance considerations. [Offered: W, S]', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 200A', 'Electrical and Computer Engineering Practice', 0.1, 'SEM', 'Areas of research and professional practice in electrical and computer engineering. Exposure to concepts from other Engineering plans. Support material for the academic term, co-operative education, and professional or career development.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 200B', 'Electrical and Computer Engineering Practice', 0.1, 'SEM', 'Areas of research and professional practice in electrical and computer engineering. Exposure to concepts from other Engineering plans. Support material for the academic term, co-operative education, and professional or career development.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 205', 'Advanced Calculus 1 for Electrical and Computer Engineers', 0.5, 'LEC, TST, TUT', 'Fourier series. Ordinary differential equations. Laplace transform. Applications to linear electrical systems. [Offered: F,W]', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 206', 'Advanced Calculus 2 for Electrical Engineers', 0.5, 'LEC, TST, TUT', 'Triple integrals, cylindrical and spherical polar coordinates. Divergence and curl, applications. Surface integrals, Green''s, Gauss'' and Stokes'' theorems, applications. Complex functions, analytic functions, contour integrals, Cauchy''s integral formula, Laurent series, residues. [Offered: F,S]', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 207', 'Signals and Systems', 0.5, 'LEC, TUT', 'Discrete, continuous and periodic signals, time- and frequency-domain analysis of continuous- and discrete-time linear systems, periodic signals and Fourier series, non-periodic signals and Fourier transforms. [Offered: F, S]', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 209', 'Electronic and Electrical Properties of Materials', 0.5, 'LAB, LEC, TUT', 'Review of wave-particle duality, basic quantum mechanics, Schrodinger equation, energy bands in crystals, basic properties of semiconductors, intrinsic and doped semiconductors, electrons and holes, metals and alloys, superconductivity, phonons and heat capacity, dielectric materials, optical properties, dielectric properties and magnetic properties of materials. [Offered: F, S]', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 222', 'Digital Computers', 0.5, 'LAB, LEC, TUT', 'Computer organization. Memory units, control units, I/O operations. Assembly language programming, translation and loading. Arithmetic logic units. Computer case studies. [Offered: F, W]', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 224', 'Embedded Microprocessor Systems', 0.5, 'LAB, LEC, TUT', 'Microprocessor system architecture, bus systems, memory systems, peripherals, parallel interfaces, serial interfaces, analog interfaces, data transfer, synchronization, error detection/correction, testing and debugging. [Offered F, W, S]', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 231', 'Electronic Devices', 0.5, 'LAB, LEC, TST, TUT', 'Review of band theory and doped semiconductors in thermal equilibrium, charge neutrality, mass action law, recombination and transport mechanisms, Boltzmann relations, derivation of p-n junction dc and ac characteristics, charge storage effects. The bipolar transistor; derivation of dc and ac terminal characteristics, equivalent circuits. The junction field effect transistor (JFET) and metal oxide semiconductor FET, derivation of dc characteristics. [Offered: F, S, last offered Fall 2010]', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 240', 'Electronic Circuits 1', 0.5, 'LAB, LEC, TUT', 'Introduction to electronic signal processing; operational amplifier circuits; diode device and circuits; MOS (metal-oxide semiconductor) and bipolar amplifier biasing networks; load-line analysis; diode, MOS and bipolar small-signal equivalent circuits; single-stage small-signal MOS and bipolar amplifiers; transistor switches. [Offered: F, W]', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 242', 'Electronic Circuits 2', 0.5, 'LAB, LEC, TUT', 'Electronic circuits and their limitations, including; differential pairs, biasing, the cascode configuration and active loads. Differential and multistage amplifiers. Feedback, stability and compensation. CMOS logic circuits. [Offered: F, S]', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 250', 'Algorithms and Data Structures', 0.5, 'LAB, LEC, TUT', 'Data structures, abstract data types, recursive algorithms, algorithm analysis, sorting and searching, and problem-solving strategies. [Offered: F,W]', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 254', 'Operating Systems and Systems Programming', 0.5, 'LAB, LEC, TUT', 'Concepts of operating systems and systems programming; utility programs, subsystems, multiple-program systems; processes, interprocess communication, synchronization, and concurrency; memory management, segmentation, and paging; loading and linking, libraries; resource allocation, scheduling, performance evaluation; I/O systems, storage devices, file systems; protection, security, and privacy. [Offered: F, S]', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 290', 'Engineering Profession, Ethics, and Law', 0.5, 'LEC, TUT', 'An introduction to engineering law and ethics: ethical theories, code of ethics and misconduct, whistle blowing, conflict of interest, health and safety, diversity, workplace equity and sexual harassment, environment, Charter of Rights and Freedoms, torts, contract, and intellectual property. Continuation of studies of professional practice: history, Professional Engineers Act and Regulation, licensing, discipline and enforcement. [Offered: F, W]', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 300A', 'Electrical and Computer Engineering Practice', 0.1, 'SEM', 'Areas of research and professional practice in electrical and computer engineering. Exposure to concepts from other Engineering plans. Support material for the academic term, co-operative education, and professional or career development.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 300B', 'Electrical and Computer Engineering Practice', 0.1, 'SEM', 'Areas of research and professional practice in electrical and computer engineering. Exposure to concepts from other Engineering plans. Support material for the academic term, co-operative education, and professional or career development.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 309', 'Introduction to Thermodynamics and Heat Transfer', 0.5, 'LEC, TST, TUT', 'Macroscopic approach to energy analysis. Energy transfer as work and heat, and the First Law of thermodynamics. Properties and states of simple substances. Control-mass and control-volume analysis. The essence of entropy, and the Second Law of thermodynamics. The Carnot cycle and its implications for practical cyclic devices. Introduction to heat transfer by conduction, convection, and radiation. Basic formulation and solution of steady and transient problems. Issues relevant to the cooling of electrical devices. [Offered: W,S]', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 316', 'Probability Theory and Statistics', 0.5, 'LEC, TUT', 'Ensemble model of randomness. Conditional probability, independence, and Bayes'' theorem. Random variables, probability distribution functions. Expected values. Collections of random variables, joint and marginal probability distributions, and correlation. Introduction to Statistics. Confidence intervals. Estimation. Chi-squared test. p-values. [Offered: W, S]', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 318', 'Communication Systems 1', 0.5, 'LAB, LEC, TUT', 'Introduction to random processes, power spectral density. Thermal noise and the white noise model. Amplitude and angle modulation, generation and detection schemes. Sampling and reconstruction, quantization. Digital baseband transmission. Overview of digital passband communications. [Offered: F, W]', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 325', 'Microprocessor Systems and Interfacing for Mechatronics Engineering', 0.5, 'LAB, LEC, TUT', 'Synchronization and data flow; interfacing to sensors and actuators; microprocessor system architecture, parallel, serial, and analog interfacing; buses; direct memory access (DMA); interfacing considerations.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 327', 'Digital Hardware Systems', 0.5, 'LAB, LEC, TST, TUT', 'Design and modelling of digital hardware systems using a hardware description language. Development process. Impact of implementation technologies. Performance analysis and optimization. Functional verification. Timing analysis. Power analysis and optimization. Faults and testability. Reliability and fault tolerance.[Offered: W, S]', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 331', 'Electronic Devices', 0.5, 'LAB, LEC, TUT', 'Review of band theory and doped semiconductors in thermal equilibrium, charge neutrality, mass action law, recombination and transport mechanisms, Boltzmann relations. Device theory and modelling of p-n junction diode and derivation of dc and ac characteristics, charge storage effects. Principles, device theory and modelling of metal-oxide semiconductor field-effect transistors (MOSFETs) and the derivation of threshold voltage, dc current characteristics, small signal ac models. Principles of bipolar transistor and derivation of dc and ac terminal characteristics, equivalent circuits. [Offered: F, W]', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 351', 'Compilers', 0.5, 'LAB, LEC, TUT', 'Programming paradigms, compilation, interpretation, virtual machines. Lexical analysis, regular expressions and finite automata. Parsing, context-free grammars and push-down automata. Semantic analysis, scope and name analysis, type checking. Intermediate representations. Control flow. Data types and storage management. Code generation. [Offered: W, S]', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 354', 'Real-time Operating Systems', 0.5, 'LAB, LEC, TUT', 'Introduction, basic concepts, process management, interprocess communication and synchronization, memory management, file systems, resource management, interrupt handling, concurrent programming. [Offered: W]', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 356', 'Database Systems', 0.5, 'LAB, LEC, TST, TUT', 'Data models, file systems, database system architectures, query languages, integrity and security, database design. [Offered: F, W]', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 358', 'Computer Networks', 0.5, 'LAB, LEC, TUT', 'This course is a comprehensive introduction to computer networks. The focus is on the concepts, the protocols, and the fundamental design principles that have contributed to the success of the internet. Topics include history of the Internet, transmission media and technologies, switching and multiplexing, protocols and layering, wired and wireless LAN (local-area networks), congestion/flow/error control, routing, addressing, internetworking (Internet) including TCP (transmission control protocol). [Offered: F, W, S]', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 362', 'Modeling and Control of Electric Drives', 0.5, 'LAB, LEC, TST, TUT', 'Principles of electromechanical energy conversion. Rotating machines. DC motors. Induction motors. Synchronous machines. [Offered: F, W, last offered Winter 2012]', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 361', 'Power Systems and Components', 0.5, 'LAB, LEC, TUT', 'This course is an introduction to basic modeling and analysis techniques in electricity generation, transmission and distribution, including basic concepts in nonlinear system analysis. Functional descriptions and modeling of generators, transformers, transmission lines, motors and other loads are discussed. Power flow analysis techniques are studied in detail, from the basic equations to their use in power networks. Fault analysis and basic protection concepts are also discussed. [Offered: F, W]', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 375', 'Electromagnetic Fields and Waves', 0.5, 'LAB, LEC, TST, TUT', 'Maxwell''s equations; plane waves; time-harmonic fields; waves at planar boundaries; boundary conditions; reflection and transmission; transmission lines; electric fields in matter; magnetic fields in matter. [Offered W, S]', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 380', 'Analog Control Systems', 0.5, 'LAB, LEC, TUT', 'Introduction to control systems. Advantages of closed-loop feedback systems. The role of the system mathematical model. Block diagrams and signal flow graphs. The basic control system design problem, stability in control systems. Frequency response analysis techniques. Root-locus analysis. Elementary lead-lag compensation. [Offered: W, S]', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 390', 'Engineering Design, Economics, and Impact on Society', 0.5, 'LEC, PRJ, TUT', 'Introduction to design-project management, the impact of technology on society and the environment, and engineering economics. [Offered: F, W]', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 400A', 'Electrical and Computer Engineering Practice', 0.1, 'SEM', 'Areas of research and professional practice in electrical and computer engineering. Exposure to concepts from other Engineering plans. Support material for the academic term, co-operative education, and professional or career development.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 400B', 'Electrical and Computer Engineering Practice', 0.1, 'SEM', 'Areas of research and professional practice in electrical and computer engineering. Exposure to concepts from other Engineering plans. Support material for the academic term, co-operative education, and professional or career development.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 403', 'Thermal Physics', 0.5, 'LEC, TUT', 'Temperature and thermodynamic equilibrium. Work, internal energy and heat; first law, with examples. Kinetic theory of gases. Basic probability theory. Microscopic states and entropy. Absolute temperature, reversibility and the second law. Thermodynamic Functions and Maxwell''s relations. Phase transitions. Third Law. Other applications of thermodynamics. [Offered: F, S]', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 404', 'Geometrical and Physical Optics', 0.5, 'LEC, TUT', 'Electromagnetic waves and the nature of light. Geometrical optics, aberrations. Physical Optics: interference, Fraunhofer and Fresnel diffraction, polarization. Optical instruments. [Offered: F, W]', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 405', 'Introduction to Quantum Mechanics', 0.5, 'LEC, TUT', 'Introduction to quantization, wave-particle duality and the uncertainty principle. The Schroedinger equation and solvable examples. Topics include stationary states of particle-in-a-box, harmonic oscillator and the hydrogen atom. Quantization of angular momentum and spin. Introduction to approximation methods including time-independent perturbation theory. Modern applications of quantum mechanics. [Offered: W]', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 406', 'Algorithm Design and Analysis', 0.5, 'LAB, LEC, TUT', 'Design and analysis of efficient, correct algorithms. Advanced data structures, divide-and-conquer algorithms, recurrences, greedy algorithms, dynamic programming, graph algorithms, search and backtrack, inherently hard and unsolvable problems, approximation and randomized algorithms, and amortized analysis. [Offered: W]', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 409', 'Cryptography and System Security', 0.5, 'LEC, TUT', 'Introduction to cryptology and computer security, theory of secure communications, points of attack, conventional cryptographic systems, public key cryptographic systems, standards, firewalls, wireless system security, applications. [Offered: W]', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 411', 'Digital Communications', 0.5, 'LEC, TUT', 'Baseband transmission techniques, digital multiplexing, line coding, pulse shaping, intersymbol interference (ISI) and equalization. Representation of signals, vector equivalent channel models, design of signal sets, pulse detection and matched filtering, optimum and maximum-likelihood receivers. Techniques of digital modulation, multicarrier modulation, probability of error, synchronization, and their performance trade-offs. Spread-spectrum communication. [Offered: S]', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 413', 'Digital Signal Processing', 0.5, 'LEC, PRJ, TUT', 'Fourier representations in discrete and continuous time. Discrete Fourier transform and fast Fourier transform algorithms. Sampling theory. Sampling and quantization errors. Transform analysis of linear time-invariant systems. Filter design. Discrete Hilbert transform. Introduction to filter banks and discrete wavelet transform. [Offered: S]', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 414', 'Communication Systems 2', 0.5, 'LEC, TUT', 'Overview of wireless communications including standards. Characterization of mobile radio propagation channels. Signal representations. Transmission and reception techniques for wireless channels. Fundamentals of cellular communications and multiple-access schemes. [Offered: S]', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHINA 391R', 'Special Topics', 0.5, 'LEC, RDG, SEM', 'This course deals with advanced topics in Chinese language and literature. Students are expected to have at least an intermediate level of language proficiency.', 'CHINA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 415', 'Multimedia Communications', 0.5, 'LEC, PRJ, TUT', 'Overview of multimedia communications system, digital representation of
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 416', 'Advanced Topics in Networking', 0.5, 'LAB, LEC, TST, TUT', 'This course introduces advanced topics in networking with a focus on applications and wireless technologies. Topics include: Cellular networks (2G, 3G, 4G and beyond), applications (Domain Name System [DNS], Simple Mail Transfer Protocol[SMTP], Post Office Protocol [POP], Internet Message Access Protocol [ IMAP], Hypertext Transfer Protocol [HTTP]) and socket programming, Content-centric networks (content delivery networks, peer-to-peer protocols, data centers, etc.), protocols for multimedia applications (Session Initiation Protocol [SIP], Real-time Transport Protocol [RTP], RTP Control Protocol [RTCP]), emerging technologies (Internet of Things, sensors, software defined networks), policy issues (network neutrality, who controls the Internet?). [Offered W]', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 417', 'Image Processing', 0.5, 'LEC, PRJ, TUT', 'This course introduces the basic theories and methodologies of digital image processing. Topics include intensity transformations for image enhancement, two-dimensional discrete Fourier transform, spatial and frequency domain linear image filtering, nonlinear image filtering, binary image processing, edge detection, image segmentation, and digital video processing basics. [Offered: S]', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 418', 'Communications Networks', 0.5, 'LEC, TUT', 'Introduction to communications networks. Network architecture. Probabilistic description of network Queuing analysis. Packet transmission and error control. Dynamic routing. Media access control. Connection admission and congestion control. Design tradeoffs and performance evaluation. Application examples. [Offered: S]', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 419', 'Communication System Security', 0.5, 'LEC, TST, TUT', 'Security architecture and infrastructure, basic principles of trust and trust models. Network domain security, protected tunnels, and network security protocols. Access authentication, remote access, authentication models and mechanisms, authentication servers and protocols. Broadcasting and multicast security, key tree based multicast key distribution, and key revocation methods. Trusted platform, hardware based trust model, secure boot, and operating system security management. Radio link protection, and seamless security for mobility. [Offered: S]', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 423', 'Embedded Computer Systems', 0.5, 'LAB, LEC, TST, TUT', 'Specification and design of embedded systems, specification languages, hardware/software co-design, performance estimation, co-simulation, verification, validation, embedded architectures, processor architectures and software synthesis, system-on-a-chip paradigm, retargetable code generation and optimization, verification and validation, environmental issues and considerations. [Offered: W]', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 429', 'Computer Architecture', 0.5, 'LAB, LEC, TUT', 'Organization and performance of conventional uniprocessors, pipelined processors, parallel processors and multiprocessors; memory and cache structures; multiprocessor algorithms and synchronization techniques; special-purpose architectures. [Offered: S]', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 432', 'Radio Frequency Integrated Devices and Circuits', 0.5, 'LAB, LEC, TST, TUT', 'An introduction to the theory and practice of Radio Frequency (RF) Integrated Circuit design. Physics and modelling of RF integrated components such as resistor, inductor, capacitor (RLC) passives, diodes, metal oxide semiconductor field-effect transistors (MOSFETs), high electron mobility transistors, hetero-junction bipolar transistors. RF integrated components properties and representation such as short channel effects, noise parameters, transit frequency (ft), maximum frequency of oscillation (fmax), and quality factor.[Offered: S]', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 433', 'Fabrication Technologies for Micro and Nano Devices', 0.5, 'LAB, LEC, TST, TUT', 'Processes for Micro and Nano electronic fabrication. Semiconductor crystal growth. Thin films by chemical and physical vapor deposition. Epitaxial growth. Plasma-assisted and wet-chemical etch processes. Nano-structured material synthesis. Junction formation. Dielectric layer growth. Photolithography. Patterning on the nano-scale. Fabrication processes for Transistors, complementary metal-oxide semiconductor (CMOS), and Thin film devices. Micro-electro-mechanical systems (MEMS) technology. Process techniques for Nanoelectronic devices. [Offered: W]', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 444', 'Integrated Analog Electronics', 0.5, 'LAB, LEC, TST, TUT', 'Analog electronics exploits the physical behaviour of electronic devices to create electronic systems. The performance of single and multiple transistor amplifying stages are studied. Followed by a study of biasing, current mirror and output stages, which are then combined in the study of operational amplifier circuits. Frequency response and feedback are then reviewed leading to a detailed study of stability and compensation for multistage and operational amplifiers. The course finishes with a look at selected topics from A/D (analog-to-digital) converters, oscillators and phase-locked loops. [Offered: W]', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 445', 'Integrated Digital Electronics', 0.5, 'LAB, LEC, TST, TUT', 'Review of the Metal-Oxide Semiconductor (MOS) transistor: Static and dynamic behavior, short channel effects, scaling trends, Simulation Program with Integrated Circuit Emphasis (SPICE) models. Complementary Metal-Oxide Semiconductor (CMOS) inverter; combinational CMOS circuit design - logic styles for low power, high performance circuits; sequential CMOS circuit design - flip-flops, pipelines, Schmitt trigger; CMOS arithmetic circuits; interconnect parasitic; clocking and timing considerations in digital Very Large Scale Integration (VLSIs). [Offered: S]', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 451', 'Software Requirements Specification and Analysis', 0.5, 'LAB, LEC, TST, TUT', 'Introduces students to the requirements definition phase of software development. Models, notations, and processes for software requirements identification, representation, analysis, and validation. Cost estimation from early documents and specifications.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 452', 'Software Design and Architectures', 0.5, 'LAB, LEC, TST, TUT', 'Introduces students to the design, implementation, and evolution phases of software development. Software design processes, methods, and notation. Implementation of designs. Evolution of designs and implementations. Management of design activities.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 453', 'Software Testing, Quality Assurance and Maintenance', 0.5, 'LAB, LEC, TST, TUT', 'Introduces students to systematic testing of software systems. Software verification, reviews, metrics, quality assurance, and prediction of software reliability and availability. Related management issues.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 454', 'Distributed Computing', 0.5, 'LAB, LEC, TUT', 'Principles of distributed computing; architectures and middleware; servers, processes, and virtualization; upper-layer network protocols, interprocess communication and remote procedure calling; concurrency, synchronization and distributed algorithms, dependable distributed systems and fault tolerance. [Offered: S]', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 456', 'Database Systems', 0.5, 'LAB, LEC, TST, TUT', 'Introduction, data models, file systems, database system architectures, query languages, integrity and security, database design. [Offered: W, last offered Winter 2013]', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 455', 'Embedded Software', 0.5, 'LAB, LEC, TST, TUT', 'Concepts, theory, tools, and practice to understand, design, and write embedded software. This course covers computing elements, structures in embedded software, resource access protocols, uniprocessor scheduling, programming-language support, languages for MDD (model-driven development), worst-case execution time analysis, and overview of embedded distributed systems. [Offered: S]', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 457A', 'Cooperative and Adaptive Algorithms', 0.5, 'LEC, TST, TUT', 'The course starts by addressing the ill-structured problems and need for computational intelligence methods. It introduces the concepts of heuristics and their use in conjunction with search methods, solving problems using heuristics and metaheuristics, constraints satisfaction. The course also introduces the concepts of cooperation and adaptations and how they are influencing new methods for solving complex problems. The course starts by illustrating how the concepts of cooperation and adaptation are manifested in nature and how such models are inspiring new types of solutions methods. Topics to be covered include: search algorithms, game playing, constraints satisfaction, meta-heuristics, evolutionary computing methods, swarm intelligence, ant-colony algorithms, particle swarm methods, adaptive and learning algorithms and the use of these algorithms in solving continuous and discrete problems that arise in engineering applications. [Offered: S]', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 457B', 'Fundamentals of Computational Intelligence', 0.5, 'LEC, TUT', 'Introduces novel approaches for computational intelligence based techniques including knowledge-based reasoning, expert systems, fuzzy inferencing and connectionist modeling based on artificial neural networks. The focus is on the use of soft computing approaches to deal effectively with real world complex systems for which their mathematical or physical models are either non-tractable or are difficult to obtain. The main thrust is on designing computationally intelligent systems with human like capabilities in terms of reasoning, learning and adaptation. Tools of computational intelligence could be used in a wide range of engineering applications involving real world problems such as in planning problems, intelligent control, autonomous robotics, speech understanding, pattern analysis, network design, face recognition, communication systems to name a few. [Offered: W]', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 458', 'Computer Security', 0.5, 'LAB, LEC, TST, TUT', 'Introduction to computer security. Models of security. Elementary cryptography. Software security, vulnerabilities, threats, defenses and secure-software development processes. Threats to networks and defenses. Security issues at the application layer. Secure design principles, techniques and security evaluation. Privacy, ethics and legal issues. [Offered: S]', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 459', 'Programming for Performance', 0.5, 'LAB, LEC, TST, TUT', 'Profiling computer systems; bottlenecks, Amdahl''s law. Concurrency: threads and locks. Techniques for programming multicore processors; cache consistency. Transactional memory. Streaming architectures, vectorization, and SIMD. High-performance programming languages. [Offered: W]', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 462', 'Electrical Distribution Systems', 0.5, 'LAB, LEC, TST, TUT', 'This course provides the students an understanding of the operation, analysis and design of electric power distribution systems, starting with estimation of the loads on the network to the detail design of the distribution system networks. The primary objective of the course is to provide students with the skills to understand the analytical and design methods and modern tools for solution of problems associated with electric distribution system engineering. [Offered: S]', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 463', 'Design & Applications of Power Electronic Converters', 0.5, 'LAB, LEC, TST, TUT', 'Principles of power conditioning. Switching characteristics of power semiconductor devices. Computer simulation of power electronic circuits. Analysis, design, and applications of power converters. [Offered: S]', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 464', 'High Voltage Engineering and Power System Protection', 0.5, 'LAB, LEC, TST, TUT', 'The course provides the fundamentals concepts of generation and measurements of high voltage ac, dc, and impulses. Briefly introduces the students to basic conduction and breakdown mechanisms of insulating materials. The scope of this course also includes understanding the basic protection system, studying the principles for protecting different elements and studying different technologies used in designing protective relays. Exposure to several state-of-art high voltage testing techniques of power system components will ensure that students have knowledge of the industrial solutions to the management of the problems associated with overvoltage and the protection mechanisms used. [Offered: W]', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 467', 'Power Systems Analysis, Operations and Markets', 0.5, 'LAB, LEC, TUT', 'This course provides a basic understanding of the main issues relevant to the operation, analysis and management of power grids, and gives an introduction to the functioning of electricity markets. The course covers the following main technical and economic issues relevant to system operators, utilities and analysts: power system economic operations; short-term operation of power systems; power flow; introduction to optimal power flows; overview of electricity markets; fault calculations; and basic concepts in power system stability and control. [Offered: W]', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 473', 'Radio Frequency and Microwave Circuits', 0.5, 'LAB, LEC, TST, TUT', 'Review of transmission line and scattering matrix representation of radiofrequency (RF) circuits, multiport RF networks, modern RF and microwave planar technology, lumped and distributed microstrip circuits, microwave couplers, Hybrids, resonators, filters, Low-noise amplifiers (LNAs), RF oscillators and mixers, CAD tools for RF circuits, Hybrid and monolithic RF circuits. [Offered: S]', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 474', 'Radio and Wireless Systems', 0.5, 'LAB, LEC, TST, TUT', 'Modern transmitter and receiver architectures, Noise and linearity in radio and wireless systems, Design considerations of RF/microwave subsystems, radio and wireless system designs, CAD tools for radio and wireless systems, Antennas, Radio wave propagation models, Indoor radio, Satellite communication, Personal communication systems (PCSs). [Offered: W]', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 475', 'Radio-Wave Systems', 0.5, 'LAB, LEC, TUT', 'Review of Maxwell''s equations and uniform plane waves. Electromagnetic wave propagation, reflection and transmission through different uniform media, metallic waveguides, multilayer structures, radiation theory, transmitting and receiving antenna, antenna arrays and applications, simple radio-wave propagation models, radio transmission systems and wireless networks. [Offered: S]', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 477', 'Photonic Devices and Systems', 0.5, 'LAB, LEC, TUT', 'Review of Maxwell''s equations and electromagnetic plane waves, Dielectric waveguides, Optical fibers, lasers and photonic transmitters, Photodetectors and photonic receivers, optical amplifiers, and photonic systems and networks. [Offered: W]', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 481', 'Digital Control Systems', 0.5, 'LAB, LEC, TST, TUT', 'Performance specifications for design. Dynamic system modelling and basic system identification. Dealing with basic nonlinear effects. Sampled data systems. Discrete-time system stability and dynamic performance. Digital control system design: emulation methods, z-domain, frequency domain, pole placement. Implementation of digital controllers. [Offered: S]', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 492A', 'Engineering Design Project', 0.5, 'PRJ', 'Individual and group work comprising the design activity and report-preparation phases of the engineering design project. The team-oriented project is to comprise a significant design experience based on the knowledge and skills acquired by students in previous courses and on cooperative work terms. Project groups reconfirm project approval, establish and maintain progress monitoring through a faculty consultant, complete the design work, and submit a written interim report. Groups also prepare the written final report and presentations delivered in ECE 492B. [Offered: F, W, S, last offered Spring 2012]', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 484', 'Digital Control Applications', 0.5, 'LAB, LEC, TUT', 'Dynamic system modeling: linear, nonlinear, state-space, sample data systems, computer simulation, system identification. Discrete system stability and dynamic performance. Nonlinear system analysis, limit cycles. Digital control system design: emulation methods, z-domain, frequency domain, pole placement. Implementation of digital controllers. Laboratory projects in computer control of mechatronic and other systems. [Offered: F, W]', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 486', 'Robot Dynamics and Control', 0.5, 'LAB, LEC, TUT', 'Homogeneous transformations. Kinematics and inverse kinematics. Denavit-Hartenberg convention. Jacobians and velocity transformations. Dynamics. Path planning, nonlinear control. Compliance and force control. [Offered: S]', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 488', 'Multivariable Control Systems', 0.5, 'LEC, TUT', 'Review of feedback control design fundamentals; SISO controller parameterizations; the fundamental effect of MIMO interaction; introduction to state-space models in continuous and discrete time; SISO techniques for MIMO design; optimal control; model-predictive control design; state estimation; decoupling, MIMO PID control design; applications in areas such as aerospace systems. [Offered: W]', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 493', 'Special Topics in Electrical and Computer Engineering', 0.5, 'LEC, TUT', 'Special courses on advanced topics will be offered from time to time, when resources are available. For current offerings, inquire at the ECE Undergraduate Office or check the ECE website.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 498A', 'Engineering Design Project', 0.5, 'PRJ, SEM', 'Team-oriented design-project which comprises a significant design experience based on the knowledge and skills acquired by students in previous courses and on co-operative work terms. Development of the design specification and plan documents, followed by the initial design work. [Offered: F, W, S]', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 498B', 'Engineering Design Project', 0.5, 'PRJ, SEM', 'Completion of the design cycle started in ECE 498A and communication of the engineering design work. Submission of a written final report. Lecture-style technical presentation by group members. Poster-style technical presentation with group members available to discuss the project. [Offered: F, W, S]', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 600', 'Analytical Methods for Electrical and Computer Engineering', 0.5, 'LEC', 'This course will discuss fundamental analytical methods required in Electrical and Computer Engineering. Topics include: linear differential equations; elementary linear systems; RLC circuits with engineering applications; numerical methods; basic statistics and optimization.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 499', 'Engineering Project', 0.5, 'PRJ', 'An engineering project requiring the student to demonstrate initiative and assume responsibility. The student will arrange for a faculty supervisor prior to registration. Students can propose their own topic. A project report is required at the end of the term.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 602', 'Introduction to Optimization', 0.5, 'LEC', 'Fundamental optimization techniques.  Modelling. Shortest path. Network flow. Matching. Set packing, covering partitioning.  Branch and bound. Cutting Planes, Dynamic programming.  Search Heuristics.  (Students will gain valuable background in optimization techniques that are applicable to a wide range of engineering problems.  They will also gain experience using a state of the art optimizer, in solving an optimization problem of their own choice using techniques discussed in the course).', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 613', 'Image Processing and Visual Communication', 0.5, 'LEC', 'This course covers the fundamental concepts and methods, as well as state-of-the-art theories and technologies in the field of image processing and visual communications. Topics include fundamental digital image and video processing methods; image analysis and understanding; statistical image modeling and perception; and robustness, scalability and security issues in visual communications.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 603', 'Statistical Signal Processing', 0.5, 'LEC', 'Optimum mininum mean-square error (MMSE) Wiener filtering.  Parametric and non-parametric spectrum estimation.  Eigenstructure-based frequency estimation. Statistical parameter estimation using maximum likelihood (ML), maximum a posteriori probability (MAP), minimum mean-square error (MMSE) and least squares (LS) methods.  Adaptive signal processing using least-mean-squares (LMS) and recursive least-squares (RLS) approaches.  Discrete-time Kalman filtering.  Recommended background:  ECE 316: Probability Theory/ECE 342: Signals and Systems/ECE413: Digital Signal Processing/ECE 604: Stochastic Processes.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 604', 'Stochastic Processes', 0.5, 'LEC', 'Theory of random variables, vectors and processes.  Conditional probabilities and expectations.  Convergence of sequences of random variables.  Markov chains in discrete and continuous time.  Poisson processes.  Basic renewal processes.  Stationary random processes, correlation, and power spectra.  Emphasis on problem solving using probabilistic approaches.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 605', 'Queueing Systems', 0.5, 'LEC', 'Introduction to queueing theory, queueing models, performance measures, performance analysis and evaluations, Poisson arrivals and exponential service times, Little''s formula, Markov and semi-Markov processes, birth-death processes, single server and multiserver queues, single stage and tandem networks, open and closed networks.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 606', 'Algorithm Design and Analysis', 0.5, 'LEC', 'This is an introductory course on algorithms at the graduate level.  It assumes familiarity with basic data structures such as lists, queues, trees and graphs, and emphasizes creativity in the design of algorithms, and rigorous analysis.  Correctness (soundness and completeness) and efficiency (with respect to average-, best- and worst-case time and space) properties are considered in the context of algorithms for classes of problems such as optimization and decision problems.  The course also gives insights into when a problem may be intractable, and how we may deal with intractability.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 607', 'Algebraic Fundamentals for Computation, Communication and Control', 0.5, 'LEC', 'Equivalence relations and congruences.  Morphisms, semigroups and monoids.  Groups:  cyclic groups, subgroups and quotient groups. Rings: subrings, quotient rings, integral domains and fields.  Partial orders, lattices and fixpoints of monotone operators.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 610', 'Broadband  Communication Networks', 0.5, 'LEC', 'This course is concerned with the fundamentals of broadband communication networks including network architecture, Switch fabrics, design methodology; traffic management, connection admission control (CAC), usage parameter control (UPC), flow and congestion control; capacity and buffer allocation, service scheduling, performance measures, performance modeling and queueing analysis.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 611', 'Digital Communications', 0.5, 'LEC', 'Representation of bandpass signals and systems, modulation and demodulation for the additive white Gaussian noise channel, optimal demodulation for signals with random phase, noncoherent detection for binary and M-ary orthogonal signals, hard and soft decision decoding for linear codes, concatenated codes, performance of coded modulation systems, characterization of fading multipath channels, diversity techniques, performance of coded systems on fading channels, direct sequence and frequency hopped spread spectrum systems.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 612', 'Information Theory', 0.5, 'LEC', 'An introduction to information measures, entropy, mutual information and information divergence, noiseless codes and the noiseless coding theorem, channel capacity for discrete and continuous channels, randomly chosen code words, the noisy coding theorem, error rate exponents, computational cut-off rate and its application to digital communications.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 614', 'Communications Over Fading Dispersive Channels', 0.5, 'LEC', 'Overview of mobile communications, characterization and modeling of wireless fading dispersive channels, optimum receiver structure, transmission performance in fading channels, diversity and performance improvement, co-channel interference, spread spectrum and multiple access, capacity analysis in cellular environments.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 617', 'Data Compression with Applications to Speech and Image Coding', 0.5, 'LEC', 'This course consists of three parts: Part 1 will be concerned with the definitions of entropies as information measures and the derivation of the rate distortion function of Gaussian sources, which will form the basis for performance comparison. Part 2 will discuss the derivations, design and performance of certain compression techniques, including dpcm/entropy coding, predictive coding, linear predictive coding (LPC), adaptive predictive coding (APC), vector quantization, and tree and trellis coding. Part 3 will consider applications of compression techniques to speech and image processing.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 621', 'Computer Organization', 0.5, 'LEC', 'Organization of high performance digital computers, high speed arithmetic algorithms, control unit and data flow organization. Pipeline systems. Stack machines, associative processors, parallel processors. Performance evaluation.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 627', 'Register-transfer-level Digital Systems', 0.5, 'LEC', 'Syntax, semantics, and usage of the VHDL hardware description language. Modeling concurrency in VHDL, in other hardware description languages, and using other simulation techniques.  Modeling, design, and implementation at the register-transfer level.  Functional verification techniques.  Timing analysis.  Introduction to power analysis and optimization.  Introduction to faults and testing.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 628', 'Computer Network Security', 0.5, 'LEC', 'Evolution of computer security. Types of security threats, hardware threats, software threats, physical threats, cryptanalysis. The theory of secure message passing. Methods of encryption, private networks, Data Encryption Standard, Public Key Cryptosystems. Secrecy and Privacy in a network environment, long haul networks, local area networks. Protocols for computer network security.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 630', 'Physics and Models of Semiconductor Devices', 0.5, 'LEC', 'Basics of semiconductor physics.  Physical principles and operation of p-n junctions, metal/semidconductor contacts, MOS capacitors, MOS field-effect transistors, and related optoelectronic devices.  Short-channel MOSFETs, modern MOSFETs, and future transistor technologies.  Introduction to device simulators and SPICE models.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 631', 'Microelectronic Processing Technology', 0.5, 'LEC', 'Sources and purification of materials, crystal structure, diffusion, oxidation, ion implantation, alloying expitaxy, impurity profiling methods, metallisation, photo lithography. Technologies for VLSI.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 632', 'Photovoltaic Energy Conversion', 0.5, 'LEC', 'Physical source of solar radiation; direct & diffuse radiations; review of electronic materials; semiconductor concepts; optical absorption; generation and recombination processes in semiconductors; operating principles of photovoltaic  devices; homo- and hetero- junction devices; equivalent circuits; quantum efficiency; current-voltage characteristics; Efficiency limits in photovoltaic devices; short circuit current and open circuit voltage losses; temperature effect; material-imposed limits; theoretical and practical limits; Photovoltaic device design and fabrication; silicon-based devices; gallium arsenide devices; thin film devices; device simulation; fabrication technologies; Advanced photovoltaic concepts; nano-structure and organic PV devices; System-level photovoltaics; module structure and design; back-end electronics; stand-alone and grid-interactive systems; photovoltaic hybrid systems.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 636', 'Advanced Analog Integrated Circuits', 0.5, 'LEC', 'Design of MOS and bipolar analog integrated circuits at the transistor level, with an emphasis on the design of single-stage and multi-stage op amps.  Related topics include biasing, compensation and noise will be covered.  In addition, higher level analog and mixed analog/digital subsystems will be discussed, time permitting.  Students enrolling in this course are expected to have a background equivalent to the material covered in ECE 242, formerly ECE 332.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 637', 'Digital Integrated Circuits', 0.5, 'LEC', 'Design of CMOS digital integrated circuits at the transistor level. Related topics include MOSFET switch and 1-V models, logic gate design, transistor sizing, interconnect parasitics, gate delay, timing design, logical effort, static and dynamic  logic families, arithmetic structures, latch and flip-flop elements, memory cells and arrays, and input/output circuitry. Students enrolling in this course are expected to have a background equivalent to the material covered in ECE 242, formerly ECE 332.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 638', 'Semiconductor Microtransducers', 0.5, 'LEC', 'Overview of basic physical processes in semiconductors and their interactions with thermal, radiant, mechanical, or chemical input signals. Review of microtransducer materials and technologies. Principles and operation of integrated thermal sensors (thermistors, thermopiles, active IC elements, pyroelectric devices), radiant sensors (optoelectronic, infrared and radiation sensors), magnetic sensors (Hall devices, magnetoresistors, magnetotransistors), (mu)mechanical sensors (piesoelectric and peizoresistive devices, flow sensors and resonant structures), chemical sensors (ion, gas and humidity sensors, CHEMFETs, biosensors, SAW devices), and micro-actuators ((mu)valves, (mu)pumps, (mu)motors). Overview or pertinent interface and modelling techniques.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 639', 'Characteristics & Applications of Amorphous Silicon', 0.5, 'LEC', 'Growth and structure of amorphous silicon (morphology, growth mechanisms, silicon bonding structure, continuous random network, hydrogenation). Electronic states in amorphous silicon (density of states, band tails, optical transitions, defects and defect states). Doping of amorphous silicon, defect reactions and metastable effects. Electronic transport and recombination in amorphous semiconductors. Using amorphous silicon in practice (contacts and surfaces). Applications of amorphous silicon (Schottky diodes, solar cells, optical and radiation sensors, thin film transistors, flat panel displays, large-area electronics).', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 644', 'Computer Aided Circuit Analysis and Design', 0.5, 'LEC', 'Formulation of equations for arbitrary circuits, active network analysis; sensitivity calculations in the frequency domain; simple integration methods for time domain solution; numerical laplace inversion for transient solution of linear lumped and distributed networks; solution of nonlinear circuits; detailed time domain solution of nonlinear networks; simulation of switched networks in time domain; introduction to switched capacitor networks; iterative simulation methods.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 647', 'Algorithms for Physical Design of Digital Integrated Circuits', 0.5, 'LEC', 'An introduction to the problems and algorithms that arise during the Computer-Aided Design (CAD) of digital circuits.  Course emphasis is on the backend of the CAD flow such as algorithms for solving problems including: technology mapping, partitioning, floor-planning, clustering, placement, routing and physical synthesis.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 685', 'Stochastic Processes for Dynamical Systems', 0.5, 'LEC', 'Random experiments and measure spaces, stochastic independence, Borel-Cantelli theorem, random variables, Chebyshev inequality, cylinder sets and the Borel sigma-algebra on function spaces, Daniell-Kolmogorov consistency theorem and random processes, separable random processes and Kolmogorov conditions for continuous sample-paths, weak and strong laws of large numbers, conditional expectations, construction of Markov processes especially Brownian motion, sample-path properties of Brownian motion.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 651', 'Foundations of Software Engineering', 0.5, 'LEC', 'Fundamentals of software requirement analysis, software development as an engineering activity, basic process models, software specifications, modularity, cohesion, coupling, encapsulation, information hiding, principles of object oriented design, software project management, quality assurance and control.  Priniciples of Software Architecture:  Fundamental software architecture styles, synchronous & as synchronous communication of software components.  Languages for software design specification:  UML (class diagrams, sequence diagrams, collaboration diagrams, state diagrams).  Overview of verification and validation techniques.  Maintenance, evolution and reengineering, configuration management.  Software metrics, quality assurance, fundamental cost and effort prediction models.  Trends in software engineering (e.g., model-driven development, agile approaches).', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 652', 'Methods and Principles of Safety-critical Embedded Software', 0.5, 'LEC', 'The application of formal test methods based on the structural and behavioural properties of software systems at the unit integration, and system testing levels; for conventional and object-oriented implementations. Related Background: Prior knowledge of a programming language and a certain maturity in compilers, operating systems, abstract data structures, object-oriented and programming skills.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 653', 'Software Testing, Quality Assurance and Maintenance', 0.5, 'LEC', 'Introduces students to systematic testing of software systems.  Software verification, reviews, metrics, quality assurance, and prediction of software reliability and availability.  Students are expected to have programming experience with reading and writing code for large projects.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 654', 'Software Reliability Engineering', 0.5, 'LEC', 'The course consists of two related parts.  The first part deals with the engineering of reliable software.  It introductes basic software reliability concepts, descritbes relevant models and discusses processes for engineering of reliable software, including schemes and patterns for the design of reliable and fault tolerant software.  The second part addresses development of secure software.  It presents key software security concept, techniques and models, overviews major software security vulnerabilities and their exploitation, and considers processes for development of secure software.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 655', 'Protocols, Software and Issues in Mobile Systems', 0.5, 'LEC', 'This course is concerned with the architecture, protocol and software aspects of mobile systems.  The topics to be discussed include mobile communication and computing systems, supporting ad hoc networks, mobile network and transport layers, wireless application protocol, support for mobility, service advertisement and discovery in mobile systems, and emerging issues such as enviornment-aware software and low-power protocols and software.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 656', 'Database Systems', 0.5, 'LEC', 'This course covers data models, file systems, database system architectures, query languages, integrity and security and database design.  Students attending this course should have at least a 4A level Electrical Engineering or Computer Engineering background.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 657', 'Tools of Intelligent Systems Design', 0.5, 'LEC', 'Conventional approaches for tackling complex systems are usually implemented under the assumption of a good understanding of the process dynamics/functionalities and its operating environment.  These techniques fail, however, to provide satisfactory results when applied to ill-defined processes (for which analytical and experimental modeling may not be easily obtained) that may operate in unpredictable and possibly noisy environment.  Recent developments in the area of intelligent systems and soft computing have presented powerful alternatives for dealing with the behavior of this class of systems.  This course outlines fundamentals of soft computing based design approaches using such tools as approximate reasoning, fuzzy inferencing, neural networks, evolutionary algorithms, and neuro-fuzzy systems.  Fundamentals and advances on these procedures are outlined along with their potential applications to various real world applications in virtually most fields of engineering including pattern recognition, system planning, classification, power generation, intelligent transportation, systems and control, intelligent mechatronics, optimization, communication, robotics and manufacturing, to name a few.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 658', 'Component Based Software', 0.5, 'LEC', 'Building large-scale and complex software systems from available parts with the goal of increasing return on investment, decreasing time to market, and assuring quality and reliability.  The course covers the basic software component concepts, overview of advanced topics on software components and component-based software engineering from research and practice.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 661', 'HVDC and FACTS', 0.5, 'LEC', 'Application of state-of-the-art high power electronics to power transmission and distribution systems.  The emphasis will be on three important application areas - high voltage direct current (HVDC) transmission systems, flexible AC transmission Systems (FACTS) and Custom Power devices.  The course addresses FACTS controllers including: static synchronous compensators (STATCOM), static synchronous series compensators (SSSC), interphase power flow controllers (IPFC) and unified spower flow controllers (UPFC).  Custom power devices such as shunt DSTATCOM, series compensating DVR and unified power quality conditioners (UPQC) are also discussed.  This course will concentrate on the operating principles, models, and control and performance of power electronic systems used in these applications.  Background required - ECE 463 or equivalent.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 662', 'Power Systems Analysis and Control', 0.5, 'LEC', 'The definitions and concepts associated with short circuit, power flow and stability analysis are presented and discussed.  Models, techniques and tools used for these types of studies are discussed with a practical perspective, and applied to the comprehensive analysis of a typical test system.  Controls and protections for voltage, angle and frequency control and regulation, such as Automatic Voltage Regulators (AVR), Power System Stabilizers (PSS) , Automatic Generation Control (AGC), under-voltage and under-frequency relays, are also studied and applied.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 674', 'Analysis and Computation of Electric and Magnetic Fields', 0.5, 'LEC', 'This course studies the static fields, capacitance and inductance by the following techniques. Classical image technique, variable separation, conformal and Schwarz-Christoffel transformations, the moment method, the optimized-simulated sources method, the variational techniques and the upper and lower bounds.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 663', 'Energy Processing', 0.5, 'LEC', 'This course is intended to embrace power electronic aspects together with the broader issues of the systems of energy processing for emerging technologies. Within this framework, topics include performance, selection and optimization of power semiconductor devices including thyristors, GTOs, triacs, BJTs, MOSFETs, IGBTs and MCTs; classification, circuits and performance of converting circuits including rectifiers, inverters, choppers and cycloconverters; control and protection of conversion circuits; requirements and constraints of energy processing systems such as variable speed drives, high energy battery installations, transportation, solar and wind generators and industrial processes.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 664', 'Power System Components and Modeling', 0.5, 'LEC', 'The basic structure, functional characteristics and protection schemes of the main components that make up a power system are studied, in particular generators, transformers, transmission lines, cables, loads, HVDC and FACTS controllers.  Models of these components for detailed electromagnetic transient analysis and phasor-based studies such as power flow and stability studies are discussed in detail, and various models are compared and validated through simulations performed with commercial software packages.  Required Background - ECE 631 or equivalent.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 665', 'High Voltage Engineering Applications', 0.5, 'LEC', 'This course deals with the new emerging technology in high voltage engineering. It is divided into five parts. The first part of the course in a review of the basic fundamentals of high voltage engineering. The second part deals with the new techniques in partial discharge measurements. The third part of the course deals with measurements of charge distribution in solid dielectric. The fourth part of the course discusses the optical fiber based monitoring techniques of the high voltage high power equipment. The last part of the course deals with the application of high voltage engineering in power systems.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 666', 'Power Systems Operation', 0.5, 'LEC', 'The course deals in details of power system operation in the context of restructured electricity markets. Basics of power system operation - economic load dispatch, concept of marginal cost, Kuhn-Tucker''s conditions of optimum, unit commitments, hydro-thermal coordination, optimal power flow analysis and security constrained economic dispatch are introduced.  On the topic of transmission open access, the course discusses transmission pricing paradigms and the role of distribution factors in pricing.  Transmission operations cover congestion management methods and firm transmission rights.  Ancillary services procurement and pricing and power system security are discussed.  Background - ECE 467 or equivalent.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 667', 'Sustainable Distributed Power Generation', 0.5, 'LEC', 'This course covers topics related to sustainable and clean energy resources; distributed generation and utility interfacing.  The following topics are covered: Wind power generators; construction; operation theory, modeling and analysis.  Wind turbine interfacing techniques with the grid.  Photovoltaic energy sources; construction, modeling, loading characteristics and interfacing requirements.  Fuel cells; types, construction, modeling and characteristics, operation theory and interfacing requirement.  Distributed generation concept; Barriers to DG interfacing; Reactive power control applications using the DG interfacing; Ancillary services supplied by DG. System protection requirements with DG.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 668', 'Distribution System Engineering', 0.5, 'LEC', 'This course covers the following topics in distribution engineering:  Load Characteristics and distribution system load forecasting; Distribution system planning; Distribution system automation; Design and application of distribution transformers; Design and optimal operation of sub-transmission lines and distribution systems; Distribution system voltage regulation; Reactive power control for distribution systems; Application of capacitors to distribution systems; Calculation of voltage drops in lateral distribution systems; Calculation of power losses in distribution systems; Introduction to distribution system protection. Background Required - basic knowledge of power system operation and analysis.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 669', 'Dielectric Materials', 0.5, 'LEC', 'Selected topics from theory of solid insulation breakdown. Conduction process in insulating liquids. Hydro dynamic processes. Theories of breakdown due to gaseous inclusion, moisture inclusion and particle contamination. Kinetic theory of gases. Breakdown mechanism in uniform electric fields. Corona and breakdown in non-uniform electric fields. Compressed gas insulation.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 671', 'Microwave and RF Engineering', 0.5, 'LEC', 'Fundamentals of microwave and RF circuit analysis, design and measurements; Generalized transmission line analysis; S-parameters; Coupler and filter design; Diode-detectors, and mixers; Low-noise and power amplifiers, oscillators; Computer aided design of RF circuits.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 672', 'Optoelectronic Devices', 0.5, 'LEC', 'Overview of optical properties of semiconductors and elements of plane wave propagation, theory and design of light emitting diodes, laser diodes, and detectors, optical spectra and transitions, spontaneous and stimulated emission, population inversion, carrier and optical confinements in heterostructures, quantum-well lasers, optoelectronic detectors, bandgap engineered graded structures, staircase type or superlattice structures for detectors, detailed quantum efficiency calculations and detector noise considerations, Introduction to monolithic integrated circuits.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 675', 'Radiation & Propagation of Electromagnetic Fields', 0.5, 'LEC', 'Mathematics of time varying electromagnetic fields, linear antennas self and mutual impedance, aperture antenna, wave diffraction theory, geometrical theory of diffraction (GTD).', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 678', 'Fourier Optics and Optical Signal Processing', 0.5, 'LEC', 'Analysis of two-dimensional linear systems, Scalar diffraction theory, Fourier transforming properties of lenses, Frequency analysis of optical imaging systems, Spatial filtering and optical information processing, Synthetic Aperture Radar (SAR) - data processing, Wavefront-reconstruction.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 682', 'Multivariable Control Systems', 0.5, 'LEC', 'An introduction to control theory for linear time-invariant finite-dimensional systems from both the state-space and input-output viewpoints. State-space theory: the concepts of controllability, observability, stabilizability, and detectability; the pole-assignment theorem; observers and dynamic compensation; LQR regulators. Input-output theory: the ring of polynomials and the field of rational functions; the algebra of polynomial and rational matrices; coprime factorization of transfer matrices; Youla parametrization, introduction to optimal control.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 683', 'System Identification', 0.5, 'LEC', 'Estimation theory, linear and nonlinear regression, numerical techniques for parameter estimation for static and dynamic models, the Kalman filter and extensions, stochastic approximation, empirical dynamic models - especially for linear sampled data systems with stochastic inputs.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 686', 'Filtering and Control of Stochastic Linear Systems', 0.5, 'LEC', 'This is a course on continuous-parameter state estimation and control for stochastic linear systems. It is based on a single unifying theme, namely that state estimation in linear systems is equivalent to projection onto a closed linear subspace generated by an observation process in a Hilbert space of random variables. This formulation of state estimation leads to the innovations theorem of Kailath, and this in turn has a number of corollaries of considerable practical importance, such as the Kalman-Bucy filtering formulae and the Rauch-Tung-Striebel prediction formulae which are much used for example in problems of inertial guidance and control in aerospace, in stochastic optimal control, and (more recently) in econometrics.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 687', 'Robot Dynamics and Control', 0.5, 'LAB, LEC, TUT', 'Homogeneous transformations. Kinematics and inverse kinematics. Denavit-Hartenberg convention. Jacobians and velocity transformations. Dynamics. Path planning, nonlinear control. Compliance and force control.  [Offered: S]', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 688', 'Nonlinear Systems', 0.5, 'LEC', 'Equilibrium points, linearization; second order systems; contraction mapping principle; existence and uniqueness of solutions to nonlinear differential equations; periodic solutions; Lyapunov stability; the Lure problem; introduction to input-output stability, introduction to nonlinear control techniques.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 700', 'Special Topics in Electrical and Computer Engineering', 0.5, 'LEC', 'None', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 710', 'Special Topics in Communications and Information Theory', 0.5, 'LEC', 'None', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 711', 'Special Topics in Digital Communications', 0.5, 'RDG', 'None', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 720', 'Special Topics in Computers and Digital Systems Software', 0.5, 'LEC', 'None', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 725', 'Computer-Aided Verification', 0.5, 'LEC', 'Application of formal methods to the verification of computer-based systems. Algebraic and automata preliminaries. Temporal logic and model checking. Decision procedures. Mechanized theorem proving. Advanced topics chosen by the instructor.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 730', 'Special Topics in Solid State Devices', 0.5, 'LEC, RDG', 'None', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 732', 'Simulation of Semiconductor Microtransducers', 0.5, 'LEC', 'Transduction principles of thermal, radiant, magnetic, mechanical, and chemical microsensors and microactuators, model equations accounting for the various signal interactions, modeling approaches, numerical solution schemes, simulation examples, mixed-signal simulation techniques. Required Background: Knowledge of basic (undergraduate) numerical methods  and device physics.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 731', 'CCD Image Sensors', 0.5, 'LEC', 'As the cost of computing decreases at a phenomenal rate, the use of large format CCD area image sensors increases. Applications of CCD image sensors include industrial such as web inspection, document scanning, manufacturing product inspection for quality and process control, manufacturing product sorting and many other industrial applications. Multimedia and computers increasingly use their CCD image sensors or active pixel CMOS type image sensors. This course will start with the basic theory of CCD image sensors and graduate to CMOS type solid state image sensors. It will concentrate on the state of the art of this field and will deal with the basic device theory, the circuit design and architectures. Photosensitivity, noise, modulation transfer function and other aspects of spatial resolution will be covered. Related Background: E&CE semiconductor and circuit undergraduate courses.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 738', 'Low Power VLSI Circuits for Wireless Communication', 0.5, 'LEC', 'This course covers the system level design of integrated circuits for wireless transceivers. Specific mixed analog/digital circuits such as: mixers, A/D and D/A converters (Nyquist rate and oversampled) for IF digitizing as well as switched capacitor filters for IF and baseband processing will be studied. Related Background: Basic knowledge of Analog Integrated Circuits.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 740', 'Special Topics in Electronic Circuits', 0.5, 'LEC', 'None', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 741', 'AHDL Modeling of Circuits and Systems', 0.5, 'LEC', 'The separation of models from the simulation engine. The interaction of models and the simulation engine. Basics of a simulation engine. Mathematical tools for modeling. Modeling using modern Analog Hardware Description Languages (AHDLs). AHDL applications to: large circuit simulation, efficient model development/verification, specialized simulators.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 750', 'Special Topics in Computer Software', 0.5, 'LEC, RDG', 'None', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 752', 'Software Architecture & Design', 0.5, 'LEC', 'Advanced study of software design and architecture; representation of architecture/design; software design methods; patterns in software design; analysis, assessment, verification and quality control for software design; case studies, current research issues and challenges. Related Background: Basic exposure to programming using C or C++, previous graduate or undergraduate course in software specifications, or software engineering should be sufficient.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 753', 'Parallel and Distributed Systems', 0.5, 'LEC', 'Models for concurrent programming; data programming; data parallel models for SIMD and MIMD computers; explicit, semiautomatic, and automatic approaches; data parallel compilers; software engineering issues such as program and data partitioning; task mapping and scheduling, concurrent program design, testing and debugging of concurrent programs, performance tuning, etc. Related Background: Basic exposure to programmin using C or Fortran, previous graduate or undergraduate course in operating system or concurrent programming would be helpful.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 754', 'Software Bug Detection and Tolerance', 0.5, 'LEC', 'This course discusses a broad range of techniques in reducing the negative impact of software bugs to improve software reliability and security. Main topics include: fundamental concepts of software reliability and security, software bug detection and diagnosis, software bug tolerance and recovery, and text analytics.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 755', 'Safety-critical Real-time Embedded Software', 0.5, 'LEC', 'This course covers the concepts and theory required to understand, design, program, and analyze safety-critical software-intensive real-time systems (and how you get the most out of your deadline-constrained life). The course specifically covers the following topics: challenges and problems of modern embedded software, building blocks of embedded systems, software modeling and design, UML, UML/MARTE, the scheduled model, the synchronous model, worst-case execution time analysis, real-time scheduling theory, interrupt handling for real-time systems, safety analysis and argumentation, and system performance measurement and evaluation.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 761', 'Applied Nonlinear Systems Theory', 0.5, 'LEC', 'This course concentrates in the numerical techniques used for the study of various stability issues in nonlinear systems characterized by sets of differential equations and algebraic constraints. Lyapunov stability theory is used to investigate in detail regions of attraction and their dynamic manifolds. Local and global bifurcations in nonlinear systems are studied together with the numerical techniques used to detect them. Different mechanisms that lead nonlinear systems to chaotic behaviour are also investigated. All concepts are illustrated using power system examples.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 760', 'Special Topics in Power Systems and High Voltage Engineering', 0.5, 'LEC', 'None', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 768', 'Power System Quality', 0.5, 'LEC', 'This course addresses all the dimensions of Power Quality (PQ) issues including mitigation strategies.  The focus is mainly on qualitative concepts and comprehensive coverage.  This course introduces PQ definitions, limitations, related international standards and mathematical techniques for PQ analysis of Power Systems.  Different identification, localization and classification techniques for PQ problems will be investigated.  The various kinds of PQ problems and their effects on load/system equipment will be looked at in detail.  Mitigation strategies like passive filtering, active and hybrid power filtering, static VAT compensation, DVR, UPQC, etc. will be investigated.  Areas covered include voltage sags, interruptions, transients, flickers, harmonics and  modeling and simulation of utility systems.  Grounding imperfection as a major cause for PQ problems will be addressed in detail.  Moreover, the requirements and impacts of distributed generation (DG) on network power quality will be studies in detail.  Effects of DG on voltage regulation, relaying losses, islanding and standards will be examined.  Background Required - Basic understanding of modeling of power system elements and analysis techniques.  Familiarity wit a programming language and/or a simulation package such as EMTDC/PSCAD and MATLAB is desirable.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 770', 'Special Topics in Antenna and Microwave Theory', 0.5, 'LEC, TUT', 'None', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 622A', 'Applied Microeconometrics', 0.5, 'LEC', 'This course provides an introduction to the analysis of microdata - data in which the unit of analysis in an individual decision-maker, such as a consumer, worker, or firm.  The course familiarizes students with the various types of mircodata structures and draws links between theoretical economic models and statistical models.  It devotes considerable attention to anlytical techniques for describing univariate and bivariate distributions, including the use of kernel density estimators and scatterplots.  The course addresses statistical inference using multivariate regressions, covering a series of techniques and issues of particular significance in the analysis of microdata.  Examples include Monte Carlo methods; bootstrapping; the use of sampling weight; omitted variable bias; measurement error; and instrumental variables.  Completion of an empirical project is a course requirement.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 780', 'Special Topics in Control', 0.5, 'LEC', 'None', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 781', 'Adaptive Control', 0.5, 'LEC', 'Adaptive control is an approach used to deal with the unavoidable problem of plant uncertainty. Rather than providing a fixed linear time-invariant controller, this approach yields a controller whoes parameters change with time. This controller typically consists of a linear time-invariant compensator together with a tuning mechanism which adjusts the controller gains; typical control objectives are stabilization and tracking.The bulk of the course will be centered on an identifier based approach. Here one chooses a model for the plant, whose parameters are unknown, and the plant parameters are recursively estimated; controller gains are computed assuming that the present estimate is corrent. We first study algorithms to carry out parameter estimation, we then look at various control laws, and finally these are combined to yield an adaptive controller. Related Background: knowledge of linear system.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 784', 'Introduction to Stochastic Calculus', 0.5, 'LEC', 'Monotone and Dynkin class theorems, introduction to discrete and continuous parameter martingales, stochastic integrals, Ito formula, Girsanov transformation. Held with: STAT 902.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 6601PD', 'Power System Components and Modeling', 0.5, 'LEC', 'Discuss in detail the basic structure, functional characteristics and protection schemes of the main components that make up a powers system, in particular generators, transformers, transmission lines, cables, loads, HVDC and FACTS controllers.  Understand the modelling and simulation of these components for detailed electromagnetic transients analyses, as well as phasor models for power flow and stability studies.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 6602PD', 'Power System Management & Electricity Markets', 0.5, 'LEC', 'The course will provide a comprehensive overview of power system operations and management, starting with a general understanding of system economics.  Many emerging issues in power sector deregulation- such as those in system reliability and system control- shall be discussed within the course.  The course will provide a perspective on operations planning models and train the participant to look at technical issues of power system operation simultaneously with the economic aspects.  Recommended Background:  Basic understanding of power system operation is required.  Knowledge of mathematical programming is desirable.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 6603PD', 'Electromagnetic Compatibility and Power Quality', 0.5, 'LEC', 'The course addresses all dimensions of Power Quality (PQ) issues including mitigation strategies.  The focus is mainly on qualitative concepts and comprehensive coverage.  The course introduces PQ definitions, limitations, related international standards and mathematical techniques for PQ analysis of power systems.  Different identification, localization and classification techniques for PQ problems will be investigated.  The various kinds of PQ problems and their effect on the load/system equipment will be looked at in detail.  The mitigation strategies such as passive filtering, active and hybrid power filtering, static VAR compensation, DVR, UPQC, etc. will be investigated.  Areas covered include voltage sags, interruptions, transients, flickers, harmonics and modeling and simulation of utility systems.  The grounding imperfection as a major cause for PQ problems will be addressed in detail.  Moreover, requirements and impact of distributed generation (DG) on network power quality will be studied in detail.  Effect of DG on voltage regulation, relaying, losses, islanding and standards will be examined.  Recommended Background:  Basic understanding of modeling of power system elements and analysis techniques is required.  Familiarity with a programming language and/or a simulation package such as EMTDC / PSCAD and MATLAB is desirable.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 6604PD', 'Distributed Generation', 0.5, 'LEC', 'The main objective of this course is to provide up-to-date knowledge about the technical and economical issues relating to the distribution generation.  In addition to an introduction to various generating technologies, a more detailed part will be included discussing various applications of power electronics.  The impacts of DG to the distribution system will be presented.  The focus will be on electrical issues such as grid connection, protection, control, and power quality.  In addition, the economical and regulatory issues will be addressed.  Recommended Background:  Basic understanding of modeling of power system elements and analysis techniques is required.  Familiarity with a programming language and/or a simulation package such as EMTDC/PSCAD and MATLAB is desirable.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 6605PD', 'Power System Protection', 0.5, 'LEC', 'Power system protections schemes are designed primarily to minimize the duration of a fault as well as to minimize the number of customers affect by the fault.  The scope of this course is to study the main elements and techniques for power system protection.  The course is divided into two main parts; protective equipment and protective techniques.  In the protective equipment; circuit breakers, relay, enclosures, fuses and isolating switches are discussed.  Different protection techniques dedicated to protect feeders, transformers, generators and motors are discussed in the second part of the course.  Recommended Background:  Basic knowledge in power system engineering is required, basic knowledge in optimization techniques, statistics, and electric circuits.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 6606PD', 'Distribution System Engineering', 0.5, 'LEC', 'This course deals with the essential aspects of distribution system engineering, starting with estimation of the loads on the network to the detail design of the distribution system networks.  The contents of this course are divided into three categories: Planning, design and operation.  In the planning part load forecasting, and planning strategies as well as distribution automation are discussed.  The design part includes the design of sub-transmission lines, distribution substations, and primary and secondary systems design considerations.  The operation part includes the voltage drop and power loss calculations, voltage regulation and application of capcitor to distribution systems.  Recommended Background:  Basic knowledge of power systems engineering is required, optimization techniques, statistics and electric circuits.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 6607PD', 'Operation and Control of Restructured Power Systems', 0.5, 'LEC', 'The course deals in detail with power system operation and control in the deregulated electricity market environment.  The topics include electricity market design and auction mechanisms, price formation, role of the independent system operator in pool versus bilateral markets, generation scheduling in deregulation, transmission pricing paradigms, congestion management, transmission rights, ancillary services procurement and pricing, and security management in deregulation.  A highlight of the course is the country- specific information provided on various operational aspects of restructured power systems worldwide.  Recommended Background:  Basic understanding of power system engineering is required.  Knowledge of mathematical programming is desirable.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 6608PD', 'Dielectrics and Electrical Insulation', 0.5, 'LEC', 'Theory of gases.  Photoelectric, thermal and field emission.  Ionization by collision, photoionization, thermal ionization and ionization by x-rays and cosmic rays.  Deionization becuase of recombination, negative ion formation, and diffusion.  Behaviour of charged particles in electric fields of low E/P and high E/P (E=electric field and P=pressure).  Breakdown Processes: Townsend mechanism, secondary effect, streamer formation from self sustained discharges to breakdown, breakdown in non-uniform fields, temporal development of breakdown, partial breakdown or corona discharges.  Solid and Liquid Dielectrics:  Types of solid and liquid insulating materials, their electrical thermal, chemical and mechanical properties; charge transport; surface discharges; breakdown mechanisms; effects of impurities on breakdown strength.  Electrohydrodynamics and its influence on breakdown mechanisms in liquids; electromechanical and intrinsic breakdown strength of solids.  Recommended Background:  Basic knowledge of physics of materials for Electrical Engineers (ECE 209 or equivalent).  Familiarity with electrical power system components is useful.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 6609PD', 'High Voltage Engineering Applications', 0.5, 'LEC', 'This course deals with generation and measurement aspects of high voltages and industrial applications of high voltage engineering.  The first part concentrates on generation of high voltage ac, dc and impluse voltages of both switching and lightning surges.  Measurements techniques based on different types of potential dividers and spark gaps for ac, dc, and impulse measurements will be covered.  The second part deals with generation and measurements of special types of high voltages needed for industrial applications.  The course also covers non-destructive measurements like surface and internal discharges, capacitance and loss factor and some optical techniques.  Recommended Background:  Basic knowledge of circuit analysis and low voltage measurement techniques is required.  Familiarity with electrical power system components is useful.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 6610PD', 'Power Electronic Converters', 0.5, 'LEC', 'This course covers a wide range of topics in power electronics including: power semiconductor devices with emphasis on their operating characteristics, power converter topologies for ac-to-dc, dc-to-dc, dc-to-ac and ac-to-ac conversions, multi-converters and multi-level converters, control techniques in power converters, modeling of power converters, applications of power converters, converter design aspects (snubber circuits, gate/base-drive circuits, thermal management, series/parallel combinations of switches), and computer simulation of power electronic systems.  Recommended Background:  Basic understanding of circuit analysis and control theory is required.  Familiarity with electric machinery and power systems is desirable.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 6611PD', 'Electric Machines and Motor Drives', 0.5, 'LEC', 'This course covers a wide range of topics in electric machines and motor drives including review of power electronic converter topologies, models and control techniques, review of electro-magnetics, dc machine structure, types and principles of operation, dc motor torque/speed characteristic, model, start-up and speed control techniques, synchronous machine structure and principles of operation, synchronous generator synchronization, loading and active and reactive power control, synchronous motor torque/speed characteristic, model, start-up and speed control techniques, induction machine structure and principles of operation, induction motor torque/speed characteristic, model, start-up and speed control techniques, single-phase induction motors, switched-reluctance motor structure, principles of operation and speed control, brushless dc motor structure, principles of operation and speed control, step motor structure, principles of operation and speed/position control, simulation of variable-speed drives, simulation tools, and industrial applications of motor drives.  Recommended Background:  Basic understanding of circuit analysis, power electronics and control theory.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 6612PD', 'FACTS: Models, Controls and Applications', 0.5, 'LEC', 'FACTS controllers are studied in detail from the functional, structural and control points of view.  The course concentrates on studying the most common thyristor-controlled FACTS controllers, in particular Static Var Compensators (SVC), Thyristor-Controlled Series Compensators (TCSC) and Thyristor Controlled Voltage and Phase Regulators (TCVR and TCPAR), and voltage-sourced converter controllers, specifically the Static Compensator (STATCOM), Static Synchronous Series Compensator (SSSC) and the Unified Power Flow Controller (UPFC).  Detailed and approximate models for various control strategies and practical applications of these controllers are discussed.  Recommended Background:  Basic understanding of power electronic converters and power systems.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 6613PD', 'Power System Analysis', 0.5, 'LEC', 'Understand the basic definitions and concepts associated with power systems analysis.  Discuss in detail techniques and tools for power system analysis, with a practical perspecitve.  The course will cover short circuit power flow, stability concepts including voltage, oscillatory, transcient and frequency stability.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 6614PD', 'Industrial Utilization of Electrical Energy', 0.5, 'LEC', 'Extensive electricity utilization represents one of the hallmarks of a modern society.  In this course, the basic cooncepts related to use of electric energy in various industrial applications and important issues related to such usage will be examined.  The course also discusses issues related to economics of energy system usage and the concept of load management.  The primary objective of the course is to provide students with the skills to understand the analytical methods and modern tools for solution of problems associated with utilization of electric energy in industrial sectors.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 6615PD', 'Design and application of DC/DC Converters', 0.5, 'LEC', 'The objective of this course is to expand the knowledge and expertise of practicing engineers in the area of DC/DC converters.  The course will focus on the modeling and design of DC/DC converters, including magnetic design and loop design, and their applications in DC motor drives, DC power supplies, power factor correction circuits, photovoltaic stand-alone and grid-connected systems and fuel cell-based stationary and mobile systems.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 6616PD', 'Electric Safety and Grounding System Design', 0.5, 'LEC', 'Grounding of power systems and equipment has great impact on system performance, system equipment integrity, safety of personnel as well as safety of the public at large.  It acquires special relevance for distribution systems where grounding directly affects the reliability of supply to the customer, survivability of end-use equipment and safety of individuals.  The main objectives of this course are - a) Discuss in detail the basic safety issues for low, medium and high voltage systems, b) Designing a reliable grounding system, c) Discuss the safety management and organizational structure and the human factor that affect electric safety.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 6617PD', 'Asset Management and Risk Assessment of Power Systems', 0.5, 'LEC', 'The course will discuss in detail the basic concepts of risk assessment and outage models of system components.  It will discuss in details the concept of asset management and its main components, application examples about the risk evaluation of transmission lines, generators and substations will be considered.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 6618PD', 'Medium and High Voltage Power Cables', 0.5, 'LEC', 'Vast urbanization and increasing pressure to meet high demand in power delivery have resulted in a significant growth in underground cable network.  In addtiion, underground cables being a critical asset group, any outages due to their failure will result in considerable service delivery and economic impact on the operation of distribution networks.  Understand the basic dielectric theory, material properties and design details of underground cables and their accessories.  Understand typical failure modes and their early detection of underground cable and their accessories.  Discuss in detail various off-line and on-line diagnostic tests for condition monitoring of underground cables.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 101', 'Introduction to Microeconomics', 0.5, 'LEC, TST', 'This course provides an introduction to microeconomic analysis relevant for understanding the Canadian economy. The behaviour of individual consumers and producers, the determination of market prices for commodities and resources, and the role of government policy in the functioning of the market system are the main topics covered.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 102', 'Introduction to Macroeconomics', 0.5, 'LEC, TST', 'This course introduces students to the measurement and behaviour of key macroeconomic variables both in Canada and around the world. Topics include national accounts, inflation, interest rates, wages, international balance of payments, business cycles, growth, employment, unemployment, poverty, and inequality.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 201', 'Microeconomic Theory for Business and Policy', 0.5, 'LEC', 'This course offers an introduction to the theory of market based economies. Topics include consumer choice, production, price and output under perfect and imperfect competition, price discrimination and two part pricing, vertical and horizontal firm boundaries and integration, and market structure.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 202', 'Macroeconomic Theory 1', 0.5, 'LEC', 'Theory of the determination of income/output (GDP), employment, unemployment, prices (inflation), and interest rates; an analysis of monetary and fiscal policy.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 211', 'Introduction to Mathematical Economics', 0.5, 'LEC, TUT', 'An introduction to mathematical techniques of particular use in economics. Topics include matrix algebra, differentation, partial derivatives, optimization techniques including constrained optimization - all developed within the context of economic problems.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 220', 'Entrepreneurship Principles and Practices', 0.5, 'LEC', 'The role of the entrepreneur, the entrepreneurial firm, and the creative society are examined. Students are exposed to the practices that enable workplace creativity and innovation with exposure to leadership theory and project management.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 221', 'Statistics for Economists', 0.5, 'LEC, TUT', 'This course introduces students to describing economic data and drawing inferences from features of economic data. Starting from fundamental axioms of probability, students will learn about the calculation of probabilities of basic events and the features of random variables, the most important tool for representing the outcomes of complex economic phenomena. Students will describe discrete and continuous random variables via their probability distributions and summary statistics such as means and standard deviations, as well as the relationships between two random variables in terms of covariance, correlation, and simple regression models. The concepts of hypothesis testing and confidence intervals, and the fundamentals of statistical inference are discussed for basic features of random variables and for comparing the features of more than one random variable.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 231', 'Introduction to International Economics', 0.5, 'LEC', 'This course explores international trade in goods and services, as well as the international exchange of financial assets. Economic theories will be examined, which help explain how international transactions affect the world''s economies. Topics include the theory of comparative advantage and the gains from trade, tariff theory, concepts and measurement of balance of payments, exchange rate systems, and the international monetary system.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 254', 'Economics of Sport', 0.5, 'LEC', 'The course will develop fundamental economic concepts in the context of the sports industry. The course begins with an investigation of some of the primary aspects of the way that the sports industry is organized. The course then considers labour economics in the context of sport. Finally, the course examines the issues that arise with the introduction of various levels of government and/or regulatory bodies in the sports industry, looking at competition between cities for sports teams.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 301', 'Microeconomic Theory 2', 0.5, 'LEC', 'Pricing and employment of inputs; general equilibrium theory; theory of modern welfare economics with some applications; intertemporal choice.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 302', 'Macroeconomic Theory 2', 0.5, 'LEC', 'An extension of the tools developed in Macroeconomic Theory 1 to analyse topics such as unemployment and inflation, government spending, finance, consumption, investment, growth, and the open economy.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 304', 'Monetary Economics', 0.5, 'LEC', 'This course explores the role of money in modern economies. Some of the topics covered will include: the demand for money; the determinants of the price-level, inflation and nominal interest rates; liquidity; bank risk and financial intermediation; private money; central banking and the money supply; government debt and money creation; monetary policy and credibility.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 311', 'Mathematical Economics', 0.5, 'LEC', 'This course builds on and expands the material in ECON 211. Multi-variate calculus and optimization are covered in detail, with a focus on economic applications of the envelope theorem. The implicit function theorem and its uses in comparative statics are explored. Difference and differential equations and their application to dynamic models are introduced, culminating in an introduction to optimal control.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 321', 'Introduction to Econometrics', 0.5, 'LEC', 'An introductory course in the theory and practice of econometrics, focusing on multiple regression analysis and associated topics such as multicollinearity, heteroskedasticity and serial correlation. Simultaneous equation models will also be introduced. Computer assignments make up part of the course.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 332', 'International Finance', 0.5, 'LEC', 'An analysis of the main issues in international finance. Topics include international borrowing and lending, intertemporal gains from trade, current account and balance of trade movements, the determination of exchange rates, and foreign exchange markets.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 341', 'Public Economics: Expenditure', 0.5, 'LEC', 'The course focuses on the rationale for government intervention in a market economy. The course begins with a consideration of market successes through the analysis of the first and second theorems of welfare economics. The course then considers market failures through an analysis of distributional issues, public goods, externalities, non-competitive market structures, and asymmetric information. Time permitting, some issues in the public economics of taxation may be covered.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 345', 'Marketing Strategy', 0.5, 'LEC', 'Building on the fundamentals introduced in Principles of Marketing, students are given an opportunity to apply these concepts to real world situations in an interactive learning environment. Using cases and simulated markets, students make strategic decisions, defend their decisions, and see the repercussions in real time.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 342', 'Public Economics: Taxation', 0.5, 'LEC', 'The course focuses on the public economics of taxation. Normative topics include the efficiency and distributional aspects of taxation and positive topics include the incentive effects of taxation and tax incidence. Time permitting, some issues in public expenditure theory may be covered.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 344', 'Principles of Marketing', 0.5, 'LEC', 'This course is designed to provide a broad understanding of the field of marketing, including consumer behaviour, segmentation, targeting, positioning, branding, and the marketing mix. Students will gain an understanding of how organizations identify the needs of potential consumers and create and deliver value to these consumers through the marketing process.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 351', 'Labour Economics', 0.5, 'LEC', 'A study of the supply of labour by individuals (and unions) and the demand for labour by firms. Topics include the labour market effects of social assistance, unemployment insurance and minimum wages, discrimination in the labour market, efficient wage contracts, the determinants of wage inflation and unemployment.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 355', 'Economics of Energy and Natural Resources', 0.5, 'LEC', 'An analysis of the economics of conservation, especially the adequacy of the market mechanism as an allocator of resource use over time. Issues concerning the economic behaviour of Canada''s fishery, forest, fuel and nonfuel mineral industries will be considered.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 357', 'Environmental Economics', 0.5, 'LEC', 'Application of economic theory to problems of the environment, in particular, air, water, and land pollution. Emphasis is on the theory of the management of common property resources.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 361', 'Cost-Benefit Analysis and Project Evaluation', 0.5, 'LEC', 'Methods for evaluating private and public projects; decision rules, efficiency conditions, and methods of conducting cost-benefit analysis. Application of the technique. This course assumes prior familiarity with probability, expected values, and variance.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 363', 'The Economics of Social Problems', 0.5, 'LEC', 'A topic-oriented course. Problems are selected from a list that includes regulatory economics, poverty, unemployment, industrial policy, safety, social policy, government deficits/debt, stabilization policy, and others. The format assists the student in gaining analytical skills through work on the selected topics.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 365', 'Economic Development of Modern Europe', 0.5, 'LEC', 'A survey of Europe''s economic development from the Industrial Revolution to 1939. Case studies of England, France, Germany, Russia and the Soviet Union are discussed. Emphasis is on technology, economic institutions, capital formation, standards of living and the role of the State.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 371', 'Business Finance 1', 0.5, 'LEC', 'The course explores decisions faced by managers of firms. In particular, decision-makers must determine which long-term real investment opportunities to exploit. Once undertaken, managers must decide how to finance the projects, for example, by debt or equity. The course develops both the conceptual framework and the tools required for these decisions. The course assumes prior familiarity with probability, expected values, and variance.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 383', 'Special Topics', 0.5, 'LEC', 'One or more special half courses will be offered at different times as announced by the Department.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 372', 'Business Finance 2', 0.5, 'LEC', 'This course examines a number of topics relevant to financial practitioners. The topics examined may include options, derivatives securities, futures markets, swaps and hedging. The course assumes prior familiarity with probability, expected values, and variance.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 381', 'Special Topics', 0.5, 'SEM', 'One or more special topics courses may be offered at different times as announced by the department.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 382', 'Special Topics', 0.5, 'LEC', 'One or more special topics courses may be offered at different times as announced by the department.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 401', 'Microeconomic Theory 3', 0.5, 'LEC', 'This course considers a number of topics in microeconomics. Possible topics include decision theory, the analysis of uncertainty, principal-agent problems, game and information theory, social choice theory and the coordination of economic activity through prices, quantities, command and coercion.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 402', 'Macroeconomic Theory 3', 0.5, 'LEC', 'The course develops and analyzes simple models of the economy that recognize explicitly the dynamic nature of decision making and market interactions. These models will be used to interpret and understand macroeconomic phenomena including money and inflation, unemployment, savings and investment, and the national debt.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 403', 'Topics in Economic Forecasting', 0.5, 'LEC', 'The course focuses on the problems of forecasting economic variables. Topics include the importance of economic forecasting; a survey of major forecasting methods including subjective probability, survey methods, exponential smoothing, econometric models, and time series models; forecast evaluation; and methods for managing forecast systems. Applications will be drawn from microeconomics, macroeconomics, finance, and special issues involving new product demand, population and technology forecasting.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 404', 'Topics in Money and Finance', 0.5, 'LEC', 'A discussion of topics in monetary policy. Topics may include: foundations of monetary theory, portfolio choice, term structure of interest rates, money supply and money demand, decision-making under uncertainty, capital asset pricing models, financial flow analysis, rational expectations and monetary policy.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 405', 'Topics in Financial Econometrics', 0.5, 'LEC', 'This course is one of the topic courses in Econometrics. The course will focus on both the Econometric theory and applications in Economics and Finance. Topics may include several main components, such as regression theory (linear/non-linear regression models, multivariate regressions), estimation methodology (OLS, MLE, GMM, WLS/GLS, Monte Carlo simulations), and time series analysis/financial modelling (stochastic process, general ARMA process, autoregressive conditional heteroskedasticity model, stochastic volatility model, high frequency data analysis, nonstationarity for time series and co-integration, continuous time stochastic process).', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 410', 'Economic Thought', 0.5, 'LEC', 'A critical survey of the development of Economic Theory from Classical Political Economy to the Keynesian Revolution.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 411', 'Advanced Mathematical Economics', 0.5, 'LEC', 'Mathematical formulation of economic theory; introduction to dynamic optimisation and optimal control theory; analysis of stability conditions; introduction to linear and nonlinear programming and game theory.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 421', 'Econometric Theory', 0.5, 'LEC', 'This course offers an advanced treatment of topics covered in ECON 322/323 through the extensive use of matrix algebra, statistical theory and numerical methods. After a review of the required mathematics, these tools will be used to derive the least-squares estimator for the standard linear model and to conduct inference about features of the model, checking the results of theory using regression software and economic data. Then this framework will be used for theoretical generalizations of the model and their estimation using regression software. By the end of the course, students will be able to estimate a linear model and conduct accurate inference in cases where economic data violate the assumptions of the standard linear regression model. This includes methods for dealing with heteroskedastic data, observations that may be dependent over time, response distributions with heavy tails or limited range (such as binary outcomes and count variables), and observations that may suffer from selection bias.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 422', 'Microeconometric Analysis', 0.5, 'LEC', 'This course investigates advanced estimation and inference techniques for microeconomic data. Students will learn about error components models that are used for economic data that exhibits significant unobserved heterogeneity. The estimation of treatment effects using fixed effects and difference-in-differences methods will be covered, and design-based methods of causal inference such as matching and regression discontinuity may be covered as well. Extensions such as multilevel or hierarchical models, limited dependent variable models, duration models or selection models may also be included. Students also learn how to apply these methods using computer software, and will use it to analyze complex data from household and firm-level surveys in assignments.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 436', 'International Trade', 0.5, 'LEC', 'An examination of theories of international trade. Topics include the gains from trade, theories of trade determination (Ricardian, Heckscher-Ohlin, increasing returns to scale), the effects of tariffs, multinational corporation behaviour, and factor mobility.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 442', 'Economics of Taxation', 0.5, 'LEC', 'This course discusses economic issues in taxation. Topics may include general equilibrium tax incidence, computable tax models, optimal taxes, development taxation, environmental taxation, tax reforms and fiscal federalism.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 445', 'Industrial Organization and Public Policy', 0.5, 'LEC', 'This course analyzes the structure of markets and how firms compete in them. The emphasis is on oligopoly markets and the use of game theory. The course focuses on differentiated goods, price discrimination, barriers to entry, vertical relationships, advertising, strategic behaviour, and empirical industrial organization including the estimation of demand and costs. Applications to competition policy emphasizing the evaluation of horizontal mergers are presented as well.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 451', 'Law and Economics', 0.5, 'LEC', 'Legal rules and jurisprudence can have a significant effect on resource allocation. A key question is whether legal regimes affect the optimality of an equilibrium and succeed in bringing society closer to a welfare maximizing outcome. From another perspective, a relevant question is on the objective or motivation behind the enactment of specific legislation. Recent studies suggest that much legislation is consistent with simple welfare concepts developed by economists. The course focuses on these issues with examples drawn from property, contract, tort, and criminal law.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 452', 'Topics in Labour Economics', 0.5, 'LEC', 'This course presents an overview of the relevant theoretical and empirical research questions related to employment relationships (pay, mobility, workplace organization). The course will offer a combination of tools (theoretical or statistical) and applications of these tools to various topics. Each topic addresses research questions for which we will see the associated seminal papers and more recent papers providing the latest findings on the given topic. The findings may come from new theoretical arguments, data improvement and/or finer methodological tools, all offering a way to better address the question.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 456', 'Topics in Health Economics', 0.5, 'LEC', 'This course explores the theories and models developed to study the health and health-care sectors from an economic viewpoint. The course will focus on the economic tools necessary to evaluate the efficiency of the market for, and the efficient allocation of scarce resources in, health and health care. Examples of possible topics to be covered are the nature of the market, supply and demand of health care, asymmetries of information, externalities, principal-agent relationships, insurance and cost-benefit analysis.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 465', 'Economics in History: Topics in European History 476-1800 AD', 0.5, 'LEC', 'A survey of the role played by selected economic variables in long-term economic growth and decline. Issues include the impact of technological change, supply shocks, inflation, warfare, climatic change, population growth or contraction, institutions, and the size and role of governments. Period coverage will range from the fall of the Roman Empire to the end of the 18th century.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 471', 'Computational Economics', 0.5, 'LEC', 'Basic concepts and techniques of computational economics. Topics may include computable general equilibrium models, data and calibration, system sensitivity, and dynamic extensions. Depending on class backgrounds and interests, applications may cover areas such as economics of taxation, international trade, industrial organization, economic history, development, environment, dynamics, and finance.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 472', 'Senior Honours Essay', 0.5, 'ESS, LEC', 'Students are required to identify a research topic, conduct research independently, write a research paper, and attend class meetings. Each student is supervised by a member of the Economics faculty. Class meetings cover topics such as a general overview of research methods in economics, research ethics, finding reference material, citing practices, and effective writing and presentation.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 483', 'Special Topics', 0.5, 'SEM', 'Special topics that may vary with individual instructor''s areas of research interest.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 484', 'Special Topics', 0.5, 'SEM', 'Special topics that may vary with individual instructor''s areas of research interest.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 487', 'Special Studies', 0.5, 'SEM', 'Research and reading courses under the direction of individual instructors. See Economics Undergraduate Officer for course registration.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 488', 'Special Studies', 0.5, 'RDG', 'Research and reading courses under the direction of individual instructors. See Economics undergraduate officer for course registration.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 489', 'Special Studies', 0.5, 'RDG', 'Research and reading courses under the direction of individual instructors. See Economics undergraduate officer for course registration.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 601', 'Microeconomic Theory I', 0.5, 'LEC', 'This course studies models of economic decision making.  Topics include: choice under uncertainty, consumer and producer theory and game theory.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 602', 'Macroeconomic Theory I', 0.5, 'LEC', 'The main purpose of this course is to provide students wil the methodological tools that underlie dynamic equilibrium analysis of the macro-economy.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 604', 'Monetary Theory and Banking', 0.5, 'LEC', 'This course for MA students examines the monetary and financial aspects of the macro-economy.  Its main purpose is to prepare students to conduct analyses of problems and policies in monetary economics.  Topics may include the link between monetary policy and output, the economic cost of aggregate fluctuations, the costs and benefits of price stability, the role of central banks and the banking system.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 605', 'Computational  Economics', 0.5, 'LEC', 'Static and dynamic general equilibrium modelling; computation, calibration and simulation. Sensitivity analysis. Policy applications.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 606', 'Research Methodology', 0.5, 'LEC', 'This course focuses on increasing students'' understanding of the roles of economic theory and empirical methods and on the development of skills in the critical analyses of economic research.  Through reading a variety of research papers and attending seminars, students will gain exposure to different research methodologies used in economics.  Students will learn how to synthesize and critique research on a particular topic by writing reviews of academic papers and/or research reports from government and non-governmental agencies, as well as by writing a paper such as literature survey on an assigned topic.  Students will enhance their writing skills and will also gain practice in presenting a research paper.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 621', 'Econometrics I', 0.5, 'LEC', 'Specification and estimation of the linear regression model. Departures from the Gauss-Markov assumptions include heteroskedasticity, serial correlation, and errors in variables. Advanced topics include generalized least squares, and simultaneous equations/instrumental variables. They may also include nonlinear regression, and limited dependent variable models. Some or all of the problem sets involve working with the computer.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 622', 'Applied Microeconometrics I', 0.5, 'LEC', 'This course provides an introduction to the analysis of microdata - data in which the unit of analysis in an individual decision-maker, such as a consumer, worker, or firm.  The course familiarizes students with the various types of mircodata structures and draws links between theoretical economic models and statistical models.  It devotes considerable attention to anlytical techniques for describing univariate and bivariate distributions, including the use of kernel density estimators and scatterplots.  The course addresses statistical inference using multivariate regressions, covering a series of techniques and issues of particular significance in the analysis of microdata.  Examples include Monte Carlo methods; bootstrapping; the use of sampling weight; omitted variable bias; measurement error; and instrumental variables.  Completion of an empirical project is a course requirement.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 623', 'Applied Macroeconometrics I', 0.5, 'LEC', 'This course focuses on the econometric techniques of empirical problems.  Topics may include some basic concepts in time series analysis (such as deterministic and stochastic processes, stationary, ACF, Ergodicity conditions), established estimation techniques (such as OLS/GLS, MLE, GMM, Monte-Carlo simulation), popular univariate and multivariate time series models (such as ARMA  process, autoregressive conditional heteroskedasticity model, stochastic volatility model, Vector Autoregression, Vector Moving Average), and non stationary models (such as random walk, unit roots, Dickey-Fuller Tests, Co-integration System and error corrections).  Some related (empirical/theoretical) published papers for each topic may be discussed in class.  An empirical project is required in the course, requiring the use of software (such as Matlab, SAS).', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 624', 'Dynamic Optimization with Applications', 0.5, 'LEC', 'After exploring solutions for first and second-order difference and differential equations, the course studies the calculus of variations, optimal control theory, discrete and continuous dynamic programming, with applications to various fields; including micro and macroeconomics, natural resource and environmental economics, finance and investment theory.  Techniques of dynamic simulation are discussed.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 631', 'International Trade', 0.5, 'LEC', 'This course presents classical and new international trade theories and discusses selected topics representative of recent empirical research.  Theory, extensions, applications and empirical tests of models such a Ricardian, Hecksher-Ohlin, increasing returns to scale and imperfect competition, political economy, heterogeneous agents (Melitz) will be covered.   Additional elective topics include trade and environment; economic geography; trade, aid and development; trade and conflict; trade and growth.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 635', 'International Trade & Development', 0.5, 'LEC', 'This course is aimed at exploring the nexus between international trade, growth, and development through a set of selected readings. The course will not present a comprehensive treatment of the subject area. Rather, it will provide an introduction to a few selected topics that are suggestive of both the typical questions posed in the literature and of the manner in which the literature has developed over time. The topics will include models of international trade under perfect and imperfect competition, models of north-south trade, immiserizing growth, the Dutch disease, trade as an engine of growth, and international trade, technological change and long-run growth.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 637', 'Economic Analysis and Global Governance', 0.5, 'LEC', 'This course demonstrates the usefulness of economic analysis to the study of global governance.  Topics include the economic analysis of international trade, foreign direct investment, and international finance. Students with more advanced economics background (as a minimum, at least one economics course above the 100 level that focused on international economics or an equivalent applied course such as development economics, environmental economics) are recommended to replace 637 with one of a list of courses, including internationally oriented economics courses, some of the PSCI international political economy courses, or Faulty of Environment courses that include significant international political economy content.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 641', 'Public Economics: Expenditure', 0.5, 'LEC', 'This course studies the economic role of the public sector in a modern market economy.  Topics include the efficient provision of public goods, externalities and public choice analysis of the growth in government spending.  Time permitting, some issues in the public economics of taxation may be covered.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 642', 'Public Economics: Taxation', 0.5, 'LEC', 'This course discusses the economic effects of taxation.  Topics may include the design of a desirable tax system, the structure of income, consumption and wealth taxes in Cananda, the efficiency cost and incidence of various types of taxes, international aspects of taxation, and fiscal federalism.  Time permitting, some issues in public expenditure theory may be covered.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 643', 'Health Economics', 0.5, 'LEC', 'This course introduces students to the role of economics in health care and health policy.  It is meant to be a survey of major topics in health economics and an introduction to the ongoing debate over health care policy.  Topics include the economic determinants of health and health policy, the market for medical care, the market for health insurance, and the role of the government in health care, and health care reform.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 645', 'Industrial Organization I', 0.5, 'LEC', 'Study of the firm as a rational economic agent, how it makes decisions and implements strategies in markets that are imperfectly competitive, and the differences in industry equilibrium relative to perfect competition.  The course covers market owe (oligopolies and monopolistically competitive models), the theory of the firm (organization and contracts), vertical restraints, differentiated products, price discrimination, advertising, barriers to entry, and strategic behaviour.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 648', 'Industrial Organization II', 0.5, 'LEC', 'This course examines public policies in imperfectly competitive markets.  The focus is on how different public policies impact dynamic welfare through their effects on innovation.  The course explores the role of innovation in antitrust policy, examines in detail different forms of  intellectual property rights, and introduces students to concepts such as  technological opportunity, induced innovation, and preference externalities.  Throughout  the course the methodological emphasis is on studying when economic research - whether theoretical or empirical - can inform public policy.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 651', 'Labour Economics', 0.5, 'LEC', 'Labour market structure and operations. Theories of real wages, labour supply, employment-unemployment, and labour unions.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 655', 'Resource Economics', 0.5, 'LEC', 'The economics of renewable and non-renewable resources in a Canadian context. Problems peculiar to the fisheries, forestry, mineral industries and oil and gas production and consumption are analyzed. Also considered are economic and constitutional issues arising from the uneven distribution of resource rents in Canada.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 657', 'Environmental Economics', 0.5, 'LEC', 'This course will review the application of the tools and theory of economics to environmental problems.  The normative foundations of economic analysis will be discussed including efficiency, intergenerational equity and sustainability.  The design and implementation of environmental policy will be analyzed including the use of command and control regulation, market-based instruments, and legal liability.  Applications to various environmental issues may include global warming, trans-boundary pollution, and trade and the environment.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 659', 'Real Options and Investment under Uncertainty', 0.5, 'LEC', 'This course considers the application of option concepts from finance to value real assests.  The focus is on using real options theory and methodology to value investments characterized by uncertainty, irreversibility, and flexibilty inthe timing of irreversible expenditures.  The course begins with an introduction to stochastic processes, Ito''s Lemma, the Black-Scholes equation, contingent claims analysis and dynamic programming. Methods to solve simple option value problems will be presented, such as binomial trees and Monte Carlo simulation.  Applications will focus on problems in natural resource and environmental economics, such as valuing the option ot drill for oil or install pollution control equipment and, time permitting, other appications in economics.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 672', 'Financial Economics', 0.5, 'LEC', 'Topics covered include: expected utility theory, no-arbitrage pricing, equilibrium-pricing models, derivative-security pricing, asymmetric information, capital structure theory and dividend policy.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 673', 'Special Topics in Economics', 0.5, 'LEC', 'One or more half-courses will be offered at different times as announced by the Department.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENBUS 631', 'Stakeholder Engagement', 0.5, 'LEC', 'This course focuses on the various approaches for business and organizations to engage both internal and external stakeholders.  Content covers the identification and characterization of primary and secondary stakeholders; drivers and challenges in environmental, social and economic dimensions of engagement; approaches, including communication, consultation, dialogue and partnership and strategic considerations, business value, risk management, limitations and pitfalls.  In additional, the course considers a firm''s responsibility and spheres of influence.  The Primary focus of this course will be on a for-profit company, though there are some applications to other types of organizations.', 'ENBUS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 701', 'Micro II', 0.5, 'LEC', 'This course builds on the models and the techniques developed in Microeconomic Theory I.  Topics may include general-equilibrium analysis, game theory, the economics of information, mechanism design and applications of bargaining theory to the theory of markets.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 702', 'Macro II', 0.5, 'LEC', 'The main purpose of this advanced course is to prepare PhD students to conduct research to conduct research in macroeconomics.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 704', 'Monetary Economics 2', 0.5, 'LEC', 'This course for PhD students examines the monetary and financial aspects of the macro-economy.  Its main purpose is to prepare students to conduct analyses of problems and policies in monetary economics.  Topics may include the link between monetary policy and output, the economic cost of aggregate fluctuations, the costs and benefits of price stability, the role of central banks and the banking system.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 721', 'Econometrics II', 0.5, 'LEC', 'The course provides a rigorous treatment of more advanced topics in econometrics.  They include system of equations, simultaneous equations, generalized method of moments, empirical likelihood, vector autoregression and dynamic models, time series models and methods, discrete dependent variables, and limited dependent variables.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 722', 'Applied Microeconometrics II', 0.5, 'LEC', 'This course reviews identification strategies used in the analysis of cross-sectional and longitudinal microdata.  Techniques primarily come from applications in labour economics, health economics and industrial organization.  These may include fixed and random effects models, duration analysis, instrumental variables estimators, nonparametric and semi-parametric analysis, decomposition techniques, quantile regression, difference-in-difference estimators, propensity score matching and regression discontinuity design.  The primary objectives of the course are to familiarize students with current methods used in microeconometric research and provide them with hands-on experience applying these methods using an appropriate statistical software package.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 723', 'Applied Macroeconometrics II', 0.5, 'LEC', 'This course covers some of the most important concepts, models and methods used in the empirical analysis of macroeconomic problems.  In particular the course covers established time series techniques as well as more recent developments such as testing for unit roots, measurement of the persistence of shocks and estimation and hypothesis testing in cointegrated systems.  Several dynamic models will be studied using time series analysis methods.  Topics covered include basic concepts in time series analysis (modelling volatility and trend), VAR Modes (including structural VAR''s), VECM and Cointegration, neural networks and estimation of DSGE models.  During the course, students will have the opportunity to explore several case studies using econometric software such as RATS and MATLAB.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 727', 'Financial Econometrics', 0.5, 'LEC', 'This course focuses on the theory and application of estimation and statistical evaluation of continuous-time stochastic processes frequently used in finance.  In particular, it provides an in-depth discussion of estimation methods and statistical tests in the context of dynamic models of the term structure of interest and option pricing.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 743', 'Topics in Health Economics', 0.5, 'LEC', 'The course builds upon the MA course in Health Economics (643).  This topics course is designed for students wishing to undertake research in the area of health policy.  The course will examine health and economic literature on mutually (instructor and students) agreed upon topics, focusing on current research issues in population health, health-services research, economic evaluation, and health policy analysis from an economic perspective.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 773', 'Special Topic in Economics', 0.5, 'LEC', 'One or more courses will be offered at different times as announced by the Department.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EFAS 32', 'Academic Skills', 0, 'LEC', 'This course helps students improve their accuracy and fluency in English by working on all four language skill areas: reading, writing, listening and speaking. Throughout the course, students focus on the skills necessary for success in an academic environment including summary writing, paraphrasing, and documentation skills.', 'EFAS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EFAS 34', 'Writing Skills', 0, 'LEC', 'This course helps students develop strategies to produce well structured, well-supported academic short essays. Students apply basic research 
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EFAS 36', 'Oral Skills', 0, 'LEC', 'This course helps improve speaking, pronunciation, listening, vocabulary, and presentation skills. The student becomes a more effective participant in conversations, discussions, and presentations by engaging in a variety of in-class communicative activities, and assignments.', 'EFAS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EFAS 42', 'Academic Skills', 0, 'LEC', 'This course helps students improve their language and academic skills including vocabulary development, note-taking and summarizing. As such, it prepares students for university studies as well as for success in tests of English proficiency like the iBT TOEFL, CAEL, and ELPE.', 'EFAS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EFAS 44', 'Writing Skills', 0, 'LEC', 'This course focuses on the most frequent grammar problems in the writing of advanced ESL students, who will be practising organizational patterns that suit both academic and professional contexts. Topics include sentence structure, idioms, style, and parallelism, as well as effective quotation, paraphrase, and reference systems.', 'EFAS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EFAS 46', 'Oral Skills', 0, 'LEC', 'This integrated course helps students improve their listening, speaking, pronunciation, and presentation skills. With regular practice, students learn to become effective participants in seminars, lectures, and small groups through student-led discussions and public speaking exercises. Students practise a variety of expressions that perform different communicative functions.', 'EFAS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENBUS 102', 'Introduction to Environment and Business', 0.5, 'LEC, TUT', 'Introduction of ways in which business has and is responding to environmental and business issues; business and sustainable development; issues of corporate/business greening.', 'ENBUS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENBUS 202', 'Environmental Management Systems', 0.5, 'LEC', 'The examination and evaluation of Environmental Management Systems such as ISO 14001. Alternate EMS systems will be compared and reviewed to identify their respective strengths and weaknesses. Case studies will be used to illustrate the ideas presented.', 'ENBUS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENBUS 203', 'Green Entrepreneurship', 0.5, 'LEC, TUT', 'This course examines the role of entrepreneurship in finding solutions to environmental challenges. Discussions will focus on understanding entrepreneurship and the forces that are shaping contemporary green entrepreneurship. Students will also be introduced to the entrepreneurship process by assessing an environmental challenge, finding a solution, and developing a green business model.', 'ENBUS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENBUS 204', 'Principles of Industrial Ecology', 0.5, 'LEC', 'The course presents the history of industrial ecology, defines its key concepts, presents its main methods, and discusses future directions. Local, national and international case studies will be discussed.', 'ENBUS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENBUS 302', 'Strategies for Environment and Business', 0.5, 'LEC, TUT', 'A review of the successes and failures of businesses which have attempted to adopt sustainability principles into their strategies and practices. Case studies will be reviewed to identify barriers to adopting environmental priorities and the processes which proved successful in overcoming these obstacles.', 'ENBUS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENBUS 306', 'Research Design', 0.5, 'LEC, TUT', 'This course introduces students to the basic principles, concepts, and
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENBUS 307', 'Industrial Ecology: Life Cycle Assessment and Management in Business', 0.5, 'LEC', 'This course builds students'' technical competencies in life cycle assessment (LCA) and in critical analysis of products'' environmental impacts through an understanding of life cycle management. Course content covers the International Organization for Standardization (ISO) life-cycle assessment framework, how to conduct technical LCA (including applying quantitative approaches using LCA software and databases, as available and appropriate), challenges of application of LCA to a range of product systems, limitations of LCA, and product life cycle management concepts for business and policy decisions. Students will use the knowledge gained to conduct their own technical LCA.', 'ENBUS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENBUS 308', 'Sustainability Management Standards and Auditing', 0.5, 'LEC', 'Standards and auditing approaches used for sustainability management are examined. Public standards, like those from the International Organization for Standardization, are contrasted with private standards, developed by specific industries for product transparency and accountability in supply-chains. Auditing practice, as defined by the ISO 19011 standard, is examined including principles, steps, reporting and important concepts. Examples of performance standards and management system standards are explored, covering industrial, social and environmental issues. Lectures may be supplemented by required field trip(s) and/or field work.', 'ENBUS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENBUS 309', 'Applied Social Marketing', 0.5, 'LEC, TUT', 'Social marketing uses social psychology and marketing concepts to promote behavioural change (e.g. anti-smoking campaigns, promotion of household energy conservation, and so on). This course will focus on developing an understanding of social marketing and behavioural change models. These concepts will be applied in the context of sustainability issues, using case studies to demonstrate successful (and not so successful) social marketing programs.', 'ENBUS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENBUS 310', 'Strategic Management for Sustainable Business', 0.5, 'LEC', 'This course draws on strategy concepts and theories explored in ENBUS 302 by focusing on the integration of business strategy with sustainability strategy. The course presents the principles that are essential for the formulation and evaluation of strategy for business and explores frameworks for strategic analysis, synthesis and action as it relates to sustainable business.', 'ENBUS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENBUS 311', 'Green Marketing', 0.5, 'LEC', 'This course explores the principles of green marketing; green consumerism; green marketing myopia; the incorporation of environmental considerations into successful products and services; the role of social marketing, government regulations and other incentives related to green marketing.', 'ENBUS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENBUS 312', 'Operationalizing Sustainable Development within Business', 0.5, 'LEC', 'Discussion of company organization structures; factors that deter change together with means of overcoming these issues; and how the greening of industry has affected organizational structure in different business sectors. Includes review of business case studies where environmental management changes have resulted.', 'ENBUS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENBUS 402A', 'Environment and Business Project', 1, 'PRJ, WSP', 'The application of the principles learned in earlier courses will focus on a particular project. Applications may include group projects of sufficient scope to demonstrate mastery of problem-solving, integration and communication on a selected topic related to environment and business, or on selected environmental issues related to a specific business operation.', 'ENBUS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENBUS 402B', 'Environment and Business Project', 0.5, 'PRJ', 'A continuation of ENBUS 402A.', 'ENBUS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENBUS 407', 'Corporate Sustainability Accounting and Reporting', 0.5, 'LEC', 'The course focuses on the environmental reporting mechanisms required of businesses in North America and overseas. The course will also evaluate best practices with reporting performance in different business sectors.', 'ENBUS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENBUS 408', 'Best Practices in Regulations', 0.5, 'LEC', 'Well-crafted environmental regulations, smart regulations, can not only protect the environment but also enhance business competitiveness. This course will discuss issues with regard to smart regulations from the viewpoint of various stakeholders: for example, governments, businesses, and customers.', 'ENBUS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENBUS 409', 'Special Topics in Environment and Business', 0.5, 'FLD, LEC, TUT', 'This course will allow for additions to the program on a short-term basis, and for the development of future permanent courses.', 'ENBUS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENBUS 410', 'Engaging Stakeholders', 0.5, 'LEC', 'Business practices need to reflect responsibility not just to shareholders, customers and staff, but also towards the local community. This course discusses what this means for management, and the strategies and practices that are needed to express this responsibility successfully.', 'ENBUS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENBUS 411', 'International Corporate Responsibility', 0.5, 'LEC', 'The course will focus on corporate responsibility, including social and environmental obligations. The course will consider methods of creating a culture of integrity; examine and evaluate methods such as due-diligence processes, risk assessment, sustainability assessment to integrate responsibility into day to day business positions; and evaluate social and sustainability reports; evaluate methods to determine the link between responsibility and increased profitability and benchmark responsibility in different business sectors including non-profit organizations.', 'ENBUS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENBUS 601', 'Business and the Case for Sustainability', 0.5, 'LEC', 'This on-site intensive course introduces students to basic business concepts and integrates them with concerns for sustainability.  The underlying emphasis is on the value and rationale, opportunities and risks for businesses to undertake and integrate environmental and social sustainability initiatives.  Business topics to be covered include an introduction to concepts and terminology from finance,accounting, strategy, marketing, organization behaviour and operations.  Guests from academia, industry, governments, and non-profit organizations present topics and case studies.  Students also learn about the MEB program, meet faculty members, meet each other, and are introduced to distance education tools.', 'ENBUS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENBUS 602', 'Introduction to Sustainability for Business', 0.5, 'LEC', 'This course reviews the history of global environmentalism; societal expectations and business responses; and the emergence of corporate social and environmental responsibility.  It systematically presents environmental (climate change, air pollution, water quality and quantity, energy and resource consumption, etc.), and social (human rights, health, poverty, ethics, fair trade, etc.) issues, their science, status and priority; technological, regulatory and policy responses; and implications for business.  This course introduces concepts for business sustainability, including systems thinking, complexity and resilience, corporate responsibility, materials and energy flows and transformations, economic and market mechanisms, and relates these to business operations, marketing and strategic management.', 'ENBUS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENBUS 620', 'Business Operations and Sustainability', 0.5, 'LEC', 'This course examines environmental and sustainability management tools for business operations; it addresses process and product considerations.  These include environmental management system tools; cleaner production and pollution prevention tools (environmental risk management, environmental impact assessment, emissions quantification and verification, eco-efficiency, etc.); product stewardship and eco-design tools (design for environment, life cycle assessment/costing, greening supply chain, etc.); sustainability accounting tools (key performance indicators, ecological footpint, social balance, full cost accounting, etc.); and human resource tools (managing teams, motivating people for sustainability, diversity policies, etc.).  The sustainability tools are explained in relation to health/safety programs, human resources management, and operations and quality management.  The course draws on practices and illustrative examples of performance of organizations in different sectors, around different environmental issues (energy, waste, water, air emissions, etc.).', 'ENBUS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENBUS 621', 'Enterprise Carbon Management', 0.5, 'LEC', 'This course exposes students to best practises in greenhouse gas management from the perspective on a business.  This course goes into depth on greenhouse gas (GHG) emissions, quantification and management.  It includes organizational GHG inventories, with reference to international and regional standards, protocols, regulations and schemes; GHG information management systems; expectations and approaches to disclosure of carbon emissions and management activities; and strategic business positioning for climate change mitigation and adaptation.', 'ENBUS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENBUS 622', 'Product Life Cycle Assessment and Management', 0.5, 'LEC', 'This course builds students'' competencies in environmental life cycle assessment (ICA) and life cycle management of products.  Course content covers product sustainability management, life cycle thinking, product stewardship approaches.  It includes life-cycle assessment of raw materials and energy rescues, manufacturing and processing, use, end-of-life and recycling, supply-chain and logistics.  The content follows international standards and related guidance for study scoping, functional unit, inventory, impact assessment and interpretation.  Management applications include ecodesign, product benchmarking, ecolabelling and green marketing.  The course also presents strengths, limitations and weaknesses of sustainability performance assessment of products.  Students apply quantitative approaches including use of ICA software and databases.', 'ENBUS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENBUS 630', 'Enterprise Marketing and Social Accountability', 0.5, 'LEC', 'Changing boundaries, and shifting social expectations create risks and opportunities for an enterprise.  This course discusses the need to remain accountable to society and manage the firm''s reputation in the age of digital information and raised sustainability disclosure expectations.  It also examines the implications for marketing and reporting.  The course introduces core tools for corporate sustainability marketing and reporting, including stakeholder engagement approaches, social accountability and carbon disclosure guidelines, sustainability reporting frameworks, sustainability standards, codes, certifications and labels, and green marketing.', 'ENBUS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENBUS 632', 'Sustainability Reporting', 0.5, 'LEC', 'This course builds students'' competencies in sustainability indicators, reporting and corporate accountability approaches for multinational corporations and other types of organizations.  The course examines global reporting standards for environmental, social and sustainability reporting that are relevant to all sectors of industry and society.  It covers the identification and roles of stakeholders certification and auditing expectations and procedures.  Content addresses materiality in sustainability reporting; regulatory context; legal implications; interrelations with financial accounting practices and standards.', 'ENBUS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENBUS 640', 'Strategies for Sustainable Enterprises', 0.5, 'LEC', 'This course looks at organizational strategy through the "lens" of sustainability.  It explores how corporations (and other types of organizations) are realigning and in some cases reinventing their corporate strategies to move toward more sustainable business models.  This includes initiatives undertaken within the firms, as well as initiatives pursued outside the firm (such as through partnerships).  Several frameworks are offered for assessing the degree to which principles of sustainability are embraced within an organization''s strategies.  In addition, tools for formulation and implementing a sustainability strategic plan are presented.  Case studies draw from different sectors and also different organizational types.', 'ENBUS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENBUS 642', 'Stakeholder Engagement, Collaborations and Partnerships', 0.5, 'LEC', 'This course builds competences in the development and deployment of strategies and processes (in for-and not-for-profit organizations) to engage both internal and external stakeholders in opportunities to accelerate sustainability.  Content covers: the identification, characterization and valuation of primary and secondary stakeholders; drivers and challenges; engagement approaches, including traditional and social media forms of communication, consultation, conflict resolution, and cross-sector partnerships.  Content also introduces: structures, including informal vs. formal interactions, face-to-face and distance meetings, industry associations, joint ventures, public-private partnerships, networks and business to not-for-profit collaborations; processes, such as partnership formation, decision-making, monitoring of progress; and strategic considerations, such as business value, risk management, control vs. influence, barriers and confluence points.  Students working as a team will present their recommendations on a real time case study to stakeholders involved.', 'ENBUS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENBUS 690', 'Enterprise Sustainability Project', 0.5, 'LEC', 'The capstone class synthesizes the learning in the program into an action research project.  Students show original application of the knowledge they have learned and reflect on the utility of the frameworks and tools using a case study approach.  After learning relevant research methods, student teams develop a proposal on a research project which meets the needs of their assigned live case study (i.e. the company or other organization which has committed to be the case in the research project).  This proposal includes a review of the relevant literature, the methodology,, the timeline, and a breakdown of task assignments.   Next students obtain ethics approval for their research project.  Then student teams apply the business sustainability toolbox, and their management skills to the real-world problem of their live case.  Each field study includes conducting a situation analysis, developing a sustainability strategic plan for the organization including a complete and coherent business case.  A Faculty member assists each team.  Students deliver field study project results as a report to both the organization and the course instructor.  Individual students also complete a reflection paper including a discussion of the case in relation to the application of the tools, frameworks and literature.', 'ENBUS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENBUS 650', 'Environmental Finance', 0.5, 'LEC', 'Financial and accounting "tools" have potential to create effective and far-reaching market-based solutions to address a range of environmental problems, including climate change, deforestation, water issues and biodiversity loss, while at the same time identifying and securing new business opportunities for companies and their customers.  An increasing number of financial and accounting products and services have emerged to direct resources and lending power to mitigate ecological degradation and/or encourage sustainable practices and decision-making.  the course considers environmental finance products, tools and services; carbon finance an economics, and other approaches to environmental assets; international capital projects, structured financing, risk and sustainability in financial modeling; full-cost accounting; relation of sustainability performance to capital market valuation and financial performance; socially responsible investing; sector governance, disclosures, regulations, principles and codes.', 'ENBUS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENBUS 690A', 'Enterprise Sustainability Project', 0.5, 'LEC', 'The capstone class synthesizes the learning in the program into a research project. Students show original application of the knowledge they have learned and reflect on the utility of the frameworks and tools using a case study approach. After learning relevant research methods, student teams develop a proposal on a research project which meets the needs of their live case study (i.e. the company or other organization which has committed to be the case in the research project). This proposal includes a review of the relevant literature, the methodology and project timeline. Where applicable, students obtain ethics approval for their research project. Each field study includes conducting a situation analysis, developing a sustainability strategic plan for the organization. A Faculty member assists each team. Students deliver field study project results as a report to both the organization and the course instructor. Individual students also complete a short reflection paper including a discussion of the case in relation to the application of sustainability frameworks and literature.', 'ENBUS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENBUS 690B', 'Enterprise Sustainability Project', 0.5, 'LEC', 'The capstone class synthesizes the learning in the program into a research project. Students show original application of the knowledge they have learned and reflect on the utility of the frameworks and tools using a case study approach. After learning relevant research methods, student teams develop a proposal on a research project which meets the needs of their live case study (i.e. the company or other organization which has committed to be the case in the research project). This proposal includes a review of the relevant literature, the methodology and project timeline. Where applicable, students obtain ethics approval for their research project. Each field study includes conducting a situation analysis, developing a sustainability strategic plan for the organization. A Faculty member assists each team. Students deliver field study project results as a report to both the organization and the course instructor. Individual students also complete a short reflection paper including a discussion of the case in relation to the application of sustainability frameworks and literature.', 'ENBUS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 51', 'Pre-University English: Essentials of Composition', 0, 'LEC', 'An introduction to the process of writing from drafting to revising to polishing. The course includes practical exercises and writing assignments to help students develop and support their ideas accurately, concisely, and clearly.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 100A', 'Fiction', 0.5, 'LEC', 'An introduction to fiction through the detailed examination of a range of novels and/or short stories.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 100B', 'Poetry', 0.5, 'LEC', 'An introduction to poetry through a detailed examination of a range of poetic texts.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 100C', 'Drama', 0.5, 'LEC', 'An introduction to dramatic literature through the detailed examination of a range of dramatic texts.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 101A', 'Introduction to Literary Studies', 0.5, 'LEC', 'An introduction to the study of literature, covering such areas of enquiry as literary history, genre, criticism, analysis, and theory.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 101B', 'Introduction to Rhetorical Studies', 0.5, 'LEC', 'This course introduces students to rhetoric: the art of persuasion. The history, theory, practice, and impacts of rhetoric will be considered. Students will analyze persuasive artifacts including propaganda, advertisements, political texts, and scientific communications. Students will also act as rhetors (users of rhetoric) to craft persuasive arguments.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 103B', 'Varieties of English', 0.5, 'LEC', 'Introduction to the study of varieties of the English language - regional, social, temporal, functional, and stylistic. The relations of languages and literature and of speech and writing will be discussed.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 104', 'Rhetoric in Popular Culture', 0.5, 'LEC', 'This course examines the role of persuasion in contemporary society by focusing on one or more topic areas: film, television, video games, comic books, music, fashion, etc. Students will explore the topic area(s) in depth using a variety of rhetorical theories and methods.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 108A', 'The Superhero', 0.5, 'LEC', 'An examination of hero figures, ranging broadly from ancient characters such as Gilgamesh to the modern comic book superhero. Literary as well as non-literary materials (e.g., film, comics, games) will be considered.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 108B', 'Global English Literatures', 0.5, 'LEC', 'An exploration of texts from a range of geographical locations, such as South Africa, Australia, the Caribbean, India, New Zealand, and Pakistan.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 108C', 'Literature and the Environment', 0.5, 'LEC', 'A literary and rhetorical examination of some of the main currents of environmental thought through the study of literature, film, advertising, and popular culture.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 108D', 'Digital Lives', 0.5, 'LEC', 'An examination of how digital communication technologies create and promote online identities and social spaces, as well as interpersonal and communal interactions.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 108E', 'Gender and Representation', 0.5, 'LEC', 'A study of the ways gender in all its diversity is constructed and gendered experience is expressed in literature, rhetoric, and a variety of media.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 108F', 'The Rebel', 0.5, 'LEC', 'A study of various works of literature in which the protagonist is a rebel against existing norms. The course will examine a number of rebel types and concepts, moral implications, and final outcomes either in successful realization or in tragic defeat.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 108H', 'Isolation and Alienation', 0.5, 'LEC', 'The study of a variety of works centering on the theme of individuals in crisis, the stress being on people at variance with their inner selves, other persons, or their world. The course will discuss the process in which wisdom and maturity are gained as the ultimate products of suffering.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 108M', 'Youth and Adolescence', 0.5, 'LEC', 'Studies the portrayal of young protagonists as they respond to the mores of adult society; their own physical, mental, and psychological development; and the expectations placed upon them by themselves and by others.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 109', 'Introduction to Academic Writing', 0.5, 'LEC, TUT', 'The course will explore a variety of issues in academic writing such as style, argument, and the presentation of information. Frequent written exercises will be required.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 119', 'Communications in Mathematics & Computer Science', 0.5, 'LEC, TUT', 'This course aims to build students'' oral and written communication skills to prepare them for academic and workplace demands. Working independently and in collaboration with others, students will analyze and produce various written and spoken forms of communication. Projects and assignments will draw on materials for Mathematics and Computer Science students.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 129R', 'Written Academic English', 0.5, 'LEC, TUT', 'Designed specifically for students for whom English is not the first language, this writing skills course provides instruction in grammar, sentence and paragraph structure, elements of composition, and academic essay writing, including a focus on theme, development of central ideas, exposition, and argumentation.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 140R', 'The Use of English', 0.5, 'LEC', 'This course examines the use of English in a variety of contexts (colloquial, scientific, legal, political, commercial, journalistic, literary, etc.) to increase critical awareness of the language and help students write more clearly and effectively.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 141R', 'The Use of English 2', 0.5, 'LEC', 'A continuation of ENGL 140R. The study of factual, emotive, scientific and imaginative writing; relevance, context, meaning, tone, feeling, and intention.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 190', 'Shakespeare', 0.5, 'LEC', 'Designed for students in all faculties, the course examines some of Shakespeare''s comedies, history plays, and tragedies. Shakespeare''s variety and flexibility in developing characters and dramatic structures are stressed, as are significant themes.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 200A', 'Survey of British Literature 1', 0.5, 'LEC, TUT', 'An historical survey of major figures, types, and trends in British literature from the Middle Ages to the late 18th century.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 200B', 'Survey of British Literature 2', 0.5, 'LEC, TUT', 'An historical survey of major figures, types, and trends in British literature from the late 18th century to the present.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 201', 'The Short Story', 0.5, 'LEC', 'This course deals with the history and techniques of the short story, with emphasis upon works by such British, American, and Canadian writers as Henry James, James Joyce, D.H. Lawrence, Ernest Hemingway, and Alice Munro.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 202A', 'The Bible and Literature 1', 0.5, 'LEC', 'A study of the major stories, themes, and literary characteristics of the Old Testament of the King James Bible (also known as the Hebrew Scripture), and of its influence on other English literature.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 202B', 'The Bible and Literature 2', 0.5, 'LEC', 'A study of the major stories, themes, and literary characteristics of the New Testament of the King James Bible and of its influence on English literature.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 203', 'Designing Digital Images and Interaction', 0.5, 'LEC', 'This course draws on multiple theoretical perspectives to introduce students to the fundamental principles of multi-modal communication design in its social context. Students will analyze, design, and produce images and interactivity for use in a variety of digital platforms, including e-learning and business applications.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 204', 'Designing Digital Video', 0.5, 'LEC', 'This course introduces students to the principles of designing time-based multi-modal communication in a social context. Students will analyse, design, and produce video for use in a variety of digital platforms, including e-learning and business applications.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 205R', 'The Canadian Short Story', 0.5, 'LEC', 'Exploration of the Canadian short story, from its beginnings - in the bush, in the north, on the land, in the small towns - through the struggles of an urbanizing society to the present. Students will be expected to work in some depth with individual authors.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 206', 'Writing Lives', 0.5, 'LEC', 'This course studies the ways the self is constructed through text by examining a variety of life-writing approaches, organized from youth to old age, along with theories of identity, memory, gender, narrative, cultural studies, and autobiography as a genre.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 208A', 'Forms of Fantasy', 0.5, 'LEC', 'A study of fantasy literature, including some subgenres such as romances, fairy tales, fables, and gothic and horror fiction.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 208B', 'Science Fiction', 0.5, 'LEC', 'Various examples drawn, for instance, from Utopian and anti-Utopian science fiction, social science fiction, "gadget" science fiction, parapsychology, and alternate worlds and beings will be considered. Some attention will be given to the historical development of the genre.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 208C', 'Studies in Children''s Literature', 0.5, 'LEC, TUT', 'A critical examination of works of children''s literature. Specific readings may range broadly, encompassing works as diverse as ancient folk tales and novels and poetry from the 18th century to the present day.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 208E', 'Women''s Writing', 0.5, 'LEC', 'This course explores a range of women''s writing and the social and cultural contexts in which they made their voices heard.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 208H', 'Arthurian Legend', 0.5, 'LEC', 'The story of Arthur and his knights of the Round Table will be discussed as it is treated at various times in various works and genres. Such matters will be considered as the character of Arthur, the concept of Camelot, and the Fellowship of the Round Table.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 208K', 'Detective Fiction', 0.5, 'LEC', 'A study of the detective novel, the novel of crime, the thriller, the novel of intrigue, and of espionage with texts drawn from various time periods and national literatures. The course includes the examination of critical approaches to the form of detective fiction.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 208L', 'Race and the Literary Tradition', 0.5, 'LEC', 'How have ideas of race been represented, transmitted, and resisted in the canon of literature in English over the centuries? Topics may include the invention of race, Eurocentrism and imaginative geography, racial beauty myths, internalized racism, and issues of gender, sexuality, and colonialism.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 208M', 'Travel Literature', 0.5, 'LEC', 'The course examines the forms and functions of travel literature as a genre. Topics will include the representation of travel as adventure, discovery, pilgrimage, and escape; travel and tourism; travel and gender; travel and colonialism.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 208N', 'Sex in Literature', 0.5, 'LEC', 'This course examines how varieties of sexual desire, sexual activity, and cultural attitudes to sex are represented in a selection of literary works from the middle ages to the present.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 210E', 'Genres of Technical Communication', 0.5, 'LEC, TUT', 'This course explores writing, presentation, and design across various genres of technical communication, with a primary focus on printed and/or online computer documentation. Other assignments might include white papers, product specifications, help-desk communication, etc.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 210F', 'Genres of Business Communication', 0.5, 'LEC', 'This course explores the genres of communication in business and other organizations. Students will study and produce instances from several of the following: reports (of several kinds), letters, email messages, marketing materials, public relations materials, and any other types of organizational communication.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 210H', 'Arts Writing', 0.5, 'LEC', 'A study of the various forms, processes, and modes of publication of professional writing in the arts. The course will consider both free-lance writing and writing within institutional contexts. Practice in research, writing, and editing will be emphasized.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 210I', 'Legal Writing', 0.5, 'LEC', 'A study of the principles, processes, and various forms of writing used in the practice of law and drafting of legislation. The history and structure of legal writing, including current debates about plain language, will be examined.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 212', 'Convict Literature', 0.5, 'LEC', 'This course examines the representation of the prison experience in literary works written by or about prisoners as well as the legal contexts of their imprisonment.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 213', 'Literature and the Law', 0.5, 'LEC', 'A study of literary works that involve legal matters and/or have led to litigation on such grounds as obscenity, treason, heresy, libel, and plagiarism.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 214', 'Themes in Canadian Literature', 0.5, 'LEC', 'The course will survey a theme which is significant to the understanding of the Canadian literary mind. Topics will vary from section to section.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 215', 'Canadian Regional Literature', 0.5, 'LEC', 'This course will provide a survey of literature set in a distinctive region of Canada.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 216', 'Canadian Multicultural Literature', 0.5, 'LEC', 'A study of writing by Canadian authors from a range of cultural and ethnic backgrounds. Works are studied in the context of the social, political and cultural forces that produced Canadian literature in general and Canadian minority literatures in particular.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 217', 'Canadian Children''s Literature', 0.5, 'LEC', 'A study of 19th- and 20th-century Canadian literature for children.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 218', 'Mennonite Literature', 0.5, 'LEC', 'A study of poetry and fiction by authors of Canadian Mennonite heritage written since 1962. The course will include a close examination of selected texts considered in the context of the various historical and cultural conditions that affected their production.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 220A', 'Languages and Society I', 0.5, 'LEC', 'This course examines the role that languages play in multilingual societies from a linguistic perspective. It focuses on topics such as dialects, language contact and change, bilingualism, language choice, and language and identity.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 220B', 'Languages and Society II', 0.5, 'LEC', 'This course examines the role that languages play in multilingual societies from a social and cultural perspective. It focuses on topics such as plurilingualism and multilingualism, language maintenance and loss, language planning and politics, multilingual and heritage language education.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 220C', 'Comparative Literature: Theory and Practice', 0.5, 'LEC', 'An introduction to comparative literature, its history as a discipline, and its continuing role in literary studies. Past and present theoretical approaches to literature are used to illustrate the interpretive possibilities achieved by overcoming national, linguistic, and other boundaries. Examples are drawn from a variety of literatures.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 233C', 'Survey of Dramatic Literature and Theory 5', 0.5, 'LEC', 'The first part of the 20th century.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 233D', 'Survey of Dramatic Literature and Theory 6', 0.5, 'LEC', 'The second part of the 20th century.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 247', 'American Literature and Popular Culture', 0.5, 'LEC', 'An introduction to American literary and cultural studies through the examination of selected movements, moments, topics, or figures, drawing on both literature and other media.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 251A', 'Criticism 1', 0.5, 'LEC', 'An introduction to strategies of reading, interpretation, and analysis of literary and non-literary texts, focusing on narrative, poetics, discourse, and rhetoric, and the acquisition of critical vocabulary.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 251B', 'Criticism 2', 0.5, 'LEC', 'An introduction to the theorizing of literary and non-literary texts. Emphasizing contemporary theories, the course will focus on the text, the reader, and culture.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 260', 'Irish Literature and the "Troubles"', 0.5, 'LEC', 'A study of Irish literature written during and about the "Troubles" (1916-1923; 1968 - present), focusing on the relationship between literature and its social, historical, and cultural contexts.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 290', 'Global Shakespeare', 0.5, 'LEC', 'An introduction to Shakespeare''s continuing influence, focusing on adaptations and appropriations of his works in various media by contemporary writers, artists, and directors around the globe.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 308', 'Race and Resistance', 0.5, 'LEC', 'An examination of how contemporary literary and cultural texts represent, reconfigure, and resist ideas of race. Analyzing literature, film, art, popular culture, and social movements, this course covers major debates in critical race theory and anti-racist practices.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 292', 'Rhetorical Theory and Criticism', 0.5, 'LEC, TUT', 'This course provides a survey of the multidisciplinary field of rhetorical studies. In addition to introducing key concepts, theoretical frameworks, and critical debates, this course examines the role of rhetoric in a range of academic disciplines and social contexts.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 293', 'Introduction to Digital Media Studies', 0.5, 'LEC', 'A study of theories of digital media, including critical, rhetorical, and semiotic approaches, and of the interpretation and creation of digital media artifacts.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 301H', 'Honours Literary Studies', 0.5, 'LEC', 'Through lectures, discussion, and presentations by visiting faculty, this course provides Honours students with an enriched survey of the discipline of literary studies. Topics of discussion will be drawn from bibliography and research methods, critical approaches to literature, literary history, genre studies, rhetoric, media perspectives, and other areas of scholarly interest.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 303', 'Special Topics in Digital Design', 0.5, 'LEC', 'In this course students will learn advanced digital design theory. They will participate in workshops with professional designers, develop specialized digital materials, and contribute signature work to their digital portfolio.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 304', 'Designing with Digital Sound', 0.5, 'LEC', 'In this course students will be introduced to sound analysis and production. Students will learn to record, edit, and implement sound in a variety of linear and non-linear media forms, with emphasis on film and video games.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 305A', 'Old English 1', 0.5, 'LEC', 'An introduction to the English language in its earliest form and to English prose in pre-Conquest England, examining Old English prose style, its principal practitioners, and their world view.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 305B', 'Old English 2', 0.5, 'LEC', 'An introduction to Old English poetry, noting in representative Old English poems those things about its purpose, style, and its audience which make it unique but which also provide the beginnings of the English poetic tradition.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 306A', 'Introduction to Linguistics', 0.5, 'LEC', 'Introduction to linguistics and the principles of linguistic analysis through an examination of English phonology, forms, syntax, and discourse.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 306B', 'Modern English Grammar', 0.5, 'LEC', 'Introduction to modern English grammar and structure - its meaningful forms and syntax. Several methods of analysis will be employed and evaluated, including the traditional, structural, transformational-generative, and functional.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 306C', 'Historical Linguistics', 0.5, 'LEC', 'Introduction to historical-reconstruction and comparative analysis. Basic phonological, morphological, syntactic changes as they manifest themselves in language will be examined.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 306D', 'The History of English', 0.5, 'LEC', 'Introduction to the linguistic history of English from earliest documents to the present, with some consideration of various modern dialects.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 306F', 'Introduction to Semiotics', 0.5, 'LEC', 'A study of systems of signs, codes, and signification in language, culture, and literature.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 306G', 'Critical Discourse Analysis', 0.5, 'LEC', 'This course provides an introduction to the theory and practice of critical discourse analysis (CDA), the close study of language and its effects in social context. Students will learn to apply discourse-analytical tools to a wide range of texts, conversations, images, and other artifacts.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 309A', 'Rhetoric, Classical to Enlightenment', 0.5, 'LEC', 'A study of rhetorical theories from antiquity through the Renaissance to the 18th century, with an emphasis on how these theories reflect changing attitudes towards language, society, and the self.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 309B', 'Medieval to Pre-Modern Rhetoric', 0.5, 'LEC', 'A study of rhetorical theories and practices from late antiquity and the medieval, Renaissance, and Enlightenment periods, with an emphasis on how those theories and practices reflect changing attitudes towards language, society, and the self.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 309C', 'Contemporary Rhetoric', 0.5, 'LEC', 'An examination of contemporary rhetorical theory and its relationships to criticism, interdisciplinary studies, and digital applications.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 309E', 'Speech Writing', 0.5, 'LEC', 'The analysis, writing, and editing of speeches. Analysis will focus on the reading and viewing of several famous 20th-century speeches using theories of communication. Writing and editing will focus on implementing oral/aural communication strategies.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 309G', 'The Discourse of Dissent', 0.5, 'DIS, LEC', 'A study of the social, historical, and rhetorical dimensions of collective action. Topics may include health and welfare movements, civil rights and anti-war protests, and environmentalism.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 310A', 'Chaucer 1', 0.5, 'LEC', 'An introduction to the poetry and the prose translations of Geoffrey Chaucer, including his dream allegories, "Troilus and Criseyde," and related compositions.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 310B', 'Chaucer 2', 0.5, 'LEC', 'A study of Geoffrey Chaucer''s "Canterbury Tales".', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 310C', 'Non-Chaucerian Middle English Literature', 0.5, 'LEC', 'Non-Chaucerian English writings during the later Middle Ages; the Middle English romance, including "Sir Gawain and the Green Knight"; alliterative literature, such as "Piers Plowman"; and representative examples of Middle English non-Chaucerian verse.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 313', 'Early Canadian Literatures', 0.5, 'LEC', 'This course examines a selection of pre-1920 Canadian texts concerning first contact, imperialism, colonization, incipient nationhood, and early multi-racial immigration that participate in the ongoing invention of Canada.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 315', 'Modern Canadian Literature', 0.5, 'LEC', 'This course focuses on the varied ways in which 20th-century writers of poetry and prose participate in the shaping of Canadian literary culture, with emphasis on the literature of the middle decades.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 316', 'Canadian Drama', 0.5, 'LEC', 'This course explores traditions and experiments in Canadian drama through an analysis of Canadian plays, especially those from 1960 to the present, in their historical and theatrical contexts.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 318', 'Contemporary Canadian Literature', 0.5, 'LEC', 'This course examines Canadian Literature written in the latter decades of the 20th century and into the 21st century. Literary works are studied in relation to relevant contemporary social, cultural and political topics, such as nationalism, indigeneity, multiculturalism and diaspora.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 319', 'History and Theory of Media 1', 0.5, 'LEC', 'This course explores the development of media technologies such as writing and print (including the book) from their beginnings to the 20th century. Emphasis will be placed on the social, political, and cultural contexts and consequences of these developing technologies.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 320', 'History and Theory of Media 2', 0.5, 'LEC', 'This course explores the social, political, and cultural contexts and consequences of contemporary technologies of representation such as print and visual media, photography and film, audio recordings, computer-mediated communications, and interactive digital media.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 322', 'Postcolonial Literature of the Americas', 0.5, 'LEC', 'This course examines postcolonial literature in English from Canada, the U.S., and the Caribbean. Through study of both written and oral genres, we will discuss how language practices adapt to and are created in colonial and postcolonial contexts. Topics may include diaspora and migration, nationalism, gender, neo-colonialism, and multiculturalism.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 325', 'Austen', 0.5, 'LEC', 'A study of selected novels by Jane Austen, including Pride and Prejudice and Emma. Her letters and juvenilia may also be considered, as well as some of the films based on or inspired by her novels.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 330A', 'Sixteenth-Century Literature 1', 0.5, 'LEC', 'A study of short poems by such writers as Wyatt, Gascoigne, Whitney, Ralegh, Spenser, the Sidneys, Shakespeare, and Donne.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 330B', 'Sixteenth-Century Literature 2', 0.5, 'LEC', 'A study of selected genres, topics, and works from Tudor literature.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 335', 'Creative Writing 1', 0.5, 'WSP', 'Designed to assist students with an interest in developing their creative writing skills in various genres, this course consists of supervised practice, discussions of craft, and peer critiques.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 336', 'Creative Writing 2', 0.5, 'WSP', 'This course is designed to assist advanced creative writers in developing a body of work in one or more genres by means of supervised practice, discussions of craft, and peer critiques.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 342', 'American Literature to 1860', 0.5, 'LEC', 'A study of developments in early American Literature. Texts may be drawn from Anglo-European movements such as gothicism and romanticism; captivity narratives and other colonial writings; African American, Native American, and other minority traditions; sentimental and domestic fiction; and American forms such as the frontier romance, and other minority literatures.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 343', 'American Literature 1860-1910', 0.5, 'LEC', 'A survey of literary developments in America from the Civil War through the turn of the 20th century, including significant movements of the period such as realism, regionalism, and naturalism; the New Woman''s writing and other developments in women''s literatures; popular forms such as the Western; and minority literatures.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 344', 'Modern American Literature', 0.5, 'LEC', 'A study of American Literature from the early twentieth century through the second world war, emphasizing aesthetic innovation in the modernist movement, and its aftermath in the social writings of the 1930s.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 345', 'American Literature in a Global Context', 0.5, 'LEC', 'A study of the ways in which movements of peoples and cultures have shaped American literature. Topics may include colonialism, immigration and migration, literary influence across borders and languages, nativism and internationalism, racial and ethnic styles and exchanges.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 346', 'American Fiction', 0.5, 'LEC', 'A study of four to five writers. Topics may include the evolution of narrative style, realism and anti-realism, literature and story, fiction and history, the novel and film, gender and ethnicity.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 347', 'American Literature Since 1945', 0.5, 'LEC', 'A study of the movements of American Literature following the second world war. The course will consider the formal and cultural diversity of writing in this period, with attention to topics such as avant-garde experiment, the persistence of realism, counter-cultural politics, feminism and literature, postmodernism, and the emergence of minority writers in the mainstream.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 348', 'American Poetry Since 1850', 0.5, 'LEC', 'A study of poems, poets, ideas, and movements, contributing to the growth of a distinctive American poetry from Whitman and Dickinson to the 21st century. Texts will be drawn from popular and avant-garde contexts, as well as the literary mainstream.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 350A', 'Seventeenth-Century Literature 1', 0.5, 'LEC', 'A study of literature by such writers as Jonson, Donne, Wroth, Herbert, Bacon, Milton, Behn, and Dryden.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 350B', 'Seventeenth-Century Literature 2', 0.5, 'LEC', 'An intensive study of Milton''s epic, Paradise Lost, in its historical and literary contexts.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 361', 'English Drama to 1642', 0.5, 'LEC', 'The Middle Ages, the Elizabethans and Jacobeans (excluding Shakespeare), and the Spanish Golden Age.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 362', 'Shakespeare 1', 0.5, 'LEC', 'A study of the plays written before 1599-1600, excluding Julius Caesar.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 363', 'Shakespeare 2', 0.5, 'LEC', 'A study of the plays written after 1599-1600, including Julius Caesar.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 364', 'Shakespeare in Performance at The Stratford Festival', 0.5, 'LEC', 'An historical, theoretical, and analytical introduction to Shakespeare''s plays in performance, both on stage and screen, this course focuses on specific problems and decisive issues of past productions and of those in the current Stratford Festival season.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 365', 'Selected Studies', 0.5, 'RDG', 'Designed to provide an in-depth study of problems and/or authors selected by the instructor. Students interested in initiating such courses are encouraged to do so by bringing their ideas to the attention of individual instructors.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 366', 'Selected Studies', 0.5, 'RDG', 'Designed to provide a study in-depth of problems and/or authors selected by the instructor. Students interested in initiating such courses are encouraged to do so by bringing their ideas to the attention of individual instructors.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 371', 'Editing Literary Works', 0.5, 'LEC', 'Investigating scholarly, educational, popular, and electronic editions, this course explores the theory and practice of editing literary texts.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 376R', 'Applied English Grammar 1', 0.5, 'LEC', 'In exploring different definitions and types of grammar (e.g., descriptive vs. prescriptive), students develop their own critical framework for explaining the structure of English. Of interest to intending teachers of English as the first or second language.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 377R', 'Applied English Grammar 2', 0.5, 'LEC', 'A continuation of ENGL 376R. Practical applications of language theories to error analysis and correction.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 392A', 'Information Design', 0.5, 'LEC', 'The theory and practice of design for print and digital media, including the study of design concepts such as space, colour, typography, interactivity, immersion, motion, and presence. Students produce designs using professional software tools.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 392B', 'Visual Rhetoric', 0.5, 'LEC', 'This course introduces students to the study of images from a rhetorical perspective, including the interaction of texts and images in such professional writing fields as advertising, book illustration, technical documentation, journalism, and public relations. Issues may include visual and textual literacy, the semiotics and rhetoric of design, and the ideological basis of social communication.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 403', 'Digital Design Research Project', 0.5, 'PRJ', 'Students work in small groups under the supervision of a faculty researcher on an ongoing, large-scale, digital design project.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 406', 'Advanced Rhetorical Study', 0.5, 'LEC', 'Topics may include communication, media, politics, science, and social movements. Students will explore the topic(s) in depth using a variety of rhetorical theories and methods.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 407', 'Language and Politics', 0.5, 'LEC', 'This course explores how language shapes and is shaped by the unequal distribution of power in modern societies. The role of language will be considered in, for example, the maintenance of sexual difference, the establishment and maintenance of national identity, and the conflict between social classes. The reading will consist of literary and theoretical texts, the latter including such writers as Bourdieu, Bakhtin, Foucault, Cameron, Lakoff, Ngugi wa Thion''go, and Paulin.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 408A', 'Writing for the Media', 0.5, 'LEC', 'This course examines the genres and strategies of both journalism and public relations. With a strong orientation towards rhetorical and linguistic theories, this course will cover audience concerns from both within and outside organizations.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 408B', 'The Discourse of Advertising', 0.5, 'LEC', 'This course introduces students to writing and editing advertising copy. Students will also be introduced to models of discourse and rhetorical analysis of advertising texts. Assignments include creating a portfolio of advertising copy and an extensive analysis of sample advertising discourse.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 408C', 'The Rhetoric of Digital Design: Theory and Practice', 0.5, 'LEC', 'Students apply a variety of analytic perspectives - design discourse, multimodal discourse, rhetorical theory, social semiotics - to the design and production of a major digital project (or compilation of projects) using professional software and hardware tools.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 409A', 'Rhetoric of Argumentation', 0.5, 'LEC', 'This course studies the discursive, social, and rhetorical principles of argumentation, including topics such as evidence, reasoning, and the organization and presentation of arguments. Scholars studied may include Richard Whatley, Jurgen Habermas, Stephen Toulmin, Chaim Perelman, Lucie Olbrecht-Tyteca, Kenneth Burke, and Pierre Bourdieu.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 410A', 'Restoration Literature', 0.5, 'LEC', 'A selection of literature, including drama, from the period 1660-1700 by such authors as Cavendish, Dryden, Behn, Etherege, Rochester, and Wycherley. Topics may include the poetry of the court wits, literary reflections of Restoration feminism and libertinism, and the rise of print culture.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 410B', 'Eighteenth-Century Literature 1', 0.5, 'LEC', 'A selection of early and mid eighteenth-century literature by such writers as Finch, Pope, Swift, Congreve, Manley, Montagu, Addison, and Steele. Topics may include satire, neo-classicism vs. literary modernism, the development of women''s publication, and generic experimentation.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 410C', 'Eighteenth-Century Literature 2', 0.5, 'LEC', 'A selection of mid and late eighteenth-century fiction by such writers as Thomson, Gray, Smart, Thrale, Johnson, Sheridan, and Wheatley. Topics may include the culture of sensibility, the emergence of (auto) biography and of (anti-) colonial writing, and the consolidation of women''s publication.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 410D', 'Eighteenth-Century Fiction I', 0.5, 'LEC', 'A selection of late 17th- and 18th-century fiction by such writers as Behn, Manley, Haywood, Defoe, Richardson, and Fielding. Topics may include the novel as an experimental form, romance and amatory fiction, and authorial engagement with issues of gender, class, and colonialism.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 410E', 'Eighteenth-Century Fiction II', 0.5, 'LEC', 'A selection of mid and late 18th-century fiction by such writers as Fielding, Haywood, Sterne, Burney, Radcliffe, and Austen. Topics may include sentiment and sociability, the gothic, and abolitionism.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 410F', 'Eighteenth-Century Women Writers', 0.5, 'LEC', 'A selection of writing by women such as Behn, Finch, Montagu, Fielding, Edgeworth, and Austen. Topics may include the culture of sensibility, romance and the gothic, and the interaction of women''s writing with discourses of race and colonialism.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 430A', 'Literature of the Romantic Period 1', 0.5, 'LEC', 'An examination of the first generation of Romantic writers, including such authors as Barbauld, Blake, Wollstonecraft, Wordsworth, and Coleridge.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 430B', 'Literature of the Romantic Period 2', 0.5, 'LEC', 'An examination of the second generation of Romantic writers, including such authors as Byron, P. B. Shelley, Mary Shelley, Keats, and Hemans.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 451A', 'Literature of the Victorian Age 1', 0.5, 'LEC', 'A critical study of early to mid-Victorian literature, including authors such as Carlyle, Arnold, Tennyson, the Bront√´s, Elizabeth Barrett Browning, Robert Browning, Gaskell, Ruskin, and Dickens. Topics may include liberty, work, gender, class, imperialism, and poetry.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 451B', 'Literature of the Victorian Age 2', 0.5, 'LEC', 'A critical study of mid to late Victorian literature, including authors such as Christina Rossetti, Dante Gabriel Rossetti, George Eliot, Newman, Hopkins, Michael Field, Wilde, and Hardy. Topics may include the "Woman Question," the crisis in religious faith, and aestheticism.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 460A', 'Early Literature of the Modernist Period in the United Kingdom and Ireland', 0.5, 'LEC', 'A study of the literatures of the United Kingdom before and after World War I, including such writers as Conrad, Forster, Hopkins, Mansfield, Shaw, Synge, Wilde, and Yeats.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 460B', 'Literature of the Modernist Period in the United Kingdom and Ireland', 0.5, 'LEC', 'A study of the literatures of the United Kingdom and Ireland from World War I to World War II, including such writers as Auden, Eliot, Isherwood, Joyce, Lawrence, Orwell, West, and Woolf.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 487', 'Topics in British Literature and Commonwealth Literature Since 1800', 0.5, 'LEC', 'A special study of a selected topic, author, genre, period, or national literature in British and Commonwealth Literature since 1800. Please see instructor for details.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 460C', 'Literature of the Postwar Period in the United Kingdom and Ireland', 0.5, 'LEC', 'A study of the literatures of the United Kingdom and Ireland after World War II, including such writers as Beckett, Greene, Larkin, Murdoch, Osborne, Pinter, and Spark.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 460D', 'Contemporary Literature of the United Kingdom and Ireland', 0.5, 'LEC', 'A study of the contemporary literatures of the United Kingdom and Ireland, including such writers as Byatt, Boland, Drabble, Heaney, Hughes, Rushdie, and Stoppard.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 463', 'Postcolonial Literatures', 0.5, 'LEC', 'This course examines postcolonial literature (fiction, poetry, and drama) from Africa, Australia, Britain, India, New Zealand, and Pakistan. Topics may include the range of creative forms and language use in texts; indigeneity and migration; intersections of gender, sexuality and race; and resistance, nationalism, and history.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 470A', 'Contemporary Critical Theory', 0.5, 'LEC', 'Contemporary critical theory offers an array of competing constructions of text and culture. This course examines several topics in recent critical theory, such as gender, race, subjectivity, textuality, and popular culture.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 470B', 'History of Literary Criticism', 0.5, 'LEC', 'An historical survey of major critical texts and movements from the Greek and Roman classics to the New Criticism of the mid 20th century, examining different critical theories and practices in a context of cultural changes.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 470C', 'Literary Studies in Digital Forms', 0.5, 'LEC', 'A critical examination of literary publication, research, and criticism in digital forms.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 471', 'Adapting Literary Works', 0.5, 'LEC', 'Focusing on adaptations of classic works of literature in English, this course examines the problems, possibilities, and principles of representing such works in other literary forms and in other media.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 481', 'Topics in the History and Theory of Language', 0.5, 'LEC', 'A special study of a selected topic in the history and theory of language. Please see course instructor for details.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 484', 'Topics in Literatures Medieval to Romantic', 0.5, 'LEC', 'A special study of a selected topic, author, genre, or period in Medieval to Romantic literatures. Please see course instructor for details.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 485', 'Topics in Literatures Romantic to Modern', 0.5, 'LEC', 'A special study of a selected topic, author, genre, or period in Romantic to Modern literatures. Please see course instructor for details.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 486', 'Topics in Literatures Modern to Contemporary', 0.5, 'LEC', 'A special study of a selected topic, author, genre, or period in Modern to Contemporary literatures. Please see course instructor for details.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 492', 'Topics in the History and Theory of Rhetoric', 0.5, 'LEC', 'A special study of a selected topic in the history and theory of rhetoric. Please see course instructor for details.', 'ENGL');
