INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ERS 605', 'Ecosystem Perspectives and Analysis', 0.5, 'LEC', 'Review of recent theoretical and applied studies of ecosystems. Critical examination of the use of ecosystem concepts in ideologically-based calls for significant alternatives in the organization of societies, institutions, individual lifestyles and philosophical beliefs.', 'ERS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ERS 610', 'Public Administration of the Environment & Natural Resources', 0.5, 'LEC', 'Contemporary instruments of policy-making and public administration in the context of natural resources will be examined. A term project which will analyze a contemporary issue in the mining, forestry, fisheries, parks or protected areas will be undertaken.', 'ERS');
