INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('FR 483', 'Introduction to Literary Theory', 0.5, 'LEC', 'A survey of critical approaches to literary texts.', 'FR');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('FR 484', 'Children''s Literature in French', 0.5, 'LEC', 'A detailed study of selected aspects of French and French-Canadian children''s literature.', 'FR');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('FR 485', 'French Women Writers', 0.5, 'LEC', 'A study of selected works by women writers in France from the Middle Ages to the 21st-century. The course will focus on the literary features of these works and on their value as reflections of the position of women in French society throughout the period.', 'FR');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('FR 486', 'Topics in French and Francophone Cultural Studies', 0.5, 'LEC', 'A study of selected topics in French and Francophone cultural studies. Topics will be chosen by the professor according to their area of specialization.', 'FR');
