INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('FR 600', 'Research Methods and Professional Communication in French Studies', 0.5, 'SEM', 'This course comprises an introduction to research tools (bibliographies, databases, etc.); an introduction to means of disseminating research (conferences, publications, etc); and a series of workshops devoted to professionalization (scholarship and grant applications, preparation of the academic cv and teaching dossier, professional communication, career options outside of the academia, etc.).  This course is compulsory for all MA level students and for PhD students who have not taken a research methods course during their MA studies.', 'FR');
