INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GENE 21T', 'Topics for Technical Courses Taken on Exchange by Mechatronics Engineering Students', 0.5, 'LEC', 'Courses taken at foreign universities by University of Waterloo engineering students while enrolled in an international exchange institution, and reserved for courses without equivalents at the University of Waterloo. These courses are treated as technical electives in the student''s plan. Such courses are reported on the student''s transcript with their original titles in English showing as the topic. The grades for these courses will be either CR or NCR.', 'GENE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GENE 22A', 'Topics for List A Complementary Studies Courses Taken on Exchange by Engineering Students', 0.5, 'LEC', 'Courses taken at foreign universities by University of Waterloo engineering students while enrolled in an international exchange institution, and reserved for courses without equivalents at the University of Waterloo. These courses are treated as list A complementary studies electives in the student''s plan. Such courses are reported on the student''s transcript with their original titles in English showing as the topic. The grades for these courses will be either CR or NCR.', 'GENE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GENE 22B', 'Topics for List B Complementary Studies Courses Taken on Exchange by Engineering Students', 0.5, 'LEC', 'Courses taken at foreign universities by University of Waterloo engineering students while enrolled in an international exchange institution, and reserved for courses without equivalents at the University of Waterloo. These courses are treated as list B complementary studies electives in the student''s plan. Such courses are reported on the student''s transcript with their original titles in English showing as the topic. The grades for these courses will be either CR or NCR.', 'GENE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GENE 22C', 'Topics for List C Complementary Studies Courses Taken on Exchange by Engineering Students', 0.5, 'LEC', 'Courses taken at foreign universities by University of Waterloo engineering students while enrolled in an international exchange institution, and reserved for courses without equivalents at the University of Waterloo. These courses are treated as list C complementary studies electives in the student''s plan. Such courses are reported on the student''s transcript with their original titles in English showing as the topic. The grades for these courses will be either CR or NCR.', 'GENE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GENE 22D', 'Topics for List D Complementary Studies Courses Taken on Exchange by Engineering Students', 0.5, 'LEC', 'Courses taken at foreign universities by University of Waterloo engineering students while enrolled in an international exchange institution, and reserved for courses without equivalents at the University of Waterloo. These courses are treated as list D complementary studies electives in the student''s plan. Such courses are reported on the student''s transcript with their original titles in English showing as the topic. The grades for these courses will be either CR or NCR.', 'GENE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GENE 101', 'Strategies and Skills for Academic Success', 0.5, 'LAB, LEC, TUT', 'The goal for this course is to help students become successful independent learners through learned study skills, a better understanding of their personality type, and an understanding of how personal wellness affects academic success. It takes a modelled approach to teaching learning strategies to students who struggle to meet the demands of a university curriculum. In the lectures, students will be taught various learning strategies that they will be able to apply to successfully meet the demands of their other courses. Students will then apply these modelled strategies in their other courses during a weekly application lab. Students will be expected to demonstrate their understanding of the material through a variety of assignments. The course will include an engineering component.', 'GENE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GENE 119', 'Problems Seminar', 0, 'SEM', 'Students may be assigned to a Problems Seminar by the Director or Associate Director of First-Year Engineering or Software Engineering according to their performance during the term. [Offered: F,W,S]', 'GENE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GENE 121', 'Digital Computation', 0.5, 'LEC, TUT', 'Introduction to electronic digital computers, hardware and software organization, examples of efficient numerical algorithms for basic scientific computations. [Offered: F,W,S]', 'GENE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GENE 123', 'Electrical Circuits and Instrumentation', 0.5, 'LAB, LEC, TUT', 'Charge, current and voltage. Voltage and current sources, resistors, capacitors and inductors. Ohm''s Law, Kirchhoff''s Laws, nodal analysis, Thevenin and Norton models, instrumentation amplifier circuits, time response, impedance. Function and characteristics of basic electrical transducers. Resolution, precision and accuracy. Basics of data acquisition.', 'GENE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 165', 'Computer Cartography: Principles and Design', 0.5, 'LAB, LEC', 'Focus is on the compilation and cartographic display of spatially referenced data. Topics covered include geographic coordinate systems, map projections, mapping quantitative data, terrain representation, compiling data from a variety of sources, and the production of effective maps based on established principles of cartographic design.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GENE 199', 'Special Topics in First Year Engineering', 0.5, 'LAB, LEC, TUT', 'Various courses dealing with selected topics related to success in Waterloo Engineering. These courses will be offered as needed.', 'GENE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GENE 299', 'Special Topics in Second Year Engineering', 0.5, 'LEC, TUT', 'Various courses dealing with selected topics of particular interest to second year engineering students. These courses will be offered as resources are available and the material is needed.', 'GENE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GENE 301', 'Special Directed Studies', 0.5, 'DIS', 'This course is provided to allow enrichment for students in engineering who have fulfilled the requirements of one or more of the courses in the 3A or 3B term by means of passing a course or courses taken during one or more work terms. The course comprises a special project pursued under the direction of a faculty member, normally in the department of the student''s plan.', 'GENE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GENE 302', 'Special Directed Studies', 0.5, 'DIS', 'This course is provided to allow enrichment for students in engineering who have fulfilled the requirements of one or more of the courses in the 3A or 3B term by means of passing a course or courses taken during one or more work terms. The course comprises a special project pursued under the direction of a faculty member, normally in the department of the student''s plan.', 'GENE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GENE 303', 'International Studies In Engineering', 0.13, 'DIS', 'Engineering students register for this course for credit towards the Designated Faculty Option in International Studies in Engineering upon return from study or work terms abroad. Credit will be assessed on the basis of a written report and individual interviews. The report may include technical, non-technical, and professional aspects of the foreign residence experience. It may discuss socio-economic aspects of life in the foreign country, and it may compare and contrast conditions in the country or countries involved in the student''s international experiences, or trace the social or political histories of those countries. In all cases the report must include materials addressed to the needs of other students considering a similar experience. The instructor for this course is the Option Co-ordinator. Restricted to students who intend to complete the option in International Studies in Engineering. [Offered: F,W,S]', 'GENE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GENE 315', 'Special Directed Non-Technical Studies', 0.5, 'DIS', 'This course is provided for students who, through academic studies during international exchange programs or other university related scholarly activities, wish to request a course credit for this work. Students wishing to claim this credit must show evidence of university level academic activity not otherwise claimed for credit.', 'GENE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GENE 395', 'Engineering Study Abroad', 2.5, 'LEC', 'Waterloo students studying abroad for academic transfer credits under an Engineering Exchange Program during a Fall term register at Waterloo under GENE 395. [Offered: F]', 'GENE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GENE 396', 'Engineering Study Abroad', 2.5, 'LEC', 'Waterloo students studying abroad for academic transfer credits under an Engineering Exchange Program during a Winter term register at Waterloo under GENE 396. [Offered: W]', 'GENE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GENE 397', 'Engineering Study Abroad', 2.5, 'LEC', 'Waterloo students studying abroad for academic transfer credits under an Engineering Exchange Program during a Spring term register at Waterloo under GENE 397. [Offered: S]', 'GENE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GENE 399', 'Special Topics in Third Year Engineering', 0.5, 'LEC, TUT', 'Various courses dealing with selected topics of particular interest to third year engineering students. These courses will be offered as resources are available and the material is needed.', 'GENE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GENE 401', 'Special Directed Studies', 0.5, 'PRJ', 'This course is provided to allow enrichment for students in engineering who have fulfilled the requirements of one or more of the courses in the 4A or 4B term by means of passing a course or courses taken during one or more work terms. The course comprises a special project pursued under the direction of a faculty member, normally in the department of the student''s plan.', 'GENE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GENE 402', 'Special Directed Studies', 0.5, 'PRJ', 'This course is provided to allow enrichment for students in engineering who have fulfilled the requirements of one or more of the courses in the 4A or 4B term by means of passing a course or courses taken during one or more work terms. The course comprises a special project pursued under the direction of a faculty member, normally in the department of the student''s plan.', 'GENE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GENE 403', 'Interdisciplinary Design Project 1', 0.5, 'LEC, PRJ', 'This is the first course in a two-course sequence where students from more than one engineering plan work together to solve an interdisciplinary engineering problem. In the course they will formulate and communicate an interdisciplinary engineering problem or opportunity; develop a feasible design project proposal and plan; generate feasible solutions that address the formulated problem; evaluate alternatives and identify preferred solution; address safety, regulatory, sustainability and professional ethics requirements, as appropriate; effectively manage design project technical and non-technical risks using project management tools and techniques; deliver a report and/or a presentation that summarizes the work completed; work effectively as an interdisciplinary team member and/or team leader.', 'GENE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GENE 404', 'Interdisciplinary Design Project 2', 0.5, 'LEC, PRJ', 'This is the second course in a two-course sequence where students from more than one engineering plan work together to solve an interdisciplinary engineering problem. At the end of this course students will be able to re-formulate and re-communicate an engineering problem or opportunity (as appropriate); revise and communicate a feasible design project plan; revise solutions that address the formulated problem (as appropriate); implement and verify design solutions by analysis, test or prototype; communicate design solutions using accepted data formats, working documents and oral presentations; address safety, regulatory, sustainability and professional ethics requirements, as appropriate; effectively manage and communicate design project plans, technical and non-technical risks, using project management tools and techniques; work effectively as an interdisciplinary team member and/or team leader.', 'GENE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GENE 411', 'Engineering Law and Ethics', 0.5, 'LEC', 'Background (Charter of Rights and Freedoms), Contracts, Torts (Negligent Malpractice), Forms of Carrying on Business, Intellectual Property (Patents, Trade Marks, Copyrights and Industrial Designs), Professional Practice (Professional Engineers Act, Professional Misconduct and Sexual Harassment), Alternate Dispute Resolution, Labour Relations and Employment Law, Environmental Law. [Offered: W]', 'GENE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GENE 412', 'Ethics and The Engineering Profession', 0.5, 'LEC', 'An analysis from the standpoint of philosophical ethics of moral issues arising in professional engineering practice. Issues include the social responsibility of engineers, conflict of interest and obligation, morally acceptable levels of risk, and moral implications of technology.', 'GENE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GENE 415', 'Special Directed Non-Technical Studies', 0.5, 'DIS', 'This course is provided for students who, through academic studies during international exchange programs or other university related scholarly activities, wish to request a course credit for this work. Students wishing to claim this credit must show evidence of university level academic activity not otherwise claimed for credit.', 'GENE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GENE 499', 'Special Topics in Fourth Year Engineering', 0.5, 'LEC, TUT', 'Various courses dealing with selected topics of particular interest to fourth year engineering students. These courses will be offered as resources are available and the material is needed.', 'GENE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GENE 501', 'Directed Studies for Visiting Exchange Students', 2.5, 'PRJ', 'An assignment of study or project work under the direction of a faculty member. The specific project is established on the basis of a written proposal that is agreed to by the supervisors at both the host and home institutions. The academic level and the time commitment shall be specified in the proposal. Registration is restricted to international exchange students at Waterloo under an Exchange Agreement with the Faculty of Engineering. Candidates for degrees at the University of Waterloo are not eligible. A student must arrange with a Waterloo faculty member to serve as advisor prior to registering for this course.', 'GENE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GENE 502', 'Directed Studies for Visiting Exchange Students', 2.5, 'PRJ', 'An assignment of study or project work under the direction of a faculty member. The specific project is established on the basis of a written proposal that is agreed to by the supervisors at both the host and home institutions. The academic level and the time commitment shall be specified in the proposal. Registration is restricted to international exchange students at Waterloo under an Exchange Agreement with the Faculty of Engineering. Candidates for degrees at the University of Waterloo are not eligible.', 'GENE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GENE 503', 'Directed Studies for Visiting Exchange Students', 2.5, 'PRJ', 'An assignment of study or project work under the direction of a faculty member. The specific project is established on the basis of a written proposal that is agreed to by the supervisors at both the host and home institutions. The academic level and the time commitment shall be specified in the proposal. Registration is restricted to international exchange students at Waterloo under an Exchange Agreement with the Faculty of Engineering. Candidates for degrees at the University of Waterloo are not eligible.', 'GENE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOE 153', 'Earth Engineering', 0.5, 'LAB, LEC, TST, TUT', 'This course studies earth materials and processes from an engineering point of view through case histories and problem sets. The course develops a geological knowledge for applications to any physical environment and provides an appreciation of the impact of engineering work on the environment. Topics include mineral and rock identification, the rock cycle, structural geology and tectonics, geology of Canada, effects of water, ice and wind. Students are also introduced to the concept of geologic time, topographic and geologic maps, and the basic principles and tools used to determine geologic history. [Offered as: CIVE 153 (W), EARTH 153 (S), ENVE 153 (S), GEOE 153 (S)]', 'GEOE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOE 298', 'Seminar', 0, 'SEM', 'General seminar. [Offered: W]', 'GEOE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOE 299', 'Seminar', 0, 'SEM', 'General seminar. [Offered: F]', 'GEOE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOE 353', 'Geotechnical Engineering 1', 0.5, 'LAB, LEC, TST, TUT', 'An introduction to geologic processes. Subsurface exploration. Classification systems. Weight-volume relationships. Soil mechanics principles including state of stress, ground water flow, consolidation and shear strength. Four lab sessions. [Offered as: CIVE 353 (W), GEOE 353 (S)]', 'GEOE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOE 398', 'Seminar', 0, 'SEM', 'General seminar. [Offered: S]', 'GEOE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOE 399', 'Seminar', 0, 'SEM', 'General seminar. [Offered: W]', 'GEOE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOE 400', 'Geological Engineering Design Project 1', 0.5, 'LEC, PRJ', 'Students undertake an independent geological engineering design project during the last two terms of their plan. The purpose of the project is to demonstrate students'' abilities to practise in a geological engineering capacity in their chosen area of expertise, using knowledge gained from their academic and employment experiences. The first part of the project (GEOE 400) will include problem identification, generation and selection of solutions, and time management. Incorporation of technical, ecological, social, political, and economic issues in the solution for the project will be required. A basic requirement of the proposed solution is that it must be compatible with the principles of sustainability. Requirements include proposal, progress report, and a final report containing recommendations for part two of the project, GENE 401. [Offered: F]', 'GEOE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOE 401', 'Geological Engineering Design Project 2', 0.5, 'PRJ', 'A continuation of GEOE 400. The final design of the major geological engineering project proposed in GEOE 400 will be undertaken. The purpose of this phase of the project is to carry out a detailed technical design of the solution proposed in GEOE 400. Requirements of this part of the two-term project include a symposium presentation and a final report. [Offered: W]', 'GEOE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOE 498', 'Seminar', 0, 'SEM', 'General seminar. [Offered: F]', 'GEOE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOE 499', 'Seminar', 0, 'SEM', 'General seminar. [Offered: W]', 'GEOE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOE 554', 'Geotechnical Engineering 3', 0.5, 'LEC, TST, TUT', 'Simulation of geotechnical consulting practice. Students are required to complete several projects, based on actual case studies, which require problem identification, evaluation of geotechnical data, analysis, design, and report preparations. [Offered: W]', 'GEOE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 100', 'On Becoming a Geographer', 0.5, 'LEC', 'An introduction to geographic themes and methods of inquiry. The emphasis will be placed on practical skills including literature searches, field observation, scholarly debate and professional writing.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 101', 'Human Geographies: People, Space and Change', 0.5, 'LEC', 'An introduction to human geography through key subfields of the discipline, examining population change, the rise of cities, our relationship to nature, social inequalities, economic forms, cultural difference and environmental change at the local and global scale.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 102', 'Global Environmental Systems: Processes and Change', 0.5, 'LEC', 'An introductory course in the study of the energy and mass flows that impact the climate and landscape of the Earth system. There is focus on understanding processes of change and variability as well as the impacts of humans on these systems that increasingly define the Anthropocene.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 181', 'Designing Effective Maps', 0.5, 'LAB, LEC', 'Focus is on the issues and foundations of modern digital cartography. Topics covered include geographic coordinate systems, map projections, mapping quantitative data, developing online maps, and the impact of maps on society. Students will learn computer techniques to produce effective maps based on established principles of cartographic design. [Also offered online]', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 187', 'Geospatial Data Science', 0.5, 'LAB, LEC', 'A specialized introduction to fundamental concepts and emerging trends in geomatics and geospatial data science. Overview of the geospatial industry, including application in government, research, and private sector. Students are introduced to cross-discipline tools and techniques for accessing, visualizing, and analyzing geospatial data.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 201', 'Fluvial Geomorphology', 0.5, 'LEC, PRJ', 'Emphasis on concepts related to fluvial processes, river mechanics, the relationship between environmental change and river regime. Selected topics include fluvial processes and landscape formation, flow and sediment regimes, channel processes, form and behaviour, river response to natural and anthropogenic change, and river management.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 202', 'Geography of the Global Economy', 0.5, 'LEC', 'An introduction to globalization of the world economy through an examination of its causes, patterns, and consequences in a variety of geographic contexts.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 203', 'Environment and Development in a Global Perspective', 0.5, 'LEC, TUT', 'Examines the interface between human development and the environment in a global context. Various perspectives are explored to link environmental issues to wealth, poverty, consumption, population, and economic globalization. Case studies, with an emphasis on developing countries, are used to illustrate linkages.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 209', 'Hydroclimatology', 0.5, 'LAB, LEC', 'An introduction to the fundamental processes governing climate and hydrological systems and the links between them. It starts with a discussion of basic atmospheric and hydrological processes and traces the flow of energy and water between the earth''s surface and the atmosphere. The water cycle is examined including evapotranspiration, precipitation, runoff and water storage in the natural reservoirs (including soil and groundwater, lakes and wetlands).', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 212', 'Japan and the Pacific Rim', 0.5, 'LEC, SEM', 'Explore Japan. Learn about its culture, economy, regions, environment, trade patterns, investment and development assistance.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 215', 'China: Diverse and Dynamic', 0.5, 'LEC', 'Changing geographies of China are examined and explained. Patterns and processes of change will be systematically analyzed for topics such as the physical environment, resources, development policy, globalization, industrialization, urbanization, and regional development. Diverse cities and regions are compared and the integration of China into the global economy is explored.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 222', 'Geographical Study of Canada', 0.5, 'LEC, TUT', 'An exploration of the geographical bases of Canada''s regional identity. How physical, demographic, and economic geographies have combined to create unique regions within Canada.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 233', 'Geography of Tourism', 0.5, 'LEC', 'This course provides foundational understanding of the nature and scope of tourism geographies. In particular, students will become familiar with the significance of tourism to modern society, economy and the environment. Geographical approaches to the study of tourism and contemporary frameworks relevant to human behavior and actions in touristic landscapes will serve as the foundations to understanding tourist spaces, places and identities.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 250', 'Urban and Economic Systems: Inter-City and Global Connections', 0.5, 'LEC', 'Contemporary patterns of urban and economic growth and urban based development. Changing trends in urban organization at the regional, national and global scale. New systems of world cities.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 271', 'Earth from Space Using Remote Sensing', 0.5, 'LAB, LEC', 'Remote sensing of the Earth''s systems (atmosphere, land, and oceans) is introduced. The course covers the principles, physics, sensor technology, processing and applications of remote sensing in the electromagnetic spectrum.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 281', 'Introduction to Geographic Information Systems (GIS)', 0.5, 'LAB, LEC', 'Introduction to the fundamental concepts and use of Geographic Information Systems (GIS). Students learn about the nature of geographic information and how to store, manipulate and analyze spatial data in a range of application areas. Students will learn underlying theory in lectures and gain a working knowledge of GIS software in lab sessions.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 293', 'Approaches to Research in Human Geography', 0.5, 'LEC, TUT', 'This course develops student skills in the area of research in human geography emphasizing all aspects of design, measurement and evaluation with a particular emphasis on personal transferable skills (e.g., oral and written communication, working as part of a team).', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 294', 'Approaches to Research in Physical Geography', 0.5, 'LAB, LEC', 'Introduces skills for conducting research in physical geography. Selected techniques used in climatology, hydrology, geomorphology and/or biogeography research will be demonstrated and the principles behind the techniques will be explained. Students get hands on experience in research design, field and laboratory techniques, data assembly and the interpretation of data.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 300', 'Geomorphology and the Southern Ontario Environment', 0.5, 'FLD, LEC', 'Study of the origin and evolution of landforms with emphasis on southern Ontario. Analysis of geomorphic processes. Study of human impact on geomorphological landscapes. The lectures will be supplemented by field trips and field work required for term projects.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 303', 'Physical Hydrology', 0.5, 'LEC, TUT', 'Fundamental processes in physical hydrology are addressed. Components of the water balance are examined to determine the nature of their variation in time and space. Precipitation, interception, infiltration, groundwater and soil water processes, evapotranspiration, runoff and storage will be examined from a theoretical and practical viewpoint, and their linkages demonstrated by lab and fieldwork.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 306', 'Human Dimensions of Natural Hazards', 0.5, 'LEC', 'This course will investigate the human dimensions of the global experience with natural hazards and associated disasters. The physical nature of a wide range of geophysical and biophysical hazards will be explored, paying particular attention to: the ways in which hazards become dangerous to humans, and the pathways by which humans can either increase or decrease their vulnerability in the face of natural hazards.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 308', 'Human Dimensions of Global Climate Change', 0.5, 'LEC', 'Climate change is one of the most profound environmental issues affecting society. The course is an introduction to the human dimensions of global climate change, including its scientific history, potential impacts for natural systems and human societies around the world, and the two societal responses: adaptation and greenhouse gas mitigation. Canadian climate change science, impacts and policy responses will be highlighted.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 309', 'Physical Climatology', 0.5, 'LEC', 'Principles of physical climatology with emphasis on regional and global change and variability. Topics include radiation and energy balances, general circulation patterns, synoptic development and micro-climatology.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 310', 'Geodesy and Surveying', 0.5, 'LAB, LEC', 'Concepts of geodesy and surveying, Earth''s gravity field and the geoid, and measurement techniques applied to geomatics are examined. Field studies include the use of the level, the total station, and GPS for doing distance and angle measurements, leveling, traversing and topographic surveying.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 311', 'Local Development in a Global Context', 0.5, 'LEC, TUT', 'The course examines the ability of local communities to influence their development trajectory. The roles and potential for collaboration among public, private, and third sector partners are explored. Social, environmental, and economic goals are interconnected within a sustainable development framework.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 316', 'Multivariate Statistics', 0.5, 'LAB, LEC, TUT', 'The theory and application of multivariate statistics, with particular emphasis upon the use of the computer.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 318', 'Spatial Analysis', 0.5, 'LAB, LEC', 'Advanced quantitative analysis in a spatial context. A selection of techniques from sampling, geostatistics, point pattern analysis and cluster detection, spatial classification, and spatial data mining.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 319', 'Economic Analyses for Regional Planning', 0.5, 'LAB, LEC, TUT', 'Practical application and critical appraisal of regional analysis techniques used by planners, economic developers and consultants. Problem based approaches to understanding the strength and leverage of business and industrial sectors, projection and forecasting, employment and demographic trends, investment decision-making and cost benefit analysis.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 323', 'Tourism Impacts - International Perspectives', 0.5, 'LEC', 'The course examines the nature, significance, and costs and benefits of tourism in the context of tourism destinations in developed and developing countries. It employs an international perspective to assess the economic, environmental, social, and cultural impacts of tourism in diverse settings.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 333', 'Recreation Geography', 0.5, 'LEC', 'Implications of existing and potential recreation supplies and demands. Topics include recreational travel, site capability, economic and ecological impact models and behavioural aspects of amenity resources.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 340', 'Settlements of Rural Canada', 0.5, 'LEC', 'An historical perspective is used to explore the shifting identities of Canada''s dispersed and nucleated rural settlements. Rural growth, decline and restructuring are examined, including a discussion of how various intervention approaches stimulate changes.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 349', 'Urban Form and Internal Spatial Structure', 0.5, 'LEC', 'An examination of the major factors giving rise to distinctive styles of urban spatial organization. Focus moves from city-wide scale to subareas/sectors - inner city, housing, retailing, etc., with emphasis on understanding and planning for the dynamics of complex environments. Applied issues or problems are dealt with throughout the course.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 351', 'Geography of Transportation', 0.5, 'FLD, LEC', 'Focuses on Canadian transportation systems and issues and is organized into three modules: links between transportation and regional economic development, urban land use - transportation interactions, and sustainable transportation. Approximately one-quarter of the course focuses on analytic techniques including network analysis, category analysis, and the gravity model. Particular attention is paid to trends in air travel and related issues.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 356', 'Resources Management', 0.5, 'LEC', 'Reviews selected theories, methods, and terminology related to economic, behavioural, institutional and decision-making aspects of resources and environmental problems.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 368', 'Conservation/Resource Management of the Built Environment', 0.5, 'LEC', 'Consideration of the constraints and guidelines that an application of the principles of ecology places on the planning and management of resources within urban spaces and the implications for urban design. The theory and history of this subject will be discussed together with urban ecomanagement, the management of waste, urban open space and parks, rehabilitated sites, and environmentally sensitive areas.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 371', 'Advanced Remote Sensing Techniques', 0.5, 'LAB, LEC', 'Advanced image processing techniques of digital remote sensing measurements (e.g. radar systems, optical and infrared systems) from ground, aircraft and satellite instrument systems. Techniques are applied to the study of physical and human environments.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 381', 'Advanced Geographic Information Systems', 0.5, 'LAB, LEC', 'Students learn theoretical and operational approaches to advanced spatial analysis using geographical information systems. Emphasis is placed on the use of automation procedures using models and programming to address a variety of topics that may include but are not limited to digital terrain modeling, suitability analysis, network analysis, and cell-based models. The domain of spatial problems explored may vary by instructor.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 387', 'Spatial Databases', 0.5, 'LAB, LEC', 'This course focuses on design and development of a GIS database. It addresses theoretical issues regarding data models used in GIS and data modeling techniques used in designing spatial databases. It considers the processing required to input data from a variety of sources and clean and edit a multi-theme database and introduces students to creation and use of internet map services.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 391', 'Field Research', 0.5, 'FLD, TUT', 'Field research course in which a specific area will be analyzed from a geographic point of view. Individual or group analysis of specific field problems.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 404', 'Soil Ecosystem Dynamics', 0.5, 'LEC', 'This course examines the role of soil in the environment, its importance as a natural resource in agricultural and forest productivity, and the effects on soil resources as a result of different management practices. It is divided into three sections: 1) introduction to soil composition, formation, and physical, chemical and biological characteristics of soil; 2) soil degradation and management approaches to rehabilitation; 3) soil pollution and the role of soil in maintaining environmental integrity.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 405', 'Wetlands', 1, 'LEC, TUT', 'Basic concepts on the distribution, classification, development, hydrology, biogeochemistry, and ecology of wetlands with an emphasis on temperate and boreal/subarctic systems. Human impacts, restoration and reclamation of wetlands are considered with the view of wetlands as functional ecosystems.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 407', 'Environmental Hydrology', 0.5, 'LAB, SEM', 'An interdisciplinary course that explores ecological processes that are linked to physical hydrology. Particular focus is on the storage and movement of water, solutes and nutrients within selected ecosystems and the ecological impacts of human activities on the ecohydrological system.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 408', 'Earth''s Future Climates', 1, 'LAB, LEC', 'This course provides students with experience using numerical models to understand changes in Earth''s climate. Building on their knowledge of the fundamental physical processes governing the climate system, students will construct, apply and evaluate a hierarchy of climate models (from simple energy balance models to full ocean-atmosphere General Circulation Models). The predictive ability of climate models, and their validation, will be considered. The different types of uncertainty inherent in climate projections will be explained and the implications assessed.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 409', 'Energy Balance Climatology', 1, 'LEC, SEM, TUT', 'A field and lecture course including the radiation and energy balances of various surfaces, the principles of turbulent energy exchange, and the biotic response to the energy environment. These concepts will be illustrated through the collection and examination of field data. The student will be responsible for presentation of a seminar on an assigned topic as well as presentation of the results of research incorporating data collected at the University of Waterloo weather station.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 410', 'Global Navigation Satellite Systems', 0.5, 'LAB, LEC', 'The course covers the essence of Global Navigation Satellite Systems (GNSS), including reference systems, satellite orbits, satellite signals, observables, mathematical models for positioning, data processing, transformation, and applications.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 411', 'Entrepreneurship and Startup Economies', 0.5, 'LEC, SEM', 'A lecture, reading, and discussion-based class that explores the spatial, social, cultural, and economic dimensions of entrepreneurship and startup economies to explore their relationship to: (1) entrepreneurial forms of work in the global economy, (2) entrepreneurship as a gendered phenomenon, (3) regional startup economies (in particular in the San Francisco and Kitchener-Waterloo contexts), and (4) the relationship between startup economies and gentrification.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 418', 'Cold Region Climates', 0.5, 'LEC, TUT', 'This course provides a comprehensive overview of the Arctic climate system. A variety of topics are considered including: key components and processes of the energy and water budgets; the current state of the cryosphere; the relationship between the cryosphere and past variability; the role of the Arctic in the global climate system; and the prediction of future changes using global climate models. Various observational perspectives on the climate system, including conventional measurements, atmospheric reanalysis, and remote sensing are presented.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 419', 'The Cryosphere', 1, 'LAB, LEC', 'This course provides a comprehensive overview of the cryosphere and cryosphere-atmosphere interactions. Topics covered include the material and thermodynamic properties of snow and ice, the role of the cryosphere in weather and climate, and the response of the cryosphere to climate change (past, present and future). Students will be introduced to, and experiment with, snow and ice process models.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 423', 'Sustainable Tourism', 0.5, 'LEC', 'This course examines sustainability issues relevant to tourism. In particular, conceptual and practical examples related to economic, environmental, and social/cultural sustainability of tourism are critically appraised, combined with reviews of policy and institutional effects on sustainability practices. The course also focuses on best practices in sustainable tourism within government, non-government and business operations.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 426', 'Geographies of Development', 0.5, 'SEM', 'Examines international development theories and practice, emphasizing the interactions between social, economic, political and environmental dimensions at the micro- and macro-scales. Selected case studies illustrate divergent outcomes of development and the contested process that development represents.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 430A', 'Field Research in Regional Geography', 0.5, 'LAB', 'Detailed analysis of a selected region with major emphasis upon a field examination of the region (several weeks duration). Offering dependent upon faculty availability and student enrolment. For additional information on duration, itinerary and travel costs of course offerings, contact the Geography Undergraduate Advisor.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 430B', 'Field Research in Regional Geography', 1, 'LAB', 'Detailed analysis of a selected region with major emphasis upon a field examination of the region (several weeks duration). Offering dependent upon faculty availability and student enrolment. For additional information on duration, itinerary and travel costs of course offerings, contact the Geography Undergraduate Advisor.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 430C', 'Field Research in Regional Geography', 1.5, 'LAB', 'Detailed analysis of a selected region with major emphasis upon a field examination of the region (several weeks duration). Offering dependent upon faculty availability and student enrolment. For additional information on duration, itinerary and travel costs of course offerings, contact the Geography Undergraduate Advisor.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 432', 'Health, Environment, and Planning', 0.5, 'LEC', 'This course examines the relationship between the environment (built/physical, economic, social, political and natural aspects) and population health. It focuses on conceptual and empirical links among current environment-health issues such as air quality, active transportation, injury prevention, climate change, and mental well-being. Emphasis is placed on the role of urban planners in collaboration with allied professionals (e.g., public health, engineering, law enforcement, architecture) in creating and maintaining healthy built environments to improve population health with a focus on key health issues.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 450', 'Changing Form and Structure of Metropolitan Canada', 0.5, 'SEM, TUT', 'Selected analysis of processes, problems and planning issues associated with the internal growth and spatial reorganization of Canadian metropolitan areas. Three or four topics are chosen for detailed investigation. These will vary from year to year.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 452', 'Resource Management Project', 1, 'PRJ', 'Concepts and techniques of resources management and institutional analysis are applied to the study of a current resource or environmental management issue.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 453', 'Urban Stormwater Management', 0.5, 'LEC', 'Urban stormwater runoff affects the water quality, water quantity, habitat and biological resources, public health and aesthetic appearance of urban waterways. This course reviews the physical and chemical processes, environmental assessment techniques and best management practices related to stormwater management in the urban systems.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 454', 'Retail Landscapes', 0.5, 'LEC', 'A critical examination of the processes that govern retail landscapes in the city and its countryside. Emphasis is placed on understanding the forces that drive the creation, maintenance, and evolution of these settings at a variety of scales. Small-group work provides an opportunity to apply this understanding in a real-world context.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 459', 'Energy and Sustainability', 1, 'SEM, TUT', 'Renewable and non-renewable energy supply systems are compared using economic and environmental measures. Consumption trends and conservation options are considered at the local and global level. Projects are used to demonstrate the economic and environmental challenges in the design of a sustainable energy system.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 461', 'Food Systems and Sustainability', 0.5, 'SEM', 'This course examines dimensions of food systems sustainability and food security, from production to consumption, from local to global scales, in the Global North and South. Specific themes covered in the course include technological and genetic change in food production, resource depletion and wider environmental impacts of the industrial food system, and policy, market-oriented, and civil society initiatives to bring about change in the food system, including organic production and localizing food systems.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 462', 'Global Food and Agricultural Politics', 0.5, 'LEC', 'This course examines the global food and agriculture system. Specific themes to be covered include political and governance issues related to the Green Revolution, global food corporations, agricultural trade liberalization, food aid, international agricultural assistance, the global agro-chemical industry, and agricultural bio-technology.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 471', 'Remote Sensing Project', 1, 'SEM, TUT', 'Digital image analysis for resource mapping and evaluation using remote sensing data. Topics range from initial data selection to final map production and assessment. Using commercial image analysis software, students will analyse data for a selected area and produce a portfolio of results. In addition, they will undertake a literature review on a selected topic and present highlights of the review at an end-of-term mini-conference.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 474', 'Special Topics in Geography', 0.5, 'SEM', 'These courses allow for additions to the program on a short-term basis, and for the development of future permanent courses.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 475', 'Independent Study of Selected Topics', 0.5, 'RDG', 'Individual study of specific topics not covered in other courses. Students must not register for this course until a faculty member has agreed to supervise the study and the student has developed a brief outline to be filed with the Associate Chair, Undergraduate Studies.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 481', 'Geographic Information Systems Project', 1, 'LAB, PRJ, SEM, TUT', 'The development, implementation, and presentation of a response to a set of GIS related project requirements is the focus of this course. Students work in small teams to enhance and develop their abilities to work with GIS and related spatial technologies and analytical methods in an advanced project setting. The nature of the project requirements and themes varies with faculty and student strengths and interests. Projects may emphasize development of software applications, use of programming, or advanced GIS analysis methods, and draw from theme areas such as environment studies and management, human and physical geography, or planning.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 483', 'Geoweb and Location-Based Services', 0.5, 'LEC', 'A critical approach to the development, implementation, and evaluation of the Geospatial Web and location-based services. Topics covered include Volunteered Geographic Information, open data, locational privacy, and mobile data collection. Assignments are used to experiment with course concepts.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 487', 'Management Issues in Geographic Information Systems', 0.5, 'SEM', 'Built around a set of key issues in the management of Geographic Information Systems. Focuses on middle management concerns and covers topics including GIS needs assessment, benchmarking, the law and spatial data, spatial data warehousing, multi-user GIS modelling and GIS application development. Uses of GIS in both public and private sector organizations are covered.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 490A', 'Honours Thesis Preparation', 0.5, 'PRJ', 'Preparatory work and first draft of thesis.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 490B', 'Honours Thesis Completion', 1, 'PRJ', 'Completion of thesis.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 600', 'Seminar in Spatial Data Handling', 0.5, 'SEM', 'An overview of concepts and methodologies in several aspects of spatial data handling: cartography, geographic information systems, remote sensing, spatial statistics. Analysis of spatial data sets. *eligible for MES.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 601', 'Environmental Change and Remote', 0.5, 'LEC', 'Principles and techniques of analyzing environmental change from long time-series of remotely sensed imagery are examined.  Case studies include the impacts of climate on sea ice and the role of natural and human-induced stress on tropical coral reefs.  Prerequisites: Geography 603 or equivalent. *eligible for MES.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 602', 'Remote Sensing of Cold Regions', 0.5, 'LEC', 'This course focuses on the extraction of hydrologically-relevant parameters from remote sensing that are germane to cold regions such as snowfall, snow cover, glaciers & ice caps, lake and river ice, permafrost, and seasonally frozen ground.  The determination of hydrological parameters in the liquid and vapour phases such precipitiation, soild moisture, surface water, and evaporation/evapotranspiration from remote sensing, as well as the integration of remote sensing data into hydrological models are also covered.  Prerequisites: UW undergraduates: GEOG 271, 371, 471 or undergraduate Geomatics degree.  Undergraduate Geography degree with suitable remote sensing experience. Students with no remote sensing experience but strong GIS experience are required to take a 675 Directed Reading course entitled "Remote Sensing Analysis" prior to enrolment in 602. *eligible for MES.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 603', 'Remote Sensing and Earth System Science', 0.5, 'SEM, TUT', 'This course will explore how remote sensing is used and in concert with empirical or physically-based models to help us better understand Earth system processes from the local to global scales.  Students will learn how remote sensing measurements are coupled with radiative transfer models, energy and mass balance models and spatial analytical models from different components of the Earth system.  Examples will be drawn from the hydrologic and carbon cycles from the human built enviornment.  Prerequisites: UW undergraduates: Geog 271, 371, 471 or undergraduate Geomatics degree.  Undergraduate degree with suitable remote sensing experience.  Students with no remote sensing experience but strong GIS experience are required to take a 675 Directed Reading course entitled  "Remote Sensing Analysis" prior to enrolment in 603. *eligible for MES.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 604', 'Spatial Statistics', 0.5, 'SEM', 'An overview of spatial statistics and their use in geographic studies. *eligible for MES.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 605', 'Spatial Information Technology, Globalization and International Development', 0.5, 'SEM', 'This course considers the role of GIS and RS technologies for problem solving and planning decision support in developing countries. Topics covered include the globalisation of information technology and its transfer from developed to developing countries and general aspects of the spatial information technology industry and its relationship to society. *eligible for MES.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 606', 'Introduction to Geographic Information Systems', 0.5, 'LEC', 'Introduction to GIS concepts and to their application in geographic studies using basic GIS software.  This course is designed for students with little or no prior experience with GIS. *eligible for MES.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 607', 'Applications of Geographic Information Systems', 0.5, 'SEM', 'Current applications of GIS and related technologies in geographic research and environmental management are examined through discussions and individual student projects.  This course is designed for students with previous GIS experience. *eligible for MES.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 608', 'Urban Remote Sensing', 0.5, 'LEC', 'An overview of high-resolution remote sensing systems and advanced study of image segmentation, data fusion, feature extraction, and change detection algorithms.  Topics include 3D city modeling, land use/cover classification, urban heat island study, urban sprawl monitoring, and risk mapping. *eligible for MES.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 609', 'GIS and Spatial Decision Support for Planning  and Resource Management', 0.5, 'SEM', 'This course introduces students to key theories, concepts and techniques that have been developed recently to improve the design support capabilities of spatial information systems.  Topics covered include participatory GIS, group-based spatial decision support systems, and the integration of multi-criteria analysis (MCA) methods with GIS to facilitate decision-making in planning.  *eligible for MES', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 611', 'Industrial Location Theory and Concepts', 0.5, 'SEM', 'Examination of industrial location theory, and themes concerning the role of manufacturing in the space-economy. Factors of location will be considered as will the way that manufacturing has led to spatial disparity on international, regional and local levels.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 613', 'Innovation and Economic Development in Cities and Regions', 0.5, 'SEM', 'This course offers a critical examination of the innovation, knowledge creation and circulation, and governance processes that underpin contemporary economic development in cities and regions.  Strategies, policies and programs that are intended to support and promote innovation and economic development in urban and regional economies are analyzed.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 615', 'Community Economic Development', 0.5, 'SEM', 'Community Economic Development is a field of theory, process and practice that is concerned with understanding the forces shaping communities and finding sustainable local solutions to economic needs.  This seminar course will examine topics such as capacity-building, asset-based strategies, social capital, poverty-alleviation, social enterprises and co-operatives, and comprehensive community initiatives, using international and local examples and case studies.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 616', 'Multivariate Statistics', 0.5, 'SEM', 'Theory and application of multivariate statistics, regression and correlation analysis, factor analysis, discriminant analysis and grouping analysis, with emphasis on the use of the computer. *eligible for MES.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 619', 'Analytical Tools for Economic Development', 0.5, 'LEC, RDG, SEM, TUT', 'The course provides an overview and hands-on application of the quantitative analytical methods regularly used by professionals in economic development and related fields.  Emerging analytical tools and techniques are explored.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 655', 'Snow Hydrology', 0.5, 'LEC, SEM', 'This course examines the processes controlling the spatial and temporal changes in snowcover, and the role of snow in Canada''s hydrological system.  Applications to water resources, hydrological prediction and terrestrial and aquatic ecosystems will be considered.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 620', 'Seminar in Human Geography', 0.5, 'SEM', 'This seminar will provide a broad survey of the interdisciplinary scope of human geography and investigate a wide range of human/society dynamics, theoretical positions and methodologies.  The format of inquiry will be that of a seminar wherein students conduct original research and exchange results through ongoing reports and discussions.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 621', 'Metropolitan Form and Structure in Canada', 0.5, 'SEM', 'A seminar on selected topics of particular relevance to understanding and planning for contemporary processes of metropolitan change in Canada.  Major topics represent an interrelated set of exogenous forces, (globalization, economic restructuring, technological innovation and immigration and demographic shift), and endogenous structural responses, (central city decline, revitalization, dispersed styles of suburban development and new urbanist and ecologically sustainable styles of planned intervention).', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 624', 'Human Activity and Travel Behaviour', 0.5, 'SEM', 'An exploration of observed patterns of human activity and travel, underlying decision-making processes, data collection methodologies, and associated impacts on environment and health.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 634', 'Selected Topics in Regional Studies', 0.5, 'SEM', 'Detailed study of physical, cultural, economic and political geography of a specified world region. (a) Europe (b) Mediterranean (c) Africa (d) Soviet Union (e) East Asia (f) Caribbean (g) Canada (h) North America.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 625', 'Qualitative Methods in Geography', 0.5, 'SEM', 'This course is designed to investigate the range of qualitative research methods (research tools) employed by human geographers and explore the methodological justifications (philosophical or theoretical underpinnings) for using this type of research approach.  Qualitative methods attempt to interpret meaning as opposed to purely measuring phenomena.  The focus of qualitative research is not descriptive measurement and prediction of phenomena, as offered by statistical description/analysis, but is more attuned to examining subjective understandings and the interpretation of meaning (hermeneutics).  This course will examine and evaluate the range of research tools comprising qualitative methods including: various interviewing methods, participant observation, ethnography, case study methods and dicourse analysis.  It will also engage with the theoretical debates and philosophical approaches that underpin qualitative research.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 635', 'International Development: Theories and Practice', 0.5, 'LEC, RDG, SEM', 'This course emphasizes both theoretical and conceptual frameworks, techniques, practices and methods for analysis of development, focusing in particular on the development - environment interface and questions of sustainability.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 637', 'Cultural Geography', 0.5, 'LEC', 'This course will engage with the parameters of cultural geography from varying perspectives, which might include origins, evolution, impacts of the cultural turn, and future directions.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 638', 'Sustainable Tourism', 0.5, 'SEM', 'This course examines sustainability issues relevant to tourism. Conceptual and practical examples related to economic, environmental, and social/cultural sustainability of tourism are critically appraised, combined with reviews of policy and institutional effects on sustainability practices. The course also focuses on best practices in sustainable tourism within government, non-government and private sector.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 639', 'Food Systems and Sustainability', 0.5, 'LEC', 'The course examines emerging and existing sustainable food systems from a North American and EU perspective.  Topics may include: technology, nature and society; policy dimensions in North American and the EU; knowledge and power; spaces of consumption; the moral economy; new urbanism and food planning; and, food networks such as commodity chains and fairly traded food.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 640', 'Seminar in Physical Geography', 0.5, 'SEM', 'Basic concepts related to temporal and spatial scales together with variability as they directly affect data collection and analysis. Leading edge research related to selected sub-disciplines of physical geography will be critically discussed. *eligible for MES.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 641', 'Climate Change: Physical Science Basis', 0.5, 'SEM', 'The course examines the interactive physical, biological and geochemical processes responsible for and resulting from climate change, including anthropogenic influences on these processes.  *eligible for MES.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 668', 'Environmental Assessment', 0.5, 'SEM', 'Techniques and technicalities covered in the first part of the course include legal aspects, teamwork, data collection and manipulation, public participation, and cost benefit analysis. These topics are followed by case histories considered from the perspective of proponent, public, consultant and government. Finally, the ethics and politics of environmental impact assessment are reviewed. Students are required to participate in a group case study on a current impact assessment issue. Estimated additional field trip cost to student: $30.00. *eligible for MES.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 642', 'Micrometeorology', 0.5, 'SEM', 'Advanced study of boundary-layer meteorology. Energy balance regimes for complex surfaces are examined. The theory of turbulent and radiant transfer is studied using field data collected during the course. *eligible for MES.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 643', 'Dynamic Geomorphology', 0.5, 'SEM', 'An overview of current issues and methodologies in geomorphology with special emphasis on processes operating in fluvial, glacial, periglacial or coastal environments. *eligible for MES.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 644', 'Applied Geomorphology', 0.5, 'SEM', 'An examination of the way in which human activities modify landscapes and the impacts of government policy and planning with respect to landscape change. The role of the geomorphologist in landscape rehabilitation. *eligible for MES.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 645', 'Fluvial & Glaciofluvial Sediment Transport', 0.5, 'SEM', 'Laboratory (hydraulic) models and sediment transport theory are emphasized in the analysis of modern and ancient (Pleistocene) landforms of fluvial and glaciofluvial origin. *eligible for MES.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 646', 'Hydrology', 0.5, 'SEM', 'Overview of hydrological processes with emphasis on Canadian examples related to the hydrologic cycle, surface hydrology, groundwater hydrology, and measurement techniques. *eligible for MES.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 647', 'Recent Advances in Wetland Studies', 0.5, 'SEM', 'Selected topics on the distribution, classification, functional dynamics, and ecosystem structure of wetlands. A scientific basis for wetlands management is emphasized. *eligible for MES.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 648', 'Paleolimnology', 0.5, 'LEC', 'This course examines the use of stratigraphic records from lakes to reconstruct environmental change.  Topics include sediment coring techniques, radiometric dating methods, analysis of physical, geochemical and biological indicators, and case studies demonstrating applications to address scientific and management questions. Practical experience is gained from a lake sediment coring field trip and subsequent laboratory analyses.  *eligible for MES.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 649', 'Hydrology of Cold Regions', 0.5, 'LEC', 'This course provides an overview of the hydrology of cold regions.  Physical processes are emphasized, but hydrological modeling, and geochemistry are included where appropriate.  *eligible for MES.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 651', 'Hydroecology for Freshwater Ecosystem Management', 0.5, 'SEM', 'This course examines the interaction in both space and time between hydrological processes and lacustrine environments.  Applications to freshwater ecosystem management are explored.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 652', 'Climate Prediction, Modeling and Scenarios', 0.5, 'LAB, SEM', 'Computer models of the physical climate system are essential tools for investigating future climate change.  This course will introduce students to the basic formulation of these models, and their strengths and limitations.  Inter-linkages between climate modeling and mitigation and adaptation policy agendas is reviewed and critiqued.  The types of climate modeling experiments peformed with such models and scenarios will be reviewed.  The latest projections of future climate on a variety of temporal and spatial scales will be presented and evaluated.  *eligible for MES', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 653', 'Land Use and the Carbon Cycle', 0.5, 'LAB, SEM', 'This course examines contemporary theories, approaches, and technologies used to study coupled natural-human systems at the intersection of land-use and land-cover change, land management, and the carbon cycle.  Topics covered include measurement and modelling techniques to improve carbon accounting, the effects of land use and land management on carbon storage and flux, and international and national policy frameworkds influencing carbon sequestration and climate change mitigation.  *eligible for MES', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 660', 'Perspectives in Resource and Environmental Management', 0.5, 'SEM', 'Current research and practice in resource and environmental management. *eligible for MES.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 661A', 'Applied Studies in Hydrology and the Environment 1', 0.5, 'SEM', 'This applied hydrology course involves defining, designing and conducting research on the hydrology and/or water quality of a specific environmental setting. Drawing on strengths and interests of students, the field and laboratory activities in this course typically involve collection, analysis and reporting of primary data. The Fall-term (661A) focuses on literature review, problem definition, methodological design and data collection; the Winter-term (661B) includes analysis, interpretation and write-up suitable for publication in a refereed journal. Group work is typically involved. *eligible for MES', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 661B', 'Applied Studies in Hydrology and the Environment 2', 0.5, 'SEM', 'This course is a continuation of GEOG 661A/PLAN661A. *eligible for MES.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 664', 'Political Ecology: Nature, Society and Sustainability', 0.5, 'LEC', 'Readings, discussions and case study analysis draw attention to the intersection of political-economy and ecological analysis, and contested understandings of environmental change, livelihoods and sustainability.  Topics may include communities and conservation, knowledge systems and power, ecological change, property rights and institutions, social movements, and methods. *eligible for MES.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 665', 'Environmental Planning Theory and Practice', 0.5, 'SEM', 'An interdisciplinary approach to environmental planning. Focuses on the socio-economic, planning, environmental science, design, and decision-making theories and methods utilized in environmental planning theory and practice. Regional and local case studies and studio projects will be used to demonstrate professional practice issues and techniques. Recommended: A senior level course in ecology, environmental science, landscape architecture or equivalent.  *eligible for MES.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 666', 'Ecosystem Approach to Park Planning', 0.5, 'SEM', 'An ecological approach to planning national and provincial parks, focusing on system planning, master planning and park administration. Examination of the theory and practice of parks planning, utilizing ecological concepts. Recommended:  a senior undergrad or graduate ecology course.  Estimated additional field trip cost to student: $30.00  *eligible for MES', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 669', 'Energy Sustainability', 0.5, 'LEC, RDG, SEM', 'Renewable and non-renewable energy supply systems are compared using economic and environmental measures.  Consumption trends, conservation options and choices are considered at the household, community and global scales.  Projects are used to demonstrate the economic and environmental challenges in the design of sustainable energy systems. *eligible for MES.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 671', 'Contemporary Perspectives on Tourism', 0.5, 'SEM', 'This course will introduce participants to a variety of topics and research methods through presentations made by active researchers from Canada and abroad.  *eligible for MES', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 672', 'Human Ecology of Stressed Environments', 0.5, 'LEC', 'An examination of socioeconomic-biophysical system interactions emphasizing multi- and transdisciplinary perspectives such as human ecology, health, ecosystems, and complexity.  Students normally focus on one particular problem and region to illustrate concepts and methods. *eligible for MES', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 673', 'International Perspectives on Resource and Environmental Management', 0.5, 'LEC', 'An overview of issues, problems and concepts in international resource and environmental management.  The complex nature of ecological/biophysical, economic, and sociocultural aspects of resource management will be explored, with particular emphasis placed on tracing challenges experienced in developing countries.  *eligible for MES', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 674', 'Climate and Society', 0.5, 'SEM', 'This course examines how weather and climate influence human systems, and the international trend twoard improved climate information.  The emphasis  is on developing competence in using analytical tools to understand the direct and indirect impatcts of weather and climate on human activities and economic sectors.  *eligible for MES.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 675', 'Selected Topics in Geography', 0.5, 'RDG', 'Topic(s) to be negotiated on an individual basis with faculty members. An outline for this course, approved by the professor in charge, must be submitted to the Program Director, within three weeks of registration.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GRK 632', 'Local Cultures in Hellenistic Greek Poetry', 0.5, 'SEM', 'This course examines the relationship between local Mediterranean cultures and Greek poetry through an examination of a variety of works composed in different genres of literature between 323 and 100 BC.  We shall focus on the manner in which unique local social customs, dialects, religions, and politics are treated in poetry written in various places of the Greek-speaking world, from Egypt to Italy.  In this way we shall gain an appreciation of the multi-cultural nature of Hellenistic society, and of the impact it has upon subjects, themes, and styles of contemporary literature.  Readings will include, but not be restricted to, the Argonautics of Apollonius of Rhodes, the Hymns of Callimachus, the Idylls of Theocritus, and the epigrams in the Greek Anthology.', 'GRK');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GRK 633', 'Ancient Religions through Epigraphy', 0.5, 'SEM', 'An introduction to the technical aspects of the study of inscriptions and an investigation of the evidence which these documents offer about ancient religious practice and sacred space.  Special attention will be paid to the class of inscriptions call leges sacrae (sacred laws).', 'GRK');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GRK 651', 'Senior Greek Composition, Grammar and Reading', 0.5, 'SEM', 'Advanced composition, translation and grammar with intensive analysis of selected passages.', 'GRK');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 676', 'Climate Change Vulnerability and Adaptation', 0.5, 'SEM', 'This course examines the impacts of changing climate on natural and human systems and focuses on the capacity of societies to adjust to, plan for and cope with changing climate and environmental conditions.  *eligible for MES', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 677', 'Climate Change, Natural Hazards and Disaster Risk Reduction', 0.5, 'SEM', 'This course explores the complex links between climate change and natural hazards, and related disasters, including disaster risk reduction as an adaptation strategy.  Emphasis is placed on examining these concepts in a developing country context.  *eligible for', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 678', 'Climate Change Governance: From Global Treaties to Local Innovation', 0.5, 'SEM', 'This course explores the theories behind, and practical approaches to, the multi-level governance of climate change. The United Nations Framework Convention on Climate Change, the Kyoto Protocol, national climate action plans and local innovation are viewed through the lenses of legitimacy, effectiveness, and participation. Finally, we consider the challenge of transformative change, and the possibility of finding synergies among climate change responses and other pressing social and environmental priorities.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 679', 'Climate Change Mitigation', 0.5, 'SEM', 'This course examines the anthropogenic sources of greenhouse gas emissions and the range of strategies to reduce these emissions and to enhance carbon sequestration.  The policy frameworks governing climate change mitigation are critically appraised from geopolitical, economic and ethics perspectives.  This course also considers the potential role of climate remediation strategies.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 685', 'Theory of Local Economic Development', 0.5, 'LEC', 'This course focuses on economic development for regions, communities and municipalities.  We examine organizational structures and strategies that a community may employ to increase its capacity to control its destiny.  Topics include: strategic economic planning, information management, partnerships, internet opportunities, entrepreneurship, incubators, tourism, sustainable development and healthy community initiatives.  Finally, we critique various evaluation methods.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 690', 'Geographic Thought and Methodology', 0.5, 'SEM', 'An analysis of changing methods and philosophy of geographic research with emphasis on problem formulation and the design and evaluation of geographic research.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 691', 'Graduate Student and Faculty Seminar in Geography', 0.5, 'SEM', 'Participation in departmental seminars; invited presentations by guest speakers, program faculty and students. Graded on a Cr/NCr basis', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 692', 'International Study', 0.5, 'SEM', 'The aim of this course is to provide academic credit for a foreign academic learning experience.This will take one of the following formats: study with a foreign expert, a specialized graduate course taken at a foreign institution, or a research/study project abroad conducted under the guidance of an approved advisor. In all instances the plan of study must be approved before a  student leaves and in each case a final approved report must be submitted.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GER 100', 'Zeitgeist and Popular Culture', 0.5, 'LEC, TUT', 'An introduction to life, society, and culture in modern Germany, Austria, and Switzerland.', 'GER');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GER 101', 'Elementary German I', 0.5, 'LAB, LEC', 'This course aims to bring German language, culture, and everyday life into the classroom. Students with little or no prior knowledge are introduced to the structure of German and develop their listening/comprehension, speaking, reading, and writing skills. Classes are communicative and emphasize interaction in German. Online exercises and audio/video materials accompany each chapter of the textbook.', 'GER');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GER 102', 'Elementary German II', 0.5, 'LAB, LEC', 'A continuation of GER 101.', 'GER');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GER 180', 'German and Russian Literary Masterpieces', 0.5, 'LEC, TUT', 'An examination of major Russian and German literary works that have influenced world literature.', 'GER');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GER 201', 'Intermediate German I', 0.5, 'LAB, LEC', 'This course continues the work of GER 101/102. It offers practice in speaking, reading, and writing, with vocabulary building, grammar, and exercises in comprehension.', 'GER');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GER 202', 'Intermediate German II', 0.5, 'LAB, LEC', 'Strengthening of communicative skills, grammar review, vocabulary building, written practice, and conversation on issues of contemporary life in German-speaking countries.', 'GER');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GER 211', 'Integrative Language Seminar I', 0.5, 'LAB, LEC', 'This course develops reading, writing, and oral skills, and intercultural competence.', 'GER');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GER 212', 'Integrative Language Seminar II', 0.5, 'LAB, LEC', 'This course builds on GER 211 to develop competence in reading, writing, oral skills, and intercultural competence.', 'GER');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GER 220', 'Once Upon a Fairy Tale: Fairy Tales, Then and Now', 0.5, 'LEC, TUT', 'This course studies fairy tales in the broader context of the history of childhood and practices of education and socialization. The course will concentrate on the German and Russian context and in particular on the work of the Brothers Grimm and Alexander Afanasyev, and will also consider fairy tales drawn from a number of different national traditions and historical periods.', 'GER');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GER 250', 'Performance German I', 0.5, 'LAB, LEC', 'This course improves students'' oral and written skills via creative performances in our everyday life. Students will be actively involved in marketing products, conducting job interviews, writing r√©sum√©s, improvising theatre skits, hosting poetry slams, or even instructing yoga - all in German.', 'GER');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GER 261', 'Understanding Conversation', 0.5, 'LEC', 'This course examines the connection between conversation and cultural identities. Looking at conversation means looking at language structures and language choice but also at gesture, gaze, laughter, and emojis. We will analyze everyday conversation as well as interaction in digital contexts, with the goal of discovering how linguistic and multimodal choices convey and construct who we are.', 'GER');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GER 262', 'Multilingualism', 0.5, 'LEC', 'This course examines the concept of multilingualism in society from a social, cultural, and linguistic perspective. It covers topics such as multilingualism in the media, endangered languages, language and identities, language maintenance and loss, language planning and politics, and multilingual education.', 'GER');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GER 271', 'German Thought and Culture: Objects', 0.5, 'LEC', 'An exploration of human-made objects that open up perspectives on German-speaking society and culture. Objects to be studied might include the Berlin Wall, the swastika, Luther''s Bible, donairs, and Volkswagens. Students acquire skills in cultural analysis and learn about major developments in literature, philosophy, religion, art, architecture, and music.', 'GER');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GER 272', 'German Thought and Culture: People', 0.5, 'LEC', 'An exploration of key figures whose contributions to the arts and intellectual life have shaped German-speaking society and culture. Persons to be studied might include Friedrich Nietzsche, Sigmund Freud, Leni Riefenstahl, and Hannah Arendt. Students acquire skills in cultural analysis and learn about major developments in literature, philosophy, religion, art, architecture, and music.', 'GER');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GER 280', 'Comparative Literature: Theory and Practice', 0.5, 'LEC', 'An introduction to comparative literature, its history as a discipline, and its continuing role in literary studies. Past and present theoretical approaches to literature are used to illustrate the interpretive possibilities achieved by overcoming national, linguistic, and other boundaries. Examples are drawn from a variety of literatures.', 'GER');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GER 298', 'Topics in Cultural Studies', 0.5, 'FLD, SEM', 'Selected topics in cultural studies.', 'GER');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GER 299', 'German Abroad', 0.5, 'LEC', 'This immersion course provides students with an introduction to and a familiarity with selected topics in German-speaking environments. The topics range from history and culture, to language and linguistic varieties in the German-speaking world, as well as arts and the German-speaking media landscape. Course offerings include classes abroad as well as field trips.', 'GER');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GER 303', 'Interactive German Language and Culture', 0.5, 'LAB, LEC', 'This course develops German language skills and cultural awareness. Discussions include such topics as Heimat, nation, migration, and study abroad. Students will engage with and reflect on different social and cultural perspectives through interviews, videos, and texts. Online interactions, including with people in and from Germany, form an integral part of the course.', 'GER');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GER 304', 'Reading and Translating', 0.5, 'LAB, LEC', 'Students learn strategies for understanding a variety of texts such as newspaper reports, manuals, and fiction. Translation exercises are used to improve language skills.', 'GER');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GER 307', 'German for Professional Purposes', 0.5, 'LEC, TUT', 'This language course teaches students the skills needed to function in German business and professional environments. Praxis-oriented language and intercultural training are combined with a discussion of the German business world.', 'GER');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GER 308', 'German through Comics', 0.5, 'LEC, TUT', 'German-language comic strips and graphic novels enable students to explore modern language use. Colloquial German language skills are sharpened in this course, and the place of the comic strip and graphic novel in society is discussed.', 'GER');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GER 309', 'The Structure of German', 0.5, 'LAB, SEM', 'Students in this course investigate grammar phenomena in electronic corpora (large principled collections of texts) to develop their language awareness and the accuracy and appropriateness of their written German.', 'GER');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GER 331', 'Exploring the German Language', 0.5, 'SEM', 'An introduction to the study of linguistics with a focus on developing the German language skills necessary for linguistic analysis.', 'GER');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GER 334', 'Exploring German Literature', 0.5, 'LEC, TUT', 'An introduction to the study of prose, poetry, and theatre. Periods in German literary history, forms of literary expression, and the methodologies used to analyze these forms are examined. Special attention is also paid to developing the German language skills necessary for literary interpretation.', 'GER');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GER 350', 'Performance German II', 0.5, 'LAB, LEC', 'This course improves students'' more advanced oral and written skills via creative performances in our everyday life. Students will be actively involved in marketing products, conducting job interviews, writing r√©sum√©s, improvising theatre skits, hosting poetry slams, or even
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GER 353', 'Intermediate Conversation and Composition on Topics in German "Landeskunde"', 0.5, 'LEC', 'Conversation and composition on topics in German "Landeskunde" with grammar review and study of German vocabulary and idiomatic expressions. This course is taught in Mannheim in conjunction with the ''"Waterloo in Germany" program.', 'GER');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GER 354', 'Intermediate Conversation and Composition on Topics in German "Landeskunde"', 0.5, 'LEC', 'Conversation and composition on topics in German "Landeskunde" with grammar review and study of German vocabulary and idiomatic expressions. This course is taught in Mannheim in conjunction with the "Waterloo in Germany" program.', 'GER');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GER 359', 'Topics in German Film', 0.5, 'LAB, SEM', 'Selected topics in German film.', 'GER');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GER 362', 'German Film Classics', 0.5, 'LAB, LEC', 'Masterpieces of German film from the silent era to present.', 'GER');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GER 363', 'German Filmmakers in Hollywood', 0.5, 'LAB, LEC', 'An examination of the work of filmmakers who have made a mark in both European and American cinema.', 'GER');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GER 364', 'German and Russian Film Pioneers', 0.5, 'LAB, LEC', 'An examination of early German and Russian cinema, and the way these two film industries influenced each other. Influential theories of film from the period will also be studied.', 'GER');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GER 383', 'Culture in the Third Reich: Racism, Resistance, Legacy', 0.5, 'LEC', 'An examination of German culture during the Nazi period (1933-1945). The course will analyze representations of Nazi ideology in the arts (film, art, architecture, and propaganda), the literature of exile and "inner emigration," and the impact of the Nazi legacy on post-war German culture.', 'GER');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GER 385', 'Culture Behind the Iron Curtain', 0.5, 'LEC, TUT', 'An examination of the culture of the Soviet Union, the German Democratic Republic, and other states that experimented with socialism in the 20th century. Representations of Marxist-Leninist and Stalinist ideologies in literature, cinema, and the arts; the role of the state in directing cultural expression; and resistance to totalitarian control of culture are examined. Western representations of the Cold War are also studied.', 'GER');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GER 395', 'Waterloo in Germany Program', 2.5, 'LEC', 'Study abroad at a German university in the fall term.', 'GER');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GER 396', 'Waterloo in Germany Program', 2.5, 'LEC', 'Study abroad at a German university in the winter term.', 'GER');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GER 397', 'Waterloo in Germany Program', 2.5, 'LEC', 'Study abroad at a German university in the spring term.', 'GER');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GER 398', 'Topics in Cultural Studies', 0.5, 'FLD, SEM', 'Selected topics in cultural studies.', 'GER');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GER 399', 'German Abroad', 0.5, 'LEC', 'This immersion course provides students with an introduction to and a familiarity with selected topics in German-speaking environments. The topics range from history and culture, to language and linguistic varieties in the German-speaking world, as well as arts and the German-speaking media landscape. Course offerings include classes abroad as well as field trips.', 'GER');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GER 407', 'Applied Apprenticeship', 0.5, 'LEC', 'For Honours German students interested in a career in international business. The course involves an apprenticeship in an industrial setting in Germany, Austria, or Switzerland of no less than 12 weeks combined with weekly written reports, a presentation, and a final report. The course is offered on a credit/non-credit basis and cannot be used to meet the Core German Course requirement. Due to the costs involved with working in a foreign country, paid positions are eligible for credit in the course.', 'GER');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GER 420', 'Topics in Language Pedagogy', 0.5, 'SEM', 'Topics in the instruction of German and other foreign/second languages.', 'GER');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GER 431', 'Senior Seminar', 0.5, 'SEM', 'Topics in German literary and cultural studies and linguistics chosen by the instructor in consultation with the department. These courses reflect research interests of the faculty and form part of a well-rounded undergraduate education in "Germanistik".', 'GER');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GER 490', 'Senior Honours Project', 0.5, 'RDG', 'German Honours students in their third or fourth year may complete, with departmental permission, a major project, the results of which must be presented in a substantial essay. The topic should reflect the student''s field of interest.', 'GER');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GER 495', 'Reading Course in Approved Topics', 0.5, 'RDG', 'Reading course in topics chosen in consultation with an advisor.', 'GER');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GER 600', 'Methods of Research', 0.5, 'LEC', 'A course designed to foster an understanding of the fundamental notions of critical inquiry and to provide training in intellectual and practical skills common to research in the areas of applied linguistics, film and literary studies.', 'GER');
