INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GER 602', 'Approaches in Literary and Cultural Theory', 0.5, 'LEC', 'Major currents in twentieth-century literary theory are discussed and applied to primary texts so that students can develop their own critical position.', 'GER');
