INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GRK 631', 'Oaths and Curses in Mediterranean Culture', 0.5, 'SEM', 'Greek oaths and curses are studied in the original, along with an examination of how these rituals are derived from Mesopotamian sources, and how they influenced Roman practices.', 'GRK');
