INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 203', 'Methods of Applied History', 0.5, 'LEC', 'This course exposes students to several different forms of applied history, which may include historic mapping, digitization, genealogy, public policy research, corporate history, legal research, tourism, the commemoration industry, historical fiction and creative non-fiction, heritage issues, and the making of historical documentaries. The specific mix of topics addressed in any particular year will vary, depending on the instructor.', 'HIST');
