INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GRK 133', 'New Testament Greek 1', 0.5, 'LEC', 'An introduction to Greek grammar with appropriate grammatical exercises and development of vocabulary.', 'GRK');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GRK 134', 'New Testament Greek 2', 0.5, 'LEC', 'The completion of the study of Greek grammar and syntax with appropriate exercises and translation of various texts of the Greek New Testament.', 'GRK');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GRK 201', 'Intermediate Greek', 0.5, 'LEC', 'This course is a continuation of GRK 102/RS 102. It will complete the initial study of ancient Greek grammar and devote substantial time to the reading of ancient Greek literature. Texts read will include a selection of poetry and prose in a variety of fields, including authors such as Homer, Herodotus, Euripides, Plato, the New Testament, and Josephus.', 'GRK');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GRK 202', 'Selections from Greek Authors', 0.5, 'LEC', 'This course is designed to follow GRK 201/RS 201 and to expand students'' experience of reading ancient Greek texts for the study of classical studies and religious studies. Texts read will include a selection of poetry and prose in a variety of fields, including authors such as Homer, Herodotus, Euripides, Plato, the New Testament, and Josephus.', 'GRK');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GRK 233', 'Intermediate New Testament Greek', 0.5, 'LEC', 'Readings in the New Testament, in the Septuagint, Papyri and the Apostolic Fathers. Advanced grammar and syntax arising from the readings.', 'GRK');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GRK 234', 'Hellenistic Greek', 0.5, 'LEC', 'An intermediate reading course in Koine Greek. The objective is to read Koine texts rapidly and with a minimum of lexical aids. The focus is on biblical (Septuagint) and extra-biblical texts of the Hellenistic and Patristic periods, such as the Apostolic Fathers, Josephus, and Hellenistic philosophy.', 'GRK');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GRK 331', 'Advanced Studies in Greek: Prose', 0.5, 'LEC', 'A selection of material from one author or several authors within the field of Greek prose. Topics and selections may include oratory, history, philosophy, Demosthenes, Herodotos, Xenophon, Plato, and Aristotle.', 'GRK');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GRK 332', 'Advanced Studies in Greek: Poetry', 0.5, 'LEC', 'A selection of material from one author or several authors within the field of Greek poetry. Topics and selections may include epic, tragedy, comedy, lyric poetry, Homer, Sophokles, Aristophanes, Simonides, and Pindar.', 'GRK');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GRK 341', 'Advanced Studies in Greek: Selected Topics', 0.5, 'LEC', 'An investigation of selected themes, topics, time periods, or genres in Greek.', 'GRK');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GRK 351', 'Greek Composition, Grammar and Reading', 0.5, 'LEC', 'Composition, translation, and grammar with intensive analysis of selected passages.', 'GRK');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GRK 421', 'Greek Epigraphy', 0.5, 'LEC', 'An introduction to Greek inscriptions as evidence for the Greek language and Greek political, religious, legal, social, and economic history.', 'GRK');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GRK 451', 'Senior Greek Composition, Grammar and Reading', 0.5, 'SEM', 'Advanced composition, translation, and grammar with intensive analysis of selected passages.', 'GRK');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GRK 490', 'Senior Studies in Greek: Selected Topics', 0.5, 'RDG', 'A selection of material from one author or several authors or an investigation of selected themes, topics, genres at the senior level. Topics or authors may include epic, tragedy, history, philosophy, Hellenistic poetry, Hesiod, Euripides, Menander, Thucydides, Apollonius, Callimachus, and Nonnos.', 'GRK');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GRK 491', 'Senior Studies in Greek: Independent Study', 0.5, 'RDG', 'Under special circumstances, and with the approval of the department, a student or small group of students may arrange to pursue individualized readings under the supervision of a faculty member.', 'GRK');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GRK 600', 'Topics in Greek Language', 0.5, 'SEM', 'This course will involve the reading of original material in ancient Greek will focus on the advanced study of the language itself, although topics may vary from single authors to literary genres to chronological periods.  This course is repeatable provided the topic is different.  Examples could include: Archaic Greek Hexameter; Greek Palaeography; Ancient Greek Hymnography; Late Antique Greek Poetry; Greek Philosophy and Poetry; Hellenistic Composition, Grammar and Reading.', 'GRK');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GRK 601', 'Topics in Greek Language', 0.25, 'SEM', '0.25 credit offering to be combined with LAT 601.  This 0.25 credit version of GRK 600 will allow faculty members to offer single-term courses that combine reading of original sources in both ancient Greek and Latin on selected topics. This course is repeatable provided the topic is different.  Examples could include:  Greek Palaeography or Greek Epigraphy.', 'GRK');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GS 901', 'Preparing for University Teaching', 0, 'LEC', 'Students will be required to attend six workshops, including two mandatory ones from TRACE (Course Design and Understanding the Learner), with a written report following the two required workshops plus reports on two more workshops.  The department or faculty TA orientation session, if offered, could be included as one of the six workshops.', 'GS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GS 902', 'Preparing for an Academic Career', 0, 'LEC', 'Students will be required to complete two projects: a research project and a teaching dossier.  To assist in the preparation of these projects, students must attend one workshop and one consultation session for each project.  Reaserch projects may take the form of a presentation and paper, a mini-workshop, or a poster presentation with supporting documentation.', 'GS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GS 903', 'Teaching Practicum', 0, 'PRA', 'Students will be required to teach at least three times (lecture, lab,  tutorial, or presentation) and receive written feedback from an observer (arranged through TRACE). Students also must provide a reflective report after each observation. (At least one event must be a large lecture.)  All work must be deemed acceptable by TRACE evaluators in order for students to pass each component. More specific guidelines and requirements for each component of the Certificate are available electronically at: www.adm.uwaterloo.ca/infotrac/CUThp.html.', 'GS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 102', 'War and Society in Europe, 1914-1945', 0.5, 'DIS, LEC', 'This course explores the impact of World Wars I and II on European society, with a special emphasis on the experiences of the ordinary person.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 103', 'Canadian History Through Biography', 0.5, 'DIS, LEC', 'An examination through lectures and films of the lives of Canadian men and women who have played formative roles in developing the Canadian nation. Examples will be drawn from such areas as politics, religion, business and labour, social reform, arts and entertainment, and sports.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 104', 'An Introduction to Western Intellectual History Since the Renaissance', 0.5, 'DIS, LEC', 'An exploration of some of the questions and answers posed by thinkers on the human predicament from Renaissance and Reformation times to the modern period. Readings range from Luther to J.P. Sartre, Shakespeare to Marx and Freud.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 105', 'Rock ''n'' Roll and US History', 0.5, 'DIS, LEC', 'This course explores the politics, culture, media, race relations, and gender relations of the United States after 1945 through the lens of rock and roll.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 106', 'Canada at War', 0.5, 'DIS, LEC', 'This course introduces students to the ways in which historians have examined Canada''s military experience. Beginning with the Boer War, and continuing through the two World Wars and the post-war era, students examine the political, social, as well as military effects of war on Canada.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 108P', 'Popular Potter', 0.5, 'LEC', 'This course examines all seven of J.K. Rowling''s Harry Potter novels.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 109', 'Ten Days That Shook the World', 0.5, 'DIS, LEC', 'Focusing on ten different days that forever transformed the world''s history, this course introduces students to moments of dramatic change in diverse time periods and places. Topics may include the Fall of Ancient and Modern Regimes; the Age of Discovery; Religious Cataclysm; Revolutionary Wars; Technological, Intellectual, and Scientific Inventions. Students are introduced to the differing methods that historians use to understand the past.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 110', 'A History of the Western World I', 0.5, 'DIS, LEC', 'This course will survey the emergence and development of the Western world, from prehistory to 1715. Complementing the chronological and narrative overview of Western culture and civilization will be thematic surveys of developments in the arts and humanities, science, and socio-political structures.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 111', 'A History of the Western World II', 0.5, 'DIS, LEC', 'This course will survey the emergence and development of the Western world from the 17th century to the present. Complementing the chronological and narrative overview of Western culture and civilization will be thematic surveys of developments in the arts and humanities, science, and socio-political structures.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 113', 'Canadian Business History: Innovators and Entrepreneurs', 0.5, 'DIS, LEC', 'This course examines the role of individuals in the growth of business in Canada. While there will be general examination of Canadian economic development, the principal focus will fall upon leading Canadian business persons and their interests and innovations. The relationship to the state of business, the place of education, and the impact of immigration are other topics that the course will consider.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 115', 'Crusading in the Middle Ages', 0.5, 'DIS, LEC', 'This course examines the historical events and cultural assumptions that led to the European phenomenon of crusading, or holy war, between 1095 and 1453.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 120', 'The United States at War, 1861-1945', 0.5, 'DIS, LEC', 'This course will explore the social, cultural, and military impact of the Civil War and World Wars I and II on American society.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 191', 'Special Topics in History', 0.5, 'DIS, LEC', 'One or more topics courses will be offered from time to time as announced by the History Department and geared to first-year students. Topics will be dependent upon special interest and/or instructional interests by non-regular or visiting faculty.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 200', 'History and Film', 0.5, 'DIS, LEC', 'An introduction to issues in modern cultural history through the study of selected narratives and documentary films with supplementary reading, lectures, and discussions.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 201', 'Columbus and After: New Worlds in the Americas', 0.5, 'DIS, LEC', 'Beginning with Columbus, this course introduces the history of early America as it was shaped by the encounters between colonizers and colonized. Particular attention is paid to the varied nature of these encounters, and their contested interpretation by historians and others.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 205', 'History of Western Sport', 0.5, 'DIS, LEC', 'This course considers the historical impact of Western sport. It traces the history from individual play through amateurism to professionalism, big business, and media. It examines sport''s social role within local, national, and international communities, and its relationship to class, gender, leisure, race, and politics.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 206', 'The Victorian Age', 0.5, 'DIS, LEC', 'During the reign of Queen Victoria (1837-1901), Britain experienced profound change with the expansion of its population, industry, and empire. Poverty and gender and racial discrimination persisted in spite of a marked expansion in political rights. This course will address both progressive and regressive forces during this era, focusing on issues of culture, politics, imperialism, and society.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 209', 'Smallpox to Medicare: Canadian Medical History', 0.5, 'DIS, LEC', 'Starting with Aboriginal medicine, the course examines topics such as the rise of the medical and nursing professions, changing public attitudes to health and disease, and the evolution of the Canadian health insurance system.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 210', 'History of Ancient Law', 0.5, 'DIS, LEC', 'A historical introduction to law in the ancient world. Babylonian, Assyrian, Hittite, and Roman law, legal practices, and concepts will be examined.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 211', 'British History to 1485', 0.5, 'DIS, LEC', 'A survey of the main stages in the transition of Britain from a remote province of the Roman Empire to a prominent state in post-Reformation Europe. Within the chronological framework, political and constitutional as well as ecclesiastical and social developments will be examined.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 213', 'A History of Popular Culture', 0.5, 'DIS, LEC', 'This course introduces students to the history of Western popular culture and may include the study of popular literature, spectacle and performance, witchcraft, crime, sexual attitudes, consumption, sports, advertising, and the media.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 214', 'History of Women in the Modern United States', 0.5, 'DIS, LEC', 'This course will examine women''s social, political, cultural, and economic position in the United States from 1920 to the present. We will study the evolving understandings of women''s "proper place" in society, which has varied based upon race, class, ethnicity, and region. We will consider women''s daily lives and the forces that brought women into the public sphere. Topics covered will include women''s political activism, legal position, sexuality, and paid and unpaid labour.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 215', 'Canadian Women in Historical Perspective', 0.5, 'DIS, LEC', 'This course will focus on the interrelationship of women and Canadian society through an examination of women''s private and public lives.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 220', 'The Vietnam War', 0.5, 'DIS, LEC', 'The Vietnam War, the longest war in U.S. history, was fought on two fronts, by American GIs abroad and anti-war protesters at home. Those two subjects, as well as a history of the war from Vietnamese perspectives, are the focus of this course.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 221', 'Racism and Response in Canadian History', 0.5, 'DIS, LEC', 'The "race problem" has appeared on the Canadian public agenda, but the issue is not of recent origin. This course examines Euro-Canadian attitudes and practices toward non-European minorities from pioneer times to the present and sets racial policies in the context of the evolution of a Canadian national identity.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 223', 'The Holocaust in History', 0.5, 'DIS, LEC', 'An examination of the Holocaust in the context of the history of modern racism. Study topics will include historic anti-Judaism, scientific racism and the development of modern antisemitism, Nazi "race" ideology, wartime policies from ghetto to genocide, resistance movements, Nuremberg trials, Holocaust denial, and universal lessons from the Holocaust.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 224', 'Food, Culture, and History', 0.5, 'DIS, LEC', 'This course will examine the role of foodstuffs and foodways in world history, with an emphasis on Canada in the 20th century. Themes such as colonialism, immigration, ethnic identity, religion, gender, famine, and political policy will be examined to explore how food, and its associated habits and customs, has been central to the evolution of cultural patterns of the past.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 225', 'History of Education in Canada', 0.5, 'LEC', 'This course considers the development of education as an institution within Canadian society and provides an understanding of significant educational issues and policies from a historical perspective.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 226', 'Canada in World War II', 0.5, 'DIS, LEC', 'The Canadian experience in World War II is still a subject of considerable debate. This course will employ lectures, films, and discussion groups to examine the war''s impact on the social, economic, political, and military life of the country from 1939 to 1945.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 227', 'The French Revolution and Napoleonic Europe', 0.5, 'DIS, LEC', 'This course will examine the causes and consequences of the French Revolution and the impact of Napoleon''s reign in France and Europe.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 230', 'Introduction to the Modern Middle East', 0.5, 'DIS, LEC', 'This course examines the modern political history of the Middle East, with an emphasis on international affairs. It examines the colonization of the Middle East, the rise of national self-determination and nation-states, enduring Arab-Israeli conflicts, the Cold War, and the impact of U.S. foreign policy in shaping the modern Middle East.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 231R', 'The History of East Asian Communities in Canada', 0.5, 'DIS, LEC', 'This course examines the evolution of the Chinese, Japanese, and Korean communities in Canada as well as their significance for Canadian economic, social, and political life in the 19th and 20th centuries.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 232', 'A History of Peace Movements', 0.5, 'LEC', 'A survey of individuals and groups that have created popular movements for peace globally and locally throughout history. The scope will be international, with a particular focus on the 19th and 20th century movements. The choice of peace movements will allow for a contrast in comparison of ideology, strategy, and impact.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 234', 'The Catholic Church in Canada', 0.5, 'DIS, LEC', 'An examination of the role played by the Church in the social, political, and economic life of Canada from 1867 to the present.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 235', 'History of Christianity', 0.5, 'DIS, LEC', 'The development of Christianity in its Roman Catholic, Eastern Orthodox, and Protestant traditions from the time of Christ to the present.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 236', 'Law and Society in the Middle Ages', 0.5, 'DIS, LEC', 'A study of the laws and legal procedures of the Middle Ages. This course examines the relationship between legal procedures and institutions and the medieval societies that produced them.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 237', 'The Ancient Near East and Egypt', 0.5, 'DIS, LEC', 'A study of the civilizations of the ancient Near East focusing on Mesopotamia (Sumer and Akkad, the Babylonian Dynasty, and the Third Dynasty of Ur), Hatti, Assyria, Egypt, and Persia.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 239', 'History of Modern China, 1911 to the Present', 0.5, 'DIS, LEC', 'Some of the topics studied in this course include: the three stages of warlordism, the May Fourth Movement, and the structure of society in the People''s Republic of China.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 242', 'Greek History', 0.5, 'LEC', 'A survey of ancient Greek history, from the Bronze Age to Alexander the Great, emphasizing particularly its political and military aspects.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 243', 'European Business History: From Workshop to Factory and Beyond', 0.5, 'DIS, LEC', 'This course examines the changing nature of work and the workplace in Europe and the impact of those changes on European society. The objective of this course is to develop a better understanding of today''s workplace and its challenges by exploring its historical roots and the forces that gave it shape.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 247', 'Mennonite History: A Survey', 0.5, 'DIS, LEC', 'This course covers Mennonite origins, teachings, migrations, settlement patterns, divisions, leaders, institutions, and religious and social practices, indeed all facets of Mennonite history in various national settings.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 250', 'What is History? An Introduction to Historical Thinking', 0.5, 'DIS, LEC', 'This course provides a collegial learning setting within which students are introduced to techniques of historical writing and research, and some examples of the best of recent historical scholarship.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 252', 'Roman History', 0.5, 'LEC', 'A survey of ancient Roman history, from the Republic to the Empire, emphasizing particularly its political and military aspects.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 253', 'Canada: Cultures and Conflicts in the Colonial Era', 0.5, 'DIS, LEC', 'This course examines the major themes in pre-Confederation Canadian history including the rise and fall of New France, the creation of British North American societies in the Maritimes and Upper Canada, and economic and political development.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 254', 'Canada Since 1867: A New Nation', 0.5, 'DIS, LEC', 'This course examines Confederation, the rise of political parties, Canadian external relations, western discontent, the impact of both World Wars, and political and economic changes in Canada since 1867.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 255', 'History of Childhood and Youth in Canada', 0.5, 'DIS, LEC', 'This course offers a Canadian history of childhood and youth. While childhood may seem like a timeless, eternal concept, it is actually a modern one. This course considers age as a category of analysis, complementing other key concepts such as gender, class, and ethnicity/race.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 257', 'The United States through the Civil War Era', 0.5, 'DIS, LEC', 'This historical survey focuses on the emergence of the United States as a nation. The topics explored may include Indigenous peoples, slavery, race, gender, labour, immigration, urbanization, culture, sectionalism, politics, and ideologies.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 258', 'The United States since the Civil War Era', 0.5, 'DIS, LEC', 'This course begins in the aftermath of the Civil War and ends at the present day. Topics may include major social movements, the place of the United States in world politics, immigration and imperialism, and the economy.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 260', 'Europe: 410-1303', 0.5, 'DIS, LEC', 'The political, cultural, economic, and ecclesiastical development of Europe from the fall of the Roman Empire to the end of the High Middle Ages.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 262', 'Early Modern Europe 1450-1700', 0.5, 'DIS, LEC', 'This course examines European life in the Early Modern Period (1450-1700) and investigates the social, political, religious, and intellectual changes during the Renaissance, the Reformations, and the era of explorations.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 263', 'The Age of Revolution: Europe in the 19th Century', 0.5, 'DIS, LEC', 'This period marks the emergence of modern-day Europe. The course focuses on the way in which European society, politics, and culture changed, and why. It also examines the continent''s descent into war in 1914.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 264', 'Western Europe Since 1945', 0.5, 'DIS, LEC', 'Western Europe since the end of World War II. Focus will be on the Cold War, political and social movements.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 265', 'Eastern Europe Since 1945', 0.5, 'DIS, LEC', 'An introduction to the social, economic, and political history of the countries of Eastern Europe since 1945. Topics may include the implementation of Communism, daily life, the secret police, women, opposition, and revolution.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 266', 'The British Empire 1857-1956', 0.5, 'DIS, LEC', 'This course assesses the transformation of the British Empire from its position of comparative strength in the mid 19th century to decolonization and the emergence of the Commonwealth after the
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 268', 'A Global History of Empires', 0.5, 'DIS, LEC', 'This course examines the role of empires in modern history. It examines how empires were formed, how they functioned, how they were resisted, and how they collapsed. While the focus will be on the European empires, we also assess other examples, including the empires of the Ottomans, the Japanese, the Chinese, and the United States.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 269', 'Indigenous Histories in Canada', 0.5, 'LEC', 'This course examines the histories of Indigenous peoples (First Nations, Inuit, M√©tis) in local and national contexts. It emphasizes the relationship between Indigenous peoples and settler society, including histories of treaty-making and colonial policies of dispossession and assimilation such as the Indian Act and the Indian residential school system. We will highlight forms of Indigenous resistance and resurgence, assertions of land rights, and the diversity of communities and cultures.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 271', 'Global Indigenous Issues', 0.5, 'DIS, LEC', 'This course examines the histories of Indigenous peoples from around the world, covering areas such as educational assimilation, the internationalization of Indigenous rights, lands and resource development conflicts, Indigenous-state relations, language and cultural revitalization, and international political activism. We examine local Indigenous histories and place these experiences into the larger global context.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 275', 'The Modern World in Historical Perspective', 0.5, 'DIS, LEC', 'This survey of the 20th century explores the non-Western world''s response to a series of selected global themes: the rise of the West; post-colonialism; war and peace; human migration; culture; international organizations; climate change; human rights; disease; and globalization.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 277', 'Canadian Legal History', 0.5, 'DIS, LEC', 'This course examines the Canadian legal system from colonial times to the present with particular emphasis on such themes as law and the economy, courts and judiciary, the legal profession, family and criminal law, women and the law, and civil liberties.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 278', 'Red Star vs. Swastika: Russia and WWII', 0.5, 'DIS, LEC', 'This course examines the Eastern Front in World War II. It investigates such themes as the impact of totalitarian state systems and radical ideologies on the war, interwar diplomacy, key campaigns, genocide, the wartime economies, propaganda, resistance and collaboration, the plight of prisoners of war, women on the battlefield, and relations among members of the opposing coalitions.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 282', 'History of Modern South Asia 1750-2000', 0.5, 'DIS, LEC', 'This course provides an overview of the political developments and conflicts that have shaped modern South Asia from 1750 to 2000 through an examination of the region''s cultures, political systems, encounters with Western imperialism, nationalist movements, and the impact of the Cold War.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 291', 'Special Topics in History', 0.5, 'DIS, LEC', 'One or more term courses will be offered from time to time as announced by the History Department. Topics will be dependent upon special research and/or instructional interests of faculty.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 300', 'History and the Human Sciences', 0.5, 'DIS, LEC', 'This introduction to historiography traces the relationship between history and other human sciences (anthropology, economics, literature, philosophy, and sociology) since the 19th century. In addition to strengthening critical skills, it offers interdisciplinary perspectives on problems of objectivity, documentary evidence, forms of story-telling, and causal explanations.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 302', 'Applied History Project', 0.5, 'DIS, PRJ', 'This course provides the opportunity to design, research, and produce a history project in a multimedia format. Students incorporate archival, visual culture, and oral history research in their projects and examine the politics, ethics, and practice of historical research and its presentation to wide audiences.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 303', 'History Gone Digital: An Introduction to History with the Web', 0.5, 'DIS, LEC', 'Digital history, the application of new and emerging technologies to the study of history, is an exciting new field. This course explores the literature on digital history and then puts theory into practice by exploring the digital collection, evaluation, and production of historical knowledge.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 304', 'Heresy and Religious Crises in Late Medieval Europe', 0.5, 'DIS, LEC', 'An exploration of the impact of social crises on late medieval religious modes of expression. Topics will include the Great Famine, the Black Death, the Avignon Papacy and Western Schism, the development of heretical movements, and the eventual disintegration of European religious unity.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 305', 'Historical Memory and National Identity', 0.5, 'DIS, LEC', 'What factors create a national identity: historical events or socially-constructed historical memory? By analyzing key themes such as elite and popular culture, historical sites and commemorations, sports, and iconic public events, this course demonstrates how nation-states and other communities have created a series of evolving national identities.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 309', 'The Discourse of Dissent', 0.5, 'DIS, LEC', 'A study of the social, historical, and rhetorical dimensions of collective action. Topics may include health and welfare movements, civil rights and anti-war protests, and environmentalism.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 310', 'The American West: Legend and Reality', 0.5, 'DIS, LEC', 'An exploration of westward expansion in the United States in the 18th and 19th centuries and its impact on American popular imagination. Themes will include explorations, indigenous peoples, labor, women, violence, and frontier culture.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 311', 'International Relations, 1890-1951', 0.5, 'DIS, LEC', 'This course examines the international relations of the great powers from the rise of Wilhelmine Germany in the 1890s to the first steps of European integration in the early 1950s. Attention will be paid to the formation of foreign policy, alliances, leadership, war-making, and peace-making.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 313', 'History of the Family in North America', 0.5, 'DIS, LEC', 'This course will consider the history of private interactions between family members in North America, as well as the family''s relationship to public forces such as politics, the law, social movements, and the economy. Other topics covered in this course include changing conventions of courtship and dating, marriage, divorce, parenthood, and childhood.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 314', 'The American Civil Rights Movement', 0.5, 'DIS, LEC', 'This course will explore the Civil Rights movement in the United States from the 1950s to the 1970s. Topics will include the origins and evolution of the movement, tactics, key figures, and the role of the federal government.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 315', 'U.S. and the World', 0.5, 'DIS, LEC', 'This course examines the history of foreign relations of the United States from the "Age of Imperialism" through the "War on Terror". Topics will include the Great War, Wilsonianism, World War II, the Cold War, human rights, and post-9/11 U.S. foreign policies.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 316', 'The Russian Revolution', 0.5, 'DIS, LEC', 'This course traces the history of the Russian Revolution from 1861 to 1924. It examines the intellectual and social roots of the Revolution, a variety of revolutionary theories, parties, agendas and methods of power struggle, the Bolshevik seizure of power and the ensuing civil war, culminating in the establishment of a communist dictatorship.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 317', 'History of Sexuality: The Pre-Modern Period', 0.5, 'DIS, LEC', 'This course introduces students to the history of Western sexuality, beginning with the ancient world and focusing primarily on the Middle Ages and the transition to modernity.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 318', 'History of Sexuality: The Modern Period', 0.5, 'DIS, LEC', 'This seminar introduces students to the history of sexuality. The course focuses on the 19th and 20th centuries.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 321', 'Human Rights in Historical Perspective', 0.5, 'DIS, LEC', 'A detailed analysis of selected topics in the history of human rights. Special attention will be paid to revolutionary developments since World War II, and to the emergence of modern human rights policies. Topics will be examined through assigned readings, lectures, and films.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 329', 'Origins of the Common Law', 0.5, 'DIS, LEC', 'A study of the common law of England from its introduction in the 11th century to the 15th century. Original documents and court cases will be examined.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 340', 'A Social History of Europe: 1789-1914', 0.5, 'DIS, LEC', 'European society amidst the dramatic changes of the 19th century. Emphasis is given to the impact of the French and industrial revolutions on class, the family, religion, and living conditions.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 341', 'The Nazi Occupation of Europe', 0.5, 'DIS, LEC', 'This course examines the nature and impact of Nazi occupation on Western and Eastern Europe preceding and during World War II and the responses of the people occupied.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 347', 'Witches, Wives, and Whores', 0.5, 'DIS, LEC', 'This course explores the ways in which Early Modern European women experienced, participated in, shaped, and responded to the world they inhabited. It investigates the ways in which women negotiated the Early Modern world as it unfolded in the Renaissance, the Reformation, European encounters around the world, and the challenges of everyday life.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 348', 'The Radical Reformation', 0.5, 'DIS, LEC', 'A study of 16th century Anabaptism - a religious Reformation movement dissenting from both Protestantism and Roman Catholicism - its origins; its social, political, and theological content; and its relationship to such independent dissenters as Sebastian Franck.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 350', 'Canada and the Americas', 0.5, 'DIS, LEC', 'This course will examine the economic, cultural, and diplomatic aspects of Canada''s relationship with the United States, Latin America, and the Commonwealth Caribbean from the time of the American Revolution to the present.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 351', 'Canada: The Immigrant Experience', 0.5, 'DIS, LEC', 'Immigrants and immigration have always been central to Canadians'' perceptions of themselves as a country and as a society. This course will examine the immigrant experience and Canada''s changing policies and attitudes toward immigration and immigrants from New France to the present.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 356', 'Russia: From Tsars to Putin', 0.5, 'DIS, LEC', 'This course examines the history of Russia and the Soviet Union from the beginning of the 20th century to the present. Topics include: origins and nature of the Russian Revolution, communist society, Stalinism, the Cold War, and impact of the communist experience on contemporary Russia.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 358', 'Nazi Germany', 0.5, 'DIS, LEC', 'An examination of the social, economic, and political history of Nazi Germany. Topics may include the rise of the Nazis, the secret police, war, population policies and mass murder, culture, and women.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 369', 'The Politics of Decolonization', 0.5, 'DIS, LEC', 'This course examines the break-up of empires in the mid/late 20th century, and assesses how the colonial experience has shaped relations between and among former imperial polities to the present. Focus is given to questions of political identity in colonial and post-colonial societies, and how these identities shape current political challenges.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 371', 'Ireland Before the Famine', 0.5, 'DIS, LEC', 'A focus on social and economic determinants of Irish History from the Penal Era to the 19th century struggle for Catholic emancipation and the Great Famine.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 372', 'Ireland After the Famine', 0.5, 'DIS, LEC', 'An exploration of the political, social, and cultural history of Ireland from the Famine to the end of the 20th century including the formation of the Irish State, the Republic, and the "Troubles".', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 374', 'Canada''s Social History', 0.5, 'DIS, LEC', 'A topical consideration of key themes, approaches, and chronologies in the history of society in Canada.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 379', 'Reformation History', 0.5, 'DIS, LEC', 'A study of the major 16th century reformers, and their intellectual background in humanism and late medieval scholasticism. Special attention will be given to the Lutheran and Reformed traditions, and their ideological, social, and political expressions.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 380', 'History of the Canadian North: From Pre-contact to the Creation of Nunavut', 0.5, 'DIS, LEC', 'The idea of "northerness" is central to our national identity, yet few "southern" Canadians have an appreciation of the historical development of Northern Canada. This course will focus on political, social, cultural, and environmental histories, and will introduce students to major themes in Canadian Northern history, from pre-contact to the creation of the territory of Nunavut in 1999.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 385', 'From Macdonald to Laurier: Canada, 1841-1921', 0.5, 'DIS, LEC', 'A topical examination of major political and social developments over this eighty-year period. These include Irish immigration, Confederation, the Riel rebellions, social reform, the development of labour and business, and the Boer and First World Wars.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 388', 'Modern Canada', 0.5, 'DIS, LEC', 'Lectures, tutorials, and independent research will provide a decade-by-decade examination of the central social, political, and economic themes that have helped characterize "modern Canada".', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 389', 'Canada in World Affairs', 0.5, 'DIS, LEC', 'An analytical and historical examination of Canadian foreign policy in the international system. Domestic sources of Canadian foreign policy and international sources of Canadian foreign policy are examined in detail.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 391', 'Special Topics in History', 0.5, 'DIS, LEC', 'One or more term courses will be offered from time to time as announced by the History Department. Topics will be dependent upon special research and/or instructional interests of faculty.', 'HIST');
