INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 491', 'Independent Study in Special Topics', 0.5, 'RDG', 'Under exceptional academic circumstances a student may seek permission to pursue a course of independent study under the direction of a faculty member. The special topic is determined in consultation between them.', 'HIST');
