INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 719', 'War and Society Major Field', 1, 'SEM', 'This field will attract those who are interested in the impact of military conflict, in conventional and non-conventional forms, on civil society.  It will focus in particular on the two World Wars and the "small wars"  that developed in the last part of the twentieth century, and be flexible enough to include a range of related topics across time and place that reflect the interests of faculty and students.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 725', 'Cold War Era History Major Field', 1, 'SEM', 'Major field seminars meet biweekly throughout the Fall and Winter terms for discussion of a reading list of 50 books selected by the instructor.  During the subsequent Spring term major field participants read a further 50 books selected by the instructor in consultation with each participant''s supervisor.', 'HIST');
