INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ITAL 101', 'Introduction to Italian Language 1', 0.5, 'LAB, LEC', 'An intensive study of the fundamentals of grammar and conversation. The language laboratory will be used.', 'ITAL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ITAL 102', 'Introduction to Italian Language 2', 0.5, 'LAB, LEC', 'A continuation of ITAL 101, with more emphasis on conversation and everyday uses of language.', 'ITAL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ITAL 155', 'Intensive Introductory Italian Language', 0.5, 'LEC', 'This course has been designed with the student of the University of Waterloo School of Architecture in mind. It aims to present the basic principles of the Italian language, geography, and culture in a practical and efficient way in order to facilitate the transition to life in Italy during the student''s period of study abroad. Throughout the course, the goal of practical, communicative competence will be emphasized and cultivated via in-class activities and assignments focusing on five areas of performance: listening, speaking, reading, writing, and culture.', 'ITAL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ITAL 201', 'Intermediate Italian 1', 0.5, 'LEC, TUT', 'Advanced study of grammar. Conversation sessions based on intermediate-level readings reflecting contemporary Italian life. Intensive practice in the spoken and written language.', 'ITAL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ITAL 202', 'Intermediate Italian 2', 0.5, 'LEC, TUT', 'A continuation of ITAL 201.', 'ITAL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ITAL 251', 'Issues in Contemporary Italian Society', 0.5, 'LEC', 'Through lectures, class discussions, and compositions in Italian, this course studies diverse aspects of contemporary Italian society including politics, history, the arts, and popular culture.', 'ITAL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ITAL 255', 'Italian for Business and Technology', 0.5, 'LEC', 'This course strengthens writing skills in Italian with emphasis on technological vocabulary, composition, business terminology, and correspondence.', 'ITAL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ITAL 311', 'Medieval Italian Literature', 0.5, 'LEC', 'An introduction to the Italian literature of the Middle Ages, with special reference to selections from the major works by Dante, Petrarch, and Boccaccio.', 'ITAL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ITAL 312', 'Renaissance Italian Literature', 0.5, 'LEC', 'An introduction to the Italian literary production of the fifteenth and sixteenth centuries, focusing on selections from the major works of the period, including some by Machiavelli, Ariosto, and Tasso.', 'ITAL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ITAL 370', 'Women Writers of the Italian Renaissance', 0.5, 'LEC', 'This course explores selected works by women writers of the Italian 16th century by focusing on their modes of adherence or challenges to the patriarchal literary and cultural canon of the day. It will examine early modern gender issues and innovative forms of self-expression as reflected in the writings of such women as Veronica Franco, Gaspara Stampa, Vittoria Colonna, Tullia D''Aragona, and Isabella di Morra. The course attempts to explore these writers'' stances as both consumers and producers of culture, as well as their contribution to the debate on women fashionable at the time.', 'ITAL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ITAL 391', 'The Italian Novel and Cinema', 0.5, 'LEC', 'A survey of some of the principal novels of the 20th century in Italy in association with their cinematic versions by eminent Italian film directors.', 'ITAL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ITAL 392', 'Modern Italian Poetry and Theatre', 0.5, 'LEC', 'This course studies the works of major Italian poets and playwrights, modern and contemporary, paying special attention to works by women authors.', 'ITAL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ITAL 394', 'Italian Studies in Italy', 0.5, 'FLD', 'Italian literature, art, culture, and history studied abroad in Italy.', 'ITAL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ITAL 396', 'Special Topics/Directed Readings', 0.5, 'RDG', 'This course gives the student an opportunity to study authors and works of special interest which are not covered in other courses.', 'ITAL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ITAL 397', 'Special Topics/Directed Readings', 0.5, 'RDG', 'Winter term of ITAL 396/ITALST 396.', 'ITAL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ITALST 111', 'Women, Family, Sex, and Tradition', 0.5, 'LEC', 'The course studies the themes of family, sex, and tradition as well as violence and identity as they are addressed in the art, the cinema, and the literature of Italian women.', 'ITALST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ITALST 260', 'Great Works in Italian Literature', 0.5, 'LEC', 'This course is a chronological survey of Italian literature from the 13th century to the 20th century. It will introduce the student to the most representative works in Italian literature. Special emphasis will be given to literary and cultural backgrounds of the authors and their works.', 'ITALST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ITALST 270', 'Modern Italy', 0.5, 'LEC', 'An examination of the evolution of modern Italy from 1789 to the present. The events which transformed Italy from a "geographic expression" into a modern unified state, including economic, social, political, and cultural developments, will be studied.', 'ITALST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ITALST 271', 'Italian Canadian Experience', 0.5, 'LEC', 'An examination of Canada''s sixth largest ethno-cultural group, from the early colonial period to the present. Emphasis will be placed on the changing nature of immigration and the evolution of the Italian community since the late nineteenth century.', 'ITALST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ITALST 291', 'Italian Culture and Civilization 1', 0.5, 'LEC', 'A survey of developments in Italian culture -- history, literature, and the arts -- up to and including the Renaissance.', 'ITALST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ITALST 292', 'Italian Culture and Civilization 2', 0.5, 'LEC', 'A survey of developments in Italian culture - history, literature, painting, and music - in the post-Renaissance period, with emphasis on modern Italy.', 'ITALST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ITALST 311', 'Medieval Italian Literature', 0.5, 'LEC', 'An introduction to the Italian literature of the Middle Ages, with special reference to selections from the major works by Dante, Petrarch, and Boccaccio.', 'ITALST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ITALST 312', 'Renaissance Italian Literature', 0.5, 'LEC', 'An introduction to the Italian literary production of the fifteenth and sixteenth centuries, focusing on selections from the major works of the period, including some by Machiavelli, Ariosto, and Tasso.', 'ITALST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ITALST 360', 'Dante''s Divine Comedy', 0.5, 'LEC', 'This course examines the various strands of what is one of the greatest works in Western literature, Dante''s Divine Comedy. The course will engage students in a critical reading of the text''s various layers of meaning, which emphasize perennial issues of our human condition.', 'ITALST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ITALST 370', 'Women Writers of the Italian Renaissance', 0.5, 'LEC', 'This course explores selected works by women writers of the Italian 16th century by focusing on their modes of adherence or challenges to the patriarchal literary and cultural canon of the day. It will examine early modern gender issues and innovative forms of self-expression as reflected in the writings of such women as Veronica Franco, Gaspara Stampa, Vittoria Colonna, Tullia D''Aragona, and Isabella di Morra. The course attempts to explore these writers'' stances as both consumers and producers of culture, as well as their contribution to the debate on women fashionable at the time.', 'ITALST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ITALST 391', 'The Italian Novel and Cinema', 0.5, 'LEC', 'A survey of some of the principal novels of the 20th century in Italy in association with their cinematic versions by eminent Italian film directors.', 'ITALST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ITALST 392', 'Modern Italian Poetry and Theatre', 0.5, 'LEC', 'This course studies the works of major Italian poets and playwrights, modern and contemporary, paying special attention to works by women authors.', 'ITALST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ITALST 394', 'Italian Studies in Italy', 0.5, 'FLD', 'Italian literature, art, culture, and history studied abroad in Italy.', 'ITALST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ITALST 396', 'Special Topics/Directed Readings', 0.5, 'RDG', 'This course gives the student an opportunity to study authors and works of special interest which are not covered in other courses.', 'ITALST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ITALST 397', 'Special Topics/Directed Readings', 0.5, 'RDG', 'Winter term of ITAL 396/ITALST 396.', 'ITALST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('JAPAN 101R', 'First-Year Japanese 1', 0.5, 'LAB, LEC, TST, TUT', 'An introductory course for students who have little or no knowledge of Japanese to develop basic listening, speaking, reading, and writing skills. Practical oral and written exercises incorporating the Hiragana Writing System provide a firm grammatical foundation for further study.', 'JAPAN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('JAPAN 102R', 'First-Year Japanese 2', 0.5, 'LAB, LEC, TUT', 'Listening, speaking, reading, and writing skills acquired in JAPAN 101R are further developed. Practical oral and written exercises incorporating the Katakana Writing System are used to develop a more solid grammatical base.', 'JAPAN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('JAPAN 111R', 'Japanese for Business 1', 0.5, 'LAB, LEC, TUT', 'An introductory course to develop basic comprehension, speaking, reading, and writing skills specifically related to the Japanese business culture. This course is designed for students with little or no previous knowledge of Japanese.', 'JAPAN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('JAPAN 112R', 'Japanese for Business 2', 0.5, 'LAB, LEC, TUT', 'A continuation of JAPAN 111R designed to further develop listening, speaking, reading, and writing skills. Language skills required for the business environment are stressed.', 'JAPAN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('JAPAN 201R', 'Second-Year Japanese 1', 0.5, 'LAB, LEC', 'A continuation of the study of grammar and vocabulary through development of listening, reading, writing, and speaking skills. Some study of Japanese culture is also included. By the end of the course, 120 Kanji (Chinese characters in their Japanese readings) will have been introduced.', 'JAPAN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('JAPAN 202R', 'Second-Year Japanese 2', 0.5, 'LAB, LEC', 'In this course students continue to develop their language skills with an increased emphasis on spoken Japanese. In addition, students work on improving grammatical accuracy and vocabulary development as well as continue to acquire basic information about Japanese culture. The writing of an additional 200 Kanji is taught.', 'JAPAN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('JAPAN 272R', 'Japanese Culture and Society', 0.5, 'LEC', 'The course explores the interface of tradition and modernity in Japanese life. Topics include traditional and modern Japanese culture, language, education, art, technology, and business.', 'JAPAN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('JAPAN 301R', 'Third-Year Japanese 1', 0.5, 'LAB, LEC', 'This course concentrates on advanced conversation, polite forms, and idioms. It provides an opportunity to revise and practise the Hiragana and Katakana writing forms. Upon completion, students should be able to write 800 characters and use a Japanese dictionary with ease.', 'JAPAN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('JAPAN 302R', 'Third-Year Japanese 2', 0.5, 'LAB, LEC', 'This course builds on the materials learned in JAPAN 301R by employing current newspaper articles for reading practice, thematic discussions, and writing exercises in order to enhance students'' skill in the use of the Japanese language.', 'JAPAN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('JS 105A', 'Classical Hebrew 1', 0.5, 'LEC', 'Biblical Hebrew for beginners. A study of the alphabet, and some of the basic vocabulary and grammar of the language.', 'JS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('JS 105B', 'Classical Hebrew 2', 0.5, 'LEC', 'A continuation of RS 131/JS 105A. Most of the rules of grammar will be covered in this course and students will begin to read texts in the original language.', 'JS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('JS 364', 'Jewish Humour: Laughing Your Way Through History', 0.5, 'LEC', 'Jewish comedians have dominated humour in North America from Groucho Marx to Jerry Seinfeld, Sarah Silverman, Amy Schumer, and Seth Rogen. Studying Jewish humour is critical for understanding the development of Judaism, Jewish identity, immigrant experience, and response to persecution.', 'JS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('JS 120', 'Relationships in the Bible (Old Testament)', 0.5, 'LEC', 'Students will be introduced to the Hebrew Bible by way of selected readings which deal with a particular aspect of the human predicament. The focus will be on exploring relationships via narrative passages in the Old Testament and particularly in the Book of Genesis. The following relationships will be discussed: spousal; human/God; parent/child; siblings; gender issues in narratives of rape, incest, seduction, and dominance.', 'JS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('JS 125', 'Great Texts in the Jewish Tradition', 0.5, 'LEC', 'This course will trace the development of biblical exegesis in the Jewish tradition. Interpretive methods and approaches to problems in the text such as redundancy, contradiction, and gaps will be surveyed, commencing with the Bible itself, through the classical period of the Talmud and concentrating on major medieval commentators. (The biblical episode of the Binding of Isaac will be used as a paradigm to illustrate various approaches to the text.)', 'JS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('JS 130', 'Power and Corruption in the Bible (Old Testament)', 0.5, 'LEC', 'This course will deal with the period of the prophets, e.g., Joshua, Kings, and Samuel. It will examine the uses and abuses of power analyzing the historical narratives and study the conflict between Saul and David, the political as well as the moral rise and fall of David.', 'JS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('JS 150', 'The Quest for Meaning in Modern Judaism', 0.5, 'LEC', 'How does an ancient religious tradition remain relevant in the face of shifting cultural morals and beliefs and especially in light of the West''s emphasis on relativism and freedom of choice? This course will explore the major themes and challenges that face Judaism and the Jewish people at the start of the 21st century. We will compare the insights of an extremely diverse group of Jewish thinkers on the place of tradition in the modern world, and examine the perpetual quest for meaning at the core of Judaism.', 'JS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('JS 203', 'Jewish Responses to the Holocaust', 0.5, 'LEC', 'The catastrophe and devastation of the Holocaust and the radical nature of its evil demanded responses within contemporary Jewish thought, identity, and experience. This course will explore philosophical, theological (Jewish law, rabbinic), literary (novels, poetry, memoirs, plays), and artistic (museums, memorials) attempts to deal with the issues the Holocaust raises.', 'JS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('JS 205', 'The Hebrew Prophets', 0.5, 'LEC', 'A study of the biblical prophets with special attention to their religious experience, social critique, visions of the future, and the writings that bear their names.', 'JS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('JS 210', 'Jewish Philosophy', 0.5, 'LEC', 'The course will explore the thought of various Jewish scholars throughout history on issues that were vital to their faith. The texts studied will be representative of the philosophical and rabbinic traditions on such matters as the nature of God, the problem of evil, creation, miracles, prophecy, and providence.', 'JS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('JS 211', 'Kabbalah: Jewish Mysticism', 0.5, 'LEC', 'This course will survey the roots, history, and symbolism of the Jewish mystical tradition known as Kabbalah. Students will be introduced to the major texts, charismatic mystical masters, and schools of Kabbalah, beginning with the ancient Rabbis through to contemporary exponents such as Hasidim and messianic groups. Particular attention will be focused on the Zohar (Book of Splendour) and popular appeals to the mystical tradition.', 'JS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('JS 215', 'Visions of Israel in Judaism: From Biblical to Modern Times', 0.5, 'LEC', 'This course surveys the significance of the land of Israel in Judaism from historical, textual, and religious perspectives. Topics covered include the politics of Ancient Israel; the concept of Israel in prayer and the rabbinic and medieval Jewish imagination; portrayals of Israel in Christian and Muslim texts; and the origins, visions, and challenges of Zionism and the modern State of Israel.', 'JS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('JS 217', 'Judaism', 0.5, 'LEC', 'An introduction to the religious tradition of the Jews in terms of beliefs, practices, ideals, and institutions from the beginning to the present time.', 'JS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('JS 233', 'The Holocaust and Film', 0.5, 'LEC', 'An examination of the Holocaust as portrayed in feature films and documentaries. Do cinematic attempts capture the horror of the Holocaust faithfully, or trivialize it? The background to anti-semitism, use of religious imagery in propaganda films, and what counts as success or failure in cinematic representations are discussed.', 'JS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('JS 250', 'Special Topics', 0.5, 'LEC', 'One or more special courses will be offered at different times. Consult the department for current offerings.', 'JS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('JS 301', 'Canada and the Holocaust', 0.5, 'DIS, LEC', 'An analysis of the response to the Holocaust, from 1933 to 1945, and the legacy of the event since the war in Canadian society.', 'JS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('JS 306A', 'Intermediate Classical Hebrew', 0.5, 'LEC', 'Reading and grammatical analysis of selected passages from the Hebrew Bible.', 'JS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('JS 306B', 'Ancient Semitic Texts and Inscriptions', 0.5, 'LEC', 'Reading and analysis of Iron Age inscriptions and ostraca in Hebrew or closely related Canaanite dialects, including the Siloam and Mesha inscriptions and the Lachish letters, plus a selection from the Dead Sea Scrolls.', 'JS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('JS 310', 'Jews in the New World', 0.5, 'LEC', 'Push and pull factors led Jews from Europe to the so-called New World (Canada, the United States, South America, the Caribbean) as well as South Africa and Australia. The mass movement of migrants leaving 19th century Europe dramatically transformed world Jewry. This course explores the experiences of various national Jewish communities, the variety of new world Jewries, and the relationship between modern states and their religious and ethnic minorities.', 'JS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('JS 313', 'Moses Maimonides: Life and Thought', 0.5, 'LEC', 'This course examines the life and thought of Moses Maimonides, the most important thinker in Jewish history. It explores his contributions to philosophy, law, biblical interpretation and his attempt to reconcile religion and science.', 'JS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('JS 339', 'The Bible (Old Testament) and Archaeology', 0.5, 'LEC', 'This course examines the Bible in relation to the archaeology and material culture of the Ancient Near East. It will explore how archaeological discoveries contribute to our understanding of the events, personalities and narratives of the Hebrew Bible.', 'JS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('JS 341', 'Jewish Contributions to Political Thought', 0.5, 'LEC', 'This course examines Jewish communal organization and contributions to political thought. Issues discussed include the nature of legitimate authority in conditions of Jewish diaspora, the intellectual and political foundations for governance in the Jewish tradition from the time of the Exodus to the establishment of the modern state of Israel.', 'JS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('JS 350', 'Special Topics in Jewish Studies', 0.5, 'SEM', 'One or more special courses will be offered at different times. Consult the department for current offerings.', 'JS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('JS 450', 'Special Topics in Jewish Studies', 0.5, 'SEM', 'One or more special courses will be offered at different times. Consult the department for current offerings.', 'JS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 1', 'Discussion of Behavioural Issues', 0, 'SEM', 'This is a discussion course', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 10', 'Ergonomics Option Seminar', 0, 'LEC', 'A seminar for students to present and discuss ergonomic issues regarding their previous semester''s work term and to participate in debate of ergonomic issues', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 100', 'Human Anatomy: Limbs and Trunk', 0.5, 'LEC', 'Functionally-oriented regional anatomy of the limbs and trunk. A brief introduction to neuroanatomy is included.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 100L', 'Human Anatomy Lab', 0.25, 'LAB', 'Regional gross anatomy of the limbs and back (and the central nervous and cardiovascular systems, briefly) is examined using pre-dissected human cadavers. For the limbs and back, emphasis is placed on structures (muscles, nerves, and bones) involved in movement at the joints.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 101', 'Biophysical Evaluation Lab', 0.25, 'LAB', 'This lab provides exposure to practical measurement skills in the tests measuring cardiovascular function, neuromuscular function and body composition. Note: Labs offered alternate weeks.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 104', 'Fundamentals of Kinesiology', 0.5, 'LEC', 'An introduction to the issues and approaches used in kinesiology. Presentation of case studies will illustrate the foundational knowledge and procedures used by kinesiologists. Emphasis will be placed on practical skills, critical analysis, problem solving, and the integration of knowledge across the breadth of kinesiology.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 105', 'Cardiovascular and Respiratory Responses to Exercise', 0.5, 'LAB, LEC', 'The basic concepts of cardiovascular, respiratory and thermoregulatory responses to physical activity will be examined and applied to situations encountered in daily life including recreational activities, sport, and the workplace.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 121', 'Biomechanics of Human Activity', 0.5, 'LEC', 'Understanding human activity from a mechanical perspective prepares the student to address major issues which include reducing the risk of injury, optimizing exercise prescription, and understanding clinical evaluations. Specifically, concepts related to functional anatomy, muscle and passive tissue mechanics, anthropometry, electromyography, and linked segment mechanics are introduced and applied to clinical, occupational and athletic situations.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 140L', 'Sport Injury Management Lab', 0.25, 'LAB', 'This lab provides exposure and training in the management of injuries in an athletic population. Techniques include musculoskeletal assessment, emergency treatment, taping and splinting supports, heating, icing, stretching, and fitting protective equipment. Current first aid and CPR certification required.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 204', 'Movement Assessment and Exercise Prescription', 1, 'LEC', 'This course is designed to provide students with an understanding of movement competency and performance enhancement of apparently healthy individuals. The breadth of tests and assessment tools to determine capabilities with respect to mobility, movement patterns, physical activity, fitness and lifestyle will be covered in detail and practiced and applied in a weekly laboratory.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 155', 'Introduction to Neuroscience for Kinesiology', 0.5, 'LAB, LEC', 'An introduction to the principles of the nervous system control of movement, cognition and learning. The course will introduce the basic structure and function of the nervous system as it relates to understanding the control of movement and behaviour. The course will provide a foundation for understanding the neural mechanisms of learning, recovery from injury to the nervous system and the factors that determine skilled performance.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 205', 'Muscle Physiology in Exercise and Work', 0.5, 'LAB, LEC', 'This course examines the structure and composition of the muscle cell and the differences between cells which provide for a diversity of function during activity. Particular attention is also given to the motor nerve and muscle cell type interactions and the applied aspects of energy supply and utilization. Note: Labs offered alternate weeks.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 210', 'Development, Aging and Health', 0.5, 'LEC', 'The physiology of human growth, development and aging is examined, with special reference to the influence of diet, environment, exercise and disease on the normal processes.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 217', 'Human Biochemistry', 0.5, 'LEC', 'An elementary course in human biochemistry including the metabolism and function of proteins, carbohydrates, lipids, enzymatic function, energy metabolism, and introductory genetics. Emphasis is placed on clinical and medical applications.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 221', 'Advanced Biomechanics of Human Movement', 0.5, 'LEC', 'The course is structured to introduce measurement, analytical and computation techniques involving multisegmental, dynamic analysis of human activity. Examples of human activity in occupational, clinical and leisure settings from the perspectives of anthropometry, kinematics, kinetics, energetics, muscle mechanics and electromyography are given. The utility of biomechanical variables in the solution of questions involving human activity is emphasized using lectures and laboratories. Note: Labs offered alternate weeks.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 222', 'Statistical Techniques Applied to Kinesiology', 0.5, 'LAB, LEC', 'An introduction to descriptive and inferential statistics and the interpretation of data. A major consideration of the course is the use of statistics in the solution of problems in Kinesiology and Health Studies.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 242', 'Introduction to Movement Disorders', 0.5, 'LAB, LEC', 'An introduction to selected movement disorders and their implications for physical activity. The movement disorders examined include those which accompany neuromuscular and perceptual-motor impairment, mental retardation, cardio-vascular and respiratory disease.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 250', 'Sociology of Physical Activity', 0.5, 'LEC', 'An introduction to the sociology of physical activity. The course examines physical activity with respect to settings such as the workplace, educational and health systems, exercise, and sport. Particular attention is directed to a consideration of the social significance of physical activity and the social influences and constraints upon access and participation.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 301', 'Human Anatomy of the Central Nervous System', 0.5, 'LAB, LEC', 'Functionally-oriented anatomy of the brain, spinal cord, cranial nerves and the tissues they innervate using predissected cadavers. This course complements other behavioural neuroscience courses including KIN 242, 357, 415, 416, 456, and PSYCH 261, 307, 396.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 307', 'Methods in Physiological Research', 0.5, 'LAB, LEC', 'An introduction to biochemical and physiological methods used by physiologists in clinical and physiological assessment including, but not restricted to: spectophometric and fluorometric techniques, tissue and blood sampling, cell culture techniques, DNA separation and staining. Western blotting, chromatography, cardiovascular imaging and body composition imaging and analysis. Course consists of lecture and laboratory to deliver both the theoretical and practical aspects of these analytical techniques.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 320', 'Task Analysis', 0.5, 'LEC, TUT', 'Task analysis is used to describe and analyze the activities of people in settings such as work, leisure and activities of daily living. There is a range of techniques developed in engineering, ergonomics and psychology to suit the investigator''s purpose and setting. This course allows students to become familiar with a wide range of techniques and use them in a variety of settings. Approximately one technique will be addressed each week in a combined lecture/tutorial setting. Presentations by students are an integral part of the courses.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 330', 'Research Design', 0.5, 'LEC', 'An introduction to the basic principles of scientific inquiry in Kinesiology. A systematic treatment of the logic and practice of methods and techniques employed in research related to physical activity with an examination of design, sampling, data gathering and analysis.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 340', 'Musculoskeletal Injuries in Work and Sport', 0.5, 'LEC', 'A course in the area of sports medicine in which injuries encountered in sport and in the workplace are examined. Materials covered include the mechanisms of injury, tissue biomechanics, pathology, assessment, treatment and prevention of acute and chronic trauma.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 341', 'Selected Topics in Sport and Work Injuries', 0.5, 'LAB, LEC', 'This course covers the mechanisms, pathology, management and prevention of catastrophic injuries encountered in sport and work. Topics include trauma to the head, face, vertebral column, and knee, thermal injury, legal liability and others as requested by the students.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 342', 'Nutrition and Aging', 0.5, 'LEC', 'This course provides an overview of the nutrition and food-related issues, recommendations and best practices for older adults throughout the continuum of care (community to long term care). Determinants of food intake are a focus with specific consideration given to eating environment, psychosocial and physiological changes that influence access, preparation, and consumption of food. An emphasis will be placed on chronic disease prevention and management of nutrition issues and challenges throughout the continuum of care.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 343', 'Micronutrient Metabolism', 0.5, 'LEC', 'This course examines the metabolism of vitamins and minerals in the context of normal human development and aging with an emphasis on disease prevention and understanding the pathogenesis of deficiencies and toxicities. The function and role of vitamins and minerals will be discussed in specific groupings.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 346', 'Human Nutrition and Metabolism', 0.5, 'LEC', 'An overview of nutrient metabolism will be presented with emphasis on diet for the prevention of disease, and maintaining health. An examination of the impact of nutrition on exercise and body composition will also be included.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 352', 'Sociology of Aging', 0.5, 'LEC', 'An introduction to individual and population aging. Topics discussed include: aging from a historical and comparative perspective; aging in subcultures; aging and the social structure; aging and social processes; aging and the environment; work and retirement; and aging and leisure patterns.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 354', 'Psychology of Physical Activity', 0.5, 'LEC', 'A comprehensive survey of psychological factors implicated in physical activity performance. Topics include social cognitive theories of physical activity behaviour, perception and sport performance, exercise training and brain function, exercise as a treatment modality for psychiatric conditions, and urban design as a means of physical activity promotion.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 356', 'Information Processing in Human Perceptual Motor Performance', 0.5, 'LEC', 'An information processing model of perceptual-motor behaviour is presented. Human performance theory is used to study processes mediating input and output information. Specifically, the subprocesses of storage of information in memory, perception, retrieval of information from memory and execution of movement are examined.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 357', 'Motor Learning', 0.5, 'LEC', 'A course focused on the bases and applications of theories of motor learning. Included are selected psychological and neurophysiological processes as they relate to these theories.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 372', 'International Exchange', 2.5, 'LEC', 'Study abroad on an Exchange Agreement approved by the Faculty of Applied Health Sciences.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 391', 'Research Apprenticeship', 0.5, 'PRJ', 'This course provides an opportunity to learn how research is done by working in the research program of a faculty member. The apprenticeship involves unpaid participation for no more than 6-8 hours per week for at least 12 weeks of the term. Detailed requirements and application forms for this course are available at the Kinesiology Undergraduate Office. A student may enrol only once in KIN 391. This course is graded as credit/no credit.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 402', 'Microgravity, Hypo- and Hyperbaric Physiology', 0.5, 'LEC', 'An examination of human cardiorespiratory and metabolic responses at rest and during work in altered gravitational and barometric environments.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 403', 'Occupational and Environmental Physiology', 0.5, 'LEC', 'An analysis of the physiological demands of work place and recreational tasks under various environmental conditions. The course will examine how an individual''s physiological potential might be influenced by the environment and specific task demands with implications for fatigue and/or injury.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 404', 'Physiological Basis of Obesity and Type 2 Diabetes', 0.5, 'LEC', 'This course will examine the environmental and physiological aspects of obesity and Type 2 Diabetes with emphasis on the molecular pathways involved in energy balance regulation. Topics to be covered include epidemiology of obesity and Type 2 Diabetes, regulation of energy intake (appetite), energy storage and energy expenditure, and the role of physical inactivity and activity in the causes and prevention of obesity and Type 2 Diabetes.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 405', 'Exercise Management', 0.5, 'LAB, LEC', 'An examination of the rationale and procedures used in the development of exercise programs for normally healthy individuals.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 406', 'Physiology of Muscle Aging and Disease', 0.5, 'LEC', 'This course will examine the cellular and molecular adaptations that occur in skeletal muscle during aging and disease. Topics to be covered include skeletal muscle satellite cells/stem cells, apoptosis and necrosis, mitochondrial dysfunction, oxidative stress, and inflammation. The influence of physical activity on these biological processes and in the prevention and treatment of skeletal disorders will also be discussed.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 407', 'Coronary Artery Disease - Prevention and Rehabilitation', 0.5, 'LEC', 'This course explores the pathophysiology, electrophysiology, risk factors, and assessment and rehabilitation options related to cardiovascular disease. Major emphasis is placed on the utility of exercise in the assessment and rehabilitation of primary and secondary cardiovascular disease.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 408', 'Cardiovascular Physiology and Pathophysiology', 0.5, 'LEC', 'This course will provide an advanced understanding of heart and blood vessel physiology in normal health, and in cardiovascular disease conditions including hypertension, heart failure, atherosclerosis, and diabetes. This will include examination of mechanisms underlying lifestyle and medical/ pharmacological management of these conditions from both
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 415', 'Clinical Neurophysiology: Fundamentals for Rehabilitation of Human Movement', 0.5, 'LAB, LEC', 'This course explores current clinical neurophysiological concepts important for the control of human movement as well as current techniques used in clinical neurophysiology. Emphasis is placed on the neurophysiology underlying human movement pathologies and the application of this understanding to rehabilitation following injury to the central nervous system.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 416', 'Neuromuscular Integration', 0.5, 'LEC', 'An examination of the neural processes involved in the maintenance of posture and the control of movement.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 418', 'Age-Related Physical and Mental Changes and Effect of Exercise on Improving Health in the Aged', 0.5, 'LEC, TUT', 'This course is designed to provide the students with the knowledge that enables them to distinguish between normal and abnormal aging; to understand the implications of exercise in health and disease; to help the students understand the different physical and mental conditions they may experience while working with an older person; and to be prepared to meet the challenges associated with an increasing older population by developing knowledgeable professionals in the field of aging.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 420', 'Occupational Biomechanics', 0.5, 'LAB, LEC', 'A course designed to provide the student with knowledge to reduce the risk of injury and increase worker productivity. Issues include identification of injury risk factors, understanding injury mechanism, quantitative assessment of injury risk and intervention strategies to reduce the risk of injury. Specific examples include the use of computerized models and EMG methods to analyze low back loading, optimizing tool design and workspace layout and the examination of related issues such as office seating and vibration.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 422', 'Human Gait, Posture, and Balance: Pathological and Aging Considerations', 0.5, 'LAB, LEC', 'This course will provide a detailed understanding of the kinematics, kinetics, and neural control of standing posture, stepping, walking, and running under normal and perturbed conditions. Measurement techniques, processing data, and the interpretation of total body and limb synergies will be emphasized from a biomechanical and neural control perspective. The problems of the elderly and the assessment of those with pathologies will be emphasized.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 425', 'Biomechanical Modelling of Human Movement', 0.5, 'LAB, LEC', 'The quantitative measurement and analysis of the movement of the human musculo skeletal system. Multisegment dynamic movements will be studied using computer programs, with emphasis on kinematics, kinetics and energetics, as well as the use of EMG in the assessment of the control of the movement. Examples are presented from pathological, normal and athletic movement.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 427', 'Low Back Disorders', 0.5, 'LEC', 'An advanced elective undergraduate course that introduces the multidisciplinary issues (psychosocial, behavioural, physiological, motor control, biomechanical and legislative) related to low back disorders. A solid scientific foundation is developed from the study of anatomy, normal function and injury mechanics. This foundation is used to substantiate the best strategies for injury prevention and optimal rehabilitation for those with back troubles.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 428', 'Upper Extremity Musculoskeletal Disorders: Prevention, Assessment, and Rehabilitation', 0.5, 'LEC', 'This course introduces the multiple factors that mediate the initiation, diagnosis, treatment and rehabilitation of disorders in the upper extremities. Beginning with a systematic review of hand, wrist, elbow, and shoulder region functional anatomy, a knowledge base is developed to provide a means for the analysis of injury mechanisms. With this base developed, current prevention, clinical diagnosis and rehabilitation techniques for specific disorders will be examined, including carpal tunnel syndrome, rotator cuff tears, and lateral epicondylitis. Pathologies arising from occupational, sport, and daily living activities will be addressed.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 429', 'Bone and Joint Health', 0.5, 'LEC', 'The overall aim is to provide a comprehensive understanding of human bone and cartilage in health and disease. Specific topics covered include: skeletal physiology, bone and cartilage growth and development, prevalence and etiology of several bone and joint conditions (e.g., osteoporosis, arthritis), clinical evaluation, diagnosis and treatment of bone and joint conditions, and the roles of nutrition and exercise in the prevention and treatment of bone and joint disease. The course will incorporate current issues, problem-based learning, research skills and student-led seminars in addition to the lecture-based framework.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 431', 'Research Proposal', 0.5, 'PRJ', 'An independent paper in the form of a research proposal on an approved topic. The topic may include survey, field, laboratory, theoretical, or applied research, program evaluation, mathematical modelling, fitness appraisal, etc. The format is to be determined with the supervisor and may be in chapters or in journal style.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 432', 'Research Project', 0.5, 'PRJ', 'An independent research project on an approved topic, supervised by a faculty member. This is the completion of the research proposed in KIN 431. The format is to be determined with the supervisor and may be in chapters or in journal style.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 433', 'Senior Essay', 0.5, 'ESS', 'An extensive critical review of the literature on an approved topic. The topics will be broader in scope than those associated with specific research proposals.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 440', 'Sport Injury Management Seminar', 0.25, 'SEM', 'This seminar reviews cases of injury management in an athletic population. Students each present a minimum of two cases, using appropriate research literature support and comparison of alternative management approaches and conclusions. Students applying for this seminar must provide an information sheet listing experiences in injury settings, related qualifications such as CPR and first aid, and marks in KIN 100, 100L, 340. Instructor consent required.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 446', 'Physiological and Biochemical Aspects of Nutrition and Health', 0.5, 'LEC', 'Selected examples of nutrient metabolism will be examined in detail in regards to dietary intake, metabolism and mechanistic effects on human health. Comprehensive coverage of all nutrients is not intended, but rather the examples will be examined in detail with a focus on research techniques and approaches, and various factors that interact with and influence nutrition.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 451', 'Social Aspects of Injury in Work and Sport', 0.5, 'LEC', 'This course will examine social aspects of injuries in the context of the workplace, sport and exercise settings. Topics to be covered include risk factors, health and safety climates, cultures of risk, injury prevention strategies, rehabilitation and return to work, and the role of the state in regard to health and safety legislation and insurance programs.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 453', 'Applied Sport Psychology', 0.5, 'LEC', 'An examination of the psychology of sport with an applied focus. Topics include the psychology of peak performance, coaching, and team building, among others.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('LS 342', 'Migration and Legality', 0.5, 'LEC', 'This course introduces students to key concepts and case study research on the regulation of human migration and its exclusionary practices. It examines how state policies, laws, and international organizations control the mobility of migrants and refugees across regions and borders.', 'LS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 456', 'Cognitive Dysfunction and Motor Skill', 0.5, 'LEC', 'An examination of issues related to understanding the cerebral organization of motor skill. Discussion of how certain movement disorders are a reflection of disturbances at different stages in the sequence of information processing.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 457', 'Cognitive, Perceptual and Motor Assessment', 0.5, 'LAB, LEC', 'This course is designed to provide the student with an introduction to the principles underlying the assessment of cognitive, perceptual and motor functions. Measurement issues associated with test development and use, factors involved in the administration and interpretation of test results, and methods of report writing will be examined. Under the supervision of a Registered Psychologist, the student will learn to administer a number of test instruments used in the assessment of cognitive, perceptual and motor functions. Assessments will be done on normal, healthy volunteers recruited from the university community.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 458', 'Social Neuroscience I: Physical Health', 0.5, 'LEC', 'This is an in-depth course intended for the intensive study of psychological, neuroscientific and social processes that together set the stage for the development of chronic illnesses in the modern world. Topics include exercise neuroscience, health-related decision processes, and neural responses to health risk communications in the media.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 470', 'Seminar in Kinesiology', 0.5, 'SEM', 'An examination of current major issues and trends in Kinesiology. Students select areas of major interest from a series of faculty introduced topics.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 470E', 'Seminar in Integrative Ergonomics', 0.5, 'SEM', 'An examination of current major issues and trends in Ergonomics.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 471', 'Contemporary Issues in Kinesiology', 0.5, 'LEC', 'One or more term courses will be offered from time to time as announced by the Kinesiology Department. Subjects will be dependent upon special research and/or instructional interests of faculty.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 472', 'Directed Study in Special Topics', 0.5, 'RDG', 'For the student who desires to pursue a particular topic in depth through guided independent research and/or reading. A faculty member must approve a student''s project prior to registration. May be repeated in subsequent terms.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 491', 'Clinical Kinesiology -- Sports Injuries Assessment', 0.5, 'CLN, LAB', 'Practical experience in the examination, diagnosis, and treatment of sports injuries under the supervision of a Certified Athletic Therapist. Case presentations and musculoskeletal assessments are discussed in a group setting. Preference will be given to those who have taken KIN 140L.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 492A', 'Clinical Kinesiology -- Cardiac Rehabilitation Practicum', 0.5, 'PRA, TUT', 'Practical experience with cardiac patients in a rehabilitation setting; major emphasis is placed on the cardiorespiratory implications of exercise assessment, prescription and management. Experience obtained in a laboratory setting and through placement at a hospital. Instructor consent required.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 492B', 'Clinical Kinesiology -- Cardiac Rehabilitation Practicum', 0.5, 'PRA, TUT', 'Continuation of KIN 492A. Practical experience with cardiac patients in a rehabilitation setting; major emphasis is placed on the cardiorespiratory implications of exercise assessment, prescription and management. Experience obtained in a laboratory setting and through placement at a hospital. Instructor consent required.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 493', 'Clinical Kinesiology: Movement Assessment Practicum', 0.5, 'PRA, TUT', 'Practical experience in movement assessment of persons from various special populations such as the normal elderly and those with neurological, degenerative or developmental disorders. Motor functions involving gait, posture and balance or upper limb movements will typically be examined in these assessments.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 494', 'Integrative Ergonomics Practicum', 0.5, 'PRA', 'A presentation must be made by each 4B student explaining quantitative and qualitative methods used, interpretation of data where applicable, explanation of interventions employed, together with an overview of components of interest to those in ergonomics. Credit requires both the off-campus practicum experiences and attendance at the seminar component over six academic terms where all students are expected to participate in debate of ergonomic issues. Occasional guest ergonomists address the group. Graded credit/non credit.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 601', 'Muscle Physiology', 0.5, 'LEC', 'An analysis of the morphological, biophysical, molecular and physiological properties of skeletal, cardiac and smooth muscle.  Topics range from the molecular regulation of muscle contraction, excitation-contraction processes, fatigue mechanisms, cell signalling, adaptability and pathophysiology.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 602', 'Respiratory and Cardiovascular Physiology', 0.5, 'LEC', 'The physiology of the cardiovascular and endocrine systems in work and superimposed environmental stresses are examined. Topics include myocardial function, hemodynamics, microcirculation, diffusion, acid base balance, body fluid regulation and the nature of hormonal and neural control systems.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 606', 'Molecular Basis of Disease', 0.5, 'LEC', 'This course will examine cellular and molecular mechanisms that play a major role in causing dysfunction of a variety of relevant tissues under a number of chronic conditions including aging, cardiovascular disease, diabetes, cancer, autoimmune disorders, neurodegenerative conditions, and muscular diseases.  Topics to be covered include satellite cells/stem cells, apoptosis and necrosis, mitochondrial dysfunction, mitochondrial DNA, oxidative stress, autophagy and protein degradation, and inflammation.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 607', 'Integrative Energy Metabolism in Health and Disease', 0.5, 'LEC', 'This course will comprehensively examine the metabolic processes involved in energy production and expenditure in different tissues.  The regulation and integration of protein, lipid and carbohydrate metabolism will be studied in the context of energy balance for healthy, aging and disease populations (including diabetes and cancer).  The course will also explore nutrition and exercise implications on metabolism and disease.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 611', 'Biomechanics of Human Motion', 0.5, 'LEC', 'An assessment of research in the biomechanics of human motion including electromyography, muscle modelling, link segment modelling and analysis, energy and power analysis. The above concepts will be utilized in the assessment of athletes and both the normal and atypical population in sport and rehabilitation contexts.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 612', 'Instrumentation and Signal Processing in Biophysical Research', 0.5, 'LAB, LEC', 'Techniques in data acquisition, reduction, and signal processing commonly employed in biophysical research are discussed in class and used in laboratory sessions. Among other instruments, the student becomes familiar with the use of a variety of transducers and their systems characteristics, electromyographs, the process of analog to digital conversion, documentation of motion, stress and strain, and the quantification of medical image data. Signal to noise enhancement is emphasized through all aspects of the course.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 613', 'Modern Methods in Biomechanical Modeling, Kinematics, and Kinetics', 0.5, 'LEC', 'Biomechanics research typically requires the integration of experimental data collection and theoretical modeling approaches to answer complex questions.  The purpose of Kin 613 is to sensitize incoming graduate students to the range, biological basis, and practicality of using or developing mathematical models for biomechanical investigations.  This contrasts with KIN 612, which focuses on promoting rigorous experimental data collection and biological signal interpretation, or Kin 611, which seeks to develop the historical foundations of biomechanical research and promote critical reading of the scientific literature.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 616', 'Neural Control of Human Movement', 0.5, 'LEC', 'An examination of current theories and evidence concerning the neural control of human movement. Topics may include: origin of kinesthesia, organization of spinal circuits and reflex actions regulating posture and movement, neural strategies for the control of upright stance and locomotion, functional organization of the motor cortex, cerebellum and basal ganglia for the control of voluntary movement.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 620', 'Ergonomic Aspects of Occupational Musculoskeletal Injuries', 0.5, 'LAB, LEC', 'This course examines the relationships between physical and organizational aspects of the work environment and occupational musculoskeletal fatigue and injuries. Emphasis will be placed on back and upper limb injuries. Major topics covered include injury mechanisms, assessment of injury risk, reporting of injury and use of injury statistics, work site interventions and evaluation of their effectiveness and relevant legislation (including Human Rights considerations). A laboratory project using the methodologies studied in the course is an important component of the course. A project within a local industry, instead of the laboratory project is preferred but optional.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 625', 'Social Neuroscience of Exercise and Eating', 0.5, 'LEC', 'An examination of current social neuroscientific theories and research in relation to the cognitive aspects of exercise participation and the neurobiology of dietary choice.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 631A', 'Introduction to Statistics', 0.25, 'LEC', 'This course introduces basic statistical concepts as they relate to Kinesiology.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 631C', 'Correlation and Regression', 0.25, 'LEC', 'The use of multiple regression models in the anlysis of multi-variable data sets. Strategies for model builidng, fitting of models, assessing model assumptions and testing  hypotheses. Application of these models to problems in the health sciences.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 631D', 'Logistic Regression and Its Application', 0.25, 'LEC', 'The use of logistic regression for the analysis of multi-variable data sets with binary response. Strategies for model building, fitting of models, assessing model assumptions and testing hypotheses. Use of these models to anlayze prospective studies, case-control studies and cross-sectional studies.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 631E', 'Analysis of Variance I', 0.25, 'LEC', 'Review of Hypothesis tests and Confidence intervals for paired and unpaired observations. The concept of a designed experiment and its associated model. Analysis of variance for one and two factor experiments including interaction. Contrasts for comparing various treatment means. Use of Residual analysis to check the statistical assumtpions of a design . SAS may be used to obtain listings used in the analysis.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 631F', 'Analysis of Variance II', 0.25, 'LEC', 'Methods for determining Expected Mean Squares. Design and Analysis of Factorial, Fractional Factorial and Repeated Measures Experiments. Design and Anlysis and other designs as time permits. SAS may be used to obtain listings used in the anlysis .', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 631G', 'Biological Deterministic Modelling and Signal Processing', 0.25, 'LEC', 'Mathematical and computer modelling of first and second order passive and active biological systems; neuron, muscle and diffusion models. Biological feedback control systems: control of posture, gait and tracking tasks. Signal processing; analog and digital filtering, harmonic analyses, impulse response, convolution.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 646', 'Physiological and Biochemical Aspects of Nutrition and Health', 0.5, 'LEC', 'Selected nutrients will be examined in detail in regards to dietary intake, metabolism, metabolism and mechanistic effects on human health.  Comprehensive coverage of nutrients is not intended, but the specific nutrients examined will include at least one example each from lipids, proteins, carbohydrates, water soluble vitamins, lipid soluble vitamins and minerals.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 651', 'Motor Learning', 0.5, 'LEC', 'An examination of current theories models and experimental literature concerned with the learning and performance of skilled movement. Topics may include: models of motor learning, expert-novice differences in skill, the automatization of skill, mental practice, the relationship between cognitive and motor skills.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 652', 'Movement Control and Learning', 0.5, 'LEC', 'This course deals with the learning and control of goal-directed eye, head, arm and hand movements. Topics include: Hick''s and Fitt''s Laws; goals, task plans and motor equivalence; the actor/environment interface; movement planning and organization; use of schemas, motor programs, feedforward and sensory feedback; motorsensory integration; and knowledge representation.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 653', 'Human Neuroscience Theory', 0.5, 'LEC', 'The course will provide a comprehensive examination of topics related to human neuroscience as it broadly relates to movement.  These topics include the central nervous system control of human movement, sensorimotor control systems and modulation, attention, psychological factors that influence emotional and behavioral adaptation to chronic illnesses, prehension, movement sequencing, limb gesturing, tool use, gait and posture.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 654', 'Instrumentation in Neuroscience Research', 0.5, 'LEC', 'Human neuroscience research techniques are discussed in class and used in laboratory sessions.  Students will learn the theory and gain practical experience with electroencephalography, event-related potentials, functional magnetic resonance imaging, transcranial magnetic stimulation, electromyography, digitized optical tacking of movement, psychophysics and survey methodologies.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 656', 'Neurobehavioural Analyses of  Perceptual and Motor Deficits', 0.5, 'SEM', 'Perceptual and motor deficits associated with damage to various brain areas are examined in terms of the underlying processing disorders. The role of cognitive (e.g., memory and language), motor (e.g., response complexity) and environmental context are considered. Case studies and clinic visits serve to complement material discussed in the seminar.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 670A', 'Neuroscience Seminar I', 0, 'SEM', 'This is a weekly seminar including both student and faculty presentations of current research and student research proposals. The content of the seminars may vary from term to term depending on the availability of invited speakers, topics of interest to students and faculty, and the research of members of the group.  Both MSc and PhD degree students must register for two of the following:  KIN 670A, 670B, 670E, 670F, 670H, 670I to fulfill degree requirements.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 670B', 'Neuroscience Seminar II', 0, 'SEM', 'This is a weekly seminar including both student and faculty presentations of current research and student research proposals. The content of the seminars may vary from term to term depending on the availability of invited speakers, topics of interest to students and faculty, and the research of members of the group.  Both MSc and PhD degree students must register for two of the following:  KIN 670A, 670B, 670E, 670F, 670H, 670I to fulfill degree requirements.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 670C', 'Seminar 1: Motor Learning', 0, 'SEM', 'Both the MSc and PhD degree students must register for two of the following: KIN 670A, 670B, 670C, 670D, 670E, 670F, 670H, 670I to fulfill degree requirements.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 670D', 'Seminar 1: Psychomotor Behavior', 0, 'SEM', 'Both the MSc and PhD degree students must register for two of the following: KIN 670A, 670B, 670C, 670D, 670E, 670F, 670H, 670I to fulfill degree requirements.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 670E', 'Biomechanics Seminar I', 0, 'SEM', 'This is a weekly seminar including both student and faculty presentations of current research and student research proposals. The content of the seminars may vary from term to term depending on the availability of invited speakers, topics of interest to students and faculty, and the research of members of the group.  Both MSc and PhD degree students must register for two of the following:  KIN 670A, 670B, 670E, 670F, 670H, 670I to fulfill degree requirements.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 670F', 'Biomechanics Seminar II', 0, 'SEM', 'This is a weekly seminar including both student and faculty presentations of current research and student research proposals. The content of the seminars may vary from term to term depending on the availability of invited speakers, topics of interest to students and faculty, and the research of members of the group.  Both MSc and PhD degree students must register for two of the following:  KIN 670A, 670B, 670E, 670F, 670H, 670I to fulfill degree requirements.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 670H', 'Physiology and Nutrition Seminar I', 0, 'SEM', 'This is a weekly seminar including both student and faculty presentations of current research and student research proposals. The content of the seminars may vary from term to term depending on the availability of invited speakers, topics of interest to students and faculty, and the research of members of the group.  Both MSc and PhD degree students must register for two of the following:  KIN 670A, 670B, 670E, 670F, 670H, 670I to fulfill degree requirements.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 670I', 'Physiology and Nutrition Seminar II', 0, 'SEM', 'This is a weekly seminar including both student and faculty presentations of current research and student research proposals. The content of the seminars may vary from term to term depending on the availability of invited speakers, topics of interest to students and faculty, and the research of members of the group.  Both MSc and PhD degree students must register for two of the following:  KIN 670A, 670B, 670E, 670F, 670H, 670I to fulfill degree requirements.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 680', 'Selected Topics in Physiology and Nutrition', 0.5, 'RDG', 'None', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 682', 'Selected Topics in Biomechanics', 0.5, 'RDG', 'None', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 700', 'Muscle Metabolism in Work', 0.5, 'LEC', 'This course will focus on extra- and intramuscular metabolism of carbohydrates, lipids and protein in animal and human skeletal and cardiac muscle during work. Special emphasis will be placed on control and integration of muscle fuel metabolism under different intensities and durations of contractile activity.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 684', 'Selected Topics in the Social Science of Sport', 0.5, 'RDG', 'None', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 686', 'Selected Topics in Neuroscience I', 0.5, 'RDG', 'None', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 687', 'Fundamentals of Behavioural Neuroscience', 0.5, 'LEC', 'This survey course will be team-taught by members of the collaborative program, and will serve to introduce students to major subareas of ongoing behavioural neuroscience research at Waterloo. Topics will include cognitive psychopathology, apraxia, topics in visual neuroscience, early experience and brain development, cognitive electrophysiology, human locomotion, neuropathology of schizophrenia, object recognition, hemispheric specialization, motor control and psychoneuroimmunology.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 702', 'Cardiorespiratory Integration', 0.5, 'LEC', 'The control of cardiorespiratory responses to exercise will be examined by detailed consideration of afferent, central neural and effector mechanisms for regulation.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 704', 'Bioactive Lipids', 0.5, 'LEC', 'Lipids are intimately linked to all cellular functions based not only on their structural role as membrane components, but also their use as fuel, interactions with proteins, as important second messengers in cell signalling and potent regulators of gene expression.  The present course will examine the properties and how they impact various biological functions and human conditions including inflammation and cardiovascular disease.  The course will also examine dominant lip analytical techniques that are specific to this field of study and examine the emerging field of lipidomics.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 713', 'Modelling of Human Musculoskeletal System during Movement', 0.5, 'LEC', 'Modelling of the human musculoskeletal system during movement is explored using a number of techniques. Assumptions and difficulties in applying modelling techniques to the human body are stressed.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 714', 'Advanced Electromyography', 0.5, 'LEC', 'Topics include modelling of the neuromuscular basis of the myoelectric signal; processing of the EMG via functions (spectral and correlational, amplitude and temporal density); changes of EMG due to fatigue myopathies, etc. Neural and muscle stimulation techniques are also examined.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 715', 'Assessment of Pathological Movement', 0.5, 'LEC', 'The use of biomechanical measurement and modelling techniques for the assessment of pathological movement. Case studies will be used to demonstrate the use of advanced biomechanics to diagnose and monitor the rehabilitation of the physically handicapped. The emphasis will be on the process of integrating many different kinetic, kinematic and EMG variables. The importance of data bases on normals and the problems of follow-up assessments on specific patients are also stressed.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 727', 'Low Back Disorders: Optimizing Prevention, Rehabilitation and Performance', 0.5, 'LEC', 'An advanced graduate course that synthesizes the multidisciplinary issues (psychosicial, behavioural, physiological, biomechanical and legislative) related to low back disorders.  A solid scientific foundation is developed from the study of anatomy, normal function and injury mechanics.  This foundation is used to substantiate the best strategies for injury prevention, rehabilitation and performance training.
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 730', 'Fundamentals of Work and Health', 0.5, 'LEC', 'This course will provide an overview of current thinking on work and health by introducing students to major substantive topics in the area from a multi-disciplinary perspective.  The course will critically examine a variety of topics that may be investigated from different disciplinary perspectives.  Classes will include presentations by faculty members representing different disciplines in the collaborative program.  Examples of topics discussed in a given term may include work-stress relationships, healthy workplaces, and vulnerable populations.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 731', 'Approaches to Research in Work and Health', 0.5, 'LEC', 'The purpose of this course is to introduce students to different ways of designing and implementing research within the topic area of work and health as well as some of the practical, political and ethical challenges.  The course content will include an examination of the epistemological positions associated with different types of research questions, analysis of case studies of research in work and health, and discussions of proposed research questions and proposals that examine problems from a variety of disciplinary perspectives.  The course will also include presentations by researchers and practitioners from the work and health domain, who will facilitate discussions on the challenges and opportunities of conducting and applying research in this area.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 732A', 'Work and Health Research Seminar (I)', 0, 'SEM', 'This seminar is a forum for student presentations about results of or proposals for research.  Invited speakers will also present results of research from time to time.  Attendance at the seminar is required for two terms (i.e., during the candidates'' first two years in the program).  Attendance beyond that is encouraged.  The range of topics that will be addressed in the seminar crosses all areas of investigation in the collaborative program.  Grading will be on a credit/no credit basis.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 732B', 'Work and Health Research Seminar (II)', 0, 'SEM', 'This seminar is a forum for student presentations about results of or proposals for research.  Invited speakers will also present results of research from time to time.  Attendance at the seminar is required for two terms (i.e., during the candidates'' first two years in the program).  Attendance beyond that is encouraged.  The range of topics that will be addressed in the seminar crosses all areas of investigation in the collaborative program.  Grading will be on a credit/no credit basis.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 750', 'Fundamentals of Aging, Health and Well-being', 0.5, 'LEC', 'This survey course will be team-taught by members of the collaborative program and will serve to introduce students to major sub-areas of aging, health and well-being research.  Topics will be covered from each Department within the Faculty of Applied Health Sciences.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 751', 'Aging, Health and Well-being Research Seminar', 0, 'SEM', 'This seminar is a forum for student presentations about results of, or proposals for research.  Invited speakers will also present  results of research from time to time.  Attendance at the seminar is required for two terms (i.e., during the candidates''  first two years in the program).  The range of topics that will be addressed in the seminar crosses all areas of investigation in the collaborative program.  Grading will be on a credit/no credit basis.  Must be registered in the PhD program in Aging, Health and Well-being.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 760', 'Selected Topics Neuroscience II', 0.5, 'RDG', 'An analysis of specific topics of interest. The form of study may involve a review of literature or the planning and execution of an independent study resulting in a paper for possible publication.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 775', 'Key Issues and Concerns in Kinesiology', 0, 'SEM', 'Kinesiology 775 provides an opportunity for Kinesiology Doctoral students to examine some of the key issues and concerns that will arise in their post graduate careers.  The course is organized in a seminar format.  Examples of topics covered in the course are: Post Doctoral Positions in University and other settings; Job Interviews; Career Paths Within Academia: Promotion and Tenure Concerns; Collaborations; Reviewing Manuscripts and Grants and Writing Reference Letters.  Ph.D. students must successfully complete this seminar at least once during their program.  Normally, this course should be taken after the first year of registration in the Ph.D. program.  Will be graded on a Credit/No Credit basis.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 780', 'Selected Topics in Physiology and Nutrition', 0.5, 'RDG', 'An analysis of specific topics of interest. The form of study may involve a review of literature or the planning and execution of an independent study resulting in a paper for possible publication.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 782', 'Selected Topics in Biomechanics', 0.5, 'RDG', 'An analysis of specific topics of interest. The form of study may involve a review of literature or the planning and execution of an independent study resulting in a paper for possible publication.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('LAT 621', 'Latin Epigraphy', 0.5, 'SEM', 'The course introduces and investigates Latin inscriptions as evidence for the Latin language and Roman political, religious, legal, social and economic history.', 'LAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KOREA 101R', 'First-Year Korean 1', 0.5, 'LAB, LEC, TUT', 'An introductory course for students who have no or little knowledge of Korean to develop listening, speaking, reading, and writing skills along with a sound basis of grammar. The distinctive features of the Korean language and writing system are introduced. Practical oral, reading, and writing exercises develop the students'' grammatical skills. Particular emphasis is placed on the acquisition of a basic working vocabulary.', 'KOREA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KOREA 102R', 'First-Year Korean 2', 0.5, 'LAB, LEC, TUT', 'Students deepen their understanding of basic grammatical (particularly verb, noun, and adverb) forms and sentence construction and enlarge their general vocabulary. Reading ability is expanded, and more attention is given to idiomatic expressions and the use of the language in actual contexts.', 'KOREA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KOREA 201R', 'Second-Year Korean 1', 0.5, 'LAB, LEC', 'Designed for students who have completed KOREA 102R or the equivalent. To achieve a balanced Korean language proficiency in listening, speaking, reading, and writing, a variety of teaching materials and methods is used. The text includes adapted versions of short stories, essays, and poems.', 'KOREA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KOREA 202R', 'Second-Year Korean 2', 0.5, 'LAB, LEC', 'A continuation of KOREA 201R. Emphasis is placed on reading and composition. Students learn complex sentence structure and widely-used idiomatic phrases for advanced reading.', 'KOREA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KOREA 272R', 'Korean Culture and Society', 0.5, 'LEC', 'The course explores the interface of tradition and modernity in Korean life. Topics include traditional and modern Korean culture, language, education, art, technology, and business.', 'KOREA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('LAT 101', 'Introductory Latin 1', 0.5, 'LEC, TUT', 'A course designed for students beginning the study of Latin or who have not yet reached the level expected in LAT 201/202. Although the teaching approach emphasizes exposure to simple texts as soon as possible, students desiring minimal competence in reading should go on to do LAT 102.', 'LAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('LAT 102', 'Introductory Latin 2', 0.5, 'LEC, TUT', 'Continuation of LAT 101. Most of the rules of Latin grammar will be covered by the end of the year, and students should have a minimal competence in reading prose texts; but for the remaining grammar and further practice students should go on to do LAT 201.', 'LAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('LAT 201', 'Intermediate Latin', 0.5, 'LEC', 'The course will complete the study of Latin grammar and move on to unadapted readings in Latin authors, particularly Caesar.', 'LAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('LAT 202', 'Selections from Latin Authors', 0.5, 'LEC', 'A course designed to follow LAT 201, including both literature and grammar review. Authors normally read are Vergil and Ovid.', 'LAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('LAT 202W', 'Selections from Latin Authors', 0.5, 'LEC', 'A course designed to follow LAT 201, including both literature and grammar review. Authors normally read are Vergil and Ovid.', 'LAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('LAT 331', 'Advanced Readings in Latin: Prose', 0.5, 'LEC', 'A selection of material from one author or several authors within the field of Latin prose. Topics and selections may include oratory and rhetoric, history, philosophy, Cicero, Quintilian, Caesar, Livy, and Apuleius.', 'LAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('LAT 332', 'Advanced Readings in Latin: Poetry', 0.5, 'LEC', 'A selection of material from one author or several authors within the field of Latin poetry. Topics and selections may include comedy, lyric poetry, elegy, epic, Plautus, Terence, Catullus, Ovid, and Vergil.', 'LAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('LAT 341', 'Advanced Studies in Latin: Selected Topics', 0.5, 'LEC', 'An investigation of selected themes, topics, time periods, or genres in Latin.', 'LAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('LAT 351', 'Latin Composition, Grammar, and Reading', 0.5, 'LEC', 'Composition, translation, and grammar with intensive analysis of selected passages.', 'LAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('LAT 381', 'Medieval Latin', 0.5, 'LEC', 'Survey of Medieval Latin poetry and prose.', 'LAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('LAT 421', 'Latin Epigraphy', 0.5, 'LEC', 'The course introduces and investigates Latin inscriptions as evidence for the Latin language and Roman political, religious, legal, social, and economic history.', 'LAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('LAT 422', 'Latin Palaeography', 0.5, 'LEC', 'A practical overview of the various styles of Latin handwriting from the late Roman Empire to the writing styles of the Renaissance humanists which introduces students to the study of original documents and manuscripts.', 'LAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('LAT 451', 'Senior Latin Composition, Grammar, and Reading', 0.5, 'SEM', 'Advanced composition, translation, and grammar with intensive analysis of selected passages.', 'LAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('LAT 490', 'Senior Studies in Latin: Selected Topics', 0.5, 'RDG', 'A selection of material from one author or several authors or an investigation of selected themes, topics, or genres at the senior level. Topics and authors may include philosophy, letter writing, history, elegy, satire, Seneca, Pliny the Younger, Tacitus, Propertius, Petronius, and Juvenal.', 'LAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('LAT 491', 'Senior Studies in Latin: Independent Study', 0.5, 'RDG', 'Under special circumstances and with the approval of the department, a student or small group of students may arrange to pursue individualized readings under the supervision of a faculty member.', 'LAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('LAT 600', 'Topics in Latin Language', 0.5, 'SEM', 'This course will involve the reading of original material in Latin with a focus on the advanced study of the language itself, although topics may vary from single authors to literary genres to chronological periods.  This course is repeatable provided the topic is different.  Examples could include: Latin Palaeography; Augustan Poetry; Epic Poetry; Advanced Grammar, Reading, and Composition; Greek and Roman Identities in the Lyric Poetry of Horace; Latin Epigraphy; or Medieval Latin.', 'LAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('LAT 601', 'Topics in Greek Language', 0.25, 'SEM', '0.25 credit offering to be combined with LAT 601.  This 0.25 credit version of GRK 600 will allow faculty members to offer single-term courses that combine reading of original sources in both ancient Greek and Latin on selected topics. This course is repeatable provided the topic is different.  Examples could include:  Greek Palaeography or Greek Epigraphy.', 'LAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('LED 611', 'Industrial Location Theory and Concepts', 0.5, 'SEM', 'Examination of industrial location theory, and themes concerning the role of manufacturing in the space-economy. Factors of location will be considered as will the way that manufacturing has led to spatial disparity on international, regional and local levels.', 'LED');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('LED 612', 'Land Development Planning', 0.5, 'SEM', 'An examination of planning issues related to the design, economics and financing of private land and building construction projects including residential high-rise condominium, low-rise residential subdivision, infill, intensification and brownfield redevelopment and industrial/commerial land development.  The course focuses on developer decision-making, analysis of risk, sources of financing, planning, environmental and engineering aspects of land development.  This course may include a field component.  (Note:  If a field trip occurs, the cost will not exceed $50.)', 'LED');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('LED 613', 'Regional Development Principles and Practice', 0.5, 'SEM', 'This course will review the principles underlying regional development theory and evaluate the application of these principles in policy and practice. The purpose of this course is to explore regional development options in the context of fiscal conservatism and intense international competition for investments.', 'LED');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('LED 615', 'Community Economic Development', 0.5, 'SEM', 'Community Economic Development is a field of theory, process and practice that is concerned with understanding the forces shaping communities and finding sustainable local solutions to economic needs.  This seminar course will examine topics such as capacity-building, asset-based strategies, social capital, poverty-alleviation, social enterprises and co-operatives, and comprehensive community initiatives, using international and local examples and case studies.', 'LED');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('LED 619', 'Regional Planning Economic and Investment Analysis', 0.5, 'LEC, RDG, SEM, TUT', 'Critical appraisal of regional economic analysis, projection and decision-making techniques. Problem based study of methods commonly used by regional planners, economic developers, and consultants. Identification of leverage sectors, trend analysis, investment decision-making and input/output analysis are discussed. New analysis applications and approaches are explored.', 'LED');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('LED 621', 'Environment & Business', 0.5, 'LEC', 'This course provides an examination of business reponses to environmental and sustainability issues, and it explores ways in which businesses have adopted and implemented management strategies to address them.  The recent history of corporate responses to societal demands for increased attention to the enviornment is explored with an emphasis on specific tools and collaborative approaches used by business. Examples include environmental management systems, environmental marketing, design for the environment, tradeable permits, ecolabelling programs and industrial ecology.  Prospects for sustainable business are also explored.', 'LED');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('LED 671', 'Contemporary Perspectives on Tourism', 0.5, 'SEM', 'This course will introduce participants to a variety of topics and research methods through presentations made by active researchers from Canada and abroad.  *eligible for MES', 'LED');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('LED 685', 'Theory of Local Economic Development', 0.5, 'LEC', 'This course focuses on economic development for regions, communities and municipalities.  We examine organizational structures and strategies that a community may employ to increase its capacity to control its destiny.  Topics include: strategic economic planning, information management, partnerships, internet opportunities, entrepreneurship, incubators, tourism, sustainable development and healthy community initiatives.  Finally, we critique various evaluation methods.', 'LED');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('LED 686', 'Practice of Local Economic Development', 0.5, 'SEM', 'This course examines development strategies for communities to achieve their economic, social and environmental goals. The focus is on practical techniques with case studies used to illustrate broader concepts and theories. A range of development tools are introduced, e.g.: SWOT analysis, economic and demographic trend analysis, information brokering, local capacity building, private/public/NGO partnerships, local/global network formation, sustainable development indicators, website design/evaluation, and outcome evaluation techniques.', 'LED');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('LED 687', 'Communication, Market Research and Marketing for the Public Sector', 0.5, 'SEM', 'Many public agencies are involved in marketing, but the subject is almost always taught from a private sector perspective. An overview of marketing strategy as applied in the private sector (product, distribution, promotion and pricing policies) will lead to a focus on applications in the public sector. Particular attention will be paid to cases where a "place" (community, region, province) is the "product" being marketed.  Students who do not meet the required prerequisites may contact the instructor.', 'LED');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('LED 688', 'Entrepreneurship and Small Business Development', 0.5, 'SEM', 'This course will identify the principles of entrepreneurship and evaluate programs designed to promote small business development. An action oriented approach is adopted to achieve entrepreneurial change in the community with sensitivity to the quality of the natural environment. Case studies are used.', 'LED');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('LS 101', 'Introduction to Legal Studies', 0.5, 'LEC', 'An introduction to the study of law, its structure, and legal institutions from a cross-cultural and historical perspective. This interdisciplinary course examines the origins of legal systems and their impact on society. Included is an analysis of the diverse historical, political, economic, and cultural conditions under which law arises and functions within society.', 'LS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('LS 102', 'Introduction to Criminal Law', 0.5, 'LEC', 'A case-study approach to the study of criminal law in Canada with a focus on basic concepts and core principles relating to legal judgements along with comparative examination between civil and criminal law and attention to legal theory.', 'LS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('LS 201', 'Women and the Law', 0.5, 'LEC', 'This course provides an introduction to feminist legal thought with a particular focus on Canadian cases, legislation, law reform, and legal literature. Included is an analysis of the ways in which law contributes to women''s legal, social, political, and economic status as well as the manner in which the law is used as a mechanism of social change for women. The intersection of gender with age, race, ethnicity, religion, and class will be addressed.', 'LS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('LS 280', 'Social Statistics', 0.5, 'LEC', 'A basic course in statistics used in social science research including sampling, central tendency, probability, and covariance, as illustrated in specifically sociological and criminological data.', 'LS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('LS 321', 'Introduction to Research Methods', 0.5, 'LEC', 'An introductory survey of the research techniques commonly employed by sociologists, criminologists, and legal studies researchers. The formulation of research designs appropriate to various kinds of intellectual problems in social science is stressed.', 'LS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('LS 322', 'Field Research Methods', 0.5, 'LEC', 'This course provides a critical evaluation of research techniques in sociology, criminology, and legal studies with an emphasis on learning and applying qualitative fieldwork approaches.', 'LS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('LS 401', 'Law, Culture, and Rights', 0.5, 'SEM', 'This seminar explores the intersection of culture and rights from a legal studies perspective in order to better understand the diversity of ways that law shapes our society, and vice versa. Students will debate and assess selected topics from the perspective of various disciplines spanning the social sciences and humanities.', 'LS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('LS 402', 'Perspectives on Legal Authority and Subjectivity', 0.5, 'SEM', 'This seminar explores the relation between those who make or administer law and select legal subjects whose lives and identities are shaped by law. Students will debate and assess selected perspectives while touching on various disciplines spanning the social sciences and humanities.', 'LS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('LS 403', 'Socio-Legal Responses to Crime', 0.5, 'LEC', 'This course examines responses to crime from historical, philosophical, legal, psychological, sociological, and other related perspectives. The primary focus is on the relationship between the law, motivations of offenders, and responses by individuals and the criminal justice system to chronic, sex, and violent offenders.', 'LS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('LS 496', 'Special Topics in Legal Studies', 0.5, 'LEC', 'This course will deal with selected topics in Legal Studies. Subjects will be dependent upon the research and/or instructional interests of faculty.', 'LS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('LS 498', 'Directed Readings in Legal Studies', 0.5, 'LEC', 'Selected study and assignments under the direction of a faculty member who teaches courses in legal studies and criminology.', 'LS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MATBUS 470', 'Derivatives', 0.5, 'LAB, LEC', 'Overview of the derivatives markets. Pricing of derivatives, including futures, forwards, swaps, and options. Hedging vs. speculating. Option Greeks. Trading strategies. Case studies.', 'MATBUS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MATBUS 471', 'Fixed Income Securities', 0.5, 'LAB, LEC', 'Analysis of fixed income securities. Duration, convexity. Structured products. Market characteristics. Valuation of bonds with embedded options. Accounting and taxation issues.', 'MATBUS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MATBUS 472', 'Risk Management', 0.5, 'LAB, LEC', 'Principles of Risk Management. Market, credit and operational risk management. Risk measures. Hedging strategies and risks in hedging. Credit migration. Global financial market regulation. Case studies.', 'MATBUS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MATH 51', 'Pre-University Algebra and Geometry', 0, 'LEC', 'Topics covered in the course include operations with vectors, scalar multiplications dot and cross products, projections, equations of lines and planes, systems of equations, Gaussian elimination, operations with matrices, determinants, binomial theorem, proof by mathematical induction, complex numbers.', 'MATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MATH 52', 'Pre-University Calculus', 0, 'LEC', 'The concepts included are limits, derivatives, antiderivatives and definite integrals. These concepts will be applied to solve problems of rates of change, maximum and minimum, curve sketching and areas. The classes of functions used to develop these concepts and applications are: polynomial, rational, trigonometric, exponential and logarithmic.', 'MATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MATH 97', 'Study Abroad', 2.5, 'LEC', 'For studies at other universities under approved exchange agreements.', 'MATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MATH 103', 'Introductory Algebra for Arts and Social Science', 0.5, 'LEC, TUT', 'An introduction to applications of algebra to business, the behavioural sciences, and the social sciences. Topics will be chosen from linear equations, systems of linear equations, linear inequalities, functions, set theory, permutations and combinations, binomial theorem, probability theory. [Offered: F,W]', 'MATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MATH 104', 'Introductory Calculus for Arts and Social Science', 0.5, 'LEC, TUT', 'An introduction to applications of calculus in business, the behavioural sciences, and the social sciences. The models studied will involve polynomial, rational, exponential and logarithmic functions. The major concepts introduced to solve problems are rate of change, optimization, growth and decay, and integration. [Offered: F,W]', 'MATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MATH 106', 'Applied Linear Algebra 1', 0.5, 'LEC, TUT', 'Systems of linear equations. Matrix algebra. Determinants. Introduction to vector spaces. Applications. [Offered: F,W,S]', 'MATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MATH 109', 'Mathematics for Accounting', 0.5, 'LEC, TUT', 'Review and extension of differential calculus for functions of one variable. Multivariable differential calculus. Partial derivatives, the Chain Rule, maxima and minima and Lagrange multipliers. Introduction to matrix algebra.', 'MATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MATH 114', 'Linear Algebra for Science', 0.5, 'LEC, TUT', 'Vectors in 2- and 3-space and their geometry. Linear equations, matrices and determinants. Introduction to vector spaces. Eigenvalues and diagonalization. Applications. Complex numbers. [Offered: F]', 'MATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MATH 115', 'Linear Algebra for Engineering', 0.5, 'LEC, TST, TUT', 'Linear equations, matrices and determinants. Introduction to vector spaces. Eigenvalues and diagonalization. Applications. Complex numbers. [Offered: F]', 'MATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MATH 116', 'Calculus 1 for Engineering', 0.5, 'LEC, TST, TUT', 'Functions: review of polynomials, exponential, logarithmic, trigonometric. Operations on functions, curve sketching. Trigonometric identities, inverse functions. Derivatives, rules of differentiation. Mean Value Theorem, Newton''s Method. Indeterminate forms and L''Hopital''s rule, applications. Integrals, approximations, Riemann definite integral, Fundamental Theorems. Applications of the integral. [Offered: F]', 'MATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MATH 117', 'Calculus 1 for Engineering', 0.5, 'LEC, TST, TUT', 'Functions of engineering importance; review of polynomial, exponential, and logarithmic functions; trigonometric functions and identities. Inverse functions (logarithmic and trigonometric). Limits and continuity. Derivatives, rules of differentiation; derivatives of elementary functions. Applications of the derivative, max-min problems, Mean Value Theorem. Antiderivatives, the Riemann definite integral, Fundamental Theorems. Methods of integration, approximation, applications, improper integrals. [Offered: F]', 'MATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MATH 118', 'Calculus 2 for Engineering', 0.5, 'LEC, TST, TUT', 'Methods of integration: by parts, trigonometric substitutions, partial fractions; engineering applications, approximation of integrals, improper integrals. Linear and separable first order differential equations, applications. Parametric curves and polar coordinates, arc length and area. Infinite sequences and series, convergence tests, power series and applications. Taylor polynomials and series, Taylor''s Remainder Theorem, applications. [Offered: W,S]', 'MATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MATH 119', 'Calculus 2 for Engineering', 0.5, 'LEC, TST, TUT', 'Elementary approximation methods: interpolation; Taylor polynomials and remainder; Newton''s method, Landau order symbol, applications. Infinite series: Taylor series and Taylor''s Remainder Theorem, geometric series, convergence test, power series, applications. Functions of several variables: partial derivatives, linear approximation and differential, gradient and directional derivative, optimization and Lagrange multipliers. Vector-valued functions: parametric representation of curves, tangent and normal vectors, line integrals and applications. [Offered: W,S]', 'MATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MATH 124', 'Calculus and Vector Algebra for Kinesiology', 0.5, 'LEC, TUT', 'Review of trigonometry and basic algebra. Introduction to vectors in 2- and 3-space: sums, addition, dot products, cross products and angles between vectors. Solving linear systems in two and three variables. Functions of a real variable: powers, rational functions, trigonometric, exponential and logarithmic functions, their properties. Intuitive discussion of limits and continuity. Derivatives of elementary functions, derivative rules; applications to curve sketching, optimization. Relationships between distance, velocity and acceleration. The definite integral, antiderivatives, the Fundamental Theorem of Calculus; change of variable and integration by parts; applications to area, centre of mass. [Offered: F]', 'MATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MATH 127', 'Calculus 1 for the Sciences', 0.5, 'LEC, TUT', 'Functions of a real variable: powers, rational functions, trigonometric, exponential and logarithmic functions, their properties and inverses. Intuitive discussion of limits and continuity. Definition and interpretation of the derivative, derivatives of elementary functions, derivative rules and applications. Riemann sums and other approximations to the definite integral. Fundamental theorems and antiderivatives; change of variable. Applications to area, rates, average value. [Offered: F,W,S]', 'MATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MATH 128', 'Calculus 2 for the Sciences', 0.5, 'LEC, TUT', 'Transforming and evaluating integrals; application to volumes and arc length; improper integrals. Separable and linear first order differential equations and applications. Introduction to sequences. Convergence of series; Taylor polynomials, Taylor''s Remainder theorem, Taylor series and applications. Parametric/vector representation of curves; particle motion and arc length. Polar coordinates in the plane. [Offered: F,W,S]', 'MATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MATH 135', 'Algebra for Honours Mathematics', 0.5, 'LEC, TST, TUT', 'An introduction to the language of mathematics and proof techniques through a study of the basic algebraic systems of mathematics: the integers, the integers modulo n, the rational numbers, the real numbers, the complex numbers and polynomials. [Offered: F,W,S]', 'MATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MATH 136', 'Linear Algebra 1 for Honours Mathematics', 0.5, 'LAB, LEC, TST, TUT', 'Systems of linear equations, matrix algebra, elementary matrices, computational issues. Real n-space, vector spaces and subspaces, basis and dimension, rank of a matrix, linear transformations, and matrix representations. Determinants, eigenvalues and diagonalization, applications. [Offered: F,W,S]', 'MATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MATH 137', 'Calculus 1 for Honours Mathematics', 0.5, 'LAB, LEC, TST, TUT', 'Absolute values and inequalities. Sequences and their limits. Introduction to series. Limits of functions and continuity. The Intermediate Value theorem and approximate solutions to equations. Derivatives, linear approximation, and Newton''s method. The Mean Value theorem and error bounds. Applications of the Mean Value theorem, Taylor polynomials, and Taylor''s theorem, Big-O notation. Suitable topics are illustrated using computer software. [Offered: F,W,S]', 'MATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MTE 597', 'Special Topics in Mechatronics Engineering: Software Systems', 0.5, 'LEC', 'Various topics at the undergraduate level in mechatronics engineering with a focus on software systems. Courses offered when resources permit.', 'MTE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MATH 138', 'Calculus 2 For Honours Mathematics', 0.5, 'LEC, TST, TUT', 'Introduction to the Riemann integral and approximations. Antiderivatives and the Fundamental theorem of calculus. Change of variables, Methods of integration. Applications of the integral. Improper integrals. Linear and separable differential equations and applications. Tests for convergence for series. Binomial series, Functions defined as power series and Taylor series. Vector (parametric) curves in R2. Suitable topics are illustrated using computer software. [Offered: F,W,S]', 'MATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MATH 661', 'Problem Solving and Proof in Geometry', 0.5, 'LEC', 'This course explores the Euclidean geometry of triangles and circles from elementary to advanced settings. The course also briefly discusses three-dimensional geometry. An emphasis is placed on proof, on problem solving, on problem creation in a geometric context, and on aspects of teaching geometry. Where possible, problems that combine multiple areas of mathematics are used.', 'MATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MATH 145', 'Algebra (Advanced Level)', 0.5, 'LEC, TST, TUT', 'MATH 145 is an advanced-level version of MATH 135. [Offered: F]', 'MATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MATH 146', 'Linear Algebra 1 (Advanced level)', 0.5, 'LAB, LEC, TST, TUT', 'MATH 146 is an advanced-level version of MATH 136. [Offered: W]', 'MATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MATH 147', 'Calculus 1 (Advanced Level)', 0.5, 'LAB, LEC, TST, TUT', 'MATH 147 is an advanced-level version of MATH 137. [Offered: F]', 'MATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MATH 148', 'Calculus 2 (Advanced Level)', 0.5, 'LEC, TST, TUT', 'MATH 148 is an advanced-level version of MATH 138. [Offered: W]', 'MATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MATH 207', 'Calculus 3 (Non-Specialist Level)', 0.5, 'LEC, TST, TUT', 'Multivariable functions and partial derivatives. Gradients. Optimization including Lagrange multipliers. Polar coordinates. Multiple integrals. Surface integrals on spheres and cylinders. Introduction to Fourier Series. [Offered: F,W,S]', 'MATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MATH 211', 'Advanced Calculus 1 for Electrical and Computer Engineers', 0.5, 'LEC, TST, TUT', 'Fourier series. Ordinary differential equations. Laplace transform. Applications to linear electrical systems. [Offered: F,W]', 'MATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MATH 212', 'Advanced Calculus 2 for Electrical Engineers', 0.5, 'LEC, TST, TUT', 'Triple integrals, cylindrical and spherical polar coordinates. Divergence and curl, applications. Surface integrals, Green''s, Gauss'' and Stokes'' theorems, applications. Complex functions, analytic functions, contour integrals, Cauchy''s integral formula, Laurent series, residues. [Offered: F,S]', 'MATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MATH 213', 'Signals, Systems, and Differential Equations', 0.5, 'LEC, TUT', 'Fourier series. Differential equations. Laplace transforms. Applications to circuit analysis. [Offered: S]', 'MATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MATH 215', 'Linear Algebra for Engineering', 0.5, 'LEC, TUT', 'Systems of linear equations; their representation with matrices and vectors; their generalization to linear transformations on abstract vector spaces; and the description of these linear transformations through quantitative characteristics such as the determinant, the characteristic polynomial, eigenvalues and eigenvectors, the rank, and singular values. [Offered F,W]', 'MATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MATH 217', 'Calculus 3 for Chemical Engineering', 0.5, 'LEC, TUT', 'Curves and surfaces in R3. Multivariable functions, partial derivatives, the chain rule, gradients. Optimization, Lagrange Multipliers. Double and triple integrals, change of variable. Vector fields, divergence and curl. Vector integral calculus: Green''s theorem, the Divergence theorem and Stokes'' theorem. Applications in engineering are emphasized. [Offered: F,W]', 'MATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MATH 218', 'Differential Equations for Engineers', 0.5, 'LEC, TUT', 'First order equations, second order linear equations with constant coefficients, series solutions, the Laplace transform method, systems of linear differential equations. Applications in engineering are emphasized. [Offered: F,S]', 'MATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MATH 225', 'Applied Linear Algebra 2', 0.5, 'LEC, TUT', 'Vector spaces. Linear transformations and matrices. Inner products. Eigenvalues and eigenvectors. Diagonalization. Applications. [Offered: F,S]', 'MATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MATH 227', 'Calculus 3 for Honours Physics', 0.5, 'LEC, TUT', 'Directional derivative and the chain rule for multivariable functions. Optimization, Lagrange multipliers. Double and triple integrals on simple domains; transformations and Jacobians; change of variable in multiple integrals. Vector fields, divergence and curl. Vector integral calculus: Line and surface integrals, Green''s Theorem, Stokes'' Theorem, Gauss'' Theorem, conservative vector fields. [Offered: F]', 'MATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MATH 228', 'Differential Equations for Physics and Chemistry', 0.5, 'LEC, TUT', 'First-order equations, second-order linear equations with constant coefficients, series solutions and special functions, the Laplace transform method. Applications in physics and chemistry are emphasized. [Offered: F,W]', 'MATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MATH 229', 'Introduction to Combinatorics (Non-Specialist Level)', 0.5, 'LEC, TUT', 'Introduction to graph theory: colourings, connectivity, Eulerian tours, planarity. Introduction to combinatorial analysis: elementary counting, generating series, binary strings. [Offered: F]', 'MATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MATH 235', 'Linear Algebra 2 for Honours Mathematics', 0.5, 'LEC, TST, TUT', 'Orthogonal and unitary matrices and transformations. Orthogonal projections, Gram-Schmidt procedure, best approximations, least-squares. Inner products, angles and orthogonality, orthogonal diagonalization, singular value decomposition, applications.', 'MATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MATH 237', 'Calculus 3 for Honours Mathematics', 0.5, 'LEC, TST, TUT', 'Calculus of functions of several variables. Limits, continuity, differentiability, the chain rule. The gradient vector and the directional derivative. Taylor''s formula. Optimization problems. Mappings and the Jacobian. Multiple integrals in various co-ordinate systems.', 'MATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MATH 455W', 'Partial Differential Equations (WLU)', 0.5, 'LEC', 'None', 'MATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MATH 239', 'Introduction to Combinatorics', 0.5, 'LEC, TST, TUT', 'Introduction to graph theory: colourings, matchings, connectivity, planarity. Introduction to combinatorial analysis: generating series, recurrence relations, binary strings, plane trees.', 'MATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MATH 636', 'Linear Algebra for Teachers', 0.5, 'LEC', 'This course explores the foundations of linear algebra and some of its applications.  An emphasis will be placed on the development of mathematical thinking and the importance of proof in mathematical teaching.  Topics include:  matrices, linear mappings, vector spaces, determinants, diagonalization, inner products, the Fundamental Theorem of Linear Algebra, and the method of least squares.', 'MATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MATH 245', 'Linear Algebra 2 (Advanced Level)', 0.5, 'LEC, TST', 'MATH 245 is an advanced-level version of MATH 235. [Offered: F,S]', 'MATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MATH 247', 'Calculus 3 (Advanced Level)', 0.5, 'LEC, TST', 'Topology of real n-dimensional space: completeness, closed and open sets, connectivity, compact sets, continuity, uniform continuity. Differential calculus on multivariable functions: partial differentiability, differentiability, chain rule, Taylor polynomials, extreme value problems. Riemann integration: Jordan content, integrability criteria, Fubini''s theorem, change of variables. Local properties of continuously differentiable functions: open mapping theorem, inverse function theorem, implicit function theorem. [Offered: F,W,S]', 'MATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MATH 249', 'Introduction to Combinatorics (Advanced Level)', 0.5, 'LEC, TST', 'MATH 249 is an advanced-level version of MATH 239. [Offered: F,W]', 'MATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MATH 600', 'Introduction to Mathematical Software for Teachers', 0.25, 'LEC', 'This course exposes students to the technical tools that professional mathematicians use.  The software presented in the course will enhance each student''s communication, presentation, visualization, and problem-solving skills.  The course will also take a brief look at the history of mathematical communication and its impact on the development of the subject.', 'MATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MATH 630', 'Foundations of Probability', 0.5, 'LEC', 'This course will explore the basic properties of probability focusing on both discrete and continuous random variables.  Topics include:  Laws of probability, discrete and continuous random variables, probability distributions, mean, variance, generating functions, Markov chains, problem solving, history of probability.', 'MATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MATH 631', 'Statistics for Teachers', 0.5, 'LEC', 'This course discusses some of the mathematical and scientific aspects of empirical, or data-based, problem solving.  Topics will include methods for the design of experiments and surveys, and the analysis of data using statistical models.  Examples will illustrate the application of these methods to data-based problems in science, health, business and industry.', 'MATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MATH 640', 'Number Theory for Teachers', 0.5, 'LEC', 'This course explores the many fascinating properties of the natural numbers. Topics include: the Euclidean algorithm, congruences and modular arithmetic, primitive roots and quadratic residues, sums of squares, multiplicative functions, continued fractions and Diophantine equations, and rational approximations to real numbers.', 'MATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MATH 641', 'Algorithm Design and Analysis', 0.25, 'LEC', 'This course explores the intersection between mathematics and computer science by examining general methods for solving problems efficiently. We will study a variety of algorithm design techniques for problems in diverse application areas and use mathematical models to compare algorithms.  Students will be introduced to "big ideas" from computer science, such as the generalization and reuse of previous solutions, and the notion of limits to the power of computation.', 'MATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MATH 647', 'Foundations of Calculus I', 0.5, 'LEC', 'This course will explore the foundations of differential calculus, the role of rigor in mathematics, and the use of sophisticated mathematical software.  Topics include:  A brief primer on logic and proof, axiom of choice and other ideas from set theory, convergence of sequences and the various forms of the completeness axiom for R, detailed study of limits, continuity and the Intermediate Value Theorem, fundamentals of differentiation and the importance of linear approximation, role of the Mean Value Theorem, the nature and existence of extrema, Taylor''s Theorem and polynomial approximation, MAPLE as a tool for discovery.', 'MATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MATH 648', 'Foundations of Calculus II', 0.5, 'LEC', 'This course explores the foundations of integral calculus and the use of series in approximating the basic functions of mathematics.  Topics include:  Understanding the Riemann Integral and its flaws, the idea of Lebesque, the geometric meaning of the Riemann-Stieltjes integral, the Fundamental Theorem of Calculus, numerical integration, numerical series, uniform convergence of functions and the extraordinary nature of power series, Fourier Series.', 'MATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MATH 650', 'Mathematical Modeling with Differential Equations', 0.5, 'LEC', 'Solving and interpreting differential and difference equations motivated by a variety of systems from the physical and social sciences.  Analytical solutions of standard linear and non-linear equations of first and second order; phase portrait analysis; linearization of non-linear systems in the plane.   Numerical and graphical solutions using mathematical software.', 'MATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MATH 660', 'Explorations in Geometry', 0.5, 'LEC', 'This course is designed to allow the student to discover fundamental facts about geometry through the interactive use of mathematical software.  Possible topics include:  An introduction to affine, projective and non-Euclidean geometry, conic sections in projective geometry, inversion in circles, the Theorems of Desargues, Pappas and Pascal.', 'MATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MATH 674', 'Special Topics in Mathematical Connections', 0.25, 'LEC', 'This course is intended to give the student insight to an important area of mathematics and how it connects with problems in the real world.  The course will consist of either a one six week module, or two three week modules each introducing a separate though possibly related area of mathematics.  The emphasis will be on how the mathematics is used in a real world context.', 'MATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MATH 680', 'History of Mathematics', 0.5, 'LEC', 'We explore the who, where, when and why of some of the most important ideas in mathematics.  Topics include:  William T. Tutte and Decryption, Euclid and the Delian Problem, Archimedes and his estimate of Pi, Al Khwarizmi and Islamic mathematics, Durer and the Renaissance, Descartes and Analytic Geometry, and Kepler and Planetary Motion.', 'MATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MATH 681', 'Problem Solving and Mathematical Discovery', 0.5, 'LEC', 'This course aims to develop the student''s mathematical problem solving ability.  Common heuristics such as problem modification, patterning, contradiction arguments and exploiting symmetry will be examined.  A wide range of challenging problems from various branches of mathematics will provide the medium through which these important principles and broad strategies are experienced.', 'MATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MATH 690', 'Summer Conference for Teachers of Mathematics', 0.25, 'SEM', 'This intense 3-day workshop focuses on the integration of problem solving technology into the curriculum and enrichment activities.  The Workshop is suitable for teachers from all over the world.', 'MATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MATH 692', 'Reading, Writing and Discovering Proofs', 0.25, 'LEC', 'Objectives: To develop the vocabulary, techniques and analytical skills associated with reading and writing proofs, and to gain practice in formulating conjectures and discovering proofs.  Emphasis will be placed on understanding logical structures, recognition and command over common proof techniques, and precision in language. 
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MATH 698', 'Reading Course in Mathematics for Teachers', 0.25, 'RDG', 'Students will undertake a reading, research and writing project on a mathematical topic of interest to teachers.', 'MATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MATH 699', 'Master of Mathematics for Teachers Capstone', 0.5, 'RDG', 'The capstone course is designed to give students an opportunity to showcase the knowledge they have gained and to provide a forum for bringing that knowledge into their own classroom.  As part of this course students will design a mini-course on an approved subject in mathematics.', 'MATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MATH 700', 'Math for Teachers - OVGS Transfer Course', 1, 'LEC', 'None', 'MATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 100', 'Introduction to Mechanical Engineering Practice 1', 0.75, 'LAB, LEC, TUT', 'This course is focused on fundamental knowledge and skills essential for academic and professional development in mechanical engineering. It covers basic methods and principles used by mechanical engineers, e.g., fundamentals of technical communication, the design process and problem solving, measurements and data analysis, engineering professionalism, safety, and intellectual property. The fundamentals of engineering graphical communication using computer-aided design (CAD) and freehand sketching will be a significant component of this course. Written, graphical and oral communications are emphasized. Examples are drawn from Mechanical Engineering. [Offered: F]', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 100B', 'Seminar', 0, 'SEM', 'Discussion of the structure of and options within the mechanical engineering curriculum; of the operation of department, faculty, university, technical societies; of student team and graduate school opportunities; of safety training; and of subject material in support of core courses. [Offered: W,S]', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 101', 'Introduction to Mechanical Engineering Practice 2', 0.75, 'LAB, LEC, TUT', 'This course is a continuation of ME 100 and is aimed at reinforcing the concepts related to technical communications, professionalism, and professional development, with emphasis on mechanical engineering concepts and practical examples. The fundamentals of programming are a significant component of this course, focusing on digital computing, and basic programming and algorithm writing. Programming examples are drawn from mechanical engineering. [Offered: W,S]', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 115', 'Structure and Properties of Materials', 0.5, 'LAB, LEC, PRJ, TST, TUT', 'The relevance of materials to engineering practice. The microstructure of materials, crystallinity and crystal imperfections, glasses and amorphous solids. Elastic and plastic deformation in metals, viscoelasticity of plastics. Strengthening mechanisms in metals, polymers and ceramics. Fracture of brittle and ductile solids. Electrical and magnetic properties of materials. [Offered: W, S]', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 200A', 'Seminar', 0, 'LEC', 'Discussion of the structure of and options within the mechanical engineering curriculum; of the operation of department, faculty, university, technical societies; of student team and graduate school opportunities; of safety training; and of subject material in support of core courses. [Offered: F,W]', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 200B', 'Seminar', 0, 'LEC', 'Discussion of the structure of and options within the mechanical engineering curriculum; of the operation of department, faculty, university, technical societies; of student team and graduate school opportunities; of safety training; and of subject material in support of core courses. [Offered: F,S]', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 201', 'Advanced Calculus', 0.5, 'LAB, LEC, TUT', 'A continuation of First Year calculus, focusing on calculus of scalar and vector functions of several variables. Both classical calculus techniques and the computer implementation of numerical methods are discussed. Partial differentiation, total derivatives, chain rule, transformation of variables, Taylor series. Applications include geometrical problems, error estimation, maxima and minima, least squares curve fits. Multiple integration in standard coordinate systems, Jacobians. Vector calculus, divergence, curl, Laplacian, and Stokes'', Green''s and Divergence theorems. Scalar flux transport, work and energy, conservative force fields. [Offered: F, W]', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 202', 'Statistics for Engineers', 0.5, 'LEC, TUT', 'Frequency distributions; measures of central tendency; standard deviation and other measures of dispersion. Probability. Binomial, Poisson, normal distributions. Techniques of sampling and statistical estimation. Tests of hypotheses; significance. The t-test and chi-squared test. Curve fitting by least squares. Statistical process control. Correlation and regression. Experimental design. [Offered: F, W]', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 203', 'Ordinary Differential Equations', 0.5, 'LEC, TUT', 'Solution of ordinary differential equations. First and higher order differential equations. Nonlinear equations. Linear equations with constant and variable coefficients. Systems of linear equations. Applications involving simple dynamical systems and principles of mass, momentum and heat conservation will emphasize the role of ordinary differential equations in understanding the behaviour of physical systems. Introduction to the Laplace transform method for solving ordinary differential equations. [Offered: F, S]', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 212', 'Dynamics', 0.5, 'LEC, TUT', 'An introduction to the kinematics of particle and rigid body motion. Impulse-momentum equations. Work-energy methods and Euler''s equations. Simple gyroscopes. Vibrations. [Offered: F, S]', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 219', 'Mechanics of Deformable Solids 1', 0.5, 'LEC, TUT', 'Concept of equilibrium, force analysis of structures and structural components, equilibrium of deformable bodies, stress and strain concepts, stress-strain relationships, stress analysis of prismatic members in axial, shearing, torsional and flexural deformations, shear force and bending moment diagrams. [Offered: F, W, S]', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 220', 'Mechanics of Deformable Solids 2', 0.5, 'LEC, TUT', 'A general treatment of the behaviour of structural components from the study of stress and strain in solids. Topics include superposition, energy theorems, theories of failure, elastic and inelastic analysis of symmetrical bending, torsion of circular members, columns and stability, and virtual work. [Offered: F, S]', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 230', 'Control of Properties of Materials', 0.5, 'LAB, LEC, TUT', 'Phase equilibria, non-equilibrium behaviour, heat treatment of metals, diffusion, strengthening processes. Alloying, composite materials, cold and hot working. Failure of engineering materials; creep, fatigue, corrosion and other environmental degradation processes. Prevention of service failures. [Offered: F, W]', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 235', 'Materials Science and Engineering', 0.5, 'LAB, LEC, PRJ, TST, TUT', 'The microstructure of crystalline and amorphous materials including metals, polymers and ceramics. Elastic and plastic deformation in metals, viscoelastic deformation of polymers and viscous deformation of ceramic glasses. Fracture of brittle and ductile solids. Phase equilibria, non-equilibrium behaviour, heat treatment of metals, diffusion, strengthening processes. [Offered: F]', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 250', 'Thermodynamics 1', 0.5, 'LEC, TUT', 'The engineering science of energy. The scope and limitations of thermodynamics. Macroscopic approach to heat, work, energy and the First Law. Properties and state of simple substances. Control-mass and control-volume energy analysis. The Second Law of Thermodynamics, principle of increase of entropy, limiting cycle efficiencies, criteria for equilibrium. [Offered: F, S]', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 262', 'Introduction to Microprocessors and Digital Logic', 0.5, 'LAB, LEC, TUT', 'Number systems, logic gates, Boolean algebra. Karnaugh maps and combinational logic design. Sequential logic and state machines. Programmable Logic Controllers (PLCs) and PLC programming using ladder logic and statement list. Microcomputer structure and operation, I/O and interfacing. Assembly language programming. Laboratory work includes microcomputer and PLC programming. [Offered: F, S]', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 269', 'Electromechanical Devices and Power Processing', 0.5, 'LAB, LEC, TUT', 'Review of circuit analysis. Basic electromagnetic theory. DC machines, synchronous generators, transformers, and induction motors. Introduction to typical speed and torque control techniques of machines using power electronic based devices.', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 300A', 'Seminar', 0, 'LEC', 'Discussion of the structure of and options within the mechanical engineering curriculum; of the operation of department, faculty, university, technical societies; of student team and graduate school opportunities; of safety training; and of subject material in support of core courses. [Offered: W,S]', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 300B', 'Seminar', 0, 'LEC', 'Discussion of the structure of and options within the mechanical engineering curriculum; of the operation of department, faculty, university, technical societies; of student team and graduate school opportunities; of safety training; and of subject material in support of core courses.
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 303', 'Advanced Engineering Mathematics', 0.5, 'LEC, TUT', 'A continuation of ME 201 and ME 203 in which both classical calculus techniques and the computer implementation of numerical methods are discussed. Partial differential equations of mathematical physics: wave, diffusion, Laplace, Poisson equations. Boundary and initial conditions. Separation of variables. Numerical methods for ordinary and partial differential equations. Applications will emphasize the role of ordinary and partial differential equations in understanding the behaviour of physical systems. [Offered: W, S]', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 321', 'Kinematics and Dynamics of Machines', 0.5, 'LEC, TUT', 'Principles of the geometry of motion, Uniform and non-uniform motion, linkage, gears, cams. Synthesis and analysis of mechanisms. Consideration of the static and dynamic forces in machines. Vibration analysis, response to shock, motion and force transmissibility, vibration isolation. [Offered: W, S]', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 322', 'Mechanical Design 1', 0.5, 'LAB, LEC, TUT', 'Adequacy assessment and synthesis of machine elements with a focus on the design process. Static failure of ductile and brittle materials, fatigue analysis of structures. Topics include the design of welds, bolted connections, springs and shafts. [Offered: F, W]', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 340', 'Manufacturing Processes', 0.5, 'LAB, LEC, TUT', 'The principles of manufacturing unit processes including casting, forming, machining and joining. Interactions between design, materials (metals, polymers, ceramics) and processes. Advantages and limitations, relative cost, and production rates of competitive processes. [Offered: W, S]', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 351', 'Fluid Mechanics 1', 0.5, 'LAB, LEC, TUT', 'Physical properties of fluids and fundamental concepts in fluid mechanics. Hydrostatics. Conservation laws for mass, momentum and energy. Flow similarity and dimensional analysis as applied to engineering problems in fluid mechanics. Laminar and turbulent flow. Engineering applications such as flow measurement, flow in pipes and fluid forces on moving bodies. [Offered: F, W, S]', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 353', 'Heat Transfer 1', 0.5, 'LAB, LEC, TUT', 'Introduction to heat transfer mechanisms. The formulation and solution of steady and transient heat conduction. Radiant heat transfer including exchange laws and view factors. Introductory convective heat transfer. [Offered: F, W]', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 354', 'Thermodynamics 2', 0.5, 'LAB, LEC, TUT', 'Emphasis on applications of thermodynamics to flow processes. Real fluids, evaluation of state functions of real fluids. Non-reacting mixtures, reacting mixtures, equilibrium considerations. [Offered: W, S]', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 360', 'Introduction to Control Systems', 0.5, 'LAB, LEC, TUT', 'Open loop and feedback control. Laws governing mechanical, electrical, fluid and thermal control components. Analogies. Analysis of some engineering control systems using block diagram algebra, transient and steady-state operation. Different modes of control. Review of Laplace Transform methods. Concepts of stability. Principles of analog computer simulation. Brief treatment of linear flow graphs and bondgraphs. [Offered: F, W]', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 362', 'Fluid Mechanics 2', 0.5, 'LAB, LEC, TUT', 'Basic equations of two-dimensional flow, potential flow, exact viscous solutions. Introduction to lubrication, boundary layers, turbulence, and compressible flow. Turbomachinery fundamentals and applications. Selected advanced topics. [Offered: F, W]', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 380', 'Mechanical Engineering Design Workshop', 0.5, 'LEC, PRJ', 'In this course, students study the design process, including needs analysis, problem definition; design criteria and critical parameter identification, generation of alternative solutions; conceptual design, detailed design, optimization; and implementation. Most of the term is devoted to a significant design project in which student groups work independently and competitively, applying the design process to a project goal set by the faculty co-ordinator. The design project typically includes construction of a prototype, and part of the course grade may depend on the performance of the prototype in a competitive test. In exceptional circumstances, the requirement for a prototype may be replaced by a computer simulation, or may be waived. Other mechanical engineering faculty members, particularly those teaching 3B courses, are available to provide advice and supervision to ME 380 students. [Offered: F, W]', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 400A', 'Seminar', 0, 'LEC', 'Research frontiers in mechanical engineering, specific discussion of research done at Waterloo, seminars by members of research groups. [Offered: S, F]', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 400B', 'Seminar', 0, 'LEC', 'Research frontiers in mechanical engineering, specific discussion of research done at Waterloo, seminars by members of research groups. [Offered: W]', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 401', 'Law for the Professional Engineer', 0.5, 'LEC', 'The Canadian Legal System, Forms of Business Organizations, Tort Law, the role of the professional; Contract Law, the Elements of a Contract, Statute of Frauds, Misrepresentation, Duress and Undue Influence, Mistake, Contract Interpretation, Discharge of Contract; Breach of Contract and fundamental breach; Agreements between the client and Engineer; General Law, the Mechanics'' Lien Act, comparative discussion of the Professional Engineers Act as it relates to the earlier statute, Intellectual Property and Industrial Property. It is intended to prepare the student for the examination in law which must be written for licensing by Professional Engineers Ontario (PEO).', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 423', 'Mechanical Design 2', 0.5, 'LEC, TUT', 'A continuation of the ME 322 course in analysis and synthesis of machinery, including advanced analysis of machine elements such as clutches, brakes, couplings, journal bearings and gears. Advanced machine design concepts such as reliability, optimization and techniques for stimulating innovative design. A synthesis project involving the machine elements studied is usually included. [Offered: F, S]', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 435', 'Industrial Metallurgy', 0.5, 'LEC, PRJ', 'This course is intended for those students interested in acquiring a working knowledge of metallurgy. It covers: metals and alloy systems, iron-carbon alloys, heat treatment and the function of alloying elements in steel, corrosion and scale resistant alloys, copper and nickel base alloys, light metals and their alloys; casting, hot and cold working of metals; soldering, brazing and welding; corrosion and oxidation; metal failure analysis. [Offered: F, S]', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 436', 'Welding and Joining Processes', 0.5, 'LAB, LEC', 'Introduction to modern welding and joining processes for metals, polymers and ceramics. Fundamentals of the joining process and the influence of the process parameters on weld dimensions, strength and quality. Fusion welding processes such as shielded metal arc, gas tungsten arc, gas metal arc, submerged arc welding and others including electron beam and laser beam welding. Resistance welding processes, solid-state welding processes, soldering and brazing. Laboratory exercises will provide hands-on experience with a number of industrially significant welding processes. [Offered: F,S]', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 452', 'Energy Transfer in Buildings', 0.5, 'LAB, LEC, PRJ', 'Thermodynamic properties of moist air; psychrometric charts; humidity measurements; direct water contact processes; heating and cooling of moist air by extended surface coils; solar radiation; heating and cooling loads on buildings; effects of the thermal environment; air conditioning calculations. [Offered: W]', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 456', 'Heat Transfer 2', 0.5, 'LEC', 'Selected topics in heat transfer fundamentals and applications. Topics to be covered include the fundamentals of convection with analytical solutions to simple laminar flow problems and approximate solutions to turbulent flow problems based on analogies between momentum and heat transfer. Also covered is radiant exchange in grey enclosures and in black enclosures containing emitting-absorbing gases. The remaining topics will be chosen from design of heat exchangers; condensation heat transfer; boiling heat transfer; and the treatment of problems in heat conduction. [Offered: F, S]', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 459', 'Energy Conversion', 0.5, 'LEC', 'Review of reserves and consumption trends of Canada''s and the world''s energy resources. Design of fossil-fuel central power plants, including boiler efficiency calculations and advanced steam and binary cycles. Review of atomic physics including fission and fusion energy. Design of nuclear fission power plants including design of reactor core for critical conditions, fuel cycles and radiation hazards. Design considerations for solar energy conversion devices including: availability of solar energy, solar-thermal converters, thermal storage and photovoltaics. Principles of fuel cells and some aspects of their design. Other topics as appropriate. [Offered: F, S]', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 481', 'Mechanical Engineering Design Project 1', 0.5, 'PRJ', 'The first of two required courses for the mechanical engineering capstone design project. This course is intended to enable students to engage in in-depth engineering design and decision-making using engineering science while encouraging creativity and resourcefulness, and addressing the criteria listed in the faculty of engineering design rubrics. Students will work in small groups on a design project of their own choosing, or as part of a major student team project. The goal is to develop a design proposal, consisting of the needs analysis, design specifications and project plan, followed by the initial and detailed design work. [Offered: F, S]', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 482', 'Mechanical Engineering Design Project 2', 0.5, 'PRJ', 'A continuation of ME 481. The final design of the major mechanical engineering project proposed in ME 481 will be undertaken. The purpose of this phase of the project is to carry out a detailed technical design and proof of feasibility of the solution proposed in ME 481 [Offered: W]', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 524', 'Advanced Dynamics and Vibrations', 0.5, 'LEC, TUT', 'This course is a continuation of ME 212 and ME 321. It includes study of planar and three-dimensional motion of rigid bodies and systems, including gyroscopic effects. Vector (Newton''s Method) and analytical (Lagrange''s Equation) methods are used to derive equations of motion. Linear vibrational analyses are performed to examine natural frequencies, stability, and mode shapes. Computer simulation of non-linear systems is discussed. [Offered: W]', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 526', 'Fatigue and Fracture Analysis', 0.5, 'LEC', 'Fatigue and Fracture Analysis of metallic components including welded joints. Review of test and design procedures. Sources of cyclic loading. Cyclic counting procedures and cumulative damage. S-N curves and effects of mean, residual and multiaxial stressing. Stress Concentrations; scatter and fatigue life distributions. Transition temperature concepts. Linear elastic fracture mechanics analysis of fatigue crack propagation and fracture initiation. Crack arrest. [Offered: W]', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 531', 'Physical Metallurgy Applied to Manufacturing', 0.5, 'LEC, PRJ', 'This course will allow the student to develop a more in depth knowledge of physical metallurgy and its application in understanding and solving relevant manufacturing problems. It will begin with a treatment of solid-state diffusion, mass transport and the principles of solidification including constitutional supercooling. This knowledge will then be applied to understand the microstructural development (and resultant properties) which occur in materials during manufacturing processes including casting, solid-state heat treatments, laser processing and various joining operations. The course will include case studies aimed at providing the students with an opportunity to apply their knowledge in a practical way. [Offered: W]', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 533', 'Non-metallic and Composite Materials', 0.5, 'LAB, LEC', 'This course is intended to provide an advanced treatment of the structure, properties and processing of non-metallic and composite materials based on polymers, metals and ceramics. The structure and properties of polymers and ceramics in bulk form and as matrices and reinforcements in composites will be covered. Processing methods for non-metallics and composites (example extrusion, injection molding etc.) will be considered. The geometrical arrangement of fibres within laminae and their influences on elastic and strength properties of composites will be described based on suitable micromechanical models. The role of the matrix and fibre/matrix interface in determining composite properties will be described. [Offered: W]', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 535', 'Welding Metallurgy', 0.5, 'LAB, LEC', 'Metallurgy of welding of steels (carbon, microalloy, low alloy and stainless steels), cast irons, aluminum-based, copper-based, nickel-based, cobalt-based, titanium-based and other alloys, (including dissimilar combinations) to explain the effects of welding processes and conditions (including post-weld heat treating) on microstructure and properties; causes and prevention of defects and deficiencies which can occur in different alloys, including porosity, cracking, embrittlement (hydrogen, temper, strain aging, ductile-brittle transition temperatures), overaging; metallurgy of soldered and brazed joints. Laboratory experiments will demonstrate microstructural effects and defects in a range of alloys for different welding processes and conditions. [Offered: W]', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 538', 'Welding Design, Fabrication and Quality Control', 0.5, 'LAB, LEC, PRJ', 'Manufacturing principles of welded mechanical components, machinery, pressure vessels and structures subject to static or dynamic loading. Design of weld joints for structures made from ferrous alloys such as plain carbon and low alloy steels and non-ferrous alloys such as aluminum alloys. Residual stresses in weldments and distortion of weldments. Quality and quality control in welding fabrication; welding standards; welding procedure qualification; nondestructive examination methods for welds and brazed joints such as radiography, dye penetrant, magnetic particle, ultrasonic, and eddy current techniques.[Offered: W]', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 547', 'Robot Manipulators: Kinematics, Dynamics, Control', 0.5, 'LAB, LEC', 'Homogeneous transformations, D-H convention, forward and inverse kinematics. Differential transformations and Jacobians. Robot dynamics. Programming, trajectory generation and joint control. End-of-arm sensing and outer loop control. Industrial applications. [Offered: W]', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 548', 'Numerical Control of Machine Tools 1', 0.5, 'LAB, LEC', 'Operation fundamentals of NC machine tools. NC part programming: manual, and CAD/CAM methods. Mechanics of metal cutting: examples of turning, milling, and drilling. Tool wear and breakage. Optimum cutting conditions. Dimensional and form errors due to static deformations. Dynamics of machining. Laboratory work provides hands-on experience in tool path generation, machining, and measurements of cutting forces and vibration. [Offered: F, S]', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 555', 'Computer-Aided Design', 0.5, 'LAB, LEC', 'Need for geometric modelling, historic developments; wire frame models; hidden line removed models; polyhedral models; surface models and solid models. Constructive solid geometry; boundary representation and decomposition modelling. Hybrid models. Data structures and their role in modelling. Curves and surfaces in modelling (Bezier, B-splines and NURBS). Geometric models and the role of engineers. Parametric and feature-based design. The course has a heavy lab component which provides exposure to solid modelling on SDRC IDEAS and PC-based CAD packages. [Offered: W]', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 557', 'Combustion 1', 0.5, 'LEC', 'Combustion thermodynamics, introduction to chemical kinetics of combustion, combustion properties of fuels, flammability of combustible mixtures. Flame propagation mechanisms, pre-mixed and diffusional; stability of flames; introduction to combustion aerodynamics, jet flames; atomization; droplet and spray combustion. Elementary ignition concepts and theory. Basic detonation theory. [Offered: W]', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 559', 'Finite Element Methods', 0.5, 'LEC', 'A course presenting the fundamental ideas involved in conventional finite element analysis in mechanical engineering. Domain discretization, interpolation and shape functions, element derivation and types, element stiffness or property equations, assembly procedure, boundary conditions, solution methods for the algebraic equation system, applications in heat transfer, fluid flow, and stress analysis. Students will, throughout the course, write and test their own finite element code through individual subroutine construction as the course progresses. [Offered: F, S]', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 561', 'Fluid Power Control Systems', 0.5, 'LEC', 'Properties of hydraulic fluids. Design and function of conventional hydraulic and pneumatic circuits. Characteristics of flow and pressure control valves. Speed control in fluid power circuits. Performance of pumps and fluid motors. Hydrostatic and hydrokinetic transmission systems. Principles of sealing, filtration and heat control in hydraulic circuits. Industrial applications of fluid power systems. [Offered: F, S]', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 563', 'Turbomachines', 0.5, 'LEC', 'Classification of turbomachines, performance parameters and laws of modelling. Basic equation of flow in turbomachines, compressible flow. Energy transfer in radial and axial turbomachines, performance characteristics, losses and efficiencies. Blade and cascade design, 3 dimensional effects. [Offered: W]', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 564', 'Aerodynamics', 0.5, 'LEC', 'An introductory course in aerodynamics for engineers. Kinematics and dynamics of inviscid flow; airfoil dynamics including thin airfoil theory, finite wings, panel methods and airfoil parameters. Boundary layer theory and boundary layer control as applied in aerodynamics. Introduction to high speed aerodynamics. Introduction to dynamics of flight including stability and control. [Offered: W]', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 566', 'Computational Fluid Dynamics for Engineering Design', 0.5, 'LEC', 'A course to develop the understanding required to simulate complex fluid flows, such as those found in turbo-machines, duct systems, and other engineering hardware. Course topics include: the physics of complex viscous fluid flows, first- and second-order finite control volume discretization methods, iterative algorithms for the solution of sparse matrix equation sets, including multi-grid acceleration, boundary condition modelling, two-equation and Reynolds stress turbulence models, and grid generation techniques. Computational fluid dynamics software is used throughout the course to simulate and analyse complex fluid flows relevant to engineering applications. [Offered: F, S]', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 567', 'Fire Safety Engineering', 0.5, 'LAB, LEC, PRJ', 'The art and science of fire safety engineering. Fundamentals of fire behaviour, fuels and flammability, heat transfer and fluid dynamics of fires and fire modeling. Practical issues and applications of fire safety, fire control and hazard assessment in the design of buildings, industrial environments and transportation systems. [Offered: W]', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 571', 'Air Pollution', 0.5, 'LEC', 'Nature and sources of air pollution, chemical and biological aspects, effects on health and environment. Physical aspects of the atmosphere, thermodynamics, vertical variation of wind and temperature, stability, convection, atmospheric turbulence, diffusion equations, plumes, thermals, jets in stratified flow, radioactive plumes, micrometeorological instrumentation, air pollution control techniques and equipment monitoring instrumentation. [Offered: W]', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 595', 'Special Topics in Mechanical Engineering', 0.5, 'LEC', 'Various courses dealing with selected topics at the undergraduate level in automation and control, solid mechanics and machine design, materials engineering and processing, fluid mechanics, and thermal engineering. Courses offered when resources permit.', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 596', 'Special Topics in Mechanical Engineering', 0.5, 'LEC', 'Various courses dealing with selected topics at the undergraduate level in automation and control, solid mechanics and machine design, materials engineering and processing, fluid mechanics, and thermal engineering. Courses offered when resources permit.', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 597', 'Special Topics in Mechanical Engineering', 0.5, 'LEC', 'Various courses dealing with selected topics at the undergraduate level in automation and control, solid mechanics and machine design, materials engineering and processing, fluid mechanics, and thermal engineering. Courses offered when resources permit.', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 598', 'Special Topics in Mechanical Engineering', 0.5, 'LEC', 'Various courses dealing with selected topics at the undergraduate level in automation and control, solid mechanics and machine design, materials engineering and processing, fluid mechanics, and thermal engineering. Courses offered when resources permit.', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 599', 'Special Topics in Mechanical Engineering', 0.5, 'LEC', 'Various courses dealing with selected topics at the undergraduate level in automation and control, solid mechanics and machine design, materials engineering and processing, fluid mechanics, and thermal engineering. Courses offered when resources permit.', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 610', 'Analytical Methods in Vibrations', 0.5, 'LEC', 'This course presents the principles relevant to understanding and modelling of vibrating systems. The systems may either be continuous, or discrete with multiple degrees of freedom. The following topics of a general nature are first covered: principles of dynamics, strain energy, virtual work, the variational principle, and Lagrange''s equation. Discrete-system topics covered are eigenvalue problems, modal analysis, natural modes of vibration, solution of the characteristic determinant, and approximate methods of solution, (e.g., using finite element methods). Continuous-system topics covered are formulation of the boundary value problem, vibration of rods and membranes, Rayleigh''s integral method, the Rayleigh-Ritz method, and Galerkin''s method. The course work includes computer algebra, computer simulation, and experiments in the laboratory.', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 620', 'Mechanics of Continua', 0.5, 'LEC', 'Mathematical preliminaries; co-ordinate transformations, introduction to tensors, tensor fields and transformations, integral theorems, analysis of deformation; deformation tensors and rates of deformation tensors and their mechanical significance, convecting and rotating axes. Analysis of stress; definition of stresses and their physical significance, rates of stresses, objective stress rates. Constitutive equations for elasticity and plasticity (Prandtl-Reuss). Hardening laws and material rate sensitivity. Anisotrophy.', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 621', 'Advanced Finite Element Methods', 0.5, 'LEC, TUT', 'The course covers principles of finite element method as applied to linear and non-linear problems. The course will start by reviewing fundamentals of finite element method including discretization, element formulation, assembling process, boundary conditions, solving system of equations, and post processing. The focus will then shift to non-linear FEM. A brief summary of variational calculus and the classical theory of plasticity will be followed by the theory of non-linear FEM including various numerical integration schemes. The course will also include use of software/programming with available codes/in-house codes in solving nonlinear problems.', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 627', 'Fatigue Analysis and Design', 0.5, 'LEC', 'Advanced fatigue analysis including strain-based and stress-based approaches. Applications will include machine and welded components. Specific topics to include cyclic stress-strain behaviour, notch analysis, damage accumulations, multi-axial and variable amplitude loading. Probabilistic approaches and interpretation of finite element results will be addressed.', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 628', 'Fracture Mechanics', 0.5, 'LEC', 'Linear elastic, elastic-plastic and fully plastic approaches to the analysis of cracked components. Calculation and measurement of fracture mechanics parameters - Charpy, strain energy release rate, stress intensity factor, crack tip opening displacement and J-integral - will be covered, including correlations between the various parameters and limitations on their use. Applications will include the analysis of sub-critical crack growth (fatigue) and design procedures, especially the failure assessment diagram approach.', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 631', 'Mechanical Metallurgy', 0.5, 'LEC', 'Elastic, anelastic and plastic properties of single crystals and polycrystalline aggregates. Relationship between single crystal and polycrystalline deformation. Dislocation theory applied to deformation processes at high and low temperatures. Microscopic aspects of ductile and brittle fracture.', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 632', 'Experimental Methods in Materials Engineering', 0.5, 'LEC', 'This course will focus on techniques used to characterise materials including metals, ceramics, polymers and composites. Techniques will include scanning and transmission electron microscopy, x-ray and neutron diffraction, thermal analysis (DSC, DMA, TGA, dilactometry), and quantitative metallography. Some of the techniques covered in lectures will be used in laboratory exercises. These exercises will be used to demonstrate the practical use of the equipment and illustrate materials processing, microstructure and property relationships.', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 645', 'Metallurgy and Plasticity in Metalworking', 0.5, 'LEC', 'The interaction of material properties and process variables in plastic deformation processes. Phenomena of hot, warm and cold working. Thermo-mechanical processing. Flow stress and workability. Effects of hydrostatic pressure. Analysis of stress and strain state in forging, rolling, extrusion, drawing and sheet metalworking.', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 648', 'Surface Modelling in Machining', 0.5, 'LEC', 'This course presents the principles behind the mathematical representation of surfaces in ways that are suitable for computers, together with the application of such representations to computer-controlled machining processes. The Bezier, B-spline and NURBS representations are all covered, as are important surface properties, like curvature, shortest-distance algorithms, ray-intersection, surface sub-division, knot insertion, and degree elevation. Machining aspects covered are three-, four- and five-axis methods, anti-gouging methods and anti-interference checking.', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 649', 'Control of Machines and Processes', 0.5, 'LEC', 'The concepts of computer-aided manufacturing, microcomputer systems and interfacing techniques for industrial applications. Conversion techniques, timing considerations, thermal and optical sensing, interpolation methods for control of drive systems and programmable controllers are representative of the topics presented. Hardware and software design of real time microcomputer systems which are then implemented in the laboratory constitute a major portion of the course requirements.', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 651', 'Heat Conduction', 0.5, 'LEC', 'Steady and transient heat conduction in isotropic media. Review of fundamental principles of heat conduction and boundary conditions. Introduction to the concept of thermal resistance of systems and of thermal constriction resistance. Derivation of gradient, divergence, Laplacian, conduction equation, boundary conditions and thermal resistance in general orthogonal curvilinear co-ordinates. Solutions of conduction equations in several co-ordinate systems. Introduction to finite difference and finite element formulations of the conduction equation in curvilinear co-ordinates.', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 652', 'Convective Heat Transfer', 0.5, 'LEC', 'Derivation of the general energy equation. Parameters required for determination of heat transfer in laminar and turbulent flows. Fully numerical solutions, exact solutions, and approximate solutions for internal and external flows. Problems involving frictional heating, property variations and mass injection at the wall will be considered. If interest is indicated, special topics such as heat transfer by boiling, condensation and evaporation will be discussed.', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 653', 'Radiation Heat Transfer', 0.5, 'LEC', 'Blackbody radiation; properties of surfaces; heat exchange between black, isothermal surfaces; heat exchange in an enclosure composed of diffuse-gray surfaces; radiation in the presence of other modes of heat transfer; radiation in absorbing-emitting media; heat exchange in enclosures containing absorbing-emitting gases; flames, luminous flames and particle radiation.', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 662', 'Advanced Fluid Mechanics', 0.5, 'LEC', 'Cartesian tensor forms of basic equations; vorticity; Reynolds number effects; ideal, irrotational flow, some exact viscous solutions. Selection of topics from: boundary layer theory with heat and mass transfer; slow viscous flows and lubrication; hydrodynamic stability of laminar flows; special topics.', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 663', 'Computational Fluid Dynamics', 0.5, 'LEC', 'This course presents the concepts and details required to develop computer codes for the simulation of complex multidimensional fluid flows. The following topics will be covered: the finite volume discretization method, discretization schemes for diffusive fluxes, iterative solution algorithms, multigrid acceleration techniques, first and second order bounded discretization schemes for advective fluxes, special treatments for the coupled momentum and mass conservation equations, pressure redistribution techniques, velocity-pressure solution techniques, and extensions to multi-dimensions. Enrichment topics will be chosen from the following research areas: grid generation, turbulence modelling, and emerging discretization and solution algorithm technologies.', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 664', 'Turbulent Flow', 0.5, 'LEC', 'Stochastic concepts, averages, correlation coefficients, auto-correlation functions, spectra. Space and time scales of turbulent fluctuations, energy dissipation in turbulence. Correlation and spectral tensors in three dimensions, isotropic forms. Equations of motion, spectral equation for isotropic turbulence. Universal equilibrium theory, the Kolmogoroff spectrum. Turbulence transport modelling for engineering calculation of turbulent shear flows.', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 670', 'Atmospheric Dynamics', 0.5, 'LEC', 'Hydrodynamic equations of motion on a rotating axis. Geostropic balance in the atmosphere and oceans, vertical variation of wind and pressure fields in the atmosphere, mechanisms of pressure change, vorticity equation.', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 705', 'Special Topics in Tribology', 0.5, 'LEC', 'Various courses dealing with selected aspects of friction, lubrication and wear, including contact phenomena, lubricant behaviour under concentrated contact conditions, and lubrication in special environments. Subject to approval of instructor.', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 706', 'Advanced Tribology', 0.5, 'LEC', 'Mechanical engineering aspects of tribology are emphasized. Topics include the fundamentals of fluid film lubrication and contact mechanics. These fundamentals are applied to model friction, surface temperatures, boundary lubrication, mixed film lubrication, elastohydrodynamic lubrication and wear. Specific applications may be presented, if time permits. ME 423 is a recommended but not an essential prerequisite.', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 707', 'Machinery Noise and Vibration Analysis', 0.5, 'LEC', 'Deterministic and random signal analysis. Digital spectral analysis. Data analysis. Further aspects of theoretical acoustics, spherical wave propagation, effects of attenuation due to damping. Applications of the fast Fourier transform. Structural acoustic coupling. Statistical analyses applied to signal processing. Gear and bearing noise. Impacting noise from machinery. Machinery health monitoring system. System identification of potential equipment breakdown.', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 709', 'Control Engineering and Mechanical Systems', 0.5, 'LEC', 'This course is aimed at applications of control to Mechanical Systems. Course contents: Review of Control; Poles and zeros, Transfer functions, Time Reponse, Actuators, Electrical Systems, PID Control, designing controllers with root locus, state space representations, phase planes, stability concepts, frequency Response. Applications to Mechanical systems: Robots, Hydraulic systems, Active Vibration Control.', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 710', 'Special Topics in Control Systems', 0.5, 'LEC', 'None', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 711', 'Non-Linear Vibrations', 0.5, 'LEC', 'Review of linear systems; free and forced vibrations; conservation systems; general autonomous systems; equilibrium and periodic solutions, linearization and Lyapunov stability criteria; Poincare-Bendixon theorem; quantitative analysis of weakly nonlinear systems in free and forced vibrations using perturbation methods; bifurcations and chaos in dynamical systems. This course will use computer programs (such as MAPLE and MATLAB) for simulation and analysis.', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 720', 'Special Topics in Solid Mechanics', 0.5, 'LEC', 'None', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 722', 'Topics in Pressure Vessel Design', 0.5, 'LEC', 'Design and analysis of pressure vessels, safety considerations and interpretation of pressure vessel codes. Fatigue and fracture modes of failure. Intersecting vessels and connections. Computer techniques of analysis.', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 725', 'Special Topics in Advanced Stress Analysis', 0.5, 'LEC', 'Various courses dealing with advanced topics in stress analysis such as finite element and other computational techniques, variational approaches, continuum mechanics, plasticity, contact and dynamic stresses. Subject to approval of instructor.', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 729', 'Special Topics in Advanced Machine Design Methods', 0.5, 'LEC', 'Various courses concerned with advanced topics and methods in machine design, such as reliability, life-cycle design, computer-aided design, and kinematic synthesis. Subject to approval of instructor.', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 731', 'Corrosion and Oxidation', 0.5, 'LEC', 'Electrochemical reactions and equilibria in ionic systems. Electrode kinetics and rates of corrosion. Modes of corrosive attack including stress corrosion cracking and hydrogen embrittlement. Corrosion prevention through materials selection, design, cathodic and anodic protection and coatings. Mechanisms and kinetics of high temperature oxidation. Selection of high temperature materials for maximum service lives. Discussion of technologically important material-environment combinations.', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 732', 'Thermodynamics and Phase Transformations', 0.5, 'LEC', 'Atomistic and thermodynamic interpretation of the fundamental properties of solids. Diffusion, solidification, solid-state transformations (civilian and military), surface behaviour, phase equilibria, oxidation, corrosion.', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 734', 'Composite Materials', 0.5, 'LEC', 'Fibrous, lamellar and particle reinforced composites. The effect of combinations of brittle and ductile phases on the mode of deformation of an aggregate. The reinforcing mechanism of continuous and discontinuous fibres. Fracture and energy absorbing characteristics of composite materials.', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 735', 'Special Topics - Welding and Joining', 0.5, 'LEC', 'Discussion of selected current topics in materials science and engineering.', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 736', 'Topics in Mechanical Metallurgy', 0.5, 'RDG', 'None', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 737', 'Microstructural Engineering Topics', 0.5, 'LEC', 'None', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 738', 'Special Topics in Materials', 0.5, 'LEC', 'None', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 739', 'Manufacturing Processes Topics', 0.5, 'LEC', 'None', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 741', 'Design of Intelligent Systems: Mechatronics', 0.5, 'LEC', 'Review of modelling and approximation of dynamic systems. Review of classical control theory. Electronic realisation of control elements and compensations: ideal and real PID. Elements of digital control theory: sampling theorem, z-transform and digital filters. Review of computer interfacing, power amplifiers, sequential logic, encoders, and motor control. The course involves practical projects and significant laboratory usage.', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 742', 'Modelling and Control of Dynamic Systems', 0.5, 'LEC', 'Review of classical system modelling. Introducing bondgraphs as a unified approach in modelling of mechanical, electrical, thermal, and fluid dynamic systems. Application of bondgraphs to multibody dynamics. State space representation and response of linear systems. Review of classical linear control theory. Introduction to modern control theory and study system characteristics: controllability, observability and stability.', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 743', 'Modal Analysis and Modelling', 0.5, 'LEC', 'Computer-aided engineering complements CAD/CAM by helping the engineer design not only individual components, but also design and analyse total systems. This course deals with the area of advanced vibration analysis and modelling using a combination of data acquisition and software analysis methods. This modal analysis approach to design uses several software packages. Topics discussed are the theory of modal analysis, parameter estimation and error assessment, computer modelling of structures, practical aspects of good data collection and manipulation. The course involves significant laboratory usage.', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 745', 'Quality Assurance and Reliability in Manufacturing', 0.5, 'LEC', 'Building quality in manufacturing processes and products through statistical design of experiments. Reliability engineering and the association with quality. Reliability models of systems. Maintainability, and fault free analysis.', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 747', 'Topics in Manufacturing', 0.5, 'RDG', 'Various courses dealing with recent advances in manufacturing systems.', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 748', 'Topics in Surface Modelling', 0.5, 'LEC', 'None', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 749', 'Special Topics in Machining', 0.5, 'LEC', 'None', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 750', 'Advanced Engineering Thermodynamics', 0.5, 'LEC', 'The assumption of local equilibrium in continuous systems; entropy production; the properties of real gases and their equations of state; statistical thermodynamics and the calculation of perfect gas properties; introduction to Second-Law Analysis; the principle of entropy maximization and the Janes-Tribus formalism.', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 751', 'Fuel Cell Technology', 0.5, 'LEC', 'Introduction to the principle and operation of various types of fuel cells (such as alkaline, proton exchange membrane, phosphoric acid, molten carbonate, solid oxide, and direct methanol fuel cells). Configuration of individual cell, stack and fuel cell system. Overview of fuel cell technology. Thermodynamics of fuel cells. Introduction to electrochemical kinetics. Transport-related phenomena and conservation equations for reacting multi-component systems. Fuel cell system design, optimization and economics. Fuel cell performance modelling. Challenges of fuel cell commercialization. (Also offered Online)', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 753', 'Solar Energy', 0.5, 'LEC', 'Terrestrial and extra-terrestrial solar radiation; radiative and optical properties of materials; basic and advanced flat plate solar thermal converters, focussing converters, solar-electric converters, solar photovoltaic cells, thermal storage; applications to building heating and cooling systems, industrial heat and central electric plants. (Also offered Online)', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 755', 'Advanced Differential Equations and Special Functions', 0.5, 'LEC', 'General linear second order ordinary differential equations. Hypergeometric functions, confluent hypergeometric functions. Legendre and Bessel functions. Orthogonality, generating functions, asymptotic expansions, integral relations. Hermite, Legendre, Laquerre and other orthogonal polynomials. Advanced Fourier series; Laplace, Fourier and other integral transforms. Problems from several areas of engineering.', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 756', 'Combustion 2', 0.5, 'LEC', 'The equations of flowing, reacting mixtures; estimation of transport properties; elements of heat transfer from flames: radiation and convection; applied chemical kinetics of combustion: effects of pressure, temperature and surfaces; explosions; kinetics of pollutant formation in flames: flame inhibition chemistry; coupled processes: ignition, flame spreading, staged reactions; diffusion flames: flame aerodynamics with turbulence, buoyancy, and swirl; furnace and burner combustion: sprays, jets, stability, noice; mass fires; behaviour, properties.', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 758', 'Thermal Contact Resistance', 0.5, 'LEC, RDG', 'Theory and application of thermal contact resistance. Parameters influencing contact resistance. Metrology of machined surfaces and their geometric interaction. Mechanical interaction of machined surfaces. Review of elasticity and plasticity theories. Discussion of modified Hertzian theory, including the effect of surface roughness. Thermal constriction resistance theories of circular, rectangular, elliptic, linear strip and annular strip contact areas. Theory of heat flux tubes. Superposition of microscopic and macroscopic resistance. Contacts in vacuum. Effect of interstitial fluids. Effect of metallic and non-metallic substances. Application of theory to industrial problems and well-defined systems such as bearings and powder substances.', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 759', 'Advanced Experimental Methods in Thermal and Fluids Engineering', 0.5, 'LEC', 'Design of experiments, error analysis, thermometry, flow visualization, anemometry, barometry, gas chromatography, radiation spectroscopy, mass spectroscopy, photography and thermal radiation flux measurement. Application of these methods to measurements in reacting and non-reacting fluids.', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 760', 'Special Topics in Thermal Engineering', 0.5, 'LEC, TUT', 'None', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 761', 'Fluid Dynamic Design of Turbomachines', 0.5, 'LEC', 'Basic equations in stationary and rotating coordinate systems, forms suitable for axial flow and centrifugal flow machinery. Analysis and experimental characteristics of two-dimensional cascades, analysis of circular cascades. Effects of turbulence. Axisymmetric and general three-dimensional flows in diffusers, inlets, volutes and blade passages.', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 762', 'Turbulent Diffusion in the Natural Environment', 0.5, 'LEC', 'Statistical quantities of interest in turbulent diffusion; Eulerian and Lagrangian probability distributions, averages, correlations, spectra. Specific prediction models for atmospheric and oceanic mixing processes, diffusion in a homogeneous field in a boundary layer. Effects of density stratification, buoyant movements.', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 765', 'Special Topics in Fluid Mechanics', 0.5, 'LEC', 'None', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 770', 'Special Topics in Numerical Methods, Fluid Flow and Heat Transfer', 0.5, 'LEC', 'Various courses dealing with numerical methods of predicting the fluid flow, heat transfer and chemical reaction in engineering equipment, in the human body, and in the environment. The methods usually involve the solution of partial differential equations of the parabolic, elliptic and hyperbolic type.', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 780', 'Special Topics in Mechatronics', 0.5, 'LEC, RDG', 'None', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MEDVL 105', 'Introduction to Medieval Studies', 0.5, 'LEC', 'An introduction to Medieval European civilization focusing on essential aspects of the discipline: history, literature, philosophy, religion, art, architecture and archaeology, law, and science and technology.', 'MEDVL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MEDVL 115', 'Crusading in the Middle Ages', 0.5, 'DIS, LEC', 'This course examines the historical events and cultural assumptions that led to the European phenomenon of crusading, or holy war, between 1095 and 1453.', 'MEDVL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MEDVL 205', 'Medieval Society', 0.5, 'LEC', 'A survey of Medieval civilization featuring such topics as the individual (male and female), political institutions, art, architecture, religion, philosophy, literature, social life, and leisure activities.', 'MEDVL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MEDVL 260', 'Europe: 410-1303', 0.5, 'DIS, LEC', 'The political, cultural, economic, and ecclesiastical development of Europe from the fall of the Roman Empire to the end of the High Middle Ages.', 'MEDVL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MEDVL 304', 'Heresy and Religious Crises in Late Medieval Europe', 0.5, 'DIS, LEC', 'An exploration of the impact of social crises on late medieval religious modes of expression. Topics will include the Great Famine, the Black Death, the Avignon Papacy and Western Schism, the development of heretical movements, and the eventual disintegration of European religious unity.', 'MEDVL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MNS 101', 'Materials and Nanosciences in the Modern World', 0.5, 'LEC', 'Overview of materials, including physical and chemical classification of materials, and structure-property relationships; survey of emerging fields in materials and nanoscience research such as: nanotechnology, quantum materials and devices, bionic research; societal impacts of materials and nanoscience research. [Offered: F]', 'MNS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MNS 102', 'Techniques for Materials and Nanosciences', 0.5, 'LEC', 'Overview of materials synthesis, including both wet chemical and dry physical based methodologies; basic metrology and materials characterization of surface and bulk properties; introduction to the design, fabrication, and evaluation of simple devices; survey of emerging new techniques in materials and nanosciences.
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MNS 201L', 'Materials and Nanosciences Laboratory', 0.5, 'LAB', 'The laboratory course is aimed at students enrolled in the MNS program and is composed of experiments involving the syntheses of materials and nano-particles; in addition to their characterization and application using modern instrumentation techniques. [Offered: W, S]', 'MNS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MNS 211', 'Chemistry and the Solid State', 0.5, 'LEC', 'Crystal structures and bonding in solids; introduction to diffraction, solid state synthesis, and thermal analysis techniques employed in the study of materials; defects, non-stoichiometry, and solid solutions; phase diagrams for solid systems. [Offered: F]', 'MNS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MNS 221', 'Physics and the Solid State', 0.5, 'LEC', 'Physical properties of crystalline and amorphous solids; introduction to electric, magnetic and optical properties of solids. [Offered: W, S]', 'MNS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MNS 321', 'Electrical and Optical Properties of Materials', 0.5, 'LEC', 'Optical properties of and electrical transport in materials; topics may include insulators, semiconductors, metals, superconductors and their applications in photovoltaics, thermoelectrics, and nanocomposites.[Offered: S, F]', 'MNS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MNS 322', 'Polymer Materials', 0.5, 'LEC', 'Basic definitions and polymer nomenclature, chain conformations, phase behaviour of polymer systems, mechanical properties, polymerization, self-assembly, morphologies. [Offered W]', 'MNS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MNS 331', 'Biomaterials', 0.5, 'LEC', 'Mechanical, chemical and biological properties of biomaterials; relationships between biomaterials and their properties, functions, manufacture, and applications; applications of biomaterials in technologies, such as tissue engineering, prosthesis manufacture, drug delivery systems, bioreactors. [Offered: W]', 'MNS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MSCI 100', 'Management Engineering Concepts', 0.75, 'LAB, LEC, TUT', 'An introduction to the practice of management engineering, including topics such as criteria based decision making with a focus on efficiency and effectiveness; cause and effect analysis, and an engineering approach to problem solving; flow and process analysis; teamwork and project management; decision-making tools; and written and verbal communication. Engineering design methods are also introduced, including a design project with small groups, as are aspects of the engineering profession including topics such as ethics, safety, and legal liability. Professional development for preparation for co-op terms is also included. [Offered: F]', 'MSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MSCI 100B', 'Seminar', 0, 'SEM', 'General Seminar. [Offered: W]', 'MSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MSCI 121', 'Introduction to Computer Programming', 0.5, 'LEC, TUT', 'An introduction to computer programming using a high level programming language. Concepts and topics covered include the basic components of algorithms (primitive operations, variables, sequencing operations, conditionals/branching, repetition/loops, and subroutines/functions), problem decomposition, abstraction, testing and debugging, pseudo-code, file based input and output, use of a modern development environment including a symbolic debugger, good coding style, pointers/references, and basic data structures (arrays, records, objects). [Offered: W]', 'MSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MSCI 131', 'Work Design and Facilities Planning', 0.5, 'LAB, LEC, TUT', 'The course introduces fundamental concepts in two main areas: The first is work analysis and design where work methods design, motion and time study, and work sampling are covered. The second covers basic concepts in facilities planning such as process analysis, flow design, facility location and layout, and material handling systems. Students will apply these concepts in design activities in labs and projects. [Offered: W]', 'MSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MSCI 200A', 'Seminar', 0, 'LEC', 'General seminar. [Offered: F]', 'MSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MSCI 200B', 'Seminar', 0, 'LEC', 'General seminar. [Offered: S]', 'MSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MSCI 211', 'Organizational Behaviour', 0.5, 'LEC', 'Introduction to the concepts of learning, person perception, attitudes and motivation in an organization. Consideration of communication, roles, norms and decision making within a group. Discussion of power, control, leadership and management in light of the above concepts. [Offered: F, W, S]', 'MSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MSCI 240', 'Algorithms and Data Structures', 0.5, 'LEC, TUT', 'Design and analysis of data structures and algorithms with an emphasis on further development of computer programming skills. Topics include algorithms for searching, sorting, stacks, queues, trees, and graphs. Comparison of algorithms on different data structures. Solutions to common engineering problems in computer science using algorithms and data structures. Introduction to mathematical analysis of space and time complexity with a focus on designing solutions that can scale to large input sizes. [Offered: F]', 'MSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MSCI 252', 'Probability and Statistics for Engineers', 0.5, 'LEC, TUT', 'This course introduces fundamental concepts in probability and statistics. It covers topics in probability, random variables, discrete and continuous probability distributions, confidence intervals, hypothesis testing, sampling, and introductory linear regression. Students are exposed to software packages that enable statistical analysis. [Offered: S, last offered Spring 2019]', 'MSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MSCI 261', 'Engineering Economics: Financial Management for Engineers', 0.5, 'LEC, TST, TUT', 'Introductory Finance: time value of money, cash flow analysis. Investment evaluation methods: present worth, annual worth and internal rate of return. Depreciation models and asset replacement analysis. The impact of inflation, taxation, uncertainty and risk on investment decisions. [Offered: F,W,S]', 'MSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MSCI 262', 'Managerial and Cost Accounting', 0.5, 'LEC, TUT', 'This course provides students with an understanding of how costs are calculated and allocated within an organization. The focus is on developing an understanding of how all aspects of engineering impact the cost structure of an organization, and how these costs are analyzed and used in corporate planning and decision making processes. [Offered: F, last offered Fall 2018]', 'MSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MSCI 263', 'Managerial Economics', 0.5, 'LEC, TUT', 'This course introduces students to key concepts in microeconomics, with an emphasis on applications to managerial decision-making. Topics include: basic analysis of supply and demand, demand functions and the theory of consumer behaviour, production and costs, market equilibrium, competition between industry participants, and pricing strategies of firms under different market structures. [Offered: S]', 'MSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MSCI 271', 'Advanced Calculus and Numerical Methods', 0.5, 'LEC, TUT', 'This course introduces students to first and second order ordinary differential equations, vector calculus, and numerical methods for solution of systems of equations, and ordinary differential equations. Applications in management engineering are emphasized. [Offered: F]', 'MSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MSCI 300A', 'Seminar', 0, 'SEM', 'General Seminar. [Offered: W]', 'MSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MSCI 300B', 'Seminar', 0, 'SEM', 'General Seminar. [Offered: F]', 'MSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MSCI 311', 'Organizational Design and Technology', 0.5, 'LEC', 'The focus of this course is on the procedures and variables involved in the design and redesign of organizations. Issues such as departmentation, differentiation, integration, internal politics, innovation, authority and control are discussed in the context of the underlying technology of the organization. Emphasis will be placed on how one designs both the technical and the organizational systems to ensure their compatibility, noting the effects that one has on the other. [Offered: F, W]', 'MSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MSCI 331', 'Introduction to Optimization', 0.5, 'LEC, TUT', 'This first course in optimization uses a quantitative approach to problem solving involving, mathematical modelling and formulations, solution methods, and output analysis. Students are introduced to a variety of practical problem formulations in Management and Engineering, a number of solution methods, including, but not limited to linear optimization, network models, project management, and decision analysis. Students are also involved in a group project, where they go through conceptual and operational model design, analytical solution, output analysis, and recommendation. [Offered: F, W, S]', 'MSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MSCI 332', 'Deterministic Optimization Models and Methods', 0.5, 'LEC, TUT', 'This course builds on the material presented in MSCI 331, and explores more advanced optimization techniques and applications. Methods, such as integer optimization, dynamic programming, and heuristics are introduced, and used to design solution alternatives for applications from management engineering. This may include network and process design in logistics, transportation, telecommunications, and healthcare. [Offered: F]', 'MSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MSCI 333', 'Simulation Analysis and Design', 0.5, 'LAB, LEC, TUT', 'This course introduces the use of discrete event simulation as an approach for understanding and analyzing complex management systems. Topics include an introduction to simulation modeling, general purpose and special purpose simulation languages, designing valid and credible simulation models, input data analysis, output analysis and experimental design. [Offered: F]', 'MSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MSCI 334', 'Operations Planning and Inventory Control', 0.5, 'LAB, LEC, TUT', 'This course exposes students to production planning and inventory control approaches in industrial and service systems. Production planning topics cover capacity and resources planning, production scheduling, manufacturing resource planning, Just-In-Time and lean manufacturing. Inventory control topics cover lot sizing policies, deterministic and stochastic inventory policies. The course involves a design project of a production and/or inventory system. [Offered: W]', 'MSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MSCI 342', 'Principles of Software Engineering', 0.5, 'LAB, LEC, TUT', 'The purpose of this course is to study methods necessary to cost-effectively address difficult problems arising in the development, management and evolution of software systems.Topics include requirements engineering and analysis; different methods for software design; techniques for building dependable software systems; verification and validation of systems cost estimation, resource estimation and project management; and maintenance issues. [Offered: F]', 'MSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MSCI 343', 'Human-Computer Interaction', 0.5, 'LAB, LEC, TUT', 'This course is designed to provide in-depth exposure to the concepts of human-computer interaction and methods of interactive information system design. The course will focus on techniques for building information systems that meet human needs and capabilities by following a system development lifecycle: user requirements analysis, information and interaction design, prototyping and evaluation. [Offered: F]', 'MSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MSCI 346', 'Database Systems', 0.5, 'LAB, LEC, TUT', 'Design and implementation of database solutions to common engineering and management problems. Multiple analytical methods for choosing optimal database designs. Topics include relational database design, data definition, entity modeling, structured query language and emerging types of database systems. [Offered: S, last offered Spring 2019]', 'MSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MSCI 400A', 'Seminar', 0, 'SEM', 'General Seminar. [Offered: S]', 'MSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MSCI 400B', 'Seminar', 0, 'SEM', 'General Seminar. [Offered: W]', 'MSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MSCI 401', 'Management Engineering Design Project 1', 0.5, 'LEC, PRJ', 'This is the first course of a two course sequence to provide students with an opportunity to engage in a significant design experience based on the engineering knowledge and skills gained in previous courses and on cooperative work terms. The instructor will review and extend concepts of project management studied in earlier courses, and students will apply these project management skills. Teams of students will formulate a design problem and submit a preliminary project proposal, make oral presentations for preliminary and interim design reviews, and submit a written interim report describing the proposed design solution. [Offered S]', 'MSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MSCI 402', 'Management Engineering Design Project 2', 0.5, 'LEC, PRJ', 'This is the second course of a two course sequence to provide students with an opportunity to engage in a significant design experience based on the engineering knowledge and skills gained in previous courses and on cooperative work terms. Each student team is required to complete the detailed design for the project defined in MSCI 401, submit a final written report, and make an oral presentation describing their design solution. [Offered: W]', 'MSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MSCI 411', 'Leadership and Influence', 0.5, 'LEC', 'This course will provide students with an overview of how management, psychology, marketing, and related fields have approached the topics of leadership, influence, and power. The first section of the course will address the antecedents of leadership (e.g., How do specific leaders emerge? What are the qualities of good leaders?). The second section of the course will address the act of leadership (e.g., How do leaders influence their followers? What limitations are inherent to leadership positions?) Specific topics covered include leadership styles, persuasion, social influence, evolutionary perspectives, motivating others, and managing conflict. [Offered: S]', 'MSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MSCI 421', 'Strategic Management of Technology', 0.5, 'LEC', 'This course covers (a) the competitive strategy that a firm uses in its product markets, and (b) the firm''s organizational strategy, i.e., how the firm organizes to meet its objectives. The course will be taught from an economic perspective, but findings from other social sciences will also be presented along with their impact on the strategy-making and implementation process. A special focus will be placed on technology firms. [Offered: S]', 'MSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MSCI 422', 'Economic Impact of Technological Change and Entrepreneurship', 0.5, 'LEC', 'This course is designed to analyze the impact of technological change and entrepreneurship at a firm and societal level, primarily in terms of the economic antecedents and consequences of new technology. The scope of the course ranges from the study of the determination of productivity and its effect on economic growth to the determination of innovative activity and performance. Prereq: (One of CIVE 292/392, ECON 101, ENVE 292/392, MSCI 261, SYDE 262) and (One of CHE 220, CIVE 224, ECE 316, ECON 221, ENVE 224, ENVS 278, KIN 222, MSCI 252 or 253, ME 202, MTE 201, NE 115/215, PSCI 314, PSYCH 292, REC 371, SDS 250R, SOC 280, STAT 202, 206, 211, 221, 231, 241, SYDE 212) and level at least 3A. [Offered: F]', 'MSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MSCI 423', 'Managing New Product and Process Innovation', 0.5, 'LEC, TUT', 'This course examines technical and organizational aspects of managing new product and process innovation. Topics include human creativity and problem solving, product design and development, product feasibility assessment, requirements engineering, managing research and development, project management, team communication, technology implementation, and innovation strategy. [Offered: W]', 'MSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MSCI 431', 'Stochastic Models and Methods', 0.5, 'LEC, TUT', 'Introduction to Operations Research models and methods for problems with random, stochastic and probabilistic components. Topics include birth and death processes, branching processes, waiting line models, and Markov decision processes. Applications include, the design, modelling, and analysis of service and manufacturing systems, with emphasis on important functions such as queueing, inventory, reliability, equipment replacement, and maintenance. [Offered: W]', 'MSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MSCI 432', 'Production and Service Operations Management', 0.5, 'LEC, TUT', 'Introduction to management, planning, and control decisions in manufacturing and service settings using quantitative approaches. Topic areas include production, inventory, distribution, quality control, facilities layout, and process design. Students are exposed to a number of examples and case studies, and work on a project that involves analysis and discussion of improved designs. [Offered: F, W]', 'MSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MSCI 433', 'Applications of Management Engineering', 0.5, 'LEC, TUT', 'This course exposes students to a variety of application areas in management engineering and introduces to them the challenges inherent in implementing new management engineering systems. Topics will be chosen from areas such as: manufacturing, services, logistics, finance, healthcare and engineering. [Offered: W]', 'MSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MSCI 434', 'Supply Chain Management', 0.5, 'LAB, LEC, TUT', 'This course focuses on the efficient use of material, information, physical and human capital resources in supply-demand networks consisting of suppliers, manufacturers, distributors, retailers and customers. It emphasizes analytic tools used to design, implement and sustain competitive supply chain systems. The material will highlight application of supply chain practices in industry and supply chain implementation challenges. Issues associated with international or global supply chains will be discussed.
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MSCI 435', 'Advanced Optimization Techniques', 0.5, 'LEC, TUT', 'This course covers more advanced topics in optimization that go beyond the contents of MSCI 331 and MSCI 332. The course will cover topics such as constraint programming, stochastic programming, large scale optimization, or complementarity problems. [Offered: W]', 'MSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MSCI 436', 'Decision Support Systems', 0.5, 'LEC, TUT', 'This course provides an introduction to analysis, design and implementation of decision support systems for engineering and business applications. Operations research modeling techniques and software are integrated with database systems and computer interfaces to create systems that aid managerial decision-making. This course also will discuss challenges in designing and implementing decision support systems based on models drawn from Operations Research. [Offered: W]', 'MSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MSCI 442', 'Impact of Information Systems on Organizations and Society', 0.5, 'LEC', 'This course is designed to familiarize the student with issues related to the impact of computer-based technologies on individual jobs, organizations, and broader societal level. Particular emphasis will be placed on critical examination of various issues including privacy, security, ethical concern and professional responsibilities. [Offered: W]', 'MSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MSCI 444', 'Information Systems Analysis and Design', 0.5, 'LEC, TUT', 'The course is intended to provide students with the knowledge of the theory and practice of information systems development stages, techniques and methodologies. Course topics may include: requirements analysis, structured and object-oriented design techniques and system implementation strategies. [Offered: W]', 'MSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MSCI 445', 'Telecommunication Systems: from protocols to applications', 0.5, 'LAB, LEC, TUT', 'An introduction to design, use and analysis of computer networks and telecommunication systems with a focus on technological issues arising in the rapidly developing field of telecommunications and information technology. The emphasis is on what engineers need to know about telecommunication to make sound business decisions, and utilize networks in software applications. The material will be taught using a top-down approach. Topics include: systems, security, applications, evolution of the field, performance, and technology.
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MSCI 446', 'Data Warehousing and Mining', 0.5, 'LEC, TUT', 'This course will present state-of-the-art practice and research in the storage, extraction, manipulation and analysis of data, with a view to using these processes for making better management decisions. Topics include: extracting, cleaning, and organizing data from transactional databases, discovering and validating patterns and relationships using statistical techniques, and using the extracted patterns for making improved management decisions. [Offered: F]', 'MSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MSCI 453', 'Business Processes and Information Technology', 0.5, 'LEC, TUT', 'This course discusses how information systems support the execution and management of internal and inter-organizational activities from the perspective of critical business processes. Topics include the information and security requirements necessary to support: the internal "order-to-cash", "procure-to-pay", and "plan-to-produce" processes, as well as inter-organizational business processes such as: Collaborative Planning Forecasting and Replenishment (CPFR) and e-Procurement. Requirements and success factors for inter-organizational business relationships are also addressed as they relate to information and process sharing. [Offered: S, first offered Spring 2011]', 'MSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MSCI 452', 'Decision Making Under Uncertainty', 0.5, 'LEC, TUT', 'This course deals with normative, descriptive, and prescriptive theories of decision making under uncertainty. It begins with analytical models such as decision trees, Bayes Theorem and Bayesian revision, value of information, basic utility theory and multi-attribute decision making. The course continues with an examination of how these theories can fail to predict actual decision making behavior. This course applies the concepts of decision-making to managerial and consumer behavior as well as behavior in negotiations. [Offered: S]', 'MSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MSCI 454', 'Technical Entrepreneurship', 0.5, 'LEC', 'Technical entrepreneurship is examined considering the role of independent business, entrepreneurial behaviour, types of business and enterprises, business structure, sources of venture concepts and capital, company operation and control, and business start-up. [Offered: W]', 'MSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MSCI 531', 'Stochastic Processes and Decision Making', 0.5, 'LEC, TUT', 'This course builds on the material covered in MSCI 431. Students will learn how to construct and analyze a range of stochastic decision models that are useful in the design and control of a wide variety of systems. Applications areas include the design of inventory control systems, telecommunication networks, healthcare polices, manufacturing control and reliability analysis. Topics include Renewal-Reward Processes, Markov Decision Processes, Matrix Analytic Methods with application areas noted above. [Offered: S]', 'MSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MSCI 541', 'Search Engines', 0.5, 'LEC, TUT', 'This course provides an opportunity for students to learn the engineering behind search engines and how to optimize search engines to provide higher quality user experiences. This course focuses on text retrieval and web search. Topics include design and construction of retrieval systems, retrieval models, and evaluation of search engines. [Offered: F, W, first offered Fall 2021, offered Winter until 2022]', 'MSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MSCI 551', 'Quality Management and Control', 0.5, 'LEC, TUT', 'The course focuses on the analysis, evaluation, and improvement of quality based on statistical tools. Topics include process capability analysis, statistical process control, experimental design and the Taguchi method, and acceptance sampling. The emphasis is on the assessment of quality and the design of alternate processes and/or quality assessment schemes to improve quality. [Offered: S]', 'MSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MSCI 555', 'Scheduling: Theory and Practice', 0.5, 'LEC, TUT', 'Scheduling is the sequencing of tasks to scarce resources. By exploring scheduling problems found in industry, this course will discuss scheduling framework and notation as well as algorithmic, heuristic, and mathematical programming solution approaches. Students will be introduced to the theoretical background in these areas, but the emphasis will be placed on modeling and solving scheduling problems in practice. Students will apply these concepts in design activities in assignments and a course project. [Offered: W]', 'MSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MSCI 597', 'Complementary Studies Topics in Management Sciences', 0.5, 'LEC, TUT', 'A complementary studies course on advanced topics in Management Sciences will be offered when resources are available. For current offerings, see the Future Term Course Offerings list or the Schedule of Classes. [Offered: F, W, S]', 'MSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MSCI 598', 'Special Topics in Management Engineering', 0.5, 'LEC, TUT', 'A course on advanced topics in management engineering will be offered when resources are available. For current offerings, see the future term course offerings list or the schedule of classes. [Offered: F, W, S]', 'MSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MSCI 599', 'Special Topics in Management Engineering Design', 0.5, 'LEC, TUT', 'A course on advanced topics in management engineering with emphasis on design will be offered when resources are available. For current topic offerings, see the future term course offerings list or the schedule of classes. [Offered: F, W, S]', 'MSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MSCI 601', 'Research Methods in the Management Sciences', 0.5, 'LEC', 'This course focuses on methods used in empirical research in the management sciences. It encompasses: stages in the research process, theory building, problem definition, research strategies and designs, measurement issues, sampling, ethical concerns, data analysis, and the communication of research results. These issues will be examined in published research and student proposals.  Priority may be given to Management Sciences students.', 'MSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MSCI 602', 'Strategic Management of Technological Innovation', 0.5, 'LEC', 'This course includes: integrating technology and business strategy; design and evolution of technology strategy; development of the firm''s innovative capabilities; creating and implementing systems for innovation; innovation challenges in established firms. In addition to a textbook, cases are used to add realism and context to this course.  Priority may be given to Management Sciences students.', 'MSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MSCI 603', 'Principles of Operations Research', 0.5, 'LEC', 'This course surveys a spectrum of models and techniques in Operations Research, with emphasis on applications. It focuses on the development of modeling skills, the interpretation of results, sensitivity analysis and computer implementations of decision support systems. Topics include linear, integer and network optimization models. Simulation analysis and other topics in stochastic processes may also be covered. The use of quantitative models in different levels of the decision making hierarchy are illustrated through case studies and readings from the Management Sciences literature.  Priority may be given to Management Sciences students.', 'MSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MSCI 605', 'Organizational Theory & Behaviour', 0.5, 'LEC', 'Individuals in organizations; including personality perceptions, learning, and motivation; group processes, including work groups, leadership, communication, decision making, and the management of organizational change. Organizational theory, research and design; including overview of theories such as bureaucracy, open systems, organizational ecology. Issues of departmentation, differentiation, integration, power and politics; governance structures including agency and stakeholder theories. Interorganizational networks and organizational sets.  Priority may be given to Management Sciences students.', 'MSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MSCI 606', 'Foundations of Senior Management', 0.5, 'LEC', 'The course provides a comprehensive background in the management principles needed to effectively enhance interpersonal skills, evaluate organizational performance and apply organizational functioning. The subject areas that make up this course, in order to provide this broad background, include: Financial Accounting, Management Accounting, Organizational Behaviour, Management, Information Systems, Production and Operations Management, and Marketing. Key objectives of the course include (1) to enable confident interactions with functional specialists, (2) to provide tools for the evaluation of organizational performance, and (3) to enhance appreciation for the contributions, and applications of management theories to organizations.  Priority may be given to Management Sciences students.', 'MSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MSCI 607', 'Applied Economics for Management', 0.5, 'LEC', 'This course will examine economic concepts that are useful to management, from decisions about proposed investments in process changes or product modifications, to strategic considerations for the firm.  Topics will include: techniques for financial evaluation of proposed investments; incorporating risk into evaluations; basic concepts from game theory; theory of demand; production functions; cost functions; competitive markets; other market structures.  Priority may be given to Management Sciences students.', 'MSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MSCI 609', 'Quantitative Data Analysis for Management Sciences', 0.5, 'LEC', 'Descriptive statistics and probability concepts; discrete and continuous probability distributions; sampling methods and the central limit theorem; estimation and confidence intervals; inference and hypothesis testing; linear regression and correlation; multiple regression and model building.', 'MSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MSCI 620', 'Organizations & Technical Systems', 0.5, 'LEC', 'This research seminar concentrates on a macro view of organizations as dynamic systems. It emphasizes the principles of effective management of organizations and technology. Further, the course examines the conceptual foundations of organizational theory and design.  Priority may be given to Management Sciences students.', 'MSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MSCI 631', 'Probabilistic  Models in Operations Research', 0.5, 'LEC', 'The goal of this course is to enable students to think probabilistically. The modelling and analysis of uncertain systems in operations research is emphasized. Tools include renewal theory, Markov processes and queuing analysis, while application areas include production and inventory control, health-management, transportation, and other problems in probabilistic operations research.  Priority may be given to Management Sciences students.', 'MSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MSCI 632', 'Discrete Event Simulation', 0.5, 'LEC', 'This course provides an introduction to Discrete Event Simulation. Topics covered include applications of discrete event simulation, simulation languages, data collection and input analysis, random number generation, validation, output analysis for a single system, comparison of several systems, variance reduction techniques and experimental design. Through a project, students will acquire the skills necessary to define a problem, develop and validate a conceptual and computer model, analyse the problem, and make recommendations.  Priority may be given to Management Sciences students.', 'MSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MSCI 633', 'Production and Inventory Management', 0.5, 'LEC', 'The course emphasizes inventory control models with deterministic and stochastic demand, and supply chain management. Other areas which might be addressed are aggregate planning, machine scheduling, material requirements planning, and multi-echelon production and distribution systems.  Priority may be given to Management Sciences students.', 'MSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MSCI 638', 'Information Systems Analysis and Design', 0.5, 'LEC', 'This course presents the foundations which underlie the development of management information systems. Emphasis is placed on the concepts, strategies, techniques and tools for identifying and specifying information systems requirements, and developing designs. Students are expected to complete an analysis and design project.  Priority may be given to Management Sciences students.', 'MSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MSCI 646', 'Database Management Systems', 0.5, 'LEC', 'The course is concerned with the design, management and use of databases.  The course combines theoretical foundations of database organization and database design methodologies with the practical aspects of developing, implementing and managing databases.  Some topics include: relational database design, entity modelling, SQL, and comparison of different types of databases.', 'MSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MSCI 651', 'International Project Management', 0.5, 'LEC', 'This course will provide students with an understanding of Project Management Fundamentals while allowing them to gain practical experience of International Project Management and Development in a live practical project.  Topics covered include: Project Management Methodologies, Project Manager''s Role, Managing Project Stakeholders, Scope, Quality and teams; Project Risk Management, Procurement, Leadership, and Ethics.', 'MSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MSCI 652', 'International Business Management', 0.5, 'LEC', 'Companies today confront an increasing array of choices regarding markets, locations for key activities, organizational structures, and processes for managing across borders.  This course provides students with the conceptual tools necessary to understand and work effectively in today''s interconnected world by developing strategic perspectives that link the global environment and the capabilities and position of the firm.  Topics include Globalization, International Strategy Development, Mode of Entry and Alliance Structures, Organizational Design, Structure and Control, Human Resources Management, Cross-cultural Issues, Ethics in International Business, International Marketing and Branding, International Operations, Exporting and Importing, and Global Corporate Sustainability.', 'MSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MSCI 700', 'Topics in Operations Research and Management', 0.5, 'RDG', 'Priority may be given to Management Sciences students.', 'MSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MSCI 702', 'Linear Programming and Extensions', 0.5, 'LEC', 'Introduction to selected applications of LP. The simplex method. Duality and sensitivity analysis. Computer solutions to LP. Network flow problems with applications and algorithms. Special topics such as revised simplex method, primal-dual method, decomposition principle, generalized upperbounding and ellipsoid methods. Priority may be given to Management Sciences students.', 'MSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MSCI 703', 'Applied Optimization', 0.5, 'LEC', 'Nonlinear and combinatorial optimization problems with roughly equal emphasison model formulation and solution techniques. Modelling emphasis is primarily on deterministic formulation of management problems such as: inventory problems, equipment replacement, capital budgeting and production-inventory optimization. Selected techniques for each problem type are discussed.  Priority may be given to Management Sciences students.', 'MSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MSCI 709', 'Logistics and Supply Chain Management', 0.5, 'LEC', 'Modern supply chain management encompasses the logistics of inventory and transportation flows, whether within a given organization or between that firm and other companies (suppliers, customers) that are part of its business.  This course thus deal with models and analyses of the inbound transportation of raw materials, manufactured components and sub-assemblies.  Another emphasis is the (outbound) physical distribution of finished goods from factory to consumer: freight transportation (various modes, customer service, multi-location inventory management and distribution-centre site selection. Specialized topics (for term projects) may be chosen from among Logistics Information Systems; Global Supply Chain Management; Vehicle Routing; or the Logistics of e-Commerce.  Priority may be given to Management Sciences students.', 'MSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MSCI 712', 'Decision Analysis Under Uncertainty', 0.5, 'LEC', 'The course goal is to understand, model and improve decision making under uncertainty and complexity. Bayesian principles and expected utility theory are combined, their underlying assumptions critically reviewed, and alternative theories surveyed. Other issues addressed include decision with multiple attributes.  Priority may be given to Management Sciences students.', 'MSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MSCI 720', 'Topics in Information and Information Systems', 0.5, 'LEC, RDG', 'Priority may be given to Management Sciences students.', 'MSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MSCI 721', 'Knowledge Engineering for Management', 0.5, 'RDG', 'This course provides the conceptual foundations for understanding knowledge management and the relevant techniques for build knowledge-based systems (KBS or expert systems).  Topics include an overview of applications of knowledge management, artificial intelligence, rule-based systems and other common knowledge representation techiniques, knowledge acquistion methods, application of KBS in management.  Students will practice designing KBS.  Priority may be given to Management Sciences students.', 'MSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MSCI 730', 'Human Computer Interaction', 0.5, 'LEC', 'This course concentrates on the theoretical and practical issues related to the design of human-computer interfaces. Aspects of human perception, cognition and various models of task analysis are discussed. Further, the course examines the principles of interface design and the related empirical evidence.  Priority may be given to Management Sciences students.', 'MSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MSCI 740', 'Topics in Management of Technology', 0.5, 'RDG', 'Priority may be given to Management Sciences students.', 'MSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MSCI 765', 'Statistical Quality Control', 0.5, 'LEC', 'This course is designed to provide the student with the statistical foundations of quality control and improvement.  Topics include: inferential statistics graphical method of data presentation, statistical process control including the use of control charts; acceptance sampling procedures, fundamentals of experimental design, and the Taguchi method.  Priority may be given to Management Sciences students.', 'MSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MSCI 767', 'Management of Quality', 0.5, 'LEC', 'This course provides an introduction to quality management by examining the philosophies, practices, tools, and decisions involved in the management of quality.  An applied approach is used in this study.  The course begins with a general overview of quality, including the various ways of defining and measuring quality and the Cost-of-quality model.  This culminates in an introduction to total quality (TOM) and its three cornerstones: continuous improvement, customer focus, and total organizational involvement.  The remainder of the course examines these cornerstones in greater detail.  The objectives of the course are: (1) to develop an appreciation for importance of managing the quality at all levels, and stages of the organization, (2) to introduce techniques for defining, monitoring, and improving quality, and (3) to encourage critical thinking about issues in managing quality, within the organization, and in society in general.  Priority may be given to Management Sciences students.', 'MSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MSCI 741', 'Economics of Technological Change', 0.5, 'LEC', 'This is an applied course in industrial organization economics. It deals with productivity, the relationship between productivity and technological change, the determinants to firms'' investments in research, development and innovation, the diffusion of innovations, entrepreneurship, and technology policy.  Priority may be given to Management Sciences students.', 'MSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MSCI 750', 'Topics in Organizational Analysis and Behaviour', 0.5, 'RDG', 'Priority may be given to Management Sciences students.', 'MSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MSCI 751', 'Knowledge Management', 0.5, 'LEC', 'This course is designed to provide the student with up-to-date understanding of the developments, issues and current research in the topics associated with knowledge as an asset in organizations.  Topics include: knowledge and economic growth and change: the global knowledge-based economy; consequences of the increasing role of the knowledge worker; strategic and organizational roles of intellectual capital; monitoring, and valuing and reporting intellectual capital.   Priority may be given to Management Sciences students.', 'MSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MSCI 753', 'Entrepreneurship and Intrapreneurship: Managing New Technology-based Firms', 0.5, 'LEC', 'An overview of the process of initiating startups, spin-outs or new ventures within an existing company. Ventures based on new technologies are a focus.  Topics include extrpreneurship and organizational culture, opportunity identification and assessment, business plans and new venture diagnostics, protecting intellectual property, finance, marketing and the entrpreneurial team.', 'MSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MSCI 760', 'Topics in Other Areas of Management Sciences', 0.5, 'RDG', 'Priority may be given to Management Sciences students.', 'MSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MSCI 770', 'Special Directed Readings', 0.5, 'RDG', 'Priority may be given to Management Sciences students.', 'MSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MTE 100', 'Mechatronics Engineering', 0.75, 'LAB, LEC, TUT', 'An introduction to mechatronics engineering and the engineering profession. Topics include the design process, project planning, data presentation, measurements and error, control logic, sensors and actuators, and intellectual property. Engineering graphics fundamentals of multi-view, isometric, oblique, and perspective projections are also covered while developing skills in computer-aided drawing (CAD), freehand sketching, and the interpretation of technical drawings. Professional development including r√©sum√© skills, interview skills, and preparation for co-op terms. A mechatronic design project/competition with small groups supplements the lecture material. [Offered: F]', 'MTE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MTE 100B', 'Seminar', 0, 'SEM', 'Discussion of structure of mechatronics engineering curriculum, operation of department, faculty, university, technical societies. [Offered: W,S]', 'MTE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MTE 111', 'Structure and Properties of Materials', 0.5, 'LAB, LEC, PRJ, TST, TUT', 'The relevance of materials to engineering practice; the relationships between macroscopic physical properties (including mechanical, photonic, thermal, electrical, and magnetic properties) and microscopic causes based on fundamental principles (including electronic and atomic structures, atomic bonding, crystal structure, and microstructure); description of the differences in macroscopic physical properties of metals, polymers, ceramics, semiconductors, and composite materials in terms of microscopic causes. [Offered: W,S]', 'MTE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MTE 119', 'Statics', 0.5, 'LEC, TST, TUT', 'Basic concepts of mechanics, vectors. Statics of particles. Rigid bodies and force systems, equilibrium of rigid bodies. Analysis of trusses and frames. Distributed forces, centroids and moments of inertia. Friction. Internal shear and bending moments in beams.[Offered: W,S]', 'MTE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MTE 120', 'Circuits', 0.75, 'LAB, LEC, TST, TUT', 'Basic electromagnetic theory; magnetic circuits; electric circuit elements; DC circuit analysis; first-order transient response; AC circuit analysis; Diodes; Transistors: regions of operation, single-transistor amplifiers [Offered: W,S]', 'MTE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MTE 140', 'Algorithms and Data Structures', 0.5, 'LAB, LEC, TST, TUT', 'Algorithms and Data Structures emphasizes the following topics: structured software design data structures, abstract data types, recursive algorithms, algorithm analysis and design, sorting and searching, hashing, and problem-solving strategies. [Offered: W,S]', 'MTE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MTE 200A', 'Seminar', 0, 'SEM', 'Discussion of the structure of and options within the mechatronics engineering curriculum; of the operation of department, faculty, university, technical societies; of student team and graduate school opportunities; of safety training; and of subject material in support of core courses. [Offered: F,W]', 'MTE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MTE 200B', 'Seminar', 0, 'SEM', 'Discussion of the structure of and options within the mechatronics engineering curriculum; of the operation of department, faculty, university, technical societies; of student team and graduate school opportunities; of safety training; and of subject material in support of core courses. [Offered: F,S]', 'MTE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MTE 201', 'Experimental Measurement & Statistical Analysis', 0.5, 'LAB, LEC, TUT', 'Measurement errors, calculations with unknown quantities, and error propagation. Sensitivity analysis. Techniques of sampling and statistical estimation. Introduction to sensors and data acquisition tools. Frequency distributions. Probability. Binomial, Poisson, normal distributions. Tests of hypotheses. Significance. The t-test and chi-squared test. Curve fitting by least squares. Correlation and regression. Design of experiments. [Offered: F,W]', 'MTE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MTE 202', 'Ordinary Differential Equations', 0.5, 'LEC, TUT', 'First Order Differential Equations; Direction Field; Separable Equations, Integrating Factors and Simple Transformations; Applications; Second and Higher Order ODE''s with Constant Coefficients - Transient and Steady State Solutions; The Laplace Transform; Systems of Equations - reduction to single equation; Matrix Differential Equations; Introduction to Partial Differential Equations. [Offered: F,W]', 'MTE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MTE 203', 'Advanced Calculus', 0.5, 'LAB, LEC, TUT', 'Review of Vectors and Vector Operations; 3-D Analytic Geometry and Space Curves; Multivariable Calculus, including Partial Differentiation, Total Differential, Chain Rule, Directional Derivative, Gradient Operator, Maxima and Minima; Multiple Integrals - Surface Area, Volume and Moments of Inertia; Line and Surface Integrals; Vector Theorems; Complex Analysis including Limits, Analytic Functions, Complex Line Integral, Cauchy''s Integral Formula; Fourier Series (real and complex) and Fourier Integrals. [Offered: F,S]', 'MTE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MTE 204', 'Numerical Methods', 0.5, 'LEC, TUT', 'Number Systems and Machine Errors; Roots of Non-Linear Equations; Matrix Calculations; Eigenvalue and Eigenvector Calculations; Interpolation and Approximation; Numerical Integration and Solution of ODE''s (linear and non-linear) and systems of ODEs; Calculation of Series; Solution Methods for PDE''s; Use of numeric and symbolic computing tools. [Offered: F,S]', 'MTE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MTE 219', 'Mechanics of Deformable Solids', 0.5, 'LEC, TUT', 'Introduction to mechanical response of materials and stress-strain relationships. Behaviour of prismatic members in tension, compression, shear, bending and torsion. Stress and strain transformations. Virtual work and energy methods. [Offered F,W]', 'MTE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MTE 220', 'Sensors and Instrumentation', 0.5, 'LAB, LEC, TUT', 'Review of circuit theory; input-output relationships, transfer functions and frequency response of linear systems; operational amplifiers, operational amplifier circuits using negative or positive feedback; diodes, operational amplifier circuits using diodes; analog signal detection, conditioning and conversion systems; transducers and sensors, difference and instrumentation amplifiers, active filters. [Offered: F,S]', 'MTE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MTE 241', 'Introduction to Computer Structures & Real-Time Systems', 0.5, 'LAB, LEC, TUT', 'Introduction to computer organization, basic real-time concepts, process management, interprocess communication and synchronization, memory management, resource management, interrupt handling, concurrent programming, file systems. [Offered: F,S]', 'MTE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MTE 262', 'Introduction to Microprocessors and Digital Logic', 0.5, 'LAB, LEC, TUT', 'Number systems, logic gates, Boolean algebra. Karnaugh maps and combinational logic design. Implementation of combinational logic circuits on Field Programmable Gate Arrays (FPGA) boards. Sequential logic and state machines. Programmable Logic Controllers (PLCs) and PLC programming using ladder logic and statement list. Microcomputer structure and operation, I/O, and interfacing and interrupts. Assembly language programming. Laboratory work includes microcomputer and PLC programming. [Offered: F,W]', 'MTE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MTE 300A', 'Seminar', 0, 'SEM', 'Discussion of the structure of and options within the mechatronics engineering curriculum; of the operation of department, faculty, university, technical societies; of student team and graduate school opportunities; of safety training; and of subject material in support of core courses. [Offered: W,S]', 'MTE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MTE 300B', 'Seminar', 0, 'SEM', 'Discussion of the structure of and options within the mechatronics engineering curriculum; of the operation of department, faculty, university, technical societies; of student team and graduate school opportunities; of safety training; and of subject material in support of core courses. [Offered: F,W]', 'MTE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MTE 320', 'Actuators & Power Electronics', 0.5, 'LAB, LEC, TUT', 'Review of circuit analysis & basic electromagnetic theory. Power electronics: power electronics circuits, H bridges, PWM control, interfacing, power amplifiers. DC servo & stepper motors, AC synchronous & induction motors. Transformers. Introduction to typical speed and torque control techniques of motors. [Offered: W,S]', 'MTE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MTE 322', 'Electromechanical Machine Design', 0.5, 'LAB, LEC, TUT', 'Design of mechanical motion transmission systems: gearing, couplings, belts and lead-screws; Sensing and measurement of mechanical motion, sensor selection; Electromechanical actuator selection and specification; PLCs and sequential controller design, digital I/O; Case studies. [Offered: F,W]', 'MTE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MTE 325', 'Microprocessor Systems and Interfacing for Mechatronics Engineering', 0.5, 'LAB, LEC, TUT', 'Synchronization and data flow; interfacing to sensors and actuators; microprocessor system architecture, parallel, serial, and analog interfacing; buses; direct memory access (DMA); interfacing considerations.', 'MTE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MTE 360', 'Automatic Control Systems', 0.5, 'LAB, LEC, TUT', 'Feedback control design and analysis for linear dynamic systems with emphasis on mechanical engineering applications; transient and frequency response; stability; system performance; control modes; state space techniques; Introduction to digital control systems. [Offered: F,W]', 'MTE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MTE 380', 'Mechatronics Engineering Design Workshop', 0.5, 'LEC, PRJ', 'In this course, students study the design process, including needs analysis, problem definition; design criteria and critical parameter identification, generation of alternative solutions; conceptual design, detailed design, optimization; and implementation. Most of the term is devoted to a significant design project in which student groups work independently and competitively, applying the design process to a project goal set by the faculty coordinator. The design project typically includes construction of an electro-mechanical prototype, and part of the course grade may depend on the performance of the prototype in a competitive test. In exceptional circumstances, the requirement for a prototype may be replaced by a computer simulation, or may be waived. [Offered: F,W]', 'MTE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MTE 400A', 'Seminar', 0, 'SEM', 'Discussion of structure of mechatronics engineering curriculum, operation of department, faculty, university, technical societies. [Offered: F]', 'MTE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MTE 400B', 'Seminar', 0, 'SEM', 'Discussion of structure of mechatronics engineering curriculum, operation of department, faculty, university, technical societies. [Offered: W]', 'MTE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MTE 420', 'Power Electronics and Motor Drives', 0.5, 'LAB, LEC, TUT', 'Analysis, design and control of power electronic converters, principles of operation and control of DC and special-purpose motors, power electronics-based control of DC and special-purpose motors. [Offered: F]', 'MTE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MTE 460', 'Mechatronic System Integration', 0.5, 'LAB, LEC, TUT', 'Mechatronic system interfaces and architecture design. Sensing and actuation in industrial mechatronics systems. Motion control. Industrial computer vision. Networks and communication. PLC based and distributed control. Discrete and hybrid control systems. Fault finding. [Offered: F,W]', 'MTE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MTE 481', 'Mechatronics Engineering Design Project', 0.5, 'PRJ', 'This course is intended to reinforce the concepts learned in MTE 380 and to extend the significant design experience obtained. Students work individually or in small groups applying the principles of engineering design and problem-solving to a design project of their own choosing. The project must incorporate all elements of Mechatronics, namely, mechanical design, electronics, computers and software. In exceptional circumstances, one or more elements may be exempted by the course instructor. The students are required to consider a need analysis, search for prior art and present alternate designs. The course ends with the selection of a final design. Projects are selected, approved, monitored and marked by a course coordinator. [Offered: F]', 'MTE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MTE 482', 'Mechatronics Engineering Project', 0.5, 'PRJ', 'This course is an extension of MTE 481. Students work on prototyping the designs they proposed and finalized in MTE 481. The students either individually or in small groups demonstrate the working prototypes; make a poster presentation for the design competition; and pitch their product on a web site. The projects are monitored by the course instructor and evaluated by the instructor with feedback from an expert judging panel. [Offered: W]', 'MTE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MTE 545', 'Introduction to MEMS Fabrication', 0.5, 'LAB, LEC, TUT', 'Introduction to MEMS. Fabrication processes for MEMS devices. Basic MEMS governing equations in different energy domains (Mechanical, electrical and thermal). Methods for layout, design and modeling of MEMS devices. Simulation techniques. Techniques for testing and characterization of MEMS devices. [Offered: F ]', 'MTE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MTHEL 131', 'Introduction to Actuarial Practice', 0.5, 'LEC, TST', 'Individual life insurance products. Introduction to property and casualty insurance. Introductory risk management, insurance pricing and valuation. Pension plan design. [Offered: F,W,S]', 'MTHEL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MTHEL 198', 'Mathematics Elective Topics 1', 0.5, 'SEM', 'Special Topics course as announced by the department.', 'MTHEL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MTHEL 206A', 'Introduction to Mathematics Education', 0.5, 'LEC', 'Current trends in education, professional practices and administration, the role of the department head, lesson planning, techniques of teaching, evaluation of students, special students, extracurricular activities, the relationship between elementary and secondary school mathematics, audio-visual materials. [Offered: S]', 'MTHEL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MTHEL 298', 'Mathematics Elective Topics 2', 0.5, 'SEM', 'Special Topics course as announced by the department.', 'MTHEL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MTHEL 398', 'Mathematics Elective Topics 3', 0.5, 'LEC', 'Special Topics course as announced by the department.', 'MTHEL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MTHEL 498', 'Mathematics Elective Topics 4', 0.5, 'SEM', 'Special Topics course as announced by the department.', 'MTHEL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MUSIC 100', 'Understanding Music', 0.5, 'LEC', 'The styles, forms, techniques, and terminology of Western music through lectures and listening, as exemplified by great works from all eras of music history.', 'MUSIC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MUSIC 111', 'Fundamentals of Music Theory', 0.5, 'LEC', 'An introduction to the primary skills of music practice emphasizing the reading and writing of musical notation. Students will learn elementary keyboard, listening, and sight-singing skills.', 'MUSIC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MUSIC 116', 'Music Ensemble', 0.25, 'ENS', 'The study of selected music literature through rehearsals and performance in one of the Music Department ensembles: Chamber Choir, Chapel Choir, University Choir, Vocal Techniques, Orchestra, Instrumental Chamber Ensembles, Jazz Ensemble, World Music Ensemble. Regular attendance at rehearsals and performances is required.', 'MUSIC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MUSIC 117', 'Music Ensemble', 0.25, 'ENS', 'The study of selected music literature through rehearsals and performance in one of the Music Department ensembles: Chamber Choir, Chapel Choir, University Choir, Vocal Techniques, Orchestra, Instrumental Chamber Ensembles, Jazz Ensemble, World Music Ensemble. Regular attendance at rehearsals and performances is required.', 'MUSIC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MUSIC 140', 'Popular Music and Culture', 0.5, 'LEC', 'An examination of the styles, forms, and development of 20th century popular music. The social, commercial, and technological aspects of popular music are considered.', 'MUSIC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MUSIC 216', 'Music Ensemble', 0.25, 'ENS', 'The study of selected music literature through rehearsals and performance in one of the Music Department ensembles: Chamber Choir, Chapel Choir, University Choir, Vocal Techniques, Orchestra, Instrumental Chamber Ensembles, Jazz Ensemble, World Music Ensemble. Regular attendance at rehearsals and performances is required.', 'MUSIC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MUSIC 217', 'Music Ensemble', 0.25, 'ENS', 'The study of selected music literature through rehearsals and performance in one of the Music Department ensembles: Chamber Choir, Chapel Choir, University Choir, Vocal Techniques, Orchestra, Instrumental Chamber Ensembles, Jazz Ensemble, World Music Ensemble. Regular attendance at rehearsals and performances is required.', 'MUSIC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MUSIC 222', 'Conducting 1', 0.5, 'LEC', 'A study of conducting techniques appropriate for song leading, choral rehearsal, and public performance. The course will include score analysis and rehearsal procedures for music from a wide variety of historical styles.', 'MUSIC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MUSIC 226', 'Music Studio', 0.5, 'STU', 'Individual instruction in Voice, Piano, Organ, Classical Guitar, and orchestral instruments.', 'MUSIC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MUSIC 227', 'Music Studio', 0.5, 'STU', 'Individual instruction in Voice, Piano, Organ, Classical Guitar, and orchestral instruments.', 'MUSIC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MUSIC 231', 'Music Cognition', 0.5, 'LEC', 'The study of music from a behavioural science perspective. Topics include auditory perception, creativity and aesthetic experience, emotive human responses, and the social psychology of music activities. Recent research in the field of music cognition will be explored in detail.', 'MUSIC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MUSIC 240', 'Introduction to Jazz', 0.5, 'LEC', 'A survey of the development of jazz schools and individual styles as well as a study of melodic, harmonic, and rhythmic improvisation. Styles will be demonstrated through recordings and live performance.', 'MUSIC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MUSIC 245', 'World Music', 0.5, 'LEC', 'A survey of traditional music outside of North America, including Africa, South America, the Far East, the South Pacific, the mid-East and Europe, with special emphasis on the role of music within the culture.', 'MUSIC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MUSIC 246', 'Soundtracks: Music in Film', 0.5, 'LEC', 'An examination of the techniques, styles, and functions of music in film, and how music interacts with image and narrative to create meanings. Weekly screenings of films selected from the 1930s to the present.', 'MUSIC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MUSIC 253', 'Cathedral and Court: Music to 1600', 0.5, 'LEC', 'A study of major developments in western music during the Middle Ages and Renaissance, including Gregorian chant, mass, secular songs, instrumental music, and other musical genres that flourished under courtly and church patronage from the early Christian church to 1600.', 'MUSIC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MUSIC 254', 'Monteverdi to Mozart: Music from 1600-1800', 0.5, 'LEC', 'A study of Baroque and Classical music of the 17th and 18th centuries in cultural, political, and artistic contexts. Topics include the development of opera, choral music, symphony, sonata, and the music of Monteverdi, Bach, Handel, Mozart, and others.', 'MUSIC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MUSIC 255', 'The Romantic Century: Beethoven and Beyond', 0.5, 'LEC', 'A study of music of the 19th century in cultural, political, and artistic contexts. Topics include the development of principal forms in the music of Beethoven, Schubert, Schumann, Liszt, Chopin, Wagner, Verdi, Brahms, Bruckner, and others.', 'MUSIC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MUSIC 256', 'Music Since 1900', 0.5, 'LEC', 'A study of music of the 20th and early 21st centuries in cultural, political, and artistic contexts. Topics include modern and contemporary composers'' challenges to traditional musical ideals in the music of Debussy, Stravinsky, Schoenberg, Cage, and others.', 'MUSIC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MUSIC 260', 'The Symphony', 0.5, 'LEC', 'A survey of the great symphonies from Haydn to Stravinsky, through lectures and listening. A portion of the course will be devoted to works being performed by the Kitchener-Waterloo Symphony Orchestra during the term.', 'MUSIC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MUSIC 261', 'Opera', 0.5, 'LEC', 'An introduction to the themes, sources, forms, and nature of opera through selected works from Monteverdi to Britten. The course explores ways that drama is interpreted through music and considers opera in a broader context of social and artistic issues.', 'MUSIC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MUSIC 270', 'Music Theory 1', 0.5, 'LEC', 'The study of basic melodic, harmonic, and voice leading concepts including an introduction to figured bass and functional harmony. Ear-training, sight-singing, and keyboard lab sessions will be integrated with written and analytical work.', 'MUSIC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MUSIC 271', 'Music Theory 2', 0.5, 'LEC', 'The study of harmony, counterpoint, and form of 18th and early 19th century music. Sight-singing, ear-training, and keyboard lab sessions will be integrated with written and analytical work.', 'MUSIC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MUSIC 275', 'Music and Technology', 0.5, 'LAB, LEC', 'A hands-on introduction to music and technology, including recording, audio editing, digital signal processing, and Musical Instrumental Digital Interface (MIDI). Course work will foster a practical understanding of digital music production software and techniques, and involve electroacoustic or acoustic composition.', 'MUSIC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MUSIC 290', 'Special Topics', 0.5, 'LEC', 'A special study of a selected topic in music. Consult department for details.', 'MUSIC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MUSIC 316', 'Music Ensemble', 0.25, 'ENS', 'The study of selected music literature through rehearsals and performance in one of the Music Department ensembles: Chamber Choir, Chapel Choir, University Choir, Vocal Techniques, Orchestra, Instrumental Chamber Ensembles, Jazz Ensemble, World Music Ensemble. Regular attendance at rehearsals and performances is required.', 'MUSIC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MUSIC 317', 'Music Ensemble', 0.25, 'ENS', 'The study of selected music literature through rehearsals and performance in one of the Music Department ensembles: Chamber Choir, Chapel Choir, University Choir, Vocal Techniques, Orchestra, Instrumental Chamber Ensembles, Jazz Ensemble, World Music Ensemble. Regular attendance at rehearsals and performances is required.', 'MUSIC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MUSIC 322', 'Conducting 2', 0.5, 'LEC', 'A continuation of the study of conducting techniques begun in MUSIC 222. The course focuses on the development of analytical and musical skills including score study, aural skills, and gestural clarity.', 'MUSIC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MUSIC 326', 'Music Studio', 0.5, 'STU', 'Individual instruction in Voice, Piano, Organ, Classical Guitar, and orchestral instruments.', 'MUSIC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MUSIC 327', 'Music Studio', 0.5, 'STU', 'Individual instruction in Voice, Piano, Organ, Classical Guitar, and orchestral instruments.', 'MUSIC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MUSIC 332', 'Aesthetics of Music', 0.5, 'LEC', 'This course employs various methods to explore questions of musical meaning. Through study and discussion of selected sources from antiquity to the present, such themes as imitation, abstraction, beauty, and other debated issues will be examined in historical context and in relation to the other arts.', 'MUSIC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MUSIC 334', 'Women and Music', 0.5, 'LEC', 'An examination of women''s roles in music from a cross-cultural perspective. Topics include women''s music as a genre, historical accounts of women''s music-making, the effects of gender ideology on women''s musical activities, and music behaviour and performance in inter-gender relations.', 'MUSIC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MUSIC 355', 'Music and Culture Travel Course', 0.5, 'FLD', 'A spring term seminar approximately three weeks long, taught on location including daily lectures, attendance at music performances, and tours to places of cultural importance. Past and possible future locations include Vienna, London, Leipzig, South Africa, Morocco, Bali, and Estonia.', 'MUSIC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MUSIC 361', 'Art Song', 0.5, 'LEC', 'A study of the music written for solo voice from the 17th century to the present.', 'MUSIC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MUSIC 362', 'Piano Literature', 0.5, 'LEC', 'A study of music for solo piano from its 17th and 18th century antecedents to the present. Using historical and analytical approaches, individual works will be considered within a tradition of particular forms, genres, and styles.', 'MUSIC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MUSIC 363', 'The Christian Hymn', 0.5, 'LEC', 'The origins of the Christian hymn and its development up to the present. The course considers the hymn as theological, poetic, musical, cultural, and spiritual expression, and the use of hymns in a variety of worship settings.', 'MUSIC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MUSIC 364', 'Worship and its Music', 0.5, 'LEC', 'The nature of worship and the role of music within worship in historical, theological, and cultural perspectives. Field trips to services of various traditions.', 'MUSIC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MUSIC 370', 'Music Theory 3 (19th Century)', 0.5, 'LAB, LEC', 'The study of chromatic harmony as well as melodic and formal aspects of 19th century music. Ear-training, sight-singing, and keyboard lab sessions will be integrated with written and analytical work.', 'MUSIC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MUSIC 371', 'Music Theory 4 (20th Century)', 0.5, 'LAB, LEC', 'The study of the compositional aspects of 20th century music, including extended tonality, atonality, 12-tone writing, neo-classical idioms, and contemporary compositional procedures. Lab sessions will cover non-tonal melodic reading and complex chord structures.', 'MUSIC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MUSIC 376', 'Composition Seminar', 0.5, 'SEM', 'An exploration of the discipline and craft of composing and arranging. Topics include choral, chamber ensemble, and orchestral techniques; electronic music; and charting for popular music. The course includes both score preparation and opportunities for recital performances of original works.', 'MUSIC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MUSIC 380', 'Directed Study in Music', 0.5, 'RDG', 'An independent term project completed under tutorial guidance of a faculty advisor.', 'MUSIC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MUSIC 381', 'Directed Study in Music', 0.5, 'RDG', 'An independent term project completed under tutorial guidance of a faculty advisor.', 'MUSIC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MUSIC 390', 'Special Topics in Music', 0.5, 'SEM', 'A special study of a selected topic in music. Consult department for details.', 'MUSIC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MUSIC 391', 'Special Topics in Music 2', 0.5, 'SEM', 'A special study of a selected topic in music. Consult department for details.', 'MUSIC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MUSIC 426', 'Music Studio', 0.5, 'STU', 'Individual instruction in Voice, Piano, Organ, Classical Guitar, and orchestral instruments.', 'MUSIC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MUSIC 427', 'Music Studio', 0.5, 'STU', 'Individual instruction in Voice, Piano, Organ, Classical Guitar, and orchestral instruments.', 'MUSIC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MUSIC 428', 'Music Studio', 0.5, 'STU', 'Individual instruction in Voice, Piano, Organ, Classical Guitar, and orchestral instruments. Students perform a senior recital.', 'MUSIC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MUSIC 491', 'Senior Research Seminar', 0.5, 'SEM', 'A research seminar for Music majors designed to give students an opportunity to develop research techniques through individual research projects and regular meetings with other students and faculty.', 'MUSIC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MUSIC 492', 'Senior Honours Thesis', 0.5, 'PRJ', 'An independent research project for Honours Music students. The topic is determined through consultation between the student and a faculty advisor.', 'MUSIC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('NANO 701', 'Fundamentals of Nanotechnology', 0.25, 'LEC', 'Students must complete any two of the 0.25 unit weight topics courses listed below.  Each topic courses will typically consist of 18 lecture hours per term.', 'NANO');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('NANO 702', 'Nanotechnology Tools', 0.25, 'LEC', 'Students must complete any two of the 0.25 unit weight topic courses listed below.  Each topic course will typically consist of 18 lecture hours per term.', 'NANO');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('NES 611', 'Ancient Near Eastern Societies', 0.5, 'SEM', 'A comparative exploration of the fundamental aspects of Near Eastern societies over a broad temporal and geographic scope.  Primary sources will be used to analyze social structures, and institutions of the peoples of the ancient Near East including the Levant.', 'NES');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('NATST 370', 'Issues in Contemporary Native Communities in Canada', 0.5, 'LEC', 'Selected aspects of the contemporary native experience as defined by the local native community. The topics examined will be placed in historical perspective. Specially selected course lecturers will be representative of the wider native community.', 'NATST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('NE 100', 'Introduction to Nanotechnology Engineering', 0.5, 'LAB, LEC, TST, TUT', 'An introduction to nanotechnology engineering and its applications. Basic engineering principles and methodology, including the roles of standards, safety, and intellectual property. The purpose, structure, format, and essential components of engineering technical reports. Introduction to the areas of nanomedicine, nanomaterials, and nanoelectronics. Professional development, including r√©sum√© skills, interview skills, and preparation for co-op terms. [Offered: F]', 'NE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('NE 101', 'Nanotechnology Engineering Practice', 0, 'SEM', 'Areas of research and professional practice in nanotechnology engineering; exposure to concepts from other engineering plans; support material for the 1A academic term. [Offered: F]', 'NE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('NE 102', 'Introduction to Nanomaterials Health Risk; Nanotechnology Engineering Practice', 0.15, 'LEC', 'Introduction to types of nanomaterials hazards their identification, toxicity, and characterization; exposure health-risk assessment; cancer and non-cancer risks. Areas of research and professional practice in nanotechnology engineering; exposure to concepts from other engineering plans; support material for the 1B academic term, including aspects of co-operative education and professional or career development.', 'NE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('NE 112', 'Linear Algebra for Nanotechnology Engineers', 0.5, 'LEC, TST, TUT', 'Matrices, operations on matrices. Determinants. Adjoints and inverses. Solution of linear equations: elimination and iterative methods. Eigenvalues and eigenvectors with engineering applications. Complex numbers. [Offered: F]', 'NE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('NE 113', 'Introduction to Computational Methods', 0.5, 'LAB, LEC, TST, TUT', 'Spreadsheets for problem solving, plotting, fitting data. Problem solution plotting, and creating complex programs in an engineering prototypical programming environment. Elementary numerical methods: Taylor-series summations, roots of equations, roots of polynomials, direct and indirect solution methods for systems of linear, and nonlinear algebraic equations, integration. Applications in nanotechnology engineering. [Offered: W]', 'NE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('NE 332', 'Quantum Mechanics', 0.5, 'LEC, TUT', 'Historical background; the differential equation approach to quantum mechanics; treatments of solvable problems such as the particle-in-a-box, harmonic oscillator, rigid rotor, and the hydrogen atom; introduction to approximation methods for more complex systems; application to solid state problems, including band theory. [Offered: S, first offered Spring 2022]', 'NE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('NE 115', 'Probability and Statistics', 0.5, 'LEC, TST, TUT', 'Elementary probability theory. Random variables and distributions. Binomial, Poisson, and normal distributions. Elementary sampling. Statistical estimation. Tests of hypotheses and significance. Regression. Goodness-of-fit tests. [Offered: F]', 'NE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('NE 121', 'Chemical Principles', 0.5, 'LEC, TST, TUT', 'Chemical reactions. Mass and charge balance. Introduction to the first, second, and third laws of thermodynamics. Chemical equilibrium. Applications of chemical equilibrium principles to proton-transfer reactions. Electronic structure of atoms and molecules. Periodicity and chemical bonding. [Offered: F]', 'NE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('NE 140', 'Linear Circuits', 0.5, 'LAB, LEC, TUT', 'Charge, current and voltage. Resistance, Ohm''s Law, Kirchhoff''s voltage and current laws. Nodal, mesh analysis and source transformation. Superposition, Th√©venin and Norton equivalents. Capacitance, inductance, electrical energy dissipation and first-order transient response circuits. Phasors, impedances and alternating current (AC) steady state analysis. Signals, amplifier concepts and nonlinear circuit analysis. Diodes circuit applications. Ideal operational amplifier circuits. Frequency filter types and active filter circuits'' configuration. [Offered: W]', 'NE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('NE 122', 'Organic Chemistry for Nanotechnology Engineers', 0.5, 'LAB, LEC, TST, TUT', 'Nomenclature, stereochemistry and reactions of important classes of organic compounds. Reaction mechanisms and energetics. Aromaticity and simple molecular orbital theory of conjugated systems. [Offered: W]', 'NE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('NE 125', 'Introduction to Materials Science and Engineering', 0.5, 'LEC, TST, TUT', 'Fundamentals of crystalline structure, crystal defects, and noncrystallinity. Structure and properties of metals, ceramics, glasses, amorphous materials, polymers, and composites. Processing and concepts of engineering design of materials. [Offered: W]', 'NE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('NE 131', 'Physics for Nanotechnology Engineering', 0.5, 'LEC, TST, TUT', 'A first course in physics that introduces basic topics in classical mechanics, wave mechanics, and physical optics. [Offered: W]', 'NE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('NE 201', 'Nanotoxicology; Nanotechnology Engineering Practice', 0.15, 'LEC, TST', 'Nanotoxicology, including inhalation and dermal exposure effects; translocation, cytotoxicity, mutagenicity, and neurotoxicity; carbon nanotubes as cancer hazards. Areas of research and professional practice in nanotechnology engineering; exposure to concepts from other engineering plans; support material for the 2A academic term, including aspects of co-operative education and professional or career development.', 'NE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('NE 202', 'Nanomaterials and Environmental Impact; Nanotechnology Engineering Practice', 0.15, 'LEC', 'Environmental fate and behaviour, bio-availability, consumer exposure, environmental exposure-assessment, aquatic toxicology, bio-accumulation and biomagnification. Areas of research and professional practice in nanotechnology engineering; exposure to concepts from other engineering plans; support material for the 2B academic term, including aspects of co-operative education and professional or career development.', 'NE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('NE 216', 'Advanced Calculus and Numerical Methods 1', 0.5, 'LAB, LEC, TST, TUT', 'Ordinary differential equations with constant coefficients. Boundary value problems and applications to quantum mechanics. Laplace and Fourier transforms, Fourier series and applications. Numerical solution of ordinary differential equations. [Offered: F]', 'NE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('NE 217', 'Advanced Calculus and Numerical Methods 2', 0.5, 'LAB, LEC, TST, TUT', 'Gradient, Divergence and Curl: Applications. Line and Surface Integrals. Green''s, Gauss'', and Stokes'' Theorems: Applications to electromagnetism and fluid mechanics. Numerical solution of partial differential equations. [Offered: S]', 'NE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('NE 220L', 'Materials Science and Engineering Laboratory', 0.25, 'LAB', 'Labs following the NE 125 Introduction to Materials Science and Engineering course. This laboratory course introduces students to techniques for the characterization of various materials, such as metals, polymers, ceramics, and composites. Experimental exercises will study the physical properties and characteristics of materials, including mechanical, thermal, electrical, and structural/morphological properties at different length scales. [Offered: F]', 'NE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('NE 224', 'Biochemistry for Nanotechnology Engineers', 0.5, 'LAB, LEC, TUT', 'An introduction to the chemistry of amino acids, carbohydrates, lipids and nucleic acids. Structure and properties of proteins and enzymes. An introduction to cell biochemistry. Applications to nanobiotechnology. [Offered: S, first offered Spring 2020]', 'NE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('NE 225', 'Structure and Properties of Nanomaterials', 0.5, 'LEC, TUT', 'Electronic orbitals in atoms, molecules and the solid state. Structures and properties of covalent and ionic solid nanoparticles including their catalytic, electrochemical, electrical, optical and magnetic properties. Semiconductors and carbon/silicon-based nanoparticles. Examples discussed include carbon nanoparticles, dendrimers, micelles and quantum dots. [Offered: S]', 'NE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('NE 226', 'Characterization of Materials', 0.5, 'LEC, TUT', 'Materials structure analysis. Materials composition and chemical bonding analysis. In-situ analysis and monitoring of fabrication process parameters. Materials properties characterization. [Offered: S]', 'NE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('NE 226L', 'Laboratory Characterization Methods', 0.25, 'LAB', 'This laboratory course introduces students to six materials characterization techniques employed routinely by nanotechnology engineers in their practices. Specifically, the six techniques are: Fourier-Transform Infrared (FTIR), Raman light-scattering, and ultra-violet and visible (UV-Vis) spectroscopies, ellipsometry, X-ray diffraction (XRD), and scanning electron microscopy (SEM). This course is intended to familiarize students with the instrumentation involved, prior to its application to nanomaterials in a follow-up laboratory course, NE 320L. [Offered: S]', 'NE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('NE 232', 'Quantum Mechanics', 0.5, 'LEC, TUT', 'Historical background; the differential equation approach to quantum mechanics; treatments of solvable problems such as the particle-in-a-box, harmonic oscillator, rigid rotor, and the hydrogen atom; introduction to approximation methods for more complex systems; application to solid state problems, including band theory. [Offered: F]', 'NE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('NE 241', 'Electromagnetism', 0.5, 'LAB, LEC, TUT', 'Coulomb''s law, electric field and electric flux, Gauss''s law, electric potential, potential and field, magnetic field, Ampere''s law, solenoid, electromagnetic induction, magnetic flux, Lenz'' law, Faraday''s law, capacitors and capacitance, inductors and inductance, Maxwell''s equations, electromagnetic fields and waves, polarization. [Offered: F]', 'NE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('NE 242', 'Semiconductor Physics and Devices', 0.5, 'LAB, LEC, TUT', 'Introduction to the physical principles and electrical behavior of semiconductor materials and devices: electronic band structure, charge carriers, doping, carrier transport, pn-junctions, metal-oxide-semiconductor capacitors, transistors, and related optoelectronic devices (photodetectors, light emitting diodes, solar cells). [Offered: S]', 'NE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('NE 301', 'Nanomaterials and Human Risks, Benefits; Nanotechnology Engineering Practice', 0.15, 'LEC', 'Detoxification and bioactivation pathways; surface modification; biopersistence; quantum dots and cellular imagining; biomedical applications of nanomaterials. Areas of research and professional practice in nanotechnology engineering; exposure to concepts from other engineering plans; support material for the 3A academic term, including aspects of co-operative education and professional or career development.', 'NE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('NE 302', 'Nanotechnology Engineering Practice', 0, 'SEM', 'Areas of research and professional practice in nanotechnology engineering; exposure to concepts from other engineering plans; support material for the 3B academic term, including aspects of co-operative education and professional or career development. [Offered: F]', 'NE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('NE 453', 'Special Topics in Nanotechnology Engineering', 0.5, 'LEC', 'Special topics that significantly span two (or more) areas of concentration in or that provide methodologies relevant to nanotechnology engineering will be offered from time to time when resources are available. [Offered: F,W]', 'NE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('NE 307', 'Introduction to Nanosystems Design', 0.25, 'SEM', 'Introduction to the engineering design process: problem definition and needs analysis; process synthesis, analysis, optimization and troubleshooting; safety and environmental protection in design; written and oral communication for design reports. Students form four-person design teams and start a team-oriented project based on the knowledge and skills acquired in previous courses and on co-operative work terms, culminating in a design proposal presentation. [Offered: F]', 'NE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('NE 318', 'Continuum Mechanics for Nanotechnology Engineering', 0.5, 'LEC, TUT', 'Scaling analysis of differential equations. Tensor operations and tensor calculus. Kinematics of a continuum: material and spatial frames, strain and displacement, conservation of mass. Stress, conservation of momentum and energy. Linear elastic solids: Hooke''s Law, infinitesimal elasticity theory and the Navier-Cauchy equation. Newtonian viscous fluids: hydrostatics, Navier-Stokes equations, flow regimes, and the Reynolds number. Engineering applications: plane elastic waves and vibrations, buoyancy forces, Couette, Poiseuille, and Stokes flows. [Offered: S]', 'NE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('NE 320L', 'Characterization of Materials Laboratory', 0.25, 'LAB', 'Follow-up labs associated with the NE 226 (Characterization of Materials) course. The laboratory exercises focus upon the synthesis and characterization of nano-based materials. Specifically, the synthesis of carbon nanotubes, quantum dots, magnetic ceramics, or other common nanomaterials will be investigated, and sample preparations for various characterization tools will be carried out. Characterization techniques such as infrared and Raman spectroscopy, x-ray diffraction, scanning electron microscope (SEM), and magnetic inductive heating will be utilized. [Offered: S]', 'NE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('NE 330L', 'Macromolecular Science Laboratory', 0.25, 'LAB', 'Labs associated with NE 333 (Macromolecular Science 1) course. Lab exercises exploring the kinetics of radical co-polymerization, the analysis of copolymer composition, concepts in the determination and control of polymer molecular weight distribution, and the kinetics and particle size development in emulsion polymerization. [Offered: F]', 'NE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('NE 333', 'Macromolecular Science', 0.5, 'LEC, TUT', 'Basic definitions and polymer nomenclature, molecular weight averages and distributions, constitutional and configurational isomerism, rubber elasticity, step-growth and free-radical chain-growth polymerizations, emulsion polymerization. [Offered: S]', 'NE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('NE 334', 'Statistical Thermodynamics', 0.5, 'LEC, TUT', 'Statistical mechanics vs. thermodynamics. Review of statistical concepts. Canonical and grand canonical ensembles. Entropy. General formulation of statistical thermodynamics. Fermi-Dirac, Bose-Einstein and Boltzmann statistics. Quantum ideal gases. Specific heat of solids. Electrons in metals and semiconductors. Radiation: the photon gas. [Offered: S]', 'NE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('NE 335', 'Soft Nanomaterials', 0.5, 'LEC', 'Introduction to the applications of macromolecules in nanotechnology. Block copolymers and self-assembled polymerization. Micelles and colloids. Dendrimers and molecular brushes. Supramolecular polymers, polymeric blends and macromolecular nanocomposites. Polymer templates. Applications in the manufacturing of nanostructured materials and nanoscale devices. [Offered: F]', 'NE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('NE 336', 'Micro and Nanosystem Computer-aided Design', 0.5, 'LAB, LEC, TUT', 'Modeling and simulation. Lumped versus distributed approaches. Review of differential-equation systems, constitutive relations, boundary conditions, and solvers for complex, coupled transport problems pertinent to micro and nanosystems. Coupling strategies. Numerical schemes for nonlinear systems. Basic modeling and simulation of micro and nanosystems, and fluidic systems. Relevant nanotechnology applications: optical, thermal, mechanical, and fluidic microstructures, and nanoscale devices. [Offered: F]', 'NE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('NE 340L', 'Microfabrication and Thin-film Technology Laboratory', 0.25, 'LAB', 'Labs associated with the NE 343 (Microfabrication and Thin-film Technology) course. Lab topics may include: thin film deposition by PECVD and PVD (sputtering); photolithography; dry and wet etching; and C-V and I-V analysis of MIS structures. [Offered: F]', 'NE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('NE 343', 'Microfabrication and Thin-film Technology', 0.5, 'LEC, TUT', 'Key processes for electronic device fabrication. Single crystal growth. Substrate preparation. Homoepitaxy, heteroepitaxy, and molecular-beam epitaxy. Ion implantation. Oxidation and diffusion. Physical and chemical vapor deposition. Sputtering and evaporation. Etching. Micromachining. Spin coating and printing. Photolithography. Effects of device scaling on chip performance. Process integration. Yield and reliability. [Offered: S]', 'NE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('NE 344', 'Electronic Circuits', 0.5, 'LEC', 'Metal-oxide-semiconductor field-effect transistor (MOSFET), circuit biasing and load-line analysis. Small-signal equivalent circuits and single stage amplifier configurations. Differential and multistage MOSFET amplifiers. The cascode configuration, current mirror and active loads. Feedback circuit configurations and stability. Oscillators, waveform shaping circuits and delay analysis. Introduction to digital circuits, the transistor switch, inverter circuits and complementary metal-oxide-semiconductor (CMOS) logic circuits. [Offered: F, first offered Fall 2021]', 'NE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('NE 352', 'Surfaces and Interfaces', 0.5, 'LEC', 'Surfaces and interfaces in microelectronics and nanofabrication. Physicochemistry of interfaces. Capillary phenomena and molecular self-assembly. Structure and properties of clean and adsorbate covered surfaces (metals, semiconductors, oxides). Reactions at surfaces and catalysis. Surface electrochemistry, growth and diffusion, nanoscale structure formation/surface patterning, biological interfaces. [Offered: F]', 'NE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('NE 353', 'Nanoprobing and Lithography', 0.5, 'LEC', 'Theory and application of nanoprobing based on scanning probe microscopy (scanning tunneling microscopy, atomic force microscopy, scanning near-field optical microscopy). Nanolithographic techniques (extreme-UV lithography, X-ray lithography, e-beam lithography, focused ion beam lithography, nano-imprint lithography and soft lithography). [Offered: F]', 'NE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('NE 401', 'Nanotechnology Engineering Practice', 0, 'SEM', 'Areas of research and professional practice in Nanotechnology Engineering; exposure to concepts from other Engineering disciplines; support material for the 4A academic term, including aspects of professional or career development. [Offered: F]', 'NE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('NE 402', 'Nanotechnology Engineering Practice', 0, 'SEM', 'Areas of research and professional practice in Nanotechnology Engineering; exposure to concepts from other Engineering disciplines; support material for the 4B academic term, including aspects of professional or career development.. [Offered: W]', 'NE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('NE 408', 'Nanosystems Design Project', 0.5, 'PRJ', 'Design work for the project proposed in NE 307, culminating in a progress report presentation. [Offered: F]', 'NE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('NE 409', 'Nanosystems Design Project and Symposium', 0.5, 'PRJ', 'Completion and presentation of the design project from NE 307 and NE 408. Teams communicate their design in the form of a final report, a poster, and a seminar presentation. [Offered: W]', 'NE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('NE 445', 'Photonic Materials and Devices', 0.5, 'LEC', 'Review of geometrical and wave optics. Optical measurements and instrumentation. Coherent radiation and lasers. Optical communications and optical networks. Optical detectors. Photonic devices. Displays. [Offered: F]', 'NE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('NE 450L', 'Nanoprobing and Lithography Laboratory', 0.25, 'LAB', 'Labs associated with the NE 353 (Nanoprobing and Lithography) course. Lab topics may include: Scanning probe microscopic characterization of polymer and bio-polymer surfaces; scanning tunneling characterization of semiconductors and thin films; fluorescence and laser microscopic testing of proteins; micro-contact printing using molecular self-assembly, nano-soft lithography by AFM. [Offered: F]', 'NE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('NE 451', 'Simulation Methods', 0.5, 'LEC', 'This course provides an introduction to and an overview of computational methods that are currently employed for the simulation of structural and bulk properties of matter, particularly as applied to physical and biological systems at the nanometer scale. Topics to be covered in this course include energy functions and force fields, geometry optimization, normal mode analysis, and reaction--path techniques at the molecular level, and an introduction to the simulation of static and dynamic properties of substances via both Monte Carlo and molecular dynamics (MD) methodologies. [Offered: F]', 'NE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('NE 452', 'Special Topics in Nanoscale Simulations', 0.5, 'LEC', 'Topics in this theme area may include: an overview of modern computational methods and algorithms in nanoscale materials, such as steered molecular dynamics, ab initio molecular dynamics, multiscale modelling, dissipative particle dynamics, transition path sampling, phase-field modelling, quantum simulations using Feynman path integral techniques, condensed-phase spectroscopy, linking of simulations to experiment, simulations and their practical applications. [Offered: W]', 'NE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('NE 454L', 'Nanotechnology Engineering Advanced Laboratory 1', 0.5, 'LAB', 'Application of experimental tools and techniques in the areas of nano-instrumentation, nano-electronics, nano-biotechnology, nanomedicine, and nanomaterials. Experimental exercises may involve lithography, film desposition, etching, simulation and design, optimization, micro-electro-mechanical-system (MEMS) fabrication, nano particle formulation reactions, encapsulation, synthetic chemistry protocols, cell culture protocols, and the use of various characterization instrumentation. [Offered: F]', 'NE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('NE 455L', 'Nanotechnology Engineering Advanced Laboratory 2', 0.5, 'LAB', 'A continuation and/or extension of NE 454L. [Offered: W]', 'NE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('OPTOM 650', 'Research Methodology for Vision Science', 0.5, 'LEC', 'The course will work through the process of the scientific method using a grant proposal to NSERC as a framework. The scientific method, research and experimental questions, experimental design, sampling, statistical theory & power, and data analysis will be covered. Ethical and budgetary issues will also be discussed.', 'OPTOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PD 10', 'Professional Responsibility in Computing', 0.5, 'LEC', 'This course considers many of the legal and ethical issues that arise in the development of software and digital-content products or services that are to be sold to the public. Course topics include laws that impose obligations on products or producers (e.g., privacy laws, accessibility laws, intellectual-property laws, regulated industries); contracts (e.g., licenses, warranties, privacy policies); and ethical responsibilities to employers, clients, the users of products, and to society as a whole.', 'PD');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('NE 459', 'Nanotechnology Engineering Research Project', 0.5, 'PRJ', 'A nanotechnology engineering research project that requires students to demonstrate initiative and to assume responsibility. Students will select projects at the end of the 4A term. Although students may propose their own projects, a faculty member will provide supervision. A project report is required at the end of the 4B term. [Offered: W]', 'NE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('NE 461', 'Micro and Nano-instrumentation', 0.5, 'LEC', 'Fabrication technology for development of micro and nanosensors, actuators, and modules (e.g., micro or, nano-electromechanical systems, micro or nanofluidics channels). Integration using examples drawn from chemical analysis micro and nano-instrumentation. An overview of current micro and nano-instrumentation. [Offered: F]', 'NE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('NE 469', 'Special Topics in Micro and Nano-instrumentation', 0.5, 'LEC', 'Topics in this theme area may include micro and nanosensors, micro and nano-actuators, micro and nanofluidics, micro and nanoscale fabrication, emerging and unconventional nanofabrication technologies.', 'NE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('NE 471', 'Nano-electronics', 0.5, 'LEC', 'Transport phenomena. Quantum confinement. Single molecule transistors. Resonant tunnelling devices. Large area and mechanically flexible electronics. Deposition and patterning techniques. [Offered: F]', 'NE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('NE 479', 'Special Topics in Nanoelectronics', 0.5, 'LEC', 'Topics in this theme area may include quantum effects in electronic devices, molecular electronics, solid state nanoelectronics, organic electronics, advanced nanofabrication technologies such as vacuum deposition, electron beam patterning and nanolithography.', 'NE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('NE 481', 'Nanomedicine and Nanobiotechnology', 0.5, 'LEC', 'Overview of biomedical engineering principles, and their utilization in understanding how our bodies interact with nano- and biomaterials: topics related to innate and acquired inflammatory response, cellular and humoral immunity, complement systems and thrombosis, biocompatibility, and toxicity will be covered. Route of administered nanoparticles will be introduced. This course will also study the formulation and manufacturing process for producing nanoparticles in the biotechnology and pharmaceutical industries. [Offered: F]', 'NE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('NES 625W', 'Myth and Burial Customs in the Ancient Near East', 0.5, 'SEM', 'A study of the myths of the Underworld expressing various views of the Afterlife is put in dialogue with the archaeological evidence of actual burial customs among different cultural and ethnic groups in the Near East and the broader Mediterranean world.', 'NES');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('NE 489', 'Special Topics in Nanoscale Biosystems', 0.5, 'LEC', 'Topics in this theme area may include nanoscale biomaterials for medical and drug delivery devices, biointerfaces, biomembranes, nanoscale patterning on biological interfaces, biomicroelectromechanical systems (BioMEMS), biomimetics, biochips, self-assembly of peptides and proteins, bioseparation, biosensors.', 'NE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('NE 491', 'Nanostructured Materials', 0.5, 'LEC', 'Application of inorganic nanostructured materials and nanocomposites. Synthesis and processing techniques for inorganic nanomaterials and the devices that use them. Students will be required to provide critical analyses and seminar presentations of patents utilizing nanomaterials.[Offered: F]', 'NE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('NE 499', 'Special Topics in Nanostructured Materials', 0.5, 'LEC', 'Topics in this theme area may include membrane nanotechnology, nanoengineered catalysts, nanoengineered polymers, and nanocomposites; manufacturing of nanotubes, nanoparticles, quantum dots, nanowires and other nanomaterials', 'NE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('OPTOM 103', 'Pathophysiology', 0.5, 'LEC', 'Pathogenesis of human disease based on a molecular and cellular framework. Fundamental concepts such as homeostasis, cell injury, protective responses (inflammation, fever, immune response) and the healing process. Systemic pathophysiology: abnormal functions of the cardiovascular system, endocrine system, nervous system, urinary system, muscular system, respiratory system and digestive system.', 'OPTOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('OPTOM 104', 'Neuroanatomy', 0.5, 'LAB, LEC', 'This course presents the detailed anatomy of the head and neck, with emphasis on the special senses. The course includes neuroanatomy as a basis for understanding how various neuronal systems function.', 'OPTOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('OPTOM 105', 'Medical Microbiology', 0.5, 'LEC', 'Bacterial physiology and genetics, normal flora, bacteria (including chlamydiae and rickettsiae), fungi, viruses, parasites and related diseases, with emphasis on molecular mechanisms of ocular disease and relevant ocular manifestations in appropriate topics.', 'OPTOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('OPTOM 106', 'Geometrical, Physical and Visual Optics', 0.5, 'LAB, LEC, TUT', 'Nature of light, wave motion and superposition, rectilinear propagation, reflection and refraction, image formation and quality, optical properties of plane and curved surfaces, prisms and thin lenses. Apertures and pupils. Thick lens theory, lens systems, ray construction, Fraunhofer diffraction and resolution limit. Fresnel diffraction. Simple optical eye models. Refractive error and its correction. Interference and coherence of light: applications. Lasers', 'OPTOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('OPTOM 108', 'Histology of Tissues and Organs', 0.5, 'LEC', 'The organization of human cells, tissues and organs. Cellular structure and function is presented as a foundation for understanding fundamental pathological constructs (e.g. carcinoma versus sarcoma). Detailed histology of major organ systems that are likely to have ocular manifestations when diseased. This coursework will provide the basis for subsequent courses in the pathobiology of ocular and systemic human disease.', 'OPTOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('OPTOM 109', 'Visual Perception 1: Perception of Light', 0.5, 'LAB, LEC', 'Sensory processes involved in visual perception. Topics include spectral sensitivity, light and dark adaptation, temporal and spatial resolution, and principles of photometry.', 'OPTOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('OPTOM 114', 'Ocular Anatomy and Physiology', 0.5, 'LAB, LEC', 'This course presents the detailed anatomy of the eye. The course includes histology, gross anatomy and physiology, as a basis for understanding how the various ocular structures function individually and interact with each other. The course emphasizes the processes operating to maintain the eye as a viable organ and provides a patho-physiological and clinical perspective of the implications of breakdown of these mechanisms.', 'OPTOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('OPTOM 124', 'Human Gross Anatomy', 0.5, 'LAB, LEC', 'A selective study of human gross anatomy as a basis for understanding disease. Strong emphasis is placed on head and neck anatomy, including the orbit, but thorough coverage of thorax, abdomen and pelvis is provided as well. Didactic material is illustrated in laboratory experience with prosected human cadavers, plasticized cadaveric material, anatomical models and computer-based learning tools.', 'OPTOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('OPTOM 126', 'Fundamentals of Visual Optics', 0.5, 'LAB, LEC', 'Refraction and reflection of light by the surfaces of the eye. Optical properties of emmetropic and ametropic eyes as a function of growth and pupil size and their measurement. Properties of the retinal image including size, blur and quality and their relationship to imperfections of the optics. Light scattered in the eye. Relationships between the retinal image and visual perception. The retinal image and accommodation.', 'OPTOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('OPTOM 134', 'Immunology', 0.5, 'LAB, LEC', 'An introduction to the vertebrate immune response; the cells and tissues of the lymphoid system; humoral and cell-mediated immunity; initiation and regulation of the immune response; the immune system and disease, techniques used in immunology. Unique features of the immunobiology of the eye are included.', 'OPTOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('OPTOM 143', 'Physiology of the Eye', 0.5, 'LAB, LEC, TUT', 'The physiology of the smooth muscles of the eye, the extraocular striate muscles, the lacrimal apparatus, the cornea, the iris, the lens, the ciliary body and the vitreous body. Production and drainage of acqueous and related influences on intraocular pressure. The vascular supply of the eye.', 'OPTOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('OPTOM 152', 'Clinical Techniques 1', 0.5, 'LEC', 'Clinical techniques for the primary care examination of the optical properties and ocular health of the ye. Case history taking. Medical emergency responses. Professional boundaries.', 'OPTOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('OPTOM 152L', 'Clinical Techniques 1 Laboratory', 0.25, 'LAB, TUT', 'Selected clinical techniques for students taking 152.', 'OPTOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('OPTOM 215', 'Systemic Disease', 0.5, 'LEC', 'Etiology, signs, symptoms, diagnosis and management of diseases affecting the organs and tissues of the human body including: circulatory, hemopoietic/lymphoid, lungs/upper respiratory tract, kidney, gastrointestinal tract, liver/biliary tract, pancreas, urogenital system, endocrine system, musculoskeletal system, skin, nervous system diseases.', 'OPTOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('OPTOM 216', 'Ophthalmic Optics 1', 0.5, 'LAB, LEC', 'Single vision spectacle lenses. Lens and frame materials. Optical and ophthalmic instrumentation. Image quality. Polarization. Tinted lenses. Decentration and prismatic effect. Obliquely crossed cylinders. Spectacle magnification. Ophthalmic laboratory procedures: measurement of spectacle lens power.', 'OPTOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('OPTOM 219', 'Visual Perception 2: Monocular and Binocular Visual Processes', 0.5, 'LAB, LEC', 'Physical space and visual space. Fundamental perceptual processes, binocular vision, stereopsis, binocular space perception. Systems of analysing binocular vision. Theory of aniseikonia. Perceptual aspects of aniseikonia.', 'OPTOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('OPTOM 231', 'Introductory Clinical Pharmacology', 0.5, 'LEC', 'Pharcaokinetic and pharmacodynamic theory. Systemic medications used to manage most major diseases. Mechanism of action, contraindications and systemic and ocular adverse drug reactions.', 'OPTOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('OPTOM 243', 'Neurophysiology of Vision', 0.5, 'LEC', 'The neural processing of colour, brightness, movement and form by the retina, lateral geniculate, cortex, superior colliculus and other brain centres. Neural mechanisms underlying binocular depth perception, the accommodative response and eye movement.', 'OPTOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('OPTOM 245', 'Diseases of the Eye 1', 0.5, 'LEC', 'Anterior segment disease including etiology, pathogenesis, signs, symptoms, differential diagnosis and management of diseases of the ocular adnexa and anterior segment of the eye: the lids, orbit and adnexa, conjuntiva, comea, uvea, sciera, lens and cataract, the glaucomas and ocular emergencies.', 'OPTOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('OPTOM 245L', 'Diseases of the Eye 1 Laboratory', 0.25, 'LAB, TUT', 'Selected clinical techniques for students taking OPTOM 245.', 'OPTOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('OPTOM 246', 'Ophthalmic Optics 2', 0.5, 'LAB, LEC', 'Multifocal spectacle lenses. Aberrations of thin lenses and spectacle lens design. Prescribing and fitting of spectacles. Environmental and occupational prescribing. Prescription analysis. Ophthalmic and visual standards. Computer vision syndrome. Ophthalmic laboratory procedures: measurement of complex spectacle lenses, and spectacle frame adjustment and repairs.', 'OPTOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('OPTOM 250', 'Optometric Jurisprudence', 0.5, 'LEC', 'Legal aspects of practising optometry in Canada. Fundamentals of Canadian law. Negligence. Informed consent. Legislation and regulations affecting optometric practice.', 'OPTOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('OPTOM 252', 'Clinical Techniques 2', 0.5, 'LEC', 'Basic and clinical ocular motility. Basic concepts of ocular motility are integrated with clinical methods. Assessment and diagnosis of strabismic and non-strabismic disorders.', 'OPTOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('OPTOM 252L', 'Clinical Techniques 2 Laboratory', 0.25, 'LAB, TUT', 'Selected clinical techniques for students taking OPTOM 252.', 'OPTOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('OPTOM 255', 'Diseases of the Eye 2', 0.5, 'LEC', 'Etiology, signs, symptoms, diagnosis, management, and epidemiology of diseases of the posterior segment of the eye; higher visual and oculomotor systems; multisystem diseases.', 'OPTOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('OPTOM 255L', 'Diseases of the Eye 2 Laboratory', 0.25, 'LAB, TUT', 'Selected clinical techniques for students taking OPTOM 255.', 'OPTOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('OPTOM 261', 'Clinical Ocular Pharmacology', 0.5, 'LEC', 'Pharmacokinetic and pharmacodynamic principles of ophthalmic drug design and delivery. Selection and use of ophthalmic diagnostic pharmaceutical agents, palliative agents and therapeutic pharmaceutical agents. Mechanism of action, contraindications and adverse drug reactions. Recommended guidelines for use and follow-up procedures.', 'OPTOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('OPTOM 262', 'Clinical Techniques 3', 0.25, 'LAB, LEC', 'Differential diagnosis and problem specific testing using a systems examination approach. Minimum database expectations. Record keeping, case presentations, counselling, referrals and report writing. Preparation for entry into Optometry Clinic including policy review and standards of practice. Observation and preliminary testing in the Optometry Clinic.', 'OPTOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('OPTOM 270', 'Public Health Optometry', 0.5, 'LEC', 'Introduction to the foundation and basic sciences of public health with an emphasis on the epidemiology of vision problems.', 'OPTOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('OPTOM 272', 'Strabismus and Aniseikonia', 0.5, 'LAB, LEC', 'Detection and evaluation of sensory and motor characteristics of vision in aniseikonic, strabismic and nonstrabismic patients. Classifications, diagnoses, prognoses, and modes of therapy for aniseikonic, nonstrabismic, and strabismic patients.', 'OPTOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('OPTOM 339', 'Visual Perception 3:Colour Vision', 0.5, 'LAB, LEC', 'An introduction to colour perception, colorimetry and colour discrimination. Characteristics of congenital and acquired colour vision deficiencies, colour vision test design and patient management.', 'OPTOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('OPTOM 342A', 'Case Analysis and Optometric Therapies 1', 0.5, 'LEC, TUT', 'The clinical application of the visual sciences. Emphasis is placed on the differential diagnostic method of analyzing clinical data with consideration given to appropriate clinical techniques, effective record keeping, recommended optometric therapies and prognoses.', 'OPTOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('OPTOM 342B', 'Case Analysis and Optometric Therapies 2', 0.5, 'LEC, TUT', 'A continuation of Optometry 342A. Emphasis is placed on the differential diagnostic method of analyzing clinical data with special emphasis on refractive and binocular vision conditions.', 'OPTOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('OPTOM 346', 'Ophthalmic Optics 3', 0.25, 'LAB', 'Spectacle frame materials. Fitting and adjusting techniques. Selection of lens design. Lenses for high myopia. Dispensing of eye protectors. Optics of low vision aids. Patient counselling and management of dispensing problems. Laboratories provide experience in practical aspects of ophthalmic dispensing.', 'OPTOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('OPTOM 347', 'Contact Lenses 1', 0.5, 'LEC', 'Indications and contra-indications for contact lens wear. Lens selection and design. Fitting and evaluating rigid and hydrogel soft contact lenses. Physico-chemical and mechanical properties of contact lens materials. Optical and mathematical concepts. The ocular physiological response to contact lens wear. Care and maintenance of contact lenses.', 'OPTOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('OPTOM 347L', 'Contact Lenses 1 Laboratory', 0.25, 'LAB', 'Selected clinical techniques for students taking OPTOM 347.', 'OPTOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('OPTOM 348A', 'Optometry Clinics', 1, 'CLN', 'Students are assigned to various areas within the clinic where, under direct clinical faculty supervision, they participate in the provision of optometric services to clinic patients. In addition to primary care, they are exposed to the provision of contact lens, ocular health and optical services.', 'OPTOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('OPTOM 348B', 'Optometry Clinics', 1, 'CLN', 'Students are assigned to various areas within the clinic where, under direct clinical faculty supervision, they participate in the provision of optometric services to clinic patients. In addition to primary care, they are exposed to the provision of contact lens, ocular health and optical services.', 'OPTOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('OPTOM 360', 'Professional Ethics and Optometric Communication', 0.5, 'LEC', 'A survey of alternative philosophical perspectives involved in resolution of sample ethical and moral issues confronting optometrists. Awareness of the explicit and implicit contents of written and vocal communications. An exploration of optometric communication issues related to letter and report writing, patient counselling, patient referral, fee presentation, and complaint management.', 'OPTOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('OPTOM 365', 'Ophthalmic Lasers and Refractive Surgery', 0.5, 'LEC', 'General principles, types and uses of lasers in eyecare. Biagnostic, thermal, ionizing, and excimer lasers. Refractive surgery. Safety and efficacy. Pre-operative and post-operative care.', 'OPTOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('OPTOM 367', 'Contact Lenses 2', 0.5, 'LAB, LEC', 'Detection and management of chronic and acute complications induced by contact lenses. Contact lens management options for special conditions such as dry eye, aphakia and keratoconus (and other corneal irregularities). Disposable lenses and replacement regimens. Extended wear options. Alternative management of refractive errors such as orthokeratology and refractive surgery. Contact lenses and presbyopia.', 'OPTOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('OPTOM 375', 'Diseases of the Eye 3', 0.5, 'LEC', 'Advanced considerations of the etiology, signs, symptoms, diagnosis, treatment and management of ocular disease. Emphasis will be placed on the clinical case management with therapeutic pharmaceutical agents.', 'OPTOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('OPTOM 375L', 'Diseases of the Eye 3 Laboratory', 0.25, 'LAB', 'Selected clinical techniques for students taking OPTOM 375.', 'OPTOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('OPTOM 377', 'Pediatric Optometry and Learning Disabilities', 0.5, 'LAB, LEC', 'Optometric examination and management of infants, children and patients with learning disabilities or multiple-challenges. General child development and the development of the optical and sensory-motor functions of the visual system. Learning disabilities and related vision problems. The role of the optometrist in conjunction with parents, teachers and psychologists.', 'OPTOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('OPTOM 380', 'Practice Management', 0.5, 'LEC', 'Practice management issues relating to solo, associate-ships, contracts, the development and running of a clinical practice, opening a practice, staff training, dealing with complaints, data collection, practice promotion and marketing, advertising, financial and legal issues.', 'OPTOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('OPTOM 385', 'Clinical Medicine for Optometric Practice', 0.5, 'LAB, LEC', 'Overview of current medical diagnoses and management of systemic diseases with ocular involvement. History and physical examination (including neurological examination), clinical laboratory testing, and diagnostic imaging. Diseases of high prevalence including cardiovascular, cancer, endocrine, and neurological disorders.', 'OPTOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('OPTOM 387', 'Gerontology and Low Vision', 0.5, 'LAB, LEC', 'Aging and its effects on the visual system in both health and disease, care and management of the aging patient. Assessment and management of visual impairment and disability, including both optical and non-optical therapies. Epidemiology and psychology of vision impairment, and associated rehabilitative services.', 'OPTOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('OPTOM 412', 'Case Analysis 3', 0.75, 'LEC, TUT', 'Building on analytical principles developed in OPTOM 342, this course involves student, case-based presentations in a grand rounds format. Each student chooses one, different, interesting case from his/her previous clinical experience. The student presents the case and answers questions related to the case and the patient''s condition(s). Faculty discussants will direct the students in assessing the basic and clinical science features of the cases. Patient cases may be chosen from any aspect of optometric practice.', 'OPTOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('OPTOM 441', 'Optometry Research Proposal', 0.5, 'SEM', 'An independent paper in the form of literature review on the student''s area of interest, experimental design proposition, and preliminary data. Before registering in the course the student and the designated supervisor must submit to the coordinator a research proposal for the student''s research area. The format of the paper is to be determined with the supervisor and may be in chapters, in journal style, or in an oral presentation, during the registered term, at seminar sessions (OPTOM 609/OPTOM 629).', 'OPTOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('OPTOM 451', 'Optometry Research Project', 0.5, 'PRJ', 'An independent research project on an approved topic, supervised by a faculty member. This is the completion of the research proposal in OPTOM 441 and it is recommended that the format of the report, to be determined with the supervisor, follow the format selected for OPTOM 441.', 'OPTOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('OPTOM 458', 'Clinical Clerkship 1', 3, 'CLN', 'Supervised optometric patient care in one or more external clinical settings.This course is complementary to OPTOM 468 Clinical Clerkship 2 and includes optometric assessment, diagnosis and management of refractive errors, disorders and diseases of the eye and visual system, along with associated systemic conditions and practice management. [Offered: S, F, W]', 'OPTOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('OPTOM 460', 'Advanced Study Topics', 0.5, 'LAB, LEC', 'Intensive study of a speciality optometric topic. Course formats may involve lectures, clinical worships, literature reviews or research projects.', 'OPTOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('OPTOM 461S', 'Optometry Seminar', 0.5, 'LEC', 'This is a seminar course', 'OPTOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('OPTOM 468', 'Clinical Clerkship 2', 3, 'CLN', 'Supervised optometric patient care in one, or more, external clinical settings. This course is complementary to OPTOM 458 Clinical Clerkship 1 and includes optometric assessment, diagnosis and management of refractive errors, disorders and diseases of the eye and visual system, along with associated systemic conditions and practice management. [Offered: S, F, W]', 'OPTOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('OPTOM 477', 'Clinical Techniques 4', 0.75, 'LAB, LEC', 'This course will provide an opportunity for optometry students to discuss and evaluate clinical techniques, instrumentation, and ideologies not covered in the current curriculum. Students will be encouraged to use their basic knowledge of the vision sciences to provide a perceptive critique of the clinical subjects addressed.', 'OPTOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('OPTOM 478', 'Clinical Clerkship 3', 3, 'CLN', 'Supervised optometric patient care in a variety of the University of Waterloo optometric clinical settings. Activities include assessment, diagnosis and management of disorders and diseases of the eye and visual system, along with associated systemic conditions, dispensing and fitting of optometric appliances and rehabilitative vision care. [Offered: S, F, W]', 'OPTOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('OPTOM 488', 'Exit Exam Remediation', 2, 'CLN', 'Remediation in areas failed during clinical exit examinations. On-site clinical rotations and self-study guided by the faculty.', 'OPTOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('OPTOM 600', 'Physiology of the Eye', 0.5, 'LEC', 'An advanced treatment of lacrimal apparatus, tears, cornea, the consequences of contact lens wear, aqueous production and drainage, intraocular pressure, the lens, accommodation, the vitreous, the blood supply to the eye, the retinal epithelium, extraocular muscles.', 'OPTOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('OPTOM 601', 'Optical Characteristics of the Eye', 0.5, 'LEC', 'The refractive state of the eye and its variables; transmittance, absorption, scattering by the ocular media; pupil size, aberrations, diffraction limitations, resolution, and contrast transfer functions.', 'OPTOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('OPTOM 602', 'Ocular Motility', 0.5, 'LEC', 'Kinematics of eye movements, specifications of the direction of regard, neurology of oculomotor functions, oculomotor systems and types of eye movements, disorders of oculomotor systems and their effects.', 'OPTOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('OPTOM 603', 'Accommodation and Convergence', 0.5, 'LEC', 'Mechanism of accommodation; stimulus to accommodation; time and amplitude characteristics of the response; synkinesis of accommodation and convergence; ACA ratio; the zone of clear single binocular vision.', 'OPTOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('OPTOM 604', 'Visual Perception of Space', 0.5, 'LEC', 'Oculocentric and egocentric directionalization; monocular clues for depth perception; distance and size relationships; real and apparent motion; the theoretical and nonius horoptomer and the apparent fronto-parallel plane; stereopsis; aniseikonia; Pulfrich effects; alley experiments and the geometry of visual space.', 'OPTOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('OPTOM 605', 'Psychophysics of Colour Vision', 0.5, 'LEC', 'Normal and defective spectral sensitivity, colour discrimination and colour matching functions. Colour perception in the peripheral retina and by small fields. Chromatic adaptation. Colour vision in animals. Theories of colour vision.', 'OPTOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('OPTOM 606', 'Radiation and the Visual Stimulus', 0.5, 'LEC', 'Measurement and specification of light; radiometric and photometric relationships; spectrophotometry and the C.I.E. colorimetric specification of visual stimuli; radiation limits of the visual system. Radiation hazards and safety criteria.', 'OPTOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('OPTOM 607', 'Neurophysiology of Vision', 0.5, 'LEC', 'An advanced course which examines photoreceptor function, the retina, the neural processing of form and colour by the geniculo-striate system, the neural control of eye movements and accommodation by midbrain and brainstem structures, the vestibular system as it relates to vision, eye-hand co-ordination, the neural processing involved in reading.', 'OPTOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('OPTOM 608', 'Special Topics in Vision Science', 0.5, 'RDG', 'Topics in the fields of specialization of the faculty may be studied by special arrangements with the faculty member, the student and his or her committee.', 'OPTOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('OPTOM 661', 'Practical Advancement Data Analysis', 0.5, 'LAB, LEC', 'Bayesian statistics, questionnaire analysis, advanced linear and non-linear modeling.', 'OPTOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('OPTOM 670', 'Vision Science Part 1', 0.5, 'LEC', 'A concentrated review of topics relating to principles of and research in the vision sciences. The course is split into 2 segments (OPTOM 690A/690B) covering ocular anatomy and ultrastructure and ocular physiology. This course is recommended for all graduate students.', 'OPTOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('OPTOM 671', 'Vision Science Part', 0.5, 'LEC', 'A concentrated review of topics relating to principles of and research in the vision sciences. The course is split into 2 segments (OPTOM 690A/690B) covering optomics of the eye and binocular vision. This course is recommended for all graduate students.', 'OPTOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('OPTOM 672', 'Comparative Anatomy & Physiology of the Vertebrate Eye', 0.5, 'LEC', 'An evaluation of evolutionary lines of development of visual structures and functions: the vascular system, accessory glands, light control mechanisms, the distribution of visual pigments, accommodation, binocular vision and neural coding.', 'OPTOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('OPTOM 680', 'Visual Development', 0.5, 'LEC', 'Consideration of the development of the optomical, and sensory motor functions of vision during the early years of life provides the basis upon which this course examines current methods and literature in basic and clinical vision sciences. Course structure will be organized to suit a student''s background (optometry, psychology, physics or biology) and their special interests.', 'OPTOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('OPTOM 681', 'Ageing and Vision', 0.5, 'LEC', 'Theories of ageing as they relate to vision.  Ageing changes in structure and function of the eye and visual system.  Impact of visual function on functional (everyday) vision.  Interaction between vision and other ageing changes.  Cortical plasticity and ageing.', 'OPTOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 736', 'Optical Properties of Semiconductors', 0.5, 'LEC', 'Reflection and refraction of electromagnetic waves at dielectric and conducting interfaces. Dispersion, absorption processes, photo effects, magneto-optical effects, emission of radiation.', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('OPTOM 610', 'The Comparative Anatomy and Physiology of the Vertbrate Eye', 0.5, 'LEC', 'An evaluation of evolutionary lines of development of visual structures and functions: the vascular system, accessory glands, light control mechanisms, the distribution of visual pigments, accommodation, binocular vision and neural coding.', 'OPTOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('OPTOM 611', 'Epidemiology of Vision Anomalies', 0.5, 'LEC', 'The distribution, determinants and frequency of vision problems. Estimates of risk. Risk factors and the development of vision problems.', 'OPTOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('OPTOM 613', 'Visual Development', 0.5, 'LEC', 'Consideration of the development of the optomical, and sensory motor functions of vision during the early years of life provides the basis upon which this course examines current methods and literature in basic and clinical vision sciences. Course structure will be organized to suit a student''s background (optomometry, psychology, physics or biology) and their special interests.', 'OPTOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('OPTOM 614A', 'Clinical OptometryPart 1-MSc', 0.25, 'CLN', 'Attendance within clinical areas agreed upon by the student, supervisor and Clinic Director and approved by the Clinic Director. The aim of the course is to broaden and develop advanced clinical skills and knowledge. Credit will be given for completion of each term of clinical activity (or its equivalent) and the presentation of one seminar (case report) a term.  Credit towards a degree may only be earned once in the progression through the MSc degree.', 'OPTOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('OPTOM 614B', 'Clinical Optometry Part 2 - MSc', 0.25, 'CLN', 'Attendance within clinical areas agreed upon by the student, supervisor and Clinic Director and approved by the Clinic Director. The aim of the course is to broaden and develop advanced clinical skills and knowledge. Credit will be given for completion of each term of clinical activity (or its equivalent) and the presentation of one seminar (case report) a term. Credit towards a degree may only be earned once in the progression through the MSc degree.', 'OPTOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('OPTOM 615', 'Visual Psychophysics', 0.5, 'LEC', 'The course will examine how various factors (luminance, contrast, psychophysical method and ocular disease) can influence the outcome of psychophysical tests that are used in both basic and clinical vision research. Consideration will be given to the utility of such tests by considering their reliability, discriminative power and validity.', 'OPTOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('OPTOM 616', 'Research Methodology for Vision Science', 0.5, 'LEC', 'The course will work through the process of the scientific method using a grant proposal to NSERC as a framework. The scientific method, research and experimental questions, experimental design, sampling, statistical theory & power, and data analysis will be covered. Ethical and budgetary issues will also be discussed.', 'OPTOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('OPTOM 620', 'Data Analysis in Vision Science', 0.5, 'LEC', 'Measurements, probability and power; Detecting difference between parametric/non-parametric variables; Analyzing repeated measures designs; Relationships between parametric/non-parametric variables; Between-eye comparison and correlation; Complex regression; Statistical re-sampling techniques; Analysis of clinical images; Accurancy and repeatability of novel clinical and experimental techniques; Introductory analysis of epidemiological and clinical trials data.', 'OPTOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('OPTOM 624A', 'Clinical Optometry Part 1 - PhD', 0, 'CLN', 'Attendance within clincial areas agreed upon by the student, supervisor and Clinic Director and approved by the Clinic Director. The aim of the course is to broaden and develop advanced clinical skills and knowledge. Any two terms of clinical activity (or its equivalent) and the presentation of one seminar (case report) a term will fulfill this requirement. Recognition of participation in this course can only be given once in the progression through the PhD degree.', 'OPTOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('OPTOM 624B', 'Clinical Optometry Part 2-PhD', 0, 'CLN', 'Attendance within clincial areas agreed upon by the student, supervisor and Clinic Director and approved by the Clinic Director. The aim of the course is to broaden and develop advanced clinical skills and knowledge. Any two terms of clinical activity (or its equivalent) and the presentation of one seminar (case report) a term will fulfill this requirement. Recognition of participation in this course can only be given once in the progression through the PhD degree.', 'OPTOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('OPTOM 628', 'Special Topics in Vision Science', 0.5, 'RDG', 'Topics in the fields of specialization of the faculty may be studied by special arrangement with the faculty member and the student''s committee. The course is designed for PhD students only. The course cannot be similar in content to 608 taken at the Master''s level.', 'OPTOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('OPTOM 651', 'Data Analysis in Vision Science', 0.5, 'LEC', 'Measurements, probability and power; Detecting difference between parametric/non-parametric variables; Analyzing repeated measures designs; Relationships between parametric/non-parametric variables; Between-eye comparison and correlation; Complex regression; Statistical re-sampling techniques; Analysis of clinical images; Accurancy and repeatability of novel clinical and experimental techniques; Introductory analysis of epidemiological and clinical trials data.', 'OPTOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('OPTOM 660', 'Visual Psychophysics', 0.5, 'LEC', 'The course will examine how various factors (luminance, contrast, psychophysical method and ocular disease) can influence the outcome of psychophysical tests that are used in both basic and clinical vision research. Consideration will be given to the utility of such tests by considering their reliability, discriminative power and validity.', 'OPTOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 601', 'MSc Thesis Proposal', 0.5, 'SEM, TUT', 'The objective of Pharm 601 are to encourage graduate students in the MSc Pharmacy program to best prepare and present their research objectives in written and oral form.  
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('OPTOM 630', 'Principles of Clinical Instruction', 0.5, 'CLN', 'Examination of three learning theories will provide the context for consideration of learning and motivation in the Optometry educational setting. Important components of clinical instruction such as breadth of professional knowledge, effectiveness of instructor-student and instructor-patient interactions, and feedback utilization will be discussed. Performance evaluation with the aim of improving practical and teaching skills will utilize, among other things, video taping.', 'OPTOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('OPTOM 631', 'Graduate Clinic', 0, 'CLN', 'Graduate clinic requires the attendance of a resident within the clinical areas agreed upon with his supervisor. This clinical course and associated research projects will provide the resident with materials designed to broaden clinical understanding. OPTOM 631 may be taken any number of times provided that the content of the program is specified in writing by the advisor and is sufficiently varied from a previous session of OPTOM 631 such that it constitutes a distinct advance in a resident''s clinical knowledge.', 'OPTOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('OPTOM 690A', 'Vision Science Part 1', 0.5, 'LEC', 'A concentrated review of topics relating to principles of and research in the vision sciences. The course is split into 2 segments (OPTOM 690A/690B) covering ocular anatomy and ultrastructure and ocular physiology. This course is recommended for all graduate students.', 'OPTOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('OPTOM 690B', 'Vision Science Part II', 0.5, 'LEC', 'A concentrated review of topics relating to principles of and research in the vision sciences. The course is split into 2 segments (OPTOM 690A/690B) covering optomics of the eye and binocular vision. This course is recommended for all graduate students.', 'OPTOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PACS 101', 'Peace is Everybody''s Business', 0.5, 'LEC', 'This course explores ways in which individuals and groups from multiple sectors of society have contributed to peace by engaging conflict constructively and advancing social justice. Attention will be given to the diversity of peacemaking approaches, and to interpersonal and intergroup as well as international applications.', 'PACS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PACS 201', 'Roots of Conflict, Violence, and Peace', 0.5, 'LEC', 'An examination of influential theories about the sources and nature of conflict, violence, and peace. Contributions from the social sciences as well as the humanities will be explored, with attention to connections between interpersonal, intergroup, and international levels of analysis.', 'PACS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PACS 202', 'Conflict Resolution', 0.5, 'LEC', 'An examination of the resolution of conflicts, ranging from interpersonal to broader social and international conflicts. Students are introduced to negotiation, mediation, and nonviolent resistance, and are encouraged to develop their own theoretical understandings that aid in addressing conflict.', 'PACS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PACS 203', 'A History of Peace Movements', 0.5, 'LEC', 'A survey of individuals and groups that have created popular movements for peace globally and locally throughout history. The scope will be international, with a particular focus on the 19th and 20th century movements. The choice of peace movements will allow for a contrast in comparison of ideology, strategy, and impact.', 'PACS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PACS 301', 'Special Topics in Peace and Conflict Studies 1', 0.5, 'LEC', 'A seminar course investigating special issues related to peace and conflict. Content may vary from year to year.', 'PACS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PACS 302', 'Special Topics in Peace and Conflict Studies 2', 0.5, 'LEC', 'A seminar course investigating special issues related to peace and conflict. Content may vary from year to year.', 'PACS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PACS 311', 'Doing Development: Issues of Justice and Peace', 0.5, 'LEC', 'This course introduces a variety of theoretical and practical perspectives on international development. It examines current and alternative development programs in terms of their contribution to justice and/or peace at local, national, and global levels.', 'PACS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PACS 312', 'Quest for Peace in Literature and Film', 0.5, 'LEC', 'A study of works of literature and film which express a resistance to war. The course examines how the desire to articulate an anti-war position has engaged the artistic sensibilities and shaped the visions and modes of expression of selected writers and filmmakers.', 'PACS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PACS 313', 'Community Conflict Resolution', 0.5, 'LEC', 'An analysis of the growing use of mediation and other conflict resolution strategies in community conflicts, race relations, church disputes, and alternatives to the legal system. The course focuses on case studies with attention to both practical and theoretical issues.', 'PACS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PACS 314', 'Conflict Resolution in the Schools', 0.5, 'LEC', 'This course examines the potential for utilizing the principles of mediation and conflict resolution in school administration, curricula, teaching, classroom management, and school counselling.', 'PACS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PACS 316', 'Violence, Non-violence, and War', 0.5, 'LEC', 'An exploration of the traditional debates concerning the legitimacy of violence and war as instruments in the pursuit of personal and political goals. The course critically examines a continuum of views from religious doctrines of non-resistance, to various forms of pacifism and non-violent resistance, "just-war theory", and political realism. The strategic arguments for political non-violent action are also considered.', 'PACS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PACS 318', 'Peacebuilding, Human Rights, and Civil Society', 0.5, 'LEC', 'This course will examine the close causal connection between violations of human rights and violent conflict/war. It will also analyse the role of human rights and civil society principles in forging or consolidating peace. Course work will include case studies, assigned readings, class participation, and simulation exercises.', 'PACS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PACS 320', 'Christian Approaches to Peacemaking', 0.5, 'LEC', 'Current Christian approaches to peacemaking in areas of conflict: war and militarism, crime, poverty, racism, and gender relations. Attention will be given to various biblical, theological, and historical bases for these approaches.', 'PACS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PACS 321', 'Gender in War & Peace', 0.5, 'LEC', 'An examination of various themes in the history of peace and war using gender as a central category of analysis. Theoretical literature and international case studies will be used to explore how the discourse and enactment of war and peace are influenced by societal constructions of gender, both historically and in the present.', 'PACS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PACS 323', 'Negotiation: Theories and Strategies', 0.5, 'LEC', 'This course explores different ways of negotiating between people and groups with conflicting interests. Students will learn the theory behind the strategies and develop practical negotiation skills you can put to use in your daily life at home, at work, and in the community.', 'PACS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PACS 324', 'Human Rights, Peace, and Business', 0.5, 'LEC', 'This course will explore the tension between the values of human security/human rights and traditional economic policy. The impact of this dynamic relationship on the well-being of individuals as well as on corporations and international economic institutions to promote peace and just development will also be analyzed.', 'PACS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PACS 326', 'Religion and Peacebuilding', 0.5, 'LEC', 'This course investigates the roles of religion and spirituality in peacemaking, exploring both obstacles and opportunities facing religious peace builders. Cases involving representatives of major world religions (Christianity, Islam, Judaism, Hinduism, and Buddhism) will provide a basis for comparing practices of faith-based advocacy for social justice, reconciliation, and coexistence.', 'PACS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PACS 327', 'Cultural Approaches to Conflict Resolution', 0.5, 'LEC', 'Cultural differences enrich our world while also creating bases for disagreement and misunderstanding among individuals, social groups, and nations. This course explores the cultural dimensions of conflict and conflict resolution, shedding light on major patterns of human difference and their implications for contemporary peacebuilding practice.', 'PACS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PACS 328', 'Fair Trade', 0.5, 'LEC', 'This course examines the history, theory, and practice of fair trade and how it differs from traditional principles of international trade. It will specifically explore the impact that the fair trade movement has had on small scale producers, consumers, and more broadly on the global economic system.', 'PACS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PACS 329', 'Restorative Justice', 0.5, 'LEC', 'This course investigates the history, theory, principles, practices, and people of restorative justice. Content will centre particularly on restorative justice as a way of dealing with crime and interpersonal violence in the Canadian context.', 'PACS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PACS 330', 'War and Peace in Christian Theology', 0.5, 'LEC', 'Christian teachings on war and peace from the early church to the present, including crusade, just war, and pacifist traditions, focusing especially on the 20th century discussion around realism, just revolution, nuclear pacifism, and non-violent resistance.', 'PACS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PACS 331', 'Trauma, Healing, and Conflict Resolution', 0.5, 'LEC', 'This course examines how the conflict resolution process can be impacted by trauma. With case examples from armed conflict to family violence, participants explore the emotional, physical, and relational aspects of conflict to better understand the potential for interventions that promote peace and justice. Studies include causes, types, and impacts of trauma; cycles of interpersonal and societal violence; frameworks for healing; and exploration of apology, reconciliation, revenge, and forgiveness.', 'PACS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PACS 390', 'Internship', 0.5, 'FLD', 'An internship allows students to engage in experiential learning with an organization that deals with peace and conflict issues, in either a Canadian or international context. The course integrates theory and practice, while facilitating the development of attitudes, strategies, skills, and knowledge that support work in a Peace and Conflict Studies-related setting. Students will identify an appropriate placement, read relevant texts, and submit a report reflecting on what the internship revealed about the integration of peace and conflict studies theory and practice.', 'PACS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PACS 391', 'Conflict Resolution Skills', 0.25, 'SEM', 'This course provides a framework for students to pursue conflict resolution
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PACS 395', 'Peace and Conflict Studies Travel Course', 0.5, 'FLD', 'A travel seminar of approximately three weeks in length, taught on location, which will involve travel to a region that has experienced conflict. Student preparation, in the form of readings and/or written assignments, will be required prior to departure. Regular lectures and interaction with persons who have worked for peace will be scheduled. Hands-on examples merging theory with practice will be emphasized. Possible locations include the Middle East, Central/South America, Africa, Asia.', 'PACS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PACS 398', 'Directed Readings in Peace and Conflict Studies', 0.5, 'RDG', 'Students may arrange independent studies in the area of peace and conflict studies on problems of special interest.', 'PACS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PACS 399', 'Directed Readings in Peace and Conflict Studies', 0.5, 'RDG', 'Students may arrange independent studies in the area of peace and conflict studies on problems of special interest.', 'PACS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PACS 401', 'Senior Research Seminar', 0.5, 'DIS, RDG, SEM', 'Each student will work on an integrative research project and will meet regularly with other students and faculty for discussion.', 'PACS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PACS 402', 'Senior Research Seminar', 0.5, 'DIS, RDG, SEM', 'A continuation of PACS 401. Each student will work on an integrative research project or projects and will meet regularly with other students and faculty for discussion.', 'PACS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PACS 601', 'Systems of Peace, Order, and Good Governance', 0.5, 'SEM', 'This course analyzes the roles and responsibilities of civil society, the market, and the state as agents capable of creating just and humane structures.  Case studies reveal how individual scan leverage collaboration among all sectors of society to advance positive systemic positive change.', 'PACS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PACS 602', 'The Practice of Peace', 0.5, 'SEM', 'This course examines the characteristics and skills of effective peace practitioners, with particular attention to ways in which disciplines of peacemaking can be cultivated by individuals and nurtured by communities.  While investigating various roles of the third party, students will explore the practical responsibilities (or tasks) and functions of peace practitioners as well as the core values and qualities that may make them more effective interveners locally, nationally or globally.  Students will be introduced to peace research methodologies, research ethics, risk management, as well as various forms of communication used in documentation, analysis, and advocacy.', 'PACS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PACS 603', 'Building Civil Society', 0.5, 'SEM', 'This course explores operational aspects of civil society organizations such as visionary leadership, goal setting, evaluation, report writing, financial management, applied research skills, and human resource management.  Students will also examine codes of conduct and practice, including rules, laws and customary understandings that guide the work of civil society organizations.  Students will research contending views of civil society organizations and their complex relationships with government and business, thereby developing a philosophical and ethical framework for evaluating civil society action.', 'PACS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PACS 604', 'Conflict Analysis', 0.5, 'SEM', 'This course examines theoretical and practical frameworks for understanding conflict, with particular attention to structures and dynamics inhibiting peace.  The course provides students with some of the analytical skills needed to understand how conflicts develop and escalate, to identify factors that can lead to or sustain violence, and to map root causes of conflict (e.g., human rights violations, needs deprivation, cultural and religious differences, inequality, resource misuse and environmental degradation) at interpersonal, intergroup, and international levels.', 'PACS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PACS 605', 'Conflict Transformation and Peacebuilding', 0.5, 'SEM', 'This course explores the theoretical and practical foundations of various approaches to working with conflict to advance positive goals such as social equity and reconciliation.  Attention will be given to a range of conflict resolution methods and practices (facilitation, negotiation, mediation, arbitration, adjudication) as well as to principles of restorative justice and dynamics of collective peacebuilding practice.', 'PACS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PACS 610', 'Contemporary Nonviolent Movements', 0.5, 'SEM', 'Through comparative case studies, this course examines contemporary nonviolent movements that illustrate pacifist and other nonviolent strategies for advancing social justice and other high-value political goals.  Local, national and transnational campaigns that seek to shape the agenda for global change are examined alongside movements of more limited scope and ambition (e.g., national liberation movements, civil rights campaigns, struggles for democracy).  Throughout, attention will be given to trends in practice and to debates concerning the effectiveness, ethical significance, and current relevance of nonviolent change methods.', 'PACS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PACS 611', 'Reconciliation', 0.5, 'SEM', 'This course uses case studies to analyze the complex issues of trauma, abuse, historical injustice and violence - and investigates approaches to healing, forgiveness and reconciliation (including memory, testimony, tribunals and reparation/atonement).  The course explores theoretical and practical models for transforming relationships, including indigenous and non-formal mechanisms employed internationally, and teaches skills that are employed by effective agents of reconciliation.', 'PACS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PACS 612', 'Culture, Religion, and Peacebuilding', 0.5, 'SEM', 'This course explores cultural, religious and identity-based dimensions of conflict and conflict resolution, examining major patterns of human difference and their implications for contemporary peacebuilding practice.  Case studies, simulations and role plays are used to expose students to the practical reality of building a common peace in the midst of diversity.', 'PACS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PACS 620', 'Special Topics in Peace and Conflict Studies', 0.5, 'SEM', 'A seminar course investigating special issues related to peace and conflict.  Content may vary from year to year.  Course may be repeated if course topic differs.', 'PACS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PACS 621', 'Peace Research', 0.5, 'SEM', 'This course requires completion of a research project that develops a student''s capacity to do research for an applied objective.  The research may be to support a grant proposal, document and contextualize a need or a human rights abuse, analyze what various agencies are doing in the face of common challenges, or write an advocacy brief to a government.  Students are expected to demonstrate a high level of competence in research analysis and writing.', 'PACS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PACS 625', 'Internship', 1, 'RDG', 'The internship allows students to engage in experiential learnig with a research institute, non-governmental organizaton or other agency/entity that deals with peace and conflict issues.  Students are expected to read relevant texts before, during and after the field study, to engage in stubstantial research on th eissues addressed by the host agency/entity and to submit a report reflecting on what the field study/internship revealed about the integration of peace studies theory and practice.  Field study placements may be either in Canada or international contexts.  Departmental consent is required.', 'PACS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PACS 626', 'Conflict Resolution Skills Training', 0.25, 'SEM', 'This course provides a framework for students to do academic work related to specific conflict resolution skills training they have received through workshops sponsored by the Center for Extended Learning or the Certified Program in Conflict Management or other credible training organizations. Students will complete this additional academic work as a Directed Study supervised by Peace and Conflict Studies faculty.  This course is offered on a credit/no credit basis.  The course may be repeated once.', 'PACS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PACS 630', 'Governance of Global Economy', 0.5, 'SEM', 'A survey of the theoretical and public policy debates relating to regulation of the global economy, examined through case studies ranging from international banking an intellectual property rights, to labour and environmental standards and the control of illicit economic activity.', 'PACS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PACS 631', 'Theories of Globalization', 0.5, 'SEM', 'This course begins with examining discussions of the historical continuities and discontinuities in globalization, including the relationships between globalization, empires and imperialism.  It then turns to focus on an interdisciplinary selection of theoretical writings on contemporary globalization.  The course concludes with preliminary investigations of some particular topics in globalization studies: identity, gender and culture, structural adjustment and world economic institutions, global health, communal violence and gender, and resistance to globalization.', 'PACS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PACS 632', 'Post-War Reconstruction and State Building', 0.5, 'SEM', 'Rebuilding states in the aftermath of conflict and state failure represents one of the foremost challenges facing the international community.  The post-Cold War era has shown that weak states represent as great a threat to international security and stability as strong ones.  The transition from war to peace and state failure to stability in these states can be conceptualized as encompassing three separate but interrelated transitions, in the economic, political and security spheres.  The course will deconstruct and analyze this triple transition, examine both its theoretical roots and practical application with reference to a number of recent case studies.', 'PACS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PACS 633', 'Human Rights in the Globalized World', 0.5, 'SEM', 'The course is a study of international and local responses to human rights abuses in the contexts of economic globalization and proliferation of armed violence.  It examines major debates on international human rights.  It also deals with specific human rights situations in the developing/transitional countries.  Topics include: universalism and cultural relativism, global economic justice, rights to food and health, women''s and children''s rights, the rights of displaced civilians, human rights and R2P, prospects for transitional justice.', 'PACS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PACS 634', 'Security Ontology-Theory', 0.5, 'RDG', 'This is a seminar in the ontology of security.  Security is a contested concept, and in this course we ask what it is and how best to pursue it.  What do we mean by security?  What are we trying to protect?  From what? Why? How do we do it?  We begin by considering the concept of security in the abstract, and we then proceed to explore various specific conceptions. Along the way we encounter both traditional and non-traditional approaches to security.', 'PACS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PACS 635', 'Security Governance: Actors, Institutions, and Issues', 0.5, 'SEM', 'In this course we examine a range of "security" issues on the global agenda - both traditional and non-traditional - and examine recent and possible future institutional and policy responses.  Issues examined include nuclear proliferation, terrorism, intrastate conflict, resource and territorial disputes, climate change, drugs, disease, and migration.  Students will have an opportunity to research in depth a specific security issue of their choice.', 'PACS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PACS 650', 'Sustainable Cities', 0.5, 'LEC, RDG, SEM', 'This course surveys the dominant trends in human settlement since the industrial revolution.  Emphasis is placed on selected problems (e.g., provision of basic services such as water supply and sanitation, waste disposal, expanding ecological footprints) faced by cities of various sizes (from mid-sized to mega), the resources available to deal with them, and the new approached to sustainability.', 'PACS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PACS 651', 'Economics for Sustainable Development', 0.5, 'LEC, RDG, SEM', 'This course introduces students to the history, theories and practices of development economics.  Select issues such as trade, (Public and private) capital flows, transnational corporations, technological change and innovation, agricultural and industrial policy and production, poverty and reduction, structural adjustment, etc. are treated, as are recent developments in globalization and global economic governance.', 'PACS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PACS 652', 'Water and Security', 0.5, 'LEC, RDG, SEM', 'The course will provide students with comprehensive background knowledge relevant to the increasingly important policy challenge of ''water security''.  The course will explore how the multiple levels of water security - human, community, state, international, global - require broad but considered policy inputs.  Emphasis will be placed on the interdependencies of difference sectors (climate security, food security, energy security) that interact within a ''web'' of water security.', 'PACS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PACS 660', 'Justice and Gender', 0.5, 'LEC', 'Theories of justice are concerned with the distribution of the basic goods of society - money, power, status, leisure, and so on. One would expect that they would be of particular interest to feminist theory, which is also concerned with the distribution of these goods. This course will consider how the gender system fares from the standpoint of liberal justice, and to what extent the promises of liberal justice can be used to overturn the unequal treatment of women. The issues of equality and difference will also be explored.', 'PACS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PACS 661', 'Ethnic Conflict and Conflict Resolution I', 0.5, 'SEM', 'This course examines the causes of ethnic conflict but focuses in particular on the strategies which states use to manage or resolve such conflicts. The review of state strategies is comprehensive in nature, and includes approaches which are morally unacceptable as well as approaches which many consider morally desirable.', 'PACS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PACS 662', 'Conflict and Conflict Resolution', 0.5, 'SEM', 'A graduate level survey of theories of conflict resolution drawn from the international relations, comparative politics, and peace studies.  Why do we have violent political conflict, and how can it be resolved?  How and why do wars begin and end?  This course focuses on political violence and conflict resolution between groups, including but not limited to states.', 'PACS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PACS 670', 'War and Peace in Christian Thought', 0.5, 'LEC', 'An examination of Christian teaching on war and peace from the early Church to the present, including the "just war" theories of Augustine, Aquinas, the Reformers and recent Catholic statements, as well as the pacifist views of Quakers, Mennonites and Brethren.', 'PACS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PACS 671', 'The Bible and Peace', 0.5, 'LEC', 'An examination of diverse biblical views of peace in relation to war, justice, and salvation with attention to their relevance for the contemporary quest for peace.  Formerly MTS 576.', 'PACS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PACS 672', 'Christianity''s Encounter with Other Faiths', 0.5, 'SEM', 'This course will examine several contemporary theological responses to the encounter of Christianity with other faiths.  The meaning and dynamics of inter-religious dialogue and the resources within the Christian faith for such an encounter will be explored.  Formerly MTS 657.', 'PACS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PD 1', 'Career Fundamentals', 0.5, 'LEC', 'This course is a combination of career development advice and work search information. Topics include preparing r√©sum√©s, developing interview skills and preparing for success in the workplace. Program specific information is provided for students pursuing different forms of work integrated education.', 'PD');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PD 2', 'Critical Reflection and Report Writing', 0.5, 'LEC', 'This course will develop students'' analytical, critical thinking, and report writing skills by focusing on critical reflection and thinking, analysis, and best practices in report writing. This course will lead students through the creation of a report to the co-op guidelines for work-term reports.', 'PD');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PD 3', 'Communication', 0.5, 'LEC', 'In this course, students will come to understand the nature and function of communication, particularly communication in the workplace, with a view to assessing, monitoring, and improving their own communications skills. At the end of this course, students will be equipped to converse cordially with peers; collaborate more effectively in teams; give and receive clear instructions and messages; resolve conflicts amicably; and appreciate the ethical dimensions of workplace communication.', 'PD');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PD 4', 'Teamwork', 0.5, 'LEC', 'An introduction to the processes and skill required of high-performance teams. Application of teamwork skills to decision making, conflict resolution and leadership. Development of self-awareness and relational skills to improve one''s ability to collaborate effectively, give and receive assistance, and empower others. Personal reflection and case discussion is integrated with observations of teamwork in the co-op experience work environment.', 'PD');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PD 5', 'Project Management', 0.5, 'LEC', 'This course is intended to provide students with knowledge of basic project management techniques and practices while placing these within their organizational context. Students should understand how these techniques and practices are used and the human issues in their application.', 'PD');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PD 6', 'Problem Solving', 0.5, 'LEC', 'This course provides students with the knowledge and skills needed to problem solve effectively in today''s workplaces. Emphasis is on understanding how both critical and creative thinking can be applied to workplace problems as well as identifying the barriers that can interfere with the problem solving process. Students will have the opportunity to apply a variety of problem solving strategies in order to develop their own Problem Solving Action Plan.', 'PD');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PD 7', 'Conflict Resolution', 0.5, 'LEC', 'This course will help students analyze and understand typical sources of conflict in the workplace, individual and group conflicts, mediation, negotiation, alternative dispute resolution, working with difficult people, and how to recover from conflict. Students will develop a personal vision/model of conflict and communication skills.', 'PD');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PD 8', 'Intercultural Skills', 0.5, 'LEC', 'This course provides students with the knowledge and skills needed to work effectively in today''s culturally and linguistically diverse workplaces. Emphasis is on understanding how cultural values shape our everyday behaviours, attitudes, thinking processes, and communication styles. Students will examine their own cultural preferences, become familiar with key models used to described cultures, and apply intercultural concepts and tools to analyze cultural issues in the workplace.', 'PD');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PD 9', 'Ethical Decision Making', 0.5, 'LEC', 'Borrowing from philosophy, game theory, and economics, this course equips students with the theoretical knowledge required to objectively evaluate and discuss ethical issues in the workplace. It provides students with practical advice regarding how to make ethical decisions in an increasingly competitive marketplace. Topics include the ethics of profit-making, why good people make bad decisions, moral hazard, and personal responsibility in group contexts.', 'PD');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PD 20', 'Engineering Workplace Skills I: Developing Reasoned Conclusions', 0.5, 'LEC', 'An introduction to critical thinking and communication in the workplace for co-op engineering students. Students will practise general critical thinking and communication skills to assess evidence, interpret textual and visual information, and work through ethical and professional situations. Topics include: the role of self-reflection in successful professional development, admitting ignorance to develop improved knowledge, understanding arguments, evaluating evidence, communicating conclusion and findings, recognizing and avoiding bias, and application to professional conduct.', 'PD');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PD 21', 'Engineering Workplace Skills II: Developing Effective Plans', 0.5, 'LEC', 'An introduction to problem formulation and problem strategies in the workplace for co-op engineering students. Students will practice defining problems, developing solution plans, and creating technical and non-technical presentations of plans. Topics include: translating client requirements into technical requirements, using SWOT analysis to identify high priority problems and issues, typical solution strategies, brainstorming, appropriate use of technology, assessing potential solution strategies for risk and impact on employer, public and environment, identifying specific tasks along with their priorities, developing and following work schedules, and communication to team members, organizational members, clients, and the public.', 'PD');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PD 22', 'Professionalism and Ethics in Engineering Practice', 0.5, 'LEC', 'This course provides a framework for understanding professionalism and ethics within society as reflected through practice in the field of professional engineering. Course topics on professionalism include: the history of engineering and professions, the licensing of professional engineers and the certification of the practice of offering engineering services to the public, and the regulation of professional engineering in Ontario through Professional Engineers Ontario. Course topics on ethics include: the philosophy of ethics and the Code of Ethics, the philosophy of justice, and the definition and disciplining of professional misconduct. Case studies will apply these principles to workplace situations including factors such as environmental ethics, whistle blowing, conflict of interest, and health and safety.', 'PD');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PDARCH 1', 'Portfolio Development', 0.5, 'LEC', 'This course will elaborate upon the graphic and communication conventions established in the fundamental architecture curriculum. Intermediate investigations in two dimensional representation and photographic manipulation are developed to enhance presentation skills within a professional and academic context. The topics covered in the course culminate in the generation of an architecture portfolio in readiness for job interviews.', 'PDARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PDARCH 2', 'Co-op Fundamentals for Architects', 0.5, 'PRA', 'This course will explore aspects of career development related to preparing for and being successful in co-op work terms, such as understanding Co-operative Education and Career Action (CECA) policies and procedures, using Waterloo Works, preparing r√©sum√©s, interviewing effectively, meeting employers'' expectations and preparing work-term reports.', 'PDARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 120', 'Introduction to the Profession of Pharmacy', 0.25, 'LEC', 'This course introduces students to the profession of pharmacy with its many opportunities. Topics include Canadian and global health care systems, the roles of health care professionals, the social determinants of health and the current and future practice of pharmacy. [Offered: W]', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PDARCH 3', 'Electronic Communications and Web Design', 0.5, 'LEC', 'This course will elaborate upon digital skills introduced in PDARCH 2. Students will learn the fundamentals of web design and will create a living, interactive, digital portfolio. Students will learn how to structure, design and manage web sites and web logs.', 'PDARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PDARCH 4', 'Writing, Editing and Research', 0.5, 'LEC', 'This course is designed to assist students in building a foundation of skills for academic research, editing texts and designing a research program in preparation for higher studies, essay writing and thesis development. This course offers concise and pragmatic approaches to the challenges of academic and professional writing, including issues in formatting, referencing, creation of bibliographies, footnoting and endnoting.', 'PDARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PDPHRM 1', 'Co-op Fundamentals', 0.25, 'RDG', 'This course explores aspects of career development related to preparing for and being successful in co-op work terms, such as preparing resumes, and interviewing effectively.', 'PDPHRM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PDPHRM 2', 'Communication for Pharmacy', 0.5, 'RDG', 'Students will have received strong grounding in key communication concepts during the first two academic semesters in the pharmacy program. This course requires that the student apply these concepts to their co-op work term and assess their own performance. Students are required to reflect on the development of their communication skills and formulate objectives to further develop their communication skills.', 'PDPHRM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PDPHRM 3', 'Drug Distribution for Pharmacy', 0.5, 'RDG', 'This course asks students to apply concepts from the pharmacy professional practice courses including: medication dispensing, jurisprudence, drug information, self care, patient safety and human resources. Students are required to reflect on the development of their technical and interpersonal skills and formulate objectives to further develop these skills. Pharmacy students must complete this PDPHRM course in their first or second work term.', 'PDPHRM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PDPHRM 4', 'Patient Safety for Pharmacy', 0.5, 'RDG', 'Patient safety has been integrated throughout the four year pharmacy curriculum. This course requires the student to apply patient safety concepts to their co-op work term environment.', 'PDPHRM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PDPHRM 5', 'Patient Care for Pharmacy', 0.5, 'RDG', 'Pharmacy students have had extensive classroom opportunities to develop their skills in providing direct patient care. This course asks students to apply their critical appraisal, patient assessment, and clinical problem-solving skills in the co-op work term to real-life scenarios. Students are expected to successfully provide pharmaceutical care to select patients in their work environment. Pharmacy students must complete this PDPHRM course in their second or third work term.', 'PDPHRM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PDPHRM 6', 'Drug Information for Pharmacy', 0.5, 'RDG', 'Students are required to apply critical appraisal concepts learned in the classroom to demonstrate their ability to effectively provide drug information and education.', 'PDPHRM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PDPHRM 7', 'Interprofessional Relations', 0.5, 'RDG', 'Students are required to demonstrate their ability to work collaboratively with other health care providers to optimize patient safety and improve health outcomes. Students will apply teamwork skills to decision making, conflict resolution and leadership. Personal reflection is integrated with observations of interprofessional relations in the co-op work environment.', 'PDPHRM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PDPHRM 8', 'Pharmacy Practice - Management and Leadership', 0.5, 'RDG', 'This course requires the student to apply the business/management concepts learned in the classroom to the workplace.', 'PDPHRM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 110', 'Systems Approach to the Study of the Human Body 1', 1, 'LAB, LEC', 'This integrated anatomy and physiology course and its continuation in PHARM 111 cover all the major human body systems using a systems approach. Major themes are the relationships between structure and function, and homeostasis of the cellular environment. [Offered: W]', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 111', 'Systems Approach to the Study of the Human Body 2', 0.5, 'LAB, LEC', 'This integrated anatomy and physiology course and its prerequisite PHARM 110 cover all the major human body systems using a systems approach. Major themes are the relationships between structure and function, and homeostasis of the cellular environment. [Offered: S]', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 120A', 'Introduction to the Profession of Pharmacy', 0.5, 'LEC, TUT', 'This first part of a two-part course sequence introduces students to the profession of pharmacy with its many opportunities. Topics include Canadian and global health care systems, the roles of health care professionals, and the current and future practice of pharmacy. Through community service learning, students will gain an understanding of citizenship, needs within a community and the people of a community. [Offered: W]', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 120B', 'Introduction to the Profession of Pharmacy', 0.5, 'LEC, TUT', 'This second part of a two-part course sequence introduces students to the profession of pharmacy with its many opportunities. Topics include Canadian and global health care systems, the roles of health care professionals, and the current and future practice of pharmacy. 
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 124', 'Pharmaceutics 1', 1, 'LAB, LEC', 'This course, and PHARM 125, consider the scientific and technical aspects important in dosage form design. The lectures and practical laboratory experiences emphasize the role of biopharmaceutics, preformulation and physical pharmacy in the development of safe and effective dosage forms. [Offered: W]', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 125', 'Pharmaceutics 2', 1, 'LAB, LEC', 'This course is a continuation of PHARM 124. [Offered: S]', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 126', 'Pharmaceutical Calculations', 0.13, 'TST, TUT', 'This course provides students with the tools necessary to carry out calculations in a wide range of settings. The scope of pharmaceutical calculations includes concentration expressions, physical and chemical properties, measurement of quantities, dosing calculations, rates of administration, and aligation methods.', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 127', 'Professional Communication Skills in Pharmacy Practice', 0.5, 'LEC', 'This course is designed to help students develop the core communication skills required for success in a career in Pharmacy. This is a foundational course. Key concepts introduced in this course are applied throughout the curriculum. [Offered: W]', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 128', 'Professional Communication Skills in Pharmacy Practice 2', 0.25, 'LEC, TUT', 'This second professional communications course builds on the skills acquired in PHARM 127, with increased focus on providing opportunities for students to practice and hone their skill. Student assignments focus on writing, presentation and patient interviewing skills. [Offered: S]', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 129', 'Professional Practice 1', 0.5, 'LEC', 'This course introduces students to the building blocks of pharmacy practice including medication dispensing, jurisprudence, drug information and patient safety. Patient care focuses on self-care and providing students with processes for assessing, educating, and counselling patients about non-prescription medications. [Offered: W]', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 130', 'Professional Practice 2', 0.5, 'LEC', 'Professional Practice 2 continues to develop the foundational knowledge and skills introduced in PHARM 129. [Offered: S]', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 151', 'Foundation and Application of Health Informatics', 0.5, 'LEC, TUT', 'This course provides a fundamental understanding of Health Informatics. This will encompass the nature, key concepts, and applications of this discipline to address challenges in the health field. The material presented will target the high profile areas of Health Informatics and point the participants in the direction of broader and deeper explorations. [Offered: S, F]', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 131', 'Professional Practice Laboratory 1', 0.25, 'LAB, TUT', 'The knowledge acquired from PHARM 129 will be applied to simulated pharmacy situations and patient case scenarios. This will provide the opportunity for students to demonstrate their technical skills and clinical knowledge in a practical context. [Offered: S]', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 141', 'Introduction to Medicinal Chemistry, Toxicology and Pharmacology', 0.5, 'LEC', 'The basic principles of medicinal chemistry, toxicology, and pharmacology are introduced and examined in the context of the autonomic nervous system. Pharmacokinetics, adverse drug reactions, drug interactions, pharmacogenetics, and the body''s response to drug compounds are also discussed. The tools and principles discovered are applied to specific disease states as well as physiological systems, including the visual and cardiovascular systems. [Offered: S]', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 150', 'Introduction to Applied Pharmaceutical Sciences', 0.25, 'LEC', 'An introduction will be provided to major themes in applied pharmaceutical sciences with an emphasis on practical application in patient care settings. An overview of key topics in applied pharmaceutical sciences will be provided, including drug nomenclature, classification and categorization, rudimentary elements of structure-function relationships, principles of drug absorption, distribution, metabolism and excretion, and the drug discovery process, with application to current issues in practice such as substance abuse and use of medicines in society. [Offered: W]', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 220', 'Integrated Patient Focused Care 1', 1, 'LAB, LEC, TUT', 'This is the first of nine sequential courses that are designed to enable students to understand, integrate, and apply concepts from pharmacology, medicinal chemistry, toxicology, clinical pharmacokinetics, clinical biochemistry and pathophysiology, and applied microbiology to patient scenarios. Students will not only learn core principles from each of these disciplines, but they will also develop skills in critical appraisal, patient assessment, and clinical problem-solving. Ultimately, students will be able to make confident decisions regarding a patient''s care plan. The first course will cover core foundational knowledge. [Offered: F]', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 221', 'Integrated Patient Focused Care 2', 1, 'LAB, LEC, TUT', 'This is the second of nine sequential courses. Topics discussed through patient scenarios and assignments will focus on two to three body systems. [Offered: F]', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 222', 'Integrated Patient Focused Care 3', 1, 'LEC, TUT', 'This is the third of nine sequential courses. Topics discussed through patient scenarios and assignments will focus on two to three body systems. [Offered: S]', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 223', 'Integrated Patient Focused Care 4', 1, 'LEC, TUT', 'This is the fourth of nine sequential courses. Topics discussed through patient scenarios and assignments will focus on two to three body systems. [Offered: S]', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 224', 'Pharmacokinetic Fundamentals', 0.5, 'LEC, TUT', 'Pharmacokinetic principles and quantitative analysis will be used to describe the events that transpire following drug administration. The influence of physiological and biochemical processes on drug pharmacokinetics will be discussed.[Offered: F]', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 227', 'Health Systems in Society', 0.25, 'LEC', 'Building on PHARM 120 and PHARM 129, students will extend their understanding of the Canadian health-care system, including its structure, financing, regulation and policies. By means of relevant health-care examples, students will develop their skills in ethical analysis, and learn to apply basic principles of public policy analysis, political science, economics, and quality measurement. Students will also be exposed to some international drug issues and how they compare with Canada''s pharmaceutical system. The course emphasizes critical analysis and writing skills. [Offered: F]', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 228', 'Professional Practice 3', 0.25, 'LAB', 'Professional Practice 3 is a lab-based course where knowledge acquired in the introductory Professional Practice courses is applied to simulated pharmacy situations and patient case scenarios. This provides students with the opportunity to demonstrate their technical skill and clinical knowledge in a practical way and helps prepare students for their first co-op work experience. [Offered: F]', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 229', 'Professional Practice 4', 1, 'LAB, LEC', 'Professional Practice 4 combines lecture and lab components, focusing on providing students with the knowledge and skills to fulfil requirements of the expanding role pharmacists play in providing patient care. Physical assessment and an understanding of how analyses and devices inform decision-making are examples of topics that may be covered. [Offered: S]', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 232', 'Medical Microbiology', 0.25, 'LAB, LEC', 'This course applies microbiological methods (aseptic techniques, streak plating, culture handling, laboratory safety, etc.) to clinically relevant microbiological and immunological practice. Course material is applicable to and coordinated with Integrated Patient Focused Care courses. [Offered: F]', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 232L', 'Medical Microbiology Laboratory 1', 0.25, 'LAB, TUT', 'This course provides an introduction to microbiology, focussing on the understanding and application of basic microbiological methods (aseptic techniques, streak plating, culture handling, laboratory safety, etc). It also delves into clinically relevant micrbiological and immunological practice and focuses on the understanding and application of available methods for determination of microbes, demonstration of technique applications to infectious microbes and investigative case studies. Course material is applicable to and coordinated with Integrated Patient Focused Care modules. [Offered: W]', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 233L', 'Medical Microbiology Laboratory 2', 0.25, 'LAB, TUT', 'This course is a continuation of medical microbiology and immunological laboratory topics and techniques introduced in PHARM 232L. The course aims to provide advanced understanding and application of medically relevant techniques to identify infectious microbes, ability to efficiently design experiments to determine specific microbes, and effectively interpret data and develop hypotheses. The use of investigative case studies and demonstration is heavily emphasized. Course material is pertinent and coordinated with the Integrated Patient Focused Care modules. [Offered: F]', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 237', 'Applications of Analyses and Devices in Pharmacy and Medicine', 0.5, 'LAB, LEC, TUT', 'This course provides an overview of the methods and applications of devices and pharmaceutical analyses in the practice of pharmacy and medicine. By means of relevant case studies, the application of the analyses and devices used in home, clinic, and laboratory settings for screening, diagnosis, and monitoring are covered. Methodologies include immunossays, gel electrophoresis/Western blot, enzyme-based assays, spectrophotometry, fluroescence and chromatography.
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 252', 'Institutional Pharmacy Practice', 0.5, 'LEC', 'This course offers insight into how organizations/institutions manage patient health. It examines the structure, governance and delivery of services within health systems such as primary care, family health teams, acute care, home care and long-term care facilities. Processes within health care institutions for optimizing management of patients, operations, facilities and medications are all addressed. [Offered: S]', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 378', 'Advanced Women''s Health Pharmacotherapeutics', 0.5, 'LEC', 'This course will examine the pharmacotherapy of health conditions commonly experienced by women across their lifespan. Topics will include menstrual and reproductive disorders; contraception, pregnancy and childbirth; and common diseases that may be experienced differently by women. The course will also explore the ethics, commercialization and medicalization of women''s health. [Offered W, S, F]', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 262', 'Foundation and Application of Health Informatics', 0.5, 'LEC, TUT', 'This course provides a fundamental understanding of Health Informatics. This will encompass the nature, key concepts, and applications of this discipline to address challenges in the health field. The material presented will target the high profile areas of Health Informatics and point the participants in the direction of broader and deeper explorations. [Offered: F]', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 290', 'Seminars in Pharmacy 1', 0.13, 'SEM', 'Through the Seminar Series of courses students are exposed to a number of speakers who address relevant topics within a content area. Speakers may include subject-matter experts, patients, and health care professionals.', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 320', 'Integrated Patient Focused Care 5', 1, 'LEC, TUT', 'This is the fifth of nine sequential courses. Topics discussed through patient scenarios and assignments will focus on two to three body systems. [Offered: W]', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 321', 'Integrated Patient Focused Care 6', 1, 'LEC, TUT', 'This is the sixth of nine sequential courses. Topics discussed through patient scenarios and assignments will focus on two to three body systems. [Offered: W]', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 322', 'Clinical Application of Pharmaceutical Sciences', 0.5, 'LEC, TUT', 'This course presents leading-edge discoveries in the pharmaceutical sciences. Students will be required to define the clinical application of such discoveries by integrating information from multiple disciplines in order to develop strategies to solve fundamental health-care problems. [Offered: S]', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 329', 'Professional Practice 5', 0.25, 'LAB', 'Professional Practice 5 is a lab-based course that refines basic skills and addresses additional areas of expanding scope of practice for pharmacists. In particular, a component of the course provides students with an opportunity to acquire skills in immunizing patients. [Offered: W]', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 350', 'Fundamentals of Business Administration and Management', 0.5, 'LEC', 'This course teaches the strategic management principles behind marketing, operations, finance and accounting, and human resources management in an integrated format. [Offered: W]', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 391', 'Seminars in Pharmacy 2', 0.13, 'SEM', 'Through the Seminar Series of courses students are exposed to a number of speakers who address relevant topics within a content area. Speakers may include subject-matter experts, patients, and health care professionals.', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 361', 'Advanced Compounding', 0.5, 'LAB, TUT', 'Building upon experiences gained in Pharmacy 124 and 125 (Pharmaceutics 1 and 2), Advanced Compounding introduces the student to more complex formulations and their use in specific patient populations. Example topics include compounding for geriatric and pediatric patients, veterinary products, USP  and its application to extemporaneous compounding, and stability and QC testing. [Offered: W, S, F]', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 362', 'Advanced Patient Self Care', 0.5, 'LEC, TUT', 'Advanced Patient Self Care teaches students to critically evaluate the use of self-care therapeutic options such as Natural Health Products, Schedule II and III drugs and functional foods in various disease states and populations. Other topics include: the Natural Health Product approval process, regulatory issues for self-selection products and reimbursement for self-care services. [Offered: W, S, F]', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 363', 'Global Infectious Disease Management', 0.5, 'LEC, TUT', 'This course addresses global infectious diseases from a public health perspective in an integrated case study format. It focuses on the role of the pharmacist, mode of transmission of these diseases, prevention, control, emergency preparedness, appropriate use of vaccines and vaccine administration. [Offered: W, S, F]', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 364', 'The Pharmacist as Educator', 0.5, 'LEC, TUT', 'This course will introduce the basic learning theory and practice that pharmacists use as educators of specific audiences, such as patients, other healthcare providers, other pharmacists, etc. [Offered: W, S, F]', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 365', 'Biotech Pharma Business Strategy', 0.5, 'LEC, TUT', 'This course introduces students to the business aspects of managing new biotechnological pharmaceutical innovation. Students will learn to analyse and connect scientific principles underlying novel and incremental technologies with the commercialization process. [Offered: W, S, F]', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 366', 'Concepts in Nutritional Sciences', 0.5, 'LEC, TUT', 'This course focuses on the role of nutrition in the attainment of optimal patient health. Topics include nutrition for optimal growth and risk reduction of chronic disease; nutrition in special populations; functional foods and nutraceuticals; enteral and parenteral nutrition; and the role of the pharmacist in nutrition counseling. [Offered: W,S, F]', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 367', 'Pediatric Pharmacy', 0.5, 'LEC, TUT', 'This course introduces the student to the more complex pediatric therapeutics and pharmacy-specific issues associated with a pediatric population. [Offered: W, S, F]', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 368', 'Advanced Drug Information & Evidence-Based Medicine', 0.5, 'LEC, TUT', 'This course builds on the knowledge and skills students have acquired about the retrieval, analysis and use of drug information. Students will apply the principles of evidence-based medicine and critically appraise literature in responding to the needs of a variety of patient and professional audiences. [Offered: W, S, F]', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 369', 'Global Medical Aid', 0.5, 'LEC, TUT', 'This course will explore the opportunities and issues in providing global medical aid and global health issues. [Offered: W, S, F]', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 370', 'Personal & New Venture Financial Management', 0.5, 'LEC, TUT', 'This course covers topics that are relevant to the financial decision making of individuals, with a particular focus on future professionals who have any interest in being involved in new business ventures. Knowledge and skills learned will allow students to take advantage of the career diversity and resulting business opportunities related to pharmacy. [Offered: W, S, F]', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 371', 'Advanced Topics in Health Economics', 0.5, 'LEC, TUT', 'This course uses economic tools and principles to examine micro and macro health- and pharmaco-economics with respect to the organization, delivery, and financing of health care, health care policy development, and assessment of drug cost versus value. [Offered: W, S, F]', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 372', 'Strategic Global Health & Pharmacy Practice', 0.5, 'LEC, TUT', 'This course focuses on the challenges of providing health care and pharmacy-related services within a global context. The course will examine how cultural variations and socio-political factors affect drug therapy and patient care. The specific impact of pharmaceuticals/vaccinations on global health will be featured. [Offered: W, S, F]', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 373', 'Healthcare Delivery in Rural and Underserved Populations', 0.5, 'LEC, TUT', 'This course will develop students'' understanding of the unique challenges and opportunities in providing healthcare to rural and underserved populations. Through lectures and field work, students will examine topics such as health care disparities, interprofessional collaboration, the role of the rural pharmacist, and underserved patient case studies. [Offered: W, S, F]', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 374', 'Complementary and Alternate Medicine', 0.5, 'LEC', 'This Complementary and Alternate Medicine course is designed to give the student an overview of complementary and alternative healing practices and will heighten student awareness that CAM is multifactorial and often includes belief systems and practices other than drug therapy. [Offered: W, S, F]', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 375', 'Substance Abuse & Chemical Dependency', 0.5, 'LEC, TUT', 'This course serves as an overview of addiction, chemical abuse and chemical dependency and how pharmacists can impact those affected. Topics include prevention, identification, treatment options, clinical aspects of treatment, and an understanding of support systems available for those in recovery. [Offered: W, S, F]', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 376', 'Practicing Pharmacy with Diverse Populations', 0.5, 'LEC, TUT', 'This course will provide students with tools to optimize patient care when working with diverse communities. Diversity will be defined in its broadest sense encompassing a discussion in how differences in ethnicity, gender, sexual orientation, physical or mental ability etc. impact patient care. Students will learn about the unique needs of different populations and through a combination of lectures and case studies learn how to sensitively and effectively work with a variety of patients. [Offered: W, S, F]', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 377', 'Drug-Induced Disease', 0.5, 'LEC, TUT', 'Drug-Induced Disease will explore the most appropriate interventions for prevention, detection and management of drug-induced diseases. Emphasis will be placed on the role of the pharmacist as a proactive practitioner in minimizing morbidity and mortality from drug-induced diseases. [Offered: W, S, F]', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 400', 'Independent Study 1', 0.25, 'RDG, TUT', 'This elective is designed to permit students to pursue areas of personal interest and/or to gain personal experience in scientific research through a research project. PHARM400 typically involves a reading/survey based project involving a review of and collection of data from relevant scientific literature, and the synthesis of new information based upon this review. Research may be conducted in the School of Pharmacy, in a hospital, community pharmacy, pharmaceutical company, etc. A maximum of 1.0 credit may be taken as Independent Study courses.', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 401', 'Independent Study 2', 0.5, 'LAB, TUT', 'This elective is designed to permit students to pursue areas of personal interest and/or to gain personal experience scientific research through a research project. PHARM 401 requires a research based project and will typically include a review of pertinent scientific literature and generation and analysis of new data. Research may be conducted in the School of Pharmacy, in a hospital, community pharmacy, pharmaceutical company, etc. A maximum of 1.0 credit may be taken as Independent Study courses.', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 415A', 'Clinical Rotation: Integrated Care', 0.5, 'LAB, TUT', 'The first part of this two course series provides assessment of student readiness to enter practice. It consists of two components: Clinic, where students provide direct patient care in interprofessional clinical settings and Symposium, where students deliver seminars oriented to patients or other healthcare professionals.[Offered: W]', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 415B', 'Clinical Rotation: Integrated Care', 0.5, 'LAB', 'The second part of this two course series provides further assessments of student readiness to enter practice. It consists of two components: Clinic, where students provide direct patient care in interprofessional clinical settings and Symposium, where students deliver seminars targeted for patients or other healthcare professionals. [Offered: S]', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 420', 'Integrated Patient Focused Care 7', 1, 'LEC, TUT', 'This is the seventh of nine sequential courses. Topics discussed through patient scenarios and assignments will focus on two to three body systems. [Offered: W]', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 421', 'Integrated Patient Focused Care 8', 1, 'LEC, TUT', 'This is the eighth of nine sequential courses. Several special topics will be covered including pediatrics and geriatrics. [Offered: W]', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 422', 'Integrated Patient Focused Care 9', 1, 'LEC, TUT', 'This is the ninth of nine sequential courses. Complex integrated cases will be covered, along with special topics. [Offered: W, S]', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 428', 'Professional Practice 4', 0.25, 'LEC', 'This is a continuation course in Pharmacy Practice. It requires students to demonstrate a consolidation of knowledge from previous courses, and to draw upon and complement material from the Pharmacy Practice, Pharmaceutical Care, and Social/Administrative Pharmacy courses and apply them to discussion of issues related to the psychology of patient care and pharmacy practice. Students will continue to utilize and develop strategic thinking and critical appraisal skills. [Offered: S]', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 460', 'Leadership in Pharmacy', 0.5, 'LEC, TUT', 'This course focuses on application of leadership principles, human, organizational and motivational behaviour, and power relationships in the healthcare workplace. [Offered: W, S, F]', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 461', 'Advanced Patient Safety', 0.5, 'LEC, TUT', 'This course focuses on applying the Canadian Patient Safety Institute''s domains to projects with community partners and local health care agencies. Students will learn to develop strategies to integrate medication safety updates and implement recommendations into their practices. [Offered: W, S, F]', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 462', 'Interprofessional Case Management', 0.5, 'LEC, TUT', 'This course focuses on developing the enhanced communication and teamwork skills necessary for working in interprofessional teams to optimize patient care. [Offered: W, S, F]', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 464', 'Advanced Therapeutic Concepts in Oncology', 0.5, 'LEC, TUT', 'This course examines advanced oncology therapeutic concepts required as foundation for patient management in the inpatient, ambulatory, and community settings. Topics include a comprehensive review of cancer epidemiology and pathophysiology, therapeutic agents used in the oncology setting, management of cancer therapy toxicities, and treatment and palliation of cancer symptoms. [Offered: W, S, F]', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 465', 'Critical Care & Emergency Medicine for Pharmacists', 0.5, 'LEC, TUT', 'This course focuses on the types of care provided by interdisciplinary health team members involved in caring for critical care and emergency room patients. Invited respiratory therapists, nurses, dieticians, pharmacists, social workers and intensivists will discuss critical care topics such as sepsis, respiratory insufficiency, toxicology, ethical deliberations and end of life care. [Offered: W, S, F]', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 466', 'Advanced Geriatric Care', 0.5, 'LEC, TUT', 'This course focuses on key concepts of ageing that are related to geriatric pharmacotherapy. Topics include: the biology and demography of ageing, optimal medication use in older people, disease presentation, geriatric assessment, roles for pharmacists and challenges and complexity of geriatric care. [Offered: W, S, F]', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 467', 'Management of Oral Anticoagulation Therapy', 0.5, 'LEC', 'This course provides students with the knowledge and skills to effectively and safely manage oral anticoagulation therapy under a medical directive. It contains both an online, self-paced learning module and computer simulated patient interactions that are designed to recreate the experience of practicing in an anticoagulation clinic. [Offered: W, F]', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 468', 'Clinical Neurology in Family Practice', 0.5, 'LEC, TUT', 'This course will provide students the opportunity to apply clinical knowledge and skills to manage pharmacotherapy problems in patients with neurological disorders. It will contain an online, self-directed learning module (with a focus on patient assessment, obtaining pertinent patient history, assessment of drug-related problems and review of drugs used in neurological disorders) and an experiential mini-clinical rotation comprised of visits to a neurology as well as to a pain clinic. [Offered: W, S, F]', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 469', 'Pharmacoepidemiology and Pharmacy Practice', 0.5, 'LEC, TUT', 'The course aims to promote an enhanced understanding of conceptual and methodological issues essential for effective critical appraisal of the relevant clinical literature and ultimately, improved patient care. Key topics to be covered will include: basic concepts of epidemiology; data sources and measures, study design and methodological issues in pharmacoepidemiology; and, applications relevant to population-based research and pharmacy. [Offered: W, S, F]', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 470', 'Advanced Medical Writing', 0.5, 'LEC, TUT', 'This course will refine students'' writing skills. Students will have opportunities to produce a variety of different documents with a focus on preparing documents suitable for publication. Topics will include audience analysis, modification of content to conform to the requirements of different journals, succinct and clear representation of data and study findings, coordinating the editing process with multiple authors etc. [Offered: W, S, F]', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 471', 'Selected Topics in Medicinal Chemistry', 0.5, 'LEC, TUT', 'Students will develop their understanding of physiochemical properties, drug design and pharmacological actions. A series of case studies on clinical drugs will be presented with major emphasis on their design, synthesis, reaction mechanisms and structure-activity relationship studies. A section of the course will address the pathophysiology and pharmacology of diseases affecting the central nervous system. [Offered: W, S, F]', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 472', 'Community Practice in a Changing Environment', 0.5, 'LEC, TUT', 'This course will prepare pharmacy students to provide services in an advanced community pharmacy practice environment. Students will explore, critique and develop innovative clinical services for the community setting. Students will also develop change management and critical thinking skills. [Offered: W, S, F]', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 490', 'Seminars in Pharmacy 2', 0.13, 'SEM', 'This course entails a weekly seminar on topics in Pharmacy. The range of materials to be presented include current issues, special topics demonstrating new trends in patient care and treatment, competing perspectives in the pharmaceutical arena, and scopes of Pharmacy practice.', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 491', 'Seminars in Pharmacy 3', 0.13, 'SEM', 'Through the Seminar Series of courses students are exposed to a number of speakers who address relevant topics within a content area. Speakers may include subject-matter experts, patients, and health care professionals.', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 495', 'Advanced Topics in Patient Focused Care', 0.5, 'LEC, TUT', 'Update to the Integrated Patient Focused Care course series taken in the BSc Pharmacy curriculum. Focus on management of complex patients, application of clinical pharmacokinetics and evidence-based advancements in pharmacotherapeutics. [Offered: W, S, F]', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 602', 'Grant Writing in the Sciences', 0.5, 'LEC', 'The course is designed to teach the student how to critically review the literature, ask the right scientific questions, generate hypotheses, and write a professional curriculum vita and a fundable grant proposal.  The course will include lectures from the instructor and guest speakers, small group discussion, student presentations and a final written grant proposal.', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 603', 'Selected Topics in Medicinal Chemistry', 0.5, 'LEC', 'This course provides an introduction to basic principles in medical chemistry such as physiochemical properties, drug design and pharmacological actions.  A brief overview of the theory and application of computer aided drug design principles will provide a basic understanding of the "in silico" drug design concepts.  The student will develop an understanding toward the concepts of peptide/petidomimetic design as therapeutic agents.  A series of case studies on clinical drugs will be presented with major emphasis on their design, synthesis, reaction mechanisms and structure-activity relationship studies.  A section of the course will address the pathophysiology and pharmacology of diseases affecting the central nervous system.  In addition, learning activities and assignments include a term paper submission and in class presentation of current topics in medicinal chemistry.', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 604', 'Gene Therapy', 0.5, 'LEC', 'Gene therapy offers extraordinary potential to treat both inherited and acquired diseases by using the body''s own machinery to produce a therapeutic compound or protein, or up-regulate/down-regulate specific cellular processes.  This course will examine the various extra- and intracellular barriers to DNA transfection, and the common vectors used in gene therapy.  Various routes of administration, such as injected, inhaled, and transdermal will be discussed in terms of their challengers, and the successes of recent formulations described in the literature.', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 605', 'Physical Chemistry and Application of Surfactants', 0.5, 'LEC', 'Surface active compounds, more commonly known as surfactants are found in nearly every aspect of day to day life, including motor oils, pharmaceuticals, cosmetics, detergents, and paints and inks to name a few.  This course will introduce the student to the structures of classical and emerging classes of surfactants and their fundamental properties that make these systems so useful.  The self-assembly of these compounds into micelles will be discussed in detail using both thermodynamic and kinetic modules and modern methods of characterizing these systems will be examined.  The application of surfactants in a variety of industries will be introduced.  The capstone activity of this course will entail a critical analysis of a relevant recent journal article, and presentation to the class.', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 606', 'Neuroscience in the 21st Century', 0.5, 'LEC', 'Intensively examines medical and technological advances in neuroscience.  Students will first develop a deep understanding of the structure and function of the central nervous system, neuronal cell biology and physiology, and neurotransmission.  Explores selected CNS disease states and identify mechanisms of neuropathology by biological, environmental, and iatrogenic causes.  Advances in technology and nanotechnology is and will continue to transform the treatment of diseases of the nervous system and these issues will be explored by expert guest lecturers.', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 607', 'Advanced Pharmaceutical Analysis', 0.5, 'LEC', 'This course provides an introduction to modern spectroscopic methods with emphasis on pharmaceutical analysis.  First few lectures will cover the theory and application of UV-Vis and IR spectroscopy.  A review of NMR spectroscopy will focus on commonly used techniques such as 1H NMR and 13C NMR and their application.  The student will get a practical experience in spectroscopic methods for structural elucidation by using UVS-VIS, IR and NMR spectrometers. In addition, a section will address the theory and application of mass spectrometry. Examples of drug molecules/pharmaceuticals and their spectroscopic characteristics will be discussed.  In addition, learning activities and assignments include problem sets and structural elucidation of an unknown organic molecule.', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 608A', 'Selected Topics in Pharmaceutical Sciences 1', 0.5, 'LEC, RDG, TUT', 'This course will discuss specialized topics that are related to the research interests of faculty members in the field of Pharmaceutical Sciences.  Topics could include, for example, the areas of drug delivery systems, diabetes, bacteriophage derived therapeutics, central nervous disorders, medicinal chemistry, pharmacokinetics, biophysical methods, bionanotechnology, nanomedicine among others.  This course may be presented in any of the following formats, reading based, lecture based, project based or online learning.', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 609', 'Advanced Pharmacokinetics', 0.5, 'LEC', 'This course provides statistical hands-on knowledge for the implementation of nonlinear mixed effects (NLME) models in the analysis of population pharmacokinetic data. In order to provide a foundation for NLME, the content builds from simple to multiple linear regression models and to linear mixed effects models. Exploratory and descriptive analyses as well as model implementation will be taught in R and Phoenix.', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 610', 'Topics in Drug Development', 0.5, 'LEC', 'This course will cover all aspects of drug development, from basic research through to post-marketing surveillance of drug safety and efficacy.  Faculty members from the UW School of Pharmacy and beyond will provide their expertise on aspects of the drug development process.  Students will gain valuable knowledge and appreciation of the drug development process from "bench to bedside".  Using real-life drug research problems, students will learn how identification of disease stat pathophysiology directs drug design, how medicinal chemistry optimizes lead molecule creation, how drug dosage forms are designed, pharmacokinetic studies, how animal models are used to test the efficacy and toxicity of lead compounds, the design and evaluation of clinical trials, drug manufacturing, perspectives from the pharmaceutical industry, as well as business and regulatory issues.', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 611', 'Special Topics in Pharmacy Practice', 0.5, 'LEC, RDG, SEM, TUT', 'This course will discuss specialized topics that are of mutual interest to the research of faculty members and graduate students in the field of Pharmacy Practice.  Topics could include, for example, pharmacy education; rural pharmacy practice; geriatrics and aging; pharmacoepidemiology; the role of technology in primary care; chronic disease management; expanded scope of practice; collaborative practice in different settings; health services design and care delivery; among others. This course may be presented in any of the following formats, reading based, lecture based, project based, or online learning.', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 100J', 'Introduction to Philosophy', 0.5, 'LEC', 'This course seeks to introduce students to the nature of philosophy. This is done through the examination of core texts and figures in the history of philosophy as well as in the discussion of perennial philosophical questions.', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 110A', 'Introduction to Philosophy: Knowledge and Reality', 0.5, 'LEC', 'An introduction to central issues in metaphysics and epistemology. Questions to be considered might include: "Can we know anything?", "Does God exist?", "Is the mind just a brain?", "Do human beings have free will?"', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 110B', 'Introduction to Philosophy: Ethics and Values', 0.5, 'LEC', 'This course is an introduction to philosophical thinking about ethics and value, that is, about what is good, right, fair, just, and worthwhile in life. Includes consideration of questions such as "What is the right thing to do?" and "Do human beings have free will?"', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 118J', 'Virtue and the Good Life', 0.5, 'LEC', 'An examination of the importance of virtue in general and of the cardinal virtues in particular (practical wisdom, justice, courage, and moderation) for the development of moral character and the enjoyment of the good life.', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 120J', 'The Meaning of Life', 0.5, 'LEC', 'We may have distinct ideas about the reason why we do this or that, but is there a point to our existence as a whole? What do or should we live for? Or is life essentially meaningless or even absurd? What do such questions mean and how can we best answer them? The views of different philosophers will be explored and compared.', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 145', 'Critical Thinking', 0.5, 'LEC, TUT', 'An analysis of basic types of reasoning, structure of arguments, critical assessment of information, common fallacies, problems of clarity and meaning.', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 200J', 'Aristotelian Logic', 0.5, 'LEC', 'An introduction to the understanding of how words are used, the formation of propositions, the construction of arguments and the examination of fallacies to help the student argue with order, facility and without error.', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 201', 'Philosophy of Sex and Love', 0.5, 'LEC', 'Classic and contemporary philosophical examination of sex and love. Questions considered might include the following: Do you love because your love-object is worthy, or do you find your love-object worthy because of your love for them? Does sex ever involve using persons as mere means to an end? Should it?', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 202', 'Gender Issues', 0.5, 'LEC', 'Philosophical analysis of issues relating to sex/gender. Questions considered might include the following: What, if anything, is the difference between sex and gender? How much of a role do facts about biology play in our ideas about sex and gender? How many sexes are there? What ethical issues arise for us in virtue of our gender?', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 204J', 'Philosophy and Culture', 0.5, 'LEC', 'An exploration of the nature of culture and its role in the life and development of the human being through an analysis of the assumptions of Western popular culture in such areas as technology and the internet, individual freedom, sexuality, and the global economy.', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 208', 'Philosophy Through Science Fiction', 0.5, 'LEC', 'Questions regarding such matters as the nature of knowledge, logic and language, mind and brain, space and time, causality, ethics, and politics are explored through classic philosophic texts and science fiction narratives.', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 209', 'Philosophy in Literature', 0.5, 'LEC', 'Philosophical themes will be explored through appropriate literary works.', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 210J', 'Human Nature', 0.5, 'LEC', 'What is a human being? The course examines this question from a philosophical perspective. Topics to be covered may include the soul, the body, emotions, the intellect, the will, relationships, sex, and human dignity.', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 215', 'Professional and Business Ethics', 0.5, 'LEC', 'Study of ethical and moral issues that typically arise in professional and business activity. What responsibilities to society at large do people in such business and professional activities as teaching, engineering, planning, architecture, and accounting have? How far should professional autonomy extend?', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 216', 'Probability and Decision Making', 0.5, 'LEC', 'This course covers a set of related topics in probability, inductive reasoning, game theory and decision theory which are of both theoretical and practical interest, having application to the philosophy of science, epistemology, political philosophy, ethics, political science, and economics. The course begins with probability theory, introduces utility theory, and discusses approaches to the theory of decision making with attention to their epistemological, social, and ethical implications.', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 218J', 'Foundations of Ethics', 0.5, 'LEC', 'What is ethics and what is it based on? What does it mean for an action, a person, or a life to be morally good? Can philosophy provide any objective and universal answers to moral questions? The views of different philosophers will be discussed.', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 219J', 'Practical Ethics', 0.5, 'LEC', 'An examination of contemporary ethical issues pertinent to our Western societies, such as abortion, euthanasia, the treatment of animals, the environment, and free expression.', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 220', 'Moral Issues', 0.5, 'LEC', 'This course surveys several controversial moral topics, and a range of philosophical views on each. Topics may include abortion, cloning, euthanasia and suicide, sexism, prostitution, pornography, torture, and animal rights.', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 221', 'Ethics', 0.5, 'LEC', 'An introduction to moral theories, including ones based on virtue, consequences, and rights and duties, with discussion of historical developments leading to those theories.', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 224', 'Environmental Ethics', 0.5, 'LEC', 'Philosophical perspectives on such issues as climate change, pollution, use of scarce resources, biodiversity, and our relations with animals and future generations. This course will also consider bioactivism and other approaches to environmental problems.', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 226', 'Biomedical Ethics', 0.5, 'LEC', 'An examination of ethical issues in the health sciences, such as the patient-doctor relationship, reproductive rights and technologies, genetic testing, allocation of medical resources, and end-of-life decisions.', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 227', 'Culture and Ethics', 0.5, 'LEC', 'This course surveys a variety of competing ethical positions that have been, and continue to be, adopted by different cultures around the world. Can two competing ethical viewpoints both be right? If not, then what grounds should we adopt to decide between them, and under what circumstances? How can we negotiate the difficulties that such differences pose for our practical judgment and moral theorizing? Students will engage these questions by reading texts in philosophical ethics and meta-ethics, and by applying what they have learned in case studies of ethical differences among cultures.', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 230J', 'God and Philosophy', 0.5, 'LEC', 'What is God? Does God exist? Can philosophy prove God or is agnosticism or atheism more reasonable? Is God compatible with evil and suffering or with a modern scientific worldview? Such questions will be explored from a variety of perspectives.', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 237', 'Introduction to the Philosophy of Religion', 0.5, 'LEC', 'A critical discussion of basic religious concepts. Among the topics covered will be faith, miracles, religious experience, immortality, arguments for the existence of God, and challenges to religious belief.', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 240', 'Introduction to Formal Logic', 0.5, 'LEC', 'An examination of classical propositional logic, covering proof methods, expressive completeness, soundness, and completeness. Also introduction to quantificational logic.', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 245', 'Critical Thinking About Science', 0.5, 'LEC', 'How does science work? Does anything fall outside the scope of scientific inquiry? Under what circumstances do scientific claims deserve our trust? Does corporate funding of research affect its credibility? What can scientific failures teach us about scientific success? By looking at case studies of recognized scientific successes and failures, this course addresses these questions in a way that emphasizes core philosophical concepts in epistemology, philosophy of science, and ethics.', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 250A', 'Great Works: Ancient and Medieval', 0.5, 'LEC', 'A historical survey of ancient and medieval philosophy in the Western tradition with an emphasis on metaphysics and epistemology.', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 250B', 'Great Works: Modern', 0.5, 'LEC', 'A historical survey of modern philosophy in the Western tradition with an emphasis on metaphysics and epistemology.', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 255', 'Philosophy of Mind', 0.5, 'LEC', 'This course will discuss fundamental questions concerning the nature of mind, including the relation between mind and body, the plausibility of commonsense views of the mind, and knowledge of other minds.', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 256', 'Introduction to Cognitive Science', 0.5, 'LEC', 'Cognitive science is the interdisciplinary study of mind and intelligence. This course will draw on philosophy, psychology, artificial intelligence, linguistics, neuroscience, and anthropology to address central questions about the nature of thinking. Topics discussed will include mental representation, computational models of mind, and consciousness.', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 258', 'Introduction to the Philosophy of Science', 0.5, 'LEC', 'This course considers fundamental questions concerning the nature of science. Consideration is given to such topics as scientific methodology, scientific revolutions, natural laws, and the debate about whether scientific theories represent reality, or just our perceptions of reality.', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 259', 'Philosophy of Technology', 0.5, 'LEC', 'What is technology? What is its relationship with modern science? Is technology a means or an end? Does the development of technology require a change in the way we conceive human nature and its place within the natural world? What new moral and metaphysical dilemmas does technology raise?', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 265', 'The Existentialist Experience', 0.5, 'LEC', 'An introduction to existentialism using both literary and philosophical texts.', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 271', 'Special Topics', 0.5, 'LEC', 'Special topics, as announced by the department.', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 305J', 'Philosophy of Nature', 0.5, 'LEC', 'A philosophical study of the most fundamental and common aspects of the natural world. After examining how such a study differs from and complements modern science, topics such as nature, matter, change, cause, time, and chance will be explored.', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 306J', 'Philosophy of Science', 0.5, 'LEC', 'A philosophical study of the approaches to the material world used by contemporary physical science. The nature and the value of the experimental method in the writings of scientists past and present will be examined.', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 311', 'Philosophy of Education', 0.5, 'LEC', 'This course surveys a number of philosophical examinations of education - its nature, purpose, methods and consequences.', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 315', 'Ethics and The Engineering Profession', 0.5, 'LEC', 'An analysis from the standpoint of philosophical ethics of moral issues arising in professional engineering practice. Issues include the social responsibility of engineers, conflict of interest and obligation, morally acceptable levels of risk, and moral implications of technology.', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 318J', 'Philosophy and the Family', 0.5, 'LEC', 'A philosophical examination of the family: its foundation, its purpose, its importance in personal growth and its relation to political community.', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 319J', 'Ethics of End-of-Life Care', 0.5, 'LEC', 'What options does a person reaching the end of life have and how can they best be cared for? How can we balance patient autonomy with the expertise of the health-care provider and the demands of the health-care system? This course will help students think philosophically and critically about issues like these in their cultural, historical, and legal context. Specific topics may include consent, human dignity, euthanasia, refusal or withdrawal of treatment, palliative care and holistic patient care, pluralism and diverse understandings of dying, and treatment of the elderly.', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 324', 'Social and Political Philosophy', 0.5, 'LEC', 'An examination of central issues in social and political philosophy. This course draws on both contemporary and historical readings to discuss issues such as: Does the state have legitimate authority to govern? What is the just way to distribute resources? What is the value of equality? Of liberty? What rights, if any, do we have, and why?', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 327', 'Philosophy of Law', 0.5, 'LEC', 'Basic themes in the philosophy of law. Issues include the nature of law and its relation to morality and politics, legal reasoning, the justification of punishment, and theories of rights, responsibility, and liability.', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 328', 'Human Rights', 0.5, 'LEC', 'What are human rights? Which do we have, and why? What are the practical implications of human rights, for both individuals and institutions? A comprehensive discussion of theory and history, of law and morality, and of national and international applications.', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 329', 'Violence, Non-violence, and War', 0.5, 'LEC', 'An exploration of the traditional debates concerning the legitimacy of violence and war as instruments in the pursuit of personal and political goals. The course critically examines a continuum of views from religious doctrines of non-resistance, to various forms of pacifism and non-violent resistance, "just-war theory", and political realism. The strategic arguments for political non-violent action are also considered.', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 331', 'Philosophy of Art', 0.5, 'LEC', 'What is art? What is beauty? What do the two have to do with each other? This course introduces students to some fundamental issues in the philosophy of art, and to a variety of philosophical views on these issues.', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 341', 'Intermediate Classical Logic', 0.5, 'LEC', 'A systematic investigation of classical predicate logic, with an emphasis on proving key results significant in other areas of philosophy. The course will include soundness and completeness, basic model theory including the L√∂wenheim-Skolem theorem, and key limitative results such as the Tarski''s indefinability theorem and G√∂del''s incompleteness theorems.', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 342', 'Non-Standard Logics', 0.5, 'LEC', 'An introduction to various non-standard logics at the centre of debates in various areas of philosophy, including metaphysics, epistemology, metaethics, and philosophy of science. Logics covered will include second order logic, classical modal, deontic, temporal, and epistemic logics, and intuitionistic logic. Other systems, such as relevance and linear logics, may also be discussed.', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 350', 'Topics in Epistemology', 0.5, 'LEC', 'Special topics in epistemology, as announced by the department. Potential topics include (but are not limited to) the epistemology of testimony, the epistemology of disagreement, normative social cognition, and the varieties and functions of ignorance.', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 362', 'Philosophy of the Social Sciences', 0.5, 'LEC', 'Problems about the fundamental methods and aims of the social sciences generally, and problems specific to Psychology, Sociology, Political Science, etc., and their relations to one another will be considered.', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 355', 'Topics in Metaphysics', 0.5, 'LEC', 'Special topics in metaphysics, as announced by the department. Potential topics include (but are not limited to) universals and particulars, the mind-body problem, realism and anti-realism, and the nature of time.', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 359', 'Philosophy of Mathematics', 0.5, 'LEC', 'A study of philosophical problems concerning mathematics. Topics discussed include formalism, intuitionism, logicism, the mathematical paradoxes, and other topics in foundations and metamathematics.', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 363', 'Philosophy of Language', 0.5, 'LEC', 'Topics in the philosophy of language such as meaning, truth, reference, speech acts, mental representations of syntax and semantics, psycholinguistics, and relations between logical and natural languages.', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 371', 'Special Topics', 0.5, 'LEC', 'Special topics, as announced by the department.', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 378', 'American Philosophy', 0.5, 'LEC', 'A survey of the leading ideas of classical American philosophers with attention to common themes, such as pragmatism.', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 380', 'History of Ancient Philosophy', 0.5, 'LEC', 'In this course students will examine one or more important figures, periods, or issues in ancient philosophy. Plato and Aristotle are among the philosophers who may be covered.', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 381', 'History of Ancient Philosophy 2', 0.5, 'LEC', 'From Aristotle to the close of classical antiquity.', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 382', 'Medieval Philosophy', 0.5, 'LEC', 'In this course students will examine one or more important figures, periods, or issues in medieval philosophy. Augustine, Boethius, Avicenna, Maimonides, Aquinas, and Scotus are among the philosophers who may be covered.', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 383', 'Medieval Philosophy 2', 0.5, 'LEC', 'The later period from the 13th century. Among those considered will be: Bonaventure, Aquinas, Scotus, and Ockham.', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 384', 'History of Modern Philosophy', 0.5, 'LEC', 'In this course, students will learn about one or more important figures, periods, or issues in modern philosophy. Descartes, Locke, Leibniz, Berkeley, Hume, and Kant are among the figures who may be covered.', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 385', 'History of Modern Philosophy 2', 0.5, 'LEC', 'Later period including Hume and Kant.', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 386', '19th- and 20th-Century Philosophy', 0.5, 'LEC', 'A course on one or more important figures or issues in 19th- or 20th-century philosophy, as announced by the department. The focus of the course may change each time it is offered. Potential figures include (but are not limited to) Hegel, Marx, Nietzsche, James, Dewey, Peirce, Frege, Carnap, and Russell.', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 402', 'Studies in Feminist Philosophy/Philosophy of Sex', 0.5, 'SEM', 'Special topics in feminist philosophy, women philosophers and/or the philosophy of sex, as announced by the Department of Philosophy.', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 403', 'Studies in Ancient Philosophy', 0.5, 'SEM', 'Special topics in ancient philosophy, as announced by the department.', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 404', 'Studies in Medieval Philosophy', 0.5, 'SEM', 'Special topics in medieval philosophy, as announced by the department.', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 405', 'Studies in Modern Philosophy', 0.5, 'SEM', 'Special topics in 17th and 18th century philosophy, as announced by the department.', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 407', 'Studies in 19th- and 20th-Century Philosophy', 0.5, 'SEM', 'Special topics in 19th- and 20th-century philosophy, as announced by the department.', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 408', 'Early 20th Century Philosophy', 0.5, 'SEM', 'Special topics in early 20th century philosophy, as announced by the department.', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 416', 'Studies in Probability and Decision Theory', 0.5, 'SEM', 'Special topics in probability and decision theory, as announced by the department.', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 418J', 'Ethics and Society', 0.5, 'LEC', 'This course examines the nature and purpose of community living as well as such traditionally controversial issues as private and public morality, the individual good and the common good, personal freedom and group responsibility.', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 420', 'Studies in Ethics', 0.5, 'SEM', 'Special topics in ethics, as announced by the department.', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 422', 'Studies in Political Philosophy', 0.5, 'SEM', 'Special topics in Political Philosophy, as announced by the department.', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 441', 'Studies in Logic', 0.5, 'SEM', 'Special topics in logic, as announced by the department.', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 447', 'Seminar in Cognitive Science', 0.5, 'SEM', 'An interdisciplinary discussion of central issues concerned with mind and intelligence such as representation, meaning, inference, and consciousness.', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 450J', 'Being and Existence', 0.5, 'LEC', 'A discussion of metaphysics as the kind of examination initiated by the Greeks (e.g., Parmenides and Aristotle) and renewed subsequently by many other philosophers (e.g., Aquinas and Heidegger), understood as ontology or the rational study of being as such. Notions such as reality, essence, existence, and analogy will be discussed.', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 451J', 'Thomas Aquinas', 0.5, 'LEC', 'An in-depth study of a particular theme in Thomas Aquinas. Specific topic to vary.', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 452', 'Studies in Epistemology', 0.5, 'SEM', 'Special topics in epistemology, as announced by the department.', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 455', 'Studies in Metaphysics', 0.5, 'SEM', 'Special topics in metaphysics, as announced by the department.', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 458', 'Studies in the Philosophy of Science', 0.5, 'SEM', 'Special topics in the philosophy of science, as announced by the department.', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 459', 'Studies in the Philosophy of Physics', 0.5, 'SEM', 'An investigation of philosophical issues raised by physics. Topics could include the interpretation of quantum theory, the arrow of time, or the nature of space and time. This course is suitable for students with a background in either philosophy, physics, or mathematics.', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 463', 'Studies in the Philosophy of Language', 0.5, 'SEM', 'Special topics in the philosophy of language, as announced by the department.', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 471', 'Special Topics', 0.5, 'SEM', 'Advanced study of special topics, as announced by the department.', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 472', 'Special Topics', 0.5, 'SEM', 'Advanced study of special topics, as announced by the department.', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 481', 'Special Topics', 0.5, 'SEM', 'Advance study of special topics, as announced by the department.', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 482', 'Special Topics', 0.5, 'SEM', 'Advanced study of special topics, as announced by the department.', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 498', 'Directed Reading in Special Areas', 0.5, 'RDG', 'Readings in selected topics chosen in discussion with instructor.', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 670', 'Fall Term Reading Course', 0.5, 'RDG', 'None', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 671', 'Winter Term Reading course', 0.5, 'RDG', 'None', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 672', 'Spring Term Reading Course', 0.5, 'RDG', 'None', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 673', 'Graduate Courses', 0.5, 'LEC', 'Graduate level courses covering specialized topics in philosophy.  These courses are often held in conjunction with 400 level philosophy courses, though of course the work requirements for graduate students are more stringent.  Students must pay attention to the restriction on the number of these courses they are allowed to count toward their degree.', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 735', 'Photoconductivity and Luminescence', 0.5, 'LEC', 'Electron processes in crystals, photoconductive processes. Electrode effects, imperfection and energy band transitions, scattering traps and trapping effects. Recombination kinetics, luminescence. Experimental methods and analysis.', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 674', 'Graduate Courses', 0.5, 'SEM', 'Graduate level courses covering specialized topics in the department''s fields of research concentration.', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 680A', 'Departmental Graduate Seminar', 0.5, 'SEM', 'A graduate level survey which acquaints students with a number of issues under active investigation in the philosophical literature in a particular area of central importance to philosophy.  Normally covers an area that falls within the department''s identified fields of research concentration.  The area will vary from year to year, but will always be one which is likely to inform students'' research whatever their eventual specialization.', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 680B', 'Departmental Graduate Seminar', 0.5, 'SEM', 'A graduate level survey which acquaints students with a number of issues under active investigation in the philosophical literature in a particular area of central importance to philosophy.  Normally covers an area that falls within the department''s identified fields of research concentration.  The area will vary from year to year, but will always be one which is likely to inform students'' research whatever their eventual specialization.', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 191', 'Electricity and Magnetism', 0.25, 'LEC, TUT', 'Supplemental course material in Electricity and Magnetism for preparation of upper year courses in this discipline. [Offered: W,S]', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 696', 'Directed Research for MA Candidates', 0.5, 'RDG', 'A one-term course which involves the writing of a research paper.  Non-thesis MA students must complete three such courses, each supervised by a different instructor, as part of the requirements for their degree.  Thesis MA students must complete one such course, supervised by the instructor who will supervise the thesis, as a preliminary stage in their thesis preparation.', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 698', 'Research Area Tutorials for PhD', 1, 'TUT', 'PhD candidates select two of these tutorials on topics approved by the department, each to be taken with a different instructor, as part of their degree requirements.  The goal of each area is to allow the student to reach a point where she or he is ready to make a research contribution to a particular area of philosophy.  One of the areas will normally be in the subject the student intends to pursue for the dissertation.', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHS 601', 'Foundations of Public Health', 0, 'LEC', 'An introduction to the philosophical, historical, ecological, legislative, and ethical foundations for understanding the practice of public health in Canada. The course is delivered in a 2-week block at the beginning of the MPH program sequence on the UW main campus.  For MPH student only.  The course must be successfully completed by MPH students before proceeding to other courses in the MPH program sequence.', 'PHS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHS 602', 'Capstone Integrative Seminar for Public Health', 0.5, 'LEC', 'The culminating experience in the UW MPH program, the Capstone provides an opportunity for students to demonstrate two very important dimensions of readiness to practice in public health:  the ability to work in a diverse and multidisciplinary team and the ability to integrate lessons learned from a variety of sources (including, but not limited to, course work and practicum) and bring them to bear on a concrete public health problem.  Graded on a Credit/Non-Credit basis.  (The course is delivered in a two-week block at the end of the MPH program sequence, on the UW main campus).', 'PHS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHS 603', 'Health Policy in Public Health', 0.5, 'LEC', 'A critical analysis of policy formulation, implementation and evaluation related to public health policy.  This course will provide an introduction to contemporary issues in public health policy, as well as an overview of the public health system in Canada.  The course will emphasize applied examples of public health within the Canadian context.', 'PHS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHS 604', 'Public Health and the Environment', 0.5, 'LEC', 'This course will explore the major sources of environmental stressors and types of environmental processes posing a risk to public health, and the mechanisms through which these interact with biological systems to exert adverse effects on human health.', 'PHS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHS 605', 'Quantitative Methods and Analysis', 0.5, 'LEC', 'This course is an introduction to biostatistics for those planning a career in public health.  Students will learn various biostatistical techniques, how to apply those techniques in the analysis of data from health studies, and how to interpret the results from those analyses.  Topics include types of data, descriptive statistics, probability, distributions of data, exploratory data analysis, confidence intervals, hypothesis testing, regression analysis, analysis of variance, and brief exposure to categorical data analysis and survival analysis.  Emphasis will be on conceptual understanding of topics as well as carrying out various data analysis applications.', 'PHS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHS 606', 'Principles of Epidemiology for Public Health', 0.5, 'LEC', 'This course introduces the principles, methods, and uses of epidemiology in the practice of public health.  After completion of this course, students will be able to critically read and interpret epidemiologic research and clearly communicate epidemiologic findings.  They will be familiar with health status measurement, data sources, screening, surveillance, outbreak investigation, and metholds to support program planning and evaluation.  Students will have a sound understanding of basic epidemiologic concepts, including prevalence, incidence, study designs, measures of association, bias, confounding and causal inference.', 'PHS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHS 607', 'Social, Cultural and Behavioural Aspects of Public Health I', 0.5, 'LEC', 'An introduction to the social, cultural and behavioural foundations of public health and outline the contributions of the social sciences to the planning and implementation of public health interventions.  Prerequisite PHS 601.', 'PHS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHS 608', 'Health and Risk Communication in Public Health', 0.5, 'LEC', 'An overview of theories, research findings, and applications of health communication and risk communication in public health.  The course examines theories of group interaction and mass communication related to community perceptions of public health problems and practices, the impact of new technologies on public health communication, intercultural issues in health communication, health literacy, social marketing, and links between health communication and public health policy.  Prerequisite PHS 601.', 'PHS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHS 609', 'Management and Administration of Public Health Services', 0.5, 'LEC', 'Approaches to strategic planning and organizational design, key concepts of human resource management in achieving the strategic objectives of public health organizations, and the fundamentals of operation planning, budgeting, financial management and project management.  Prerequisite PHS 601.', 'PHS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHS 611', 'The Health Care System', 0.5, 'LEC', 'This course examines the environment in which health systems operate, with a focus on policy formulation legislative frameworks, governance structures, and funding models.  Special attention is given to issues related to electronic health records and health information systems. A focus on international settings with strong track records in health informatics is central to this course.', 'PHS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHS 612', 'Data Structures and Standards in Health Informatics', 0.5, 'LEC', 'This course focuses on health data as a key component of all health informatics systems.  Topics include ontologies and other classification taxonomies found in health systems, data standards (with a focus on Canadian implementations of international standards), privacy and security of health data, client/patient assessment tools, and ethical considerations.', 'PHS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHS 613', 'Information Technology for the Health Professional', 0.5, 'LEC', 'This course introduces health professionals to the conceptual and physical building blocks of large scale distributed health information system.  Network architectures, web architectures, distributed processing, securing information, database structures, and other technical topics are covered with a specific focus on the concepts that need to be understood by health professionals to facilitate interaction with technical system specialists.', 'PHS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHS 614', 'Foundations of Program Evaluation', 0.5, 'LEC', 'Methods and applications of intervention evaluation in public health, as a means to ensure the effectiveness, accountability and continuous improvement of public health interventions. Basic evaluation models and concepts of evaluation design are provided as an introduction, including the relationship between intervention planning (organization and program planning) and evaluation. Case examples are used to illustrate methodological, political and ethical challenges of program evaluation in the public health context.', 'PHS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHS 615', 'Requirements Specification and Analysis in Health Systems', 0.5, 'LEC', 'This course introduces students to the requirements of definition phase of software development.  Models, notations, and processes for software requirements identification, representation, validation, and analysis are discussed, as are mechanisms to evaluate the efficacy and efficiency of health information systems.', 'PHS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHS 616', 'Decision Making and Systems Thinking in Health Informatics', 0.5, 'LEC', 'This course focuses on the process by which decisions are made and how decision-making processes can be codified and machine supported.  It will include topics such as reasoning and inference, techniques for managing uncertainty and information representation.  The course is anchored in a systems thinking approach to problem solving.', 'PHS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHS 617', 'Population Intervention for Disease Prevention and Health Promotion', 0.5, 'LEC', 'This course introduces the principals and methods for the effective design, selection and implementation of public health interventions to address socio-behavioural risk factors.  At completion, students will be able to: determine when interventions are justified; differentiate between individual and population level interventions; describe various types of interventions; use theory and evidence to select and design interventions including the appropriate mixes of intervention types, sites, and delivery systems.  Emphasis is placed on self learning through reading and problem-based learning.  Lectures highlight and link important  constructs, theories, perspectives, and bodies of evidence.', 'PHS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHS 623', 'Risk and Exposure Assessment in Public Health', 0.5, 'LEC', 'Methods used to assess human health risks associated with biological, chemical and physical exposures in the environment, focusing on hazard identification, dose-response assessment, exposure assessment, and risk characterization.  The course examines the strengths and weaknesses of various types of risk assessment approaches, the inherent uncertainties in each stage of risk assessment, and the interactive role of risk assessment and risk management in public health decision-making.  Course open to MPH students.  Others may be admitted with consent of instructor.', 'PHS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHS 624', 'Environmental Toxicology in Public Health', 0.5, 'LEC', 'Introduction to the underlying principles governing the interactions of foreign chemicals with biological systems, including a description of the human health effects that can occur as a result of chemico-biological interactions in the environment.  Course open to MPH students.  Others may be admitted with consent of instructor.', 'PHS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHS 631', 'Public Health Surveillance', 0.5, 'LEC', 'Fundamental principles of public health surveillance and monitoring, describe the source of public health data in Canada and other countries.  Topics covered include forecasting, information management and organization, and technological innovations for assessment, evaluation and program delivery.', 'PHS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHS 632', 'Health Economics and Public Health', 0.5, 'LEC', 'An exploration of the socioeconomic determinants of health, the role of economics in public health policy, and the uses of economic evaluation methods in public health decision-making.  Course open to MPH students.  Others may be admitted with consent of instructor.', 'PHS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHS 633', 'Water Quality and Public Health', 0.5, 'LEC', 'This course covers the principles and methods of water quality management, major water contaminants (including infectious agents) affecting public health, prevention strategies and regulatory monitoring and reporting activities in Canada.
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHS 634', 'Environmental Epidemiology for Public Health', 0.5, 'LEC', 'This course will cover epidemiological theories, methods, and applications used to address major environmental risks to public health, through a focus on the design, conduct, and interpretation of results from epidemiological studies of various designs. Public health risks will include the major pathways for exposure to microbial, chemical and physical hazards (e.g. ambient and indoor air, drinking and recreational water, food, physical contact).', 'PHS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHS 635', 'Public Health, Environment and Planning', 0.5, 'LEC', 'An exploration of the complex set of relationships among public health, environment and planning at the community and global levels.  Topics covered include the concept of sustainable development and how it relates to public health.', 'PHS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHS 636', 'Applied Epidemiology: Advanced Concepts and Applications for Public Health', 0.5, 'LEC', 'This course employs a case-study approach to demonstrate methods for investigation and control of communicable disease outbreaks and clusters of chronic diseases and injuries.  Course open to MPH students.  Others may be admitted with consent of instructor.', 'PHS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHS 637', 'Public Health Informatics', 0.5, 'LEC', 'This course examines the application of information technologies and information systems in public health practice.  Selected topics include managing information to deliver value; data standards in public health; privacy, confidentiality and security in public health; surveillance systems, informatics of toxicology and environmental public health.  Course open to MPH students.  Others may be admitted with consent of instructor.', 'PHS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHS 638', 'Selected Topics in Public Health', 0.5, 'LEC', 'Fall/Winter/Spring specific topics in public health.  Course open to MPH students.  Others may be admitted with consent of instructor.', 'PHS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHS 641', 'Professional Experience Practicum', 1.5, 'PRA', 'The supervised practicum is intended to provide the student with an opportunity to apply course learning in a health or health system setting.  The placement may involve any of the activities or functions of students'' field of study: public health, informatics, evaluation, etc.  A contract stipulating practicum objectives, and work to be completed and evaluated to meet these objectives, must be jointly approved by the student, the student''s field supervisor, the academic supervisor, and the practicum coordinator.  A written report by the student, together with a letter from the field supervisor, are used by the practicum coordinator to evaluate student performance at midterm and completion of the practicum.  Graded on a Credit/Non-credit basis.  Course open to School of Public Health and Health Systems students only.  Completion of all prerequisite coursework is required before commencing the practicum.', 'PHS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 711', 'Scattering Theory', 0.5, 'LEC', 'Review of potential theory of scattering. Applications chosen from elastic and inelastic neutron, X-ray, light, charged-particle, and atomic and molecular beam scattering.', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHS 661', 'Geographic Information  Systems and Public Health', 0.5, 'LEC', 'The course introduces the concepts, methods, and applications of Geographic Information System (GIS), with a focus on spatial analysis in public health.  The strong emphasis on assignments that require application of concepts and methods provides students with hands-on experience of important GIS applications in public health, including disease surveillance and control, disease risk estimation, health service planning, mapping disease, disease cluster detection, and analyzing environmental hazards at the neighbourhood, municipal, regional, and international level.  Students will learn the fundamentals of GIS, methodologies for analysing spatial data, study design, spatial data issues and interpretation of the results of analyses for GIS applications in public health.  There is no prerequisite for this course.  However, basic knowledge of statistics with an interest in information technology, public health, and mapping is highly desirable.', 'PHS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHS 662', 'Global Health', 0.5, 'LEC', '"Global Health" refers to health issues and concerns that transcend national borders, class, race ethnicity, and culture.   This course will examine Global Health issues and challenges in the 21st century from a Public Health perspective.   Issues range from socioeconomic factors, health systems, culture, human rights, ecological sustainability, humanitarian assistance, and war and peace.  Group work is emphasized.  There may be opportunities for service learning or interviews with frontline providers to explore Global Health in a local context.', 'PHS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHS 663', 'Human Development  and Health', 0.5, 'LEC', 'Course content includes normative and non-normative development across the lifespan as it is relevant to individual and population health and public health programming.  The human is treated as an independent system nested within an environmental system.  Consideration will be given to the interaction of the commonly recognized developmental domains, the individual''s context, and wellness.  Topics of discussion may include the impact on health and wellness of developmental continuity, resilience, transitions, and milestones.', 'PHS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 1', 'Pre-University Physics', 0, 'LEC', 'This course covers the topics in Ontario Secondary Schools essential for first year university physics. Topics include: motion in one and two dimensions using vectors as appropriate, Newton''s laws of motion applied using free body diagrams, energy, geometric optics, simple waves in one and two dimensions, electrical and magnetic effects. Successful completion of this course fulfils the University admission requirements where high school Physics is necessary. No University Credit. Offered by Distance Education only.', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 10', 'Physics Seminar', 0, 'SEM', 'This seminar brings together Physics students in all years to hear invited speakers, view physics-related films, and learn about current research. [Offered: F,W]', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 111', 'Physics 1', 0.5, 'LEC, TST, TUT', 'An introduction to physics for students intending to concentrate their further studies in biology, dentistry, medicine and paramedicine; includes particle kinematics and dynamics, energy and momentum conservation, and rotational mechanics.', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 111L', 'Physics 1 Laboratory', 0.25, 'LAB', 'For students who have taken or are taking PHYS 111.', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 112', 'Physics 2', 0.5, 'LEC, TST, TUT', 'A continuation of PHYS 111; includes simple harmonic motion, electrostatic force and potential, electric current and power, DC circuits, magnetic field and induction, wave motion, sound and optics.', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 112L', 'Physics 2 Laboratory', 0.25, 'LAB', 'For students who have taken or are taking PHYS 112.', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 115', 'Mechanics', 0.5, 'LEC, TST, TUT', 'Brief review of kinematics. Particle dynamics, work, energy, conservation of energy. Conservation of linear momentum, collisions, rotational kinematics and dynamics, conservation of angular momentum. Equilibrium of rigid bodies. [Offered: F]', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 121', 'Mechanics', 0.5, 'LEC, TST, TUT', 'An introductory course in physics for students intending to concentrate their future studies in the physical sciences, optometry or mathematics; includes particle kinematics and dynamics, forces in nature, work and energy, conservation of energy and linear momentum, rotational kinematics and dynamics, and conservation of angular momentum.', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 121L', 'Mechanics Laboratory', 0.25, 'LAB', 'For students who have taken or are taking PHYS 121. Students intending to follow a Physics or Mathematical Physics plan must take PHYS 131L.', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 122', 'Waves, Electricity and Magnetism', 0.5, 'LEC, TST, TUT', 'Simple harmonic motion, resonance, damped harmonic motion, wave motion and sound, electrostatic force and potential, electric current and power, capacitors, DC circuits, LRC circuits, introduction to magnetic fields Lorentz Force.', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 139', 'Scientific Computer Programming', 0.5, 'LEC, TUT', 'Introduction to scientific computer programming techniques as applied to problem solving in physics, with examples from first year mechanics. Simple sequential programs, control structures, functions, data types, data storage and scientific graphing. Introduction to object oriented programming. Numerical differentiation, integration, root determination and solution of linear equation systems. [Offered: W. The last offering of this course will be winter 2011.]', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 122L', 'Waves, Electricity and Magnetism Laboratory', 0.25, 'LAB', 'For students who have taken or are taking PHYS 122. Students intending to follow a Physics or Mathematical Physics plan must take PHYS 132L.', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 124', 'Modern Physics', 0.5, 'LEC, TST, TUT', 'An introductory course in modern physics; includes relativity, quantum physics, atomic physics, nuclear physics, particle physics and gravitation. [Offered: W]', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 125', 'Physics for Engineers', 0.5, 'LEC, TST, TUT', 'Oscillations; simple harmonic motion. Wave motion, travelling and standing waves; transverse and longitudinal waves, including sound. Geometrical optics; reflection and refraction. Physical optics; interference and diffraction. Quantum physics; quantization of radiation; hydrogen atom. [Offered: W,S]', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 131L', 'Mechanics Laboratory', 0.25, 'LAB', 'For students who have taken or are taking PHYS 121 and who intend to follow a Physics or Mathematical Physics plan; all other students who have taken or are taking PHYS 121 should select PHYS 121L.', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 132L', 'Waves, Electricity, Magnetism and Measurement Laboratory', 0.5, 'LAB', 'For students who have taken or are taking PHYS 122 and who intend to follow a Physics or Mathematical Physics plan; all other students who have taken or are taking PHYS 122 should select PHYS 122L.', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 175', 'Introduction to the Universe', 0.5, 'LEC', 'A survey course in astrophysics intended for Physics and Astronomy students. Astrophysical processes, the sky, the Sun, stars, black holes, the Milky Way and other galaxies, Big Bang cosmology.', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 175L', 'Introduction to the Universe Laboratory', 0.25, 'LAB', 'For students who are taking PHYS 175. [Offered: W]', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 222', 'Electricity and Magnetism 1', 0.5, 'LEC', 'Coulomb''s law, electric field, Gauss'' law, potential, capacitance, properties of dielectrics, current, resistance, electromotive force, D.C. circuits and instruments. [Offered: F]', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 223', 'Electricity and Magnetism 2', 0.5, 'LEC', 'Magnetic fields, induced electromotive forces, magnetic properties of matter, alternating currents, electromagnetic waves. [Offered: W]', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 224', 'Electricity and Magnetism for Life and Medical Physics', 0.5, 'LEC, TUT', 'Coulomb''s law, electric field, Gauss'' law, potential, current, resistance, electromotive force, D.C. circuits, magnetic fields, induced electromotive forces; applications include cell membrane potentials, action potentials, role of charge in structure and function of DNA, basis for the magneto-encephalogram and biomedical instrumentations. [Offered: F]', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 224L', 'Electricity and Magnetism Laboratory', 0.25, 'LAB', 'For students who have taken or are taking PHYS 224; only for Honours Life Physics students taking the Medical Physics Specialization.', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 225', 'Modeling Life Physics', 0.5, 'LEC, TUT', 'Introduction to modeling living systems and their components. Statistical methods in data analysis, curve fitting, including p values. Fourier series and transforms, structural analysis, including nearest neighbor distributions in biomedical applications. Introduction to methods for analysis of transport properties in biological systems. Use of computers in these areas. [Offered: F]', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 226', 'Geometrical Optics', 0.5, 'LEC, TUT', 'Fermat''s principle, reflection and refraction at plane and spherical surfaces, thin and thick lenses, optical instruments such as magnifiers, microscopes, telescopes, spectrometers, normal magnification. [Offered: F]', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 232L', 'Measurement Laboratory', 0.25, 'LAB', 'A laboratory that teaches programming (e.g.LabVIEW) for the computer interfacing of physics experiments and automatic data collection.', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 233', 'Introduction to Quantum Mechanics', 0.5, 'LEC, TUT', 'Introduction to quantization, wave-particle duality and the uncertainty principle The Schroedinger equation and solvable examples. Topics will include stationary states of particle-in-a-box, harmonic oscillator and the hydrogen atom. Quantization of angular momentum and spin. Introduction to approximation methods including time-independent perturbation theory. Modern applications of quantum mechanics. [Offered: W]', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 234', 'Quantum Physics 1', 0.5, 'LEC, TUT', 'Background of quantum physics. Introduction to formalism of quantum physics. Introduction to operators. Quantization, waves and particles. The uncertainty principle. The Schroedinger equation for one-dimensional problems: bound states in square wells. Harmonic oscillator; transmission through barriers.', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 236', 'Computational Physics 1', 0.5, 'LAB, LEC, TUT', 'Introduction to scientific computer programming techniques as applied to problem solving in physics, with examples from first year mechanics. Procedural programs, control structures, functions, and data storage. Numerical differentiation, integration, and solution of linear equation systems. Data analysis and visualization. [Offered: F]', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 239', 'Computational Physics 2', 0.5, 'LEC, TUT', 'Object-oriented programming applied to physical problems in astrophysics, electromagnetism, classical and quantum mechanics. Solution methods for differential equations and Monte Carlo techniques. [Offered: W,S]', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 241', 'Electricity and Magnetism', 0.5, 'LEC, TUT', 'Coulomb''s law, electric fields, Gauss'' law, electric potential. Capacitance, current, resistance, circuits. Magnetic fields, Ampere''s Law, induced electromotive forces, magnetic properties of materials. [Offered: F. The last offering of this course will be fall 2011.]', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 242', 'Electricity and Magnetism 1', 0.5, 'LEC, TUT', 'Electrostatics in vacuum, electric potential, conductors and currents, magnetostatics in vacuum, electromagnetic induction. [Offered: W,S]', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 242L', 'Electricity and Magnetism Laboratory', 0.25, 'LAB', 'For students who have taken or are taking PHYS 242.', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 246', 'Physical Optics', 0.5, 'LEC, TUT', 'Nature of light, wave motion, superposition of waves, interference of light, Fraunhofer diffraction and resolution limit of optical instruments; the diffraction grating and the analysis of light. Fresnel diffraction. Polarized light. Coherence of light, lasers, holography. Fibre Optics. [Offered: W]', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 256', 'Geometrical and Physical Optics', 0.5, 'LEC, TUT', 'Electromagnetic waves and the nature of light. Geometrical optics, aberrations. Physical Optics: interference, Fraunhofer and Fresnel diffraction, polarization. Optical instruments. [Offered: F]', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 256L', 'Optics Laboratory', 0.25, 'LAB', 'For students who have taken or are taking PHYS 256.', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 260L', 'Intermediate Physics Laboratory', 0.25, 'LAB, LEC', 'Experiments in selected physics topics.', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 263', 'Classical Mechanics and Special Relativity', 0.5, 'LEC', 'Newtonian dynamics of particles and systems of particles. Oscillations. Gravity and the central force problem. Lorentz transformations and relativistic dynamics. [Offered: W,S]', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 270', 'Astronomical Observations, Instrumentation and Data Analysis', 0.5, 'LEC', 'Telescopes, instrumentation and observations at different wavelengths (radio, sub-millimetre, infrared, optical, X-ray). Probability and statistics. Data archives and data analysis. [Offered: W,S]', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 270L', 'Astronomical Observations, Instrumentation and Data Analysis Laboratory', 0.25, 'LAB', 'For students who are taking PHYS 270. [Offered: W,S]', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 275', 'Planets', 0.5, 'LEC', 'Terrestrial and gas giant planets in the Solar System, asteroids and comets. Extrasolar planets and astrobiology. Star and planet formation. [Offered: F]', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 280', 'Introduction to Biophysics', 0.5, 'LEC', 'Introduction to a physical understanding of biological systems at macro and molecular scales. The course is intended for 2nd year science and engineering students and will cover a broad spectrum of topics in biophysics, as well as an introduction to neurobiology, nanotechnology and biotechnology. [Offered: W,S]', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 334', 'Quantum Physics 2', 0.5, 'LEC', 'Formalism of quantum mechanics. Operator approach to the harmonic oscillator. Quantum mechanics in three dimensions: Hydrogen atom, angular momentum and spin. Time-independent perturbation theory. Fine structure of hydrogen. Zeeman effect. Identical particles. The variational principle. Ground state of the helium atom. Applications in atomic and molecular physics. [Offered: W]', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 335', 'Condensed Matter Physics', 0.5, 'LEC', 'Overview of condensed matter ordered and disordered systems. Thermodynamic origin of order and phase transitions. Waves. Properties of the solid state. Crystals and fractals. Overview of Fourier Series. Reciprocal lattice. Diffraction. Classical elastic theory of the crystalline state. Electrons in a periodic potential, Band structure and Fermi surface. [Offered: W]', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 339', 'Scientific Computation 2', 0.5, 'LEC', 'Introduction to selected topics in numerical treatment of problems in condensed matter physics, astrophysics, optics, and/or biophysics. Examples of covered computational methods are: Monte Carlo method, Molecular Dynamics, optimization, and solution to partial differential equations. [Offered: W]', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 342', 'Electricity and Magnetism 2', 0.5, 'LEC', 'Magnetic fields, Ampere''s law, induced electromotive forces, magnetic devices, magnetic properties of materials, inductance, introduction to Maxwell''s equations and electromagnetic waves.', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 358', 'Thermal Physics', 0.5, 'LEC, TUT', 'Temperature and thermodynamic equilibrium. Work, internal energy and heat; first law, with examples. Kinetic theory of gases. Basic probability theory. Microscopic states and entropy. Absolute temperature, reversibility and the second law. Thermodynamic Functions and Maxwell''s relations. Phase transitions. Third Law. Other applications of thermodynamics. [Formerly PHYS 258. Offered: F, S]', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 359', 'Statistical Mechanics', 0.5, 'LEC', 'Fundamental postulate of statistical thermodynamics. Entropy. Microcanonical, canonical and grand canonical ensembles. Fermi-Dirac, Bose-Einstein and Boltzmann Statistics. Maxwell-Boltzmann velocity distribution. Applications to specific heat of solids, classical and quantum gases, electrons in metals, Planck''s law of radiation, and Bose-Einstein condensation. [Offered: W]', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 360A', 'Modern Physics Laboratory 1', 0.25, 'LAB', 'Selected experiments in mechanics, optics, electronics, atomic, molecular, nuclear and solid state physics.', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 360B', 'Modern Physics Laboratory 2', 0.25, 'LAB', 'Continuation of 360A.', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 363', 'Intermediate Classical Mechanics', 0.5, 'LEC', 'Non-inertial frames of reference. Calculus of variations. Lagrangian mechanics. Coupled oscillations and normal modes. Hamiltonian dynamics.', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 364', 'Mathematical Physics 1', 0.5, 'LEC, TUT', 'Sturm-Liouville theory. Legendre, Bessel and other special functions. Fourier series and introduction to Fourier transforms. Separation of variables. Green''s function. [Offered: F,S]', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 365', 'Mathematical Physics 2', 0.5, 'LEC', 'Introduction to probability and statistics. Complex variables, Cauchy-Riemann conditions, Cauchy integral formula, Taylor and Laurent expansions, residue theorem, contour integrals and applications. Fourier and Laplace transforms with applications. [Offered: W]', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 381', 'Cellular Biophysics', 0.5, 'LEC', 'Structure and function of cellular membranes, membrane lipid and protein structure and dynamics, membrane potential and ion transport, nerve conduction, vision and interaction of light with membranes, muscle contraction and energy transduction.', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 370L', 'Astronomy Laboratory 1', 0.25, 'LAB', 'Selected experiments in astronomy. [Offered: W]', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 375', 'Stars', 0.5, 'LEC', 'Stellar distances, masses, ages. Stellar interiors and atmospheres, star formation and evolution. Supernovae, white dwarfs, neutron stars, black holes. [Offered: W]', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 380', 'Molecular and Cellular Biophysics', 0.5, 'LEC', 'Cell structure and molecular composition; intermolecular interactions and hydration; protein structure and function; cytoskeletal filaments; DNA structure, packing and chromosomes; rate equations and biological dynamics (e.g., cytoskeletal polymerization); self-assembly; cell membranes; action potentials and biological electricity; molecular motors; cell motility. [Recommended PHYS 280/BIOL 280. Offered: F, S]', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 383', 'Medical Physics', 0.5, 'LEC', 'Applications of physics in medicine. The course will address basic concepts of medical imaging, nuclear medicine and radiation isotopes, radiation therapy and biomedical laser applications. Nuclear structure and binding energy. Nuclear decays, radioactivity and nuclear reactions. Interaction of radiation with matter. [Offered: W]', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 391', 'Electronics', 0.5, 'LEC', 'p and n materials, pn diodes, junction and FET transistors. Transistor amplifiers and their equivalent circuits. Operational amplifiers. Oscillators and power supplies. Noise. [Offered: W even years]', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 391L', 'Electronics Laboratory', 0.25, 'LAB', 'For students who have taken or are taking PHYS 391.', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 392', 'Scientific Measurement and Control', 0.5, 'LEC', 'Logic gates, flip-flops and shift registers, analogue to digital and digital to analogue conversion, feedback control systems, noise and spectral analysis, RF electronics. [Formerly PHYS 353. Offered: W odd years]', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 392L', 'Scientific Measurement and Control Laboratory', 0.25, 'LAB', 'For students who have taken or are taking PHYS 392.', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 393', 'Physical Optics', 0.5, 'LEC', 'Fourier Optics: diffraction, dispersion, coherence, imaging. Polarization: birefringence, Jones vectors, Mueller Matrices. Fresnel Equations: optics at an interface, thin films. [Offered: W even years]', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 394', 'Light-Matter Interactions', 0.5, 'LEC', 'Lasers: semi-classical interaction, properties, cavity, Gaussian beams. Radiation and Detectors. Introduction to nonlinear optics. [Offered: W odd years]', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 395', 'Biophysics of Therapeutic Methods', 0.5, 'LEC', 'The effect of radiation of various kinds on cells and tissues; elements of radiobiology and photobiology; molecular mechanisms of radiation-induced DNA damage and cell death, repair of radiation damage, dose-response relationships; tumor radiobiology and therapies, radiotherapy, photodynamic therapy, combination therapies; radiosensitizers and photosensitizers for cancer therapies; transdisciplinary advances in physical methods (ultrafast laser and ultrasound techniques) for biomedical applications.', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 396', 'Biophysics of Imaging', 0.5, 'LEC', 'Introduction to imaging concepts in biophysics with emphasis on the interrelationship between the physics principles of an imaging modality and the associated image reconstruction; methods for imaging at macroscopic to microscopic scales; computed tomography, magnetic resonance imaging, ultrasound, PET, optical imaging, optical and fluorescence microscopy, scanning probe microscopy (AFM, STM), optical tweezers, electron microscopy.', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 434', 'Quantum Physics 3', 0.5, 'LEC, TUT', 'Symmetries and conservation laws. Review of time-independent perturbation theory (degenerate and non-degenerate. Rayleigh-Schrodinger, Brillouin-Wigner and canonical perturbation theory; effective Hamiltonian derivation). Time-dependent perturbation theory (1st and 2nd order, adiabatic perturbation, Aharonov-Bohm effect). Fermi''s golden rule. Two-level systems. Emission and absorption of radiation (applications). Second quantization of electromagnetic field in free space; photons. Spontaneous emission and natural lifetime; Lamb shift. Elements of scattering theory. Introduction to the Dirac equation. [Offered: F]', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 435', 'Current Topics in Condensed Matter Physics', 0.5, 'LEC, TUT', 'Physics pertaining to collective and emergent phenomena in condensed matter systems. Examples of topics to be covered include: magnetism, superconductivity, heavy Fermion systems, quantum hall effect, protein folding, membranes, DNA physics, polymer physics, Modern experimental and theoretical techniques. [Offered: W]', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 437A', 'Research Project', 0.5, 'PRJ', 'A research project in any area of Physics approved by the course co-ordinator(s). The student is required to present a summary of the project orally and to submit a written report in a style suitable for publication. Some projects, especially those with an experimental emphasis, will likely continue as 437B. In these cases, students will submit an interim written report, in addition to the oral presentation. [Offered: F,W]', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 441A', 'Electromagnetic Theory', 0.5, 'LEC', 'Electrostatics, magnetostatics, and the macroscopic description of dielectrics and magnetic materials. Includes appropriate mathematical techniques, potential theory and the method of images. [Offered: F. The last offering of this course will be fall 2014.]', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 437B', 'Research Project (continued)', 0.5, 'PRJ', 'A continuation of the project undertaken in PHYS 437A. The student is required to present a summary of the project orally or by poster and to submit a written report in a style suitable for publication. [Offered: W]', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 442', 'Electricity and Magnetism 3', 0.5, 'LEC', 'Review of the origin of Maxwell''s equations, conservation laws, potential formulation of Maxwell''s equations and gauge choices, vacuum solutions of Maxwell''s equations (free space, waveguides, dispersion), solution to Maxwell''s equations for arbitrary sources (static and time-dependent problems), relativistic formulation of electrodynamics, macroscopic Maxwell''s equations and plane waves in macroscopic media. [Offered: F]', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 444', 'Introduction to Particle Physics', 0.5, 'LEC', 'This course introduces students to the standard model of particle physics. Topics covered include symmetries, particle classification, experimental methods and tools, scattering, Feynman diagrams, gauge theories, quantum electrodynamics, quarks, quantum chromodynamics, weak interactions, and the Higgs mechanism. [Offered: W]', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 454', 'Quantum Theory 2', 0.5, 'LEC', 'The Hilbert space of states, observables, and time evolution. Feynman path integral and Greens functions. Approximation methods. Co-ordinate transformations, angular momentum, and spin. The relation between symmetries and conservation laws. Density matrix, Ehrenfest theorem, and decoherence. Multiparticle quantum mechanics. Bell inequality and basics of quantum computing. [Offered: F]', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 460A', 'Advanced Laboratory 1', 0.25, 'LAB', 'Selected advanced experiments in mechanics, optics, electronics, atomic, molecular, nuclear and solid state physics.', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 460B', 'Advanced Laboratory 2', 0.25, 'LAB', 'A continuation of PHYS 460A.', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 461', 'Nanophysics', 0.5, 'LEC', 'Fundamentals of nanotechnology. Applications of nanotechnology in biology and medicine. Nanotechnology and society. Physical foundations of nanodevices. Conduction at the nanoscale. Modern nanodevices. [Offered: F]', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 467', 'Introduction to Quantum Information Processing', 0.5, 'LEC, TST', 'Basics of computational complexity; basics of quantum information; quantum phenomena; quantum circuits and universality; relationship between quantum and classical complexity classes; simple quantum algorithms; quantum Fourier transform; Shor factoring algorithm; Grover search algorithm; physical realization of quantum computation; error-correction and fault-tolerance; quantum key distribution. [Offered: W]', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 468', 'Introduction to the Implementation of Quantum Information Processing', 0.5, 'LEC, TUT', 'Photonic quantum computing, Superconducting qubits, NMR, Ion Trap quantum computing, Atomic quantum computing. [Offered: W]', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 475', 'Cosmology', 0.5, 'LEC', 'Robertson-Walker metric and Friedmann equations. Observational cosmology. Dark matter and dark energy. Gravitational lensing. Big Bang nucleosynthesis, the cosmic microwave background. Inflation. Structure formation.', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 480', 'Radiation Biophysics', 0.5, 'LEC', 'The effect of radiation of various kinds on cells and tissues; mechanisms of damage, repair theories, genetic effects, dose-response relationships; cancer radiotherapy (x-rays, electrons, neutrons, protons, negative Pi mesons); other types of cancer therapies used in conjunction with radiotherapy (e.g. hyperthermia); late effects of radiation; carcinogenesis; risk vs. benefit; applications.', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 476', 'Introduction to General Relativity', 0.5, 'LEC', 'Tensor analysis. Curved space-time and the Einstein field equations. The Schwarzschild solution and applications. The Friedmann-Robertson-Walker cosmological models. [Offered: W]', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 490', 'Special topics in Physics', 0.5, 'LEC', 'A lecture course offered in a particular branch of physics, subject to availability of instructor.', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 601', 'Perimeter Scholars International Quantum Field Theory 1', 0.5, 'LEC', 'Canonical quantization of fields, perturbation theory, derivation of Feynman diagrams, applications in particle and condensed matter theory, renormalization in phi^4.', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 602', 'Perimeter Scholars International Statistical Physics', 0.5, 'LEC', 'A brief review of ensembles and quantum gases, Ising model, Landau theory of phase transitions, order parameters, topology, classical solutions.', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 603', 'Perimeter Scholars International Quantum Field Theory 2', 0.5, 'LEC', 'Feynman Path Integral, abelian and nonabelian guage theories and their quantization, spontaneous symmetry breaking, nonperturbative techniques: 
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 604', 'Perimeter Scholars International Relativity', 0.5, 'LEC', 'Special relativity, foundations of general relativity, Riemannian geometry, Einstein''s equations, FRW and Schwarzschild geometries and their properties', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 605', 'Perimeter Scholars International Quantum Theory', 0.5, 'LEC', 'Schrodinger equation: free particle, harmonic oscillator, simple time-dependent problems.  Heisenbrerg picture and connection with classical physics.  Entaglement and non-locality.  Pure and mixed states, quantum correlators, measurement theory and interpretation.', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 606', 'Perimeter Scholars International Information and Data Analysis', 0.5, 'LEC', 'Probability, entropy, Bayesian inference and information theory.  Maximum likelihood methods, common probability distributions, applications to real data including Monte-Carlo methods.', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 607', 'Perimeter Scholars International Dynamical Systems', 0.5, 'LEC', 'Maps, flows, stability, fixed points, attractors, chaos, bifurcations, ergodicity, approach to chaos.  Hamiltonian systems, Liouville measure, Poincare theorem, integrable systems with examples.', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 608', 'Perimeter Scholars International Computation', 0.5, 'LEC', 'Common algorithms for ode and pde solving, with numerical analysis.  Common tasks in linear algebra.  Focus on how to write a good code, test it, and obtain a reliable result.  Parallel programming.', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 609', 'Perimeter Scholars International Conformal Field Theory', 0.5, 'LEC, SEM, TUT', 'This course is an introduction to the key ideas and techniques of conformal field theories.  These theories play a central role In the study of phase transitions in statistical physics and condensed matter systems, as well as in string theory.  Conformal field theories provide a theoretical laboratory in which the constraints imposed by symmetries allow for the exact solution of field theories, which have found important applications in both physics and mathematics.', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 610', 'Perimeter Scholars International Mathematics Physics', 0.5, 'LEC, SEM, TUT', 'This course will include the study of Perturbation Theory (Regular and Singular) Speeding Up Convergence:  Shanks Transformations and Richardson Extrapolation Taylor Series, Fourier Series and the Gibbs Phenomenon Using Diveregent Series: Generalised Summation Analytic Continuation, Riemann Surfaces and Branch Points Continued Functions and Pade Approximants Steiltjes Functions, the Herglotz Property and the Carleman Bound Feynman Diagrams.', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 611', 'PSI Condensed Matter Physics', 0.5, 'LEC, TUT', 'Theories of Condensed Matter Physics emphasizing the consequences of broken symmetries and the resulting collective modes which emerge, including broken translational symmetry and phonons, broken spin rotational symmetry and magnons, electronic properties of normal metals, and broken U(1) gauge symmetry in superconductors, excitations above the gap and collective modes in superconductors.', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 621', 'Perimeter Scholars International Cosmology', 0.25, 'SEM', 'FRW metric, Hubble expansion, dark energy, dark matter, CMB.  Thermodynamic history of early universe.  Growth of perturbations, CDM model of structure formation and comparison to observations, cosmic microwave background anisotropies, inflation and observational tests.', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 622', 'Perimeter Scholars International Standard Model', 0.25, 'SEM', 'Application of Yang-Mills theory to particle physics, QCD and its tests in the perturbative regime, theory of weak interactions, precision tests of electroweak theory, CKM matrix and flavour physics, open questions.', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 623', 'Perimeter Scholars International String Theory', 0.25, 'SEM', 'Superstring spectrum in 10d Minkowski, as well as simple toroidal and orbifold compactifications.  T-duality, D-branes, tree amplitudes.  Construct some simple unified models of particle physics.  Motivate the 10- and 11-dimensional supergravities.  Simple supergravity solutions and use these to explore some aspects of AdS/CFT duality.', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 624', 'Perimeter Scholars International Mathematical Physics Topics', 0.25, 'SEM', 'Differential forms, de Rham cohomology, differential topology and characteristic classes, monopoles and instantons, Kahler manifolds, Dirac equation, zero modes and index theorems.', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 625', 'Perimeter Scholars International Supersymmetry', 0.25, 'LEC, SEM, TUT', 'The following topics will be discussed:  The Lorentz Group: Properties and Representations; Manipulating Spinors; Supersymmetry Algebra and Representations; Superfields and Superspace; 4d Supersymmetric Lagrangians; Supersymmetry Breaking; Constructing the MSSM; Collider Phenomenology of Supersymmetric Theories; and Dark Matter.', 'PHYS');
