INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 701', 'Quantum Mechanics 1', 0.5, 'LEC', 'Review of formalism of nonrelativistic quantum mechanics including symmetries and invariance. Approximation methods and scattering theory. Elementary quantum theory of radiation. Introduction to one-particle relativistic wave equations.', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 702', 'Quantum Mechanics 2', 0.5, 'LEC', 'Concepts of relativistic quantum mechanics, elementary quantum field theory, and Feynman diagrams. Application to many particle systems. Students who have not taken PHYS 701 but have an equivalent background in Quantum Mechanics may seek the instructor''s consent to register in this course.', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 703', 'Introduction to Quantum Field Theory', 0.5, 'LEC', 'Review of relativistic quantum mechanics and classical field theory.  Quantization of free quantum fields (the particle interpretation of field quanta).  Canonical quantization of interacting fields (Feynman rules).  Application of the formalism of interactin quantum fields to lowest-order quantum electrodynamic processes.  Radiative corrections and renormalization.', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 704', 'Statistical Physics 1', 0.5, 'LEC', 'Statistical basis of thermodynamics; microcanonical, canonical and grand canonical ensembles; quantum statistical mechanics, theory of the density matrix; fluctuations, noise, irreversible thermodynamics; transport theory; application to gases, liquids, solids.', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 705', 'Statistical Physics 2', 0.5, 'LEC', 'Phase transitions. Fluctuation phenomena. Kubo''s theory of time correlation functions for transport and spectral properties; applications selected from a variety of topics including linearized hydrodynamics of normal and superfluids, molecular liquids, liquid crystals, surface phenomena, theory of the dielectric constant, etc. Students who have not taken PHYS 704 but have an equivalent background in Statistical Physics may seek the instructor''s consent to register in this course.', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 706', 'Electromagnetic Theory', 0.5, 'LEC', 'Solutions to Maxwell''s equations; radiation theory; normal modes; multipole expansion; Kirchhoff''s diffraction theory; radiating point charge; optical theorem. Special relativity; transformation laws for the electromagnetic field; line broadening; dispersion; Kramers-Kronig relations. Magnetohydrodynamics and plasmas.', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 708', 'Applications of Group Theory', 0.5, 'LEC', 'Introduction to group theory; symmetry, the group concept, representation theory, character theory. Applications to molecular vibrations, the solid state, quantum mechanics and crystal field theory.', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 709', 'Green''s Function Method', 0.5, 'LEC', 'Review of essential quantum field theory. Zero and finite temperature Green''s functions. Applications.', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 710', 'Atomic Physics', 0.5, 'LEC', 'Emphasis on atomic structure and spectroscopy. Review of angular momentum, rotations, Wigner-Eckart theorem, n-j symbols. Energy levels in complex atoms, Hartree-Fock theory, radiative transitions and inner shell processes. Further topics selected with class interest in mind, at least one of which to be taken from current literature.', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 712', 'Special Topics in Theoretical Physics', 0.5, 'RDG', 'None', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 713', 'Molecular Physics', 0.5, 'LEC', 'Angular momentum and the rotation of molecules; introduction to group theory with application to molecular vibrations; principles of molecular spectroscopy; spectra of isolated molecules; intermolecular interactions and their effects on molecular spectra; selected additional topics (e.g., electronic structure of molecules, experimental spectroscopic techniques, neutron scattering, correlation functions, collision induced absorption, extension of group theory to molecular crystals, normal coordinate analysis, etc.).', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 714', 'Nonlinear Optics', 0.5, 'LEC', 'Laser and nonlinear optics; techniques for controlling laser beams such as mode selection and mode locking; laser spectroscopy.', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 715', 'Nuclear Physics', 0.5, 'LEC', 'Static properties of nuclei; alpha, beta, gamma-decay; two-body systems; nuclear forces; nuclear reactions; single-particle models for spherical and deformed nuclei; shell, collective, interacting boson models.', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 716', 'Special Topics in Subatomic and Nuclear Physcis', 0.5, 'LEC', 'Offered on demand.  Course content depends on topic and instructor.', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 717', 'Intermediate and High Energy Physics', 0.5, 'LEC', 'Strong, electromagnetic and weak interactions. Isospin, strangeness, conservation laws and symmetry principles. Leptons, hadrons, quarks and their classification, formation, interactions and decay.', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 718', 'Special Topics in Subatomic and Nuclear Physics', 0.25, 'LEC', 'Offered on demand.  Course content depends on topic and instructor.', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 730', 'Liquid State Physics', 0.5, 'LEC', 'Physical properties of atomic liquids; distribution functions and equilibrium properties, elementary perturbation theories and integral equation theories; simple metals, simple computer simulation; viral expansions and thermodynamic derivatives of g(r); experimental determination of g(r).', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 731', 'Solid State Physics 1', 0.5, 'LEC', 'Phonons, electron states, electron-electron interaction, electron-ion interaction, static properties of solids.', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 732', 'Solid State Physics 2', 0.5, 'LEC', 'Transport properties; optical properties; magnetism; superconductivity; disordered systems.', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 733', 'Special Topics in Theoretical Condensed Matter Physics', 0.5, 'RDG', 'None', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 737', 'Special Topics in Surface Physics', 0.5, 'LEC', 'Offered on demand.  Course content depends on topic and instructor.', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 738', 'Special Topics in Condensed Matter and Materials Physics', 0.25, 'LEC', 'Offered on demand.  Course content depends on topics and instructor.', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 745', 'Special Topics in Experimental Physics', 0.5, 'LEC', 'Offered on demand.  Course content depends on topic and instructor.', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 748', 'Microprocessors in the Physics Laboratory', 0.5, 'LEC', 'Interfacing and programming of microprocessors for applications in physics, including signal averaging, auto- and cross-correlation analysis, multichannel spectrum analysis, and Fourier transformation. Consideration of hardware versus software methods for optimization of speed and system size.', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 747', 'Optical Electronics', 0.5, 'LEC', 'Optoelectronic component fabrication, light propagation in linear and nonlinear media, optical fiber properties, electro-optic and acousto-optic modulation, spontaneous and stimulated emission, semiconductor lasers and detectors, noise effects in fiber systems.', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 749', 'Special Topics in Experimental Physics', 0.25, 'LEC', 'Offered on demand.  Course content depends on topic and instructor.', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 751', 'Clinical Applications of Physics in Medicine', 0.5, 'LEC', 'This course provides an overview of the application of physics to medicine. The physical concepts underlying the diagnosis and treatment of disease will be explored. Topics will include general imaging principles such as resolution, intensity and contrast; x-ray imaging and computed tomography; radioisotopes and nuclear medicine, SPECT and PET; magnetic resonance imaging; ultrasound imaging and radiation therapy.', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 752', 'Molecular Biophysics', 0.5, 'LEC', 'Physical methods of determining macromolecular structure: energetics, intramolecular and intermolecular forces, with applications to lamellar structures, information storage, DNA and RNA, recognition and rejection of foreign molecules.', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 754', 'Special Topics in Biophysics', 0.5, 'RDG', 'None', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 757', 'Special Topics in Biophysics', 0.25, 'RDG', 'None', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 767', 'Quantum Information Processing', 0.5, 'LEC', 'Review of basics of quantum information and computational complexity; Simple quantum algorithms; Quantum Fourier transform and Shor factoring algorithm: Amplitude amplification, Grover search algorithm and its optimality; Completely positive trace-preserving maps and Kraus representation; Non-locality and communication complexity; Physical realizations of quantum computation: requirements and examples; Quantum error-correction, including CSS codes, and elements of fault-tolerant computation; Quantum cryptography; Security proofs of quantum key distribution protocols; Quantum proof systems.  Familiarity with theoretical computer science or quantum mechanics will also be an asset, though most students will not be familiar with both.', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 768', 'Special Topics in Quantum Information Processing', 0.5, 'LEC', 'Offered on demand.  Course content depends on topic and instructor.', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 769', 'Special Topics in quantum Information Processing', 0.25, 'LEC', 'Offered on demand. Course content depends on topic and instructor.', 'PHYS');
