INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 775', 'Inter-Institution Exchange', 0.5, 'SEM', 'At the Director''s discretion, a PhD or MSc student may receive course credit for a term of specialized studies at another institution. Formal evaluation is required.', 'PHYS');
