INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PSCI 214', 'Quantitative Analysis', 0.5, 'LAB, LEC', 'An introduction to the use of quantitative methods in Political Science. Only a rudimentary understanding of mathematics is required.', 'PSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PMATH 465', 'Geometry of Manifolds', 0.5, 'LEC', 'Point-set topology; smooth manifolds, smooth maps and tangent vectors; the tangent bundle; vector fields, tensor fields and differential forms. Other topics may include: de Rham cohomology; Frobenius Theorem; Riemannian metrics, connections and curvature.', 'PMATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PMATH 467', 'Algebraic Topology', 0.5, 'LEC', 'Topological spaces and topological manifolds; quotient spaces; cut and paste constructions; classification of two-dimensional manifolds; fundamental group; homology groups. Additional topics may include: covering spaces; homotopy theory; selected applications to knots and combinatorial group theory.', 'PMATH');
