INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PMATH 632', 'First Order Logic and Computability', 0.5, 'LEC', 'The concepts of formal provability and logical consequence in first order logic are introduced, and their equivalence is proved in the soundness and completeness theorems.  Goedel''s incompleteness theorem is discussed; making use of the halting problem of computability theory.  Relative computability and the Turing degrees are further studied.', 'PMATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PMATH 641', 'Algebraic Number Theory', 0.5, 'LEC', 'An introduction to algebraic number theory; unique factorization, Dedekind domains, class numbers, Dirichlet''s unit theorem, solutions of Diophantine equations.', 'PMATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PMATH 642', 'Fields and Galois Theory', 0.5, 'LEC', 'Normal series, elementary properties of solvable groups and simple groups, algebraic and transcendental extensions of fields, adjoining roots, splitting fields, geometric constructions, separability, normal extensions, Galois groups, fundamental theorem of Galois theory, solvability by radicals, Galois groups of equations, cyclotomic and Kummer extensions. Students without the required prerequisite may seek consent of the department.', 'PMATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PMATH 644', 'Rings, Modules and Representations', 0.5, 'LEC', 'Jacobson structure theory, density theorem, Jacobson radical, Maschke''s theorem.  Artinian rings, Artin-Wedderburn theorem, modules over semi-simple Artinian rings.  Division rings.  Representations of finite groups.  Finitely generated modules over principal ideal domains.   Students without the required prerequisite may seek consent of the department.', 'PMATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PMATH 646', 'Introduction to Commutative Algebra', 0.5, 'LEC', 'Module theory:  classification of finitely generated modules over PIDs, exact sequences and tensor products, algebras, localisation, chain conditions.  Primary decomposition, integral extensions, Noether''s normalisation lemma, and Hilbert''s Nullstellensatz.', 'PMATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PMATH 650', 'Lebesgue Integration and Fourier Analysis', 0.5, 'LEC', 'Lebesgue measure on the line, the Lebesgue integral, monotone and dominated convergence theorems, LP spaces, completeness and dense subspaces; separable Hilbert space, orthonormal bases; Fourier analysis on the circle, Dirichlet kernel, Riemann-Lebesgue lemma, Fejer''s theorem and convergence of Fourier series.', 'PMATH');
