INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PMATH 652', 'Topics in Complex Analysis', 0.5, 'LEC', 'The Riemann mapping theorem and several topics such as analytic continuation, harmonic functions, elliptic functions, entire functions, univalent functions, special functions. Students without the required prerequisite may seek consent of the department.', 'PMATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PMATH 665', 'Geometry of Manifolds', 0.5, 'LEC', 'Point-set topology; smooth manifolds, smooth maps, and tangent vectors; the tangent bundle, vector fields, tensor fields, and differential forms. Other topics may include: de Rham cohomology; Frobenius Theorem; Riemannian metrics, connections and curvature.', 'PMATH');
