INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PMATH 701', 'Graduate Algebra', 0.5, 'LEC', 'Isomorphism theorems, classical structures theorems for finite groups, nilpotent and solvable groups, free groups, presentation modules over Principal Ideal Domains (PIDs), Hilbert Basis Theorem, Groebner bases, Artin-Wedderbury Theorem, field extensions, decompositions, claculation of Galois groups.', 'PMATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PMATH 702', 'Graduate Analysis', 0.5, 'LEC', 'Zorn''s Lemma and the Axiom of Choice, cardinality, introduction to topological spaces, bases, nets, continuous functions and weak topologies, compactness, connectedness, Banach spaces, Contraction Mapping Principal, finite-dimensional spaces C(X) and C_O(X), Stone-Weierstrass Theorem, Arzela-Ascoli Theorem, Urysohn''s Lemma, idelas in C_O(X).', 'PMATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PMATH 733', 'Model Theory and Set Theory', 0.5, 'LEC', 'Model theory: the semantics of first order logic including the compactness theorem and its consequences, elementary embedding and equivalence, the theory of definable sets and types, quantifier elimination, and w-stability.  Set theory: well-orderings, ordinals, cardinals, Zermelo-Fraenkel axioms, axiom of choice, informal discussion of classes and independence results.', 'PMATH');
