INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PMATH 753', 'Functional Analysis', 0.5, 'LEC', 'Banach and Hilbert spaces, bounded linear maps, Hahn-Banach theorem, open mapping theorem, closed graph theorem, topologies, nets, Hausdorff spaces, Tietze extension theorem, dual spaces, weak topologies, Tychonoff''s theorem, Banach-Alaoglu theorem, reflexive spaces.', 'PMATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PMATH 763', 'Introduction to Lie Groups and Lie Algebras', 0.5, 'LEC', 'An introduction to matrix Lie groups and their associated Lie algebras: geometry of matrix Lie groups; relations between a matrix Lie group and its Lie algebra; representation theory of matrix Lie groups.', 'PMATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PMATH 764', 'Introduction to Algebraic Geometry', 0.5, 'LEC', 'An introduction to algebraic geometry through the theory of algebraic curves.  General algebraic geometry:  affine and projective algebraic sets, Hilbert''s Nullstellensatz, co-ordinate rings, polynomial maps, rational functions and local rings.  Algebraic curves: affine and projective plane curves, tangency and multiplicity, intersection numbers, Bezout''s theorem and divisor class groups.', 'PMATH');
