INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PMATH 810', 'Banach Algebras and Operator Theory', 0.5, 'LEC', 'Banach algebras, functional calculus, Gelfan transform, Jacobson radical, Banach space and Hilbert space operators, Fredholm alternative, spectral therorem for compact normal operators, ideals in C^*-algebras, linear functionals and states, Gelfand-Naimark-Segar (GNS) construction, von Neumann algebras, strong/weak operator topologies, Double Commutant theorem, Kaplansky''s density theorem, spectral theorem for normal operators.', 'PMATH');
