INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PSYCH 608A', 'Child Psychopathology & Psychotherapy', 0.5, 'SEM', 'This course is designed to familiarize clinical students with a range of child psychotherapy modalities, such as behavioural and cognitive therapies, parental guidance, family and narrative therapies, and play therapy. Only students in the Clinical Psychology program are permitted to take this course.', 'PSYCH');
