INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PSYCH 734A', 'Practicum in Supervision I', 0.5, 'PRA', 'The students in this fall course will serve as the primary supervisor for a case being seen by a junior therapist. Class will meet weekly to discuss assigned readings and to discuss the cases the students are supervising. The course instructor will also be following the supervised cases of the student therapists, although most of the supervision will be left to the senior trainee. Clinical students are required to take the course in their fourth or fifth year. Only students in the Clinical Psychology program are permitted to take this course. Graded on a Cr/NCr basis.', 'PSYCH');
