INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SE 102', 'Seminar', 0, 'SEM', 'General seminar. [Offered: W]', 'SE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SE 201', 'Seminar', 0, 'SEM', 'General seminar. [Offered: F]', 'SE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SE 202', 'Seminar', 0, 'SEM', 'General seminar. [Offered: S]', 'SE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SE 212', 'Logic and Computation', 0.5, 'LEC, TST, TUT', 'Formal logic. Proof systems and styles. Rudimentary model theory. Formal models of computation. Logic-based specification. Correctness proofs. Applications in software engineering. [Offered: F]', 'SE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SE 301', 'Seminar', 0, 'SEM', 'General seminar. [Offered: W]', 'SE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SE 302', 'Seminar', 0, 'SEM', 'General seminar. [Offered: F]', 'SE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SE 350', 'Operating Systems', 0.5, 'LAB, LEC, TUT', 'An introduction to the fundamentals of operating system function, and implementation. Topics include concurrency, synchronization, processes, threads, scheduling, memory management, file systems, device management, and security. [Offered: W]', 'SE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SE 380', 'Introduction to Feedback Control', 0.5, 'LAB, LEC, TUT', 'Introduction to control systems. Advantages of closed-loop feedback systems. The role of the system mathematical model. Block diagrams and signal flow graphs. The basic control system design problem, stability in control systems. Frequency response analysis techniques. Root-locus analysis. Elementary lead-lag compensation. [Offered: F]', 'SE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SE 382', 'Human-computer Interaction', 0.5, 'LAB, LEC, TUT', 'The purpose of this course is to introduce students to the design, implementation, and evaluation of contemporary human-computer interfaces. Topics include user-centered design, psychological considerations, and usability. Emphasis is on practical systems, such as PC-based window/mouse/keyboard systems. Labs alternate weeks. [Offered W]', 'SE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SE 390', 'Design Project Planning', 0.5, 'LEC, PRJ', 'Students undertake a substantial customer-driven group project as part of the SE 390/490/491 design-project sequence covering all major phases of the software-engineering lifecycle. Lectures describe expectations and project-planning fundamentals. Students form groups, decide on a project concept, complete a project-approval process, develop high-level requirements for the project, perform a risk assessment, develop a test plan, and complete a first-iteration prototype. Social, legal, and economic factors are considered. [Offered: F]', 'SE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SE 401', 'Seminar', 0, 'SEM', 'General seminar. [Offered: S]', 'SE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SE 402', 'Seminar', 0, 'SEM', 'General seminar. [Offered: W]', 'SE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SE 463', 'Software Requirements Specification and Analysis', 0.5, 'LAB, LEC, TUT', 'Introduces students to the requirements definition phase of software development; models, notations, and processes for software requirements, identification, representation, analysis, and validation. Cost estimation from early documents and specifications.', 'SE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SE 464', 'Software Design and Architectures', 0.5, 'LAB, LEC, TUT', 'Introduces students to the design, implementation, and evolution phases of software development. Software design processes, methods, and notation. Implementation of designs. Evolution of designs and implementations. Management of design activities.', 'SE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SE 465', 'Software Testing and Quality Assurance', 0.5, 'LAB, LEC, TUT', 'Introduces students to systematic testing of software systems. Software verification, reviews, metrics, quality assurance, and prediction of software reliability and availability. Related management issues.', 'SE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SE 490', 'Design Project 1', 0.5, 'LEC, PRJ', 'Continuing from SE 390, students undertake a substantial customer-driven group project. Project groups establish and maintain project control processes, delivering a series of iterations on their initial SE 390 prototype. Adaptive methodologies are encouraged and supported. [Offered: S]', 'SE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SE 491', 'Design Project 2', 0.5, 'LEC, PRJ', 'Final implementation, testing, and communication of the design project started in SE 390. Technical presentations by groups. Analysis of social, legal, and economic impacts. Final release of the project. Project retrospective. [Offered: W]', 'SE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SE 498', 'Advanced Topics in Software Engineering', 0.5, 'LEC', 'See the course offerings list for topics available. [Offered: As available]', 'SE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SE 499', 'Project', 0.5, 'PRJ', 'A software engineering project requiring the student to demonstrate initiative and assume responsibility. The student normally proposes their own project, which must involve substantial engineering science or engineering design. The student must find a faculty supervisor. A project report is required at the end of the term. Plan consent required. [Offered: F, W, S]', 'SE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SI 101R', 'Introduction to Arabic 1', 0.5, 'LAB, LEC, TUT', 'This course is designed for students with no previous knowledge of Arabic. It focuses on recognizing the Arabic alphabet and cursive writing. Students will build up basic vocabulary and construct simple sentences. Students will exchange basic conversations in Modern Standard Arabic and be exposed to Egyptian and Levantine colloquial Arabic.', 'SI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SI 102R', 'Introduction to Arabic 2', 0.5, 'LAB, LEC, TUT', 'Language structures and verb conjugations will be covered at a beginner level. Students will exchange conversations in varied contexts and build compound sentences and short paragraphs on topics of interest.', 'SI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SI 121R', 'Islam in the World', 0.5, 'LEC', 'An introduction to the caliphates, cities, and cultures of Islam, through film and selected texts, including the experience of Islam in Canada as revealed through its writers. This course provides a foundation for Studies in Islam, establishing a context for multidisciplinary approaches to a wide variety of topics.', 'SI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SI 201R', 'Intermediate Arabic 1', 0.5, 'LAB, LEC, TUT', 'This is an intermediate course focusing on reading comprehension of authentic Arabic materials. Students are introduced to verb patterns and subject-verb agreement rules and write longer paragraphs and short essays.', 'SI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SI 202R', 'Intermediate Arabic 2', 0.5, 'LAB, LEC, TUT', 'This is an intermediate course where students construct complex sentences and write essays. They read, analyze, and translate authentic texts. Students participate in conversations on topics of interest and learn complex grammar patterns, lexical derivations, and dictionary use.', 'SI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SI 221R', 'Islam, the West, and the Modern World', 0.5, 'LEC', 'This course examines conceptions of East-West divide and confrontation as well as topics of interest, both geo-political and religious, that influence the relationships of Muslims and the Western world. The focus is on the history of Muslims in the West and the challenges of integration of Muslim communities in pluralistic, secular societies.', 'SI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SI 301R', 'Advanced Arabic 1', 0.5, 'LAB, LEC, TUT', 'This is an advanced course focusing on honing reading comprehension skills. Students write longer essays using advanced grammatical structures, and they participate in longer conversations.', 'SI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SI 302R', 'Advanced Arabic 2', 0.5, 'LAB, LEC, TUT', 'This is an advanced level course that provides additional practice of the four integrated language skills at the advanced level. Students examine and analyze texts more deeply and confidently, responding to the texts in oral presentations and class discussions.', 'SI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SI 315R', 'Islam, Women, and the Modern World', 0.5, 'LEC', 'This course examines the lives and contributions of Muslim women in history - past and present - and political, social, and cultural influences that have affected the lives of Muslim women around the world. Challenging stereotypes of Muslim women and using religious, historical, and literary images, the course focuses on contributions of Muslim women as religious leaders, scholars, and politicians.', 'SI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SI 375R', 'Special Topics in Islam', 0.5, 'LEC', 'This course allows for in-depth study of selected topics in interdisciplinary studies in Islam.', 'SI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SI 390R', 'Understanding Islam', 0.5, 'SEM', 'This course brings together the diverse strands that constitute the student''s learning and experience in required as well as elective courses of Studies in Islam. Exploring Islam in a variety of contexts, the approach is interdisciplinary and integrative.', 'SI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SMF 204', 'Introduction to Human Sexuality', 0.5, 'LEC', 'This course provides a broad interdisciplinary overview of theories and research on human sexuality. Topics may include: human anatomy, sexual health, sexual response cycle, sexual orientation, gender, sex work, sexual practices, fetishes and paraphilias, and attraction/intimacy/love.', 'SMF');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SMF 205', 'The Dark Side of Sexuality', 0.5, 'LEC', 'As with all profound and meaningful human activities, sexuality has the potential to involve harm, manipulation, and abuse. This course offers a theoretical and empirical examination of the dark side of human sexuality. Topics may include sex work, pornography, sexual violence and abuse, incest, compulsive sexual behaviour, and paraphilias.', 'SMF');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SMF 206', 'Couples, Marriages, and Families', 0.5, 'LEC', 'This course provides an overview of couple, marital, and family relationships from a broad, interdisciplinary perspective.', 'SMF');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SMF 207', 'Parents, Children, and Family Relations', 0.5, 'LEC', 'This course examines caregiver/parent-child relationships from a broad interdisciplinary perspective over the lifespan. Topics may include: the transition to parenthood, parenting practices and their intersection with child/adult development, social and cultural influences on families with children, same-sex parenting, gender variance/fluidity and family relationships, family relations after parental separation, and care of aging parents.', 'SMF');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SMF 208', 'Introduction to Systemic Therapies and Anti-Oppressive Practices', 0.5, 'LEC', 'This course provides an overview of various theoretical and therapeutic practices in relational and sex therapy, and serves as an introduction to social justice, anti-oppressive, and inclusionary therapeutic practices. In doing so, the intersections of social locations such as class, culture, dis/ability, gender, and race within various social contexts are critically analyzed.', 'SMF');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SMF 220', 'Research Methods', 0.5, 'LEC', 'This course introduces students to the philosophy and methods of social science and humanities research, including an examination of issues and approaches to conducting research in the areas of sexuality, couples, and families.', 'SMF');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SMF 230', 'Introduction to Statistics in Sexuality, Marriage, and Family Studies', 0.5, 'LEC', 'The goal of this introductory statistics course is to help students understand the logic and appropriate application of commonly used descriptive and inferential statistics, with examples drawn from various disciplines relevant to sexuality, marriage, and family studies.', 'SMF');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SMF 301', 'Communication and Counselling Skills', 0.5, 'LEC', 'This course is an examination and analysis of the theories and methods of communication as applied within the processes of individual, relational, and family consultation and counselling.', 'SMF');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SMF 304', 'Human Sexuality in Relationships', 0.5, 'LEC', 'This course examines the development and experience of sexuality in a relational context from infancy to old age. Emphasis will be placed on sexual interaction and communication in adult relationships, but the effects of relationships on sexual experiences during infancy, childhood, adolescence, and old age may also be examined.', 'SMF');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SMF 305', 'Social Issues and Controversies in Human Sexuality', 0.5, 'LEC', 'This course will provide a detailed examination of selected issues and controversies in the area of human sexuality. Topics may include the role of sex education in schools, nature vs. nurture, censorship, and surrogate motherhood.', 'SMF');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SMF 306', 'The Formation and Maintenance of Close Relationships', 0.5, 'LEC', 'The interpersonal dynamics involved in the formation and maintenance of close relationships will be examined. Topics such as attraction, commitment, love, trust, and communication will be examined from various theoretical and empirical perspectives.', 'SMF');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SMF 307', 'Conflict, Crisis, and Dissolution in Close Relationships', 0.5, 'LEC', 'Families and close relationships are among the most important and valued human experiences, but they can also be the source of much conflict and pain. This course will examine the dark side of close relationships, with a focus on topics such as conflict and conflict resolution, family violence, poverty, separation and divorce, illness, and death and bereavement.', 'SMF');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SMF 308', 'Relational Therapy', 0.5, 'SEM', 'Modern and postmodern therapeutic approaches to working clinically within relational contexts (e.g., family, romantic, other relationship configurations) are taken up. This course emphasizes cultural sensitivity, social justice, and anti-oppressive practices in therapeutic settings. Students will apply theory to practice and explore the ethical implications associated with doing clinical work.', 'SMF');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SMF 309', 'Sex Therapy', 0.5, 'SEM', 'This course examines therapeutic approaches and clinical issues when working with sexuality related problems. Research and theoretical issues in the field of sex therapy will be discussed and applied to clinical contexts. The applied focus enables students to connect clinical theory and ethical issues to practice.', 'SMF');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SMF 310', 'Sexual and Relational Ethics', 0.5, 'LEC', 'A study of social relationships and systems that support sexual identities and relationship structures which generate ethical issues related to attitudes, values, and behaviours at both the individual and group level. This course may address sexual and relationship ethics on local, national, and transnational scales focusing on how issues of ethics and morality have been socially constructed.', 'SMF');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SMF 317', 'History of Sexuality: The Pre-Modern Period', 0.5, 'DIS, LEC', 'This course introduces students to the history of Western sexuality, beginning with the ancient world and focusing primarily on the Middle Ages and the transition to modernity.', 'SMF');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SMF 318', 'History of Sexuality: The Modern Period', 0.5, 'DIS, LEC', 'This seminar introduces students to the history of sexuality. The course focuses on the 19th and 20th centuries.', 'SMF');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SMF 319', 'History of Sexuality: Special Topics', 0.5, 'SEM', 'A specialized course in topics related to the history of sexuality. Topics cover the application of theory and research in any area related to the history of sexuality.', 'SMF');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SMF 365', 'Special Topics in Human Sexuality', 0.5, 'LEC', 'A specialized course in topics related to human sexuality. Topics cover the application of theory and research in any area related to human sexuality.', 'SMF');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SMF 366', 'Special Topics in Couples, Marriages, and Family Studies', 0.5, 'LEC', 'A specialized course in topics that cover the application of theory and research in any area related to couples, relationships, and families.', 'SMF');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SMF 367', 'Special Topics in Sexuality, Marriage, and Family Studies', 0.5, 'LEC', 'A specialized course that approaches topics of sexuality, marriage, and family from a humanities perspective. These integrated courses take a theoretical approach to the subject matter.', 'SMF');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SMF 404', 'Independent Study in Sexuality, Relationships, and/or Families', 0.5, 'RDG', 'An independent, in-depth study based on empirical research and/or extensive reading of a topic in the area of sexuality, relationships, and/or families. The project must be approved by the academic supervisor of the course prior to enrolment.', 'SMF');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SMF 406', 'Independent Study: Special Topics in Marriage and Family Studies', 0.5, 'RDG', 'An independent, in-depth study, based on empirical research and/or extensive reading, of a topic in the area of marriage and family. The project must be approved by the academic supervisor of the course prior to registration.', 'SMF');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SMF 408', 'Independent Study: Special Topics in Couple and Family Therapy', 0.5, 'RDG', 'An independent, in-depth study, based on empirical research and/or extensive reading, of a topic in the area of marriage and family therapy. The project must be approved by the academic supervisor of the course prior to registration.', 'SMF');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SMF 460', 'Practicum and Professional Ethics', 0.5, 'PRA, SEM', 'This course involves an assigned unpaid apprenticeship in a human sexuality or family studies setting combined with regular seminar meetings. The practicum will require no more than 10 hours per week. Paid or volunteer positions that are obtained outside the context of this course are not eligible for credit in this course.', 'SMF');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SMF 461', 'Practicum and Applied Theory', 0.5, 'PRA, SEM', 'This course involves an assigned unpaid apprenticeship in a human sexuality or family studies setting combined with regular seminar meetings. The practicum will require no more than eight hours per week. Paid or volunteer positions that are obtained outside the context of this course are not eligible for credit in this course.', 'SMF');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SMF 462', 'Research Thesis and Applied Theory', 0.5, 'PRJ, SEM', 'Under supervision of a faculty member, students normally will review literature, present an oral research proposal, analyze data, write a scholarly research paper, and deliver a seminar presentation based on the paper.', 'SMF');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SMF 490', 'Practicum and Professional Ethics', 0.75, 'PRA, SEM', 'This course involves an assigned unpaid apprenticeship in a human services setting combined with regular seminar meetings. The practicum will require 15 hours per week. Paid or volunteer positions that are obtained outside the context of this course are not eligible for credit in this course.', 'SMF');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SMF 494', 'Seminar in Sexuality', 0.5, 'SEM', 'This seminar allows students to integrate their knowledge in the domain of sexuality studies. Topics reflect current issues from a theoretical and research perspective.', 'SMF');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SMF 496', 'Seminar in Family Studies', 0.5, 'SEM', 'This seminar allows students to integrate their knowledge in the domains of couples, marriages, and family studies. Topics reflect current issues from a theoretical and research perspective.', 'SMF');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SMF 498', 'Seminar in Therapy: Couple, Family, and Sex Therapy', 0.5, 'SEM', 'This seminar allows students to integrate their knowledge in the domain of couples, family, and sex therapy. Topics reflect current issues from a theoretical and research perspective.', 'SMF');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 101', 'Introduction to Sociology', 0.5, 'LEC', 'An introduction to the basic concepts and frames of reference of sociological investigation and interpretation. Topics for analysis will include communities, associations and institutions, classes and status groups, crowds and publics, social processes, and social change. Special attention is given to Canadian society.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 101R', 'Introduction to Sociology', 0.5, 'LEC', 'An introduction to the basic concepts and frames of reference of sociological investigation and interpretation. Topics for analysis will include communities, associations and institutions, classes and status groups, crowds and publics, social processes, and social change. Special attention is given to Canadian society.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 102', 'Social Problems', 0.5, 'LEC', 'The specific social problems discussed will vary but will include problems of well-being, problems of inequality and power, and problems related to modernization.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 200', 'Sociology of Marriage and Family', 0.5, 'LEC', 'An introduction to the sociological perspectives on marriage and the family in urban-industrial societies. Special attention is given to marriage and the family in Canada. Comparisons with U.S. and Britain will be undertaken.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 201', 'Victims and Society', 0.5, 'LEC', 'This course will examine the substance of victimization: the scientific study of victims, the process, etiology, and consequences of victimization. Topics will include victims and politics, the victims'' movement, victim-precipitation, the victimization of women, and family violence.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 202', 'Classical Sociological Theory', 0.5, 'LEC', 'An examination of the nature and function of sociological theory and the types of theory that founded the discipline in the 19th and early 20th centuries.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 204', 'Sociology of Adolescence', 0.5, 'LEC', 'The social definitions of adolescence in cross-cultural and historical perspective. Social roles of adolescents in the institutional structures of urban-industrial societies with special emphasis on the family, education, and the economy. The relationship of adolescents'' social roles to processes of social change and stability.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 204R', 'Sociology of Adolescence', 0.5, 'LEC', 'The social definitions of adolescence in cross-cultural and historical perspective. Social roles of adolescents in the institutional structures of urban-industrial societies with special emphasis on the family, education, and the economy. The relationship of adolescents'' social roles to processes of social change and stability.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 206', 'Gender Relations', 0.5, 'LEC', 'An examination of gender relations in Canadian society, including historical changes and the contemporary situation. Emphasis is placed on a consideration of the social construction of gender, the gender structure of institutions, and gender inequality. This course also examines selected issues in contemporary gender relations.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 207', 'Sociology of Education', 0.5, 'LEC', 'Attention will be focused on the concepts and theories of sociology as they apply especially to the educational system. This course is designed for students who plan to enter the teaching field.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 207R', 'Sociology of Education', 0.5, 'LEC', 'Attention will be focused on the concepts and theories of sociology as they apply especially to the educational system. This course is designed for students who plan to enter the teaching field.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 209', 'Ancestry, History and Personal Identity', 0.5, 'LEC', 'In this course each student analyses his or her own family history in light of social, cultural, and economic trends over the past century as a means of understanding the basis of his or her own identity. The analysis is reported in an essay of about 25 pages.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 210', 'Sociology of Sport', 0.5, 'LEC', 'This course examines sport in modern societies and the distinctive features of Canadian sport. Attention is directed to the relationship between sport and other institutions, including the economy and political system. Contemporary issues, including racial and gender inequality and controversies over violence and drugs are also considered.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 222', 'Juvenile Delinquency', 0.5, 'LEC', 'A systematic analysis and criticism is presented of biological, psychological, psychoanalytical, and sociological theories of juvenile delinquency. Attention is given to statistics and contemporary research with special emphasis on the distribution and types of delinquent subcultures.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 223', 'Deviance: Perspectives and Processes', 0.5, 'LEC', 'The deviance-making process is examined in a variety of social contexts. This course examines the emergence of rules and control agencies, the processes by which people become involved in deviant activities, and the contingencies affecting their careers as deviants.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 223R', 'Deviance: Perspectives and Processes', 0.5, 'LEC', 'The deviance-making process is examined in a variety of social contexts. This course examines the emergence of rules and control agencies, the processes by which people become involved in deviant activities, and the contingencies affecting their careers as deviants.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 224R', 'Poverty in Canada and its Social Consequences', 0.5, 'LEC', 'A sociological analysis of poverty in contemporary Canada as it underlies a multiplicity of interlocking social problems.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 226', 'Juvenile Justice', 0.5, 'LEC', 'An examination of theories of juvenile justice, juvenile law, and the structure and operations of juvenile systems, especially in Canada.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 227', 'Criminology', 0.5, 'LEC', 'An examination of the major theories of crime causation and their implications for the development of social policy. Both historical and contemporary theories will be discussed.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 228', 'Sociology of Criminal Justice', 0.5, 'LEC', 'Decisions to process offenders and the role of social factors in the Canadian criminal justice system are critically examined. Focal issues include police discretion, the legal profession, and prison systems.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 229', 'Selected Topics in Criminology', 0.5, 'LEC', 'Sociological analysis of research and theory on selected criminal activities. Motivation, modus operandi, and the social characteristics of offenders will be examined in relation to such specific crimes as drug and sexual offenses, theft, robbery, murder, organized crime, and/or other criminal activities.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 232', 'Technology and Social Change', 0.5, 'LEC', 'This course examines how forms of society influence technological developments and, reciprocally, how society is influenced by technology. It covers such topics as the de-skilling of work, technocracy, communications technology, and cyberspace, from an historical perspective, looking for the unifying features of technologies ranging from Stone Age tools to the microprocessor.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 234', 'Social Psychology and Everyday Life', 0.5, 'LEC', 'Introducing students to symbolic interaction, a sociological social psychology, this course examines: the impact of culture on socialization experiences; the development of self-identities and social reputations; and interaction patterns in a variety of casual, occupational and deviance contexts.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 237', 'Collective Behaviour', 0.5, 'LEC', 'The sociological analysis of the behaviour of crowds, mobs, publics and related phenomena and their relationships to social organization and social change.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 241', 'Sociology of Work and Occupations', 0.5, 'LEC', 'This course is an introduction to the study of work and occupations including labour market trends, professions and the professionalization process, how work is experienced, balancing work and family, and the impact of work on lifestyles, leisure, and retirement.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 263', 'Organized Crime', 0.5, 'LEC', 'An examination of select criminal organizations in North America. Particular attention will be given to the social history of "the mafia" and the development of legal tools for policing criminal organizations. Additional themes for discussion include enterprise and economic crimes, corruption, and the role of women in organized crime.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 243', 'Occupational Sociology', 0.5, 'LEC', 'An introduction to the study of work and occupations; the problems of occupational choice, occupational socialization and identification; the concepts of career and career mobility; the professionalization process, the nature of professions; the impact of occupation on life styles, leisure and retirement.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 246', 'Mass Communication', 0.5, 'LEC', 'This course provides an introduction to the social processes and functions of mass media communication - with particular reference to the Canadian context. Emphasis is focused on the relationship between mass communication and the ongoing reconstruction of social reality.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 248', 'Health, Illness, and Society', 0.5, 'LEC', 'This course discusses health, illness, and the distribution of disease in society. It introduces concepts such as social determinants of health, population health, and culturally competent care. The strength and the limitations of the Canadian health care system will be examined through a sociological lens.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 249', 'Sociology of Mental Disorder', 0.5, 'LEC', 'An examination of sociological research and theory in the field of mental disorders particularly as they apply to issues of law and social control. Topics include mental health legislation, the medical model of mental "illness," the epidemiology of mental disorder, family processes and psychiatric hospitalization, public attitudes and social stigma, and specific forms of mental disorder.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 250', 'Contemporary Japanese Society', 0.5, 'LEC', 'An introduction to the basic institutions and cultural values in contemporary Japanese society. Topics will include family, community, religion, education, work, social stratification, ethos, and the economic behaviour of Japan overseas.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 253', 'Demographic Change in Canada', 0.5, 'LEC', 'An introduction to the study of human population, with a focus on mortality, fertility, migration, and spatial distribution in Canada. Methods and measures used in demographic research, sources of demographic data, and the health and social implications of the major demographic trends are discussed.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 256', 'Ethnic and Racial Relations', 0.5, 'LEC', 'Relations between different racial and cultural groups, analysis of majority-minority group status with special reference to Canada.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 258', 'Millennialism and Violence', 0.5, 'LEC', 'An examination of the nature and causes of episodes of mass violence inspired by apocalyptic beliefs. This course will use historical and contemporary case studies and theoretical discussions of the social and psychological factors precipitating violence.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 260', 'Religion in Sociological Perspective', 0.5, 'LEC', 'An examination of the nature of religious experience, the elements of religious group life, and the ways in which religions are a source of social stability and peace as well as of social change and conflict.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 261', 'Religion in America', 0.5, 'LEC', 'The course examines religion in the American context regarding issues like secularization, the nature and the influence of sects (e.g., Mormonism, Pentecostalism), Protestant revivalism and televangelism, and the impact of non-Christian traditions (e.g., Buddhism, Islam).', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 262', 'Cults and New Religious Movements', 0.5, 'LEC', 'This course examines various cults and new religious movements (e.g., Scientology, Krishna Consciousness, Neo-paganism) and places them within the context of our sociological knowledge of their emergence, who joins and why, and other issues.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 275', 'Mennonites as a Sociological Community', 0.5, 'LEC', 'An analysis of the Mennonites as a social movement, their transition to a sectarian community, transformation to a religious-ethnic society, and present pluralistic profile. Case studies of/and field visits to area Mennonites included.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 280', 'Social Statistics', 0.5, 'LEC', 'A basic course in statistics used in social science research including sampling, central tendency, probability, and covariance, as illustrated in specifically sociological and criminological data.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 286', 'Environment and Behaviour', 0.5, 'LEC', 'A study of the interaction between social organization and ecological factors such as pollution, energy and land resources.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 302', 'Contemporary Sociological Theory', 0.5, 'LEC', 'An examination of the nature and functioning of sociological theory in the 20th and 21st centuries, with a focus on selected types of theory that have been influential in shaping the discipline.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 307', 'Problems in Contemporary Education', 0.5, 'LEC', 'A study of problems arising from the interplay between institutionalized education and the forces of rapid social change in the contemporary society. It emphasizes the changing roles of the learners and instructors and social dimensions of newer learning theories and programs.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 312', 'Sociology of Science', 0.5, 'LEC', 'An examination of the social character of the development of science and the production of scientific knowledge. Specific topics will include defining science, cultural influences on the rise of science, the social nature of scientific institutions, selective bias in scientific procedures, and the social construction of scientific facts.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 315', 'Class, Status and Power', 0.5, 'LEC', 'Analysis of social classes in society including their basis for development, composition and consequences for society. Special attention is given to social stratification in Canada.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 321', 'Introduction to Research Methods', 0.5, 'LEC', 'An introductory survey of the research techniques commonly employed by sociologists, criminologists, and legal studies researchers. The formulation of research designs appropriate to various kinds of intellectual problems in social science is stressed.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 322', 'Field Research Methods', 0.5, 'LEC', 'This course provides a critical evaluation of research techniques in sociology, criminology, and legal studies with an emphasis on learning and applying qualitative fieldwork approaches.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 325', 'Sexuality and the Law', 0.5, 'LEC', 'Despite the commonly held belief that sexuality is nothing more than "doing what comes naturally," cultural definitions, including prohibitions against specific forms of conduct, impinge upon the most private or intimate of acts. This course examines the social construction and control of sexuality through law.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 326', 'Punishment and Society', 0.5, 'LEC', 'A critical criminological and sociological examination of theories and practices of punishment. This course will examine transformations in penal theory, penal management, and penal institutions and their social and policy implications.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 327', 'Policing in a Democratic Society', 0.5, 'LEC', 'A critical examination of the police as social control agents in contemporary democratic societies. Topics include the historical evolution of policing; police recruitment, training, and education; police/community relations; the occupational subculture of the police; police authority and discretion; private policing; and police deviance and criminality.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 336', 'Sociology of Professions', 0.5, 'LEC', 'An examination of the distinctive nature of professions; professional recruitment, socialization and identification; professional careers; the professionalization of occupations; relationship to government; professional specialization; status, power and mobility of professionals.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 339', 'The Knowledge Society and Waterloo Region', 0.5, 'SEM', 'Examines the theory and the practice of the "knowledge society", the form of social and economic organization which has largely replaced industrial society. Along with examining sociological works on the knowledge society, the Region of Waterloo is used as a case study. This is both to better understand the concept of knowledge society from the case study, and to assess the ambitions of Waterloo Region to become a "knowledge capital."', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 340', 'Sociology of Organizations', 0.5, 'LEC', 'This course examines the evolution and characteristics of the modern organization, focusing on changing approaches to corporate strategy and management in the context of the new economy. Using case studies, we will examine how networks, gender, technological innovations and environmental pressures shape organizational practices.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 342', 'Migration and Legality', 0.5, 'LEC', 'This course introduces students to key concepts and case study research on the regulation of human migration and its exclusionary practices. It examines how state policies, laws, and international organizations control the mobility of migrants and refugees across regions and borders.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 345', 'Cyberspace and Social Life', 0.5, 'LEC', 'A critical investigation of the new theoretical and empirical research into the nature and social significance of computer-mediated communication. The Internet will be examined as a multi-mediated arena of performance for the negotiation, expression, and transformation of our conceptions of identity, community, spirituality, power and authority.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 346', 'Social Movements', 0.5, 'LEC', 'The analysis of varieties of social movements and their relationships to social organization and social change.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 347', 'Sociology of Leisure', 0.5, 'SEM', 'This seminar style course focuses on recent research on relationships between leisure and the social, cultural, political, and technological aspects of society. The course will use sociological literature to examine leisure in the lives of individuals, groups, and society. Topics will include leisure and multiculturalism, social stratification, gender, popular culture, consumption, tourism, religion, and the family.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 349', 'Migration and Development', 0.5, 'LEC', 'This course explores theoretical perspectives and empirical studies on development and the related field of migration studies. Specific themes may include history and colonialism, seasonal and labour migration, forced migration, immigration policies and their social and economic implications, and transnationalism and diasporas.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 352', 'Sociology of Aging', 0.5, 'LEC', 'An introduction to individual and population aging. Topics discussed include: aging from a historical and comparative perspective; aging in subcultures; aging and the social structure; aging and social processes; aging and the environment; work and retirement; and aging and leisure patterns.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 354', 'Comparative Health Care Systems', 0.5, 'SEM', 'This course discusses health care systems around the world with sociological perspectives and introduces health care system evaluation criteria to examine different nations'' health care models, including health care delivery and financing. Some basic research methods for health care systems studies will be introduced.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 355J', 'Power and Parenting', 0.5, 'LEC', 'An examination of contemporary parent-child relations in terms of a reconceptualization of power. Special attention will be given to contemporary interpretive (e.g. hermeneutic) approaches to a reformulation of the relation between power and action.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 362', 'Canadian Society: Special Topics', 0.5, 'LEC', 'A critical examination of special issues and topics bearing on the nature of Canadian society and/or the interrelationship of Canada with other aspects of the international community. Topics will vary according to the instructor''s interests, but may include such issues as multiculturalism, immigration, national unity, globalization, poverty, and industrial development and policy.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 365', 'Urban Life and Culture', 0.5, 'SEM', 'Using a symbolic interactionist approach, this course examines central features of urban community life. Particular attention is given to the corporate, commercial, consumptive and communications aspects of urban society as well as residential practices and street life.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 366', 'Entertainment Motifs: An Interactionist Analysis', 0.5, 'LEC', 'Approaching entertainment from a symbolic interactionist perspective, this course examines entertainment as a set of socially constituted and engaged processes. Building primarily on analytical and ethnographic materials from the classic Greek era to the present time, this course considers the roles (viewpoints, activities, and interchanges) of people involved in producing, promoting, accessing, experiencing, and critiquing, an assortment of commercial and noncommercial entertainment venues.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 368', 'Custodial and Rehabilitative Institutions', 0.5, 'LEC', 'Total institutions are concerned with resocialization of inmates. This course considers the structure of maximum security prisons, mental hospitals, isolated work environments and concentration camps, emphasizing their philosophies, their organization, their goals, and their effectiveness in modifying and controlling behaviour.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 369J', 'The Sociology of Community', 0.5, 'LEC', 'This course examines how our contemporary concern with community is connected with the rise of modern society and the development of the urban-rural debate. Our anxieties about community will be shown to be connected to our anxieties about family. Special attention will be given to the interpretive approach to these issues.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 370', 'Sociology of Law', 0.5, 'LEC', 'This course examines the social construction of law and its administration as a social process. Topics will include law as an instrument of social control and social change, legal culture, the identification and evaluation of criminal suspects, the trial process and the rights of special groups. The specific laws highlighted will vary.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 372', 'Good and Evil in Social Relations', 0.5, 'LEC', 'In the tradition of sociology as a moral science, this course asks how, if at all, good and evil can be empirically distinguished and accounted for. Both current and historical issues are addressed, with particular attention to the Holocaust.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 375R', 'Studies in Sociology', 0.5, 'LEC', 'This course will deal with selected topics in sociology. Subjects will be dependent upon the research and/or instructional interests of faculty.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 378', 'Sociology of Women', 0.5, 'LEC', 'An examination of the growing sociological literature on women''s roles, experiences, realities, problems and challenges. Particular emphasis is placed on critiques of traditional sociological theory and methodology and the emergence of new theories and methodologies which better reflect women''s experiences.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 382', 'Survey Methodology', 0.5, 'LEC', 'The design of sample surveys. Survey procedures from the conceptual level through sampling, measurement, questionnaire design, administration and analysis of responses are illustrated within the context of practical examples and student projects.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 398R', 'Independent Study', 0.5, 'RDG', 'An independent in-depth study of a selected area of concern to the student within the discipline of sociology. Available to individuals or small groups of third- or fourth-year Social Development Studies majors and arranged with one of the faculty members from the program.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 399R', 'Independent Study', 0.5, 'RDG', 'An independent in-depth study of a selected area of concern to the student within the discipline of sociology. Available to individuals or small groups of third or fourth-year Social Development Studies majors and arranged with one of the faculty members from the program.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 401', 'Theoretical Perspectives on Gender', 0.5, 'SEM', 'An examination of sociological theories of gender and gender relations. Emphasis is placed on tracing historical changes in interpretations of gender, with a particular consideration of contemporary theoretical debates. Approaches to be considered include feminist theories as well as political economy and cultural studies.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 402', 'Sociology of Religion', 0.5, 'SEM', 'This course examines key substantive, theoretical, and methodological issues of the sociology of religion through the detailed study of important classical and contemporary works in the field.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 404', 'Sociology of Knowledge', 0.5, 'SEM', 'This seminar undertakes to develop a general theory of the relation of social thought to social action, comparative value systems and the role of the scientist, artist, and intellectual in society.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 405', 'Seminar in Classical Sociological Theory', 0.5, 'SEM', 'An analysis of selected original writings by the major figures in the classical sociological tradition of the 19th and early 20th centuries and selected critical literature.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 406', 'Seminar in Contemporary Sociological Theory', 0.5, 'SEM', 'An analysis of selected original writings by influential figures in contemporary theory (i.e., mid-20th century onwards) and selected critical literature.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 407', 'Canadian Social Thought', 0.5, 'SEM', 'We examine the development of sociological theory in Canada by focusing on major historical and contemporary figures and theories representative of English-language sociology. We examine, in addition, sociologically important scholars and schools of thought in history and political economy as well as selected developments in French-language Canadian sociology.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 408', 'Contemporary Debates in Sociological Theory', 0.5, 'SEM', 'This course deals with recent controversies in sociological theory; e.g., Giddens'' theories of structuration and the state; critical theory, including the works of Habermas on communicative action; postmodernist theory; the positivist/interpretive debate; and varieties of psychoanalytic theory.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 409', 'Knowing and Acting: Social Theory from the Early Greeks to the Present', 0.5, 'SEM', 'Using a symbolic interactionist frame, this course examines the enduring problem of connecting social theory and human activity. Building on some of the works of the early Greeks and others (hermeneutic scholars, pragmatists) who addressed matters of intersubjectivity and human accomplishment, the emphasis is on developing theory that is informed centrally by an ethnographic examination of community life in the making.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 410', 'Symbolic Interaction and Ethnographic Research', 0.5, 'SEM', 'An application of symbolic interactionist theory, this course examines the contingencies affecting data collection and analysis of ongoing group life. While doing field work, students have an opportunity to examine basic features of interactionist thought.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 414', 'Power, Persuasion, and Management', 0.5, 'SEM', 'This course studies influence work (and resistance) from a symbolic interactionist perspective. Building on materials that focus on tactical interchange from classical, interim, and contemporary sources, this course considers the ways that people attempt to shape one another''s activities and experiences in various social situations. The material introduced covers the fuller range of persuasive interchange in community life from dyadic encounters to international relations.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 415', 'Social Networks', 0.5, 'SEM', 'A survey of applications of the concept of the network in studying social structures. Examples will be drawn from diverse areas, such as interpersonal relations, community studies, social support, interorganizational relations, elites, deviant groups, etc.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 416', 'Educational Theory and Practice', 0.5, 'SEM', 'An examination of the major theoretical explanations and practices in education. Topics include an analysis of the various social processes that have been developed to define and transmit knowledge, skills, attitudes, values, and power relations from one generation to the next. The role of state-sponsored formal education systems in the context of world economic systems will be explored.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 417', 'Sociology of Higher Education', 0.5, 'SEM', 'This course examines the relationship between higher education and society. Topics include: major trends, student culture, how and why students are stratified across fields of study and various kinds of postsecondary institutions, and the relationship between education and the labour market.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 434', 'Sociology of At-Risk Youth', 0.5, 'SEM', 'This course examines the social attributes and surrounding conditions associated with at-risk youth. It will focus on the development of youth in three major institutions - education, criminal justice, and mental healthcare. This course will focus on the attributes of youth themselves, but also to changing institutional definitions and practices. This course will include an experiential learning component outside regular classroom hours.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 418', 'Social Theory and Popular Culture', 0.5, 'SEM', 'This course will critically assess contemporary approaches to the study of culture and media in sociology and related fields. The course will examine the production, mediation, and consumption of cultural artifacts, images, and spaces, and the problems of culture as ideology, reproduction, and resistance.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 420', 'Seminar in Social Inequality', 0.5, 'SEM', 'Analysis of social stratification and inequality in industrial societies, with emphasis on Canada. Issues of class, power, wealth, and occupational structure will be examined.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 421', 'Quantitative Methods', 0.5, 'SEM', 'Design and data analysis in contemporary sociological research, with an emphasis on the analysis of secondary data and computer applications.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 424', 'Seminar in Sociology of Health', 0.5, 'SEM', 'Current issues and debates within health care, including the social organization of the health care system and the socio-cultural determinants of health and illness, are examined from a sociological perspective.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 428', 'Sentencing as a Social Process', 0.5, 'SEM', 'This course examines in depth the process and results of criminal sentencing. Topics include types of sentences for criminal and quasi-criminal offences, objectives of sentences, factors affecting sentences, the process of sentencing, the administration and effectiveness of sentences, and unresolved debates in sentencing.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 435', 'Environmental Sociology', 0.5, 'SEM', 'Inquiry into the relationship between the natural environment and society. Review of issues relating to technology, social change, politics of environmental reform, factors that contribute to environmental-resource conflict and policy.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 450R', 'Senior Seminar in Special Topics', 0.5, 'SEM', 'Senior seminars may include weekly readings, individual and/or group projects, class presentations and discussions, research proposals, essay/literature reviews, assignments, midterms, and final exams. Consult departmental listings for topics and prerequisites for the current year.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 451', 'Global Development', 0.5, 'SEM', 'This course explores theoretical perspectives on development through an examination of the social and cultural consequences of development efforts across the globe. It offers an approach through cases and themes that may include conflict and inequalities, cultural constructions of peoples and places, global aid governance, and alternative approaches to development.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 452', 'Humanitarianism', 0.5, 'SEM', 'This course explores the theoretical and historical background of humanitarianism, and offers an approach to this field through specific cases and themes that may include: the paradoxes of humanitarian intervention, war and humanitarianism, genocide and refugees, gendered bodies, and social justice activism.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 459', 'Sociology of Work and Occupations', 0.5, 'SEM', 'This seminar examines major theoretical perspectives and issues in the sociology of work.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 490R', 'Special Studies', 0.5, 'ESS', 'An independent, in-depth study, based on empirical research and/or extensive reading in sociology under the direction of individual instructors in sociology. Available to individuals or small groups of fourth-year Social Development Studies majors and arranged with one of the faculty members from the plan. The project must be approved by the academic supervisor of the course prior to registration.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 497', 'Honours Research Practicum', 0.5, 'PRJ', 'Denoting a preprofessionalization experience, this course provides selected students with an opportunity to work [to a maximum of 8 hours per week over one term] as an apprentice with a specific instructor on a research project in which the instructor is currently engaged.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 498', 'Directed Studies', 0.5, 'RDG', 'Selected study and assignments under the direction of a faculty member.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 499A', 'Senior Honours Essay', 0.5, 'RDG', 'Directed reading and research in a selected area of sociology inquiry.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 499B', 'Senior Honours Essay', 0.5, 'RDG', 'Directed reading and research in a selected area of sociology inquiry.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 696', 'Sociology of the Life Course', 0.5, 'SEM', 'In sociology, the life course has been used to understand a wide range of social issues and phenomena, including health, social inequality, crime and deviance, and many others.  This course will explore the history of the life course tradition in sociology, its theoretical and conceptual components, and various qualitative and quantitative analytical approaches used in life course research, as well as particular applications of the life course to contemporary issues in sociology.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 697', 'Practicum in Survey Administration', 0.5, 'SEM', 'A practicum giving training in the administration of social survey research. Normally this will be in conjunction with the K-W Metropolitan Area Survey (KWMAS), an annual area survey administered by the Department of Sociology. Weekly seminars will combine examination of the scholarly literature on issues in survey research with case materials from the KWMAS. Students will gain practical experience in such issues as dealing with clients, working out a production schedule for the survey, assembling a questionnaire, writing an OHR (ethics) brief, making sampling design decision, and conducting pretest fieldwork. The course requires a paper on some aspect of survey administration.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 700', 'Sociological Theory', 0.5, 'SEM', 'A critical overview of selected original writings of major sociological theorists from the 19th and 20th centuries (including, among others, Marx, Weber, Durkheim, Mead, Schutz, Wollstonecraft, Martineau, D. Smith, Giddens, and Habermas). Attention is paid throughout to issues in the philosophy of social science and sociology of knowledge.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 703', 'Social Theory and Enacted Realities: From the Early Greeks to the Present Time', 0.5, 'SEM', 'Using a symbolic interactionist/social constructionist frame as the integrating mechanism, this course examines the enduring problem of connecting social theory with human activity.  Building on the works of early Greeks (e.g., Protagoras, Plato, Aristotle, Thucydides) and others (e.g., hermeneutic scholars, pragmatists, and contemporary social scientists) who have addressed matters of intersubjectivity and human accomplishment, the emphasis is on articulating theory that is attentive to community life in the making.  Focusing on the interlinkages of speech, objects, and activity, particular attention is given to the development of pragmatist thought within rhetoric, poetics, philosophy, history, and the social sciences.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 704', 'Key Theoretical Debates', 0.5, 'SEM', 'This course examines the conflicting views of six to ten pairs of theorists, from Hegel and Comte to major thinkers in our time. The study of the materials will allow students to become conscious of their personal locations in the history of social thought, and develop coherent theoretical positions of their own.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 705', 'Theory and Research in Social Organization', 0.5, 'SEM', 'A survey of current theories of social organization, which examines social institutions and their interconnections. Social structure is treated as a predictor of behaviour.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 706', 'Theory and Research in Social Psychology', 0.5, 'SEM', 'A survey of the field of social psychology. Major theoretical approaches are examined and compared in the context of course readings, discussion and student presentations. Topics address the processes of socialization, communication, attribution, social influence, identity, group structure, cognition, deviance, the life cycle, and social structure and personality.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 707', 'Canadian Sociological Thought', 0.5, 'SEM', 'An overview of the development of sociological theory in Canada. Focusing on English-language sociology, the course deals with major figures (Dawson, Hughes, Marsh, Porter, Clement) and theories in the discipline (human ecology, Fabianism, functionalism, elite studies). It examines as well scholars and schools of thought in closely related disciplines (Innis, the "new political economy", feminist scholarship) and related developments in Quebecois sociology.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 708', 'Contemporary Debates in Sociological Theory', 0.5, 'SEM', 'Deals with recent controversies in sociological theory; e.g. Giddens'' theories of structuration and the state; critical theory, including the works of Habermas on communicative action; postmodernist theory and varieties of psychoanalytic theory.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 709', 'Selected Problems in Sociological Theory', 0.5, 'SEM', 'Relation of sociological theory to specific problems of sociological analysis.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 710', 'Intermediate Social Statistics', 0.5, 'SEM', 'Applied multiple regression/correlation, with emphasis on data processing/computing, model construction and interpretation and underlying statistical assumptions.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 711', 'Techniques in Longitudinal Analysis', 0.5, 'SEM', 'This course will cover topics related to the analysis of longitudinal and repeated-measures data in the social sciences, mainly using large multivariate datasets.  Topics covered will include popular techniques including analysis of various duration measures, including event history and related models, growth-curve or latent trajectory models, and will also expose participants to newer approaches including sequence analytic methods.  Open to all students, this course may be of particular interest to students planning research in connection with the Statistics Canada longitudinal surveys housed in the university''s South Western Ontario Research Data Centre.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 712', 'Elements of Social Research', 0.5, 'SEM', 'The social science research process is examined within quantitative, qualitative and mixed methods approaches.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 785', 'Cities and Security', 0.5, 'SEM', 'An examination of the urban aspects of security, surveillance, war and terrorism. Particular attention will be given to the contemporary embrace of resilience as a rationality of urban security. Additional themes include the militarization of policing, the role of ''big data'' in the intensification of urban surveillance, and the relationship between security and urban environments.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 713', 'Design and Data Analysis in Quantitative Research', 0.5, 'SEM', 'Continuation of SOC 712 and 710 at a more advanced level. Philosophical and theoretical issues in quantitative research. Experimental and non-experimental research designs and procedures. Causal modelling and inference. Measurement theory. Latent variables. Longitudinal designs and data analysis. Use of computer software.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 714', 'Ethnographic Research in the Social Sciences', 0.5, 'SEM', 'Attending to the ways in which group life is (socially) constructed, this course provides (1) greater linkage of theory and method; (2) grounding in the interactionist literature; and (3) experience in data collection and analysis.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 715', 'Mixed Methods Research', 0.5, 'SEM', 'Strategies are introduced to design, implement and critically assess the appropriateness of mixed methods different designs integration, interpretation, logistics, benefits and the challenges involved in conducting mixed methods research.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 716', 'Qualitative Methods', 0.5, 'SEM', 'This course covers the basic techniques for collecting, interpreting, and analyzing qualitative data.  The course will operate on two interrelated dimensions, one focused on understanding the relationship between theory, methods and data, the other focused on practical techniques such as identifying informants, and coding and analyzing data.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 717', 'Reflexive Research Methodologies: Contemporary Interpretive Traditions', 0.5, 'SEM', 'Drawing on the intimate intertwining of theory and methodology in such areas as phenomenology, ethnomethodology, hermeneutics and analysis, this course takes a practical approach to the reflexive research strategies these methodologies employ.  Students will be encouraged to develop research proposals in line with their exposure to these methodologies.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 719', 'Elements of Social Research Design', 0.5, 'SEM', 'An instructor will teach in their area of specialization.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 720', 'Social Inequality', 0.5, 'SEM', 'This course will deal with class, status and power groups in an industrial society as they strive to maximize their share of wealth, prestige and power. Occupational structures of modern societies will be examined.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 725', 'Sociology of Health', 0.5, 'SEM', 'An overview of sociological approaches - theoretical and methodological - to the study of health, illness, and health care.  Both the social organization of the health care system and the socio-cultural determinants of health and illness are considered.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 730', 'Sociology of Religion', 0.5, 'SEM', 'The course examines key substantive, theoretical and methodological issues of the sociology of religion through the detailed study of important classical and contemporary works in the field. Representative issues addressed are: the social and psychological nature and function of religious experience, the character of conversion processes, the social and political implications of religious ideologies and organizations, the status of religious beliefs and practices in an age of seeming secularization. Attention will be given to both western and non-western religious traditions as well as to both established and newer forms of religious life.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 735', 'Environmental Sociology', 0.5, 'SEM', 'Inquiry into the relationship between the natural environment and society. Review of the theoretical and paradigmatic approaches used to study society-environmental interactions, and analysis of issues relating to technology, social change, distributional impacts, politics of environmental reform and factors that contribute to environmental-resource conflict, and policy decisions as they relate to the larger ecological complex.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 740', 'Sociology of Deviance', 0.5, 'SEM', 'The seminar undertakes a critical examination of the major theoretical perspectives in the sociology of deviance.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 744', 'Sociology of Crime and Justice', 0.5, 'SEM', 'An overview of current sociological theories and research in the areas of crime and delinquency, and juvenile and criminal justice.  The focus of the course is on recent developments in theory and research that have received widespread attention from sociological criminologists.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 745', 'Deviance: An Interactionist Perspective', 0.5, 'SEM', 'Viewing deviance as an emergent social phenomenon, this course considers: the social construction of norms and deviance definitions; the emergence of deviance in the context of social movements and work situations; and the reputations and career contingencies of persons involved in deviant activities.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 750', 'Sociology of Gender Roles', 0.5, 'SEM', 'An examination of women''s and men''s changing gender roles with particular emphasis on theoretical explanations of gender relations. Topics discussed include gender roles in the family, politics and the economy, socialization, health care, violence and reproduction.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 751', 'Theories of Gender Relations', 0.5, 'SEM', 'A critical examination of theories concerning the origin of sex inequality and an attempt to identify indicators of changing status of the sexes as well as factors which account for such changes.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 759', 'Sociology of Work and Occupations', 0.5, 'SEM', 'The seminar examines extant theoretical perspectives on the study of work. Current theories of work, critical issues in the field, and changes in the roles of occupations in the social structure are examined with the view towards new perceptions of work in our society.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 760', 'Social Networks', 0.5, 'SEM', 'An examination of the use of the concept "social network" in studying social structure; including the applicability of the concept to social theories, methods of operationalizing and analyzing social networks, and examples of substantive research employing the concept.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 765', 'Political Sociology', 0.5, 'SEM', 'A critical examination of political and governmental strategies for identifying social problems and managing the conduct of individuals, groups, and populations.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 766', 'Participatory Action Research', 0.5, 'SEM', 'This course is designed to introduce students to the history, theoretical background and application of Participatory Action Research as it is used within the social sciences.  Participatory Action Research (PAR) represents an alternative and collaborative approach to the design implementation and outcomes of a community-based research project whereby democratic processes of social and organizational change are priority.  A PAR approach is both a method for conducting research and a framework for the ongoing encouraging and/or facilitating social change and social action.  The use of various methodologies including peer researchers, mixed methods (quantitative and qualitative), knowledge translation, mobilization strategies for the purpose of needs assessment, program evaluation and policy research, will be explored.  Examples of PAR in Canada will be examined as well as examples from international practice.  Both the benefits and challenges of a PAR approach will be discussed throughout the course.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 768', 'Community Engagement and Social Development', 0.5, 'SEM', 'This course highlights the importance of participation by community members in matters related to their well-being and with methods to encourage their involvement.  Community based research (CBR) as a method to engage communities towards social development will be explored.  Processes of community entry and community engagement, particularly with disenfranchised and/or  marginalized communities, will be examined, whereby methods for enhancing community capacity, inclusion, and empowerment will be given special attention.  The role of effective leadership toward creating and sustaining social innovation will be discussed and examples of innovative social development practices in Canada will be assessed as well as examples from international practice.  The theoretical and historical background of Social development will be covered, included social capital theory, and political economy perspectives of urban sustainability.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 770', 'Comparative Social Structure', 0.5, 'SEM', 'The course is devoted to the comparative study of selected western and non-western societies in relation to their institutions, structures, value systems, changes and interactions.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 774', 'Family and Kinship', 0.5, 'SEM', 'Family and kinship are viewed as the rudimentary structures of society from which other structures differentiate. They are shown to persist in modern society as a reinforcement structure beneath the specialized institutions.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 776', 'Sociology of Knowledge', 0.5, 'SEM', 'The seminar undertakes to develop a general theory of the relation of social thought to social action, comparative value systems and the role of the scientist, artist and intellectual in society.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 778', 'Theorizing Discourses of Health, Illness and Disease in Everyday Life', 0.5, 'SEM', 'This course will focus on identifying areas of strain or conflict in public health and everyday life in relation to medical, literary, philosophical and everyday discourses of health and sickness.  It will examine contested representations of the relations of health and life, healing and cure, pleasure and pain, self-governance and negligence, body and mind, and policy and polity.  Core texts will span a wide variety of fields, eras and authors (e.g., Beckett, Benjamin, Descartes, Foucault, Freud, Gadamer, Goffman, Garfinkel, Parsons, Plato).', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 780', 'Theories of Social Change', 0.5, 'SEM', 'A systematic review and analysis of major theories of social change. Theoretical problems are examined within a specific context such as social organization, economic institutions, social stratification, and urban structures.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 781', 'Global Development Governance', 0.5, 'SEM', 'The course explores theoretical perspectives on the global governance of development, with critical attention to how processes of global development shape local environments and their inhabitants, challenge notions of state sovereignty and territory, and engender diverse responses to regimes of control.  Cross-disciplinary perspectives will enable students to engage with a wide range of sociological, ethnographic, and political analyses of development through case studies and themes.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 782', 'Law, Globalization and Women''s Empowerment', 0.5, 'SEM', 'The course will explore the theoretical debates within feminist scholarship surrounding the use of Western liberal legal approaches to prosecute gender violence and improve the socio-economic status of women globally.  The course readings will draw from various literatures, including liberal and transnational feminist, postcolonial, and socio-legal and governmentality literatures, to analyse and critically evaluate the concept and deployment of women''s empowerment in global contexts.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 783', 'Security and Regulation', 0.5, 'SEM', 'An examination of criminological and sociological theory and research on the problem of security and related regulatory agencies.  Various forms of regulation will be examined (e.g. discipline, surveillance, control, security detention, security intelligence, risk management) as they apply to particular bodies, mobilities, borders, and private and public spaces.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 786', 'Interdisciplinary Seminar in Aging', 0.5, 'SEM', 'Faculty members and students from the disciplines of Biology, Sociology, Psychology and Statistics meet to develop an integrated view of aging and death in their biological and social aspects. Topics examined include evolutionary and genetic factors, demography of aging, social attitudes, and human aging patterns.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 789', 'Graduate Readings in Sociology', 0.5, 'RDG', 'Selected readings in a specific topic including the preparation of a research paper under the supervision of a faculty member.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('STAT 846', 'Mathematical Models in Finance', 0.5, 'LEC, TUT', 'Mathematical techniques used to price and hedge derivative securities in modern finance. Modelling, analysis and computations for financial derivative products, including exotic options and swaps in all asset classes. Applications of erivatives in practice.', 'STAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOCIN 601', 'Social Innovations in Complex Systems', 0.5, 'LAB, LEC, RDG, SEM, TUT', 'This course explores theory, resilience theory and emerging theories of social innovation and their practical applications.  It also considers the role of leadership and institutional, social and policy entrepreneurs in successful innovation.', 'SOCIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOCIN 602', 'Design Thinking for System Change', 0.5, 'LAB, LEC, RDG, SEM, TUT', 'This course covers the history of design thinking as a tool for supporting system change at local and broad scales - from designers like Bruce Mau, who uses this approach to describe what he calls "massive change", to complexity thinkers like Brian Arthur who explores the nature of technological innovation.  The course also investigates the methodological implications of design thinking approaches to strategy development and implementation.', 'SOCIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOCIN 603', 'Team Process and Research Skills:  Communication, Facilitation, Collaboration, Problem Solving', 0.5, 'LEC, RDG, SEM, TUT', 'This is a multi-modal, skill-based course designed to help students act effectively in complex social systems and institutions.  Students will learn and practice strategies related to the management of diversity, conflict negotiation, and decision-makiing.  Self-diagnostic exercises and structured reflection assist students in identifying and understanding their own preferences and the roles they might most naturally and successfully take in group processes.', 'SOCIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOCIN 604', 'Social Entrepreneurship and Scaling Social Innovation', 0.5, 'LAB, LEC, SEM, TUT', 'This course explores different approaches that can be used to bring social innovations to scale so that they can have a broad, durable impact on a system.  The role of social entrepreneurs and institutional entrepreneurs will be introduced and explored as being key to catalyzing changes in resource flows, the understanding of problems and in shifting laws and policies that govern the problem domain.  New approaches to generating financing and revenue for social good, the governance and partnership structures these imply, and how they can enable or constrain systems change.  Examples will be drawn from Canadian and international contexts form the perspectives of entrepreneurs, investors/donors, policymakers and beneficiaries', 'SOCIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOCIN 605', 'Social Innovation Project', 0.5, 'LAB, LEC, RDG, SEM, TUT', 'The social innovation project is an applied, action-learning opportunity and all modules will be structured to support the development of a team project.  These projects entail collaborative research and the design of a strategy for social innovation.  The strategies focus on specific problem domains and integrate cross-sectoral perspectives (private, public, not-for-profit).  Expert panels will frame issues, offer insights, post challenges and review the final project presentations.', 'SOCIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOCWK 120R', 'Introduction to Social Work', 0.5, 'LEC', 'Presentation of the values, knowledge, and skill base; principles and purposes of the profession; and an examination of methods of practice. Traditional and innovative social work settings are discussed. Historic development of social work and its influence on contemporary practice are reviewed.', 'SOCWK');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOCWK 220R', 'Social Work with Individuals - Theory and Practice 1', 0.5, 'LEC', 'A presentation of some of the theoretical constructs necessary for the understanding of the individual in the casework relationship, as well as an introduction to some appropriate casework interventions. Emphasis in the course will be theoretical.', 'SOCWK');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOCWK 221R', 'Social Group Work', 0.5, 'LEC', 'Presentation of some of the theoretical constructs necessary for an understanding of social group work as well as an introduction to methodology and interventions.', 'SOCWK');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOCWK 222R', 'Community Organization 1', 0.5, 'LEC', 'An examination of social work practice as it relates to functional and geographical communities. The course will explore the theoretical foundations of organization practice as well as a variety of models.', 'SOCWK');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOCWK 240R', 'Palliative Care', 0.5, 'LEC', 'An introduction to the concepts and practices in palliative care. Topics include historical and philosophical background, the hospice movement, current approaches in palliative care, the multi-discipline team, stress factors, suicide and cross-cultural beliefs of death, illness and loss as they affect the terminally ill and their families.', 'SOCWK');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOCWK 300R', 'Canadian Social Welfare Policy', 0.5, 'LEC', 'This course will introduce students to the concept of social welfare as it has evolved in the post-industrial era. It will examine various social, economic and political factors that have shaped Canadian social welfare policy and it will consider present-day factors that are transforming that policy.', 'SOCWK');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOCWK 301R', 'Understanding Diversity in Canada', 0.5, 'LEC', 'This course explores culture; its components, characteristics, processes, theories, issues; and the history of diversity in Canadian social systems. It also examines the implications of diversity for social work practice and fosters culturally competent practice with micro and macro social systems.', 'SOCWK');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOCWK 320R', 'Social Work with Individuals - Theory and Practice 2', 0.5, 'LEC', 'Considers some of the intellectual components of the social work skills necessary for working with individuals. Social work theories of the individual will be examined in order for the student to learn some clinical applications relevant to the casework relationship.', 'SOCWK');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOCWK 321R', 'Social Work with Families', 0.5, 'LEC', 'Presentation of some of the theoretical constructs necessary for an understanding of the family in the social work relationship as well as an introduction to methodology and interventions.', 'SOCWK');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOCWK 322R', 'International Perspectives in Community Organization', 0.5, 'LEC', 'An exploration of community organization activities in the context of international processes of colonization and globalization.', 'SOCWK');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOCWK 326R', 'Philosophy and History of Social Welfare', 0.5, 'LEC', 'Social welfare from the 18th century to the present. The effects of religious, political, economic and cultural factors on social welfare, development, and the continuing influence of inherent attitudes, philosophies, and values on this complex institution. Focus on the Canadian social welfare system.', 'SOCWK');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOCWK 355R', 'Child Maltreatment: Identification and Prevention', 0.5, 'LEC, SEM', 'The objectives of this course are to provide an understanding of the dimensions and causes of child maltreatment, to develop skills identifying cases of this social problem, and to explore current methods of management and treatment of persons involved in child maltreatment situations.', 'SOCWK');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOCWK 356R', 'Developmental Disabilities and the Family', 0.5, 'LEC', 'This course explores the nature and extent of developmental disability in Canada. It reviews traditional approaches to support services and social work practice with people with disabilities and their families, and it examines how these have changed in response to social change movements and milestones. Students consider social work interventions with individuals and families from a strengths-based perspective emphasizing advocacy, empowerment and social support.', 'SOCWK');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOCWK 357R', 'Family Violence', 0.5, 'LEC', 'An application of the principles and models of medical, psychogenic, and sociogenic adjustment to an understanding of family violence. The treatment of victims of family violence, the prevention of such violence and social policies affecting family welfare are considered.', 'SOCWK');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOCWK 365R', 'Social Work in Health Care', 0.5, 'LEC', 'Analysis of social work in the medical setting, concentrating on identification and treatment of emotional, family, and community aspects of illness. Emphasis is on the concrete application of professional social work to health care, while comparing medical and social work values and concepts of illness.', 'SOCWK');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOCWK 367R', 'Social Work with the Elderly', 0.5, 'LEC', 'An examination of social work theory and practice concerning the needs of the elderly. Social work strategies of intervention with the healthy and frail aged will be considered from the individual, group, family, community, and bureaucratic perspectives.', 'SOCWK');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOCWK 375R', 'Studies in Social Work', 0.5, 'LEC', 'This course will deal with selected topics in social work. Subjects will be dependent upon the research and/or instructional interests of faculty.', 'SOCWK');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOCWK 390A', 'Family Violence: An Advanced Seminar', 0.5, 'LEC, SEM', 'Social work concepts and practices introduced in preceding family violence courses will be considered in depth. Over the course of two terms, a seminar format will be used to explore etiological and intervention issues pertaining to the various forms of family violence.', 'SOCWK');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOCWK 390B', 'Family Violence: An Advanced Seminar', 0.5, 'LEC, SEM', 'A continuation of SOCWK 390A. Social work concepts and practices introduced in preceding family violence courses will be considered in depth. A seminar format will be used to explore etiological and intervention issues pertaining to the various forms of family violence.', 'SOCWK');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOCWK 398R', 'Independent Study', 0.5, 'RDG', 'An independent in-depth study of a selected area of concern to the student within the discipline of social work. Available to individuals or small groups of third- or fourth-year Social Development Studies students and arranged with one of the faculty members from the program.', 'SOCWK');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOCWK 399R', 'Independent Study', 0.5, 'RDG', 'An independent in-depth study of a selected area of concern to the student within the discipline of social work. Available to individuals or small groups of third- or fourth-year Social Development Studies students and arranged with one of the faculty members from the program.', 'SOCWK');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOCWK 421R', 'Advanced Family Practices', 0.5, 'LEC', 'This course examines the integrated use of advanced knowledge, assessment and interventions addressing selected social problems in families, individuals, and communities.', 'SOCWK');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOCWK 450R', 'Senior Seminar in Special Topics', 0.5, 'SEM', 'Senior seminars may include weekly readings, individual and/or group projects, class presentations and discussions, research proposals, essay/literature reviews, assignments, midterms, and final exams. Consult departmental listings for topics and prerequisites for the current year.', 'SOCWK');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOCWK 490R', 'Special Studies', 0.5, 'ESS', 'An independent, in-depth study, based on empirical research and/or extensive reading in social work under the direction of individual instructors in social work. Available to individuals or small groups of fourth-year Social Development Studies majors and arranged with one of the faculty members from the plan. The project must be approved by the academic supervisor of the course prior to registration.', 'SOCWK');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SPAN 101', 'Introduction to Spanish 1', 0.5, 'LAB, LEC', 'This course is for students with no previous knowledge of Spanish. The basic elements of Spanish grammar will be studied, with emphasis on group and individual oral practice. Focus will be given to the development of skills in listening/comprehension, speaking, reading, and writing. Multimedia exercises accompany each chapter of the textbook. Students will be expected to use them in the multimedia language centre and at home.', 'SPAN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SPAN 102', 'Introduction to Spanish 2', 0.5, 'LAB, LEC', 'This course is a continuation of SPAN 101, with emphasis being placed on more advanced elements of Spanish grammar and the spoken language. Students will be provided with a range of opportunities to gain practical insights into the customs and cultural contexts of the contemporary Spanish-speaking world.', 'SPAN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SPAN 223W', 'Early Spanish Literature (WLU)', 0.5, 'LEC', 'This course will study some of the most significant texts from medieval time to the 18th century. Special emphasis is placed on textual analysis.', 'SPAN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SPAN 150', 'The Hispanic World Through Literature and the Arts', 0.5, 'LEC', 'A study of Hispanic cultures as represented in their literature, film, and visual arts with particular attention to issues of race, gender, sexuality, and cultural and national identity.', 'SPAN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SPAN 201A', 'Intermediate Spanish 1', 0.5, 'LEC, TUT', 'This course develops intermediate skills in reading, writing, speaking, and listening. The primary goals are to build communicative competence and enhance social and cultural awareness of the Spanish-speaking world.', 'SPAN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SPAN 201B', 'Intermediate Spanish 2', 0.5, 'LEC, TUT', 'The course continues development of intermediate skills in reading, writing, speaking, and listening. The primary goals of the course are the further enhancement of communicative competence, and social and cultural awareness. Particular attention is placed on the use of the subjunctive mood.', 'SPAN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SPAN 210', 'Intermediate Spanish for Native Speakers', 0.5, 'LEC', 'This is an intensive course designed for students of Spanish-speaking background (native or near-native) who have not received formal language training. The course focuses on a comprehensive study of Spanish grammar. It includes a critical analysis of colloquial Spanish, or anglicized vocabulary, and influences of other languages on modern Spanish. A major component of the course is the acquisition and development of writing skills and oral discourse at the academic level. The course further develops listening comprehension and reading skills.', 'SPAN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SPAN 217', 'First Nations, Native Americans, Pueblos Originarios', 0.5, 'LEC', 'This course will introduce and expose students to the Indigenous civilizations that embody the Americas. Students will look at the cosmogonies, histories, and cultures of Indigenous peoples from pre-Columbian times to the 20th century. They will analyze Latin America''s Pueblos Originarios'' perspectives on conquest, colonization, and independence, and compare such perspectives to those of the Canadian First Nations and Native Americans in general. Through their study of colonial, social, economic, and political relations, students will examine how the meanings of these categories changed over time and how Indigenous peoples have contributed to the ethnically diverse regional societies that continue to characterize Latin America today.', 'SPAN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SPAN 222W', 'Modern Spanish Literature (WLU)', 0.5, 'LEC', 'This course will study some of the most salient literary texts produced from the 18th century up to the present time. The course also aims to introduce the student to literary analysis and, therefore, particular attention will be paid to the question of genre, terminology, literary movements and textual analysis.', 'SPAN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SPAN 218', 'Parallel Revolutions in a Nascent Continent', 0.5, 'LEC', 'This course provides a broad foundation to the varied and complex geographical, ethnic, social, and historical forces that have shaped contemporary Latin American nations from the early 19th-century independence period to current affairs. Students will be introduced to the diverse regions of the continent: Mexico, Central America, the Caribbean, the Bolivarian republics, and the Southern Cone. Students will analyze and discuss nation-building factors such as European-African-Asian-New World interactions, ethnic mixtures, military takeovers, revolutions, U.S. interventions in the region, and migratory experiences. Discussions will also be aimed at contrasting and comparing the contemporary and diverse cultural manifestations of the peoples living in Latin America and the cultural transformation of Latin Americans who have emigrated to other parts of the world, as well as the perceptions of Latin Americans in other cultures.', 'SPAN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SPAN 220W', 'Topics in Spanish Culture(WLU)', 0.5, 'LEC', 'This course is designed to provide students with the opportunity to explore and develop their knowledge of Spanish culture. The course will offer a wide-ranging survey of cultural topics. It will take a broad view of the country by covering aspects of daily life, history, and social, economic and political changes.', 'SPAN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SPAN 227', 'Introduction to Latin American Poetry and Drama', 0.5, 'LEC', 'This course presents an introduction to Latin American poetry and theatre produced since pre-Columbian times. Through the study of representative works the course covers the most important periods and movements of Latin American poetry and theatre, and introduces the student to literary analysis by paying special attention to questions of terminology, genre, and literary history.', 'SPAN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SPAN 228', 'Introduction to Latin American Literature', 0.5, 'LEC', 'Through reading representative works, this course introduces students to the study of Latin American literature, paying special attention to questions of critical approach and terminology, genre, literary history, and the historicity of texts.', 'SPAN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SPAN 301A', 'Spanish in Context 1', 0.5, 'LEC, TUT', 'This course promotes students'' oral and written abilities, as well as their critical understanding of Latin American history and culture. Course components include: written and oral/aural practice of Spanish through oral reports, class discussions and debates on assigned topics, short literary works, and films. It offers a review of more advanced grammatical structures with the aim of achieving greater accuracy.', 'SPAN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SPAN 301B', 'Spanish in Context 2', 0.5, 'LEC, TUT', 'This course further develops the development of critical understanding of Latin American history and culture. Course components include: assigned readings in a variety of genres, critical cultural analysis, in-class discussions, written essays, and the viewing of films.', 'SPAN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SPAN 305W', 'The Hispanic Realist Novel (WLU)', 0.5, 'LEC', 'Study of the fundamental narrative techniques and ideology in some of the most representative realist novels of the 19th century.', 'SPAN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SPAN 326', 'Theatre of the Spanish Golden Age: Texts and Cultural Contexts', 0.5, 'LEC', 'Representative dramatic texts from the early modern period will be studied in relation to the history and society of Imperial Spain. Students will consider the texts as vehicles to support the political agenda and moral education of the time, with discussion centering on such issues as the formation of state, court culture, social order and disorder, and cultural discourses of identity and difference.', 'SPAN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SPAN 327W', 'Cervantes & His Time (WLU)', 0.5, 'LEC', 'A general introduction to literature in its historical and cultural background. A special study through the close analysis of representative works. Emphasis will be given to the study of Don Quijote.', 'SPAN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SPAN 334', 'Narrating Place and Ethnicity in 19th Century Latin America', 0.5, 'LEC', 'This course will provide insight into Latin American political and cultural history by viewing key 19th and early 20th century texts in the context of ideas about nation, nation-building processes, and the emergence of the modern nation-state. These issues will also be viewed in the context of aesthetic developments in Latin America.', 'SPAN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SPAN 335W', 'Aesthetic Practices of Spanish and Latin American Filmmakers(WLU)', 0.5, 'LEC, TUT', 'The objective of this course is to provide students with the background to analyse and understand the rapid evolution/regression of Spanish and Latin American cultures in the 20th century. The movies presented will be studied in their socio-historical context. A reading list based on a selection of pertinent literature on the course topics will provide the necessary background to understand the content of the films.', 'SPAN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SPAN 344', 'Special Topics in Hispanic Studies', 0.5, 'LEC', 'Special topics in Hispanic studies, as announced by the department.', 'SPAN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SPAN 345', 'Directed Studies', 0.5, 'RDG', 'Study in a limited field under tutorial guidance.', 'SPAN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SPAN 350', 'Poetry of the Tango', 0.5, 'LEC', 'This course examines the poetic value of tango lyrics as an authentic merger of the learned and the popular poetry. The poetic aspects of tango from its birth at the turn of the twentieth century to the present, with special attention to the so called "golden age of tango" (1940), are explored.', 'SPAN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SPAN 366', 'Aesthetics of Rupture: Latin American Avant-garde Movements', 0.5, 'LEC', 'This course explores the artistic and cultural projects advanced by different avant-garde movements in Latin America over the early decades of the twentieth century. Through the study of representative visual and literary works, the course traces how the avant-garde worked to develop a new artistic language for Latin America.', 'SPAN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SPAN 386', 'Memory and Performance in Latin American Literature', 0.5, 'LEC', 'This course studies the participation of poetry and theatre in the reconstruction of the historical memory in Latin America. It will examine the varied means of expressions used by poets and play writers to rebuild fragments of the historical reality frequently omitted by the official discourse.', 'SPAN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SPAN 387', 'Gender, Power, and Representations in Latin America', 0.5, 'LEC', 'This course analyzes women''s cultural production in Latin America. Examining the construction and the representation of gender/sex differences, the course explores the power relations inherent in these representations, while also examining how gender roles and expectations are linked to representations of class, race, sexuality, age, nationality, and ability.', 'SPAN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SPAN 390', 'Introduction to Spanish Business Translation', 0.5, 'LEC', 'This course is designed for students enrolled in their third year of the General or Honours program in Spanish. A strong command of Spanish and English is required. Intense concentration of the Spanish language through oral and written work will focus on business-oriented fundamentals of communication such as letter writing, as well as the translation of primary source materials such as newspapers and business documents.', 'SPAN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SPAN 400', 'Memories and Representations: Constructive Truths and Competing Realities', 0.5, 'LEC', 'This course studies the emergence of testimonial discourse in Latin America in relation to the socio-political turmoil that has characterized Latin American life in the past decades. Contemporary fictional and non-fictional texts, as well as documentaries and movies, will be discussed in order to establish the grounds of testimonial discourse as a genre.', 'SPAN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SPAN 401A', 'Advanced Composition and Conversation 1', 0.5, 'LEC, TUT', 'This course is aimed at intense development of written and oral skills. Written assignments emphasize grammatical style and structure; oral class conversations are based on selected themes or topics relating to Spain and Latin America.', 'SPAN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SPAN 401B', 'Advanced Composition and Conversation 2', 0.5, 'LEC, TUT', 'A continuation of SPAN 401A.', 'SPAN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SPAN 410', 'Visual Culture in the Contemporary Hispanic World', 0.5, 'LEC', 'This course explores the expressive avenues opened up by the confluence of verbal and visual representation in contemporary Hispanic cultural production. Through a close analysis of representative visual and literary works from Latin America and Spain covering a variety of genres and media, including poetry, novel, short story, theatre, painting, photography, and film, the many ways in which words and images interact with each other and make meaning will be examined.', 'SPAN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SPAN 415', 'The Hispanic Transatlantic', 0.5, 'LEC', 'This course explores cultural interactions between Spain and Latin America, and among Spain, Latin America, and the U. S., in literature, music, film, and popular culture from the early modern period to the present. Topics include imperialism, the relationship between modernity and colonialism, diasporas, contact zones, transculturation, rearticulation of transnational identities, coexistence in difference, borderlines, mestizo cultural spaces, and cultures of resistance.', 'SPAN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SPAN 418', 'Modernity and the Colonial Encounter in Latin America', 0.5, 'LEC', 'This course explores the divergent consequences of the colonial "encounter" for the indigenous cultures and for the Western world. Students analyze colonial cultural productions in order to understand the role of the colonial experience in the economic development of Western Europe, as well as the underdevelopment of Latin America.', 'SPAN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SPAN 430', 'Literary Women in Early Modern Hispanic Culture', 0.5, 'LEC', 'This course will examine works by and about women in early modern Hispanic culture (16th and 17th centuries), with particular attention to engagements with and subversions of patriarchal culture in theatre, prose, and poetry. Authors to be discussed may include, among others, Santa Teresa de Avila, Sor Juana Ines de la Cruz, Maria de Zayas, and Ana Caro.', 'SPAN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SPAN 445', 'History of the Spanish Language', 0.5, 'LEC', 'This course presents a detailed study of the external and internal history of the language. Topics treated include a brief outline of factors involved in linguistic evolution and language formation. The main features of the phonetic evolution from Latin to Spanish are studied with emphasis on the formation of speech sounds and the factors that spearheaded the mutations.', 'SPAN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SPAN 450', 'Theory and Practice of Translation', 0.5, 'LEC', 'This course examines the theory of translation, combined with intense practical experience. The students continue the review of Spanish and English grammar in a practical context as a basis for translation between the two languages. The course also enhances the students'' lexical and semantic knowledge of the Spanish language through direct experience in translation.', 'SPAN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SPAN 490', 'Advanced Translation', 0.5, 'LEC', 'This course is designed for students enrolled in the third and fourth year of the General or Honours program in Spanish. Fluency in both Spanish and English is required. The objective of this course is to refine translation techniques in order to enable the student to provide accurate and idiomatic translations of English texts in Spanish and vice versa.', 'SPAN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SPAN 497', 'The Novel in Latin America', 0.5, 'LEC', 'This course studies the 20th century novel and its development from the 1930s to the present through representative authors. While special attention is paid to the individual works in the last half century, the individual works are analyzed for their value as the expression of social and historical reality.', 'SPAN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SPCOM 100', 'Interpersonal Communication', 0.5, 'LEC', 'Focuses on the one-to-one, face-to-face communication in both the personal and professional realms. Such topics as verbal and non-verbal interactions, listening, and the better management of interpersonal communication will be studied.', 'SPCOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SPCOM 101', 'Theories of Communication', 0.5, 'LEC', 'This is an introductory course addressing the major theoretical issues in, approaches to, and applications of communication. Theories from various branches of communication, including interpersonal, group/organizational, rhetorical, mass and cultural, will be explored in-depth. Historical, current, and practical critiques of each theory will be conducted. Coursework is designed to encourage students to give critical consideration to the place of theory within the field of Speech Communication and within everyday life.', 'SPCOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SPCOM 102', 'Introduction to Performance', 0.5, 'WSP', 'This workshop course introduces students to the creative processes of performance in a range of formal and informal settings. Emphasis is placed on the student''s development as a performer. Prior performance experience is not required.', 'SPCOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SPCOM 111', 'Leadership, Communication, and Collaboration', 0.5, 'LEC', 'This course focuses on developing the qualities and transferable skills necessary for integration, continuous learning, and professional development. This course establishes a common baseline of leadership, communication, and collaboration capabilities for Accounting and Financial Management majors. Focus will be on the design and delivery of various types of communication and the development of basic teamwork skills.', 'SPCOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SPCOM 204', 'Leadership, Teams, and Communication', 0.5, 'LEC, TST', 'This course examines leadership, team dynamics, and communication in organizational contexts. Students will develop communication knowledge and skills to enhance their abilities to be effective in leadership and team roles.', 'SPCOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SPCOM 220', 'Performance Studies', 0.5, 'WSP', 'This workshop course in performance studies explores performance as a way of knowing. It investigates performance as artistic practice and as a means of understanding historical, social, and cultural practices, including drama/theatre texts, poetry, narratives, and texts of everyday life.', 'SPCOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SPCOM 223', 'Public Speaking', 0.5, 'LEC', 'Theory and practice of public speaking. A workshop course involving design and delivery of various kinds of speeches, and the development of organizational, vocal, listening, and critical skills. Students will be videotaped.', 'SPCOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SPCOM 225', 'Interviewing', 0.5, 'LEC', 'Theory and practice of interviewing. A workshop course which teaches theory, design, and presentation of interviews. Videotaping student exercises will enhance interview design and delivery, as well as listening and critical skills.', 'SPCOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SPCOM 226', 'Introduction to Intercultural Communication', 0.5, 'LEC', 'This course introduces the basic theories and practices for communicating among people of different racial, ethnic, and cultural backgrounds. This class provides an opportunity for discussion of variations within and among cultures and encourages students to examine their cultural heritage.', 'SPCOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SPCOM 227', 'Leadership', 0.5, 'WSP', 'A workshop course in leadership combining theoretical and experiential perspectives. Students will develop and apply knowledge, skills, and attitudes necessary to be effective and perceptive communicators in a leadership position.', 'SPCOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SPCOM 228', 'Public Communication', 0.5, 'LEC', 'This course introduces a theoretical framework for understanding the nature and significance of public communication. Strategies and techniques typically employed in political and commercial contexts are examined.', 'SPCOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SPCOM 300', 'Special Topics in Digital Design', 0.5, 'LEC', 'In this course students will learn advanced digital design theory. They will participate in workshops with professional designers, develop specialized digital materials, and contribute signature work to their digital portfolio.', 'SPCOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SPCOM 323', 'Speech Writing', 0.5, 'LEC', 'The analysis, writing, and editing of speeches. Analysis will focus on the reading and viewing of several famous 20th-century speeches using theories of communication. Writing and editing will focus on implementing oral/aural communication strategies.', 'SPCOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SPCOM 324', 'Small Group Communication', 0.5, 'LEC', 'A workshop course which works from theory to develop the skills to work in groups effectively. The principles of group dynamics, leadership, and conflict resolution will be studied and implemented in small group meetings and presentations.', 'SPCOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SPCOM 325', 'Organizational Communication', 0.5, 'LEC', 'This course examines organizational theory, the communication process, and the interplay between the two. Students will develop the skills to identify, analyze, and solve a variety of organizational communication problems through case study group work and other workshop-style methods.', 'SPCOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SPCOM 326', 'Performing the Voice', 0.5, 'WSP', 'Students explore techniques to access and develop the voice as a resource for performance.', 'SPCOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SPCOM 329', 'Digital Presentations', 0.5, 'LEC', 'In this course students will be introduced to design and production of digital business presentations. They will develop specialized digital materials and contribute work to their digital portfolio.', 'SPCOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SPCOM 399', 'Communication Inquiry', 0.5, 'SEM', 'This course identifies critical communication inquiry at the individual, group, public, and collective levels of theory and practice. Students will work to develop strategies of engagement or thinking that could extend, modify, or overturn standing theoretical positions and initiate new inquiry. Critique and criticism are developed in order to explore advanced questions within the field of Speech Communication, and students are introduced to critical research methods used by scholars in the field of Speech Communication.', 'SPCOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SPCOM 401', 'Gender, Communication, and Culture', 0.5, 'LEC', 'In this course students examine multiple relationships between communication and gender. The class emphasizes how communication creates gender and power roles and how communicative patterns reflect, sustain, and alter social conceptions of gender.', 'SPCOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SPCOM 402', 'Advanced Intercultural Communication', 0.5, 'LEC', 'This course is designed to study the intersection of communication and culture. In this course, culture is defined broadly to include a variety of contexts, such as race, ethnicity, nationality, gender, age, and class. Students gain theoretical and practical understanding of the opportunities and obstacles that exist as individuals and communities communicate within and across cultures.', 'SPCOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SPCOM 404', 'Communicating Across Differences: Spiritual Development in a Diverse World', 0.5, 'WSP', 'In this workshop course students will develop the knowledge and skills necessary to communicate effectively across differences. Addressing issues of diversity and commonality in the spiritual life and cross-cultural patterns in spiritual experiences, students will apply communication and contemplative practices to explore the intersection of spiritual development, interpersonal dialogue and trans-traditional community-building.', 'SPCOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SPCOM 420', 'Persuasion', 0.5, 'SEM', 'This course examines the communicative, psychological, and sociological aspects of persuasion and persuasive messages, with attention to interpersonal contexts, the role of images, and persuasion in the media and public discourse. This course will explore the ways in which the sending and receiving of persuasive messages involve cognition, emotions, and social norms in everyday contexts.', 'SPCOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SPCOM 430', 'Communication and Social Justice', 0.5, 'SEM', 'An examination of the ways in which communication on the part of individuals, groups, and institutions contributes to fostering justice in social contexts. Areas of focus include communication and justice in interpersonal, workplace, community, and national/international contexts.', 'SPCOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SPCOM 431', 'Crisis Communication', 0.5, 'LEC', 'This case study course examines the communication strategies involved in a public response to crisis, using such fields as sports, the entertainment world, politics, and business.', 'SPCOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SPCOM 432', 'Conflict Management', 0.5, 'LEC', 'This course focuses on how conflict is communicated and resolved in one-on-one, group, and organizational contexts. Theoretical perspectives, simulations, role plays, and self-assessment exercises will enhance students'' abilities to be successful communicators in conflict situations.', 'SPCOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SPCOM 433', 'The Organizational Consultant', 0.5, 'WSP', 'This workshop course will examine the communication skills necessary for the organizational consultant. Participation in videotaped simulations will allow students to explore consulting styles and training interventions by developing the communications expertise and knowledge specific to the consulting professional.', 'SPCOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SPCOM 434', 'The Discourse of Dissent', 0.5, 'DIS, LEC', 'A study of the social, historical, and rhetorical dimensions of collective action. Topics may include health and welfare movements, civil rights and anti-war protests, and environmentalism.', 'SPCOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SPCOM 440', 'Performative Inquiry and Practice', 0.5, 'LEC, SEM, WSP', 'This course explores how to create, perform, and analyze performance texts, here defined as including drama/theatre texts, poetry, narratives, and the texts of everyday life. Through readings and creative investigation, students will explore the links between the participant, the researcher, the site, and the impulse of inquiry.', 'SPCOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SPCOM 475', 'Communication Ethics', 0.5, 'SEM', 'An examination of the interplay between communication and ethics from historical and pragmatic perspectives. Issues discussed include communication in a variety of settings, such as intrapersonal, interpersonal, organizational, public, and intercultural interactions as they relate to personal development, values, meaning making, and ethical ways of communicating.', 'SPCOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SPCOM 490', 'Selected Seminars in Speech Communication', 0.5, 'SEM', 'Seminars in special areas of speech communication.', 'SPCOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SPCOM 491', 'Selected Seminars in Speech Communication', 0.5, 'SEM', 'Seminars in special areas of speech communication.', 'SPCOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SPCOM 499A', 'Senior Seminar', 0.5, 'LEC', 'This course is designed to give the student an opportunity to complete a comprehensive presentation in their major area of concentration.', 'SPCOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SPCOM 499B', 'Senior Seminar', 0.5, 'LEC', 'This course is designed to give the student an opportunity to complete a comprehensive presentation in their major area of concentration. Second part of THPERF/SPCOM 499.', 'SPCOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('STAT 202', 'Introductory Statistics for Scientists', 0.5, 'LEC, TST, TUT', 'Elementary probability, populations, samples and distributions with biological examples. Methods for data summary and presentation. Estimation, hypothesis testing, two-sample techniques and paired comparisons, regression, correlation. [Offered: F,W]', 'STAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('STAT 206', 'Statistics for Software Engineering', 0.5, 'LEC, TUT', 'Empirical problem solving with applications to software engineering. An introduction to probability theory. An introduction to distribution theory and to methods of statistical inference, including confidence intervals and hypothesis testing. An introduction to regression. [Offered: F]', 'STAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('STAT 211', 'Introductory Statistics and Sampling for Accounting', 0.5, 'LAB, LEC, TUT', 'Descriptive statistics, probability, discrete and continuous random variables. Sampling distributions and simple hypothesis testing. Introduction to survey sampling. [Offered: W]', 'STAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('STAT 220', 'Probability (Non-Specialist Level)', 0.5, 'LEC, TST, TUT', 'The laws of probability, discrete and continuous random variables, expectation, central limit theorem. [Offered: F,W]', 'STAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('STAT 221', 'Statistics (Non-Specialist Level)', 0.5, 'LEC, TST, TUT', 'Empirical problem solving, measurement systems, causal relationships, statistical models, estimation, confidence intervals, tests of significance. [Offered: F, W]', 'STAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('STAT 230', 'Probability', 0.5, 'LEC, TST, TUT', 'This course provides an introduction to probability models including sample spaces, mutually exclusive and independent events, conditional probability and Bayes'' Theorem. The named distributions (Discrete Uniform, Hypergeometric, Binomial, Negative Binomial, Geometric, Poisson, Continuous Uniform, Exponential, Normal (Gaussian), and Multinomial) are used to model real phenomena. Discrete and continuous univariate random variables and their distributions are discussed. Joint probability functions, marginal probability functions, and conditional probability functions of two or more discrete random variables and functions of random variables are also discussed. Students learn how to calculate and interpret means, variances and covariances particularly for the named distributions. The Central Limit Theorem is used to approximate probabilities.', 'STAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('STAT 231', 'Statistics', 0.5, 'LEC, TST, TUT', 'This course provides a systematic approach to empirical problem solving which will enable students to critically assess the sampling protocol and conclusions of an empirical study including the possible sources of error in the study and whether evidence of a causal relationship can be reasonably concluded. The connection between the attributes of a population and the parameters in the named distributions covered in STAT 230 will be emphasized. Numerical and graphical techniques for summarizing data and checking the fit of a statistical model will be discussed. The method of maximum likelihood will be used to obtain point and interval estimates for the parameters of interest as well as testing hypotheses. The interpretation of confidence intervals and p-values will be emphasized. The Chi-squared and t distributions will be introduced and used to construct confidence intervals and tests of hypotheses including likelihood ratio tests. Contingency tables and Gaussian response models including the two sample Gaussian and simple linear regression will be used as examples.', 'STAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('STAT 240', 'Probability (Advanced Level)', 0.5, 'LEC, TST', 'STAT 240 is an advanced-level enriched version of STAT 230.', 'STAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('STAT 241', 'Statistics (Advanced Level)', 0.5, 'LEC, TST, TUT', 'STAT 241 is an advanced-level enriched version of STAT 231.', 'STAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('STAT 316', 'Introduction to Statistical Problem Solving by Computer', 0.5, 'LEC, TUT', 'This is an applications oriented course which prepares the nonmathematical student to use the computer as a research tool. Topics include aids for statistical analysis and the preparation of documents such as reports and theses. The course provides sufficient background for application to other problems specific to the individual''s field. [Offered: W]', 'STAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('STAT 321', 'Regression and Forecasting (Non-Specialist Level)', 0.5, 'LEC, TUT', 'Modeling the relationship between a response variable and several explanatory variables via regression models. Model diagnostics and improvement. Using regression models for forecasting, Exponential smoothing. Simple time series modeling. [Offered: W]', 'STAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('STAT 322', 'Sampling and Experimental Design (Non-Specialist Level)', 0.5, 'LEC, TUT', 'Planning sample surveys; simple random sampling; stratified sampling. Observational and experimental studies. Blocking, randomization, factorial designs. Analysis of variance. Applications of design principles. [Offered: F]', 'STAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('STAT 330', 'Mathematical Statistics', 0.5, 'LEC, TUT', 'This course provides a mathematically rigorous treatment for topics covered in STAT 230 and 231, and to make essential extensions to the multivariate case. Maximum likelihood estimation. Random variables and distribution theory. Generating functions. Functions of random variables. Limiting distributions. Large sample theory of likelihood methods. Likelihood ratio tests. 
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('STAT 446', 'Mathematical Models in Finance', 0.5, 'LEC, TUT', 'Mathematical techniques used to price and hedge derivative securities in modern finance. Modelling, analysis and computations for financial derivative products, including exotic options and swaps in all asset classes. Applications of derivatives in practice. [Offered: F,W]', 'STAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('STAT 331', 'Applied Linear Models', 0.5, 'LEC, TUT', 'Modeling the relationship between a response variable and several explanatory variables (an output-input system) via regression models. Least squares algorithm for estimation of parameters. Hypothesis testing and prediction. Model diagnostics and improvement. Algorithms for variable selection. Nonlinear regression and other methods. [Offered: F,W,S]', 'STAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('STAT 332', 'Sampling and Experimental Design', 0.5, 'LEC, TUT', 'Designing sample surveys. Probability sampling designs. Estimation with elementary designs. Observational and experimental studies. Blocking, randomization, factorial designs. Analysis of variance. Designing for comparison of groups. [Offered: F,W,S]', 'STAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('STAT 333', 'Applied Probability', 0.5, 'LEC, TUT', 'Review of basic probability. Generating functions. Theory of recurrent events. Markov chains, Markov processes, and their applications. [Offered: F,W,S]', 'STAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('STAT 334', 'Probability Models for Business and Accounting', 0.5, 'LEC, TUT', 'Random variables and distribution theory, conditional expectations, moment and probability generating functions, change of variables, random walks, Markov chains, Markov processes. [Offered F,S]', 'STAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('STAT 337', 'Introduction to Biostatistics', 0.5, 'LEC, TUT', 'This course will provide an introduction to statistical methods in health research. Topics to be covered include types of medical data, measures of disease prevalence and incidence, age and sex adjustment of disease rates, sensitivity and specificity of diagnostic tests, ROC curves, measures of association between risk factors and disease, major sources of medical data in the Canadian context including surveys, registries, and clinical studies such as cohort studies, clinical trials and case-control studies. Papers from the medical literature will be used throughout to illustrate the concepts. Introduction to SAS for data analysis and an introduction to database management tools. [Offered: F]', 'STAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('STAT 340', 'Computer Simulation of Complex Systems', 0.5, 'LEC, TUT', 'Building and validation of stochastic simulation models useful in computing, operations research, engineering and science. Related design and estimation problems. Variance reduction. The implementation and the analysis of the results. [Offered: W,S]', 'STAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('STAT 341', 'Computational Statistics and Data Analysis', 0.5, 'LEC, TUT', 'Approximation and optimization of noisy functions. Simulation from univariate and multivariate distributions, multivariate normal distribution, mixture distributions and introduction to Markov Monte Carlo. Introduction to supervised statistical learning including discrimination methods. [Offered: F]', 'STAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('STAT 371', 'Applied Linear Models and Process Improvement for Business', 0.5, 'LEC, TUT', 'Practical and theoretical aspects of simple and multiple linear regression models. Model building, fitting and assessment. Process thinking and improvement. Strategies for variation reduction such as control charting. Process monitoring, control and adjustment. Applications to problems in business. [Offered: F,W,S]', 'STAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('STAT 372', 'Survey Sampling and Experimental Design Techniques for Business', 0.5, 'LEC, TUT', 'Design and analysis of surveys. Management of sample and non-sample error. Simple random sampling and stratified random sampling. Additional topics in survey sampling. Observational and experimental studies. Principles for the design of experiments. Analysis of Variance, factorial experiments and interaction. Application to problems in business. [Offered: F,W,S]', 'STAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('STAT 373', 'Regression and Forecasting Methods in Finance', 0.5, 'LEC, TUT', 'Application of regression and time series models in finance; multiple regression; algebraic and geometric representation of least squares; inference methods - confidence intervals and hypothesis tests, ANOVA, prediction; model building and assessment; time series modeling; autoregressive AR(1) models - fitting, assessment and prediction; moving average smoothing, seasonal adjustment; non-stationarity and differencing. [Offered: F]', 'STAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('STAT 430', 'Experimental Design', 0.5, 'LEC, TUT', 'Review of experimental designs in a regression setting; analysis of variance; replication, balance, blocking, randomization, and interaction; one-way layout, two-way layout, and Latin square as special cases; factorial structure of treatments; covariates; treatment contrasts; two-level fractional factorial designs; fixed versus random effects; split-plot and repeated-measures designs; other topics. [Offered: F,S]', 'STAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('STAT 431', 'Generalized Linear Models and their Applications', 0.5, 'LEC, TUT', 'Review of the normal linear model and maximum likelihood estimation; regression models for binomial, Poisson and multinomial data; generalized linear models; and other topics in regression modelling. [Offered: F,W,S]', 'STAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('STAT 433', 'Stochastic Processes', 0.5, 'LEC', 'Point processes. Renewal theory. Stationary processes. Selected topics. [Offered: F]', 'STAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('STAT 435', 'Statistical Methods for Process Improvements', 0.5, 'LEC, TUT', 'Statistical methods for improving processes based on observational data. Assessment of measurement systems. Strategies for variation reduction. Process monitoring, control and adjustment. Clue generation techniques for determining the sources of variability. Variation transmission. [Offered: W]', 'STAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('STAT 436', 'Introduction to the Analysis of Spatial Data in Health Research', 0.5, 'LEC', 'The objective of this course is to develop understanding and working knowledge of spatial models and analysis of spatial data. The course provides an introduction to the rudiments of statistical inference based on spatially correlated data. Methods of estimation and testing will be developed for geostatistical models based on variograms and spatial autogressive models. Concepts and application of methods will be emphasized through case studies and projects with health applications. [Offered: W]', 'STAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('STAT 437', 'Statistical Methods for Life History Analysis', 0.5, 'LEC', 'Statistical methods for the analysis of longitudinal data; hierarchical models, marginal models, and transitional models. Parametric and semiparametric methods for the analysis of survival data under censoring and truncation. [Offered: W]', 'STAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('STAT 440', 'Computational Inference', 0.5, 'LEC', 'Introduction to and application of computational methods in statistical inference. Monte Carlo evaluation of statistical procedures, exploration of the likelihood function through graphical and optimization techniques including EM. Bootstrapping, Markov Chain Monte Carlo, and other computationally intensive methods. [Offered: W]', 'STAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('STAT 441', 'Statistical Learning - Classification', 0.5, 'LEC', 'Given known group membership, methods which learn from data how to classify objects into the groups are treated. Review of likelihood and posterior based discrimination. Main topics include logistic regression, neural networks, tree-based methods and nearest neighbour methods. Model assessment, training and tuning. [Offered: F]', 'STAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('STAT 442', 'Data Visualization', 0.5, 'LEC, TUT', 'Visualization of high dimensional data including interactive methods directed at exploration and assessment of structure and dependencies in data. Methods for finding groups in data including traditional and modern methods of cluster analysis. Dimension reduction methods including multi-dimensional scaling, nonlinear and other methods. [Offered: F]', 'STAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('STAT 443', 'Forecasting', 0.5, 'LEC, TUT', 'Modelling techniques for forecasting time series data: smoothing methods, regression including penalty/regularization methods, the Box-Jenkins framework, stationary and non-stationary processes, both with and without seasonal effects. Other topics may include: ARCH/GARCH models, Bayesian methods, dynamic linear models, Markov Chain Monte Carlo simulation, spectral density analysis, and periodograms. [Offered: F,W,S]', 'STAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('STAT 444', 'Statistical Learning - Function Estimation', 0.5, 'LEC', 'Methods for finding surfaces in high dimensions from incomplete or noisy functional information. Both data adaptive and methods based on fixed parametric structure will be treated. Model assessment, training and tuning. [Offered: W]', 'STAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('STAT 450', 'Estimation and Hypothesis Testing', 0.5, 'LEC, TUT', 'Discussion of inference problems under the headings of hypothesis testing and point and interval estimation. Frequentist and Bayesian approaches to inference. Construction and evaluation of tests and estimators. Large sample theory of point estimation. [Offered: W]', 'STAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('STAT 454', 'Sampling Theory and Practice', 0.5, 'LEC', 'Sources of survey error. Probability sampling designs, estimation and efficiency comparisons. Distribution theory and confidence intervals. Generalized regression estimation. Software for survey analysis. [Offered: W]', 'STAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('STAT 464', 'Topics in Probability Theory', 0.5, 'LEC', 'Special Topics course as announced by the department.', 'STAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('STAT 466', 'Topics in Statistics 1', 0.5, 'LEC', 'Special Topics course as announced by the department.', 'STAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('STAT 467', 'Topics in Statistics 2', 0.5, 'LEC', 'Special Topics course as announced by the department.', 'STAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('STAT 468', 'Readings in Statistics 1', 0.5, 'RDG', 'Reading course as announced by the department.', 'STAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('STAT 469', 'Readings in Statistics 2', 0.5, 'RDG', 'Reading course as announced by the department.', 'STAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('STAT 631', 'Introduction to Statistical Methods in Health Informatics', 0.5, 'LEC', 'Exploratory data analysis and data visualization.  Confounding, censoring, selection bias, study designs and meta-analysis.  Statistical modelling for continuous and binary data.  Use of a statistics package, such as SAS, to analyze case studies will be important throughout.  This is open only to students registered in the Masters of Health Informatics plan.', 'STAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('STAT 690', 'Literature and Research Studies', 0.5, 'RDG', 'None', 'STAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('STAT 830', 'Experimental Design', 0.5, 'LEC', 'Review of experimental designs in a regression setting; analysis of variance; replication, balance, blocking, randomization, and interaction; one-way layout, two-way layout, and Latin square as special cases; factorial structure of treatments; covariates; treatment contrasts; two-level fractional factorial designs; fixed versus random effects; split-plot and repeated-measures designs; other topics.', 'STAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('STAT 831', 'Generalized  Linear Models and Applications', 0.5, 'LEC', 'Review of normal linear regression and maximum likelihood estimation. Computational methods, including Newton-Raphson and iteratively reweighted least squares. Binomial regression; the role of the link function. Goodness-of-fit, goodness-of-link, leverage. Poisson regression models. Generalized linear models. Other topics in regression modelling.', 'STAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('STAT 833', 'Stochastic Processes', 0.5, 'LEC', 'Random walks, renewal theory and processes and their application, Markov chains, branching processes, statistical inference for Markov chains.', 'STAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('STAT 835', 'Statistical Methods for Process Improvement', 0.5, 'LEC, TUT', 'Statistical methods for improving processes based on observational data. Assessment of measurement systems. Strategies for variation reduction. Process monitoring, control and adjustment. Clue generation techniques for determining the sources of variability. Variation transmission.', 'STAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('STAT 836', 'Introduction to the Analysis of Spatial Data in Health Research', 0.5, 'LEC', 'The objective of this course is to develop understanding and working knowledge of spatial models and analysis of spatial data.  The course provides an introduction to the rudiments of statistaical inference based on spatially correlated data.  Methods of estimation and testing will be developed for geostatistical models based on variograms and spatial autoregressive models.  Concepts and application of methods will be emphasized through case studies and projects with health applications.', 'STAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('STAT 837', 'Analysis of Longitudinal Data in Health Research', 0.5, 'LEC', 'This course will provide an introduction to principles and methods for the analysis of longitudinal data.  Conditional and random effect of modeling approaches to regression analysis will be covered, as well as semiparametric methods based on generalized estimating equations.  The importance of model assessment and parameter interpretation will be emphasized.  Problems will be motivated by applications in epidemiology, clinical medicine, health services research, and disease natural history studies.  Students will be required to think critically about appropriate strategies for data analysis.  Analysis will be carried out with appropriate statistical software.', 'STAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('STAT 840', 'Computational Inference', 0.5, 'LEC', 'Introduction to and application of computational methods in statistical inference.  Monte Carlo evaluation of statistical procedures, exploration of the likelihood function through graphical and optimization techniques including EM.  Bootstrapping, Markov Chain Monte Carlo, and other computationally intensive methods.', 'STAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('STAT 841', 'Statistical Learning - Classification', 0.5, 'LEC', 'Given known group membership, methods which learn from data how to classify objects into the groups are treated.  Review of likelihood and posterior based discrimination.  Main topics include logistic regression, neural networks, tree-based methods and nearest neighbour methods.  Model assessment, training and tuning.', 'STAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('STAT 842', 'Data Visualization', 0.5, 'LEC', 'Visualization of high dimensional data including interactive methods directed at exploration and assessment of structure and dependencies in data.  Methods for finding groups in data including traditional and modern methods of cluster analysis.  Dimension reduction methods including multi-dimensional scaling, nonlinear and other methods.', 'STAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('STAT 844', 'Statistical Learning - Function Estimation', 0.5, 'LEC', 'Methods for finding surfaces in high dimensions from incomplete or noisy functional information.  Both data adaptive and methods based on fixed parametric structure will be treated.  Model assessment, training and tuning.', 'STAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('STAT 850', 'Estimation and Hypothesis Testing', 0.5, 'LEC, TUT', 'Discussion of inference problems under the headings of hypothesis testing and point and interval estimation. Frequentist and Bayesian approaches to inference. Construction and evaluation of tests and estimators. Large sample theory of point estimation.', 'STAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('STAT 854', 'Sampling Theory and Practice', 0.5, 'LEC', 'Sources of survey error. Probability sampling designs, estimation and efficiency comparisons. Distribution theory and confidence intervals. Generalized regression estimation. Software for survey analysis.', 'STAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('STAT 890', 'Topics in Statistics', 0.5, 'RDG', 'None', 'STAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('STAT 937', 'Introduction  to Biostatistics and Epidemiology', 0.5, 'LEC', 'Design of medical studies including cohort designs, case-control studies and clinical trials. Measures of association in epidemiological studies. Analysis of data from cohort studies, clinical trials and case-control studies. Other topics such as multiplicity in clinical trials, group randomized designs, sequential and group sequential procedures as time permits. Use of a statistical package (e.g. SAS or S-Plus) to analyze medical data.', 'STAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('STAT 891', 'Topics in Probability', 0.5, 'RDG', 'None', 'STAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('STAT 901', 'Theory of Probability 1', 0.5, 'LEC', 'Probability measures, random variables as measurable functions, expectation, independence, characteristic functions, limit theorems, applications.', 'STAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('STAT 902', 'Theory of Probability 2', 0.5, 'LEC', 'Review of conditioning on sigma-fields; martingale theory (discrete and continuous-time) and applications; counting processes; Brownian motion; stochastic differential and integral equations and applications; general theory of Markov processes (including martingale problems and semigroup theory), diffusions; weak convergence of stochastic processes on function spaces; functional versions of the central limit theorem and strong laws; convergence of empirical processes.', 'STAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('STAT 906', 'Computer Intensive Methods for Stochastic Models in Finance', 0.5, 'LEC', 'Review of basic numerical methods. Simulation of random variables, stochastic processes and stochastic models in finance. Numerical solution of deterministic and stochastic differential equations. Valuation of complex financial instruments and derivative securities. Project and paper.', 'STAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('STAT 908', 'Statistical Inference', 0.5, 'LEC', 'Principles of Inference: sufficiency, conditionality, and likelihood; examples and counter examples; conditional inference and ancillarity. Theory of Hypothesis Testing: Neyman-Pearson lemma; similar tests; invariant tests. Asymptotic Theory: maximum likelihood and related theory; large-sample properties of parametric significance tests. Interval Estimation: confidence intervals and significance intervals; location and scale models, conditional intervals. Introduction to Decision Theory: loss and risk functions, admissibility; minimax and Bayes rules; prior and posterior analysis. The course content of Stat 850 is a presumed prerequisite for Stat 908.', 'STAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('STAT 923', 'Multivariate Analysis', 0.5, 'LEC', 'Multivariate problems as extensions of univariate problems, discriminant analysis, canonical correlation and principle component analysis.', 'STAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('STAT 929', 'Time Series 1', 0.5, 'LEC', 'Iterative model building. ARIMA models, application to forecasting, seasonal models, applications.', 'STAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('STAT 930', 'Time Series 2', 0.5, 'LEC', 'Multiple time series modeling including transfer function and intervention analysis. Various special topics in time series such as outliers, robustness, order determination methods, Kalman filtering, sampling and aggregation, seasonal adjustments.', 'STAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('STAT 931', 'Statistical Methods for the Design and Analysis of Epidemiological Studies', 0.5, 'LEC', 'This course covers a wide range of topics pertaining to the design and statistical analysis of observational health studies.  The course is divided into three areas:
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('STAT 932', 'Statistical Methods for the Design and Analysis of Randomized Intervention Trials', 0.5, 'LEC', 'This course covers topics relevant for the design, conduct and analysis of clinical intervention trials.  The statistical theory behind the methodology as well as practical issues will be discussed.  The course is divided into three areas:
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('STAT 935', 'Analysis of Survival Data', 0.5, 'LEC', 'This course deals with methods of analyzing data on the time to failure with particular emphasis on the use of regression models for such data. Both parameteric and semi-parametric regression models will be considered.', 'STAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('STAT 936', 'Longitudinal Data Analysis', 0.5, 'LEC', 'This course is designed to teach students the appropriate techniques for analyzing data that is collected over time.  This data could arise from biomedical, population public health studies as well as finance and actuarial science applications.  The course will teach how to recognize the added complexity of longitudinal data versus the univariate response data which is typically seen in introductory and generalized linear model courses.  The course emphasizes the importance of the covariance structure for longitudinal responses.  The students will study the difference between subject-specific and population-averaged models and how to recognize problems where one or the other approach might be more appropriate.  They will be expected to use statistical software in applications in order to analyze longitudinal data.', 'STAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('STAT 938', 'Statistical Consulting', 0.5, 'LEC', 'This course will cover some of the basic tools of a statistical consultant. Topics will include the use of statistical packages, problem-solving techniques, discussion of common statistical consulting problems, effective communication of statistical concepts and management of consulting sessions.', 'STAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('STAT 946', 'Topics in Probability and Statistics', 0.5, 'LEC', 'Topics of current interest', 'STAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SYDE 622', 'Machine Intelligence', 0.5, 'LEC', 'Overview of machine intelligence, underlying assumptions and ill-structured problems. Programming language paradigms, logical and functional programming, object-oriented paradigm, machine intelligence tools and development systems. Knowledge representation hypothesis, logic-based, associational, analogical, procedural, and probabilistic representations. Reasoning, planning, machine learning, and applications.', 'SYDE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('STAT 947', 'Topics in Biostatistics', 0.5, 'LEC', 'Topics of current interest', 'STAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('STAT 974', 'Financial Econometrics', 0.5, 'LEC', 'The focus of this course is on the statistical modelling, estimation and inference and forecasting of nonlinear financial time series, with a special emphasis on volatility and correlation of asset prices and returns.  Topics to be covered normally include: review on distribution and dynamic behaviour of financial time series, univariate and multivariate GARCH processes, long-memory time-series processes, stochastic volatility models, modelling of extreme values, copulas, realized volatility and correlation modelling for ultra high frequency data and continuous time models.', 'STAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('STV 100', 'Society, Technology and Values: Introduction', 0.5, 'LEC', 'This course examines the interaction of the technologies developed by a culture with the values and social organization of that culture. The course exposes students to various definitions of society, technology and values, and it presents alternative views about how the three interact. These views are then applied to a number of spheres of influence, including patterns of employment and the role of work; medicine and health; polity and economy; sustainable development and the environment.', 'STV');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('STV 201', 'Society, Technology and Values: Special Topics', 0.5, 'LEC', 'Study of the interaction of society, technology and values in a particular topic area under tutorial guidance by visiting or adjunct faculty.', 'STV');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('STV 202', 'Design and Society', 0.5, 'LEC', 'The course uses design as a vehicle for examining technology and society interaction, particularly how we design, create, and choose technology. Design and Society has been created to introduce students to various methods to help them view design and technology from a technology-society perspective. The overriding aims of the course are to (a) develop an ability to see through the eyes of others and promote thinking about technology in terms of users as well as producers or creators, (b) develop critical thinking skills, and (c) present design and the choice of technology as an activity involving societal and individual concerns rather than as a purely technical matter. The course does not require advanced technical knowledge and is open to students of all faculties.', 'STV');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('STV 203', 'Biotechnology and Society', 0.5, 'LEC', 'Biotechnologies are currently migrating from the laboratory to a wide range of consumer products. In everything from foods to pharmaceuticals, genetic engineering is entering daily life. The biotechnology revolution will likely have as much of an impact on our lives as the information technology revolution. However, there is a wide and potentially dangerous gap between research and development on the one hand and societal awareness of its impact on the other. This course is intended to help students gain a deeper appreciation for the issues, including ethics, surrounding biotechnology and society. It will do this through an investigation of such interactions between biotechnologies and society as genetics and the law, foods and the media, and soil remediation and the environment. Consideration will also be given to the role of computing in biotech research and in the human genome project.', 'STV');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('STV 205', 'Cybernetics and Society', 0.5, 'LEC', 'Cybernetics is the study of the interfaces between humans and machines. This course will present an overview of cybernetics from automation and robotics to prosthetics and wearable computing. The roles of men and women in a post-human era will be discussed along with related themes dealing with gender, cyberspace, politics and popular culture.', 'STV');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('STV 302', 'Information Technology and Society', 0.5, 'LEC', 'The course has been designed to provide a framework or set of intellectual tools to help students understand and evaluate technological change. These tools will be applied to the development of information technology, its interaction with society, and possible future scenarios. Selected topics include understanding digitization as culture, visions of the future and the perils of prediction, as well as issues related to application areas such as the home, manufacturing, office work, design and services, education, and law. The course will also consider some of the privacy and personal dignity issues associated with information technology.', 'STV');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('STV 303', 'Cross-Cultural Change, Technology and Society', 0.5, 'SEM', 'The course will examine the concept of cross-cultural change as more than a consequence of contact between ethnic and national groupings. Technological change and the mutual interaction between technology and culture produce new cross-cultural change and challenges. The purpose of the course is to help students understand and identify the complex interaction between technology and culture and its impact on design and the choice of technology. Students will be expected to use this knowledge as a decision-making tool.', 'STV');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('STV 400', 'Society, Technology and Values: Senior Project', 0.5, 'PRJ', 'An independent, supervised research project related to the interaction of society, technology and values. Projects may take any format that demonstrates scholarly merit. Formats may include essays, impact studies, designs, computer software, or other media. Students are responsible for proposing suitable projects and are encouraged to seek faculty advice on plausible topics.', 'STV');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('STV 401', 'Society, Technology & Values: Advanced Topics', 0.5, 'LEC', 'Advanced study of the interaction of society, technology and values in a particular topic area under tutorial guidance by visiting or adjunct faculty.', 'STV');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('STV 404', 'Technology in Canadian Society', 0.5, 'LEC', 'The course begins by questioning whether or not there is anything distinctive about Canadian design and its context. Through the use of historical and contemporary cases, this course examines factors influencing design and technology choice in Canada and their outcomes. The future of design is also considered in terms of current trends, issues, challenges and opportunities.', 'STV');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SUSM 601', 'Foundations for Sustainability Management', 0.25, 'LEC', 'In this course the scientific foundations and fundamentals for sustainability management will be introduced. Basic theoretical, ethical, scientific, and technical foundations of sustainable development will be taught in addition to technical knowledge like statistical literacy and basic environmental knowledge.', 'SUSM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SUSM 602', 'Theories and Concepts of Sustainability Management', 0.5, 'LEC', 'In this course theories and concepts such as international sources of sustainability concepts, basic environmental and ecological economics, social and environmental justice, sustainable management and finance, uncertainty, complexity, risk and decision making in sustainability management, etc. will
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SUSM 603', 'Research Methods for Sustainable Management', 0.5, 'LEC', 'This course will introduce quantitative and qualitative research methods such as statistical methods, case study analysis, life cycle assessment, survey methods, impact measurement, etc. Students will learn skills like risk assessment methods, sustainability measurement and reporting, qualitative and quantitative data analysis, literature review, ethical aspects of research and proposal writing.', 'SUSM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SUSM 605', 'Thesis Development', 0.25, 'SEM', 'The goal of the course is to ensure that all students will have a completed thesis proposal by the end of term.  Each student will make an oral presentation of their proposal to their committee members and other members of SEED.  The course will include discussions about how best to frame good research questions, find sources, organize a thesis, conduct ethical research, and communicate results.  Students will make regular class presentations throughout the term as their research proposal develops and thus gain skills in written, graphical oral presentation.', 'SUSM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SUSM 620', 'Sustainable Operations', 0.5, 'LEC', 'This course explores the foundations of operations management in the context of sustainability. Through the use of both peer-reviewed and practitioner articles, this course will provide students with a comprehensive understanding of the core concepts in the field of operations management while integrating emergent social and environmental issues. Theoretical and empirical exploration of core operations concepts such as supply chain management, process configuration, quality and process improvement, and lean systems will be undertaken. Once a strong foundational understanding of these core concepts is achieved, students will then explore the opportunities and threats posed by emergent social and environmental issues to organizations, and how those issues can be integrated into the strategy underlying operations and process management. Concepts such as eco-efficiency, sustainable supply chain management, environmental management systems (e.g., ISO 14000), and life cycle assessment will be explored.', 'SUSM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SUSM 630', 'Marketing for Sustainability', 0.5, 'LEC', 'Marketing for sustainability is a key component of a successful business strategy in todays marketplace. Through a business-to-consumer lens, this course uses the principles of marketing and social psychology to explore ways in which businesses can foster sustainable behaviour through the application of both green marketing and social marketing tools. The course introduces core tools for sustainability marketing including green marketing strategies, behaviour change theories and principles of community-based social marketing.', 'SUSM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SUSM 640', 'Strategy for Sustainable Enterprises', 0.5, 'LEC', 'This online course looks at organizational strategy through the "lens" of sustainability. It covers foundational strategic management theory in the context of sustainability strategies. Tools for formulating and implementing a sustainability strategic plan are presented. Case studies draw from different sectors and also different organizational types.', 'SUSM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SUSM 650', 'Sustainable Finance', 0.5, 'LEC', 'Financial and accounting "tools" have potential to create effective and far-reaching market-based solutions to address a range of sustainability management problems, including climate change, north-south relations, water issues and social change, while at the same time identifying and securing new business opportunities for companies, the financial sector and their customers. An increasing number of financial and accounting products and services have emerged to direct resources and lending power to mitigate environmental and/or encourage sustainable practices and decision-making. The course considers sustainable finance products, tools and services; sustainability accounting, and other approaches that foster sustainable development, such as full-cost accounting; relation of sustainability performance to capital market valuation and financial performance; socially responsible investing; sustainable lending, , impact investment, principles and codes.', 'SUSM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SUSM 675', 'Reading Course', 0.5, 'RDG', 'Particular offerings may be initiated by a faculty member in consultation with  interested students. In all cases instructor consent is required.', 'SUSM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SWK 600R', 'Diversity and Health', 0.5, 'LEC', 'This course studies the impact of diversity on health in Canada and across nations.  It provides an understanding of the complex interaction among aspects of diversity.  The course examines and critiques the methods used in the study of these concepts and issues related to the measurement of health among diverse groups.  The ultimate goal of this examination is to help students develop an appreciation of the impact diversity has on assessments and study of health, health status, and health promotion in Canada and other nations.  The course is designed to integrate different sources of information about diversity by utilizing critical thinking skills for the consumption of health information.  (Note:  This is an intensive on campus course with an online component).', 'SWK');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SWK 601R', 'Health Policy', 0.5, 'LEC', 'This course offers critical analysis of health policy formulation, implementation, and evaluation related to population health initiatives and health care delivery.  The course includes discussion of the role of various regional, provincial, and national agencies in health care policy formation.  It examines various health care systems, and their funding as well as investigates how the Canadian healthcare system compares with systems in other countries.  (Note:  This is an intensive on campus course with an online component).', 'SWK');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SWK 602R', 'Social Work Practice in Health', 0.5, 'LEC', 'This course examines practice models and multi-level methods of intervention for effective social work practice in health care, including health promotion, disease prevention, assessment, treatment, rehabilitation, continuing care, and discharge planning within the context of social, economic, environmental, and cultural variations.  The distribution, determinants, as well as psychological and behavioural aspects of health and disease across the life span are addressed.  Practice models incorporate competence and empowerment as central themes.  New models of care are considered, including primary health care, the impact of socio-cultural factors on health and well being, the significance of family relationships and resources in the management of chronic and complex health conditions, and interventions that support individual and family capacity to adapt to acute health crises or chronic health conditions.  (Note:  This is an online course).', 'SWK');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SWK 603R', 'Critical Exploration of Supervision and Leadership Roles for Social Worker', 0.5, 'LEC', 'This course examines effective leadership in a tumultuous health care environment characterized by changing patient populations, health care technologies, professional practices, community needs/expectations, and rapid shifts in service delivery modalities.  In this environment, health care professions are increasingly challenged to develop and oversee staff capable of delivering contemporary benchmark professional practice.  Competent supervision requires practice knowledge, keen perspective, and the ability to support, guide, educate and direct practitioners of varying skill and experience.  This course provides inquiry-based opportunities for students to critically examine their leadership styles, developing foundational knowledge and skills for effective involvement in organizational change, staff management, coaching and supervision, coordination of inter-professional teams and development of policies to address the social determinants of health.  (Note:  This is an online course).', 'SWK');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SWK 604R', 'Evaluation of Health and Human Service Programs', 0.5, 'LEC', 'This course introduces students to theoretical and practical aspects of program evaluation.  Students learn about the application of data collection skills to all phases of developing a health or human services program or service innovation, from needs assessment to analysis of findings to implementation of changes based on results.  Students learn to appreciate how these skills can be used as practical tools for identifying health and human services problems, for developing and implementing programs including taking a reflective practice approach, ensuring equity and fairness in program delivery (i.e., combating disparities), and generally promoting health and human services through effective and efficient programming.  (Note:  This is an online course).', 'SWK');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SWK 605R', 'Knowledge Mobilization and Evidence-Based Practice', 0.5, 'LEC', 'The goal of knowledge mobilization is to make research in health and social science more useful to policy, practice, and the public.  In this course students can learn how to gather, evaluate, synthesize and summarize scholarly knowledge in ways that meet the needs, timeframes, cultures, and realities of stakeholders. Activities include framing academic research in the context of public issues and creating knowledge products for practitioners and policy-makers in the students'' areas of interest.  (Note:  This is an online course).', 'SWK');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SWK 608R', 'Health Issues and Ethics', 0.5, 'LEC', 'This course considers the nature of health itself, and explores the complexities of a health care system that corresponds with our values and ethics while being effective and financially stable.  Three related themes serve as substrates for the semester: the nature of health, delivery of health care, and populations at risk.  Issues such as how health and illness are defined, the roles of preventive and curative approaches, and the impacts and ethics of new technologies will be addressed.  The course compares public and private models, focuses on community health care as a developing new paradigm, and examines the particular challenges of marginalized and vulnerable committees.  (Note:  This is an online course).', 'SWK');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SWK 609R', 'Social Work Practice in Mental Health', 0.5, 'LEC', 'This course aims to equip students to provide sensitive and effective help to clients by presenting material concerning a range of mental health problems and their treatment.  The course considers social work practice across the continuum of mental health care services.  Attention is focused on how social workers assess, support and intervene with consideration of both the client and his or her environment. (Note:  This is an online course).', 'SWK');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SWK 650R', 'Interprofessional Psychosocial Oncology: Introduction to Theory and Practice', 0.5, 'LEC, SEM, TUT', 'This elective course provides graduate students with an introduction to the field of psychosocial oncology.  Emphasis will be placed on understanding and interpreting the experience of cancer informed by theory, evidence and illness narratives. Case based learning in small interprofessional groups will allow students to explore a variety of key learning themes relevant to psychosocial oncology including distress assessment, depression, anxiety, adjustment and coping, sexuality, loss and grief. Small group work will allow students to develop a rich understanding of the cancer experience and competency in psychosocial oncology assessment, interprofessional collaboration, and cultural safety. Attention to diversity will be integrated throughout the course.', 'SWK');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SWK 651R', 'Relational Practice with Families', 0.5, 'LEC, SEM, TUT', 'Using case based learning in small interprofessional groups, students will explore a variety of key learning themes relevant to the interprofessional care of families. Themes that will be addressed include: family theory, models of family & couple counseling (particularly from a systemic and strengths based perspective), family assessment, therapeutic conversations and interventions. Case examples will be drawn from the experience of families across the cancer illness trajectory, from diagnosis through to death and dying, bereavement and long term survivorship. Small group work will allow student to develop a rich understanding of the cancer experience from the perspective of families, as well as competency in family assessment, intervention, interprofessional collaboration, and cultural safety. Attention to diversity will be integrated throughout the course.', 'SWK');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SWK 652R', 'Sexual Health & Counseling in Cancer', 0.5, 'LEC, SEM, TUT', 'Cancer causes wide-ranging impacts on sexual health and well-being and numerous studies have identified that people affected by cancer want support, education and counseling in this area. While health professionals consistently acknowledge that sexuality is within their scope of practice, few health professionals consistently assess or intervene in sexual health problems in cancer settings. This course is designed to provide graduate students in the health professions with the knowledge and skills to intervene with the sexual problems that arise in cancer.', 'SWK');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SWREN 120R', 'Introduction to Social Work', 0.5, 'LEC', 'Presentation of the values, knowledge, and skill base; principles and purposes of the profession; and an examination of methods of practice. Traditional and innovative social work settings are discussed. Historic development of social work and its influence on contemporary practice are reviewed.', 'SWREN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SWREN 220R', 'Social Work with Individuals - Theory and Practice 1', 0.5, 'LEC', 'A presentation of some of the theoretical constructs necessary for the understanding of the individual in the casework relationship, as well as an introduction to some appropriate casework interventions. Emphasis in the course will be theoretical.', 'SWREN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SWREN 221R', 'Social Group Work', 0.5, 'LEC', 'Presentation of some of the theoretical constructs necessary for an understanding of social group work as well as an introduction to methodology and interventions.', 'SWREN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SWREN 222R', 'Community Organization 1', 0.5, 'LEC', 'An examination of social work practice as it relates to functional and geographical communities. The course will explore the theoretical foundations of organization practice as well as a variety of models.', 'SWREN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SWREN 224R', 'Poverty in Canada and its Social Consequences', 0.5, 'LEC', 'A sociological analysis of poverty in contemporary Canada as it underlies a multiplicity of interlocking social problems.', 'SWREN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SWREN 250R', 'Social Statistics', 0.5, 'LEC', 'This introductory level statistics course will emphasize the collection, manipulation, descriptive presentation, and statistical analysis of social research data.', 'SWREN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SWREN 251R', 'Social Research', 0.5, 'LEC', 'Introduction to the philosophy and methodology of applied social science research, including treatment of the problems and strategies of research design and execution.', 'SWREN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SWREN 300R', 'Canadian Social Welfare Policy', 0.5, 'LEC', 'This course will introduce students to the concept of social welfare as it has evolved in the post-industrial era. It will examine various social, economic and political factors that have shaped Canadian social welfare policy and it will consider present-day factors that are transforming that policy.', 'SWREN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SWREN 301R', 'Understanding Diversity in Canada', 0.5, 'LEC', 'This course explores culture; its components, characteristics, processes, theories, issues; and the history of diversity in Canadian social systems. It also examines the implications of diversity for social work practice and fosters culturally competent practice with micro and macro social systems.', 'SWREN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SWREN 311R', 'Public Policy and Native Peoples in Canada', 0.5, 'LEC', 'This course examines the evolution, logic, processes, and impacts of government policies developed specifically for Native peoples, with particular attention to government policy as both a cause of and a response to social problems within Native communities.', 'SWREN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SWREN 312R', 'Homelessness & Public Policy', 0.5, 'LEC', 'This course provides students with a basic overview of homelessness in modern society. Its goal is to familiarize students with the human, social, political and economic aspects of homelessness. Throughout, the emphasis will be on understanding homelessness from a public policy framework - its incidence and prevalence, etiology, consequences, and strategies for its prevention and amelioration.', 'SWREN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SWREN 321R', 'Social Work with Families', 0.5, 'LEC', 'Presentation of some of the theoretical constructs necessary for an understanding of the family in the social work relationship as well as an introduction to methodology and interventions.', 'SWREN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SWREN 349R', 'Cross-Cultural Psychology', 0.5, 'LEC, SEM', 'This course focuses on human psychology (behaviours, cognition, emotion, motivation, and personality) across cultures, with the aim of raising awareness and understanding of human commonality and diversity. It examines theoretical, methodological, and ethical issues in cross-cultural psychology in light of the current literature.', 'SWREN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SWREN 411R', 'Integrative Practice: Aboriginal Perspectives and Social Work', 0.5, 'LEC', 'This integrative practice course examines the impact of policy, service delivery and large and small systems on Aboriginal Peoples in Canada. Long-standing policies of assimilation and colonization are considered from a political, economic, and social perspective. With a critical eye towards matters of discrimination and oppression, specific attention is given to the culturally-specific skills and approaches particular to traditional and current helping with Aboriginal Peoples in Canada.', 'SWREN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SWREN 414R', 'Interviewing and Assessment in Social Work Practice', 0.5, 'LEC', 'This social work methods course explores the theory and skills of generalist social work practice, with particular attention to interviewing, communication, and assessment skills used with individuals, families, organizations and communities. Emphasis is placed on developing capacity for "critical reflection" on the social work practitioner''s professional and personal self.', 'SWREN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SWREN 422R', 'Advanced Macro Practice', 0.5, 'LEC', 'The course examines and compares the strengths, limitations, differential uses, and interconnections of the range of skills needed for community organization, social planning, and social policy practice. Practical applications such as conference planning, proposal writing and fund raising will be explored.', 'SWREN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SWREN 423R', 'Advanced Social Group Work Practice', 0.5, 'LEC', 'The aim of this course is to develop student''s knowledge of different approaches to social group work practice, and the ways in which specialized knowledge of client''s life conditions, life circumstances, and significant life events informs social work practice with groups within a generalist framework. Emphasis is placed upon conceptualizing and analyzing group work skill and the capacity for self-directed practice with groups directed towards increasing the resiliency of the group as a whole and the resiliency of individual group members. A salient goal is the use of democratic means so that group process in all social work groups reflects a vision of social justice.', 'SWREN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SWREN 424R', 'Diversity and Empowerment', 0.5, 'LEC', 'This social work course focuses on issues of diversity and social injustice, with emphasis on culturally sensitive and anti-oppressive practice. It is based on generalist and empowerment perspectives in responding to needs and facilitating changes at appropriate system levels.', 'SWREN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SWREN 431R', 'Fields of Practice Research', 0.5, 'LEC', 'This course examines contemporary fields of practice. Attention is given to historical developments, service delivery systems, and practices. Research activities help develop a critical understanding of how social experience impacts the ways that people with diverse experiences access and encounter some of the major fields of practice.', 'SWREN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SWREN 434R', 'Selected Theories for Social Work Practice: Analysis and Application', 0.5, 'LEC', 'This course focuses on analysis and application of selected theories and practice methods within a generalist-eclectic approach to social work practice with numerous populations in a variety of settings.', 'SWREN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SWREN 441A', 'Practicum 1A', 0.5, 'PRA', 'This first practicum phase helps students understand the general structure and functioning of the helping network and the place of agency-based community settings in it. Simultaneously, the students begin to integrate interviewing and assessment skills to formulate an intervention plan with sensitivity to the diversity of the setting. A bi-weekly seminar runs concurrently with the practicum.', 'SWREN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SWREN 441B', 'Practicum 1B', 0.5, 'PRA', 'This first practicum phase helps students understand the general structure and functioning of the helping network and the place of agency-based community settings in it. Simultaneously, the students begin to integrate interviewing and assessment skills to formulate an intervention plan with sensitivity to the diversity of the setting. A bi-weekly seminar runs concurrently with the practicum.', 'SWREN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SWREN 441R', 'Practicum 1', 1, 'PRA', 'This first practicum phase helps students understand the general structure and functioning of the helping network and the place of agency-based community settings in it. Simultaneously, the students begin to integrate interviewing and assessment skills to formulate an intervention plan with sensitivity to the diversity of the setting. A bi-weekly seminar runs concurrently with the practicum.', 'SWREN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SWREN 442A', 'Practicum 2A', 0.5, 'PRA', 'The second practicum phase develops students'' intervention skills. Opportunity is given to enhance social work practice skills specific to particular client populations within the context of diversity, and to develop awareness of one''s professional self in relation to social work practice within the agency context. A bi-weekly seminar runs concurrently with the practicum.', 'SWREN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SWREN 442B', 'Practicum 2B', 0.5, 'PRA', 'The second practicum phase develops students'' intervention skills. Opportunity is given to enhance social work practice skills specific to particular client populations within the context of diversity, and to develop awareness of one''s professional self in relation to social work practice within the agency context. A bi-weekly seminar runs concurrently with the practicum.', 'SWREN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SWREN 442R', 'Practicum 2', 1, 'PRA', 'The second practicum phase develops students'' intervention skills. Opportunity is given to enhance social work practice skills specific to particular client populations within the context of diversity, and to develop awareness of one''s professional self in relation to social work practice within the agency context. A bi-weekly seminar runs concurrently with the practicum.', 'SWREN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SWREN 443A', 'Practicum 3A', 0.5, 'PRA', 'The third practicum phase helps students demonstrate understanding of, and ability to use, a range of interventions within the context of diversity. Professional identification and the integration of theory with practice are emphasized. A bi-weekly seminar runs concurrently with the practicum.', 'SWREN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SWREN 443B', 'Practicum 3B', 0.5, 'PRA', 'The third practicum phase helps students demonstrate understanding of, and ability to use, a range of interventions within the context of diversity. Professional identification and the integration of theory with practice are emphasized. A bi-weekly seminar runs concurrently with the practicum.', 'SWREN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SWREN 443R', 'Practicum 3', 1, 'PRA', 'The third practicum phase helps students demonstrate understanding of, and ability to use, a range of interventions within the context of diversity. Professional identification and the integration of theory with practice are emphasized. A bi-weekly seminar runs concurrently with the practicum.', 'SWREN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SWREN 470R', 'Mental Health and Addiction Issues: Social Work Responses', 0.5, 'LEC', 'This course will focus on social work practice in the fields of mental health and addictions providing social work students with knowledge concerning the etiology and intervention issues related to mental health and addictions. A life-span perspective will allow an integration of practice, policy and research in relation to mental health, addictions, substance use and concurrent disorders. The continuum of services, including community-based and institutional care perspectives will be presented, along with "best practice" models for the future.', 'SWREN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SWREN 471R', 'Social Work with Older Adults: Critical Issues and Future Trends', 0.5, 'LEC', 'Focusing on social work practice, policy and research, the course examines issues of aging such as health, economic changes, role transition, family dynamics and losses. The course also looks at implications of societal issues and the influence of recent Canadian research in the field of aging. Current innovative and traditional programs will be examined for their relevance for future work with populations of older adults.', 'SWREN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SWREN 472R', 'International Context of Practice: An Experiential Learning Opportunity', 0.5, 'LEC', 'This course provides a framework for understanding a developing country through a comparative study with Canada. The collective responses of citizens in meeting community needs in the context of colonial legacies and contemporary global influences are highlighted in both settings. The international learning experience usually takes place during Reading Week and is complemented by two days of pre-trip learning focusing on Canadian realities and one post-trip day to reflect upon the comparative analysis.', 'SWREN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SYDE 101', 'Communications in Systems Design Engineering-Written and Oral', 0.25, 'LEC, TUT', 'This course introduces first-year students to systems design engineering with a focus on the engineering profession, and technical communication skills. Through in-class and independent activities leading to formative and summative assessments, students will consider and reflect on communication intention, audience, content, medium, format, and tone to demonstrate and improve upon their listening, written and oral communication skills for academic, engineering, and professional context. [Offered: F]', 'SYDE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SYDE 101L', 'Communications in Systems Design Engineering-Visualization', 0.25, 'LAB, LEC', 'This laboratory course introduces students to visual communication methods relevant to engineering analysis and design. Through in-class and independent activities leading to formative and summative assessments, students will consider and reflect on communication intention, audience, content, medium, format, and level of detail to demonstrate and improve upon their skills in graphing, freehand sketching, technical drawing, and computer-aided design (CAD). [Offered: F]', 'SYDE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SYDE 102', 'Seminar', 0, 'SEM', 'Systems design first-year students will meet with a faculty member designated as their class professor. Performance in assignments, conceptual difficulties with courses, interrelation of coursework, later work and engineering practice will be discussed. Non-credit course. [Offered: S]', 'SYDE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SYDE 111', 'Fundamental Engineering Math 1', 0.5, 'LEC, TST, TUT', 'Functions: trigonometric, exponential, log, inverse functions. Differential calculus: limits, continuity, derivatives, differentials, applications. Sequences and series: convergence, power series, Taylor expansions. Simple numerical methods. [Offered: F]', 'SYDE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SYDE 112', 'Fundamental Engineering Math 2', 0.5, 'LEC, TUT', 'Integration: Indefinite and definite integral; techniques of integration; improper integrals, numerical methods, applications. Vector differential calculus: Partial, total, and directional derivative; Gradient divergence and curl; Jacobian. Applications. [Offered: S]', 'SYDE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SYDE 113', 'Matrices and Linear Systems', 0.25, 'LEC, TUT', 'Geometry and algebra: root-finding, vectors, coordinate systems, lines and planes, conic sections, complex numbers. Introduction to numerical computation. Floating point arithmetic, accuracy and sources of error. Matrix algebra, inverses. Analytical and numerical techniques for systems of linear equations. [Offered: F]', 'SYDE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SYDE 281', 'Mechanics of Deformable Solids', 0.5, 'LEC, TUT', 'Introduction to mechanical response of materials and stress-strain relationship. Behaviour of prismatic members in tension, compression, shear, bending and torsion. Shear-force and bending-moment diagrams. Introduction to instability. [Offered: F]', 'SYDE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SYDE 114', 'Numerical and Applied Calculus', 0.25, 'LEC, TUT', 'Matrices and linear systems: determinants, eigenvalues and eigenvectors, LU decomposition, conditioning, numerical methods. First order ordinary differential equations: analytical techniques, applications, elementary numerical methods, convergence. [Offered: S]', 'SYDE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SYDE 121', 'Digital Computation', 0.5, 'LAB, LEC, TUT', 'Computer systems, problem solving, data and programs, structured programming, arrays, matrices and pointers, correct and efficient algorithms, data structures. [Offered: F]', 'SYDE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SYDE 161', 'Introduction to Design', 0.5, 'LEC, TUT', 'Multidisciplinary system design, the design process, problem definition, life-cycle design, design specification, concept/design generation and evaluation, design for manufacturing and assembly, system modelling and analysis, introduction to mechanical design, prototyping, safety and responsibility in engineering design, design documentation. [Offered: F]', 'SYDE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SYDE 162', 'Human Factors in Design', 0.5, 'LEC, TUT', 'Design of human-machine environments, design to reduce human error. Analytical methods of determining user needs in systems with humans. Information processing and human sensory processes and consideration of these elements in the design of systems with humans. Human physical capabilities and consideration of these in ergonomic design. [Offered: S]', 'SYDE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SYDE 181', 'Physics 1 (Statics)', 0.5, 'LEC, TUT', 'Basic concepts of mechanics, vectors. Statics of particles. Rigid bodies and force systems, equilibrium of rigid bodies. Analysis of trusses and frames. Distributed forces, centroids and moments of inertia. Friction. Virtual work. [Offered: F]', 'SYDE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SYDE 182', 'Physics 2 (Dynamics)', 0.5, 'LEC, TUT', 'Kinematics of particles, rectilinear and curvilinear motion. Kinetics of particles, application to space mechanics. Energy and momentum methods. Systems of particles. Kinematics and kinetics of rigid bodies; planar motion. Vibrations. [Offered: F, W, S]', 'SYDE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SYDE 192', 'Digital Systems', 0.5, 'LEC, TUT', 'Digital technology, combinatorial logic, binary arithmetic, synchronous sequential circuits, design methodology, algorithmic state machines, microcomputer interfacing. [Offered: S]', 'SYDE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SYDE 192L', 'Digital Systems Laboratory', 0.25, 'LAB', 'Laboratory experiments for students taking SYDE 192. [Offered: S]', 'SYDE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SYDE 201', 'Seminar', 0, 'SEM', 'Systems design second-year students will meet a faculty member designated as their class professor. Performance in assignments, conceptual difficulties with courses, interrelation of coursework, later work and engineering practice will be discussed. Non-credit course. [Offered: W]', 'SYDE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SYDE 202', 'Seminar', 0, 'SEM', 'Systems design second-year students will meet a faculty member designated as their class professor. Performance in assignments, conceptual difficulties with courses, interrelation of coursework, later work and engineering practice will be discussed. Non-credit course. [Offered: F]', 'SYDE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SYDE 211', 'Advanced Engineering Math 1', 0.5, 'LEC, TUT', 'Vector calculus: double and triple integrals, line and surface integrals, fundamental theorems, applications. Difference methods: root-finding, finite and divided differences, numerical differentiation, interpolation. Second order linear ordinary differential equations and systems. Applications in vibration. [Offered: F,W]', 'SYDE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SYDE 212', 'Probability and Statistics', 0.5, 'LEC, TUT', 'Elementary probability theory. Random variables and distributions. Binomial, Poisson, and normal distributions. Elementary sampling. Statistical estimation. Tests of hypotheses and significance. Regression. Goodness-of-fit tests. Analysis of experimental measurements. [Offered: F]', 'SYDE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SYDE 223', 'Data Structures and Algorithms', 0.5, 'LAB, LEC, TUT', 'Algorithms and Data Structures emphasizes the following topics: structured software design, data structures, abstract data types, recursive algorithms, algorithm analysis and design, sorting and searching, hashing, problem-solving strategies and NP-completeness. [Offered: W,S]', 'SYDE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SYDE 252', 'Linear Systems and Signals', 0.5, 'LEC, TUT', 'Models and analysis of linear systems. Discrete time systems, continuous time systems; difference and differential equations; impulse and frequency response. Complex frequency, functions of complex variables, transform domain techniques: Z transforms; Fourier analysis, Laplace transform. Transfer functions and frequency response, frequency domain analysis of linear systems; sampling theory, stability, and linear filters. [Offered: F, S]', 'SYDE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SYDE 261', 'Design, Systems, and Society', 0.5, 'LEC, TUT', 'This non-technical course will help students understand how others think about technology, and then use this knowledge to make better choices when designing, specifying, choosing, and implementing technology. This course includes topics such as meanings of design and their implications; designed in/designed out analysis; reductionism and integration in design; the limits of objective thinking; alternate ways to define function; content and context; "we/me/them/it" analysis; redefining what constitutes acceptable technology; learning from the margins; reading design, and understanding system boundaries as being defined by what we do rather than what we say. [Offered: W]', 'SYDE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SYDE 262', 'Engineering Economics of Design', 0.5, 'LEC, TUT', 'This course examines a variety of economic factors in Engineering and how they impact design. Topics include business plans, price and output decisions, choosing among alternative inputs, production processes, evaluating alternative investments, equipment service life and depreciation, new products. [Offered: F]', 'SYDE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SYDE 283', 'Physics 3 (Electricity, Magnetism and Optics)', 0.5, 'LEC, TUT', 'Introduction to the fundamental laws of electricity, magnetism, and optics; electric fields, voltage, resistance, current, properties of conductors and semiconductors, capacitance, properties of dielectrics, magnetic fields, Faraday''s Law and inductance, properties of magnetic materials; electromagnetic waves and the nature of light, geometrical optics; reflection and refraction, physical optics; interference and diffraction. [Offered: W]', 'SYDE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SYDE 285', 'Materials Chemistry', 0.5, 'LEC, TUT', 'The course will present how the fundamentals of chemistry are applied to materials science and engineering. Concepts such as chemical bonding, crystal structure, phase diagram, redox reaction, and electrochemistry will be introduced in the context of materials science. Properties, processing and structure of metals, semiconductors, polymers, ceramic, nanomaterials and biomaterials will be presented. [Offered: W]', 'SYDE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SYDE 286', 'Mechanics of Deformable Solids', 0.5, 'LEC, TUT', 'Introduction to mechanical response of materials and stress-strain relationship. Behaviour of prismatic members in tension, compression, shear, bending and torsion. Shear-force and bending-moment diagrams. Introduction to instability. [Offered: F]', 'SYDE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SYDE 292', 'Circuits, Instrumentation, and Measurements', 0.5, 'LEC, TUT', 'Active and passive circuit elements, Kirchhoff''s laws, mesh and nodal circuit analysis, principle of superposition; step response of first and second order networks; sinusoidal steady state analysis using complex impedance phasors; input-output relationships, transfer functions and frequency response of linear systems; operational amplifiers, operational amplifier circuits using negative or positive feedback; diodes, operational amplifier circuits using diodes; analog signal detection, conditioning and conversion systems; transducers, difference and instrumentation amplifiers, active filters, A/D and D/A conversion. [Offered: F]', 'SYDE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SYDE 292L', 'Circuits, Instrumentation, and Measurements Laboratory', 0.25, 'LAB', 'Laboratory experiments for students taking SYDE 292. [Offered: F]', 'SYDE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SYDE 301', 'Seminar', 0, 'SEM', 'Systems design third-year students will meet with a faculty member designated as their class professor. Performance in assignments, conceptual difficulties with courses, interrelation of coursework, later work and engineering practice will be discussed. Non-credit course. [Offered: S]', 'SYDE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SYDE 302', 'Seminar', 0, 'SEM', 'Systems design third-year students will meet with a faculty member designated as their class professor. Performance in assignments, conceptual difficulties with courses, interrelation of coursework, later work and engineering practice will be discussed. Non-credit course. [Offered: W]', 'SYDE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SYDE 311', 'Advanced Engineering Math 2', 0.5, 'LEC, TUT', 'Series solutions of ordinary differential equations; Bessel functions, orthogonal functions, Fourier expansions and integral, Legendre polynomials, Sturm-Liouville systems. Partial differential equations; parabolic, hyperbolic and elliptic equations, numerical methods. Numerical integration; Gaussian quadrature, Runge-Kutta methods. Nonlinear systems; qualitative analysis, phase plane, Newton-Raphson method. [Offered: S]', 'SYDE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SYDE 312', 'Applied Linear Algebra', 0.5, 'LEC, TUT', 'Vector spaces, linear independence, linear maps and matrix representations. Inner product spaces and orthogonality. Gramm-Schmidt algorithm and orthogonal projection. Interpolation and curve-fitting. Eigenvalues and eigenvectors, diagonalization, singular value decomposition. Applications and numerical methods. [Offered: W]', 'SYDE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SYDE 322', 'Software Design', 0.5, 'LEC, TUT', 'Software requirements specification; software architecture; design patterns; software testing and quality assurance; software maintenance; design of efficient algorithms and methods for their analysis, mathematical algorithms, string processing algorithms, geometrical algorithms, exhaustive search and traversal techniques, introduction to lower bound theory and NP-completeness. Case studies and engineering examples. [Offered: W]', 'SYDE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SYDE 332', 'Introduction to Complex Systems', 0.5, 'LEC, TUT', 'The overwhelming majority of societal and ecological issues of pressing importance are complex systems; nonlinear interacting systems poorly characterized by linear analyses and Gaussian statistics. This course introduces the mathematics needed to understand such interactions, including nonlinear dynamics, critical and bifurcation behaviours, large-scale systems, power-law distributions, and statistical inference. The mathematical methods will be motivated by a set of case studies comprised of pressing large-scale interconnected problems such as global warming, energy shortages, desertification, overpopulation, poverty, and economic instability, to be investigated from a systems engineering perspective that will connect the mathematical analyses to real-world examples. [Offered: W]', 'SYDE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SYDE 334', 'Applied Statistics', 0.5, 'LEC, TUT', 'Review of basic Normal theory, t, chi-squared, and F distributions. Simple linear regression. Lack of fit. Analysis of variance. Multiple linear regression, variable selection techniques, indicator variables, diagnostics. Brief introduction to non-linear regression, factorial experimentation. [Offered: W]', 'SYDE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SYDE 348', 'User Centred Design Methods', 0.5, 'LEC, TUT', 'This course approaches the design of tasks, tools, products, and systems from a user-centered design perspective. Emphasis is on the human factors and usability methods, and techniques that can and should be applied throughout the iterative design process. While design issues pertaining to human-computer interaction are discussed, the methods presented can be applied to the design of almost any user interface. Major topics include; user research methods for usability and user experience, inspection methods, user testing, applied statistical analysis. [Offered: W]', 'SYDE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SYDE 422', 'Machine Intelligence', 0.5, 'LEC, TUT', 'The objective of this course is to introduce the students to current intelligent system concepts. An overview of different learning schemes will be provided, including: Decision Tree, Bayesian, Inductive, Analytical and Rule-based Learning. The main focus of the course will be on Neural Nets, Genetic Algorithms and Reinforcement Learning. [Offered: W]', 'SYDE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SYDE 351', 'Systems Models 1', 0.5, 'LEC, TUT', 'Introduction to systems modelling and analysis. Graph theoretic models and formulation of system equations. State space formulation and solution. Time and frequency domain solutions. Application to engineering systems. [Offered: W, S]', 'SYDE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SYDE 352', 'Introduction to Control Systems', 0.5, 'LEC, TUT', 'Classical and state space representations of control systems. Stability, controllability, observability and sensitivity. Routh-Hurwitz and root-locus methods. Frequency domain behaviour, Bode plots, Nyquist stability criteria. Pole placement, PID, phase-lead and phase-lag controllers.(labs alt. weeks) [Offered: W]', 'SYDE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SYDE 352L', 'Control Systems Laboratory', 0.25, 'LAB', 'Laboratory experiments for students taking SYDE 352. [Offered: W]', 'SYDE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SYDE 361', 'Engineering Design', 0.5, 'LAB, LEC, TUT', 'The methodology of design; defects, needs and the problem definition; criteria and generation of alternative solutions; feasibility analysis; optimization; selection, implementation and solution. The lecture material is supplemented by a term-long design project done in small groups. [Offered: S]', 'SYDE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SYDE 362', 'Systems Design Workshop 1', 0.5, 'LAB, LEC', 'Engineering design project course where students work in small groups applying the principles of engineering problem solving, systems analysis, simulation, optimization and design to a problem of their own choosing. Students have individual project supervisors as well as an overall coordinator who provides the framework for the term projects. [Offered: W]', 'SYDE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SYDE 372', 'Introduction to Pattern Recognition', 0.5, 'LEC, TUT', 'Pattern recognition as a process of data analysis. Pattern features as components in a random vector representation. Classification techniques; distance measures in feature space, probabilistic (Bayesian) decision theory, linear discriminants. Clustering and feature extraction. Applications; optical character recognition, speech recognition, industrial robot vision, medical diagnosis, remote sensing and satellite image analysis, fault detection and diagnosis in complex systems such as nuclear reactors. [Offered: W]', 'SYDE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SYDE 381', 'Thermodynamics', 0.5, 'LEC, TUT', 'An introductory course in engineering thermodynamics structured for students in Systems Design. Classical thermodynamics is presented as the systematic study of energy; its use, degradation, and waste. Introduction to heat transfer by conduction, convection, and radiation. Applications focus on problems of energy and environment. The concepts of statistical thermodynamics are introduced. [Offered: S]', 'SYDE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SYDE 383', 'Fluid Mechanics', 0.5, 'LAB, LEC, TUT', 'Fundamental concepts in systems involving fluid flow. Basic treatment of statics, kinematics and dynamics of fluids. Conservation of mass, momentum and energy for a control volume. Dimensional analysis and similarity. Flow in pipes and channels. Brief introduction to boundary layers, lift and drag, ideal and compressible flow. [Offered: S]', 'SYDE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SYDE 384', 'Biological and Human Systems', 0.5, 'LEC, TUT', 'In this course, students will become familiar with the physiology and anatomical structures of the human body. The structure, functions and properties of the major biological systems (musculoskeletal, nervous, and cardiovascular) will be presented in relation to modeling biological systems and the design of biomedical devices (imaging, assistive and diagnostic). Various aspects of pathology and how they influence measurements will also be introduced. [Offered: W]', 'SYDE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SYDE 401', 'Seminar', 0, 'SEM', 'Systems design fourth-year students will meet with a faculty member designated as their class professor. Conceptual difficulties, the interrelation of course work and engineering practice will be discussed. Non-credit course. [Offered: F]', 'SYDE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SYDE 402', 'Seminar', 0, 'SEM', 'Systems design fourth-year students will meet with a faculty member designated as their class professor. Conceptual difficulties, the interrelation of course work and engineering practice will be discussed. Non-credit course. [Offered: W]', 'SYDE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SYDE 411', 'Optimization and Numerical Methods', 0.5, 'LEC, TUT', 'Root-finding methods. Linear programming, simplex and interior-point methods. Local and global optimization methods. Constrained optimization. Multiobjective and multidisciplinary design optimization.[Offered: F]', 'SYDE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SYDE 461', 'Systems Design Workshop 2', 0.5, 'LAB, LEC', 'The first half of a two-term engineering design project continuing the systems design workshop sequence. An interim progress report is presented at the end of the first term. [Offered: F]', 'SYDE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SYDE 462', 'Systems Design Workshop 3', 0.5, 'LAB, LEC', 'The concluding half of the fourth-year systems design workshop. [Offered: W]', 'SYDE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SYDE 522', 'Machine Intelligence', 0.5, 'LEC, TUT', 'The objective of this course is to introduce students to current intelligent system concepts. An overview of different learning schemes will be provided, including decision tree, bayesian, inductive, analytical and rule-based learning. The main focus of this course will be on neural nets, genetic algorithms and reinforcement learning. [Offered: W]', 'SYDE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SYDE 531', 'Design Optimization Under Probabilistic Uncertainty', 0.5, 'LEC, TUT', 'Optimization methods for real world problems have to deal with probabilistic uncertainty either due to data uncertainty or manufacturing uncertainty or both. Maximizing the expected value of the objective function subject to reliability (or risk) constraints is commonly used in such design or decision-making problems. Common methods used are stochastic programming, stochastic dynamic programming, chance-constraints, yield optimization and tolerance design. Example applications are selected from water management, energy systems, financial engineering, and manufacturing. [Offered: W]', 'SYDE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SYDE 533', 'Conflict Resolution', 0.5, 'LEC, TUT', 'Formal methods for studying engineering decision-making problems involving multiple participants and multiple objectives. Topics include the graph model for conflict resolution, normal game form, metagame analysis, games with misperceptions, preference elicitation, human behaviour under conflict, evolution of a conflict, decision-making under uncertainty, sensitivity analyses, multiple criteria decision analysis, group decision and negotiation, coalition analysis, decision support systems, and real-world applications of the foregoing concepts. [Offered: F]', 'SYDE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SYDE 542', 'Interface Design', 0.5, 'LEC, TUT', 'This course focuses on the design of computer interfaces for simple to complex systems. Examples of applications are used to illustrate theoretical approaches. Main topics include forms of visual display; auditory display and soft controls; context, navigation and layout; development techniques; design for engagement. [Offered: W]', 'SYDE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SYDE 543', 'Cognitive Ergonomics', 0.5, 'LEC, TUT', 'This course focuses on the role engineering psychology research plays in design of the information displays and devices associated with simple and complex cognitive tasks. Main topics include signal detection and target location tasks, navigation tasks, training tasks, communication tasks, human error, stress and mental workload, supervisory control, and situational awareness. [Offered: F]', 'SYDE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SYDE 544', 'Biomedical Measurement and Signal Processing', 0.5, 'LEC, TUT', 'This course develops an understanding of biomedical measurements through the examination of electromyographic (EMG), and electroencephalographic (EEG), electrocardiographic (ECG) signals. Measurement of human-body position, force, and pressure, and related instrumentation will also be presented. Signal processing techniques will be discussed in the context of extraction and application of useful biomedical signals. [Offered: W]', 'SYDE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SYDE 552', 'Computational Neuroscience', 0.5, 'LEC, TUT', 'Introduction to quantitative principles in the analysis of neurophysiological systems. Biophysics of excitable membranes. Encoding of sensory information in neural spiking activity. Bayesian models in perception and motor control. Models of synaptic plasticity, learning, and memory. [Note for systems design engineering students: It is recommended that one of BIOL 273, 376, 377 or BME 284 or SYDE 384 be taken before or concurrently with SYDE 552. Offered: W]', 'SYDE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SYDE 553', 'Advanced Dynamics', 0.5, 'LEC, TUT', 'Newtonian and Eulerian formulation of particle and rigid body kinematics and dynamics. Energy (Lagrangian and Hamiltonian) formulations of particle and rigid body dynamics; generalized co-ordinates, generalized forces, holonomic constraints, Lagrange multipliers. [Offered: F]', 'SYDE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SYDE 556', 'Simulating Neurobiological Systems', 0.5, 'LEC, TUT', 'This course develops and applies a general framework for understanding neural computation in the context of recent advances in theoretical and experimental neuroscience. Particular emphasis is placed on understanding representation, nonlinear computation, and dynamics in real neurobiological systems. Students will apply signal processing, control theory, linear algebra, probability theory, and similar quantitative tools for the purpose of modelling sensory, motor, and cognitive systems. Topics covered include single neuron function, neural coding, neural dynamics, attractor networks, learning, statistical inference, locomotion, working memory, etc. Familiarity with neural systems is helpful but not essential. [Offered: W]', 'SYDE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SYDE 575', 'Image Processing', 0.5, 'LAB, LEC, TUT', 'Beginning with a discussion of quantitative models of imaging systems, this course moves on to apply methods of linear systems theory and signal processing to image processing. Simple spatial domain techniques as well as spatial frequency domain methods, and digital filter design for image enhancement and restoration are discussed. The key methods and problems are surveyed; edge detection, image denoising, image segmentation, image enhancement, image compression, image registration, and feature detection. Applications to machine vision, remote sensing, and medical imaging will be emphasized. [Offered: F]', 'SYDE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SYDE 621', 'Mathematics of Computation', 0.5, 'LEC', 'Review of mathematical and computational preliminaries; sources of error in floating-point arithmetic; solution of linear equations, eigen value problems, singular value decomposition, non-linear equations, ordinary differential equations and issues in designing mathematical software. The emphasis in this course will be on solution techniques rather than modelling and equation formulation.', 'SYDE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SYDE 625', 'Tools of Intelligent Systems Design', 0.5, 'LEC', 'The course outlines fundamentals of intelligent systems design using tools of computational intelligence and soft computing. These include fuzzy logic, neural networks, genetic algorithms and other hybrid techniques such as neuro fuzzy systems and fuzzy-generated algorithms.', 'SYDE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SYDE 631', 'Time Series Modelling', 0.5, 'LEC', 'The theory and application of time-series modelling are presented for describing phenomena measured at discrete points in time. The types of time-series models include stationary auto regressive moving average (ARMA), nonstationary, special families of seasonal, transfer function-noise (multiple inputs-single output), intervention, and multivariate (multiple inputs-multiple output) models. Applications are used for explaining how the foregoing models are fitted to both natural and socio-economic time series by following the identification, estimation and diagnostic check stages of model construction. Other topics include simulation in engineering design, forecasting in the operation of large-scale projects, and environmental impact assessment.', 'SYDE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SYDE 632', 'Optimization Methods', 0.5, 'LEC', 'This course is intended to give a broad treatment of the subject of practical optimization. Emphasis will be given to understanding the motivation and scope of various optimization techniques for constrained and unconstrained problems. The methods discussed include, but are not limited to: Newton''s method and its variants, secant methods and conjugate gradient methods for unconstrained problems; active set methods, penalty methods and Lagrangian methods for constrained problems. In order to use, adapt and modify these methods, details that affect their performance will be discussed.', 'SYDE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SYDE 633', 'Remote Sensing Systems', 0.5, 'LEC', 'A survey of modern quantitative remote sensing using optical, infrared, and microwave radiation.  The principles and technologies for acquiring and understanding remotely sensed image data are discussed.  Physical principles of EM propagation and interaction between the radiation and terrestrial and atmospheric materials.  Principles and operation of sensor systems.  Principles of pattern recognition and image processing techniques unique to remote sensing.  Applications of remote sensing to monitoring vegetation, soil, oceans, and inland waters, and snow and ice.', 'SYDE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SYDE 642', 'Cognitive Engineering Methods', 0.5, 'LEC', 'This course examines the fundamentals of modern perspectives on interface design for complex systems using current methods in cognitive engineering.  We discuss Cognitive Work Analysis, Brunswick''s'' Lens Model, Goal Directed Task Analysis, Situation Awareness Oriented Design, Naturalistic Decision Making, Contextual Inquiry, Macro-cognitive Methods, Activity Theory, Concept Mapping, Cognitive Task Analysis, Social Network Analysis and their application to different types of human engineering problems.  Students in this course will learn multiple methods in cognitive engineering with an emphasis on knowing the differences in foundation, assumptions and appropriate application of the methods.  Students will be expected to apply the methods in a realistic research context, applying for ethics clearance and working with actual participants.  Examples of appropriate topics may include understanding how people work with complex or automated systems models. Finally this course discusses aspects of the current research environment in cognitive engineering, with the objective of developing successful future researchers in this area.', 'SYDE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SYDE 676', 'Information Theory in Pattern Synthesis and Analysis', 0.5, 'LEC', 'Fundamental concepts and properties of various information measures are introduced with applications to pattern synthesis and analysis. Applications include (but are not restricted to): pattern recognition and discovery in general event sequences, mixed-mode data and relational structures; inductive learning; random graphs and their application to structural pattern recognition and search strategy in rule networks under uncertainty to class characterization, class discriminations and pattern enhancement of random data and processes. Emphasis is placed on systems and signal pattern analysis.', 'SYDE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SYDE 643', 'Collaborative Systems Design', 0.5, 'LEC', 'Interaction of humans with technological systems often takes place within the broader context of a collaborative setting.  Therefore, there is an increasing trend for the design of these systems to incorporate and support group interactions.  This course will emphasize the study of collaboration from an interdisciplinary perspective and the derivation of system design criteria.  Topics will include group theories, collaboration requirements (including communication, coordination, team awareness), quantitative and qualitative research methods (including laboratory studies, surveys, ethnographic research methods), data analysis, and collaboration technologies.', 'SYDE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SYDE 644', 'Human Factors Testing', 0.5, 'LEC', 'The focus of the course is on in-depth explorations of primary methods of data collection used in human factors engineering research and product design.  We will begin with various philosophical positions regarding human factors for testing and evaluation as they relate to both industrial and research applications.  We will discuss the applications and implications of data collection involving human participants; the limitations of statistical analyses; and the potential application of human factors research as guidelines for product or system design.  Students will be encouraged to explore human factors methods related to their own area of research.', 'SYDE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SYDE 652', 'Dynamics of Multibody  Systems', 0.5, 'LEC', 'In this course, linear graph theory is used to model the topology of 2-d and 3-d systems of rigid bodies connected by mechanical joints, springs, dampers, and actuators. Graph-theoretic methods are then used to systematically derive the kinematic and dynamic equations; the numeric solution of these equations provides a simulation of the system''s motion. Topics include: review of kinematics, dynamics and graph theoretic (GT) methods; application to one-dimensional mechanical systems; GT representation of two-dimensional components and systems; formulation and solution of governing system equations; extension to three dimensional mechanical systems with flexible bodies and mechatronic components; application to kinematic and dynamic analysis of mechanisms, robotic manipulators, vehicles and satellites.', 'SYDE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SYDE 654', 'Graphic Theoretic Models for Complex Systems', 0.5, 'LEC', 'This course extends material in SY DE 551 to include complex systems, systems with uncertainty and systems design issues. Material covered includes: non-linear systems models, their formulations and solutions; higher-order sensitivity models and solutions; second moment analysis and robust design methods for systems with probabilistic components. Examples are taken from electro-mechanical disciplines.', 'SYDE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SYDE 655', 'Optimal Control', 0.5, 'LEC', 'This course is intended to provide an understanding of the principles of optimal control and how they are used in various engineering applications.  Dynamic programming, variational approach and Pontryagin''s Minimum Principle, linear quadratic optimal control, discrete-time optimal control, constrained optimal control systems and model predictive control are introduced.  Numerical methods for optimal control problems are also discussed briefly.', 'SYDE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SYDE 661', 'Model-Based Robust Design', 0.5, 'LEC', 'Robust design encompasses the theories and methodologies that make performance measures (responses, critical times, energy, etc.) invariant to uncertainties in design variables (environmental conditions, manufacturing processes, material dimensions and properties, etc.).  In this course you will learn how robust design methods and mathematical models of engineering systems help find improved designs at a lower cost.  Topics include: the building of simple, efficient, meta-models through computer experiments to replace traditional mechanistic models.  Performance measures and their design specifications.  Sensitivity and importance analysis to select design variables.  Second moment methods using Taylor series to provide parameter (mean) design.  Probabilistic methods combined with manufacturing and scrap costs to perform simultaneous parameter design and tolerance allocation.  Desirability functions and loss functions to deal with multiple competing performance measures.  Integrated design by constrained optimization.  Examples come from industrial processes, as well as hydraulic, electrical and mechatronic systems.  Mathematics required: Total derivatives, Matrix calculus, Kronecker product, singular value decomposition.  Matlab serves as the computing tool.  Course notes are available.', 'SYDE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SYDE 671', 'Advanced Image Processing', 0.5, 'LEC', 'This course is intended to provide insights into advanced topics in image processing.  The topics discussed include but are not limited to:  multi-scale and probabilistic image respresentation and analysis, image restoration, invariant image representation, image reconstruction, image fusion, otical flow, image segmentation, and image registration.  Recent research papers and review papers from the field will be sued as complimentary material to weekly lectures.', 'SYDE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SYDE 672', 'Statistical Image Processing', 0.5, 'LEC', 'Images are all around us!  The proliferation of low-cost, high-quality imaging devices has led to an explosion in acquired images.  Where these images are acquired from a microscope, telescope, satellite, or medical imaging device there is a statistical image processing task:  the interference of something - an artery, a road, a DNA marker, an oil spill - from imagery, possibly noisy, blurry, or incomplete.  The goal of this course is to address methods for solving multidimensional statistical problems, emphasizing theory, mathematical modeling, and algorithms.  Specific topics of interest include inverse problems, multidimensional modeling, random fields, and hierarchical methods.', 'SYDE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SYDE 673', 'Video Processing and Analysis', 0.5, 'LEC', 'This course introduces methods to acquire state (both spatial and temporal) estimations from video streams.  Video streams are analyzed as dynamic systems, linear and non-linear.  If the system can be approximated as linear and Gaussian in terms of the dynamic noise in the process and measurement, then Kalman filter techniques are used.  This refers to sequential state estimation.  For nonlinear dynamical systems, the EFK (Extended Kalman filter) can be used by a liberalization of the stat space model.  Particle filters are used to address state estimation problems where the systems are non-linear and non-Gaussian.  Particle Filters are rooted in Bayesian estimation and Monte Carlo procedures.  The course builds upon these techniques in studying visual tracking and the components of Visual SLAM (Simultaneous Localization and Mapping) procedures.', 'SYDE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SYDE 674', '3D Computer Vision', 0.5, 'LEC', 'This course focuses on 3D computer-vision and camera/optical-based 3D shape-measurement techniques.  Topics include stereo-camera 3D measurement, structures-light techniques, laser-camera range-sensing, fringe-projection phase shifting methods, curve and surface representation, surface fitting, and range-image registration.  The measurement methods will include setup of measurement systems and calibration of instrumentation.  Techniques will be demonstrated and practical exercises will be given.  Biomedical applications will be discussed.  Students will complete an individual course project with written report and oral presentation in class.', 'SYDE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SYDE 675', 'Pattern Recognition', 0.5, 'LEC', 'Pattern recognition addresses the problem of detecting and classifying patterns in data, a process of machine perception in which objects are assigned to classes to which they are most similar. This course introduces the three modern approaches to pattern recognition: statistical, structural and neural. Specific topics include distance and probability based approaches in multidimensional feature spaces, feature extraction, clustering and performance measures; pattern grammars, syntax analysis and grammatical inference; connectionist models, pattern associators, back propagation and self-organizing networks.', 'SYDE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SYDE 677', 'Medical Imaging', 0.5, 'LEC', 'This course introduces the fundamental concepts for medical imaging which include medical image formation (X-ray, CT, MRI, sonography); storage and formats (DICOM, DICOM RT, PACS); visualization, detection and analysis (enhancement, segmentation, registration, compression); safety and regulations for imaging devices & software (IEEE standards, Health Canada Licensing, FDA Clearance).', 'SYDE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SYDE 682', 'Advanced MicroElectroMechanical Systems:  Principles, Design & Fabrication', 0.5, 'LEC', 'This course provides specific knowledge in microelectromechanical systems (MEMS) and devices including microactuators, microsensors, micro-domain forces, microfabrication, and their actuation principles.  Application domains of MEMS in RF, optics, and biosensing will be discussed.  Specific topics include MEMS actuation mechanisms such as electrostatic, electromagnetic, thermal, and piezoelectric; and sensing mechanisms such a peizoresistive, capacitve, optical, and bio-transducer.  Topics such as lithography, thin-film deposition methods, etching techniques will be taught.  The course covers practical examples, device architecture, fabrication design rules and fabrication procedures.', 'SYDE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SYDE 683', 'Modeling, Simulation and Design of MEMS and NEMS', 0.5, 'LEC', 'This course involves the rigorous grounding in the theory and practice of MEMS design as well as ways of extending MEMS (micro-electro-mechanical systems) to apply to the design of NEMS (nano-electro-mechanical systems).  Modeling and simulation processes as they apply to MEMS and NEMS are presented.  Concepts covered include basics of statics and dynamics necessary to construct lumped-mass models, an introduction to the use of reduced-order models in MEMS/NEMS design, the use of these modeling techniques with the use of commercial FEM software (COMSOL, ANSYS, and Coventor) in the simulation and design of MEMS/NEMS, and discussing the most effective uses and limitations of each of these approaches.  The course involves building effective MEMS by design not by trial and error.  Analytical tools for exploring the possibilities of nano-electro-mechanical systems (NEMS) are introduced.', 'SYDE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SYDE 684', 'Materials Biocompatibility', 0.5, 'LEC', 'The course covers fundamental topics of biocompatibility of materials in medicine (polymers, ceramics, metals, composites, bioengineered materials).  Fundamental principles of materials science (bonding, atomic/molecular structure) as well as interfacial engineering of materials will be reviewed.  Materials response to biological systems such as corrosion, degradation, leaching and fracture will be studied in the context of specific biomedical applications.  The host response to materials (immune, inflammatory response and coagulation) will also be treated.  Specific examples of material interactions with biological systems such as bone, blood, skin and the eye will be studied.', 'SYDE');
