INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PSYCH 627A', 'Psychotheraphy Practicum II', 0.5, 'PRA', 'This course offers supervised psychotherapy training in one-to-one therapy for students enrolled in the fourth year of the clinical training program. Ordinarily students will enrol in all three terms because psychotherapy practice requires year-round service. If, however, a student''s work demands a placement off-campus or otherwise requires a full commitment to other activities, the third (spring) term may be omitted. Graded on a Cr/NCr basis.  Only students in the clinical Psychology program are permitted to take this course.', 'PSYCH');
