INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SYDE 624', 'System Simulation: Advanced Topics', 0.5, 'LEC', 'Review of continuous (time driven) and discrete (event driven) system simulation methods using digital computers. Simulation languages and their design; man-machine interface considerations; object-oriented methods; visual data representations; front-end and back-end processors; computer animation of simulation output. Developments in systems simulation due to parallel and high-speed computer architectures. Applications of systems simulation in engineering analysis and design.', 'SYDE');
