INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOCIN 63', 'Team Process and Research Skills: Communication, Facilitation, Collaboration, Problem Solving', 0.5, 'LEC, RDG, SEM, TUT', 'This is a multi-modal, skill-based course designed to help students act effectively in complex social systems and institutions. Students will learn and practice strategies related to the management of diversity, conflict negotiation, and decision-makiing. Self-diagnostic exercises and structured reflection assist students in identifying and understanding their own preferences and the roles they might most naturally and successfully take in group processes.', 'SOCIN');
