INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AFM 100', 'Introduction to Experiential Learning', 0, 'SEM, TUT', 'The objective of this non-credit course is to expose students to the expectations for experiential learning, helping them develop the skills and relationships needed to allocate their time and maintain balance across their academics, career, health and wellness, and community.', 'AFM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AFM 479', 'Cases and Applications in Finance II', 0.5, 'LEC, TST', 'This course builds on the theory of finance and uses cases and applications to further address finance issues and competences, including risk management, equities, derivatives, and other investments.', 'AFM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AMATH 390', 'Mathematics and Music', 0.5, 'LEC', 'An introduction to some of the deep connections between mathematics and music. Topics covered include acoustics, including pitch and harmonics, basic Fourier analysis, the mathematics behind the differing pitch and timbre of string, wind and percussion instruments, scales and temperaments, digital music, musical synthesis.', 'AMATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ANTH 100', 'Introduction to Anthropology', 0.5, 'LEC', 'Anthropology aims at understanding what it is to be human and to examine how humanity is manifested in the diversity of cultures and societies across time and space. This course gives the student an overview of the breadth of anthropology and orients them toward thinking anthropologically in our shared worlds.', 'ANTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ANTH 204', 'Biological Anthropology', 0.5, 'LAB, LEC', 'This course surveys the lines of inquiry in biological anthropology including evolutionary theory, anthropological genetics, primatology, hominin fossil record, osteology and skeletal biology, and modern human biocultural adaptations. A laboratory component provides students an experiential approach to the material.', 'ANTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ANTH 372', 'Archaeological Field School', 0.5, 'FLD, LEC', 'Data gathering techniques are studied and applied in field work on archaeological sites.', 'ANTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ANTH 381', 'Anthropology of South Asia', 0.5, 'LEC', 'An introduction to culture and society in South Asia, focusing on caste, kinship, gender, religion, material culture, regional and national identity, ethnic conflict, globalization, and life in the diaspora.', 'ANTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ANTH 382', 'Anthropology of Contemporary China', 0.5, 'LEC', 'This course explores cultural and social change in contemporary China, including Taiwan, Hong Kong, and global migration. Topics include politics, identity, medicine and disease, urbanization, food security, post-colonialism, and transnationalism.', 'ANTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ANTH 430', 'Science as Practice and Culture', 0.5, 'LEC', 'An examination of science as being constituted by practices which are inevitably social and cultural, as well as of scientific expertise in contemporary issues of new technology, biomedicine, and environmental conflict.', 'ANTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ANTH 447', 'Global Health and Medical Anthropology', 0.5, 'SEM', 'This course is an exploration of global health in anthropological perspective. It includes case studies as well as theoretical and critical readings on global health topics.', 'ANTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ANTH 498', 'Anthropology Capstone', 0.5, 'LEC', 'This course explores themes of current anthropological research as they intersect across the major subfields of the discipline. Topics may vary.', 'ANTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BASE 32', 'Introduction to Skills & Strategies for University', 0.5, 'LEC', 'This integrated course introduces students to essential skills and learning strategies required for active participation in a university lecture environment. Academic vocabulary, listening, and note-taking skills, as well as a variety of reading and study strategies are developed within a discipline-specific context that provides targeted language and study skills support for a concurrent credit course.', 'BASE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BASE 34', 'Introduction to Academic Writing and Research Skills', 0.5, 'LEC', 'This course provides instruction in conventional patterns of academic writing and introduces students to basic research skills which they then apply to produce well-structured and supported text. This course also encourages the development of written fluency and accuracy through a process approach to writing that includes a focus on grammatical structures, sentence variety, cohesive devices and self-editing strategies.', 'BASE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BASE 36', 'Introduction to Academic Speaking and Communication Strategies', 0.5, 'LEC', 'This course introduces students to a variety of formal and informal academic speaking environments and helps them to improve their confidence, vocabulary, pronunciation, listening and presentation skills in order to become more engaged participants in conversations, discussions, and presentations, both in and out of the classroom.', 'BASE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BASE 42', 'Enhanced Skills & Strategies for University', 0.5, 'LEC', 'This course provides students with additional skills and learning strategies to assist in navigating a greater variety of university contexts, including, but not limited to, those pertaining to their concurrent credit course. With an emphasis on building and applying critical thinking skills to better engage with credit course content, this course also presents students with opportunities to expand their discipline-specific vocabulary and heighten interpersonal and teamwork skills.', 'BASE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BASE 44', 'Extended Academic Writing and Research Skills', 0.5, 'LEC', 'Through the extension of students'' research skills and their understanding of rhetorical modes and grammatical structures, this course encourages students to develop an independent approach to writing and research in order to complete a research report related to an area of disciplinary interest. Strategies to revise for accuracy, clarity, and succinctness are also examined.', 'BASE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BASE 46', 'Effective Academic Speaking and Communication Strategies', 0.5, 'LEC', 'With a greater focus on materials organization, group management, and delivery impact, this course encourages students to become more effective overall communicators in a variety of speaking contexts. Persuasive speaking, interview skills, and student-led seminars are additional components of this course, which continues to provide opportunities for students to refine their pronunciation and enhance speaking accuracy and fluency.', 'BASE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BET 100', 'Foundations of Entrepreneurial Practice', 0.5, 'LEC', 'Successful entrepreneurs combine intellectual rigour with innovation to create solutions that have impact. It is a process that starts with a search for "big ideas" using tools such as research, observation, and appreciative inquiry. Next, entrepreneurs analyze situations and apply creativity techniques to develop solutions. Finally, solutions are tested against business drivers including customer value propositions, customer identification, channel selection, and resource needs. This course covers the three steps that form the foundation of all entrepreneurial success: big ideas, solutions, and testing. Because of its broad scope, the course is valuable to students from different academic backgrounds, entrepreneurship interests, and program level.', 'BET');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BET 310', 'Enterprise Co-op Entrepreneurship Planning and Execution', 0.5, 'LEC', 'This course is taken concurrently with a student''s first E Co-op term. This is a requirement for the venture creation theme consolidating current E Co-op workshops and seminars with the addition of online content. Topics include: Business planning and execution; Understanding customers and your value proposition; Refining your business model; Corporate legal issues; Hiring employees; Understanding your competitors; The local entrepreneurial ecosystem; Grants and funding. Requires a post-mortem/root cause analysis report to be submitted at the end of the term. (Note: This is a requirement for the venture creation theme consolidating current E Co-op workshops and seminars with the addition of online content. Students will be given permission if enrolled in an E Co-op term. Offered: F,W,S)', 'BET');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BET 320', 'Entrepreneurial Strategy', 0.5, 'LEC', 'Once your "big idea" has been formulated, what is the best way to realize its potential? With whom should you partner? How do you protect your intellectual property? What is the optimal business model? The objective of this course is to provide an understanding of the commercialization process, with a specific focus on assessment of opportunities and development of an entrepreneurial business plan. Course content includes the importance of strategic partnerships, understanding the bases of competition, conducting industry and market level analyses, evaluating innovative ideas for commercial potential, business planning in a corporate context, intellectual property strategy, and strategic tools needed for launching new ideas.', 'BET');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BET 410A', 'Capstone Entrepreneurship Planning and Execution Part 1', 0.25, 'LEC', 'Goal setting and project planning; team building; understanding your competitive landscape; understanding your customers; patents and patent searches taken concurrently with capstone projects.', 'BET');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BET 410B', 'Capstone Entrepreneurship Planning and Execution Part 2', 0.25, 'LEC', 'Commercialization options and developing a business model; legal issues; creating a business case for the capstone project; presentation skills and "pitching"; post-mortem and root cause analysis taken concurrently with capstone projects. Includes the post-mortem/root cause analysis report to be submitted at the end of the term', 'BET');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 101', 'Biology in the Modern World', 0.5, 'LEC', 'This course will introduce a variety of fundamental concepts of biology to non-majors. The goal is to improve scientific literacy. Topics will include the scientific method, biodiversity, genetics, evolution, physiology, and ecology.', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 414', 'Parasitology', 0.5, 'LEC, TUT', 'This course will explore how parasites affect their host(s) and interact with other parasites. Attention will be given to eukaryotic parasites impacting human and domestic animal health. Public measures of parasite control will be covered. [Offered: W]', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BME 102', 'Seminar', 0, 'SEM', 'Biomedical engineering first-year students will meet with a faculty member designated as their class professor. Performance in assignments, conceptual difficulties with courses, interrelation of coursework, later work, and engineering practice will be discussed. Non-credit course. [Offered: W]', 'BME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BME 184', 'Engineering Biology', 0.5, 'LEC, TUT', 'Introduction to basic concepts of biochemistry and cell biology. Overview of the chemistry of amino acids, carbohydrates, lipids and nucleic acid. Structure and properties of proteins and enzymes. Elements of cell structure and diversity, and relationship of biochemistry with cell metabolism. A focus on biomedical engineering with relevant examples such as biomimetic engineering design, system biology and tissue engineering. [Offered: W]', 'BME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BME 184L', 'Engineering Biology Laboratory', 0.25, 'LAB', 'Laboratory experiments for students taking BME 184. [Offered: W]', 'BME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BME 201', 'Seminar', 0, 'SEM', 'Biomedical engineering second-year students will meet with a faculty member designated as their class professor. Performance in assignments, conceptual difficulties with courses, interrelation of coursework, later work, and engineering practice will be discussed. Non-credit course. [Offered: F]', 'BME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BME 202', 'Seminar', 0, 'SEM', 'Biomedical engineering second-year students will meet with a faculty member designated as their class professor. Performance in assignments, conceptual difficulties with courses, interrelation of coursework, later work, and engineering practice will be discussed. Non-credit course. [Offered: S]', 'BME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BME 213', 'Statistics and Experimental Design', 0.5, 'LEC, TUT', 'Fundamentals of probability and statistics, and applications to biomedical engineering. Random variables and statistical distributions, statistical estimation, hypothesis testing, regression, and experiment design considerations. Applications to biomedical experiments, biomedical imaging data, and clinical trials. [Offered: S]', 'BME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BME 261', 'Prototyping, Simulation and Design', 0.5, 'LEC, TUT', 'Problem-solving approaches, agile design and development, rapid prototyping, revision control, design patterns, development cycles, and simulation. Topics will be reinforced in the context of biomedical engineering projects conducted in groups within a collaborative environment. [Offered: S]', 'BME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BME 281', 'Mechanics of Deformable Solids', 0.5, 'LEC, TUT', 'Introduction to mechanical response of materials and stress-strain relationship. Behaviour of prismatic members in tension, compression, shear, bending, and torsion. Shear-force and bending-moment diagrams. Introduction to instability. Mechanical properties of biological tissues, and viscoeleastic models. Applications to bone, cartilage, and biomedical implants. [Offered: F]', 'BME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BME 281L', 'Mechanics of Deformable Solids Laboratory', 0.25, 'LAB', 'Laboratory experiments for students taking BME 281. [Offered: F]', 'BME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BME 282', 'Materials Science for Biomedical Engineers', 0.5, 'LEC, TUT', 'Crystalline structure, crystal defects, non-crystalline materials, structure and properties of metals, ceramics, glasses, semi-conductors, polymers, and composites. Factors in materials design, material selection and processing in the context of biomedical devices and instruments will also be discussed.[Offered: F]', 'BME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BME 283', 'Chemistry Principles', 0.5, 'LEC, TUT', 'The stoichiometry of compounds and chemical reactions. Periodicity and chemical bonding. Energy changes in chemical systems. Electronic structure of atoms and molecules, correlation with the chemical reactivity of common elements, inorganic and organic compounds. Discussion of the structure, nomenclature and reactions of important classes of organic compounds. Stereochemistry and its role in reaction mechanisms. [Offered: F, first offered Fall 2015]', 'BME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BME 284', 'Physiological and Biological Systems', 0.5, 'LEC, TUT', 'The structure, functions, and properties of the major biological systems (musculoskeletal, nervous, cardiovascular) will be presented in relation to the design of biomedical devices (imaging, assistive, and diagnostic). Concepts in modeling biological systems will be introduced. Various aspects of pathology and how they influence medical device design will also be discussed. [Offered: S]', 'BME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BME 284L', 'Physiology and Anatomy Laboratory', 0.25, 'LAB', 'Laboratory experiments for students taking BME 284. [Offered: S]', 'BME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BME 285', 'Engineering Biology', 0.5, 'LEC, TUT', 'Introduction to basic concepts of biochemistry and cell biology. Overview of the chemistry of amino acids, carbohydrates, lipids and nucleic acid. Structure and properties of proteins and enzymes. Elements of cell structure and diversity, and relationship of biochemistry with cell metabolism. A focus on biomedical engineering with relevant examples such as biomimetic engineering design, system biology, and tissue engineering. [Offered: F]', 'BME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BME 286', 'The Physics of Medical Imaging', 0.5, 'LEC, TUT', 'The fundamental laws of electricity, magnetism and optics will be taught through the introduction to basic concepts of medical imaging: radiation for imaging, x-ray, computed tomography (CT), magnetic resonance imaging (MRI), ultrasound or sonography imaging, electric impedance tomography, confocal microscopy, fluoroscopy. Radionuclide imaging: Single Photon Emission Computed Tomography (SPECT), and Positron Emission Computed Tomography (PET). Emerging technologies: Elastography, THz imaging, Molecular imaging will also be discussed. [Offered: S, first offered Spring 2016]', 'BME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BME 292', 'Digital Systems', 0.5, 'LEC, TUT', 'Digital technology, combinatorial logic, binary arithmetic, sequential circuits, digital design, and microcontrollers. Topics will be reinforced in the context of biomedical microcontrollers and sensors used in physiological monitoring and clinical support systems. [Offered: S]', 'BME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BME 292L', 'Digital Systems Laboratory', 0.25, 'LAB', 'Laboratory experiments for students taking BME 292, focusing on circuit construction, simulation, and design. [Offered: S]', 'BME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BME 301', 'Seminar', 0, 'SEM', 'Biomedical engineering third-year students will meet with a faculty member designated as their class professor. Performance in assignments, conceptual difficulties with courses, interrelation of coursework, later work, and engineering practice will be discussed. Non-credit course. [Offered: W]', 'BME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BME 302', 'Seminar', 0, 'SEM', 'Biomedical engineering third-year students will meet with a faculty member designated as their class professor. Performance in assignments, conceptual difficulties with courses, interrelation of coursework, later work, and engineering practice will be discussed. Non-credit course. [Offered: F]', 'BME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BME 351', 'Linear Signals and Systems', 0.5, 'LEC, TUT', 'Models and analysis of linear systems in the context of measurement and processing of biosignals such as electroencephalography (EEG), electrocardiography (ECG), and electromyography. Discrete and continuous time systems, difference and differential equations, impulse and frequency response, transform domain techniques, transfer functions and frequency response, frequency domain analysis of linear systems, sampling theory, stability, and linear filters. [Offered: W, first offered Winter 2017]', 'BME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BME 352', 'Control Systems', 0.5, 'LEC, TUT', 'Classical and state space representations of control systems. Stability, controllability, observability and sensitivity. Routh-Hurwitz and root-locus methods. Frequency domain behaviour, Bode plots, Nyquist stability criteria. Pole placement, PID, phase-lead and phase-lag controllers. Application to anatomical system models, including musculoskeletal and cardiovascular systems, and to physiological feedback systems. [Offered: F, first offered Fall 2017]', 'BME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BME 352L', 'Control Systems Laboratory', 0.25, 'LAB', 'Laboratory experiments for students taking BME 352. [Offered: F, first offered Fall 2017]', 'BME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BME 354', 'Anatomical Systems Modeling', 0.5, 'LEC, TUT', 'Introduction to systems theory as a general modeling method, and applied to the skeletal, neuromuscular, central nervous, cardiovascular, and respiratory systems of the human body. Time-domain simulations, sensitivity analyses, and parameter identification, with the latter driven by experimental measurements of system performance. [Offered: F, first offered Fall 2017]', 'BME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BME 361', 'Biomedical Engineering Design', 0.5, 'LAB, LEC, TUT', 'Design methods: problem definition, requirements analysis, criteria and generation of alternative solutions, feasibility analysis, and optimization. Product development. Design survey of biomedical equipment and assistive technologies. A term-long design project in small groups. [Offered: W]', 'BME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BME 362', 'Biomedical Engineering Design Workshop 1', 0.5, 'LAB, LEC', 'Engineering design project course where students work in small groups applying the principles of engineering problem solving, systems analysis, simulation, optimization, and design to a biomedical engineering problem of their own choosing. Lecture topics include project management, risk management, standards, regulatory clearance, and certification for biomedical devices. [Offered: F]', 'BME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BME 364', 'Engineering Biomedical Economics', 0.5, 'LEC, TUT', 'This course examines key economic issues in health care and biomedical industries. Topics include the market for medical care, health insurance, various models of healthcare delivery and competition and the role of government in policy, financing and delivery of health care. This course will train students to use economic analysis to model and understand the complex interactions between health care delivery, insurance markets, health innovators, governments, and firms. [Offered: F]', 'BME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BME 381', 'Biomedical Engineering Ethics', 0.5, 'LEC, TUT', 'This course explores ethical issues in biomedical engineering practice, including professional ethics, medical ethics, the ethics of human and animal subject use in biomedical research, and the impact of biomedical engineering solutions on society and the environment. [Offered: W]', 'BME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BME 384', 'Biomedical Transport: Biofluids and Mass Transfer', 0.5, 'LEC, TUT', 'Fundamental concepts in systems involving fluid flow. Basic treatment of statics, kinematics, and dynamics of fluids. Mass transfer, conservation of mass, momentum and energy for a control volume. Dimensional analysis and similarity. Discussion of flow in pipes and channels and brief introduction to boundary layers, lift and drag, ideal and compressible flow will be specifically covered in the context of the cardiovascular system (macrocirculation and microcirculation). [Offered: F]', 'BME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BME 391', 'Circuits, Instrumentation, and Measurements', 0.5, 'LEC, TUT', 'Basic concepts of hardware measurement systems pertaining to the measurement of biosignals: active and passive circuit elements, Kirchhoff''s laws, mesh and nodal circuit analysis, principle of superposition, step responses of first and second order networks, sinusoidal steady state analysis, input-output relationships, transfer functions and frequency response of linear systems, operational amplifiers, analog signal detection, conditioning and conversion systems, transducers, difference and instrumentation amplifiers, A/D and D/A conversion. Examples will be presented in the form of physiological monitoring hardware for vital measurements such as electroencephalography (EEG), electrocardiography (ECG), and electromyography (EMG). [Offered: W, first offered Winter 2017]', 'BME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BME 391L', 'Circuits, Instrumentation, and Measurements Laboratory', 0.25, 'LAB', 'Laboratory experiments for students taking BME 391. [Offered: W, first offered Winter 2017]', 'BME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BME 401', 'Seminar', 0, 'SEM', 'Biomedical engineering fourth-year students will meet with a faculty member designated as their class professor. Performance in assignments, conceptual difficulties with courses, interrelation of coursework, later work, and engineering practice will be discussed. Non-credit course. [Offered: F]', 'BME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BME 402', 'Seminar', 0, 'SEM', 'Biomedical engineering fourth-year students will meet with a faculty member designated as their class professor. Performance in assignments, conceptual difficulties with courses, interrelation of coursework, later work, and engineering practice will be discussed. Non-credit course. [Offered: W]', 'BME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BME 411', 'Optimization and Numerical Methods', 0.5, 'LEC, TUT', 'Interpolation and curve fitting, root-finding methods, local and global optimization methods, constrained optimization, multiobjective and multidisciplinary design optimization for biomedical applications such as implanted chips and therapeutic regimens. [Offered: F]', 'BME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BME 461', 'Biomedical Engineering Design Workshop 2', 0.5, 'LAB, LEC', 'The first half of a two-term engineering design project continuing the biomedical design workshop sequence. A prototype and interim progress report are presented at the end of the first term. Lecture topics include safety and risk analysis of biomedical technologies. [Offered: F]', 'BME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BME 462', 'Biomedical Engineering Design Workshop 3', 0.5, 'LAB, LEC', 'The concluding half of the fourth-year Biomedical Engineering Design Workshop. [Offered: W]', 'BME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EARTH 439', 'Flow and Transport Through Fractured Rocks', 0.5, 'LEC', 'Fractures are ubiquitous in geologic media and important in disciplines such as physical and contaminant hydrogeology, geotechnical engineering, civil and environmental engineering, petroleum engineering among other areas. Despite the importance of fractures, its characterization and predictions of groundwater flow and contaminant transport are fraught with significant difficulties. Students are taught to deal with fractures in hydrogeology, to conceptualize them, and to build reliable models for predicting groundwater flow and contaminant transport. Offered [W]', 'EARTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EARTH 695', 'Earth MSc Seminar Proposal', 0.5, 'SEM', 'A written research proposal, and a seminar and defence of the proposal to be given by all MSc students (thesis option) within two terms of entering this program.', 'EARTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EASIA 231R', 'Calligraphy to Conceptual Art: Text as an Image in Islamic and East Asian Visual Arts', 0.5, 'LEC', 'Exploring the history of Islamic and East Asian calligraphic arts and the study of Eastern and Western artists, this course examines multiple ways in which writing and other forms of visible language are incorporated into visual arts. Through studio projects, students explore conceptual approaches to text-based image making.', 'EASIA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EASIA 260R', 'Modern Chinese Literature (1917 - present day)', 0.5, 'LEC, SEM', 'This survey course explores modern Chinese literature beginning from 1917 and the New Culture movement. Selected works and relevant critiques are used to prompt discussion of cultural and literary topics, particularly of changes in Chinese culture and literature from the beginning of the 20th century to the present day.', 'EASIA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EASIA 377R', 'Cold War in East Asia', 0.5, 'LEC', 'This course examines the origins, developments, and contemporary implications of the Cold War in East Asia.', 'EASIA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 650', 'Methods and Tools for Software Engineering', 0.5, 'LEC', 'Software Systems - Systems programming and operating systems, scripting, system calls, libraries, compilers and interpreters.  Mathematical logic - propositional & predicate logic, and some higher-order logics, syntax, semantics, entailment, deduction, use of logic in software.  Data structures - lists, stacks, queues, heaps, trees, graphs, and algorithms to manipulate such data structures.  Graduate students who have previously taken ECE 750 with the topic title Methods and Tools for Software Engineering are not eligible to take ECE 650.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 412', 'Topics in Game Theory', 0.5, 'LEC', 'This course builds on ECON 392 to give students a broader exposure to game theory. Possible topics include extensive games with imperfect information, evolutionary games, repeated games, as well as applications of game theory such as auctions and negotiations.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 741', 'Advanced Public Economics Expenditure', 0.5, 'LEC', 'This course familiarizes PhD students with recent developments in research on government expenditures. By the end of the course, students are expected to know what are the main current academic and policy debates in relation to public expenditures as well as what research papers from the most recent important contributions to these debates.  This course is an essential part of preparation for students aiming to write a thesis in public economics.  Topics include, but are not limited to, the efficient provision of public goods, externalities, public choice and voting, fiscal federalism and equalization, and public policy reform.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 742', 'Advanced Public Economics: Taxation', 0.5, 'LEC', 'This course provides an advanced research-oriented exploration of selected issues in the economics of taxation.  By the end of the course, students are expected to know what are the main current academic and policy debates in relation to personal and corporate taxation as well as what research papers form the most recent important contributions to these debates.  This course is an essential part of  preparation for students aiming to write a thesis in public economics.  Topics include, but are not limited to, applied tax incidence and analysis, dynamic tax policy modeling, optimal taxation, tax competition, fiscal federalism and equalization, and tax policy reform.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 751', 'Advanced Labour Economics', 0.5, 'LEC', 'This course offers a study of the core topics in labour economics.  Labour supply, labour demand, wage determination and unemployment are presented in the context of consumer and producer choice theory.  Knowledge of graduate level microeconomic theory and econometrics is required.  Although no familiarity with specialized econometric methods is assumed, this course will introduce students to common estimation techniques employed in labour economics.  Students will also learn about the more recent treatment of the core topics through readings of current research analyzing models'' extensions.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 757', 'Advanced Environmental Economics', 0.5, 'LEC', 'This course is intended for PhD students who specialize in the field of environmental economics.  It provides an advanced analysis of the interplay between economic activities and the environment, and environmental regulation.  The course will focus on the application of several advanced economic tools and methods covered in the core PhD theory courses to environmental problems.  Topics may include the application of general equilibrium theory, game theory, real options theory, optimal control theory, dynamic optimization, and econometric analysis to environmental policy analysis and design.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EMLS 101R', 'Oral Communications for Academic Purposes', 0.5, 'LEC', 'Designed specifically for students for whom English is not the first language, this course introduces group discussion strategies and presentation skills. Students will improve their informal and formal speaking skills to meet current social, academic, and business practices. A focus on pronunciation and vocabulary assists learners to better engage in conversations and listen to and deliver English presentations. Materials organization, rhetorical devices, nonverbal communication, group management, and intonation patterns are examined.', 'EMLS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EMLS 102R', 'Clear Communication in English Writing', 0.5, 'LEC, TUT', 'Designed specifically for students for whom English is not the first language, this course offers sentence-level instruction in grammar and idiom to teach students to produce, evaluate, and edit writing under time constraints. It emphasizes readability and error reduction in sentences and paragraphs.', 'EMLS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EMLS 103R', 'Effective English Pronunciation', 0.5, 'LEC', 'Designed specifically for students for whom English is not the first language, this course has an intensive focus on the structure of the English sound system with a view to improving pronunciation. Vowel and consonant sounds are practised as well as the phonetic elements above those sounds, such as stress, intonation, and rhythm. The goal is for students to identify their problem areas and to develop strategies to improve their comprehensibility.', 'EMLS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EMLS 104R', 'Reading and Listening for Academic Purposes', 0.5, 'LEC', 'Designed specifically for students for whom English is not the first language, this course offers instruction in reading and listening strategies. Students learn macro and micro reading and listening strategies to improve their reading comprehension and speed and their listening and note-taking skills. Students also build their academic vocabulary.', 'EMLS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EMLS 110R', 'Communicating in Canadian Academic Contexts', 0.5, 'LEC', 'Designed specifically for students for whom English is not the first language, this course introduces students to North American academic expectations. Students learn to identify attitude and intent in text, to recognize North American patterns of organization, to select and acknowledge appropriate primary and secondary sources, and to present ideas cohesively in speaking and writing.', 'EMLS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EMLS 129R', 'Written Academic English', 0.5, 'LEC, TUT', 'Designed specifically for students for whom English is not the first language, this writing skills course provides instruction in grammar, sentence and paragraph structure, elements of composition, and academic essay writing, including a focus on theme, development of central ideas, exposition, and argumentation.', 'EMLS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 294', 'Game Studies', 0.5, 'LEC', 'This course introduces students to the field of humanities-based game studies. Topics may include the debate between ludological (rules-based) and narratological (story-based) approaches, procedural studies, platform and software studies, gamification, games and adaptation studies, and games as rhetorical objects.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 295', 'Social Media', 0.5, 'LEC', 'This course surveys the popular social media landscape and charts scholarly approaches, both methodological and theoretical, to understanding and analyzing social media texts. Topics to be addressed may include memes, social networks, fan communities and texts, digital identity, and other emergent social media forms.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ERS 374', 'Special Topics in Environment, Resources and Sustainability', 1, 'FLD, LEC', 'Course content varies according to instructor availability and demand for specific topics in environment, resources and sustainability and may include field courses.', 'ERS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ERS 422', 'Biosphere Reserves as Social-Ecological Systems', 0.5, 'FLD, RDG', 'A term-long reading course introducing students to social-ecological systems, using the UNESCO Biosphere Reserve in Georgian Bay. Internationally known as "experiments in sustainable development," biosphere reserves pursue conservation of biodiversity, sustainable resource management, and aim to build social and ecological resilience. Students will link theories from their readings to guest lectures and excursions that address aquatic ecosystems, sustainable forestry, species at risk management, and indigenous knowledge.', 'ERS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ERS 454', 'Parks and Protected Areas: Issues and Trends', 0.5, 'SEM', 'Government decisions to conserve rather than develop natural resources are nationally and internationally significant, but are often controversial. This course examines public policy objectives, issues and diverse perspectives related to the conservation of marine and terrestrial environments through the establishment and ongoing management of parks and protected areas. Lectures, seminar discussions and assignments engage students in the exploration of key public policy conservation issues.', 'ERS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ERS 473', 'Special Topics in Environment, Resources and Sustainability', 0.5, 'FLD, LEC', 'Course content varies according to instructor availability and demand for specific topics in environment, resources and sustainability and may include field courses.', 'ERS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ERS 622', 'Biosphere Reserves as Social-Ecological Systems', 0.5, 'LEC, RDG, SEM', 'The Georgian Bay Biosphere Reserve is one of 16 UNESCO biosphere reserves in Canada, and is used as a setting to learn about sustainable community development, adaptive resource management, and social and ecological resilience.  The main objective is to link practical experience "on the ground" with some of the theoretical concepts related to sustainability and complex social ecological systems.', 'ERS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ERS 654', 'Parks and Protected Areas', 0.5, 'LEC, RDG, SEM', 'Government decisions to conserve rather than develop natural resources are nationally and internationally significant, but are often controversial.  This course examines public policy objectives, issues and diverse perspectives related to the conservation of marine and terrestrial environments through the establishment and ongoing management of parks and protected areas.  Lectures, seminar discussions and assignments engage students in the exploration of key public policy conservation issues.', 'ERS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ERS 684', 'Soil in the Environment', 0.5, 'LEC, RDG, SEM', 'This course examines the role of soil in the environment and its importance as a natural resource in agricultural and forest productivity and the effects on soil due to mismanagement.', 'ERS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EVSY 302W', 'Climate Chnge and Soc (WLU)', 0.5, 'LEC', 'None', 'EVSY');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('FINE 258', 'Aspects of the Cinemas of the Americas', 0.5, 'LAB, SEM', 'This course takes a historical and thematic approach to analyzing the history, context, and various practices of cinema in the Americas.', 'FINE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('FINE 275', 'Calligraphy to Conceptual Art: Text as an Image in Islamic and East Asian Visual Arts', 0.5, 'LEC', 'Exploring the history of Islamic and East Asian calligraphic arts and the study of Eastern and Western artists, this course examines multiple ways in which writing and other forms of visible language are incorporated into visual arts. Through studio projects, students explore conceptual approaches to text-based image making.', 'FINE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('FINE 383', 'Computational Digital Art Studio', 0.5, 'STU', 'An upper-level studio course to create computational projects that function as art works and aesthetic experiences. Students will work in interdisciplinary teams to combine computer science principles with fine art technical and conceptual skills. [Offered: W]', 'FINE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GENE 21N', 'Topics for Technical Courses Taken on Exchange by Nanotechnology Engineering Students', 0.5, 'LEC', 'Courses taken at foreign universities by University of Waterloo engineering students while enrolled in an international exchange institution, and reserved for courses without equivalents at the University of Waterloo. These courses are treated as technical electives in the student''s plan. Such courses are reported on the student''s transcript with their original titles in English showing as the topic. The grades for these courses will be either CR or NCR.', 'GENE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GENE 21U', 'Topics for Technical Courses Taken on Exchange by Management Engineering Students', 0.5, 'LEC', 'Courses taken at foreign universities by University of Waterloo engineering students while enrolled in an international exchange institution, and reserved for courses without equivalents at the University of Waterloo. These courses are treated as technical electives in the student''s plan. Such courses are reported on the student''s transcript with their original titles in English showing as the topic. The grades for these courses will be either CR or NCR.', 'GENE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 654', 'Applied Biogeochemistry', 0.5, 'LEC, TUT', 'This course focuses on ecological and biogeochemical processes that are linked to the hydrological cycle and how these relate tothe management of natural resources.  The objectives of this interdisciplinary course are to explore topics that integrate ecosystem processes with physical hydrology, and examine the impacts of human activities on the ecohydrological system.  Therefore the focus of this course is on the storage and movement of water, solutes and nutrients within selected ecosystems, considering the ecological consequences of human activit.  *eligible for MES', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 377R', 'Cold War in East Asia', 0.5, 'LEC', 'This course examines the origins, developments, and contemporary implications of the Cold War in East Asia.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 660', 'Transnational and Global History: Old Problems and New Directions', 0.5, 'SEM', 'This course examines transnational and global historical processes, focusing on temporal and geographic scales of analysis outside of traditional national histories, and promotes linking the local and the global.  It looks at global forces influencing particular societies and encourages students to place themselves outside conventional local, regional, and national boundaries, and will critically consider a number of the metanarratives that have informed and continue to inform historiography, particularly idea such as modernity, progress, and the ongoing preoccupation with the ''rise of the west''.  Given these questions, and the almost endless scope of a course that purports to take the world as its focal point, weekly seminars will begin with a discussion of the possibilities offered by as well as the limits to transnational/global/world history, the various interpretative frameworks in use and their proponents as well as the challenges that transnational/global/world history poses.  We will then focus on particular case studies or themes so as to promote discussion that is as much historiographical as it is historical. Such themes/case studies may include:  feminism and imperialism, famine and climate change, disease and ecology, military technology and governmentally, global trade and the rise of consumer society(s), colonial knowledge and shifting ideas of race.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('INDEV 476', 'Contemporary Issues in Development Practice', 0.5, 'SEM', 'This course focuses on contemporary development issues - environment, gender, migration, climate change, democracy, to name several. It also highlights the ways and means for students to respond to one or more of these issues, particularly as they relate to their field placements.', 'INDEV');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('INDEV 490A', 'Honours Thesis: Project Preparation', 0.5, 'ESS', 'The Honours Thesis/Project provides students with an opportunity to pursue a specific research topic or applied project of their choosing relating to the study of International Development. The paper will be prepared over two semesters. 490A involves the guided preparation of a research/project proposal.', 'INDEV');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('INDEV 490B', 'Honours Thesis: Project Completion', 1, 'ESS', 'The Honours Thesis/Project provides students with an opportunity to pursue a specific research topic or applied project of their choosing relating to the study of International Development. The paper will be prepared over two semesters. 490B involves the formal completion of the thesis/project.', 'INDEV');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('INTEG 452A', 'Real World Problem Solving A', 0.5, 'LEC, PRJ, SEM, STU', 'The first part of a two-term senior project course. Students will work as a design group to address a real world problem over the course of the two-term sequence. With the instructor in an advisory role, students will meet with a real world client, typically from outside the university, to understand the client''s problem and needs. The focus of the first term will be a comprehensive design brief, background research and the presentation of one or more proposed solutions for the client''s approval.', 'INTEG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('INTEG 452B', 'Real world Problem Solving B', 0.5, 'LEC, PRJ, SEM, STU', 'The culmination of the two-term senior project sequence. Students will continue to develop solutions to meet the client''s needs as identified in the first term. The focus of the second term is to continue to prototype, refine, and ultimately implement the solution for the client. In addition to the completed solution, students will present a final complete project brief including an evaluation of the solution together with recommendations for further development.', 'INTEG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('JS 114', 'Jews and Jewishness', 0.5, 'LEC', 'This course examines the central elements that make up contemporary Jewish identity/identities, traditional and otherwise, with particular focus on the North American Jewish experience and the nature of Jewish modernity. It explores historical, literary, religious, and social issues that have contributed to the formation of these identities.', 'JS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('JS 131', 'Big Ideas of the Bible', 0.5, 'LEC', 'The Bible is the most translated and most read document in human history. This course explores central biblical ideas, from creation to the end of time, violence to redemption, and political intrigue to family ties, and examines their impact on Judaism, Christianity, and Western Culture.', 'JS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('JS 237', 'Insiders and Outsiders in the Bible', 0.5, 'LEC', 'This course explores the tension between insiders and outsiders in the Bible, examining the rationale for and implications of issues such as purity, holy war, the chosen people, and the Gentiles.', 'JS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('JS 338', 'Seeking Wisdom in the Bible', 0.5, 'LEC', 'This course explores the theme of wisdom in the Bible and related literature in the period from ancient Israel to the first century CE. It examines the varied cultural influences on biblical wisdom traditions.', 'JS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('LS 240', 'Terrorism', 0.5, 'LEC', 'This course offers an introduction to the study of terrorism, with a primary focus on sociological approaches. The course examines the history, causes, and diversity of forms of terrorist groups and the process of radicalization, suicide terrorism, and some aspects of the counter-terrorism response of states.', 'LS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('LS 425', 'Crossing Borders: Law and Global Deviance', 0.5, 'SEM', 'This seminar-style course, positioned at the intersection of sociology and law, examines illicit cross border activity such as terrorism, piracy, drugs, trafficking, and illegal immigration. Each cross-border activity will be examined, along with the way states respond to it politically and legally at a national and international level. Ethnographic research on the activity will provide for rich descriptions of how and why people participate in such activities.', 'LS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MATH 642', 'Introduction to Computer Science: A Mathematical Perspective', 0.5, 'LEC', 'The connections between mathematics and computer science are varied and deep.  This course will introduce students to foundational ideas in computer science and their relationship to foundational ideas in mathematics through the use of a functional programming language designed for education.  No prior experience with programming is required, though students with exposure to popular programming languages will also benefit from this alternate approach.', 'MATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MATH 643', 'Theory of Computation', 0.5, 'LEC', 'This course will use mathematics to study the foundations of problem solving and computation.  A decision problem (answering "yes" or "no") can be characterized as a set of strings of characters encoding inputs that yield "yes" answers, and a computer can be characterized as a simple mathematical machine model that processes strings of characters.  By studying properties of classes of problems of increasing complexity, we will be able to establish relationships among classes, membership in classes, and hard problems for classes.  The course will culminate in the examination of classes and models that correspond to the power of computers, and the demonstration that there exist problems that cannot be solved.', 'MATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MSCI 723', 'Big Data Analytics', 0.5, 'LEC, RDG', 'This course focuses on methods and algorithms for turning very large collections of data into actionable insight.  Topics may include data profiling, transformation and cleaning, data mining, data warehousing and cloud computing.  Applications will be drawn from various areas such as smart grid analytics and ubiquitous computing.  Students will read and present papers and complete a research project.  Priority may be given to Management Science students.  Previous studies in programming, algorithms, statistics and database management are necessary background topics for those taking this course.', 'MSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MSCI 724', 'Design and Analysis of Information Procurement Mechanisms', 0.5, 'LEC, RDG', 'This course covers analytical models and analysis for information procurement mechanisms.  This class leverages optimization, game theory, and probability theory.  Topics covered in this course include: mechanism design, scoring rules, prediction markets, and modeling feedback in markets.', 'MSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MSCI 731', 'Matrix-Analytic Methods in Stochastic Models', 0.5, 'LEC, RDG', 'This course introduces matrix-analytic methods and shows their applications in queuing theory, inventory theory, supply chain management, telecommunications networks, reliability, finance mathematics, and risk and insurance analysis.  In the first half of the course, the basic theory on phase-type distributions, Markovian arrival processes, and matrix-geometric solutions is introduced.  In the second half of the course, applications of matrix-analytic methods in stochastic modeling (queuing, reliability, inventory, supply chain, etc.) are examined.  Previous study of stochastic models is required, such as MSCI 631.', 'MSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MSCI 734', 'Network Models and Applications', 0.5, 'LEC, RDG', 'This course treats the broad subject of network models.  Network flow problems form a special class of linear programming that arise in a wide variety of applications including transportation, telecommunications, logistics and supply chain management. The purpose is to study the properties of network flow models, to discuss extensions and various solutions approaches, and to survey some applications.', 'MSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MSCI 744', 'Science and Technology Policy', 0.5, 'LEC, RDG', 'Science and Technology are increasingly viewed as important tools to promote the social, economic, and political goals of modern economies.  Accordingly, science and technology (S&T) policy issues have assumed prominence in contemporary policy discourse.  Firms are often operated under different policy environments and their strategies should be formulated under the various environments.  This course provides students with the background and tools needed to critically evaluate issues in S&T policy debates.  After reviewing the basics of science and technology policy, we will discuss a few instruments of S&T policy, e.g., intellectual property rights, tax and subsidy, public funding for research, and prizes.  Throughout the course, we will integrate the theoretical discussions with cases of contemporary S&T policy issues.  Previous study of economics is required, such as MSCI 607.', 'MSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('NE 111', 'Introduction to Programming for Engineers', 0.25, 'LEC, TST', 'Introduction to programming and numerical computing using a high-level interpreted programming language. Programming fundamentals, computer architecture, design and use of functions, strings and text input/output, relational operators, conditionals, lists, loops, designing algorithms, numerical computing, plotting, and file input/output.', 'NE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 155', 'Introduction to Drug Information Fundamentals', 0.25, 'LEC', 'This course provides students with basic skills in retrieving and assessing drug information and medical literature. The foundation acquired in this course will be used throughout the Integrated Patient Focused Care series in critically appraising literature to support an evidence-based patient care model. [Offered: S]', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 379', 'Ethical Decision-Making in Pharmacy Practice', 0.5, 'LEC', 'This course explores the principles of ethical decision-making in healthcare and applies them to various scenarios and actual cases in different pharmacy practice settings. Discussion will focus on ethical dilemmas where a variety of stakeholders and implications must be considered. [Offered: W, S, F]', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 425', 'Symposium', 0.5, 'LEC', 'Symposium offers senior students the opportunity to deliver a seminar in a content area of their choice. Students will demonstrate an ability to critically appraise literature from a variety of sources and synthesize information in both written and presentation formats. [Offered: W, S]', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 430', 'Clinical Rotation 1: Primary Care', 2.5, 'CLN', 'Students will provide supervised direct patient care in a primary care setting. Over the course of 24 weeks, students will be placed in three 8-week rotations where the student''s ability to apply therapeutic knowledge in a live practice setting will be refined and assessed. [Offered: W, S]', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 440', 'Clinical Rotation 2: Institutional', 2.5, 'CLN', 'Students will provide supervised direct patient care in an institutional setting. Over the course of 24 weeks, students will be placed in three 8-week rotations where the student''s ability to apply therapeutic knowledge in a live practice setting will be refined and assessed. [Offered: W, S]', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 450', 'Clinical Rotation 3: Elective', 2.5, 'CLN', 'Students will practice in a variety of Pharmacy settings under the supervision of qualified preceptors. Over the course of 24 weeks, students will be placed in three 8-week rotations where the student''s ability to apply therapeutic knowledge in a live practice setting will be refined and assessed. [Offered: W, S]', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 473', 'Advanced Infectious Disease', 0.5, 'LEC', 'This course is designed to expand upon students'' understanding of infectious disease pharmacotherapy, with a focus on syndromes requiring a higher degree of medical intervention than commonly seen in primary care. The aim of the course is to develop practicing pharmacists with the knowledge, skills and critical dispositions necessary for optimizing the use of anti-infectives in challenging infectious syndromes. [Offered: W, S, F]', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 474', 'Advanced Pharmacotherapeutics in the Hospital Setting', 0.5, 'LEC', 'This course will provide students the opportunity to apply clinical knowledge and skills to manage pharmacotherapy problems commonly encountered in hospitalized patients. The focus will be on disease states not covered in the Integrated Patient Focused Care Series. Additionally, problems unique to delivery of care in the hospital setting, such as formulary management, utilization review, and medication problems relating to transitions among care settings will be explored. [W, S, F]', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 475', 'Advanced Pharmacotherapeutics in the Ambulatory Care Setting', 0.5, 'LEC', 'This course will provide students the opportunity to apply clinical knowledge and skills to manage pharmacotherapy problems commonly encountered by pharmacists practicing in an outpatient environment. The focus will be on disease states not covered in the Integrated Patient Focused Care Series. Additionally, problems unique to delivery of care in the ambulatory setting, such as coordination of care within an interdisciplinary team, working within the constraints of the Ontario Drug Benefit (ODB) program and insurance plan formularies, and provision of best practice care with limited resources. [Offered: W, S, F]', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 476', 'Advanced Skills in Patient Engagement', 0.5, 'LEC', 'Research shows that patients actively involved in managing their health report better outcomes. This course is an innovative skills-based program that combines theory and practical techniques to help learners develop the ability to assess readiness, motivate, and support patients in improving their health. Relevant clinical tools will be introduced along with opportunities for hands-on practice. [W, S, F]', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 496', 'Advanced Professional Practice', 0.5, 'LAB, LEC', 'Update to the Professional Practice course series taken in the BSc Pharmacy curriculum. Focus on skills necessary to incorporate changes in the scope of pharmacy practice into a variety of practice settings. This is predominantly an online course with required attendance at a workshop component. [Offered: W, S, F]', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 497', 'Clinical Rotation 1: Direct Patient Care Fundamentals', 2.5, 'CLN', 'Advanced practice rotation in an approved clinical practice setting. Involves patient case workups; defence and demonstration of knowledge and application of patient care principles. Submission of a clinical experience portfolio. [Offered W, S, F]', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 498', 'Clinical Rotation 2: Direct Patient Care', 2.5, 'CLN', 'Advanced practice rotation (8 weeks) in an approved clinical practice setting (institutional or primary care, depending on prior experience assessed through PHARM 497). Provision of direct patient care in an inter-professional setting. 
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 499', 'Clinical Rotation 3: Elective', 2.5, 'CLN', 'Advanced practice rotation (8 weeks) in an approved clinical practice setting. Provision of direct patient care in an inter-professional setting distinct from previous experience assessed through the clinical experience portfolio and courses taken since completing the BSc Pharm. [Offered W, S, F]', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 612', 'Principles and Practices in Systemic Treatments for Cancer', 0.5, 'LEC', 'This course is designed to accommodate graduate students from a variety of backgrounds who wish to increase their understanding of cancer and its treatment.  It will cover the full range of issues involved in the rational use of anti-cancer agents, from the older cytotoxic drugs through to newer agents targeted at particular steps in cell regulatory pathways.  A broad overview of the pathology of cancer is included.  Although drug mechanisms will be addressed, there is an equivalent emphasis on what these drugs mean to the patient.  Students ill consider the disease itself, the behavior of the diagnosed patient, the issues around drug administration, the extensive side effects and the various supportive strategies that are required to take the patient through the very stressful course of systemic cancer therapy.', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 613', 'Principles and Practices in Systemic Treatments for Cancer', 0.5, 'LEC', 'This course is designed to accommodate graduate students from a variety of backgrounds who wish to increase their understanding of cancer and its treatment.  It will cover the full range of issues involved in the rational use of anti-cancer agents, from the older cytotoxic drugs through to newer agents targeted at particular steps in cell regulatory pathways.  A broad overview of the pathology of cancer is included.  Although drug mechanisms will be addressed, there is an equivalent emphasis on what these drugs mean to the patient.  Students will consider the disease itself, the behavior of the diagnosed patient, the issues around drug administration, the extensive side effects and the various supportive strategies that are required to take the patient through the very stressful course of systemic cancer therapy.', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 600', 'Seminar in Cognitive Science', 0.5, 'SEM', 'Cognitive Science is the interdisciplinary study of mind and intelligence. This seminar will discuss controversial topics concerning the nature of human and computer intelligence', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 650', 'Perimeter Scholars International Explorations in Quantum Gravity', 0.25, 'LEC, TUT', 'This course will introduce the students to the basic techniques and results of the field of loop quantum gravity.  Possible topics include: 1) The Ashtekar variables and the Hamiltonian formalism; 2) The Plebanski action for general relativity; 3) The Hilbert space of loop quantum gravity and basic operators such as the area and volume operators; 4) Spin foam models; 5) Group field theory; 6) Quantum black hole horizons.', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PS 625', 'Economics and Public Policy I', 0.5, 'SEM', 'This course teaches students fundamental principles of microeconomics with an emphasis on cost-benefit analysis.  Topics covered may include demand and supply, pareto-efficiency, surplus and deadweight loss, discounting, time-stream evaluation and investment criteria, the measurement of welfare change, shadow prices, and the valuation of intangibles.  In order to ensure policy relevance, Treasury Board of Canada guidelines are used as a template for cost-benefit analysis techniques.', 'PS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PS 626', 'Economics and Public Policy II', 0.5, 'SEM', 'This course focuses on the rationale for government intervention in a market economy.  The course begins with a consideration of market successes through the analysis of the first and second theorems of welfare economics.  The course then considers market failures through an analysis of distributional issues, public goods, externalities, non-competitive market structures, and asymmetric information.  The role and degree of government intervention in health care, education, social assistance, employment insurance, and pension plans is also discussed.', 'PS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PSCI 497A', 'Study Abroad Experience', 0.25, 'PRA', 'While participating in a study abroad experience, students complete reflections on their experience and what they are learning.', 'PSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PSCI 497B', 'Study Abroad Experience', 0.25, 'SEM', 'After completing a study abroad experience, students complete a project that integrates what they learned while studying abroad with contemporary politics, policy, and governance. Students also participate in a seminar in which they complete assignments and share their experiences with other students.', 'PSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PSCI 498A', 'Current Issues in Political Science', 0.5, 'SEM', 'Students in this course attend talks or conferences, monitor the media, and do independent research to investigate how a broad concept in political science plays out in contemporary politics, policy, and governance. Students also participate in a seminar in which they complete assignments and share their experiences with other students.', 'PSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PSCI 498B', 'Research Apprenticeship Experience', 0.5, 'PRA, SEM', 'Research apprenticeship in which students assist a professor with a research project. Students also participate in a seminar in which they complete assignments, share their experiences with other students, and complete a final project related to their research apprenticeship.', 'PSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PSCI 498C', 'Civic Engagement Experience', 0.5, 'PRA, SEM', 'Students participate in a civic engagement experience and reflect on how concepts in political science help to illuminate it. Students also participate in a seminar in which they complete guided reflections, share their experiences with other students, and complete a final project related to their civic engagement experience.', 'PSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PSYCH 316', 'Pragmatic Language Development', 0.5, 'LEC', 'This course is an in-depth introduction to pragmatic language development with a focus on children''s conversational and narrative ability. Typical and atypical pragmatic language development is examined from a longitudinal, social-cognitive, and multi-cultural perspective and with respect to longer-term outcomes such as social competence, friendship, and success in school.', 'PSYCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PSYCH 878', 'Job Performance', 0.5, 'SEM', 'The primary focus of this course will be job performance and related issues.  Readings will cover job analysis, performance appraisal, and the "criterion problem" in I/O psychology.  The definition, measurement, and operationalization of job performance will be emphasized.', 'PSYCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PSYCH 879', 'Personnel Selection', 0.5, 'SEM', 'The primary focus of this course will be personnel selection.  Readings will focus on constructs used in personnel selection, methods used in personnel selection, and issues related to fairness and bias in personnel selection.  The importance of measurement of psychological constructs will be emphasized.  A working knowledge of correlation and regression is required.', 'PSYCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('RS 114', 'Jews and Jewishness', 0.5, 'LEC', 'This course examines the central elements that make up contemporary Jewish identity/identities, traditional and otherwise, with particular focus on the North American Jewish experience and the nature of Jewish modernity. It explores historical, literary, religious, and social issues that have contributed to the formation of these identities.', 'RS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SI 230R', 'Islamic Visual Culture: Art, Architecture, and Aesthetics', 0.5, 'LEC', 'This course explores Islamic visual culture by focusing on three main design elements in Islamic art and architecture: calligraphy, arabesque, and geometry. The course emphasizes the aesthetic, visual, and cultural importance of ornamentation in Islamic art and examines its manifestation in the work of contemporary Muslim artists.', 'SI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SI 231R', 'Calligraphy to Conceptual Art: Text as an Image in Islamic and East Asian Visual Arts', 0.5, 'LEC', 'Exploring the history of Islamic and East Asian calligraphic arts and the study of Eastern and Western artists, this course examines multiple ways in which writing and other forms of visible language are incorporated into visual arts. Through studio projects, students explore conceptual approaches to text-based image making.', 'SI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 240', 'Terrorism', 0.5, 'LEC', 'This course offers an introduction to the study of terrorism, with a primary focus on sociological approaches. The course examines the history, causes, and diversity of forms of terrorist groups and the process of radicalization, suicide terrorism, and some aspects of the counter-terrorism response of states.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 270', 'International Migration', 0.5, 'LEC', 'This course examines different international migration flows, including irregular immigration, refugees and asylum seekers, and low- and high-skilled labourers. Drawing on migration theories, it critically examines why people move, how states respond to different flows, and how migration controls intersect with gender, race, class, and nationality.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 310', 'Social Networks', 0.5, 'LEC', 'A survey of applications of the concept of the network in studying social structures. Examples will be drawn from diverse areas, such as interpersonal relations, community studies, social support, interorganizational relations, elites, deviant groups, etc.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 425', 'Crossing Borders: Law and Global Deviance', 0.5, 'SEM', 'This seminar-style course, positioned at the intersection of sociology and law, examines illicit cross border activity such as terrorism, piracy, drugs, trafficking, and illegal immigration. Each cross-border activity will be examined, along with the way states respond to it politically and legally at a national and international level. Ethnographic research on the activity will provide for rich descriptions of how and why people participate in such activities.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 784', 'International Migration:  Practice, Theory & Regulation', 0.5, 'LEC, SEM', 'This course explores theoretical perspectives on migration and critically examines how states deter or facilitate migration flows, including irregular immigration, refugees and asylum seekers, and low and high-skilled labourers.  A multidisciplinary approach allows students to investigate the ubiquitous rise of border controls as a state tool to control migration, and how their implementation intersects with gender, race, class and nationality.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SPAN 401', 'Spanish in Motion', 0.5, 'LEC, TUT', 'This course will further develop effective communication strategies at an advanced level through a wide variety of activities. Students will be able to develop arguments in diverse contexts and formats.', 'SPAN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SWK 672R', 'International Context of Practice (Mexico); Experimental Learning', 0.5, 'LEC, SEM', 'This course provides a framework for understanding a developing country through a comparative study with Canada. The collective responses of citizens in meeting community needs in the context of colonial legacies and contemporary global influences are highlighted in both settings.  The international learning experience usually takes place during Reading Week and is complemented by two days of pre-trip learning focusing on Canadian realities and on post-trip day to reflect upon the comparative analysis.  (Note: this is international experience with an on campus component)', 'SWK');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SWK 690R', 'Special Topics in Social Work', 0.5, 'LEC, RDG, SEM, TUT', 'None', 'SWK');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 208G', 'Gothic Monsters', 0.5, 'LEC', 'A study of monstrosity, fear, terror, and horror in the gothic mode from its origins to the present, with attention to the ways various genres (from the novel to new media) represent gothic sexualities, genders, politics, and aesthetics.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 601', 'Foundations of Biology in Engineering', 0.5, 'LEC', 'This biomedical engineering core course focuses on equipping students with the foundational knowledge in human biology through a problem-solving oriented treatment of biological phenomena at both physiological and cellular levels.  The major aim of this course is to develop students'' literacy in biology and to show them how various biological phenomena can be analytically explained and justified with numbers.  It is also intended to illustrate how biology knowledge can be integrated with engineering principles to address real-world diagnostic needs.  Case descriptions would be included of medical devices that involved discipline-specific knowledge in electrical and computer engineering.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EASIA 204R', 'Korean Culture and Society', 0.5, 'LEC', 'The course explores the interface of tradition and modernity in Korean life. Topics include traditional and modern Korean culture, language, education, art, technology, and business.', 'EASIA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AHS 100', 'Foundations of a Healthy Lifestyle', 0.5, 'LEC', 'This course will introduce the student to the basics of health, wellness and disease, with an emphasis on assessing and evaluating the student''s own lifestyle and health risk, and committing to a plan to begin or maintain a healthy lifestyle across the life course.', 'AHS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AHS 600', 'Foundations of Qualitative Research Methodologies', 0.5, 'SEM', 'This workshop oriented course provides a foundational overview of various qualitative research processes beginning with philosophical underpinnings, and continuing through theoretical perspectives, methodologies, methods, analysis, and write-up. Specifically, ontological and epistemological approaches of prediction, understanding, emancipation, and deconstruction will be introduced and discussed as students immerse themselves in rigorous fieldwork in relation to current qualitative trends in health and well-being.', 'AHS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AMATH 383', 'Introduction to Mathematical Biology', 0.5, 'LEC', 'An introduction to the mathematical modelling of biological processes, with emphasis on population biology. Topics include ecology, epidemiology, microbiology, and physiology. Techniques include difference equations, ordinary differential equations, partial differential equations, stability analysis, phase plane analysis, travelling wave solutions, mathematical software. Includes collaborative projects and computer labs.', 'AMATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AMATH 877', 'Foundations of Quantum Theory', 0.5, 'LEC', 'Review mathematical formulation of operational quantum theory; theory of measurements and decoherence; quantum-classical contrast; review of historical perspectives on interpretation, including EPR paradox; Bell''s theorem, non-locality and contextuality; PBR theorem; selected topics including overviews of current interpretations of quantum mechanics and critical experiments in quantum foundations.', 'AMATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ANTH 272', 'Issues in Contemporary Indigenous Communities in Canada', 0.5, 'LEC', 'An examination of First Nations and M√©tis cultures and cultural development from the perspective of local Indigenous communities. The course will feature lectures, discussions, and occasional guest speakers representative of the wider Indigenous community.', 'ANTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EASIA 291R', 'Special Topics in East Asian Studies', 0.5, 'LEC', 'This course deals with selected topics in East Asian studies.', 'EASIA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 126', 'Environmental Building Design', 0.5, 'LEC', 'An introduction to environmental design practices leading to low carbon design. Topics of discussion include passive heating and cooling, solar geometry, climate and meteorological influences, microclimate, site design, daylighting, active systems, embodied energy, sustainable rating systems, sustainable design philosophies such as cradle to cradle, biomimicry and design for disassembly. Energy-related issues will be addressed and energy-based software design programs will be introduced. Understanding the role of design in an energy efficient/passive solar building will be a central learning outcome.', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 225', 'Theory and Design of the Contemporary Landscape', 0.5, 'LEC', 'This course provides an historical overview of the ideas of nature and landscape in Western thought. ''Nature'', ''Ecology'', and ''Landscape'' are treated as cultural constructs, related to specific philosophical, technological, economic, political, and social issues. Many of these issues will be considered as the course of study traces the evolution and transformation of contemporary landscape. [Offered: S]', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 248', 'Enlightenment, Romanticism and the 19th Century', 1, 'LEC, TUT', 'This course investigates the cultural and architectural history of the scientific, agricultural, industrial and social revolutions. Depiction of early modern culture and those of the Enlightenment and the long nineteenth century as one in which the notion of environmental order as the fulfilling of natural law is replaced by a notion of order as the historical creation of autonomous will. Analysis of the art and architecture from this era with emphasis on the Baroque, Neo-Classicism, Historicism, and the Industrial Revolution. Selected works in philosophy, literature, art and architecture will be studied. [Offered: S]', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 263', 'Integrated Environmental Systems', 0.5, 'LEC', 'This course is focused on the integrated environmental systems of buildings with an aim to develop the knowledge and skills appropriate to architectural practice. Subjects covered include environmental parameters, air and water systems, heating and cooling loads, energy conservation, ventilating and air conditioning systems, plumbing and waste systems, artificial source lighting and daylighting, acoustics, and fire protection criteria and systems, with reference to building codes and standards. [Offered: F]', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 264', 'Building Science', 0.5, 'LEC', 'The physio-technical factors that influence building design for performance: durability, efficiency, health and sustainability will be explored. Common building design construction problems, their causes and solutions, will be examined with the aid of case studies. Using the principles of building science, good details of masonry, wood, steel and glass will be developed.[Offered: W]', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EASIA 202R', 'Chinese Culture and Society', 0.5, 'LEC', 'The course explores the interface of tradition and modernity in Chinese life. Topics include traditional and modern Chinese culture, language, education, art, technology, and business.', 'EASIA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 428', 'Rome and the Campagna (Rome)', 0.5, 'LEC', 'History of settlement and building in Rome and the surrounding area from antiquity to the present. Acts of design in architecture, urban form and landscape related to political, cultural and spiritual authority of Rome. Comparison drawn between the image of the city, represented in literature and art, and the material facts of the place. Field trips, lecture.', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 429', 'Global Cities', 0.5, 'SEM', 'A global cities course enabling students to travel abroad and study first hand the architecture and urbanism of cities across Europe, Asia and South America. These course offerings are thematically based and involve field trips to specific architectural sites, cities, and regions around the world.', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('STAT 438', 'Advanced Methods in Biostatistics', 0.5, 'LEC', 'Causal inference methodologies including propensity score matching and inverse probability weighting. Methods for handling incomplete data and covariate measurement error; likelihood based on joint models, estimating functions.', 'STAT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 465', 'Advanced Structures: Design and Analysis', 0.5, 'LEC, TUT', 'This course builds on the knowledge acquired in the previous structural design courses, to convey practical experience in the application of skills to a specific structure. Students will perform an analysis of a full-scale prototype or structural case-study. Course requirements include the production of a comprehensive structural analysis report that demonstrate students'' mastery of the principles of structural behaviour, analysis and design. In this course, students will also be introduced to other types of advanced structural systems such as tensile structures, shells, and tensegrity systems. [Offered: F]', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 510', 'Special Topics in Visual and Digital Media', 0.5, 'SEM', 'A range of special topics within visual and digital media that include course offerings in parametric design and scripting, advanced visualization, interaction design, and digital fabrication. [Offered: F,W,S]', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 520', 'Special Topics in Urbanism and Landscape', 0.5, 'SEM', 'A range of special topics courses within urbanism and landscape. [Offered: F,W,S]', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 540', 'Special Topics in Architectural History and Theory', 0.5, 'SEM', 'A range of special topics courses within cultural and architectural history, theory and criticism. [Offered: F,W,S]', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 570', 'Special Topics in Building Technology and Environmental', 0.5, 'SEM', 'A range of special topics courses within building technologies, structures, materials and methods and environmental systems including coursework in alternative energy systems, ecological design and design build. [Offered: F,W,S]', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 610', 'Architectural Research and Analysis', 0.5, 'LEC', 'This course exposes students to research methodologies and diagrammatic strategies used for the analysis of works of architecture, approaches to design and key texts. These will act as case studies to learn about the conceptual ideas, formal/spatial principles, material systems and operative design strategies employed in the making of architecture while introducing students to analytical methods and processes considered integral to advanced research and design.  The rigorous consideration of precedent and design research methods fosters a deeper connection of students'' work with the discipline, better enabling them to position their ideas within a larger conceptual discourse while giving them the methodological tools to prepare them for the undertaking of a research and design thesis.', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 629', 'Global Cities', 0.5, 'SEM', 'An elective offering that enables students to travel abroad and study firsthand the architecture and urbanism of cities across Europe, Asia and South America. Sections are thematically based and include field trips to specific architectural sites, cities and regions around the world. Additional travel costs associated with field trips to be determined based on location.', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 640', 'Contemporary Theory, Culture and Criticism', 0.5, 'LEC', 'Against the backdrop of the larger interdisciplinary field known as cultural studies, this course will trace and examine critical bodies of theory that have influenced the development of contemporary architectural thought and practice.  This course will emphasize and study important writings and theoretical systems that have emerged in various fields including philosophy, art theory/criticism, literary theory, psychoanalysis, the natural and social sciences, and cultural studies, to investigate the ways in which these have generated conceptual tools within architectural theory, criticism and analysis while contributing to the evolution of architectural practice. 
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 662', 'Steel & Concrete: Design, Structure and Construction', 0.5, 'LEC, TUT', 'Architectural case studies are used to examine conceptual development, structural design, building process and the selection of structural steel and concrete systems. Topics such as tension, flexural and compression members; and connections are studies using calculations, design aids, rules of thumb and the latest CSA design standards. Heldwith ARCH 362- graduate students will be required to complete additional coursework for graduate level course credit.', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 690', 'Design Studio', 1.5, 'STU', 'The Design Studio is subdivided into various sections, providing students the opportunity to select an Architectural studio of their interest and undertake a detailed exploration of design project within this theme. Studio fee $25.00.', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 691', 'Design Studio - Comprehensive Building Design', 1.5, 'STU', 'Through the integration and application of skills and knowledge to a complex building project, students will develop designs to a high level of detail. A concern for technical material, environmental and legal aspects of Architecture will support open speculation and innovative design. Studio fee $25.00.', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 693', 'Thesis Research & Design Studio II', 1.5, 'STU', 'Graduate Thesis Research & Design Studio II guides students through the development of their thesis projects. Students may draw from a wide range of sources both within and beyond the conventional boundaries of the architectural discipline, using methods of exploration that mat employ the tools of architectural production and critique, and that culminate in a written research document and/or design proposal. The goal of the Graduate Thesis is to make an active contribution to the field by opening up new ways of understanding an existing architectural topic. Studio fee $ 25.00.', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AVIA 374', 'Special Topics in Aviation', 0.5, 'LEC, SEM, TUT', 'A course offered in a particular branch of aviation, subject to availability of instructor.', 'AVIA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BET 411', 'Capstone Entrepreneurship Planning and Execution', 0.5, 'LEC', 'This course taken concurrently with a non-engineering capstone project, consists of workshops, seminars, and online content. Topics include goal setting and project planning, team building, understanding your competitive landscape and customers, patents and patent searches, commercialization options and developing a business model, legal issues, creating a business case for the capstone project, presentation skills and "pitching", post-mortem and root cause analysis.', 'BET');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BET 412', 'Advanced Topics in Entrepreneurship', 0.5, 'PRJ', 'This course will be taken by students in the corporate entrepreneurship track of the Entrepreneurship Minor, and approved by the program co-ordinator in order to meet the experiential component requirements in the minor with special projects as assigned.', 'BET');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BET 420', 'Entrepreneurship for Social Impact', 0.5, 'LEC', 'This course explores how innovative approaches can address social problems, create organizations that make positive change, have significant social impact, and be sustainable and economically viable. Topics include the principles of social entrepreneurship, design thinking applied to identifying significant problems, innovative solutions for social impact, and identifying opportunities for social change. Students develop skills and tools for refining and implementing their ideas as well as measuring the success and value of mission-driven organizations. Through case studies, lectures, and workshops, students also develop skills in strategic thinking with a socially conscious business mindset to create high-impact ventures. [Offered: F]', 'BET');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 266', 'Introduction to Computational Biology', 0.5, 'LAB, LEC', 'This course provides an introduction to computational methods in biology, including exploration of bioinformatics databases and tools, analysis of protein and DNA sequences, and computer-aided modelling of biological systems. Labs will include practical experience applying computational tools to biological problems.', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 341', 'Fundamentals of Immunology', 0.5, 'LAB, LEC', 'This course will provide students with an understanding of the fundamental concepts of the vertebrate immune system. Topics will focus on the components of the immune system, innate and adaptive immunity, immune cell development, communication, and recognition of non-self leading to an
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EASIA 360R', 'Pre-modern Chinese Literature', 0.5, 'LEC', 'This course is an introductory survey of the main genres of classical and pre-modern Chinese literature. Individual works are related to the evolution of the literary tradition and its interaction with history, philosophy, and politics. Classical and historical writing, poetry, drama, and novels are introduced.', 'EASIA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BIOL 469', 'Genomics', 0.5, 'LAB, LEC', 'An applied course on the computational exploration of genomes. Topics include comparative and evolutionary genomics, metagenomics, and genomics applications in health/medicine. The lab will involve analysis of genomic datasets using scripting and bioinformatic software.', 'BIOL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BME 252', 'Linear Signals and Systems', 0.5, 'LEC, TUT', 'Models and analysis of linear systems in the context of measurement and processing of biosignals such as electroencephalography (EEG), electrocardiography (ECG), and electromyography. Discrete and continuous time systems, difference and differential equations, impulse and frequency response, transform domain techniques, transfer functions and frequency response, frequency domain analysis of linear systems, sampling theory, stability, and linear filters. [Offered: S]', 'BME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BME 353', 'Control Systems', 0.5, 'LEC, TUT', 'Classical and state space representations of control systems. Stability, controllability, observability and sensitivity. Routh-Hurwitz and root-locus methods. Frequency domain behaviour, Bode plots, Nyquist stability criteria. Pole placement, PID, phase-lead and phase-lag controllers. Application to anatomical system models, including musculoskeletal and cardiovascular systems, and to physiological feedback systems. [Offered: W, first offered Winter 2017]', 'BME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BME 353L', 'Control Systems Laboratory', 0.25, 'LAB', 'Laboratory experiments for students taking BME 353. [Offered: W, first offered Winter 2017]', 'BME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BME 355', 'Anatomical Systems Modelling', 0.5, 'LEC, TUT', 'Introduction to systems theory as a general modeling method, and applied to the skeletal, neuromuscular, central nervous, cardiovascular, and respiratory systems of the human body. Time-domain simulations, sensitivity analyses, and parameter identification, with the latter driven by experimental measurements of system performance. [Offered: W]', 'BME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BME 356', 'Control Systems', 0.5, 'LEC, TUT', 'Classical and state space representations of control systems. Stability, controllability, observability, and sensitivity. Routh-Hurwitz and root-locus methods. Frequency domain behaviour, Bode plots, Nyquist stability criteria. Pole placement, PID, phase-lead and phase-lag controllers. Application to anatomical system models, including musculoskeletal and cardiovascular systems, and to physiological feedback systems. [Offered: F]', 'BME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BME 386', 'The Physics of Medical Imaging', 0.5, 'LEC, TUT', 'The fundamental laws of electricity, magnetism, and optics will be taught through the introduction to basic concepts of medical imaging: radiation for imaging, x-ray, computed tomography (CT), magnetic resonance imaging (MRI), ultrasound or sonography imaging, electric impedance tomography, confocal microscopy, fluoroscopy. Radionuclide imaging: Single photon emission computed tomography (SPECT), and positron emission computed tomography (PET). Emerging technologies: elastography, tHz imaging, molecular imaging will also be discussed. [Offered: F]', 'BME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BME 392', 'Circuits, Instrumentation, and Measurements', 0.5, 'LEC, TUT', 'Basic concepts of hardware measurement systems pertaining to the measurement of biosignals: active and passive circuit elements, Kirchhoff''s laws, mesh and nodal circuit analysis, principle of superposition, step responses of first and second order networks, sinusoidal steady state analysis, input-output relationships, transfer functions and frequency response of linear systems, operational amplifiers, analog signal detection, conditioning and conversion systems, transducers, difference and instrumentation amplifiers, A/D and D/A conversion. Examples will be presented in the form of physiological monitoring hardware for vital measurements such as electroencephalography (EEG), electrocardiography (ECG), and electromyography (EMG). [Offered: F]', 'BME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BME 392L', 'Circuits, Instrumentation, and Measurements Laboratory', 0.25, 'LAB', 'Laboratory experiments for students taking BME 392. [Offered: F]', 'BME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BME 393', 'Digital Systems', 0.5, 'LEC, TUT', 'Digital technology, combinatorial logic, binary arithmetic, sequential circuits, digital design, and microcontrollers. Topics will be reinforced in the context of biomedical microcontrollers and sensors used in physiological monitoring and clinical support systems. [Offered: W]', 'BME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHE 314', 'Chemical Reaction Engineering', 0.5, 'LEC, TUT', 'Review of stoichiometry and chemical kinetics. Homogeneous reactors: isothermal operation; batch; semi-batch; continuous tank; plug flow reactor design. Continuous stirred-tank reactors (CSTRs) in series; plug flow reactor with recycle. Multiple reactions in reactor networks. Temperature effects in adiabatic and non-isothermal reactors. Yield, selectivity and optimal operation of reactors. Heterogeneous catalysis and effectiveness factors in two-phase reactors. [Offered: W, S]', 'CHE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHE 361', 'Bioprocess Engineering', 0.5, 'LEC, TUT', 'Review of elementary aspects of microbiology, biochemistry, molecular biology, and genetic engineering. Introduction of biological systems for the production of commercial goods and services, e.g., foods, pharmaceuticals, chemicals, fuels, diagnostics, waste treatment, and biomaterials. Introduction to design of bioprocess systems, including biosafety and sustainability. Development of reaction kinetics associated with biological systems. Quantification of metabolism. Development of material balances for key constituents in bioreactors operated in different modes, e.g., batch, fed-batch, continuous stirred-tank reactor (CSTR), perfusion, recycle. Introduction to mass and heat transfer considerations for bioreactors. Dynamic simulation of cultures defined by ordinary differential equations. Introduction of downstream processes associated with biological systems and recovery of biological products. [Offered: F, W]', 'CHE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHE 425', 'Strategies for Process Improvement and Product Development', 0.5, 'LEC, TUT', 'A course in practical statistics at a level one step beyond an elementary course. Material includes regression analysis for linear and nonlinear models, analysis of variance, statistical inference, single and multiple comparisons, and an introduction to the design of experiments including single factor designs, multifactor designs, response surface methods, d-optimality (with empirical and mechanistic models), and the analysis of undesigned data. Applications to process improvement, product development and research problems will be explored. Use of statistical analysis software to apply these techniques. [Offered: F, W]', 'CHE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PD 12', 'Reflection and Learning in the Workplace', 0.5, 'LEC', 'This course strengthens self-awareness and goal-setting in the workplace and, by extension, in academic and personal life. Students set, track, and meet goals. They assess their own progress and performance. Students learn to adapt to changing situations, to take initiative and responsibility, and to effectively accept assignments. This course helps students transfer both classroom and workplace learning to new contexts.', 'PD');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 101', 'Introduction to Biochemical Sciences', 0, 'SEM', 'The Biochemist in society: Lectures, workshops, or seminars focusing on academic and professional development [Offered: F]', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 201', 'Environmental Impact and Management of Resources 1', 0.5, 'LEC', 'Introduction to the management of resources used in the production, conversion and disposal/recycling of chemicals. Biobased versus fossil resources. Introduction to the life cycle analysis of a material. Principles of green chemistry. [Offered: F; Based in Bordeaux; online only]', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 239', 'Introduction to Biological Systems', 0.5, 'LEC', 'Introduction to the biology and biochemistry of a selection of plants and microorganisms, with emphasis on the chemical components of living matter (nucleic acids, amino acids, carbohydrates, lipids, DNA, RNA, peptides, proteins) and the synthesis and breakdown of complex molecules in plants and microorganisms. [Offered: W; online only]', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 301', 'Environmental Impact and Management of Resources 2', 0.5, 'LEC', 'Indices and methods for quantifying the environmental impact of chemical and biochemical processes. Life cycle analysis of processes, starting from the extraction of the required raw materials through to the disposal or recycling of the products. Introduction to regulatory policies. [Offered: W; Based in Bordeaux; online only]', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 302', 'Innovation and Project Management', 0.5, 'LEC', 'Examples drawn from the chemical industry are used to expose students to a methodology for developing an innovative idea into a well-defined working plan. Students will use the methodology to develop ideas for their fourth-year projects. [Offered: F; online only]', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 339', 'Methods and Tools for Biosyntheses', 0.5, 'LEC', 'Biochemical reactions involved in respiration, fermentation, cell metabolism. Enzymatic reactions and their use in major industrial processes for transforming or producing chemicals. Biodigesters and bioreactors. Critical analysis of various processes using energy and mass balances. [Offered: F; online only]', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 363', 'Organic Process Chemistry', 0.5, 'LEC', 'This course is an introduction to the important aspects of Process Chemistry for the industrial production of pharmaceutically active organic compounds. Topics covered include: industrial organic synthesis and process design, scaling to kilogram quantities, green chemistry principles, process safety and hazard identification, industrial separation and purification, and meeting quality guidelines and regulations. [Offered: F or W]', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PACS 335', 'Perspectives in Music and Peace', 0.5, 'SEM', 'This course examines music''s role in peace and conflict from a variety of perspectives through listening, reading, discussion, and using examples from around the world. Topics include the use of music in dissent and resistance, and as an instrument of propaganda, conflict, and peacebuilding.', 'PACS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 479', 'Preparation of Biobased Compounds and Materials', 0.5, 'LEC', 'Catalysis and biocatalysis. "Green solvent" and solvent-free reactions. Biobased synthesis of polymers and amphiphiles. Bioreactions in chemical synthesis. [Offered: F; online only]', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 491B', 'Biobased Chemistry Research Project 2', 0.5, 'PRJ', 'Students work individually on extending the scope of the team projects started in CHEM 491A. [Offered: W]', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 100', 'Civil Engineering Concepts', 0.8, 'LAB, LEC, TST, TUT', 'An introduction to the fundamental methods, principles and skills of civil engineering. Fundamentals of technical communication, the engineering design process and problem solving. Completion of a pre-design study and report for a civil engineering project. Independent and team work. Fundamentals of engineering computation: units, data collection, measurement, and error analysis. Field surveying (automatic level, engineer''s transit, differential Global Positioning System (GPS), total station). Laboratory on engineering graphics auto-computer assisted diagnosis (AutoCAD) and computational software (Excel, Matlab). Aspects of the engineering profession (code of ethics, negligence, misconduct, role of the Professional Engineers Ontario (PEO), etc.), diversity in the workplace, and professional development. Preparation for the University of Waterloo co-operative education program (Co-operative Education and Career Action (CECA), r√©sum√© writing, job search and interview skills). [Offered: F]', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 104', 'Mechanics 1', 0.25, 'LEC, TST, TUT', 'Newtonian mechanics. Force systems; vectors, forces and moments; equilibrium and free body diagrams. Mechanics of simple structures. [Offered: F]', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 105', 'Mechanics 2', 0.75, 'LEC, TST, TUT', 'Distributed forces, centroids and moment of inertia. Introduction to foundations and fluid statics. Basic structural analysis. Plane trusses. Beam diagrams. Stress-strain-temperature relationships. Behaviour of members in tension, compression and bending. Thin-walled pressure vessels. Friction. [Offered: W,S]', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 115', 'Linear Algebra', 0.25, 'LEC, TUT', 'Linear systems of equations, matrices and determinants. Introduction to the eigenvalue problem. Applications. [Offered: F]', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 241', 'Transport Principles and Applications', 0.5, 'LEC, TST, TUT', 'Application of scientific principles to the planning, design, maintenance and management of transportation systems. The basic principles of transportation engineering for contemporary urban transportation modes; auto, transit, cycling and walking, and intercity modes; rail and air. Transportation economics, environmental impacts, and demand estimation. [Offered: F]', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 310', 'Introduction to Structural Design', 0.75, 'LEC, TST, TUT', 'Introduction to structural systems. Systems for carrying gravity and lateral loads in buildings. Structural design concepts. Analysis and design of concrete beams, and one-way slabs. Design of steel beams and tension members. Comparison of steel and concrete framing systems. [Offered: F]', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 341', 'Transportation Engineering Applications', 0.5, 'LEC, TST, TUT', 'Traffic engineering and travel forecasting. Evaluation, design and management of urban transport systems through advanced traffic control techniques. Quantitative methods for evaluating investments in transportation infrastructure or operational changes. [Offered: W]', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 382', 'Hydrology and Open Channel Flow', 0.5, 'LAB, LEC, TST, TUT', 'Introduction to the water cycle, flood frequency analysis, design storms. Analysis of hydrographs and rainfall-runoff response mechanisms in urban and natural systems. Mass continuity and water budgets at the watershed scale. Impact of land use change on hydrologic response. Quantification of open channel flow; subcritical and supercritical flow regimes. Dynamic forces on submerged structures and low/scour beneath bridges. [Offered as: CIVE 382 (W), ENVE 382 (F,W)]', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 392', 'Economics and Life Cycle Cost Analysis', 0.5, 'LEC, TST, TUT', 'Project financing, life-cycle cost analysis, time value of money, sensitivity analyses, tax, financial implications of infrastructure projects, quantitative decision-making, financial aspects of a business plan. [Offered: S]', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 484', 'Physical Infrastructure Planning', 0.5, 'LEC, TUT', 'The need for infrastructure and environmental assessments; the impacts of infrastructure on urban form; core infrastructure concepts; economics of infrastructure costs, finance and pricing. Infrastructure evaluation and management methods.', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 505', 'Structural Dynamics', 0.5, 'LEC, TUT', 'Dynamics of discrete and continuous structures. Free and forced vibrations of single and multi-degree of freedom systems. Shock loads, earthquake loads, response spectra. Analysis and design of frames for shock and earthquake loads. [Offered: W]', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CIVE 230', 'Engineering and Sustainable Development', 0.5, 'LEC, TST, TUT', 'This course introduces the concept of sustainability and how it applies to decision-making in civil engineering. The course begins by defining sustainability, both practically and technically, and describing the concepts of systems, and systems interactions. Quantitative methods and measures of effectiveness are derived and applied to components of sustainability; air quality, water quality, energy, transportation and solid waste. Economic concepts and their applicability to sustainability are described for both developed and developing countries. [Offered: S]', 'CIVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 634', 'Security and Privacy in Health Systems', 0.5, 'LEC', 'An overview of basic security and privacy principles relevant in the design and use of applications in health settings.  Program security, operating system security, network security, data security, and issues related to the management of security and privacy policies are introduced.  Master of Health Informatics students only.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 636', 'Introduction to Computer Networks and Distributed Computer Systems', 0.5, 'LEC', 'An introduction to computer networks, Internet protocols, and distributed systems.  Layered models, naming, addressing and routing, reliable communication, security, and elements of distributed system design. Master of Health Informatics students only.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 638', 'Principles of Data Management and Use', 0.5, 'LEC', 'A user-oriented approach to the management of large collections of data.  Relational database technology, relational algebra, SQL, database views, transactions, data modelling methodology, entity-relationship models.  Introduction to several current topics in database research, such as warehousing, data mining, managing data streams, data cleaning, data integration, and distributed/parallel databases.  Master of Health Informatics students only.', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DAC 203', 'Designing with Digital Sound', 0.5, 'LEC', 'In this course students will be introduced to sound analysis and production. Students will learn to record, edit, and implement sound in a variety of linear and non-linear media forms, with emphasis on film and video games.', 'DAC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DRAMA 100', 'Introduction to Theatre', 0.5, 'WSP', 'This course introduces students to the processes of text-based theatre creation and production. Students produce an existing play text by developing and implementing an original conceptual approach. Prior experience in theatre-making is not required.', 'DRAMA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DRAMA 206', 'Production Participation 1', 0.5, 'WSP', 'Students work with faculty, staff, and visiting artists to explore and develop basic proficiency in specific areas of performance creation. Areas include research, conception, writing, direction, design, performance, production, stage management, production management, publicity, dramaturgy, critique, and documentation.', 'DRAMA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DRAMA 207', 'Production Participation 2', 0.5, 'WSP', 'Students work with faculty, staff, and visiting artists to explore and develop basic proficiency in specific areas of performance creation. Areas include research, conception, writing, direction, design, performance, production, stage management, production management, publicity, dramaturgy, critique, and documentation.', 'DRAMA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SI 240R', 'Migration, Diaspora, and Exile: Muslim Narratives', 0.5, 'LEC', 'This course examines Muslim narratives written in the United States, Canada, and the United Kingdom. It investigates the diversity of Islamic culture and expression in diasporic contexts, exploring an array of experiences written from various socio-cultural locations. The major issues of focus are the dilemmas of individual religiosity and religious identity within diasporic contexts, the politics of identity and community, and the ways in which Islam is lived and formulated (or reformulated) in these contexts.', 'SI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DRAMA 246', 'Design for Performance', 0.5, 'SEM', 'Students explore historical and current perspectives on the principles of design for performance.', 'DRAMA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DRAMA 248', 'Management for the Arts', 0.5, 'SEM', 'Students explore a broad range of arts management concepts. Assignments take students'' individual interests into account.', 'DRAMA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DRAMA 278', 'Theatre and Technology', 0.5, 'LEC, SEM', 'Students explore the development of theatre technologies and their significance in historical and current performance contexts.', 'DRAMA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DRAMA 280', 'Theatre and Performance in Canada', 0.5, 'SEM', 'Students explore theatre and performance in Canada in its historical, political, and cultural contexts.', 'DRAMA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DRAMA 282', 'Gender and Performance', 0.5, 'SEM', 'Students explore representations of gender on stage, in the media, and in politics.', 'DRAMA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DRAMA 284', 'Site-Specific Performance', 0.5, 'WSP', 'Students explore performance sites as resources for creation.', 'DRAMA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DRAMA 286', 'Early English Theatre', 0.5, 'SEM', 'Students explore the early development and continuing relevance of English language theatre and performance practices.', 'DRAMA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DRAMA 288', 'Language, Theatre, and Performance', 0.5, 'SEM', 'Students explore the relationship between language and meaning in historical and contemporary theatre and performance.', 'DRAMA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DRAMA 300', 'Theories of Theatre and Performance', 0.5, 'SEM', 'Students study and critique a range of existing works of theatre and performance in the context of related commentary, paying close attention to concepts of knowledge, experience, and positionality.', 'DRAMA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DRAMA 316', 'Production Participation 5', 1, 'WSP', 'Students work at an intermediate level with faculty, staff, and visiting artists to explore and increase proficiency through extensive contributions to specific areas of performance creation. Areas include research, conception, writing, direction, design, performance, production, stage management, production management, publicity, dramaturgy, critique, and documentation.', 'DRAMA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DRAMA 368', 'Collaborative Creation', 0.5, 'WSP', 'Students explore techniques for collaborative creation in theatre and performance.', 'DRAMA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DRAMA 374', 'Sustainability in Performance', 0.5, 'SEM', 'Students explore the interchange between artistic practice and current topics in sustainability research.', 'DRAMA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DRAMA 376', 'Political Theatre and Performance', 0.5, 'SEM', 'Students explore the relationships among form, political engagement, culture, and agency in theatre and performance.', 'DRAMA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DRAMA 378', 'Black Theatre and Performance', 0.5, 'SEM', 'Students explore the relationship of black theatre and performance to artistic and public life in North America.', 'DRAMA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DRAMA 379', 'Virtual Theatre and Performance', 0.5, 'LAB, SEM', 'Students explore and create theatre and performance in virtual reality.', 'DRAMA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DRAMA 400', 'Collaborative Performance Project', 1.5, 'WSP', 'Students work at an advanced, independent level with faculty, staff, and visiting artists to create and reflect upon a major, collaborative performance project. As core members of the creative team, students engage in all areas of project creation, including research, conception, writing, direction, design, performance, production, stage management, production management, publicity, dramaturgy, critique, and documentation.', 'DRAMA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SMF 400', 'Capstone Seminar', 0.5, 'SEM', 'This seminar invites students to integrate their knowledge in the domains of sexuality, relationships, and families and make connections among theories, research, and practices. The capstone focus provides students the opportunity for critical self-reflection on their university experience.', 'SMF');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DRAMA 410', 'Collaborative Performance Project', 1.5, 'WSP', 'Students work at an advanced, independent level with faculty, staff, and visiting artists to create and reflect upon a major, collaborative performance project. As core members of the creative team, students engage in all areas of project creation, including research, conception, writing, direction, design, performance, production, stage management, production management, publicity, dramaturgy, critique, and documentation.', 'DRAMA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DRAMA 416', 'Production Participation 9', 1, 'WSP', 'Students work at an advanced, independent level with faculty, staff, and visiting artists to create and reflect upon a major, collaborative performance project. Through extensive contributions to production, students engage in all areas of project creation, including research, conception, writing, direction, design, performance, production, stage management, production management, publicity, dramaturgy, critique, and documentation.', 'DRAMA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('DRAMA 417', 'Production Participation 10', 1, 'WSP', 'Students work at an advanced, independent level with faculty, staff, and visiting artists to create and reflect upon a major, collaborative performance project. Through extensive contributions to production, students engage in all areas of project creation, including research, conception, writing, direction, design, performance, production, stage management, production management, publicity, dramaturgy, critique, and documentation.', 'DRAMA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EARTH 321', 'Introduction to Geomicrobiology', 0.5, 'LEC', 'This course will provide an overview of how microbial life has affected Earth''s environment through time. Emphasis will be on geomicrobial processes in the lithosphere and hydrosphere. Geomicrobial interactions with key elements will be discussed. [Instructor consent required for students lacking EARTH 221 prerequisite; Offered: F]', 'EARTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EASIA 302R', 'Chinese Foreign Policy since 1949', 0.5, 'LEC, SEM', 'This course examines the major elements and evolution of Chinese foreign policy since 1949 and China''s external relations in the context of rising Chinese power.', 'EASIA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EASIA 304R', 'Korean Law and Society', 0.5, 'LEC', 'This course examines the legal system, legal culture, and socio-cultural traditions of the Republic of Korea from a comparative legal perspective.', 'EASIA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EASIA 362R', 'Introduction to Pre-Modern Japanese Literature', 0.5, 'LEC', 'Students study a selection of works of early Japanese literature from its beginnings to the 17th century and examine a wide variety of genres, including poetry, diaries, military tales, and philosophical tracts composed during that time period.', 'EASIA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EASIA 363R', 'Introduction to Early Modern Japanese Literature', 0.5, 'LEC', 'A study of a selection of works of Japanese literature from the 17th century to the early 20th century, examining a wide variety of genres in early modern Japan.', 'EASIA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 204A', 'Numerical Methods 1', 0.25, 'LAB', 'Application of computational methods to engineering problems. Number systems, errors and error propagation. Roots of nonlinear equations. Introduction to numerical linear algebra. Interpolation and numerical integration. [Offered: F, W]', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 204B', 'Numerical Methods 2', 0.25, 'LAB', 'Application of computational methods to engineering problems. Introduction to numerical solutions of ordinary differential equations, optimization. [Offered: F, S]', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PSYCH 238', 'Organizational Psychology', 0.5, 'DIS, LEC', 'Survey of organizational, group, and individual processes involved in work motivation, group dynamics, leadership, organizational climate, and organizational culture.', 'PSYCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 100', 'Principles of Economics', 0.5, 'LEC', 'This course provides an introduction to the basic concepts and tools of micro and macro economic analysis of individuals, businesses, and government in the global world. Topics may include consumers, producers, market structures, national income accounting, unemployment, and inflation.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 106', 'Introduction to Computer Programming 2', 0.5, 'LAB, LEC, TST', 'A continuation of the introduction to computer programming begun in CS 105. The use of programming, in conjunction with libraries, as a means of solving practical problems in art, design, and data processing. Basic text processing, manipulation of images and sound, handling and visualization of tabular and hierarchical data. Introductions to user interfaces, physical simulation, and object-oriented programming. [Offered: W]', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EMLS 601R', 'Speaking English for Professional Purposes', 0.5, 'LEC', 'A focus on speaking, pronunciation, vocabulary, and listening skills helps second language speakers of English in both academic and professional contexts.  The course targets pronunciation errors that prevent comprehensibility and develops delivery skills for teaching, presenting  papers, and defending theses.  It includes strategies for reducing errors in speech and improving listening comprehension.', 'EMLS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EMLS 602R', 'Scholarly Writing in English', 0.5, 'LEC', 'Designed specifically for graduate students for whom English is not the first language, English 602R introduces the skills and knowledge required to write a variety of informative and persuasive documents, both at university and at work. A primary goal is to enhance students'' ability to write for readers with varying levels of technical knowledge. To that end, the course focuses not only on vocabulary building (including idioms) but also on clear, concise and correct communication.', 'EMLS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EMLS 612R', 'Professional Writing for Engineers', 0.5, 'LEC', 'Designed specifically for graduate students for whom English is not the first language, English 612R introduces the skills and knowledge required to write a variety of documents frequently required of engineers both at university and at work.  The course goals are to teach the conventions of professional documents, increase vocabulary, and enhance the writer''s ability to write clearly, concisely, and correctly.', 'EMLS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENBUS 112', 'Operationalizing Sustainable Development within Business', 0.5, 'LEC, TUT', 'This course will explore organizational structures within business. Discussions will focus on factors that deter organizational change along with the means of overcoming these issues. A variety of guest speakers will demonstrate how the greening of industry has affected organizational structure in different business sectors. This course includes a review of business case studies where environmental management changes have resulted.', 'ENBUS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENBUS 211', 'Principles of Marketing for Sustainability Professionals', 0.5, 'LEC', 'This course explores the principles of marketing with a focus on sustainability issues. Topics that will be explored include green consumerism; greenwashing, the incorporation of environmental considerations into successful products and services; the role of social marketing, government regulations and other incentives related to green marketing.', 'ENBUS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENBUS 406', 'Industrial Ecology: Sustainable Materials', 0.5, 'LEC', 'Sustainability of industrial materials is examined, including resources, production, use and end-of-life. Different types of industrial materials and major applications (buildings, transportation, communications, etc.) are covered. Industrial ecology and management approaches help restructure production systems and support sustainable development.', 'ENBUS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EASIA 203R', 'Japanese Culture and Society', 0.5, 'LEC', 'The course explores the interface of tradition and modernity in Japanese life. Topics include traditional and modern Japanese culture, language, education, art, technology, and business.', 'EASIA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENBUS 412', 'Advanced Strategic Management for Sustainable Business', 0.5, 'LEC', 'This course draws on strategy concepts and theories explored in ENBUS 302 by focusing on the integration of business strategy with sustainability strategy. The course presents the principles that are essential for the formulation and evaluation of strategy for business and explores frameworks for strategic analysis, synthesis and action as it relates to sustainable business.', 'ENBUS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENBUS 414', 'Sustainability for Small and Medium Enterprises', 0.5, 'LEC', 'The course will focus on the application of material learned in earlier courses and contemporary business knowledge to the challenges of integrating sustainability practices into small and medium enterprise. Overall term goals include the development of a small business plan. Weekly presentations will be made in class on small and medium business case studies, often by the actual business.', 'ENBUS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 210C', 'Genres of Creative Writing', 0.5, 'LEC', 'This course introduces students to both contemporary and historical forms of creative writing. Students will explore genres of poetry, prose, and/or drama through their own writing. Students will also investigate the culture of publishing, learn key revision strategies, and workshop the writing of their peers to develop their critical abilities.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 210G', 'Grant Writing', 0.5, 'LEC', 'The course covers researching, organizing, drafting, and editing proposals and applications for government grants for organizations. Topics may include interviews with domain experts, working with proposal guidelines and checklists, establishing milestones and expectations, using past proposals as models, treating individual sections as separate sub-genres, and creating coherence and flow in the final draft.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 210J', 'Technical Editing', 0.5, 'LEC', 'This course will introduce students to practices and tools of technical editing, such as language and format editing, verification and fact-checking, style guide consistency, discourse appropriateness, and the use of profession-specific software.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 211', 'First Nations, Metis, and Inuit Literatures', 0.5, 'LEC', 'This course examines literary works in a variety of genres by First Nations, Metis, and Inuit authors in Canada. Students will study the literature in relation to key concepts that recur in indigenous literary criticism such as land, teaching, and respect.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 275', 'Fiction and Film', 0.5, 'LEC', 'A study of the relationships between written and cinematic narrative focusing on adaptations of fiction to film and the different narrative techniques of each medium.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 280', 'Literatures of Migration', 0.5, 'LEC', 'This course explores the literatures of one or more diasporic communities in North America (for example, African, Asian, Caribbean, Middle Eastern, South Asian) and topics such as memory, generational difference, and cultural hybridity.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 291', 'Global Literatures', 0.5, 'LEC', 'How has border-crossing shaped the field of English literary studies? In this course, students will discuss works of literature from around the world that explore such themes as colonialism, migration, transnationalism, and the global.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 472', 'Research Methods in Technical Communication', 0.5, 'LEC', 'This course teaches students the practice and theory of research methods in the field of technical and professional communication. Topics may include resource validity and renewal cycles, data-gathering techniques and analytics, interview techniques for subject-matter experts, rapid research skills, and user-experience design.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENVE 225', 'Environmental Modelling', 0.75, 'LEC, TST, TUT', 'Modelling of environmental engineering processes via the solution of differential equations. Mass transfer processes in continuum and discrete systems, with applications to natural and engineered systems. Reactor theory. Understanding initial and boundary conditions. Classical and numerical solution techniques for solving differential equations. Volume and surface integration. Reynolds transport theorem. [Offered: F]', 'ENVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENVE 277', 'Air Quality Engineering', 0.5, 'LEC, TST, TUT', 'Quantitative introduction to indoor and outdoor air pollution sources, and major processes including emission rates, atmospheric dispersion, chemistry, and deposition. Air quality standards and regulations. Basic atmospheric science and meteorology to support the fate and transport of air pollutants. Overview of control and treatment methods. Indoor air exposure estimates and the function of the HVAC components. [Offered: F]', 'ENVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENVE 279', 'Energy and the Environment', 0.5, 'LEC, TST, TUT', 'Conservation of energy, energy balances on closed systems. Steady-state and transient heat transfer via convection, radiation, and conduction. Mechanical and electrical work. Internal energy, enthalpy, and specific heats of solids, liquids and gases. Phase change in natural environmental systems; The basics of heat engines, refrigerators, and heat pumps. Function, evaluation, and design of energy resource technology: wind and hydroelectric turbines, photovoltaics, geothermal energy, biomass and biofuel, natural gas and petroleum extraction, and tidal energy. Renewable energy policy and implications. [Offered: F]', 'ENVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENVE 280', 'Fluid Mechanics', 0.5, 'LAB, LEC, TST, TUT', 'An introduction to fluid mechanics. Fluid properties. Review of fluid statics. Buoyancy. Bernoulli equation. The momentum equation and applications. Laminar and turbulent flow. Dimensionless numbers. Closed conduit flow including friction losses. Pipe network analysis including energy losses and efficiencies. Four lab sessions. [Offered: W]', 'ENVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENVE 376', 'Biological Processes', 0.5, 'LAB, LEC, TST, TUT', 'Common microbial substrates and metabolisms in engineered and natural systems, kinetics of microbial growth, stoichiometry of nutrient uptake, continuous flow stirred tanks with/without recycle, aeration system design, applications to wastewater treatment, solid waste management, groundwater, and soil remediation. [Offered: W]', 'ENVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENVE 383', 'Advanced Hydrology and Hydraulics', 0.5, 'LAB, LEC, TST, TUT', 'Physical and mathematical models of hydrological processes at the landscape scale, and hydraulic phenomena in channels. Advanced models of evapotranspiration and snow energy balances. Hydrologic modelling: parameterization, boundary conditions, calibration. Simulation of energy losses, backwater effects, and gradually varying flow profiles in open channels. Sediment transport, scour, and erosion. [Offered: W]', 'ENVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 108T', 'Tolkien: From Book to Film', 0.5, 'LEC', 'A study of J.R.R. Tolkien''s The Hobbit (1937) and The Lord of the Rings (1954-55), and their film adaptations by Peter Jackson (2001-03, 2012-14).', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENVE 400', 'Environmental Engineering Design Project 1', 0.5, 'LEC, PRJ', 'Students undertake an independent environmental engineering design project during the last two terms of their plan. The purpose of the project is to demonstrate students'' abilities to practise in an environmental engineering capacity in their chosen area of expertise, using knowledge gained from their academic and employment experiences. The first part of the project (ENVE 400) will include problem identification, generation and selection of solutions, and time management. Incorporation of technical, ecological, social, political, and economic issues in the solution for the project will be required. A basic requirement of the proposed solution is that it must be compatible with the principles of sustainability. Requirements include proposal, progress report, and a final report containing recommendations for part two of the project, ENVE 401. [Offered: F]', 'ENVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENVE 401', 'Environmental Engineering Design Project 2', 0.5, 'PRJ', 'A continuation of ENVE 400. The final design of the major environmental engineering project proposed in ENVE 400 will be undertaken. The purpose of this phase of the project is to carry out a detailed technical design of the solution proposed in ENVE 400. Requirements of this part of the two-term project include a symposium presentation and a final report. [Offered: W]', 'ENVE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENVS 205', 'Sustainability: The Future We Want', 0.5, 'LEC', 'This course introduces students to the goals, principles, and practices of sustainability. Sustainability thinking, policies, and programs aim to increase and maintain human well-being by integrating or reconciling current social and economic goals (e.g., economic growth; poverty alleviation; gender inequality) with long-term environmental objectives (e.g., improved environmental quality; climate stability; biodiversity conservation; and managing resources to ensure future access). Case studies are used to illustrate solutions for realizing sustainability, emphasizing efforts underway for reaching the United Nations Sustainable Development Goals (SDGs). The course assesses four intersecting and sometimes competing approaches for realizing sustainability: technological innovation; market-based approaches; state regulation and investment; and individual and collective (''grassroots'') action. Key values underlying sustainability are explored, including notions of the "good life", democracy, social justice, and efficiency.', 'ENVS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENVS 274', 'Special Topics in Environment', 0.5, 'FLD, LEC, SEM', 'This special topics course covers material relevant to all students in the Faculty of Environment. Course content is not specific to an individual program and is specified by the instructor at the level indicated by the course number.', 'ENVS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENVS 374', 'Special Topics in Environment', 0.5, 'FLD, LEC, SEM, WSP', 'This special topics course covers material relevant to all students in the Faculty of Environment. Course content is not specific to an individual program and is specified by the instructor at the level indicated by the course number.', 'ENVS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENVS 400', 'First Peoples and Business Development', 0.5, 'LEC', 'This course examines the cultural, legal, constitutional, geographical and economic context of business development among, with and within First Nations'', M√©tis and Inuit communities.', 'ENVS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ANTH 309', 'The Archaeology of North America', 0.5, 'LEC', 'This course examines past Indigenous lifeways in North America north of Mexico from the time of earliest settlement to contact with Europeans. It provides a broad survey of space-time systematics as well as conceptual, methodological, and ethical issues relevant to study of the North American archaeological record.', 'ANTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ERS 342', 'Professional Conservation and Restoration Practice II', 1, 'FLD, LEC', 'An applied ecology course that is designed for those interested in becoming professional ecologists. In keeping with the Ecological Society of America''s Professional Ecologist Certification and the Society for Ecological Restoration''s Certification Programme, the core vehicle is a 7-10 day field trip that involves on-site discussions of the successes and challenges of ecological restoration and/or conservation projects with practitioners. Normally, these projects are located in the Carolinian Zone and have some focus on coastal areas of Lake Erie. The course will provide a platform for learning the advanced and professional principles of restoration and conservation ecology and the restoration of ecosystem services.', 'ERS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ERS 346', 'Wildlife Ecology', 0.5, 'LAB, LEC', 'This course introduces the main concepts and principles of wildlife ecology. Topics include: population dynamics, animal behavior, habitats, genetics, predation, and habitat use. The lab component will introduce students to wildlife data collection, analysis, and interpretation.', 'ERS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ERS 443', 'Ecosystem Field Research', 1, 'FLD, LEC', 'This field research course is designed to involve students in high level intensive research on the function and/or structure of ecosystems as they change because of successional and human processes. Students will normally undertake an experimental approach to an ecosystem-based problem and evaluation outcomes of their experiment or long-term data sets. The course will be focused on one ecosystem per offering, e.g. marine, aquatic, terrestrial forest, mountain. It is expected that the course will be off-campus at a field station or protected area within North America but there may be opportunities to deliver it outside of North America. When offered, the syllabus will provide details on exact location.', 'ERS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ERS 446', 'Wildlife Management', 0.5, 'LAB, LEC', 'This course introduces the main concepts and principles for the management of wildlife species. This course builds on ERS 346 and explores the application of the principles from that course to the management of wildlife. The lab component will build on the skills of wildlife data collection, analysis, and interpretation presented in ERS 346.', 'ERS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 313', 'Advanced Visualization and Analysis', 0.5, 'LEC', 'This course will explore the study of architectural precedent as both an analytic and generative tool exposing students to diagrammatic strategies and advanced visualization methods to be used as a support for urban and architectural analysis and design. This course will expose students to architectural visualization techniques, information design and the management of complexity using Building Information Modelling (BIM) and other digital tools. [Offered: W]', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GER 301', 'Language, Culture, and Identity', 0.5, 'SEM', 'This course provides an introductory overview of fundamental concepts of language, culture, and identity and relates them to foreign/second language learning and teaching. The course is not language-specific but rather addresses general questions related to learning and using more than one language.', 'GER');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GER 790', 'Topics in Intercultural Perspectives', 0.5, 'SEM', 'This course is normally taught in Mannheim, Germany.', 'GER');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GGOV 644', 'International Migration:  Practice, Theory & Regulation', 0.5, 'LEC, SEM', 'This course explores theoretical perspectives on migration and critically examines how states deter or facilitate migration flows, including irregular immigration, refugees and asylum seekers, and low and high-skilled labourers.  A multidisciplinary approach allows students to investigate the ubiquitous rise of border controls as a state tool to control migration, and how their implementation intersects with gender, race, class and nationality.', 'GGOV');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 212', 'The Computing Society', 0.5, 'LEC', 'This course examines the historical and current relationships between computer technology and society. It explores the impact and consequences of computing from a societal perspective, but also considers various nontechnical factors and values that have shaped computing technology and practice. The scope of the course will range from early mechanical aids, through the mid 20th century invention of electronic digital computers, to the networks and mobile applications of the 21st century. Technological studies relating to gender, education, employment, and war will be used as focal points. Material artifacts will form a core element of the course.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 289', 'JFK: The Decision-Maker Behind the Myth', 0.5, 'SEM', 'This course re-examines John F. Kennedy''s presidency, refuting myths about his decision-making in war and peace. Students grapple with historical sources in a trans-media platform. They are invited to relate the past to contemporary issues of war and peace in an intensive learning experience.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PSCI 420', 'Gender and Global Politics', 0.5, 'SEM', 'Does looking at the world through the lens of gender change how we see the state, sovereignty, diplomacy, security, trade, migration, globalization, governance, and other foundational concepts in global politics? We review feminist theories of politics, with a particular focus on international relations and global governance; examine how gender shapes the roles and experiences of women and men in global politics; and discuss how to do feminist research.', 'PSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HLTH 107', 'Sociology of Activity, Health, and Well-being', 0.5, 'LEC, TUT', 'This course will focus on the social and cultural factors that influence leisure, activity, health and well-being in different settings and among different populations. Sociological literature will be used to examine activity, health, and well-being in the lives of individuals, groups, and society.', 'HLTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HLTH 173', 'Contemporary Issues in Health 1', 0.5, 'LEC, SEM', 'One or more term courses will be offered from time to time as announced by the School of Public Health and Health Systems. Topics will be dependent upon special research and/or instructional interests of the faculty.', 'HLTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HLTH 273', 'Contemporary Issues in Health 2', 0.5, 'LEC, SEM', 'One or more term courses will be offered from time to time as announced by the School of Public Health and Health Systems. Topics will be dependent upon special research and/or instructional interests of the faculty.', 'HLTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HLTH 301', 'Applied Health Promotion: Theory and Practice', 0.5, 'LEC, TUT', 'This course introduces health promotion history, theories, and change strategies that address individual-level behaviour plus sociocultural, economic, political, and environmental conditions and resources for health. Topics include educational, persuasive, organizational, regulatory, and empowerment approaches. Examples may be drawn from school and workplace programs, mHealth and eHealth, social marketing, community development, and social mobilization and advocacy for policy change.', 'HLTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HLTH 373', 'Contemporary Issues in Health 3', 0.5, 'LEC, SEM', 'One or more term courses will be offered from time to time as announced by the School of Public Health and Health Systems. Topics will be dependent upon special research and/or instructional interests of the faculty', 'HLTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HLTH 401', 'Global Health', 0.5, 'LEC', 'We are becoming a global community; increasingly, health concerns are international in nature and impact. The student will build upon core content concerning population and public health theories, international health care systems, and cultural sensitivity to address emerging global health concerns on the perspective of public and population health.', 'HLTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HLTH 441', 'Advanced Qualitative Methods', 0.5, 'LEC', 'This course will build on concepts and theories concerning qualitative methods introduced in HLTH 344. The student will have the opportunity to tailor the material to the core function upon which they are focusing using case study discussion and practical applications.', 'HLTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HLTH 481', 'Community Learning Project', 0.5, 'FLD, SEM', 'Service learning is a method of teaching and reflection designed to use community service as a vehicle to enrich and integrate classroom learning, teach civic responsibility, encourage lifelong civic engagement, and strengthen communities. Students will be required to identify and design community learning placements, including the establishment of specific learning and outcome objectives, and a method of evaluation. Provisions must be approved by the community agency and the course coordinator. Placements are normally 8 to 10 hours per week for 8 weeks. In the final week of the term students will meet to reflect upon and share their experience with fellow students. Students will also complete a written report detailing whether and how they achieved their objectives, what they learned from the process relevant to the practice of health promotion, and how their experience utilized or exemplified course based material and assignments. Reports will be reviewed and graded as pass/fail by both the community partner and a faculty service leader.', 'HLTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('REC 313', 'Mobilizing Resources for Recreation and Sport Delivery', 0.5, 'LEC', 'This course focuses on mobilizing resources in high impact public and not-for-profit sector organizations. Topics may include developing strategic partnerships, securing financing, sponsorships, grants and donations, enabling co-production, harnessing social capital, support from volunteers, and alternative forms of service delivery.', 'REC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 107', 'Sociology of Activity, Health, and Well-being', 0.5, 'LEC, TUT', 'This course will focus on the social and cultural factors that influence leisure, activity, health and well-being in different settings and among different populations. Sociological literature will be used to examine activity, health, and well-being in the lives of individuals, groups, and society.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ANTH 465', 'Borders, Boundaries, and Crossings', 0.5, 'SEM', 'This seminar covers anthropological perspectives on the relationship between state borders and socio-cultural boundaries (across race, gender, class, language, and religion). Readings include ethnographies about contested borders, border dwellers, and border crossers.', 'ANTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('RS 312', 'Muslim Lives and Practices Worldwide', 0.5, 'LEC', 'This course examines the diversity of Muslim lives and practices worldwide. Selected case studies raise issues such as gender, authority, stereotypes, fundamentalism, and secularism.', 'RS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('LS 202', 'Criminal Law', 0.5, 'LEC', 'A case-study approach to the study of criminal law in Canada with a focus on basic concepts and core principles relating to legal judgements along with comparative examination between civil and criminal law and attention to legal theory.', 'LS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('LS 206', 'Canadian Government & Politics', 0.5, 'DIS, LEC', 'An examination of Canada''s federal system, parliamentary government, and national political processes, such as the party system, interest groups, the electoral system, and voting behavior.', 'LS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('LS 221', 'Research Methods', 0.5, 'LEC', 'An introductory survey of the research techniques commonly employed by sociologists, criminologists, and legal studies researchers. The formulation of research designs appropriate to various kinds of intellectual problems in social science is stressed.', 'LS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('LS 222', 'Juvenile Delinquency', 0.5, 'LEC', 'A systematic analysis and criticism is presented of biological, psychological, psychoanalytical, and sociological theories of juvenile delinquency. Attention is given to statistics and contemporary research with special emphasis on the distribution and types of delinquent subcultures.', 'LS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('LS 223', 'Deviance: Perspectives and Processes', 0.5, 'LEC', 'The deviance-making process is examined in a variety of social contexts. This course examines the emergence of rules and control agencies, the processes by which people become involved in deviant activities, and the contingencies affecting their careers as deviants.', 'LS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('LS 224', 'Victims and Society', 0.5, 'LEC', 'This course will examine the substance of victimization: the scientific study of victims, the process, etiology, and consequences of victimization. Topics will include victims and politics, the victims'' movement, victim-precipitation, the victimization of women, and family violence.', 'LS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('LS 226', 'Sociology of Mental Disorder', 0.5, 'LEC', 'An examination of sociological research and theory in the field of mental disorders particularly as they apply to issues of law and social control. Topics include mental health legislation, the medical model of mental "illness," the epidemiology of mental disorder, family processes and psychiatric hospitalization, public attitudes and social stigma, and specific forms of mental disorder.', 'LS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('LS 227', 'Criminology', 0.5, 'LEC', 'An examination of the major theories of crime causation and their implications for the development of social policy. Both historical and contemporary theories will be discussed.', 'LS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('LS 228', 'Sociology of Criminal Justice', 0.5, 'LEC', 'Decisions to process offenders and the role of social factors in the Canadian criminal justice system are critically examined. Focal issues include police discretion, the legal profession, and prison systems.', 'LS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('LS 229', 'Selected Topics in Criminology', 0.5, 'LEC', 'Sociological analysis of research and theory on selected criminal activities. Motivation, modus operandi, and the social characteristics of offenders will be examined in relation to such specific crimes as drug and sexual offenses, theft, robbery, murder, organized crime, and/or other criminal activities.', 'LS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('LS 235', 'History of Ancient Law', 0.5, 'DIS, LEC', 'A historical introduction to law in the ancient world. Babylonian, Assyrian, Hittite, and Roman law, legal practices, and concepts will be examined.', 'LS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('LS 236', 'Law and Society in the Middle Ages', 0.5, 'DIS, LEC', 'A study of the laws and legal procedures of the Middle Ages. This course examines the relationship between legal procedures and institutions and the medieval societies that produced them.', 'LS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('LS 237', 'Canadian Legal History', 0.5, 'DIS, LEC', 'This course examines the Canadian legal system from colonial times to the present with particular emphasis on such themes as law and the economy, courts and judiciary, the legal profession, family and criminal law, women and the law, and civil liberties.', 'LS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('LS 249', 'Mental Disorder and the Law', 0.5, 'LEC', 'An examination of laws, law enforcement, and social policies in relation to mental disorder. Included is an analysis of mental health legislation, involuntary commitment, mental health courts, and various issues related to the police and the correctional system''s handling of mentally disordered offenders.', 'LS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('LS 263', 'Organized Crime', 0.5, 'LEC', 'An examination of select criminal organizations in North America. Particular attention will be given to the social history of "the mafia" and the development of legal tools for policing criminal organizations. Additional themes for discussion include enterprise and economic crimes, corruption, and the role of women in organized crime.', 'LS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('LS 271', 'Conflict Resolution', 0.5, 'LEC', 'An examination of the resolution of conflicts, ranging from interpersonal to broader social and international conflicts. Students are introduced to negotiation, mediation, and nonviolent resistance, and are encouraged to develop their own theoretical understandings that aid in addressing conflict.', 'LS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('LS 272', 'Psychology and Law', 0.5, 'LEC', 'Psychological principles drawn from a variety of subdisciplines (e.g., social, clinical, cognitive) will be surveyed in terms of their relevance and application to the legal system. Topics may include jury selection and decision-making, eyewitness testimony, insanity defense, competency assessment, risk assessment, and attitudes toward law and the legal process.', 'LS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('LS 273', 'Children''s Rights in Canada', 0.5, 'LEC', 'This course examines children''s rights from a moral and comparative legal perspective. Students explore the welfare and developmental interests of children; the corresponding duties of parents, custodians, educators, and social workers; and the nature and scope of public educational authority in the common law jurisdictions of Canada.', 'LS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('LS 283', 'Business Law', 0.5, 'LEC, TST', 'Particular attention is given to the law relating to contracts and business organizations. Other areas of study include sources of law, the judicial process, real and personal property, torts, agency, credit, and negotiable instruments.', 'LS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('LS 286', 'Law in Popular Culture', 0.5, 'LEC', 'Much is at stake in how law is portrayed in paintings, literature, music, television, and movies. In this course students will draw on a range of disciplines in the social sciences and humanities to study how law is represented in popular culture, and how these representations, in turn, impact how we view law.', 'LS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('LS 291', 'Legal Writing', 0.5, 'LEC', 'A study of the principles, processes, and various forms of writing used in the practice of law and drafting of legislation. The history and structure of legal writing, including current debates about plain language, will be examined.', 'LS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('LS 292', 'Literature and the Law', 0.5, 'LEC', 'A study of literary works that involve legal matters and/or have led to litigation on such grounds as obscenity, treason, heresy, libel, and plagiarism.', 'LS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('LS 300', 'Sociology of Law', 0.5, 'LEC', 'This course examines the social construction of law and its administration as a social process. Topics will include law as an instrument of social control and social change, legal culture, the identification and evaluation of criminal suspects, the trial process and the rights of special groups. The specific laws highlighted will vary.', 'LS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('LS 306', 'Juvenile Justice', 0.5, 'LEC', 'An examination of theories of juvenile justice, juvenile law, and the structure and operations of juvenile systems, especially in Canada.', 'LS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('LS 319', 'Negotiation: Theories and Strategies', 0.5, 'LEC', 'This course explores different ways of negotiating between people and groups with conflicting interests. Students will learn the theory behind the strategies and develop practical negotiation skills you can put to use in your daily life at home, at work, and in the community.', 'LS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('LS 325', 'Sexuality and the Law', 0.5, 'LEC', 'Despite the commonly held belief that sexuality is nothing more than "doing what comes naturally," cultural definitions, including prohibitions against specific forms of conduct, impinge upon the most private or intimate of acts. This course examines the social construction and control of sexuality through law.', 'LS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('LS 326', 'Punishment and Society', 0.5, 'LEC', 'A critical criminological and sociological examination of theories and practices of punishment. This course will examine transformations in penal theory, penal management, and penal institutions and their social and policy implications.', 'LS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('LS 327', 'Policing in a Democratic Society', 0.5, 'LEC', 'A critical examination of the police as social control agents in contemporary democratic societies. Topics include the historical evolution of policing; police recruitment, training, and education; police/community relations; the occupational subculture of the police; police authority and discretion; private policing; and police deviance and criminality.', 'LS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('LS 331', 'Human Rights in Historical Perspective', 0.5, 'DIS, LEC', 'A detailed analysis of selected topics in the history of human rights. Special attention will be paid to revolutionary developments since World War II, and to the emergence of modern human rights policies. Topics will be examined through assigned readings, lectures, and films.', 'LS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('LS 344', 'Restorative Justice', 0.5, 'LEC', 'This course investigates the history, theory, principles, practices, and people of restorative justice. Content will centre particularly on restorative justice as a way of dealing with crime and interpersonal violence in the Canadian context.', 'LS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('LS 351', 'Philosophy of Law', 0.5, 'LEC', 'Basic themes in the philosophy of law. Issues include the nature of law and its relation to morality and politics, legal reasoning, the justification of punishment, and theories of rights, responsibility, and liability.', 'LS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('LS 352', 'Human Rights', 0.5, 'LEC', 'What are human rights? Which do we have, and why? What are the practical implications of human rights, for both individuals and institutions? A comprehensive discussion of theory and history, of law and morality, and of national and international applications.', 'LS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('LS 363', 'Canadian Constitutional Law', 0.5, 'LEC', 'An introduction to the nature and basic principles of constitutional law. Explores constitutional conventions, the distribution of powers in the Canadian federalism, Aboriginal and treaty rights, and the Charter of Rights and Freedoms.', 'LS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('LS 365', 'Transnational Migration', 0.5, 'LEC', 'This survey of transnational migration in the contemporary moment examines the reasons people have migrated voluntarily (e.g., as migrant workers) and involuntarily (e.g., as refugees), and how international and domestic law and policies affect, enable, and/or criminalize the movements of people across state borders.', 'LS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('LS 366', 'Global Governance', 0.5, 'LEC', 'Global governance is governing beyond the state. An examination of diverse problems - global warming, international human rights, terrorism, property rights disputes, and health crises - which transcend borders and demand cooperative global solutions.', 'LS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('LS 372', 'Criminal Profiling', 0.5, 'LEC', 'Foundational assumptions for, and basic approaches to, criminal profiling will be considered, along with a survey of relevant techniques in the context of numerous case studies. Limitations and alternatives to profiling will also be addressed.', 'LS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('LS 373', 'Public Policy and Native Peoples in Canada', 0.5, 'LEC', 'This course examines the evolution, logic, processes, and impacts of government policies developed specifically for Native peoples, with particular attention to government policy as both a cause of and a response to social problems within Native communities.', 'LS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('LS 374', 'The Evolution of Family Law in Canadian Society', 0.5, 'LEC', 'This course examines the evolution of family law in Indigenous, francophone, anglophone, and other communities in Canada. Taking an interdisciplinary approach, the course not only explores the demographic, economic, social, and political contexts in which family law developed but also assesses its significance for Canadian society.', 'LS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('LS 386', 'Law and Violence', 0.5, 'LEC', 'Discussions about law''s coercive power raise important questions about the legitimacy of state-sanctioned violence. This course uncovers the roots and limits of law''s power as it relates to questions of violence, obedience, and dissent.', 'LS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('INDEV 262', 'Introduction to Global Emerging Cities', 0.5, 'LEC', 'This course focuses on the dynamics of growth and change in global emerging cities, with a focus on the urban transition underway in cities, with emphasis on Asia and Africa. Current urban challenges (e.g. social inequality, uneven development, climate change) are discussed, as well as opportunities for innovative planning and sustainable urban development.', 'INDEV');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MEDVL 251R', 'The History of Islamic Civilization from 1300-1800: The Islamic Gunpowder Empires', 0.5, 'LEC', 'This course studies in comparative fashion the rise, consolidation, and decline of the three major Islamic empires of the early modern period (c. 1300-1800): the Ottomans in Anatolia, the Balkans, and the Middle East; the Safavids in Iran; and the Mughals in India.', 'MEDVL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('LS 413', 'Surveillance and Society', 0.5, 'SEM', 'An examination of the way monitoring technologies alter and shape social life in terms of security, fear, control, and vulnerability.', 'LS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('LS 428', 'Sentencing as a Social Process', 0.5, 'SEM', 'This course examines in depth the process and results of criminal sentencing. Topics include types of sentences for criminal and quasi-criminal offences, objectives of sentences, factors affecting sentences, the process of sentencing, the administration and effectiveness of sentences, and unresolved debates in sentencing.', 'LS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('LS 434', 'Sociology of At-Risk Youth', 0.5, 'SEM', 'This course examines the social attributes and surrounding conditions associated with at-risk youth. It will focus on the development of youth in three major institutions - education, criminal justice, and mental healthcare. This course will focus on the attributes of youth themselves, but also to changing institutional definitions and practices. This course will include an experiential learning component outside regular classroom hours.', 'LS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('LS 461', 'Transnational Organized Crime', 0.5, 'SEM', 'This seminar course examines contemporary legal regimes surrounding transnational organized crime, terrorism and organized crime, and international money laundering.', 'LS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('LS 462', 'Government and Politics of Indigenous Peoples', 0.5, 'SEM', 'An introduction to the history and development of government policy and regulations that have altered the political and social structures of aboriginal societies. We explore contemporary challenges to government policy and regulations, as reflected in the struggles for land, aboriginal rights, and self-government, as well as the development of a global identity.', 'LS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('LS 463', 'Rights and Public Policy', 0.5, 'SEM', 'An examination of the role that rights and rights discourse plays in public policy development and policy change. Examines Canadian public policy in relation to the Charter of Rights and Freedoms and human rights legislation, as well as cases in comparative context.', 'LS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('LS 464', 'Justice and Gender', 0.5, 'SEM', 'Theories of justice are concerned with the distribution of the basic goods of society - money, power, status, leisure, and so on. This course considers how the gender system fares from the standpoint of liberal justice, and to what extent the promises of liberal justice can be used to overturn the unequal treatment of women.', 'LS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('LS 492', 'Communication and Social Justice', 0.5, 'SEM', 'An examination of the ways in which communication on the part of individuals, groups, and institutions contributes to fostering justice in social contexts. Areas of focus include communication and justice in interpersonal, workplace, community, and national/international contexts.', 'LS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MNS 410', 'Special Topics in Solid-State Materials', 0.5, 'LEC', 'Topics in this theme area may include: transistors and semiconducting materials and devices,superconducting materials and superconductivity-based devices (such as Josephson junctions, SQUIDS), effects of low dimensionality and interfaces, quantum dots, magnetoresistance, spintronics, design and synthesis of new materials. (Note: At least one elective course is offered annually in the solid-state materials theme area. For a current list of offerings, see the academic plan advisor). [Offered: W]', 'MNS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MNS 431', 'Special Topics in Nano-Biomaterials', 0.5, 'LEC', 'Topics in this theme area may include: biomaterials on atomic and nanometre scales in which DNA, RNA, and/or polypeptides are incorporated as components; physical, chemical and biological processes; nanoscale forces, adhesion, indentation; applications, such as nanomotors, nano-biomimetic materials, nanocomposite materials, nano-biosensors. (Note: At least one elective course is offered annually in the Nano-Biomaterials theme area. For a current list of offerings, see the academic plan advisor). [Offered: W]', 'MNS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MTHEL 300', 'Professional Communications in Statistics and Actuarial Science', 0.5, 'LEC', 'This course introduces students to oral and written communication in the fields of statistics and actuarial science. With emphasis on the public presentation of technical knowledge, the ability to give and receive constructive feedback, and communication in a collaborative environment, this course helps students develop proficiencies in critical workplace skills. This course is writing intensive and includes extensive collaborative assignments.', 'MTHEL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('NATST 272', 'Issues in Contemporary Native Communities in Canada', 0.5, 'LEC', 'An examination of First Nations and M√©tis cultures and cultural development from the perspective of local native communities. The course will feature lectures, discussions, and occasional guest speakers representative of the wider native community.', 'NATST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('NE 109', 'Societal and Environmental Impacts of Nanotechnology', 0.5, 'LEC, TUT', 'An introduction to the field of nanotechnology and its ability to impact society and affect sustainability. How utilization of nanotechnology has simultaneously improved societal standards of living and introduced new ethical, health, and environmental concerns will be examined. Topics covered will include: the history of changing perceptions of and attention devoted to developments in nanotechnology; special health and environmental concerns associated with nanomaterials; the development and means of implementation of regulations to deal with the use of nanomaterials; ethical issues associated with nanotechnology and its applications. Intellectual property issues in general, and those pertaining to nanotechnology specifically, will also be discussed briefly. Relevant case studies will be presented and discussed. [Offered: F]', 'NE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('NE 215', 'Probability and Statistics', 0.5, 'LEC, TST, TUT', 'Elementary probability theory. Random variables and distributions. Binomial, Poisson, and normal distributions. Elementary sampling. Statistical estimation. Tests of hypotheses and significance. Regression. Goodness-of-fit tests. [Offered: F]', 'NE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('OPTOM 682', 'Visual Motor Neuroscience', 0.5, 'LEC', 'This course will examine the behavior and neural substrates of eye movements, specifically covering fixation, saccades, vestibular ocular interactions, smooth pursuit and the near triad synkinesis (vergence, accommodation and pupil responses).  Oculomotor behaviors for each system will be defined with respect to their specific stimuli, their dynamic and steady state responses.  Adaptive process within and across systems will be examined.  Control theory will be introduced as a means to model each system.  The course will allow emphasis to be focused on oculomotor systems pertinent to the student''s research.', 'OPTOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('OPTOM 683', 'Visual Sensory Neuroscience', 0.5, 'LEC', 'Visual sensory processing of light, form and space; Ocular somatosensory processing.', 'OPTOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PACS 332', 'Ethics of Peacebuilding', 0.5, 'LEC', 'This course will explore ethical challenges in peacebuilding. It will answer the questions: What are ethical problems in peacebuilding? How can we better understand and assess these problems? And, how can we improve our ability to support, or engage in, ethical peacebuilding practice?', 'PACS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PACS 333', 'Advanced Mediation Practice', 0.5, 'LEC, WSP', 'This course will give students both a thorough understanding of the mediation process and practical, hands-on mediation experience. Students will develop in-depth, first-hand knowledge and experience resolving conflicts through the role of the mediator as third party. The course will include a series of role-plays, readings, and case studies.', 'PACS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 614', 'Systematic Review and Meta-Analysis', 0.5, 'LEC, TUT', 'This course will provide students with an overview of the rationale and stages involved in the conduct of a formal systematic review and meta-analysis of a well-defined clinical/health research question. The overarching aim is to provide students with the tools to critically appraise and conduct a systematic review and meta-analysis.  Students will largely work in pairs to progress through each step involved (with feedback from instructors at each stage) and to produce a final systematic review and meta-analysis to be presented/submitted at the end of the course.  
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 615', 'Strategic Management of Biopharmaceutical Technology', 0.5, 'LEC, TUT', 'The strategic development and commercialization of novel and incremental bio/pharma technological innovation is rapidly expanding across all industries and with it comes the need for adept management of the process.  Effective management involves ensuring technological precaution such that the risks and ambiguities associated with advanced technologies are minimized.  Additionally, management must be strategic to maximize the successful achievement of corporate objectives based on the alignment of internal resources with the external environment. The transfer of technology from lab to market and successful propagation is usually impeded by a lack of cohesion between scientific understanding and business adeptness.  This course also serves to analyse and connect scientific principles underlying novel and incremental technologies with strategic management and the commercialization process from an entrepreneurial perspective.', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 616', 'PhD Thesis Proposal', 0.5, 'SEM, TUT', 'The objective of Pharm 616 are to encourage graduate students in the PhD Pharmacy program to best prepare and present their research objectives in written and oral form. 
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 617', 'Formulations', 0.5, 'LEC, SEM', 'The formulation of chemicals into products for specific purposes dates back to the stone age. Formulated products are now part of everyday life and include pharmaceuticals, cosmetics, personal care products, detergents, agricultural products, etc. This course will examine the chemical formulation of various consumer products with an emphasis on pharmaceutical formulations; major components used in formulations, surface chemistry, and molecular interactions will be discussed. Analytical methods for testing formulations, raw materials, and environmental and sustainability considerations will also be discussed.', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 105', 'Introduction to Computer Programming 1', 0.5, 'LAB, LEC, TST', 'An introduction to the fundamentals of computer programming through media computation. Students will learn to write interactive graphical programs. Fundamental language concepts such as variables, conditionals, loops, functions, and arrays. Programming concepts such as coding style, modular design, testing, and debugging. Media concepts such as 2D graphics drawing, input, animation, and image processing. [Offered: F]', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHS 651', 'Theory and Applications in Program Evaluation', 0.5, 'LEC', 'An advanced program and policy evaluation course that provides theoretical knowledge, skills, and application of program evaluation approaches, including organizational and program planning. A more in-depth coverage of topics will be presented, including case studies and the understanding and use of program and policy intervention theory. This includes the creation of logic models, the identification of the purpose of the evaluation, the development of an appropriate evaluation design, and consideration of factors associated with knowledge use.', 'PHS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHS 652', 'Qualitative and Mixed Methods and Analysis', 0.5, 'LEC', 'This course applies qualitative and mixed method approaches to program evaluation and public health practice. A critical analysis will inform health policy and practice based on methodological approaches most appropriate tot he type of evaluation being conducted. Data collection tools, such as focus groups or interviews will be taught, as well as useful techniques for data analysis, interpretation of results, and presentation of findings.', 'PHS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHS 653', 'Evaluation Practice and Management', 0.5, 'LEC', 'Key concepts necessary for successful evaluation practice and management are discussed and applied using case examples. Evaluation practice examples that are discussed include learning to understand and incorporate consideration of diversity, societal, organizational and environmental context in the conduct of evaluations. Key project management concepts relevant to each step of the evaluation of intervention will be covered, including (but not limited to) selecting research questions and focusing design considerations, budgets, timelines, politics, and contingency plans as well as creating and working in teams.', 'PHS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHS 654', 'Systems Thinking and Analysis in Health Program Planning and Evaluation', 0.5, 'LEC', 'This course provides the theory and tools needed to apply systems thinking and a systems approach to health program evaluation. An in-depth understanding of various forms of evaluation --process, utilization-focused, stake-holder, formative, developmental, and comprehensive evaluation -- will be acquired.', 'PHS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHS 655', 'Survey Methods', 0.5, 'LEC', 'This course provides in-depth coverage of measurement development and data gathering methods needed for evaluation data collection. Various measurement approaches will be studied, including questionnaire and survey design. Students will develop a measure (e.g. psycho/socio-metric scale) and test its reliability and validity, and understand precision and bias in questionnaire design and survey data. Students will acquire critical skills needed to determine if an existing measure is applicable to a particular evaluation design.', 'PHS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 474', 'Galaxies', 0.5, 'LEC', 'Galaxy structure, formation and evolution. Dynamics and stellar populations; gas and dust; supermassive black holes; large-scale structure. [Offered: F]', 'PHYS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 483', 'Advanced Therapeutic Concepts in Oncology and Medical Physics', 0.5, 'LEC', 'This course examines advanced oncology therapeutic concepts required as a foundation for patient management in the inpatient, ambulatory, and community settings. Topics include a comprehensive review of cancer epidemiology and pathophysiology, therapeutic agents used in the oncology setting, management of cancer therapy toxicities, and treatment and palliation of cancer symptoms. Physics methods in radiation therapy and the medical physicist''s role in cancer treatment will be addressed. [Offered: W]
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHYS 491', 'Special Topics in Life, Medical and Biophysics', 0.5, 'LEC', 'A lecture and project course offered in areas of life, medical and biophysics, which will add to the learning experience of the students in these plans at a fourth year level. Lecture topics may include advanced topics in: molecular and cellular biophysics, imaging, biophotonics, interaction of radiation with biomaterials, radiation dosimetry and other physics based diagnostics and therapeutics. Projects may include studies of recent advances in any areas of medical and biophysics. 
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARTS 102', 'Foundations for Writing', 0.5, 'LEC, WSP', 'This course helps students to develop the fundamental writing skills required by the Faculty of Arts. Students will be introduced to theories of strong composition and grammar, as well as relevant terminology, and will focus on practical applications, based on readings from Arts disciplines.', 'ARTS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PLAN 705', 'Design in Planning', 0.5, 'SEM', 'The foundation of this course is the intersection of the strong relation between urban design as the shaping of urban space and urban planning as the strategic and regulatory mediation of urban change. Establishing the basis of this relationship, the course critically explores contemporary issues in urban design and their implications for urban futures, planning, the environment, and the public realm. Case studies drawn from around the globe are featured.', 'PLAN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PMATH 930', 'Topics in Logic', 0.5, 'LEC', 'None', 'PMATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PMATH 940', 'Topics in Number Theory', 0.5, 'LEC', 'None', 'PMATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PMATH 945', 'Topics in Algebra', 0.5, 'LEC', 'None', 'PMATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PMATH 950', 'Topics in Analysis', 0.5, 'LEC', 'None', 'PMATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PMATH 965', 'Topics in Gemetry and Topology', 0.5, 'LEC', 'None', 'PMATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PMATH 990', 'Topics in Pure Mathematics', 0.5, 'LEC', 'None', 'PMATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PSCI 359', 'Politics of South Asia', 0.5, 'LEC', 'This course examines how international and domestic politics interact in South Asia. It covers bilateral relations within South Asia and the involvement of external powers such as United States and China. The course also delves into the challenge of political development shaped by class, ethnicity, religion, and gender.', 'PSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PSCI 370W', 'The Political Economy of Eastern Asia (WLU)', 0.5, 'LEC', 'None', 'PSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EASIA 391R', 'Special Topics', 0.5, 'LEC, RDG, SEM', 'This course deals with selected topics in East Asian studies.', 'EASIA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PSCI 450', 'Politics of Authoritarianism', 0.5, 'SEM', 'What is the difference between non-democracies like China, hybrid regimes like Russia, one-party states like Singapore, patrimonial and personalist states in Africa, or military dictatorships in Latin America? This course examines how political power is structured in authoritarian regimes and how it shapes the relationship between state and society.', 'PSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PSYCH 444R', 'Psychological Interventions', 0.5, 'SEM', 'This course reviews research showing how brief, non-invasive, low-cost, social psychological treatments can have small but significant benefits months and years later. Topics include school-based interventions, health interventions, psychological well-being interventions, prejudice-reduction interventions, when and why interventions can fail, and the theories behind how interventions work.', 'PSYCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PSYCH 451', 'Honours Seminar - Child and Adolescent Psychopathology', 0.5, 'SEM', 'This seminar addresses issues in child/adolescent psychopathology such as mood/anxiety disorders, conduct disorder, psychosis, eating disorders, Tourette''s Syndrome, and severe behavioural problems. Emphasis is on theories and research concerning social-emotional functioning, diagnosis, therapy, and socio-cultural contexts. The course also considers challenges and barriers encountered when raising and teaching children/adolescents with a mental illness.', 'PSYCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PSYCH 459', 'Honours Seminar - Close Relationships', 0.5, 'SEM', 'This course covers social psychological theories of close relationships with emphasis on reading and discussion of original empirical articles. Topics may include attraction, partner selection, trust and power, attachment, communications, conflict, intimacy, and relationship maintenance and dissolution. Student roles include presenting articles, leading discussions, and submitting a written research proposal.', 'PSYCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PSYCH 823', 'Research Apprenticeship I', 0.5, 'PRA', 'Intended for students who will be seeking further studies at the Ph.D. level, this research apprenticeship will provide a student with a more advanced laboratory research project over two terms and requiring approximately an extra 3.5 hours per week in addition to the 10 hours per week required of the Research Lab Internship required of all MASc DCS students. The particular project that the student will be engaged in, and the ultimate end product of the Apprenticeship at the end of the two terms, will be determined by the student and advisor together.', 'PSYCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PSYCH 824', 'Research Apprenticeship II', 0.5, 'PRA', 'Intended for students who will be seeking further studies at the Ph.D. level, this research apprenticeship will provide a student with a more advanced laboratory research project over two terms and requiring approximately an extra 3.5 hours per week in addition to the 10 hours per week required of the Research Lab Internship required of all MASc DCS students. The particular project that the student will be engaged in, and the ultimate end project of the Apprenticeship at the end of the two terms, will be determined by the student and advisor together.', 'PSYCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PSYCH 875', 'Organizational Psychology', 0.5, 'SEM', 'This course considers psychological theories and research that focuses on the behavior of people at work. In particular, there will be an emphasis on the psychology of the individual at work (often referred to as the "micro" component of organizational psychology). Accordingly, topics examined will be self-regulation, negotiation, ethics, and citizenship behavior. By necessity, coverage of these topics will not be comprehensive; the primary goal of the course is to simply introduce students to a broad range of theories and topics that are relevant to organizational psychology.', 'PSYCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PSYCH 876', 'The Psychology of Justice in the Workplace', 0.5, 'SEM', 'This doctoral seminar will examine historic and contemporary psychological theories and research on justice with a focus on understanding the dynamics of justice in the work setting.', 'PSYCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ANTH 377', 'Dental Anthropology', 0.5, 'LEC', 'This course introduces students to the analysis of human teeth, which is of central importance to paleopathology, paleoanthropology, forensic science, and contemporary human biomonitoring research. Students will also gain hands-on experience with laboratory techniques as they explore how to address anthropological questions with their data.', 'ANTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('REC 107', 'Sociology of Activity, Health, and Well-being', 0.5, 'LEC, TUT', 'This course will focus on the social and cultural factors that influence leisure, activity, health and well-being in different settings and among different populations. Sociological literature will be used to examine activity, health, and well-being in the lives of individuals, groups, and society.', 'REC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('REC 172', 'Special Topics in Leisure Studies 1', 0.5, 'LEC', 'One or more term courses will be offered from time to time as announced by the Recreation and Leisure Studies Department. Subjects will be dependent upon special research and/or instructional interests of faculty.', 'REC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('REC 272', 'Special Topics in Leisure Studies 2', 0.5, 'LEC', 'One or more term courses will be offered from time to time as announced by the Recreation and Leisure Studies Department. Subjects will be dependent upon special research and/or instructional interests of faculty.', 'REC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('REC 373', 'Qualitative Approaches to Leisure Research', 0.5, 'LEC', 'An exploration of qualitative methodologies used in the fields of leisure. Philosophies, theoretical orientations, and ethical considerations will be emphasized as students discuss and participate in various approaches to research design, data collection, analysis, and representation.', 'REC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SPCOM 335', 'Power, Agency, Community', 0.5, 'SEM', 'This course examines the ways in which individuals and institutions communicate to enact agency and build communities situated within power relations. Students will develop their understanding of what power and agency are, the relationship of each to communication, and how each constitutes community life.', 'SPCOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('RS 115R', 'Sex, Politics, and Religion in the U.S. and Canada', 0.5, 'LEC', 'What are the three topics that should not be talked about in polite company? Sex, politics, and religion. Breaking the taboo, this course examines religious practices and conflicts around sexuality and sexual behaviour in the United States and Canada, and how they have manifested in political contests and public policy.', 'RS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SPAN 395', 'Cultural Dimensions in English/Spanish Literary Translation', 0.5, 'LEC', 'This course focuses on the actual practice of translating literature, not only as the transformation of textual expressions, but also as a process of cultural interpretation. Besides reinforcing vocabulary and grammar, students will also review and research notions of different literary genres (poetry, narrative, and theatre), and solidify their knowledge of cultural studies.', 'SPAN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('RS 227', 'Buddhism in North America', 0.5, 'LEC', 'This course examines the history and development of Buddhism in North America. Topics such as the adaptation of traditional Buddhism to North American culture and its development as a Western religion will be examined.', 'RS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SCI 207', 'Physics, the Universe, and Everything', 0.5, 'LEC, TUT', 'How does the universe work? How can it even exist? What is the ultimate nature of reality? This course explores big picture questions that connect humanity to the nature of the physical world. Beginning with introduction to Einstein''s ideas and the quantum nature of the universe, this course considers how physicists tackle some of the mysteries (e.g., existence itself) and challenges (e.g., the world''s energy problem) in science today.', 'SCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SI 320R', 'Introduction to Modern Arab and Muslim Drama', 0.5, 'LEC', 'The course explores modern Arab and Muslim drama in English from three perspectives: contexts (literary, social, economics, and political); genres, trends, and styles; critical issues such as politics, wars, revolutions, gender, violence, class, and identity. However, the focus will be chiefly on diaspora, political violence, ritual, gender, and women''s rights.', 'SI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 205', 'Social Problems', 0.5, 'LEC', 'The specific social problems discussed will vary but will include problems of well-being, problems of inequality and power, and problems related to modernization.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 221', 'Research Methods', 0.5, 'LEC', 'An introductory survey of the research techniques commonly employed by sociologists, criminologists, and legal studies researchers. The formulation of research designs appropriate to various kinds of intellectual problems in social science is stressed.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 225', 'Games and Gamers', 0.5, 'LEC', 'This course examines games, gamers, and gaming culture as entry points to understanding the social implications of digital media technologies.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 306', 'Juvenile Justice', 0.5, 'LEC', 'An examination of theories of juvenile justice, juvenile law, and the structure and operations of juvenile systems, especially in Canada.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 320', 'Social Problems in a Global Context', 0.5, 'LEC', 'This course examines causes of and responses to critical social problems in different world regions with a focus on how these problems intersect with processes of globalisation. Topics include global economic inequality, global health, gender inequality, the environment, and war.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 324', 'Digital Cultures', 0.5, 'LEC', 'This course examines how digital play represents and structures social interaction and how games develop social norms, influence social consensus, and respond to deviance.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 413', 'Surveillance and Society', 0.5, 'SEM', 'An examination of the way monitoring technologies alter and shape social life in terms of security, fear, control, and vulnerability.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 461', 'Transnational Organized Crime', 0.5, 'SEM', 'This seminar course examines contemporary legal regimes surrounding transnational organized crime, terrorism and organized crime, and international money laundering.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('STAT 814', 'Systematic Review and Meta-Analysis', 0.5, 'LEC, TUT', 'This course will provide students with an overview of the rationale and stages involved in the conduct of a formal systematic review and meta-analysis of a well-defined clinical/health research question. The overarching aim is to provide students with the tools to critically appraise and conduct a systematic review and meta-analysis.  Students will largely work in pairs to progress through each step involved (with feedback from instructors at each stage) and to produce a final systematic review and meta-analysis to be presented/submitted at the end of the course.  
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('STV 210', 'The Computing Society', 0.5, 'LEC', 'This course examines the historical and current relationships between computer technology and society. It explores the impact and consequences of computing from a societal perspective, but also considers various nontechnical factors and values that have shaped computing technology and practice. The scope of the course will range from early mechanical aids, through the mid 20th century invention of electronic digital computers, to the networks and mobile applications of the 21st century. Technological studies relating to gender, education, employment, and war will be used as focal points. Material artifacts will form a core element of the course.', 'STV');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('WKRPT 200M', 'Work-term Report', 0.13, 'PRJ', 'A work-term report presenting in detail a technical project, activity, or analysis engaged by the student normally during the preceding work term. The report is evaluated on the basis of written communication skills and technical proficiency in the subject matter as demonstrated by the report. Work-term reports are required courses for the degree but their grades are not included in the average.', 'WKRPT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('WKRPT 300M', 'Work-term Report', 0.13, 'PRJ', 'A work-term report presenting in detail a technical project, activity, or analysis engaged by the student normally during the preceding work term. The report is evaluated on the basis of written communication skills and technical proficiency in the subject matter as demonstrated by the report. Work-term reports are required courses for the degree but their grades are not included in the average.', 'WKRPT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('WKRPT 400M', 'Work-term Report', 0.13, 'PRJ', 'A work-term report presenting in detail a technical project, activity, or analysis engaged by the student normally during the preceding work term. The report is evaluated on the basis of written communication skills and technical proficiency in the subject matter as demonstrated by the report. Work-term reports are required courses for the degree but their grades are not included in the average.', 'WKRPT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ANTH 241', 'Food as Culture', 0.5, 'LEC', 'The course addresses the topic of food from an anthropological perspective. It examines how the acquisition of food has shaped human biology and forms of social organization. Topics examined include the relationship of food to colonialism, political economy, ritual, and taboo.', 'ANTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 351', 'Management Issues in Community Pharmacy Practice', 0.25, 'LEC', 'This business course leverages concepts presented in PHARM 350 and applies them to the specific opportunities and challenges found in community practice settings. Topics that will be addressed include business planning and human resource management in the community pharmacy context.', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BME 285L', 'Engineering Biology Laboratory', 0.25, 'LAB', 'Laboratory experiments for students taking BME 285. [Offered: F]', 'BME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MEDVL 250R', 'The History of Islamic Civilization from Late Antiquity to 1300', 0.5, 'LEC', 'This course surveys the diverse history of Islamic societies from the seventh to the thirteenth century. The primary focus is on the central Islamic lands from Egypt to Iran. In addition to the standard narrative, the course also examines salient aspects of Islamic civilization on a thematic basis.', 'MEDVL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SI 241R', 'Sacred Spaces and Human Geographies: Literary Expressions', 0.5, 'LEC', 'Using the Muslim dimension as a central theme, this course explores the social, cultural, and political implications to be found in a range of postcolonial literatures from Africa, South Asia, and the Middle East. Students investigate and analyze a variety of issues and concerns manifest in these literatures: identities and native sensibilities, nationalism and politics, cultural memory, and issues of sacred space and place.', 'SI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ANTH 415', 'Archaeologies of Landscape', 0.5, 'SEM', 'This course examines the ways in which past peoples experienced, transformed, remembered, represented, and controlled landscapes. Readings and discussions emphasize both contemporary theoretical approaches and case studies from various settings in an effort to illuminate the social, political, economic, and ideological dimensions of landscape.', 'ANTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CS 383', 'Computational Digital Art Studio', 0.5, 'STU', 'An upper-level studio course to create computational projects that function as art works and aesthetic experiences. Students will work in interdisciplinary teams to combine computer science principles with fine art technical and conceptual skills. [Offered: W]', 'CS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('JAPAN 391R', 'Special Topics', 0.5, 'LEC, RDG, SEM', 'This course deals with advanced topics in Japanese language and literature. Students are expected to have at least an intermediate level of language proficiency.', 'JAPAN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KOREA 391R', 'Special Topics', 0.5, 'LEC, RDG, SEM', 'This course deals with advanced topics in Korean language and literature. Students are expected to have at least an intermediate level of language proficiency.', 'KOREA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ARCH 671', 'Technical Report', 0.5, 'SEM', 'Students will investigate and report on technical issues as they relate to the development of the Comprehensive Building Project in the Parallel Design Studio. Innovation and integration in architectural design will be stressed with respect to structure, building envelope, environmental systems, sustainable assessment system, health and life safety, movement systems, site planning and the integration of information technology.', 'ARCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AFM 344', 'Introduction to Business Analytics', 0.5, 'LEC, TST, TUT', 'This course introduces students to business analytics with a focus on problems that accounting and finance professionals face in the real world.', 'AFM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MNS 10', 'Materials and Nanosciences Seminar', 0, 'SEM', 'Lectures, workshops, or seminars focusing on academic and professional development [Offered: F, W]', 'MNS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 324', 'Integrated Patient Focused Care 8', 1, 'LEC, TUT', 'This is the eighth of nine sequential courses. Topics discussed though patient scenarios and assignments will focus on two to three body systems. [Offered: F]', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ACC 800', 'Directed Readings in Accounting', 0.5, 'RDG', 'This selected topics course is an intensive reading course on specific topics that are not available in the regular departmental course offerings. The course is supervised by a faculty member and normally requires students to undertake a systematic program of reading leading to the writing of a research paper. This course does not replace any of the required accounting PhD seminar courses.  Only one reading course can count toward the required program courses. Departmental/graduate officer consent is required.', 'ACC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PD 11', 'Processes for Technical Report Writing', 0.5, 'LEC', 'This course develops flexible and broadly applicable report writing skills by involving students in various stages of the writing process, from drafting to revision. Students develop context-specific content for their own technical report and learn strategies for peer review.', 'PD');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 330', 'Professional Practice 6', 0.5, 'LAB, LEC', 'Professional Practice 6 combines lecture and lab components and focuses on preparing students for their first years of professional life. [Offered: F]', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('BME 186', 'Chemistry Principles', 0.5, 'LEC, TUT', 'The stoichiometry of compounds and chemical reactions. Periodicity and chemical bonding. Energy changes in chemical systems. Electronic structure of atoms and molecules, correlation with the chemical reactivity of common elements, inorganic and organic compounds. Discussion of the structure, nomenclature and reactions of important classes of organic compounds. Stereochemistry and its role in reaction mechanisms. [Offered: W]', 'BME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 323', 'Integrated Patient Focused Care 7', 1, 'LEC, TUT', 'This is the seventh of nine sequential courses. Topics discussed through patient scenarios and assignments will focus on two to three body systems. [Offered: F]', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SI 250R', 'The History of Islamic Civilization from Late Antiquity to 1300', 0.5, 'LEC', 'This course surveys the diverse history of Islamic societies from the seventh to the thirteenth century. The primary focus is on the central Islamic lands from Egypt to Iran. In addition to the standard narrative, the course also examines salient aspects of Islamic civilization on a thematic basis.', 'SI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SI 251R', 'The History of Islamic Civilization from 1300-1800: The Islamic Gunpowder Empires', 0.5, 'LEC', 'This course studies in comparative fashion the rise, consolidation, and decline of the three major Islamic empires of the early modern period (c. 1300-1800): the Ottomans in Anatolia, the Balkans, and the Middle East; the Safavids in Iran; and the Mughals in India.', 'SI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('NE 250', 'Work-term Report 1', 0.13, 'PRJ', 'An engineering report based upon a technical project, activity, or analysis carried out by the student, normally during work-term employment following the 2A academic term. Evaluation is based upon a level of written communication, technical proficiency, and engineering analysis appropriate to a second-year engineering student. [Offered: S]', 'NE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 352', 'Management Issues in Pharmacy Practice in Organizations', 0.25, 'LEC', 'This business course leverages concepts presented in PHARM 350 and applies them to the specific opportunities and challenges found in institutional settings. Topics that will be addressed include business planning and human resource management in hospitals and long-term care facilities.', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHARM 353', 'Entrepreneurship in Pharmacy', 0.25, 'LEC', 'This business course leverages concepts presented in PHARM 350 and applies them to the specific opportunities and challenges facing entrepreneurs and/or individuals creating innovative solutions within more traditional pharmacy environments. Topics that will be addressed include business planning and relationship management for entrepreneurs.', 'PHARM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ANTH 221', 'Language and Society', 0.5, 'LEC', 'This course introduces students to the study of language and culture. Topics covered include language socialization, narrative, the politics of multilingualism, and hate speech.', 'ANTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EASIA 361R', 'Modern Chinese Literature (1917 - present day)', 0.5, 'LEC, SEM', 'This survey course explores modern Chinese literature beginning from 1917 and the New Culture movement. Selected works and relevant critiques are used to prompt discussion of cultural and literary topics, particularly of changes in Chinese culture and literature from the beginning of the 20th century to the present day.', 'EASIA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('AHS 107', 'Sociology of Activity, Health, and Well-being', 0.5, 'LEC, TUT', 'This course will focus on the social and cultural factors that influence leisure, activity, health and well-being in different settings and among different populations. Sociological literature will be used to examine activity, health, and well-being in the lives of individuals, groups, and society.', 'AHS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 206', 'Money and Banking 1', 0.5, 'LEC', 'This course offers an overview of the functioning of the financial system both in Canada and abroad. It includes discussions of money and inflation, financial assets, and financial institutions and intermediaries.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 207', 'Economic Growth and Development 1', 0.5, 'LEC', 'This course offers an overview of the enormous differences in living standards across countries and over time, and it considers how these can be traced to differences in economic productivity, investment, population, natural resources, government, inequality, and culture.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 212', 'Introduction to Game Theory', 0.5, 'LEC', 'Game theory analyzes the strategic interaction between agents. It has applications in various fields including economics, law, political science, biology, and computer science. This course introduces the basic tools of game theoretic analysis and examines applications such as auctions, business strategy, and curbing carbon emissions. The course includes an experiential learning component where students play strategic games against each other.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 255', 'Introduction to the Economics of Natural Resources', 0.5, 'LEC', 'This course uses the theory and tools of economics to explore key problems in natural resource use and management. The meaning and implications of natural resource scarcity are explored, as well as how the insights of economics can be used to guide policies to promote a more sustainable path for our future. The course examines issues of economic efficiency and equity in specific resource sectors such as energy resources, fisheries, forestry, and water.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 256', 'Introduction to Health Economics', 0.5, 'LEC', 'Why are some people healthy and others not? Why is health different from other goods and services? How do the many stakeholders interact in health care markets? Why do countries have different health care systems? This course introduces students to how the theories and tools of economics can be used when examining health, health care, and health care policies. Concepts such as supply and demand, uncertainty, and utility will be introduced in the context of health and health care markets. While international institutions may be discussed, the course will emphasize Canadian health care markets.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 261', 'Philosophy of Economics', 0.5, 'LEC', 'This course considers conceptual, methodological, foundational, and ethical issues in economic theory and practice. Questions include: What can philosophy teach us about economic methodology and justification? Are economic formalizations useful idealizations of human behaviour? Is economics a science? What role do values play in economic reasoning and policy-making?', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 262', 'History of Economic Thought', 0.5, 'LEC', 'This course studies approaches to economic problems in historical context. Authors examined may include Smith, Malthus, Ricardo, Marx, J.S. Mill, Walras, Marshall, Veblen, Keynes, Hayek, Aumann, Debreu, and Stiglitz.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 290', 'Models of Choice in Competitive Markets', 0.5, 'LEC', 'Choice lies at the heart of all economic models. This course focuses on choice by consumers and firms. It explains the notion and use of utility functions and budgets, and shows how their interaction allows economists to make predictions about behaviour. The constrained maximization techniques from ECON 211 are used extensively, and extended to situations covering uncertainty (risk) and time. Profit maximization by firms is explored.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 306', 'Macroeconomics', 0.5, 'LEC', 'This course introduces students to the analysis of inter-temporal trade-offs in macroeconomics and macroeconomic policy. Its main objective is to show how economists use economic theory to analyze the role of incentives and general equilibrium considerations in the macro-economy. Applications include national savings and investment, business cycles, and monetary and fiscal policy.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 322', 'Econometric Analysis 1', 0.5, 'LEC, TUT', 'This course covers the core topics needed to estimate linear models using ordinary least squares and to interpret estimates for cross-sectional data. Students will learn to interpret the coefficients of linear models for continuous and discrete regressors, to conduct reliable inference for different specifications of the error term, and to determine which model is the most suitable among the class of linear models. It concludes with an introduction to the method of instrumental variables. Students will be introduced to a statistical software package and will be required to complete regular computer-based assignments throughout the course that either: (i) simulate the statistical distribution of the least squares estimator under an assumed model; or (ii) estimate the parameters of an assumed model using a sample of data from the real world.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 323', 'Econometric Analysis 2', 0.5, 'LEC, TUT', 'This course covers the most important methods used in applied economics research beyond the least-squares estimator. It starts by exploring solutions to the endogeneity problem in detail, emphasizing proper ways of conducting causal inference. It extends the methods covered in ECON 322 to the case in which the data are observed over time. Students will learn how to estimate and interpret dynamic models and how these models affect our ability to do inference. The course also covers methods for data in which the response variable is either qualitative, with or without multiple levels, or count data. For that purpose, it introduces students to maximum likelihood estimation, and the estimation of models by probit, logit, and Poisson regressions. Assignments have the same data-based focus as in ECON 322.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 366', 'Gender and Economics', 0.5, 'LEC', 'This course explores historical trends and economic theories and models to investigate the development of gender norms and changes in those norms across time. It questions whether physiological differences, economic models (e.g., comparative advantage, specialization, or discrimination) or social constructs can explain gender differences in roles within the family, education, work, pay, and poverty and the changes in these differences across time. Social policies designed to diminish gender differences will be scrutinized. Some topics/questions that may be addressed include the rise in labour force participation of women post World War II; occupational and wage differences between men and women; changing educational attainment; and how men and women make decisions regarding marriage, fertility, child care, elder care, and divorce.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 391', 'Equilibrium in Market Economies', 0.5, 'LEC', 'Central to the study of economics is the concept of an equilibrium in which the actions of individuals are not only individually optimal, but are also consistent with each other; such as when the total amount of a good that individuals wish to purchase equals the total amount available. This course introduces students to the notion of equilibrium for a market and market economy. The properties of such equilibria are explored, both in terms of model logic (existence, uniqueness) as well as in terms of the desirability of the equilibrium outcomes (efficiency, welfare). Partial equilibrium (in a single market) as well as general equilibrium (in all markets at once) are covered for models with endowments, production, uncertainty, and time.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 392', 'Strategic Situations and Welfare Economics', 0.5, 'LEC', 'Many economic decisions are made in circumstances where there are no competitive markets. This course introduces students to individual and group choice in such settings. It covers how to model such settings as "games" in both the extensive and strategic form, and introduces the key equilibrium concepts associated with these models. An introduction to considerations in group decision making via cooperative game theory and welfare economics is provided in the last part of the course.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 393', 'Market Failures', 0.5, 'LEC', 'This course explores the many ways in which the assumptions of the perfectly competitive model are violated in the real world, and the consequences of these market failures for consumers and firms. The course examines potential government intervention to "fix" the failures as well as the limits of government intervention. Topics include monopoly/monopsony, externalities, the tragedy of the commons, public goods, asymmetric information, and incomplete information.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 406', 'Money and Banking 2', 0.5, 'LEC', 'This course examines the monetary and financial aspects of the macro-economy. It prepares students to conduct analyses of problems and policies in monetary economics. Topics may include analyses of the banking system, financial crises, and monetary policy.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 407', 'Economic Growth and Development 2', 0.5, 'LEC', 'The goal of this course is to understand the enormous differences in living standards across countries and over time. It uses economic analysis to understand the sources of investment and innovation, and the role of economic, social, and political institutions in economic growth and development.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 408', 'Business Cycles', 0.5, 'LEC', 'This course prepares students to conduct analyses of problems and policies associated with macroeconomic fluctuations. It examines the empirical features of business cycles, and discusses how macroeconomic models help to understand business cycles and to assess the consequences of alternative macroeconomic policies.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 409', 'Workers, Jobs, and Wages', 0.5, 'LEC', 'This course examines the role of labour markets in the macro-economy. It focuses on the analysis of aggregate employment, unemployment, labour force participation, and earnings inequality. The main goal of the course is to understand current labour-market outcomes both in Canada and around the world.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 423', 'Time Series Econometrics', 0.5, 'LEC', 'This course introduces students to popular methods and practices of analyzing data that exhibit dependence over time. The topics may cover fundamental linear time series models (autoregressive processes, moving average processes and their generalizations), modelling time-varying volatility (the autoregressive conditional heteroskedasticity or ARCH family of models and stochastic volatility models), multivariate time series analysis, non-stationary models, and ongoing topics in modern time series econometric research. The course is designed to balance the introduction of statistical theory and applications with empirical data. A main component of this course consists of practical exercises and an empirical project with real data.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 474', 'Numerical Methods for Economists', 0.5, 'LEC', 'This course covers important topics related to scientific computing through applications in microeconomics, macroeconomics, and econometrics. The topics include floating point arithmetic, nonlinear equations, constrained and unconstrained optimization, numerical derivatives and numerical integration, differential equations, and dynamic models. Students will also develop their programming skills by learning strategies to write structured and efficient programs.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 491', 'Advanced Microeconomics', 0.5, 'LEC', 'This course prepares students for graduate-level theory courses. Students will be exposed to a selection of topics treated with the mathematical rigour required for graduate work.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECON 496', 'Advanced Macroeconomics', 0.5, 'LEC', 'This course provides students with the methodological principles that underlie modern analyses of the macro-economy, and to prepare students for graduate-level macroeconomic theory courses.', 'ECON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('FINE 217', 'Art of the 18th Century in Europe', 0.5, 'LEC, SEM', 'This course is designed to introduce students to the culture of 18th century Europe. Particular attention is paid to France and Britain but these countries are discussed within the wider context of Europe and, at times, much of the world. By looking at the art, artists, patrons, and collectors, some of the issues that will be discussed include cosmopolitanism, nationalism, empire-building, "taste", consumerism, and women in society.', 'FINE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('FINE 259', 'Aspects of European Cinema', 0.5, 'LAB, SEM', 'This course offers a wide-ranging set of approaches to historical, contextual, and practical topics in the history of European cinema production.', 'FINE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('FINE 307', 'Advanced Topics in Studio', 0.25, 'STU', 'This course provides Fine Arts students with an opportunity to study a range of advanced topics that will explore, expand, and refine personal areas of interest in studio practice. Advanced topics will be announced from term to term.', 'FINE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('FINE 308', 'Honours Studio/Seminar', 0.25, 'STU', 'This is an advanced Fine Arts studio course with an emphasis on development of the student''s individual and independent research interests. Students will be guided by faculty through critiques as well as open forums.', 'FINE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('FINE 403', 'Honours Concept and Research', 0.5, 'FLD, RDG, SEM', 'This seminar and fieldwork course explores aspects of theory, art history, and visual culture that are relevant to each student''s areas of interest and contemporary art practice. Gallery visits and artists'' talks are components of this course.', 'FINE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('FR 250', 'Intermediate Spoken French', 0.5, 'LEC', 'A course intended to develop the oral and aural skills.', 'FR');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('FR 296', 'French Culture & Literature: Origins to 1715', 0.5, 'LEC', 'A survey of French culture and literature from their origins to 1715.', 'FR');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GENE 23E', 'Topics for Natural Science Elective Courses Taken on Exchange by Electrical Engineering Students', 0.5, 'LEC', 'Courses taken at foreign universities by University of Waterloo engineering students while enrolled in an international exchange institution, and reserved for courses without equivalents at the University of Waterloo. To earn credit the exchange courses will include the exploration of the physical and chemical interactions of the natural world, and the systematic observation and understanding of physical and natural phenomena through analytical and/or experimental techniques. These courses are treated as natural science electives in the student''s plan. Such courses are reported on the student''s transcript with their original titles in English showing as the topic. The grades for these courses will be either CR or NCR.', 'GENE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GENE 23K', 'Topics for Natural Science Elective Courses Taken on Exchange by Civil Engineering Students', 0.5, 'LEC', 'Courses taken at foreign universities by University of Waterloo engineering students while enrolled in an international exchange institution, and reserved for courses without equivalents at the University of Waterloo. To earn credit the exchange courses will include the exploration of the physical and chemical interactions of the natural world, and the systematic observation and understanding of physical and natural phenomena through analytical and/or experimental techniques. These courses are treated as natural science electives in the student''s plan. Such courses are reported on the student''s transcript with their original titles in English showing as the topic. The grades for these courses will be either CR or NCR.', 'GENE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GENE 23Q', 'Topics for Natural Science Elective Courses Taken on Exchange by Computer Engineering Students', 0.5, 'LEC', 'Courses taken at foreign universities by University of Waterloo engineering students while enrolled in an international exchange institution, and reserved for courses without equivalents at the University of Waterloo. To earn credit the exchange courses will include the exploration of the physical and chemical interactions of the natural world, and the systematic observation and understanding of physical and natural phenomena through analytical and/or experimental techniques. These courses are treated as natural science electives in the student''s plan. Such courses are reported on the student''s transcript with their original titles in English showing as the topic. The grades for these courses will be either CR or NCR.', 'GENE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GENE 23S', 'Topics for Natural Science Elective Courses Taken on Exchange by Software Engineering Students', 0.5, 'LEC', 'Courses taken at foreign universities by University of Waterloo engineering students while enrolled in an international exchange institution, and reserved for courses without equivalents at the University of Waterloo. To earn credit the exchange courses will include the exploration of the physical and chemical interactions of the natural world, and the systematic observation and understanding of physical and natural phenomena through analytical and/or experimental techniques. These courses are treated as natural science electives in the student''s plan. Such courses are reported on the student''s transcript with their original titles in English showing as the topic. The grades for these courses will be either CR or NCR.', 'GENE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 325', 'Geographies of Health', 0.5, 'LEC, TUT', 'A critical examination of major themes related to the distribution, diffusion, determinants and delivery of health and health care with a particular emphasis on the roles of space, place and environment.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HLTH 601', 'Lifespan Approaches to Disease Prevention and Health Promotion', 0.5, 'LEC', 'This course examines issues in health and disability from a multidisciplinary lifespan perspective. An integrated approach is taken that considers biological, behavioral, and social factors relevant to health and disability at different ages, and locates issues of prevention, treatment, management, and policy within a broadly-based public health orientation.', 'HLTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GER 200', 'Transcultural Studies', 0.5, 'LEC', 'This course provides an introduction to transcultural views of literature, film, language, and literature and media culture (e.g., fairy tales and their adaptation in other cultures, authors who work in multiple languages, transcultural comics, multilingual and transcultural films, local cultures'' interaction with global arts and media scenes, etc.).', 'GER');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GERON 310', 'Development, Aging and Health', 0.5, 'LEC, TUT', 'The physiology of human growth, development and aging is examined, with special reference to the influence of diet, environment, exercise and disease on the normal processes.', 'GERON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GERON 320', 'Psychosocial Perspectives on Lifespan Development and Health', 0.5, 'LAB, LEC', 'This course will focus on the normative psychosocial aspects of development of the individual and their influence on the individual''s physical and mental health and well-being. Through the use of a developmental systems, lifespan approach, the course will emphasize the life-long process of development.', 'GERON');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 421', 'Special Topics in History', 0.5, 'DIS, LEC', 'A special study of a selected topic in history. Please see course instructor for details.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 422', 'Special Topics in History', 0.5, 'SEM', 'This seminar is a special study of a selected topic in history. Please see course instructor for details.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HIST 450', 'The History Capstone', 1, 'SEM', 'The capstone challenges students with an opportunity to synthesize and showcase, at a high level of achievement, the disciplinary skills and knowledge they have gained during the course of their studies in History. It encourages students to pursue individual research interests and presentation formats as limited only by historical methodology, academic rigour, and the consent of the instructor.', 'HIST');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HLTH 204', 'Quantitative Approaches to Health Science', 0.5, 'LEC, TUT', 'An introduction to descriptive and basic inferential statistics and their application to Health Research. Various statistical techniques commonly used in Health Research are covered.', 'HLTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HLTH 230', 'Introduction to Health Informatics', 0.5, 'LEC', 'Health informatics is the multidisciplinary field that aims to optimize the collection, storage, organization, retrieval, analysis, and communication of health data, information, and knowledge. Fueled by recent advances in information technology, informatics increasingly plays a vital role in many areas of health. This introductory course gives an overview of health informatics by discussing modern informatics issues and state-of-the-art applications in various sub-fields within health informatics including bioinformatics, medical informatics, and public health informatics.', 'HLTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HLTH 280', 'Applied Public Health Ethics', 0.5, 'LEC', 'This course explores ethical issues in health sciences, emphasizing population and public health. The course begins by considering canonical ethical theories and frameworks for applying these theories to population health. Specific topics in population health, including the conflict between public health and individual autonomy, the just distribution of health resources, and responsibility for health outcomes will then be discussed. The overall objective of the course is to provide students with tools to discuss and assess ethical arguments and to form their own views on issues within population health.', 'HLTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HLTH 304', 'Health Communication', 0.5, 'LEC', 'This course presents an overview of theories, issues, and compelling directions in health communication, with attention to health literacy, the role of the media, public health campaigns, provider-patient communication, and risk communication. Topics may highlight: social marketing; media advocacy; use of mobile phones, social media, and the Internet for health communication; entertainment education; communication in health care settings.', 'HLTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HLTH 320', 'Psychosocial Perspectives on Lifespan Development and Health', 0.5, 'LAB, LEC', 'This course will focus on the normative psychosocial aspects of development of the individual and their influence on the individual''s physical and mental health and well-being. Through the use of a developmental systems, lifespan approach, the course will emphasize the life-long process of development.', 'HLTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HLTH 370', 'Ecological Determinants of Health', 0.5, 'LEC', 'This course will examine the links between ecosystems, global ecological change, and the health and well-being of human communities. It will review the myriad ways in which the Earth, as a living system, is the ultimate determinant of human health. The course is divided into three parts: in the first part key environmental changes are addressed, including climate change, ecotoxicity, resource depletion, species extinction, and stresses on ocean systems as well as the social and human forces that drive these changes. The second part of the course focuses on the implications of these ecological changes and population health. The third part of the course will address potential solutions.', 'HLTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENBUS 314', 'Sustainable Business Models', 0.5, 'LEC, SEM', 'Development of alternative business models has important implications for sustainability. This course will explore the emergence of entirely new sustainable business models along with the revitalization of ''old'' alternative business models from the past.', 'ENBUS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HLTH 412', 'Comparative Health Systems', 0.5, 'LEC', 'This course addresses the history and development of health systems, and comparison of the social ethics, organization, and financing of different national health systems. Topics include: the design of health systems; strengths and weaknesses of alternative systems for public health and health care delivery; current strategies for health system reform in resource-rich and resource-constrained nations; and ethical issues associated with health system reform. This course uses a case-study, problem-based approach.', 'HLTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HLTH 603', 'Health Policy', 0.5, 'LEC', 'A critical analysis of health policy formulation, implementation and evluation related to population health initiatives and health care delivery. The course will include discussion of the role of various regional, provincial, and national agencies in health care policy formation. It will examine various health care systems, their funding and how the Canadian healthcare system compares with systems in other countries. Prerequisite: Consent of instructor.', 'HLTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('INTEG 420A', 'Senior Research Project A', 0.5, 'PRJ, SEM', 'Each student will work on a short research project under the dual direction of a member of the Knowledge Integration Department and an advisor from a discipline related to the topic. The results of this project will be presented in thesis form, and will be critically examined by members of this and, where pertinent, other departments.', 'INTEG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('INTEG 420B', 'Senior Research Project B', 1, 'PRJ, SEM', 'A continuation of INTEG 420A.', 'INTEG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 310', 'Development, Aging and Health', 0.5, 'LEC, TUT', 'The physiology of human growth, development and aging is examined, with special reference to the influence of diet, environment, exercise and disease on the normal processes.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KIN 332', 'Research Design and Statistics in Kinesiology', 0.5, 'LAB, LEC', 'An introduction to research design and statistics and its application to Kinesiology and clinical health practice. A major focus is identifying appropriate research methods (including study design, sampling, measurement, and statistical analysis) for a given research question.', 'KIN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MSCI 391', 'Work-term Report', 0.13, 'PRJ', 'A work-term report presents in detail a technical project, activity, or analysis engaged by the student normally during the preceding work term, related to management engineering. The report is evaluated on the basis of written communication skills and technical proficiency in the subject matter as demonstrated by the report. Reports are due on the 10th day of lectures for the academic term in which the report is required. Any resubmissions granted are due by the "Lectures End" date. [Offered: W]', 'MSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MSCI 392', 'Work-term Report', 0.13, 'PRJ', 'A work-term report presents in detail a technical project, activity, or analysis engaged by the student normally during the preceding work term, related to management engineering. The report is evaluated on the basis of written communication skills and technical proficiency in the subject matter as demonstrated by the report. Reports are due on the 10th day of lectures for the academic term in which the report is required. Any resubmissions granted are due by the "Lectures End" date. [Offered: F]', 'MSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MSCI 491', 'Work-term Report', 0.13, 'PRJ', 'A work-term report presents in detail a technical project, activity, or analysis engaged by the student normally during the preceding work term, related to management engineering. The report is evaluated on the basis of written communication skills and technical proficiency in the subject matter as demonstrated by the report. Reports are due on the 10th day of lectures for the academic term in which the report is required. Any resubmissions granted are due by the "Lectures End" date. [Offered: S, first offered Spring 2020]', 'MSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MTE 309', 'Introduction to Thermodynamics and Heat Transfer', 0.5, 'LEC, TST, TUT', 'Macroscopic approach to energy analysis. Energy transfer as work and heat, and the First Law of thermodynamics. Properties and states of simple substances. Control-mass and control-volume analysis. The essence of entropy, and the Second Law of thermodynamics. The Carnot cycle and its implications for practical cyclic devices. Introduction to heat transfer by conduction, convection, and radiation. Basic formulation and solution of steady and transient problems. Issues relevant to the cooling of electrical devices. [Offered: W,S]', 'MTE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MUSIC 110', 'Music in Cultural Contexts', 0.5, 'LEC', 'An intensive study of music from around the globe and throughout the ages, employing several approaches in studying how music reflects and responds to its diverse social, political, and historical contexts.', 'MUSIC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MUSIC 232', 'Music as a Global Phenomenon', 0.5, 'LEC', 'A survey of music studied in a cross-cultural perspective with regard to its sound, performance practices, meanings, and functions from selected world traditions found in the diverse cultures of Africa, Asia, Latin America, North America, Europe, and the Middle East.', 'MUSIC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MUSIC 233', 'Musical Rhythm in Global Perspective', 0.5, 'LEC', 'A multidisciplinary survey of rhythm in selected musics examined in terms of scientific, symbolic, and experiential knowledge that connects it to the concepts of time, history, biology, evolution, anthropology, philosophy, psychology, and contemporary globalized culture.', 'MUSIC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MUSIC 262', 'Music for Vocal Ensemble', 0.5, 'LEC', 'A survey of the great masterpieces for vocal ensemble from Medieval times to the present studied through reading, lecture, and listening. Principle choral genres of each historical era are studied and discussed including works performed by the Grand Philharmonic Choir during the term.', 'MUSIC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MUSIC 333', 'Music and Landscape', 0.5, 'SEM', 'This course explores how landscape is represented in music from Vivaldi and Beethoven to popular genres and Canadian music. Topics include the role of landscape and music in cultural identity, ways in which music evokes space and place, music and the environment, soundscape, and site-specific music.', 'MUSIC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MUSIC 335', 'Perspectives in Music and Peace', 0.5, 'SEM', 'This course examines music''s role in peace and conflict from a variety of perspectives through listening, reading, discussion, and using examples from around the world. Topics include the use of music in dissent and resistance, and as an instrument of propaganda, conflict, and peacebuilding.', 'MUSIC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MUSIC 392', 'Special Topics in Global Music', 0.5, 'SEM', 'A study of a selected topic in global music. Consult department for details.', 'MUSIC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('NE 345', 'Photonic Materials and Devices', 0.5, 'LEC', 'Wave nature of light, refractive index and dispersion, group velocity, irradiance and Poynting vector, Snell''s law, Fresnel''s Equation, antireflection coatings, absorption of light, temporal and spatial coherence, dielectric waveguides and optical fibers, planar waveguides, dispersion in waveguides; light emitting diodes (LED), pn junction, LED materials, stimulated emission, lasers, photodetectors, photovoltaic devices, solar cells. [Offered: F]', 'NE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('NE 350', 'Work-term Report 2', 0.13, 'PRJ', 'An engineering report based upon a technical project, activity, or analysis carried out by the student, normally during work-term employment following the 2B academic term. Evaluation is based upon a level of written communication, technical proficiency, and engineering analysis appropriate to a third-year engineering student. [Offered: F]', 'NE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('NE 381', 'Introduction to Nanoscale Biosystems', 0.5, 'LEC', 'Specific aspects of biosystems required for the engineering of nanobiotechnological applications: topics to be covered may include surface and bulk science concepts needed for the development of lab-on-chip systems and those aspects of molecular biology of the cell necessary for application to medical diagnostics. Elements of design required for the development of modern instrumentation may also be covered, thereby providing a solid foundation for more advanced topics and applications. [Offered: F]', 'NE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('NE 450', 'Work-term Report 3', 0.13, 'PRJ', 'An engineering report based upon a technical project, activity, or analysis carried out by the student, normally during work-term employment following the 3B academic term. Evaluation is based upon a level of written communication, technical proficiency, and engineering analysis appropriate to a fourth-year engineering student. [Offered: W]', 'NE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PACS 315', 'Engineering and Peace', 0.5, 'SEM', 'This course is built on the conviction that the kinds of problems engineers seek to address, and the ways they seek to address them, should matter for anyone interested in advancing peace in the world. Topics include historical connections between the discipline of engineering and warfare, understanding the engineering method and mindset, and technological frontiers for peacebuilding.', 'PACS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('NE 454A', 'Nano-instrumentation Laboratory 1', 0.25, 'LAB', 'Application of experimental tools and techniques in nano-instrumentation. Experimental exercises involve circuit simulation and design, circuit prototyping, design of a driver circuit for a quartz crystal microbalance (QCM), printed circuit board (PCB) design and layout optimization, and the use of various characterization instrumentation. [Offered: F]', 'NE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('NE 454B', 'Nano-electronics Laboratory 1', 0.25, 'LAB', 'Application of experimental tools and techniques involved in nanotechnology. Experimental exercises may involve simulation, design, optimization of micro-electro-mechanical-system (MEMS) devices, and the generation of a mask layout. [Offered: F]', 'NE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('NE 454C', 'Nanobiosystems Laboratory 1', 0.25, 'LAB', 'Application of experimental tools and techniques in nanobiotechnology. Experimental exercises may involve simulation and design, optimization, nanoparticle formulation reactions, microfluidics, and the use of various characterization instrumentation. [Offered: F]', 'NE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('NE 454D', 'Nanostructured Materials Laboratory 1', 0.25, 'LAB', 'Application of experimental tools and techniques in nanomaterials. Experimental exercises may involve design, use of image analysis software, optimization, electrodeposition, encapsulation and templating, synthetic chemistry protocols, and the use of selected characterization instrumentation. [Offered: F]', 'NE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('NE 455A', 'Nano-instrumentation Laboratory 2', 0.25, 'LAB', 'Application of experimental tools and techniques in nano-instrumentation. Experimental exercises involve printed circuit board (PCB) assembly and soldering, measurement of the formation of a nonanethiol self- assembled monolayer, determination of the partition coefficient for a solvent vapour into a monolayer-protected-cluster film deposited on a quartz crystal microbalance (QCM), and the use of various characterization methods. [Offered: W]', 'NE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('NE 455B', 'Nano-electronics Laboratory 2', 0.25, 'LAB', 'Application of experimental tools and techniques employed in nanotechnology. Experimental exercises may involve microfabrication (photolithography, film deposition, and etching) and testing of micro-electro-mechanical-system (MEMS) devices. [Offered: W]', 'NE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('NE 455C', 'Nanobiosystems Laboratory 2', 0.25, 'LAB', 'Application of experimental tools and techniques employed in Nanobiotechnology. Experimental exercises may involve investigation of microbial culture protocols, biosensor applications, and the use of various characterization techniques. These exercises also stress the need for safe handling of micro-organisms and biomaterials. [Offered: W]', 'NE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('NE 455D', 'Nanostructured Materials Laboratory 2', 0.25, 'LAB', 'Application of experimental tools and techniques employed in nanomaterials. Experimental exercises investigate catalytic activity enhancement by nanoparticles in fuel cell reactions. This includes the use of synthetic chemistry protocols to prepare nanoparticles on multi-walled carbon nanotubes. The exercise also stresses safe handling of nanomaterials and the use of various characterization methods. [Offered: W]', 'NE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PACS 623', 'Directed Reading in Peace and Conflict Studies', 0.5, 'RDG', 'This reading course gives students space to study literature that explores the full theoretical and contemporary scope of readings pertaining to the Peace and Conflict Studies field. These readings will be in conjunction with coursework. Students must seek out the approval of a faculty member who is willing to supervise them and have the approval of the department chair. Students must write a topic proposal and outline of coursework prior to obtaining permission to enroll in the course.', 'PACS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 205', 'Philosophy of Economics', 0.5, 'LEC', 'This course considers conceptual, methodological, foundational, and ethical issues in economic theory and practice. Questions include: What can philosophy teach us about economic methodology and justification? Are economic formalizations useful idealizations of human behaviour? Is economics a science? What role do values play in economic reasoning and policy-making?', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 206', 'Philosophy of Sport', 0.5, 'LEC', 'This course uses the tools of philosophy to ask important questions about sport, such as: What is sport? What is the value of sport for participants or fans? Are violent sports unethical? Should performance enhancing drugs be banned? What is luck, and how is it different from skill?', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 251', 'Metaphysics and Epistemology', 0.5, 'LEC', 'This course introduces philosophical theories about the nature of reality (metaphysics), how we gain knowledge of it (epistemology), and of why such questions matter. Topics may include skepticism, the nature of rationality, knowledge of other minds, realism and anti-realism, and free will and determinism.', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 283', 'Great Works: Ancient and Medieval', 0.5, 'LEC', 'A historical survey of ancient and medieval philosophy in the Western tradition.', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 284', 'Great Works: Modern', 0.5, 'LEC', 'A historical survey of modern philosophy in the Western tradition.', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 356', 'Intelligence in Machines, Humans, and Other Animals', 0.5, 'LEC', 'A comparison of the representations and processes that produce intelligent performance, such as problem-solving, learning, and language in natural and artificial systems.', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PMATH 333', 'Introduction to Real Analysis', 0.5, 'LEC', 'The purpose of the course is to present the familiar concepts of calculus at a rigorous level and to provide students who took the MATH 137/MATH 138/MATH 237 sequence with the background needed to be successful in PMATH 351 and PMATH 352. Topics discussed include the completeness properties of the reals; the density of the rationals; the topology of real n-dimensional space: open and closed sets, connectedness, compactness (by open covers), the Heine-Borel theorem, completeness; sequences in real n-dimensional space: convergence, Cauchy sequences, subsequences, the Bolzano-Weierstrass theorem; multivariable functions: limits, point-wise and uniform continuity, the extreme value theorem, uniform convergence of sequences of functions, Taylor''s theorem, term-by-term differentiation of power series; integration in real n-dimensional space: Riemann integrability, Fubini''s theorem for continuous functions on rectangles, term-by-term integration of power series.', 'PMATH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PSYCH 389', 'Social Science Advanced Research Methods Topics', 0.5, 'LAB, SEM', 'Topics explore social science research methods in Psychology. Consult departmental listings for upcoming topics, which may include naturalistic observation, factorial experiment design, behavioural coding, survey/questionnaire construction, interviewing, and/or linguistic analysis. Activities may include research proposals, group and/or individual projects (e.g., lab experience, data collection), research reports, critiques of published/proposed research, and student presentations. [Offered: F]', 'PSYCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PSYCH 390', 'Natural Science Advanced Research Methods Topics', 0.5, 'LAB, SEM', 'Topics explore natural science research methods in Psychology. Consult departmental listings for upcoming topics, which may include methods for studying psychophysiology, neuroanatomy, and/or cognitive processes (e.g., perception, attention, memory, information-processing, decision-making). Activities may include research proposals, group and/or individual projects (e.g., lab experience, data collection), research reports, critiques of published/proposed research, and student presentations. [Offered: W and/or S]', 'PSYCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PSYCH 466', 'Emergent Literacy', 0.5, 'PRA, SEM', 'Emergent literacy refers to the knowledge and skills children acquire from birth on through the preschool years that are important to the development of literacy (reading and writing). Students will learn about emergent literacy via a once-weekly seminar component and a once-weekly unpaid practicum placement at a local elementary public school where students will have the opportunity to read one-on-one with children who are at the beginning stages of reading. The practicum component will involve a commitment of up to 30 hours during the formal lecture period. Students wishing to enter the course must obtain a police check prior to the second week of classes. Transportation to the apprenticeship/volunteer setting is the student''s responsibility. Please review the course application form for information on safety for students on unpaid work placement and insurance responsibilities. Course application forms and further details are available on the Psychology undergraduate website. Paid or volunteer positions that are obtained outside the context of this course are not eligible for credit in this course.', 'PSYCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PSYCH 470', 'Special Topics in Applied Psychology', 0.5, 'LEC', 'Topics cover practical applications of theory and research in one or more of the six areas of psychology. Consult departmental listings for upcoming topics. Topics may include applications of psychology to health, well-being, law, education, policy analysis, management, marketing, regulation, systems design, community organizing, conflict resolution, and/or communications.', 'PSYCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('REC 105', 'Interdisciplinary Approaches to Leisure', 0.5, 'LEC', 'This course examines the intersection of disciplines and methodologies that are the foundation of leisure studies. The major principles of each discipline and how they inform our understanding of leisure will be discussed. Some of the disciplinary foundations considered may include economics, geography, history, philosophy, planning, psychology, sociology, and the arts.', 'REC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('REC 120', 'Program Management and Evaluation', 0.5, 'LEC', 'The scope of recreation program design and delivery is examined with particular emphasis on needs assessment, planning, implementation, and evaluation. This course emphasizes the application of the various management principles required for service delivery. A field trip fee estimated at $125 per student may be required.', 'REC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('REC 201', 'Leisure and Social Justice', 0.5, 'LEC', 'This course focuses on what social justice means in leisure studies and explores why social justice matters by introducing issues related to inclusivity, privilege, power, marginalization, and oppression. Areas of exploration may include but are not limited to age, class and poverty, (dis)ability, non-human interactions, race and ethnicity, sexual and gender identities, and status.', 'REC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('REC 213', 'Principles of High Performance Organizations in Recreation and Sport', 0.5, 'LEC', 'This course introduces students to the building blocks of high performance organizations. Principles such as social responsibility and innovation will guide the discussions. Specific emphasis will be given to leadership, critical thinking, and the development of problem solving skills to address societal and business challenges across multiple sectors.', 'REC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('REC 218', 'Social Entrepreneurship for Change', 0.5, 'SEM', 'This course examines social entrepreneurship as a means for improving individual and community well-being. Focus will be placed on learning and implementing tools which businesses, citizens, and consumers can use to address social issues. Students will complete the course with an understanding of their personal responsibility and role as community/ global citizen which will aid them in their career development. Particular emphasis may be placed on the role of social entrepreneurship in addressing issues related to social justice, inclusion, and sustainable management practices.', 'REC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SDS 449R', 'Race and Gender Equality', 0.5, 'SEM', 'This course examines stereotypes and prejudices from a social sciences perspective, focusing on understanding and applying the research in schools, the workplace, and the home. Topics include overt and subtle prejudice, situational barriers to stigmatized groups'' success, and interventions that promote equality by reducing prejudice and its negative effects.', 'SDS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SMF 101', 'Introduction to Relationships and Families', 0.5, 'LEC', 'This course provides an overview of couple, marital, and family relationships from a broad, interdisciplinary perspective.', 'SMF');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SMF 200', 'Special Topics in Sexualities, Relationships, or Families', 0.5, 'LEC', 'This course introduces students to special topics in sexualities, relationships, and/or families.', 'SMF');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SMF 211', 'Dynamics of Dating', 0.5, 'LEC', 'This course examines the dynamics of intimate relationships in the context of the modern, Western construct of dating. Topics explored may include long-distance relationships, polyamorous relationships, online dating, hook-up culture, shifting romantic and family formation dynamics, modern communications and technology, and the role of pop culture in dating trends.', 'SMF');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SMF 212', 'Navigating Sexuality and Relationships in Mid/Later Life', 0.5, 'LEC', 'This course reviews the process of navigating relationships and sexuality as persons age. Topics may include physical/biological changes, desire, youth-focused culture, relationship dissolution, dating, and technologies.', 'SMF');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SMF 213', 'Sexual Health and Well-Being', 0.5, 'LEC', 'This course offers a theoretical and empirical examination of sexuality from a health perspective. Approaches to counselling, prevention, and treatment will be discussed. Topics may include sexually transmitted infections (STI) and pregnancy prevention, pregnancy and post-partum health, healthy romantic and sexual relationships, chronic and long-term illnesses, and sexual cancers.', 'SMF');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SMF 214', 'Constructing Erotics', 0.5, 'LEC', 'This course examines the social construction of the erotic, in particular how culture, politics, religion, literature, economics, medicine, or technology interact to regulate the definition and performance of sexuality across cultures. Topics may include the sexual body (e.g., beauty and desire), the regulation of sex (e.g., taboos, laws, religion), and other cultural constructions of sexuality (e.g., sexual education).', 'SMF');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SMF 215', 'Sexuality and Popular Culture', 0.5, 'LEC', 'This course examines how sexuality is depicted in, shaped by, and contested in popular culture. It introduces different theories in the study of popular culture and sexuality through a range of historical and contemporary texts and practices.', 'SMF');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SMF 216', 'Sexual Pleasure', 0.5, 'LEC', 'This course offers a theoretical and empirical examination of the pleasurable and recreational aspects of human sexuality. Topics may include optimal sexuality, romantic passion, sexual techniques, sex toys, BDSM, and sexual role-playing. A range of pleasure discourses and practices (beyond those focused on the genitalia) are addressed.', 'SMF');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PSYCH 748', 'Self-Regulation and Motivation', 0.5, 'SEM', 'This seminar provides an overview of classic and contemporary social psychological approaches to the study of self-regulation and motivation.  We will spend some time trying to answer the questions that necessarily begin any study of self-regulation and motivation, "what is motivation? What is it that people really want?," examining several major theories that have suggested different answers to those questions.  We will also examine major theories of goal structure and the dynamics of goal pursuit.  We''ll use this foundation to think broadly about what social psychological research has to say about how people manage (and mismanage) life''s opportunities and challenges, especially how individuals respond to success and failure, persist in the face of challenge, and resist temptation.', 'PSYCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SMF 491', 'Practicum and Applied Theory', 0.75, 'PRA, SEM', 'This course involves the continuation of the SMF 490 practicum placement along with weekly seminar meetings that focus on integration of theory and practice.', 'SMF');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SMF 499A', 'Thesis - Part 1', 0.75, 'PRJ', 'Under supervision of a faculty member, students normally will review literature, design a study, present a research proposal, analyze data, write a scholarly research paper, and deliver an oral presentation based on the paper (e.g., at the SMF Research Symposium).', 'SMF');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SMF 499B', 'Thesis - Part 2', 0.75, 'PRJ', 'Continuation of SMF 499A.', 'SMF');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SPCOM 339', 'Media, Images, and Communication', 0.5, 'SEM', 'This course investigates how distinct modes of communication impact the creation of messages, dissemination of knowledge, and reception of meaning. Students will examine aural, textual, and visual communication in the context of print, electronic, and digital media.', 'SPCOM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('VCULT 400', 'Visual Culture Seminar', 0.5, 'LAB, SEM', 'This fourth-year honours seminar leads students through a variety of topics in the theory and history of visual culture. Through class discussions, readings, and presentations, students will engage in an interdisciplinary dialogue around what constitutes visual culture today and how to analyze it.', 'VCULT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('VCULT 401', 'Advanced Visual Culture Seminar', 0.5, 'LAB, SEM', 'This fourth-year level advanced visual culture seminar is designed to focus student research skills toward the development of a thesis project.', 'VCULT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CHEM 383', 'Medicinal Chemistry', 0.5, 'LEC, TUT', 'This course discusses the drug discovery process and drug synthesis. Topics include enzymes and receptors, hit and lead discovery, lead optimization, QSAR, prodrugs, drug delivery, drug metabolism, mechanisms of selected drugs and laboratory and process scale synthesis of selected drugs. [Formerly CHEM 482; Offered F]', 'CHEM');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('CLAS 326', 'The Body, Dress, and Religion', 0.5, 'LEC', 'From clothing to hairstyles to tattoos, dress can express beliefs, aspects of identity, and power. This course examines dress in a variety of religions from ancient Greece to the modern era. The course includes attention to the look and feel of dress on the body, conflicts that can arise over dress and religion, as well as the role of dress in the formation and maintenance of religious communities.', 'CLAS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EARTH 627', 'Radioactive Isotope Systems', 0.5, 'LEC', 'Principles of radioactive decay and geochronology.  Applications of geochronology and isotopic tracing using the U-Th-Pb, Re-OS, Lu-Hf, Sm-Nd, Rb-Sr, and Ar-Ar Isotope systems, U-series disequilibrum, cosmogenic nuclides, and extinct radionuclides.  Offered in alternate years.', 'EARTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EASIA 120R', 'Monsters and Magic in Japanese Popular Culture', 0.5, 'LEC', 'An examination of the roles played by monsters, magic, and supernatural creatures in Japanese popular culture, with attention to their treatment in Buddhism, Shinto, Daoism, and folk practices. Topics include modern-day creations such as Godzilla and Pokemon and traditional beings such as deities, ghosts, angry spirits, goblins, and shapeshifters.', 'EASIA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 608', 'Quantitative Methods in Biomedical Engineering', 0.5, 'LEC', 'This biomedical engineering core course focuses on topics related to the use of quantitative tools in biomedical engineering research studies.  It will teach students how to conduct statistical analysis of biomedical data, design biomedical experiments that can offer statistical insight, and apply computational methods to solve problems in biomedical engineering.  Educational emphasis will be placed on developing students'' core competence in biostatistics and biomedical computing, so as to prepare them to pursue biomedical engineering investigations that are backed by quantitative reasoning and numerical insights.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 642', 'Radio Frequency Integrated Circuit Design', 0.5, 'LEC', 'Transistor-level design of circuits for wideband RF front-ends. An overview of 2G (GSM) and 3G (W-CDMA) standards and relevant radio architectures is presented, with key system specifications mapped onto circuit specifications. On-chip passive component design and simulation aimed at maximizing RF performance is discussed in detail. Circuit examples include: wideband preamplifiers and gain blocks, I-Q up/downconverters, voltage- and digitally-controlled oscillators (VCO/DCO), and power amplifier drivers. Design of circuit blocks for mm-wave frequency applications and RF testing, packaging and characterization are also discussed. Understanding of analog circuit design and semiconductor devices, and analog circuit simulation experience (e.g., SPICE) is required.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 657A', 'Data & Knowledge Modelling & Analysis', 0.5, 'LEC', 'Engineers encounter data in many of their tasks. Whether the sources of this data may be experiments, databases, computer files, or the Internet, there is a dire need for effective methods to model and analyze the data and extract useful knowledge and information from it. This course aims to provide engineering graduate students with essential knowledge of data representation, grouping, mining and knowledge discovery.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 659', 'Intelligent Sensors & Wireless Sensor Networks', 0.5, 'LEC', 'This course is concerned with recent developments in intelligent sensors and wireless sensor networks. This course will introduce students to diverse fundamental issues encountered in designing and analyzing intelligent sensors and sensor networks (mobile and stationary), with emphasis on mission critical applications.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 676', 'Quantum Information Processing Devices', 0.5, 'LEC', 'This course introduces the fundamental concepts and the most recent achievements in physical realization of quantum information devices and systems in three platforms; Nuclear Magnetic Resonance (NMR), quantum photonics, and superconducting electric circuits.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ECE 677', 'Quantum Electronics and Photonics', 0.5, 'LEC', 'The course is introductory and emphasizes the fundamental concepts and engineering applications without a previous exposure to quantum mechanics. Examples and problems are designed to address the applications of the course contents to real problems in electronic, optoelectronic, photonic and superconductive devices.', 'ECE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('EMLS 622R', 'Thesis Writing in English', 0.5, 'SEM', 'This course develops the skills and knowledge required to write theses and journal articles. A guided analysis of academic texts clarifies disciplinary conventions. Written assignments are directly related to the student''s research work, and feedback is customized. 
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENBUS 612', 'Social Entrepreneurship and Scaling Social Innovation', 0.5, 'LAB, LEC, SEM, TUT', 'This course explores different approaches that can be used to bring social innovations to scale so that they can have a broad, durable impact on a system.  The role of social entrepreneurs and institutional entrepreneurs will be introduced and explored as being key to catalyzing changes in resource flows, the understanding of problems and in shifting laws and policies that govern the problem domain.  New approaches to generating financing and revenue for social good, the governance and partnership structures these imply, and how they can enable or constrain systems change.  Examples will be drawn from Canadian and international contexts form the perspectives of entrepreneurs, investors/donors, policymakers and beneficiaries', 'ENBUS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 248', 'Literature for an Ailing Planet', 0.5, 'LEC', 'Can the humanities change how cultures relate to environments and the natural world? This course surveys environmental thought in works of literature and in popular culture.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ENGL 378', 'Professional Communications in Statistics and Actuarial Science', 0.5, 'LEC', 'This course introduces students to oral and written communication in the fields of statistics and actuarial science. With emphasis on the public presentation of technical knowledge, the ability to give and receive constructive feedback, and communication in a collaborative environment, this course helps students develop proficiencies in critical workplace skills. This course is writing intensive and includes extensive collaborative assignments.', 'ENGL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('FR 605', 'Community Fieldwork in French', 0.5, 'PRA', 'This graduate practicum in French community work offers advanced experiential learning in an area related to the student¬øs professional interest. A student wishing to pursue this possibility must submit to the Associate Chair, Graduate Studies, a proposal that outlines the plans for the practicum (e.g., the organizing unit, the on-site supervision, the nature of the community work, the relevance to the French graduate program) and the arrangements for evaluation.', 'FR');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('FR 606', 'Research or Professional Practicum in French', 0.5, 'PRA', 'This graduate practicum in French offers advanced experiential learning in an area related to the student¬øs professional or research interest. A student wishing to pursue this possibility must submit to the Associate Chair, Graduate Studies, a proposal that outlines the plans for the practicum (e.g., the organizing unit, the on-site supervisor, the nature of the professional or research work, the relevance to the French graduate program) and the arrangements for evaluation.', 'FR');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 374', 'Special Topics in Geography', 0.5, 'LEC, SEM, TUT', 'These courses allow for additions to the program on a short-term basis, and for the development of future permanent courses.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 693', 'Human Geography Special Topics Course', 0.5, 'LAB, LEC, SEM, TUT', 'Seminar topics offered one time only, or to introduce a potential permanent course within the designated subject area.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 694', 'Environmental Management Special Topics Course', 0.5, 'LAB, LEC, SEM, TUT', 'Seminar topics offered one time only, or to introduce a potential permanent course within the designated subject area.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 695', 'Geomatics Special Topics Course', 0.5, 'LAB, LEC, SEM, TUT', 'Seminar topics offered one time only, or to introduce a potential permanent course within the designated subject area.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 696', 'Environmental Science Special Topics Course', 0.5, 'LAB, LEC, SEM, TUT', 'Seminar topics offered one time only, or to introduce a potential permanent course within the designated subject area.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GER 230', 'Vikings!', 0.5, 'LEC', 'This course will examine the Viking Age cultures, which flourished in Northern Europe and beyond from c. 800 to 1100 CE. The goal of this course is to familiarize students with the Viking Age and to deepen students'' understanding of the processes through which peoples in the present give meaning to the past.', 'GER');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GER 793', 'Knowledge Transfer & Reflection', 0.25, 'PRA', 'The course encompasses two learning elements: A Knowledge Transfer Project and an Intercultural Reflection. Both elements are designed to encourage reflection on your academic and experiential learning progress and to engage with a wider audience.', 'GER');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HLTH 604', 'Analysis and Management of Health Information for Aging Societies', 0.05, 'LEC', 'The course focuses on how information from surveys and administrative or clinical databases can be used to support evidence-informed decision making in the context of population aging.  The course will combine an overview of health policy issues and service delivery with methodological considerations in the analysis of health information from a variety of sources.', 'HLTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HLTH 605', 'Regression Models', 0.5, 'LEC', 'The use of regression models (for continuous and categorical outcomes) for analysis of multi-variable data sets.  Strategies for model building, fitting of models, assessing model assumptions and testing hypothesis.  Application of these models to problems in the health sciences.', 'HLTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HLTH 605A', 'Survey Research Methods', 0.25, 'LEC', 'None', 'HLTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HLTH 605E', 'Analysis of Variance II', 0.25, 'LEC', 'Methods for determining Expected Mean Squares. Design and Analysis of Factorial, Fractional Factorial and Repeated Measures Experiments. Design and Anlysis and other designs as time permits. SAS may be used to obtain listings used in the anlysis .', 'HLTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HLTH 606', 'Epidemiological Methods', 0.5, 'LEC', 'An investigation of the epidemiology of selected non-infectious diseases with emphasis on the identification of risk factors and on the methodology of epidemiological investigations.', 'HLTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HLTH 607', 'Mechanisms of Disease Processes', 0.5, 'LEC', 'The focus of the course is on basic physiological and immunological mechanisms involved in major contemporary health problems, including those which are contributed to by behavioural factors.', 'HLTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HLTH 610', 'Program Development and Service Delivery for the Elderly', 0.5, 'LEC', 'The various programs and services, particularly in the non-medical areas, will be discussed. Emphasis will be placed on various programs which are available to the elderly residing in the community, such as home care and homemaking services, various outreach programs, including day hospitals, placement and coordination services, geriatric assessment services, vacation relief beds and foster home programs. The reasons for the changes which have taken place over time will also be investigated. In addition, the cost-effectiveness and methods how such cost-effectiveness can be evaluated will be outlined. As with HSG 703, students in this course will be expected to undertake some field work.', 'HLTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HLTH 619', 'Fundamental Research Methods in Health Informatics', 0.5, 'LEC', 'This course focuses on the fundamental methods that are commonly employed in modern health informatics research in order to collect, store, organize, process, analyze, and communicate data/information/knowledge.  Emphasis is on computerized methods and concepts.  Students will gain both theoretical knowledge and practical experience so that they can apply the learned methods in practice after completing this course.', 'HLTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HLTH 620', 'Selected Topics', 0.5, 'RDG', 'Fall/Winter/Spring Specific topics may include health behaviour change, decision making in health delivery systems, stress coping strategies, risk assessment and management, psychopharmacology and addiction, nutrition and chronic disease, psychosocial factors in disease, accident and injury control, health assessment, health and the aging person, etc.', 'HLTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HLTH 621', 'Decision Making and Decision Support in Health', 0.5, 'LEC', 'One of the major aims of heal informatics is to help health professionals make better decisions.  To this end, diverse models and methods of decision making and decision support have been developed and implements in health care settings. This course reviews theories, methods, and technologies for aiding the process of making decisions in health care.', 'HLTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HLTH 622', 'Nutritional Aspects of Chronic Disease', 0.5, 'SEM', 'Nutrition is integral to the etiology, prevention and treatment of chronic diseases.  This course will examine nutritional aspects of key chronic diseases affecting the Canadian population.  There will be an opportunity for students to explore, in depth, specific conditions and aspects of nutritional assessment or intervention that interest them.', 'HLTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HLTH 641', 'Practicum', 0.5, 'PRA', 'Fall/Winter/Spring The supervised practicum is intended to enhance basic or applied research skills. The placement may involve a combination of research design or program development, data collection (using quantitative or qualitative methods), analyses, interpretation, and presentation. A contract stipulating practicum objectives, and work to be completed to meet these objectives must be jointly approved by the student, the practicum supervisor, the academic supervisor, and the practicum coordinator. A written report by the student, together with a letter from the field supervisor, are used by the co-ordinator to evaluate student performance. This course is intended for Masters level students. Graded on a Cr/NCr basis. Prerequisite: consent of instructor.', 'HLTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HLTH 642', 'Multidisciplinary Perspectives on Aging', 0.5, 'LEC', 'This course provides an opportunity for critical discussion and analysis of a range of key issues in aging and aging research, and of health system and health policy issues affecting older persons.  Speakers will represent a variety of disciplines, reflecting the importance of interdisciplinary perspectives in aging research, as well as the combination of biomedical and psychosocial factors that contribute to healthy aging or to the development of frailty and chronic disease.', 'HLTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HLTH 671', 'Psychopharmacology', 0.5, 'LEC', 'The primary objective of the course will be to provide a basic understanding of how drugs can alter the function of neural cells, and how these changes can affect mood, cognition, and behaviour.  Key topics to be discussed include:  a) biological principles of pharmacology, b) general structure and function of the nervous system, c) major neurotransmitter systems of the brain, d) mechanisms of drug action on neurotransmission, and e) pharmaotherapy.', 'HLTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HLTH 672', 'Epidemiologic Methods in Aging Research', 0.5, 'LEC', 'This course introduces the application of epidemiologic methods to the study of older persons.  This lecture/discussion will present core epidemiologic concepts and methodologic issues in epidemiologic students of older persons.  We will then study predictors of health and three major health outcomes:  longevity/mortality, functional status/disability, and disease.  Students will critically evaluate epidemiologic studies in aging research and will investigate a specific topic in this field in depth.', 'HLTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HLTH 701', 'Interdisciplinary Seminar in Public Health and Health Systems', 0.5, 'SEM', 'This advanced seminar examines modes of research inquiry in the health sciences.  Issues related to measurement, casual inference, experimental and quasi-experimental research designs, descriptive, interpretive, and ethnographic methods of data collection will be examined.  The objectives of the course are to build aspiring scholars'' expertise in diverse modes of inquiry and systems of knowledge production in the health sciences, in critical assessment and contextualization of methodologies within the academic, interdisciplinary research, collaboration, and knowledge translation.', 'HLTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HLTH 704', 'Advanced Qualitative Methods for Health Research', 0.5, 'SEM', 'The aim of this course is to equip students with advanced knowledge and skills to understand, conceptualize, design, conduct, and critically evaluate qualitative health research.  It is designed for students wishing to develop their own research at the master''s or doctoral level.  Emphasis is on: 1) exploring the relationship between techniques designed to gather particular kinds of data and the broader epistemological and theoretical questions underpinning qualitative health research; and 2) giving students an opportunity to practically engage with key qualitative research skills (interviewing, data analysis) and to reflect on the ethical, theoretical, and practical issues that arise in conducting qualitative research in health settings.', 'HLTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HLTH 705', 'Advanced Statistical Methods for Analyzing Public Health and Health Systems Data', 0.5, 'LEC', 'This course will focus on statistical methods for analyzing and drawing inferences from longitudinal data, time-to-event data and incomplete/missing data, with emphasis on their application for public health sciences.  Topics include linear and generalized linear regression models (for correlated data), and imputation techniques for incomplete correlated and multilevel data.  Students will learn appropriate methods to implement (particularly through the use of software) and interpret analyses of various types of complex public health data.', 'HLTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HLTH 706', 'Advanced Epidemiological Methods', 0.5, 'SEM', 'Building on HLTH 606, this course provides an in-depth survey of theory and methods in epidemiology, focusing on answering complex research questions using epidemiologic methods.  Course topics include modeling multinomial, count, rate, and survival data and disease transmission; infectious disease dynamics; assessing the reliability/validity of measurement instruments; an in-depth examination of various study designs (e.g., case control, nested case control, case-cohort, randomized controlled trials, panel studies, and hybrid study designs); critical appraisal of the medical literature; and systematic reviews and meta-analysis.', 'HLTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HLTH 719', 'Advanced Research Methods in Health Informatics', 0.5, 'LEC', 'This course covers advanced research methods in health informatics that are often applied in cutting-edge health informatics research.  Fundamental research methods will be extended to greater depth.  Latest information technology will be discussed and applied in the context of health informatics.', 'HLTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HLTH 720', 'Advanced Topics', 0.5, 'RDG', 'An in-depth analysis of specific topics of interest. Representative topics may include advanced epidemiological and statistical methods for health research, cancer prevention and control, advanced health program evluation, psychoneuroimmunology, addictions, caregiver issues, etc. The form of the study may include a literature review or the planning and execution of and execution of an independent study leading to a mamor paper for possible publication.', 'HLTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HLTH 732B', 'Work and Health Research Seminar (II)', 0, 'SEM', 'This seminar is a forum for student presentations about results of or proposals for research.  Invited speakers will also present results of research from time to time.  Attendance at the seminar is required for two terms (i.e., during the candidates'' first two years in the program).  Attendance beyond that is encouraged.  The range of topics that will be addressed in the seminar crosses all areas of investigation in the collaborative program.  Grading will be on a credit/no credit basis.', 'HLTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HLTH 741', 'Advanced Practicum', 0.5, 'RDG', 'The supervised research practicum is intended to enhance basic or applied research skills as demonstrated through previous practica, research assistantships or other research experiences.The placement may involve a combination of research design or program development, data collection (using quantitative or qualitative methods), analyses, interpretation and presentationa. A contract stipulating practicum objectives, and work to be completed to meet these objectives must be approved jointly by the student, the practicum supervisor, the academic supervisor, and the practicum co-ordinator. A written report by the student, together with a letter from the field supervisor, are used by the co-ordinator to evaluate student performance.', 'HLTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HLTH 750', 'Fundamentals of Aging, Health and Well-being', 0.5, 'LEC', 'This survey course will be team-taught by members of the collaborative program and will serve to introduce students to major sub-areas of aging, health and well-being research.  Topics will be covered from each Department within the Faculty of Applied Health Sciences.', 'HLTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('HLTH 751', 'Aging, Health and Well-being Research Seminar', 0, 'SEM', 'This seminar is a forum for student presentations about results of, or proposals for research.  Invited speakers will also present  results of research from time to time.  Attendance at the seminar is required for two terms (i.e., during the candidates''  first two years in the program).  The range of topics that will be addressed in the seminar crosses all areas of investigation in the collaborative program.  Grading will be on a credit/no credit basis.  Must be registered in the PhD program in Aging, Health and Well-being.', 'HLTH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KOREA 301R', 'Third-Year Korean 1', 0.5, 'LAB, LEC', 'This course provides opportunities to strengthen students'' reading skills through engagement with articles, short stories, and folk tales. Emphasis is on grammatical analysis and contextual interpretation. Written compositions and oral presentations provide opportunities for personal response and reflection.', 'KOREA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('KOREA 302R', 'Third-Year Korean 2', 0.5, 'LAB, LEC', 'This course provides additional opportunities for engagement with texts at an advanced level. Students develop specialized vocabulary by studying and practicing complex sentences and idioms.', 'KOREA');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('LS 387', 'Criminal Justice Institutions', 0.5, 'LEC', 'The societal context of policing, courts, and corrections is examined to understand how social control and the law are used in the criminal justice system. Special emphasis is placed on the socio-economic, legal, political, and situational environment that shapes responses to different categories of offenders and offences.', 'LS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 640', 'Autonomous Mobile Robotics', 0.5, 'LAB, LEC', 'This course presents the fundamentals of Autonomous Mobile Robotics, including both perception and planning for autonomous operation. Topics in Perception include sensor modeling, vehicle state estimation using Bayes Filters, Kalman Filters, and Particle Filters, and simultaneous localization and mapping. Topics in Planning include vehicle motion modeling and control, reactive, graph based and optimal motion planning. An emphasis on examples from recent research in the area pervades the course content. The course requires background knowledge in state space modeling, linear algebra, probability theory and optimization theory.', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 659', 'Energy and Environment', 0.5, 'LEC', 'Review Canada''s and the world''s energy situation from energy reserve availability, to consumption trends, to environmental impact. Energy resources covered include fossil fuels, nuclear, solar, wind, geothermal, and others. The technological capabilities and challenges associated with, and physics and engineering principles behind, a range of energy conversion systems are covered. The energy conversion systems covered are fossil fuel systems, nuclear fission power plants, photovoltaic and thermal solar collector systems, wind energy systems, and a range of other systems as appropriate from wave and tidal energy to fuel cells. The thermodynamics of energy conversion systems is considered from the perspectives of optimization and improving energy conversion efficiency. May be held with ME 459 (Energy Conversion) but with enhancements for the ME 659 students. Also offered Online.', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 671', 'Fundamental Fire Dynamics', 0.5, 'LEC', 'Fuels and their chemical structure. Polymers and polymer decomposition, ignition of liquids and solids, geometric and chemical effects. Combustion processes and reaction stoichiometry. Ignition, catalysis and combustion kinetics. Heat of reaction, flame temperature, fire heat release rate. Flammability, evaporation, flashpoint. Conduction, convection and radiation heat transfer in fire analysis. Gas and flame radiation, point source, planar models. Premixed flames, explosions, deflagrations. Suppression, flame arrestors, "explosion proof" equipment. Diffusion and jet flames, fire plumes, flame heights, CO, soot and smoke formation. (Also offered Online)', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 672', 'Advanced Fire Dynamics', 0.5, 'LEC', 'Ignitability of materials, flame spread, burning rates. Specification of design fires. Stages of room fires, hot layer development and composition. Thermal stratification, entrainment, ceiling flames and jets, smoke filling. Principles of ventilation, wall and ceiling vents, pressure distributions, smoke control. Wall and ceiling heat transfer, corner effects. Enclosure and zone radiation. Room fire growth and spread, flashover, rapid fire growth phenomena. Use of zone/field models for fire development. (Also offered Online)', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 673', 'Fire Modelling', 0.5, 'LEC', 'Introduction to Computational Fluids Dynamics (CFD) simulation of complex, fire-related flows. First-and second-order finite control volume discretization and iterative solution methods. Introduction to finite element analysis. Boundary conditions, turbulence models and grid generation techniques. Assessment of strengths, limitations and uncertainty in simulation of complex applications relevant to fire engineering. Introduction to zone and field models, such as CFAST and FDS, with application to practical fire engineering applications and design. ME 671 recommended but not required. (Also offered Online)', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 680', 'Advanced Design Engineering', 0.5, 'LEC, SEM, STU', 'An overview of advanced engineering design, from the establishment of the need to preliminary design, including formal and informal methods to facilitate and encourage innovation. Focus is places on a fundamental understanding of the problem and various theoretical and practical techniques to arrive at feasible solutions. There is a strong focus on piratical applications, including case studies, and culminating in a major design project.', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 681', 'Advanced Design Engineering- Design Project 1', 0.5, 'PRA, RDG, STU', 'In this course students apply design methods, principles and practices learned in the previous course (ME 680) to a highly authentic design project challenge. Students apply methods, principles and practices related largely to proposal refinement, needs assessment, conceptual design, embodiment design, design verification and design project communication. Aspects of design safety, sustainability and professional ethics and emphasized during the realization of the design project. Three graded design project reviews provide formative and summative assessment opportunities, as well as weekly instructor mentorship and assignments, preferably in a studio setting.', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('ME 682', 'Advanced Design Engineering- Design Project 2', 0.5, 'PRA, RDG, STU', 'In this course students apply design methods, principles and practices learned in the previous courses (ME 680 and ME 681) to a highly authentic design project challenge. Students apply methods, principles and practices related largely to embodiment design, design verification, design implementation and refinement and design project communication. Aspects of design safety, sustainability and professional ethics and emphasized during the realization of the design project. Three graded design project reviews provide formative and summative assessment opportunities, as well as weekly instructor mentorship and assignments, preferably in a studio setting.', 'ME');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MSCI 718', 'Statistical Methods for Data Analytics', 0.5, 'LEC, RDG', 'The objective of this course is to develop skills with a range of procedures and programs for multivariate data analysis. The focus will be on practical issues such as selecting the appropriate analysis, preparing data for analysis, menu-driven and syntax programming, interpreting output, and presenting results of a complex nature.', 'MSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MSCI 719', 'Operations Analytics', 0.5, 'LEC, RDG', 'This course covers predictive analytics that provides techniques to model the relationships between inputs and outcomes, and construct predictions about future outcomes, and prescriptive analytics that provides tools to optimize actions against a complex set of objectives to find best practices and design best policies under all circumstances. The theoretical techniques will be applied to such chains, service industries, healthcare systems, revenue management, inventory management, and sports.', 'MSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MTE 544', 'Autonomous Mobile Robots', 0.5, 'LAB, LEC, TUT', 'Fundamentals of autonomous mobile robotics, including both perception and planning for autonomous operation, sensor modelling, vehicle state estimation using Bayes Filters, Kalman Filters, and Particle Filters as well as onboard localization and mapping. Topics in planning include vehicle motion modelling and control, as well as graph based and probabilistic motion planning of (Micro Electro Mechanical Systems) MEMS devices. [Offered: F]', 'MTE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('MTE 546', 'Multi-sensor Data Fusion', 0.5, 'LAB, LEC', 'Sensor data and information fusion systems. Sensor modelling, including characterization of uncertainty. Sensor fusion approaches for estimation and decisions including weighted least squares, extended Kalman Filter, Dempster-Shafer evidential reasoning, artificial neural networks; Outlier rejection; Spatial and temporal registration. Course project involving independent study of one aspect of sensor data fusion. [Offered: W]', 'MTE');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 207J', 'Philosophy and J.R.R. Tolkien', 0.5, 'LEC', 'An introduction to the implicit philosophical content of Tolkien''s works, with a special emphasis on Lord of the Rings. Problems such as the existence of God, fate, free will, evil, death, and technology will be explored. Tolkien''s views will be examined in relation to philosophical works.', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 675', 'Graduate Courses in Applied Philosophy', 0.5, 'SEM', 'Graduate level courses covering specialized topics in Applied Philosophy.These courses are often held in conjunction with PHIL 673, Graduate Courses in Philosophy; the course requirements in PHIL 675 will involve application of philosophical methods and theories to practical problems. These courses are often held in conjunction with 400 level philosophy courses, through of course the work requirements for graduate students are more stringent. Students must pay attention to the restriction on the number of these courses they are allowed to count toward their degree.', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 676', 'Graduate Courses in Applied Philosophy', 0.5, 'SEM', 'Graduate level courses covering specialized topics in the department''s field of research concentration. These courses are often held in conjunction with PHIL 674, Graduate Course in Philosophy; the course requirements in PHIL 676 will involve application of philosophical methods and theories to practical problems.', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHIL 699', 'Applied Research Placement Tutorial', 1, 'SEM', 'Students in the Applied Philosophy program enroll in this tutorial in conjunction with a placement at a host organization. The Applied Research Placement will prepare the student to make a research contribution on a specific philosophical topic and  to make an applied contribution outside of philosophy.', 'PHIL');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PHS 656', 'Quantitative Methods and Analysis for Program', 0.5, 'LEC', 'This course will provide an introduction to quantitative methods for use in program evaluation practice. Students will learn the importance of quantitative  methods and analysis, appropriate analytic techniques to address certain evaluation questions, and how to interpret and report the results from those analyses. Study designs as well as descriptive and inferential statistics will be reviewed. Specific topics will include measures of central tendency and dispersion, probability, analysis of variance, categorical data analysis, correlation and regression, and data visualization. Emphasis will be on the theoretical and conceptual understanding and application of quantitative analysis relevant to program evaluation.', 'PHS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PLAN 475', 'Special Topics in Planning', 1, 'DIS, FLD, LAB, LEC, SEM, TUT', 'Course content varies according to instructor availability and demand for specific topics in planning, including field courses.', 'PLAN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PLAN 706', 'Contemporary Issues in Planning', 0.5, 'SEM', 'The nature and implications of key forces of change, trends and current issues are identified in the context of urban and regional planning practice. Case studies, drawn from Canadian and international experiences and challenges, will frame the exploration of evolving, contemporary issues.', 'PLAN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PSCI 302', 'Local Government', 0.5, 'LEC', 'An examination of the politics and administration of local government in Canada. Topics may include the relationship between municipalities and other levels of government, current governance challenges facing local officials, and the design and implementation of public policy within a complex local political environment.', 'PSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PSCI 303', 'Environmental Policy and Politics', 0.5, 'LEC', 'This course provides an introduction to major historical and contemporary debates in Canadian environmental policy and politics in comparative context. It emphasizes the divergent policy interests and political strategies influencing environmental outcomes.', 'PSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PSCI 493', 'Field Course', 0.5, 'FLD', 'A combination of academic study and field experience in topics of political science.', 'PSCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PSYCH 493', 'Data Analysis and Graphing in R', 0.5, 'LAB, PRJ, SEM', 'The course aims to make students effective, autonomous users of R, a statistics software program with graphical applications and utility in many careers. Students learn to code syntax, analyze, and graph data using R. Each student conducts an R-based data analysis project of particular interest to them.', 'PSYCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PSYCH 758', 'Applied Practicum in Cognitive Psychology', 0.5, 'PRA', 'Principles derived from research in cognitive psychology are widely used in applied contexts.  As such training in cognitive psychology opens a number of doors for non-academic career paths.  The goal of this course is to provide students with experience in this domain.  To this end, students will work in an industry position related to cognitive psychology.  Through this course, students will gain experience applying their research skills in an applied context, learn new skills relevant to applied research and/or knowledge translation, and expand their professional networks.', 'PSYCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('PSYCH 759', 'Research Practicum in Cognitive Psychology', 0.5, 'PRA', 'Research in cognitive psychology is comprised of a wide array of methods and approaches.  Researchers able to effectively use this diverse toolkit are better positioned to solve important problems in cognitive psychology.  The goal of this course is to provide students with novel experiences conducting research in cognitive psychology.  To this end, contingent on the availability of appropriate opportunities, students will work in a laboratory outside that of their primary research advisor on an independent research project relevant to cognitive psychology.  Through this course, students will gain experience applying new methods and approaches in cognitive psychology, learn new research skills, and expand their professional networks.', 'PSYCH');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('RS 266', 'Death and Dying', 0.5, 'LEC', 'Death is more than a physical event. It also has legal, social, cultural, psychological, and religious significance. This course examines views about dying, death, and death rituals in several Western and Eastern religions, as well as in some Indigenous spiritualities.', 'RS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('RS 316', 'Religion and War: The Language and Imagery of War within Islam, Christianity, and Judaism', 0.5, 'LEC, SEM', 'This course explores the phenomena of militant words and images within Islam, Christianity, and Judaism. Considered in conversation with one another, these Abrahamic traditions challenge students to consider the interconnectedness of these communities, histories, and textual traditions.', 'RS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SCI 10', 'Communication Skills for Science', 0, 'SEM', 'The course is designed to develop communication skills to prepare international Science students for assignments, group work, oral presentations, and technical writing. The course combines Science specific content and approaches to problem solving with specific learning objectives for written and spoken communication. Activities may include a field trip, a tour of department laboratories, and group work for an oral presentation. The course is taught by a department member, a writing instructor, and numerous peer mentors. The course focuses on providing individual feedback, self-improvement, and increasing student competence and confidence. [Offered F]', 'SCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SCI 240', 'Science of Forensics and Criminalistics', 0.5, 'LEC', 'An introduction to the scientific methods and techniques for gathering physical evidence from crime scenes and the investigation of objects and sites associated with other kinds of illegal and criminal activity such as fraud, forgery, and gross negligence. This course examines the features and characteristics of minerals, rocks, fossils, gemstones, sand and soils and considers applications in forensic science and criminalistics. Other topics such as the geology of art, forensic anthropology and environmental forensics are included. [Offered: W]', 'SCI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SDS 322R', 'Community Engagement and Social Development', 0.5, 'LEC', 'This course highlights the importance of community engagement for individual, community, and societal well-being and introduces theory and methods related to social inclusion and participation in community. Through case examples and hands-on experience, this course explores approaches to research and professional practice designed to stimulate social development through community engagement.', 'SDS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SDS 451R', 'Community Based Research for Social Development', 0.5, 'SEM', 'This course introduces research approaches that prioritize community participation in the active creation and democratic use of knowledge for social development. Through concrete examples, this course explores the role of power in knowledge production, and examines fundamental principles and dilemmas in community-engaged research.', 'SDS');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SI 120R', 'Literary Arabic for Heritage Speakers', 0.5, 'LAB, LEC, TUT', 'Designed for heritage speakers who have had some meaningful exposure to Arabic (usually a spoken dialect), this language course focuses on improving technical literacy in Modern Standard Arabic (Fusha) by concentrating on grammar and literary skills. Upon finishing, students will be prepared to join non-heritage students in either intermediate or advanced Arabic.', 'SI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SI 131R', 'Arab Culture', 0.5, 'LEC', 'Integrating elements of Arab history, geography, language, literature (prose and poetry), art, music, theatre, cinema, politics, press, humour, dance, fashion, and food, this course explores varied expressions of Arab culture. Students will also study the phenomenon of Orientalism and the impact of European colonialism upon the Arabs.', 'SI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SI 132R', 'Introduction to Modern Arab and Muslim Drama', 0.5, 'LEC', 'The course explores modern Arab and Muslim drama in English from multiple perspectives: literary, social, economic, and political.', 'SI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SI 316R', 'Religion and War: The Language and Imagery of War within Islam, Christianity, and Judaism', 0.5, 'LEC, SEM', 'This course explores the phenomena of militant words and images within Islam, Christianity, and Judaism. Considered in conversation with one another, these Abrahamic traditions challenge students to consider the interconnectedness of these communities, histories, and textual traditions.', 'SI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SI 420R', 'Islamic Spirituality (Sufism) and Psychotherapy', 0.5, 'SEM', 'This course explores Muslim conceptions of the soul and psychotherapy within religious and spiritual frameworks. Students will place contemporary models and psychotherapeutic approaches in dynamic conversation with the psycho-spiritual strategies of traditional Sufi systems and sages.', 'SI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SI 450R', 'Reading and Interpreting the Qur''an (in translation)', 0.5, 'SEM', 'This course serves as a comprehensive introduction to the sacred text of Islam. Students engage in a process of scriptural reasoning as they read the Qur''an and learn a wide array of hermeneutical approaches, from traditional forms of exegesis to new trends in the academic study of the Qur''an.', 'SI');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 334', 'Public Policy', 0.5, 'LEC', 'An examination of the policy-making process in liberal democratic societies, and an evaluation of the tools employed by policy analysts to understand that process.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SOC 387', 'Criminal Justice Institutions', 0.5, 'LEC', 'The societal context of policing, courts, and corrections is examined to understand how social control and the law are used in the criminal justice system. Special emphasis is placed on the socio-economic, legal, political, and situational environment that shapes responses to different categories of offenders and offences.', 'SOC');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('WKRPT 100M', 'Work-term Report', 0.25, 'PRJ', 'A work-term report presenting in detail a technical project, activity, or analysis engaged by the student normally during the preceding work term. The report is evaluated on the basis of written communication skills and technical proficiency in the subject matter as demonstrated by the report. Work-term reports are required courses for the degree but their grades are not included in the average.', 'WKRPT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('GEOG 415', 'Economic Geography Project', 1, 'PRJ', 'Economic geography concepts and techniques are applied to the study of a current economic or social issue facing a local, regional, or national jurisdiction.', 'GEOG');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SWK 610R', 'Substance Abuse & Chemical Dependency', 0.5, 'LEC', 'This course serves as an overview of addiction, chemical abuse and chemical dependency and how social workers and pharmacists can independently or as a member of an interpersonal health team impact those affected. Topics include prevention, identification, treatment options, clinical aspects of treatment, and an understanding of support systems available for those in recovery. (Note: This is an online course).', 'SWK');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SWK 653R', 'Palliative Care', 0.5, 'LEC, SEM, TUT', 'Palliative care is a model of health care that cares for people with terminal illnesses, along with their families, in order to relieve suffering and improve quality of life. Care is provided from early diagnosis through to bereavement care of the family. Palliative care is delivered by an interprofessional team which is most often comprised of medicine, nursing, social work and chaplaincy; other professions are often involved such as pharmacy, occupational therapy and others. (Note: this is an online course).', 'SWK');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('SWREN 331R', 'Social Inequality, Social Justice, and Social Action', 0.5, 'LEC', 'This course examines the hidden causes of inequality and associated social injustices. It provides a snapshot of main issues associated with modern society and the evidence that ties them to persistent inequality and injustice. The course reviews not only the major types of inequality but also social and public policy responses to them.', 'SWREN');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('TAX 614', 'An Introduction to Accounting for Income Taxes', 0.5, 'LEC', 'The goal of financial accounting is to reflect the economic activities of the firm in its financial statements. This course introduces students to the accounting for income taxes with a particular focus on analyzing differences between accounting and tax treatments, computing tax provisions, and disclosing tax information in corporate financial statements. The course also provides exposure to the both internal and external users of tax accounting disclosures. In doing so, the course builds a solid grounding in the preparation of accounting information, but also helps students gain an appreciation for the role financial accounting in tax planning and compliance decisions.', 'TAX');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('WKRPT 200A', 'Arts Work-term Report 2', 0.13, 'PRJ', 'A work-term report presenting in detail a technical project, activity, or analysis engaged by the student normally during the preceding work term. The report is evaluated on the basis of written communication skills and technical proficiency in the subject matter as demonstrated by the report. Work-term reports are required courses for the degree but their grades are not included in the average. However, failed work-term report evaluations may delay a student''s progress as indicated in the Examinations and Promotions section of this calendar. Details regarding the evaluation of the work-term reports can be obtained from the student''s program advisor.', 'WKRPT');
INSERT INTO course (courseCode, title, credit, courseTypes, description, subjectTitle) VALUES ('WKRPT 200E', 'Environment Work-term Report 2', 0.13, 'PRJ', 'A reflection exercise through which students document the linkages between their work-term experiences and progress towards achieving their academic, professional and personal goals. The documentation can be presented in various forms, consistent with the Faculty''s guidelines. The report is evaluated on the basis of both the quality of presentation and strength of reflection. Work-term reports are required courses for the degree but their grades are not included in the average. However, failed work-term report evaluations may delay a student''s progress as indicated in the Examinations and Promotions section of this calendar. Details regarding the evaluation of the work-term reports can be obtained from the student''s program advisor.', 'WKRPT');
